INSERT INTO `icno` VALUES (14, 2, 'ABS指示灯', '该指示灯用来显示ABS工作状况。当打开钥匙门，车辆自检时，ABS灯会点亮数秒，随后熄灭。如果未闪亮或者启动后仍不熄灭，表明ABS出现故障。', '/uploadFile/d8972a1a-282b-4ca3-a1d7-b5a202bd7780.png');
INSERT INTO `icno` VALUES (29, 6, '禁止通行', '表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', '/uploadFile/paCongIcno1002.jpg');
INSERT INTO `icno` VALUES (30, 6, '禁止三轮车机动车通行', '表示禁止三轮机动车驶入。此标志设在禁止三轮机动车驶入的路段入口处。', '/uploadFile/paCongIcno1004.jpg');
INSERT INTO `icno` VALUES (31, 6, '禁止拖拉机驶入', '表示禁止拖拉机驶入。此标志设在禁止拖拉机驶入的路段入口处。', '/uploadFile/paCongIcno1006.jpg');
INSERT INTO `icno` VALUES (32, 6, '禁止非机动车进入', '表示禁止非机动车进入。此标志设在禁止非机动车进入行的路段入口处。', '/uploadFile/paCongIcno1008.jpg');
INSERT INTO `icno` VALUES (33, 6, '禁止驶入标志', '表示禁止一切车辆驶入。此标志设在单行路的出口处或禁止驶入的路段入口。', '/uploadFile/paCongIcno1010.jpg');
INSERT INTO `icno` VALUES (34, 6, '禁止大型客车驶入', '表示禁止大型客车驶入。此标志设在禁止大型客车驶入的路段入口处。', '/uploadFile/paCongIcno1012.jpg');
INSERT INTO `icno` VALUES (35, 6, '禁止农用车驶入', '表示禁止农用运输车驶入。此标志设在禁止农用运输车驶入的路段入口处。', '/uploadFile/paCongIcno1014.jpg');
INSERT INTO `icno` VALUES (36, 6, '禁止畜力车进入', '表示禁止畜力车进入。此标志设在禁止畜力车进入的路段入口处。', '/uploadFile/paCongIcno1016.jpg');
INSERT INTO `icno` VALUES (37, 6, '禁止机动车驶入', '表示禁止各类机动车驶入。此标志设在禁止机动车通行路段的入口处。', '/uploadFile/paCongIcno1018.jpg');
INSERT INTO `icno` VALUES (38, 6, '禁止小型客车驶入', '表示禁止小型客车驶入。此标志设在禁止小型客车驶入的路段入口处。', '/uploadFile/paCongIcno1020.jpg');
INSERT INTO `icno` VALUES (39, 6, '禁止二轮摩托车驶入', '表示禁止两轮摩托车驶入。此标志设在禁止两轮摩托车驶入的路段入口处。', '/uploadFile/paCongIcno1022.jpg');
INSERT INTO `icno` VALUES (40, 6, '禁止人力货运三轮车进入', '表示禁止人力货运三轮车进入。此标志设在禁止人力货运三轮车进入的路段入口处。', '/uploadFile/paCongIcno1024.jpg');
INSERT INTO `icno` VALUES (41, 6, '禁止载货汽车驶入', '表示禁止载货机动车驶入。此标志设在载货机动车驶入的路段入口处。', '/uploadFile/paCongIcno1026.jpg');
INSERT INTO `icno` VALUES (42, 6, '禁止汽车拖、挂车驶入', '表示禁止汽车拖、挂车驶入。此标志设在禁止汽车拖、挂车驶入的路段入口处。', '/uploadFile/paCongIcno1028.jpg');
INSERT INTO `icno` VALUES (43, 6, '禁止某两种车驶入', '表示禁止某两种车驶入。此标志设在禁止某两种车驶入的路段入口处。', '/uploadFile/paCongIcno1030.jpg');
INSERT INTO `icno` VALUES (44, 6, '禁止人力客运三轮车进入', '表示禁止人力客运三轮车进入。此标志设在禁止人力客运三轮车进入的路段入口处。', '/uploadFile/paCongIcno1032.jpg');
INSERT INTO `icno` VALUES (45, 6, '禁止人力车进入', '表示禁止人力车进入。此标志设在禁止人力车进入的路段入口处。', '/uploadFile/paCongIcno1034.jpg');
INSERT INTO `icno` VALUES (46, 6, '禁止向左转弯', '表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置。', '/uploadFile/paCongIcno1036.jpg');
INSERT INTO `icno` VALUES (47, 6, '禁止直行和向左转弯', '表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', '/uploadFile/paCongIcno1038.jpg');
INSERT INTO `icno` VALUES (48, 6, '解除禁止超车', '表示禁止超车路段结束。此标志设在禁止超车的终点。', '/uploadFile/paCongIcno1040.jpg');
INSERT INTO `icno` VALUES (49, 6, '禁止骑自行车下坡', '表示禁止骑自行车下坡通行。此标志设在禁止骑自行车下坡通行的路段入口处。', '/uploadFile/paCongIcno1042.jpg');
INSERT INTO `icno` VALUES (50, 6, '禁止向右转弯', '表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', '/uploadFile/paCongIcno1044.jpg');
INSERT INTO `icno` VALUES (51, 6, '禁止直行和向右转弯', '表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。', '/uploadFile/paCongIcno1046.jpg');
INSERT INTO `icno` VALUES (52, 6, '禁止车辆临时或长时停放', '表示在限定的范围内，禁止一切车辆临时或长时停放，禁止车辆停放的时间、车种和范围可用辅助标志说明此标志设在禁止车辆停放的地方。', '/uploadFile/paCongIcno1048.jpg');
INSERT INTO `icno` VALUES (53, 6, '禁止骑自行车上坡', '表示禁止骑自行车上坡通行。此标志设在禁止骑自行车上坡通行的路段入口处。', '/uploadFile/paCongIcno1050.jpg');
INSERT INTO `icno` VALUES (54, 6, '禁止直行', '表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', '/uploadFile/paCongIcno1052.jpg');
INSERT INTO `icno` VALUES (55, 6, '禁止掉头', '表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', '/uploadFile/paCongIcno1054.jpg');
INSERT INTO `icno` VALUES (56, 6, '禁止车辆长时停放', '禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', '/uploadFile/paCongIcno1056.jpg');
INSERT INTO `icno` VALUES (57, 6, '禁止行人进入', '表示禁止行人进入。此标志设在禁止行人进入的路段入口处。', '/uploadFile/paCongIcno1058.jpg');
INSERT INTO `icno` VALUES (58, 6, '禁止向左向右转弯', '表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', '/uploadFile/paCongIcno1060.jpg');
INSERT INTO `icno` VALUES (59, 6, '禁止超车', '表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', '/uploadFile/paCongIcno1062.jpg');
INSERT INTO `icno` VALUES (60, 6, '禁止鸣喇叭', '表示禁止鸣喇叭。禁止鸣喇叭的时间和范围可用辅助标志说明。', '/uploadFile/paCongIcno1064.jpg');
INSERT INTO `icno` VALUES (61, 6, '限制宽度', '表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。', '/uploadFile/paCongIcno1066.jpg');
INSERT INTO `icno` VALUES (62, 6, '限制速度', '表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。该图为：限制行驶时速不得超过40公里。此标志设在需要限制车辆速度的路段的起点。', '/uploadFile/paCongIcno1068.jpg');
INSERT INTO `icno` VALUES (63, 6, '减速让行', '表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。此标志设在视线良好交叉道路的次要路口。', '/uploadFile/paCongIcno1070.jpg');
INSERT INTO `icno` VALUES (64, 6, '限制高度', '表示禁止装载高度超过标志所示数值的车辆通行，该图为：装载高度不得超过3.5米。此标志设在最大允许高度受限制的地方。', '/uploadFile/paCongIcno1072.jpg');
INSERT INTO `icno` VALUES (65, 6, '解除限制速度', '表示限制速度路段结束。此标志设在限制车辆速度路段的终点。', '/uploadFile/paCongIcno1074.jpg');
INSERT INTO `icno` VALUES (66, 6, '会车让行', '表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。', '/uploadFile/paCongIcno1076.jpg');
INSERT INTO `icno` VALUES (67, 6, '限制质量', '表示禁止总质量超过标志所示数值的车辆通行，该图为：装载总质量不得超过10吨。此标志设在需要限制车辆质量的桥梁两端。', '/uploadFile/paCongIcno1078.jpg');
INSERT INTO `icno` VALUES (68, 6, '停车检查', '表示机动车必须停车接受检查，标志中可加注说明检查事项。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。', '/uploadFile/paCongIcno1080.jpg');
INSERT INTO `icno` VALUES (69, 6, '限制轴重', '表示禁止轴重超过标志所示数值的车辆通行，该图为：限制车辆轴重量不得超过7吨。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。', '/uploadFile/paCongIcno1082.jpg');
INSERT INTO `icno` VALUES (70, 6, '停车让行', '表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', '/uploadFile/paCongIcno1084.jpg');
INSERT INTO `icno` VALUES (71, 7, '十字交叉', '除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', '/uploadFile/paCongIcno1086.jpg');
INSERT INTO `icno` VALUES (72, 7, 'Y形交叉', '表示前方Y形路口。设在Y形路口以前的适当位置。', '/uploadFile/paCongIcno1088.jpg');
INSERT INTO `icno` VALUES (73, 7, '反向弯路', '表示前方是反向弯路。此标志设在两个相邻的方向相反的弯路前适当位置。', '/uploadFile/paCongIcno1090.jpg');
INSERT INTO `icno` VALUES (74, 7, '两侧变窄', '车行道两侧变窄主要指沿道路中心线对城缩窄的道路。此标志设在窄路以前适当位置。', '/uploadFile/paCongIcno1092.jpg');
INSERT INTO `icno` VALUES (75, 7, 'T形交叉', '丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', '/uploadFile/paCongIcno1094.jpg');
INSERT INTO `icno` VALUES (76, 7, '环形交叉', '有的环形交叉路口，由于受线形限制或障碍物阻挡。此标志设在面对来车的路口的正面。', '/uploadFile/paCongIcno1096.jpg');
INSERT INTO `icno` VALUES (77, 7, '连续弯路', '表示前方有连续弯路，引起注意。此标志设在有连续三个以上弯路的道路以前适当位置。', '/uploadFile/paCongIcno1098.jpg');
INSERT INTO `icno` VALUES (78, 7, '右侧变窄', '车行道右侧缩窄。此标志设在窄路以前适当位置。', '/uploadFile/paCongIcno1100.jpg');
INSERT INTO `icno` VALUES (79, 7, '左侧T形交叉', '丁字形标志原则上设在与交叉口形状相符的道路上。左侧丁字路口此标志设在进入丁字路口以前的适当位置。', '/uploadFile/paCongIcno1102.jpg');
INSERT INTO `icno` VALUES (80, 7, '向左急弯路', '表示前方左急转弯。向左急弯路标志设在左急转弯的道路前方适当位置。', '/uploadFile/paCongIcno1104.jpg');
INSERT INTO `icno` VALUES (81, 7, '上陡坡', '表示前方有陡坡路段。此标志设在纵坡度在7%和市区纵坡度在大于4%的陡坡道路前适当位置。', '/uploadFile/paCongIcno1106.jpg');
INSERT INTO `icno` VALUES (82, 7, '左侧变窄', '车行道左侧缩窄。此标志设在窄路以前适当位置。', '/uploadFile/paCongIcno1108.jpg');
INSERT INTO `icno` VALUES (83, 7, 'T形交叉', '丁字形标志原则上设在与交叉口形状相符的道路上。此标志设在进入T字路口以前的适当位置。', '/uploadFile/paCongIcno1110.jpg');
INSERT INTO `icno` VALUES (84, 7, '向右急弯路', '表示向右急弯路。设在右急转弯的道路前方适当位置。', '/uploadFile/paCongIcno1112.jpg');
INSERT INTO `icno` VALUES (85, 7, '下陡坡', '表示前方下陡坡。此标志设在纵坡度在7%和市区纵坡度在大于4%的陡坡道路前适当位置。', '/uploadFile/paCongIcno1114.jpg');
INSERT INTO `icno` VALUES (86, 7, '窄桥', '表示前方有窄桥。此标志设在桥面宽度小于路面宽度的窄桥以前适当位置。', '/uploadFile/paCongIcno1116.jpg');
INSERT INTO `icno` VALUES (87, 7, '双向交通', '双向行驶的道路上，采用天然的或人工的隔离措施，把上下行交通完全分离。由于某种原因（施工、桥、隧道）形成无隔离的双向车道时，须设置此标志。', '/uploadFile/paCongIcno1118.jpg');
INSERT INTO `icno` VALUES (88, 7, '注意信号灯', '注意前方有信号灯。此标志设在不易发现前方位信号灯控制的路口前适当位置。', '/uploadFile/paCongIcno1120.jpg');
INSERT INTO `icno` VALUES (89, 7, '易滑路段', '表示前方路面易滑，行驶至此路段必须减速慢行。此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。', '/uploadFile/paCongIcno1122.jpg');
INSERT INTO `icno` VALUES (90, 7, '堤坝路', '表示前方是堤坝路。此标志设在沿水库、湖泊、河流等堤坝路以前适当位置。', '/uploadFile/paCongIcno1124.jpg');
INSERT INTO `icno` VALUES (91, 7, '注意行人', '表示前方有人行横道，注意行人。一般设在郊外道路上划有人行横道的前方。城市道路上因人行横道线较多，可根据实际需要设置。', '/uploadFile/paCongIcno1126.jpg');
INSERT INTO `icno` VALUES (92, 7, '注意落石', '表示前方路段经常有落石掉落，注意行车安全。此标志设在左侧有落石危险的傍山路段之前适当位置。', '/uploadFile/paCongIcno1128.jpg');
INSERT INTO `icno` VALUES (93, 7, '傍山险路', '表示前方路段是伴山险路，注意驾驶。此标志设在山区地势险要路段（道路外侧位陡壁、悬崖危险的路段）以前适当位置。', '/uploadFile/paCongIcno1130.jpg');
INSERT INTO `icno` VALUES (94, 7, '村庄', '表示前方路段有村庄。此标志设在不易发现前方有村庄或小城镇的路段以前适当位置。', '/uploadFile/paCongIcno1132.jpg');
INSERT INTO `icno` VALUES (95, 7, '注意儿童', '注意前方有儿童出没。此标志设在小学、幼儿园、少年宫、儿童游乐场等儿童频繁出入的场所或通道处。', '/uploadFile/paCongIcno1134.jpg');
INSERT INTO `icno` VALUES (96, 7, '注意落石', '注意前方道路山坡有石头掉落。此标志设在右侧有落石危险的傍山路段之前适当位置。', '/uploadFile/paCongIcno1136.jpg');
INSERT INTO `icno` VALUES (97, 7, '傍山险路', '注意前方道路是傍山险路，谨慎驾驶。此标志设在山区地势险要路段（道路外侧位陡壁、悬崖危险的路段）以前适当位置。', '/uploadFile/paCongIcno1138.jpg');
INSERT INTO `icno` VALUES (98, 7, '隧道', '注意前方有隧道。此标志设在进入隧道前的适当位置。', '/uploadFile/paCongIcno1140.jpg');
INSERT INTO `icno` VALUES (99, 7, '注意牲畜', '注意前方有牲畜出没。此标志设在经常有牲畜活动的路段特别是视线不良的路段以前适当位置。', '/uploadFile/paCongIcno1142.jpg');
INSERT INTO `icno` VALUES (100, 7, '注意横风', '注意前方路段经常出现横风。此标志设在经常有很强的侧风并有必要引起注意的路段前适当位置。', '/uploadFile/paCongIcno1144.jpg');
INSERT INTO `icno` VALUES (101, 7, '堤坝路', '注意前方是堤坝路。此标志设在沿水库、湖泊、河流等堤坝路以前适当位置。', '/uploadFile/paCongIcno1146.jpg');
INSERT INTO `icno` VALUES (102, 7, '渡口', '特别是有的渡口地形较位复杂、道路条件较差，使用此标志能引起驾驶员的谨慎驾驶、注意安全。此标志设在汽车渡口以前适当位置。', '/uploadFile/paCongIcno1148.jpg');
INSERT INTO `icno` VALUES (103, 7, '驼峰桥', '注意前方路段出现驼峰桥，减速慢行。此标志设在注意前方是拱度较大，不易发现对方来车，应靠右侧行驶并应减速慢行。', '/uploadFile/paCongIcno1150.jpg');
INSERT INTO `icno` VALUES (104, 7, '无人看守铁路道口', '注意前方有无人看守的铁道路口，小心驾驶。此标志设在道口以前适当位置。', '/uploadFile/paCongIcno1152.jpg');
INSERT INTO `icno` VALUES (105, 7, '左右绕行', '用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左右绕行通过。放置在路段前适当位置。', '/uploadFile/paCongIcno1154.jpg');
INSERT INTO `icno` VALUES (106, 7, '注意危险', '此标志设在以上标志不能包括的其他危险路段以前的适当位置，用以提醒车辆驾驶人谨慎驾驶。本标志一般不单独使用，其下应设辅助标志，说明危险原因。', '/uploadFile/paCongIcno1156.jpg');
INSERT INTO `icno` VALUES (107, 7, '路面不平', '表示前方路段不平。此标志设在路面不平的路段以前适当位置。', '/uploadFile/paCongIcno1158.jpg');
INSERT INTO `icno` VALUES (108, 7, '注意非机动车', '提醒前方有非机动车出没。此标志设在混合行驶的道路并经常有非机动车横穿、出入的地点以前适当位置。', '/uploadFile/paCongIcno1160.jpg');
INSERT INTO `icno` VALUES (109, 7, '左侧绕行', '此标志表示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。放置在路段前适当位置。', '/uploadFile/paCongIcno1162.jpg');
INSERT INTO `icno` VALUES (110, 7, '斜杠符号', '表示距无人看守铁路道口，一道斜杠标志设在距离铁路道口50m的位置。放置在路段前适当位置。', '/uploadFile/paCongIcno1164.jpg');
INSERT INTO `icno` VALUES (111, 7, '过水路面', '注意前方路面有积水。此标志设在过水路面或漫水桥路段以前适当位置。', '/uploadFile/paCongIcno1166.jpg');
INSERT INTO `icno` VALUES (112, 7, '事故易发路段', '提醒前方路段经常发生事故，谨慎驾驶。此标志设在交通事故易发路段以前适当位置。', '/uploadFile/paCongIcno1168.jpg');
INSERT INTO `icno` VALUES (113, 7, '右侧绕行', '此标志表示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。放置在路段前适当位置。', '/uploadFile/paCongIcno1170.jpg');
INSERT INTO `icno` VALUES (114, 7, '无人看守铁路道口100米', '表示距无人看守铁路道口，二道斜杠标志设在距离铁路道口100m的位置。放置在路段前适当位置。', '/uploadFile/paCongIcno1172.jpg');
INSERT INTO `icno` VALUES (115, 7, '有人看守铁路道口', '表示前方有人看守铁路道口。此标志设在不易发现的道口以前适当位置。', '/uploadFile/paCongIcno1174.jpg');
INSERT INTO `icno` VALUES (116, 7, '慢行', '提醒减速慢行。此标志设在前方需要减速慢行的路段以前适当位置。', '/uploadFile/paCongIcno1176.jpg');
INSERT INTO `icno` VALUES (117, 7, '施工', '用以告示前方道路施工，车辆应减速慢行或绕道行驶。该标志可以作为临时标志支设在施工路段以前适当位置。', '/uploadFile/paCongIcno1178.jpg');
INSERT INTO `icno` VALUES (118, 7, '无人看守铁路道口150米', '表示距无人看守铁路道口，三道斜杠标志设在距离铁路道口150m的位置。该标志设在适当位置。', '/uploadFile/paCongIcno1180.jpg');
INSERT INTO `icno` VALUES (119, 8, '直行', '表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', '/uploadFile/paCongIcno1182.jpg');
INSERT INTO `icno` VALUES (120, 8, '直行和向右转弯', '表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯的路口以前适当位置。', '/uploadFile/paCongIcno1184.jpg');
INSERT INTO `icno` VALUES (121, 8, '立交直行和左转弯行驶', '表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', '/uploadFile/paCongIcno1186.jpg');
INSERT INTO `icno` VALUES (122, 8, '鸣喇叭', '表示机动车行至该标志处必须鸣喇叭。此标志设在公路的急转弯处、陡坡等视线不良路段的起点。', '/uploadFile/paCongIcno1188.jpg');
INSERT INTO `icno` VALUES (123, 8, '向左转弯', '表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', '/uploadFile/paCongIcno1190.jpg');
INSERT INTO `icno` VALUES (124, 8, '向左和向右转弯', '表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', '/uploadFile/paCongIcno1192.jpg');
INSERT INTO `icno` VALUES (125, 8, '立交直行和右转弯行驶', '表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', '/uploadFile/paCongIcno1194.jpg');
INSERT INTO `icno` VALUES (126, 8, '最低限速', '表示机动车驶入前方道路之最低时速限制。此标志设在高速公路或其他道路限速路段的起点。', '/uploadFile/paCongIcno1196.jpg');
INSERT INTO `icno` VALUES (127, 8, '向右转弯', '表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', '/uploadFile/paCongIcno1198.jpg');
INSERT INTO `icno` VALUES (128, 8, '靠右侧道路行驶', '表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', '/uploadFile/paCongIcno1200.jpg');
INSERT INTO `icno` VALUES (129, 8, '环岛行驶', '表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', '/uploadFile/paCongIcno1202.jpg');
INSERT INTO `icno` VALUES (130, 8, '单行路向左或向右', '表示一切车辆向左或向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', '/uploadFile/paCongIcno1204.jpg');
INSERT INTO `icno` VALUES (131, 8, '直行和向左转弯', '表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', '/uploadFile/paCongIcno1206.jpg');
INSERT INTO `icno` VALUES (132, 8, '靠左侧道路行驶', '表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', '/uploadFile/paCongIcno1208.jpg');
INSERT INTO `icno` VALUES (133, 8, '步行', '表示该街道只供步行。此标志设在步行街的两端。', '/uploadFile/paCongIcno1210.jpg');
INSERT INTO `icno` VALUES (134, 8, '单行路直行', '表示一切车辆单向行驶。此标志设在单行路的路口和入口处的适当位置。', '/uploadFile/paCongIcno1212.jpg');
INSERT INTO `icno` VALUES (135, 8, '干路先行', '表示干路先行，主干道有优先行驶权。此标志设在车道以前适当位置。', '/uploadFile/paCongIcno1214.jpg');
INSERT INTO `icno` VALUES (136, 8, '直行车道', '表示车道的行驶方向。此标志设在导向车道以前适当位置。', '/uploadFile/paCongIcno1216.jpg');
INSERT INTO `icno` VALUES (137, 8, '机动车行驶', '表示车道机动车行驶。此标志设在道路或车道的起点及交叉路口入口处前适当位置。', '/uploadFile/paCongIcno1218.jpg');
INSERT INTO `icno` VALUES (138, 8, '允许掉头', '表示允许掉头。此标志设在允许机动车掉头路段的起点和路口以前适当位置。', '/uploadFile/paCongIcno1220.jpg');
INSERT INTO `icno` VALUES (139, 8, '允会车先行', '表示会车时，本车道车先行通过，对面车道让行。此标志设在车道以前适当位置。', '/uploadFile/paCongIcno1222.jpg');
INSERT INTO `icno` VALUES (140, 8, '直行和右转合用车道', '表示车道的行驶方向行。此标志设在导向车道以前适当位置。', '/uploadFile/paCongIcno1224.jpg');
INSERT INTO `icno` VALUES (141, 8, '机动车车道', '表示该车道只供机动车行驶。设在该车道的起点及交叉路口和入口前适当位置，在标志无法正对车道时，可以不标注箭头。', '/uploadFile/paCongIcno1226.jpg');
INSERT INTO `icno` VALUES (142, 8, '人行横道', '表示该处为专供行人横穿马路的通道。此标志设在人行横道的两侧。', '/uploadFile/paCongIcno1228.jpg');
INSERT INTO `icno` VALUES (143, 8, '分向行驶车道', '表示车道的行驶方向。此标志设在导向车道以前适当位置。', '/uploadFile/paCongIcno1230.jpg');
INSERT INTO `icno` VALUES (144, 8, '非机动车行驶', '表示非机动车行驶。此标志设在道路或车道的起点及交叉路口入口处前适当位置。', '/uploadFile/paCongIcno1232.jpg');
INSERT INTO `icno` VALUES (145, 8, '右转车道', '表示车道的行驶方向。此标志设在导向车道以前适当位置。', '/uploadFile/paCongIcno1234.jpg');
INSERT INTO `icno` VALUES (146, 8, '公交线路专用车道', '表示该车道专供本线路行驶的公交车辆行驶。此标志设在进入该车道的起点及各交叉口入口处以前适当位置。', '/uploadFile/paCongIcno1236.jpg');
INSERT INTO `icno` VALUES (147, 8, '非机动车车道', '表示该车道只供非机动车行驶。设在该车道的起点及交叉路口和入口前适当位置，在标志无法正对车道时，可以不标注箭头。', '/uploadFile/paCongIcno1238.jpg');
INSERT INTO `icno` VALUES (148, 8, '非机动车车道', '表示该车道只供非机动车行驶。设在该车道的起点及交叉路口和入口前适当位置，在标志无法正对车道时，可以不标注箭头。', '/uploadFile/paCongIcno1120.jpg');
