INSERT INTO `subject` VALUES (11, '驾驶机动车在道路上违反道路交通安全法的行为，属于什么行为??', '', '1', '1', NULL, '违章行为', '违法行为', '过失行为', '违规行为', 'B', '驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。根据《道路交通安全法》第二条规定：中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都应当遵守本法。 第八十八条 对道路交通安全违法行为的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', 1, 1, NULL, '2022-02-22 11:26:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (12, '机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？', '', '1', '1', NULL, '刑事责任', '民事责任', '经济责任', '直接责任', 'A', '根据《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。', 1, 1, '2021-12-19 00:08:19', '2021-12-19 00:08:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (13, '机动车驾驶人造成事故后逃逸构成犯罪的，吊销驾驶证且多长时间不得重新取得驾驶证？', '', '1', '1', NULL, '5年内', '10年内', '终生', '20年内', 'C', '《道路交通安全法》第一百零一条 违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。 造成交通事故后逃逸且构成犯罪的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。【逃终生】', 1, 1, '2021-12-19 00:08:19', '2021-12-19 00:08:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (14, '驾驶机动车违反道路交通安全法律法规发生交通事故属于交通违章行为。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。 依据《道路交通安全法》第二条规定，中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都应当遵守本法。 第八十八条规定，对道路交通安全违法行为的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', 1, 1, '2021-12-19 00:08:20', '2021-12-19 00:08:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (15, '驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。 依据《道路交通安全法》第八十七条，公安机关交通管理部门及其交通警察应当依据事实和本法的有关规定对道路交通安全违法行为予以处罚。对于情节轻微，未影响道路通行的，指出违法行为，给予口头警告后放行。', 1, 1, '2021-12-19 00:08:20', '2021-12-19 00:08:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (16, '对未取得驾驶证驾驶机动车的，追究其法律责任。', '', '2', '1', NULL, '', '', '', '', 'A', '对未取得驾驶证驾驶机动车的，应追究其法律责任。 【法律依据】《道路交通安全法》第九十九条规定：未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处十五日以下拘留。', 1, 1, '2021-12-19 00:08:20', '2021-12-19 00:08:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (17, '对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第一百零一条规定：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。', 1, 1, '2021-12-19 00:08:21', '2021-12-19 00:08:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (18, '造成交通事故后逃逸且构成犯罪的驾驶人，将吊销驾驶证且终生不得重新取得驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '【假1吊2骗3醉5逃终生】《道路交通安全法》第一百零一条：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', 1, 1, '2021-12-19 00:08:21', '2021-12-19 00:08:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (19, '驾驶机动车在道路上违反交通安全法规的行为属于违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车在道路上违反道路交通安全法的行为属于违法行为。根据《道路交通安全法》第二条规定，中华人民共和国境内的车辆驾驶人、行人、乘车人以及与道路交通活动有关的单位和个人，都应当遵守本法。 第八十八条 对道路交通安全违法行为的处罚种类包括：警告、罚款、暂扣或者吊销机动车驾驶证、拘留。', 1, 1, '2021-12-19 00:08:21', '2021-12-19 00:08:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41, '动画1中有几种违法行为？', '/uploadFile/yuanBeiTu1.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'B', '动画中有2种违法行为：1、未系安全带；2、开车打电话。', 8, 1, '2021-12-19 01:13:25', '2021-12-19 01:13:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (42, '动画2中有几种违法行为？', '/uploadFile/yuanBeiTu2.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'B', '动画中有2种违法行为：1、一边驾驶一边打电话；2、遇有前方车辆停车排队等候或者缓慢行驶时，占用公交车道行驶。', 8, 1, '2021-12-19 01:13:26', '2021-12-19 01:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (43, '动画3中有几种违法行为？', '/uploadFile/yuanBeiTu3.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'B', '动画中有2种违法行为：1、上道路行驶的机动车号牌被遮挡；2、超速行驶，最高限速60公里/小时，速度表指针指示很显然是超过60公里/小时的（看仪表盘上黑点的位置）。', 8, 1, '2021-12-19 01:13:26', '2021-12-19 01:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (44, '动画4中有几种违法行为？', '/uploadFile/yuanBeiTu4.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'C', '动画中机动车有3种违法行为：1、遮挡号牌；2、未按导向箭头行驶，在直行车道左转弯；3、违反交通信号灯的指示通行。', 8, 1, '2021-12-19 01:13:26', '2021-12-19 01:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (45, '动画5中有几种违法行为？', '/uploadFile/yuanBeiTu5.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'C', '动画中有3种违法行为：1、一边开车一边接打电话；2、酒后驾驶；3、无证驾驶。 通过驾驶人的通话内容“刚喝完酒，在去驾校的路上，准备报名学个驾驶证”可知，驾驶人不但酒后驾驶，而且无证驾驶。', 8, 1, '2021-12-19 01:13:27', '2021-12-19 01:13:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (46, '动画6中有几种违法行为？', '/uploadFile/yuanBeiTu6.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'C', '动画中有3种违法行为：1、一边开车一边打电话；2、未系安全带；3、遇有前方车辆停车排队等候或者缓慢行驶时，违法占用应急车道行驶。', 8, 1, '2021-12-19 01:13:27', '2021-12-19 01:13:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (47, '动画7中有几种违法行为？', '/uploadFile/yuanBeiTu7.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'B', '由动画可知，机动车存在以下违法行为：1、遮挡号牌；2、未按照交通标志的指示通行，在设有禁止左转弯标志的路口左转弯行驶。 由于该地段禁止左转弯，因此无论汽车是否打了转向灯，只要左转就已经违法。大家考场上遇到这道题目，应特别注意。 【法律依据】 第十一条　驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。 机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。 第四十四条　机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；', 8, 1, '2021-12-19 01:13:28', '2021-12-19 01:13:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (48, '动画8中有几种违法行为？', '/uploadFile/yuanBeiTu8.gif', '1', '4', NULL, '一种违法行为', '二种违法行为', '三种违法行为', '四种违法行为', 'B', '由动画可知，机动车存在2种违法行为：1、遮挡号牌；2、遇有前方车辆停车排队等候或者缓慢行驶时，违法占用非机动车道行驶。', 8, 1, '2021-12-19 01:13:28', '2021-12-19 01:13:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (49, '林某驾车以110公里/小时的速度在城市道路行驶，与一辆机动车追尾后弃车逃离被群众拦下。经鉴定，事发时林某血液中的酒精浓度为135.8毫克/百毫升。林某的主要违法行为是什么？', '', '3', '4', NULL, '醉酒驾驶', '超速驾驶', '疲劳驾驶', '肇事逃逸', 'ABD', '林某的主要违法行为有： 1、\"以110公里/小时的速度在城市道路行驶\"属于超速行驶； 2、\"与一辆机动车追尾后弃车逃逸\"属于肇事逃逸； 3、\"血液中的酒精浓度为135.8毫克/百毫升\"属于醉酒驾驶。', 8, 1, '2021-12-19 01:13:29', '2021-12-19 01:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (110, '驾驶机动车应当随身携带哪种证件？', '', '1', '1', NULL, '工作证', '驾驶证', '身份证', '职业资格证', 'B', '《道路交通安全法》第十一条　驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。【两证两标一号牌】 第九十五条　上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。', 1, 1, '2021-12-19 00:08:21', '2021-12-19 00:08:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (111, '未取得驾驶证的学员在道路上学习驾驶技能，下列哪种做法是正确的？', '', '1', '1', NULL, '使用所学车型的教练车由教练员随车指导', '使用所学车型的教练车单独驾驶学习', '使用私家车由教练员随车指导', '使用所学车型的教练车由非教练员的驾驶人随车指导', 'A', '《道路交通安全法实施条例》第二十条，学习机动车驾驶，应当先学习道路交通安全法律、法规和相关知识，考试合格后，再学习机动车驾驶技能。 在道路上学习驾驶，应当按照公安机关交通管理部门指定的路线、时间进行。在道路上学习机动车驾驶技能应当使用教练车，在教练员随车指导下进行，与教学无关的人员不得乘坐教练车。学员在学习驾驶中有道路交通安全违法行为或者造成交通事故的，由教练员承担责任。', 1, 1, '2021-12-19 00:08:22', '2021-12-19 00:08:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (112, '机动车驾驶人初次申领驾驶证后的实习期是多长时间？', '', '1', '1', NULL, '6个月', '12个月', '16个月', '18个月', 'B', '《机动车驾驶证申领和使用规定》第七十四条，机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 1, 1, '2021-12-19 00:08:22', '2021-12-19 00:08:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (113, '在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂哪种标志？', '', '1', '1', NULL, '注意新手标志', '注意避让标志', '统一式样的实习标志', '注意车距标志', 'C', '《机动车驾驶证申领和使用规定》第七十四条机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂统一式样的实习标志。', 1, 1, '2021-12-19 00:08:22', '2021-12-19 00:08:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (114, '以欺骗、贿赂等不正当手段取得驾驶证被依法撤销驾驶许可的，多长时间不得重新申请驾驶许可？', '', '1', '1', NULL, '3年内', '终身', '1年内', '5年内', 'A', '《机动车驾驶证申领和使用规定》第八十八条，申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，申请人在三年内不得再次申领机动车驾驶证。【骗3】', 1, 1, '2021-12-19 00:08:22', '2021-12-19 00:08:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (115, '驾驶人要按照驾驶证载明的准驾车型驾驶车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第十九条　驾驶机动车，应当依法取得机动车驾驶证。驾驶人应当按照驾驶证载明的准驾车型驾驶机动车；驾驶机动车时，应当随身携带机动车驾驶证。', 1, 1, '2021-12-19 00:08:23', '2021-12-19 00:08:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (116, '上路行驶的机动车未随车携带身份证的，交通警察可依法扣留机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十五条规定：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车。 不携带身份证的话，是不会被扣留车辆的。', 1, 1, '2021-12-19 00:08:23', '2021-12-19 00:08:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (117, '上路行驶的机动车未放置检验合格标志的，交通警察可依法扣留机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。', 1, 1, '2021-12-19 00:08:23', '2021-12-19 00:08:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (118, '伪造、变造机动车驾驶证构成犯罪的将被依法追究刑事责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 1, 1, '2021-12-19 00:08:24', '2021-12-19 00:08:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (119, '机动车驾驶人在实习期内驾驶机动车不得牵引挂车。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第六十五条规定：机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 1, 1, '2021-12-19 00:08:24', '2021-12-19 00:08:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (120, '驾驶拼装机动车上路行驶的驾驶人，除按规定接受罚款外，还要受到哪种处理？', '', '1', '1', NULL, '处10日以下拘留', '暂扣驾驶证', '吊销驾驶证', '追究刑事责任', 'C', '根据《道路交通安全法》的规定，对驾驶拼装的机动车或已达到报废标准的机动车上路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证，对车辆予以收缴，强制报废。', 1, 1, '2021-12-19 00:08:24', '2021-12-19 00:08:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (121, '驾驶报废机动车上路行驶的驾驶人，除按规定罚款外，还要受到哪种处理？', '', '1', '1', NULL, '撤销驾驶许可', '收缴驾驶证', '强制恢复车况', '吊销驾驶证', 'D', '根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 1, 1, '2021-12-19 00:08:24', '2021-12-19 00:08:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (122, '对驾驶已达到报废标准的机动车上路行驶的驾驶人，会受到下列哪种处罚？', '', '1', '1', NULL, '处15日以下拘留', '吊销机动车驾驶证', '处20以上200元以下罚款', '追究刑事责任', 'B', '根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 1, 1, '2021-12-19 00:08:25', '2021-12-19 00:08:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (123, '对驾驶拼装机动车上路行驶的驾驶人，会受到下列哪种处罚？', '', '1', '1', NULL, '处15日以下拘留', '依法追究刑事责任', '处200以上2000元以下罚款', '吊销机动车行驶证', 'C', '根据《道路交通安全法》第一百条规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。请注意：吊销驾驶证，不是吊销行驶证。', 1, 1, '2021-12-19 00:08:25', '2021-12-19 00:08:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (124, '驾驶机动车上路前应当检查车辆安全技术性能。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十一条规定：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', 1, 1, '2021-12-19 00:08:25', '2021-12-19 00:08:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (125, '不得驾驶具有安全隐患的机动车上道路行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十一条：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', 1, 1, '2021-12-19 00:08:25', '2021-12-19 00:08:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (126, '拼装的机动车只要认为安全就可以上路行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第一百条规定：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 1, 1, '2021-12-19 00:08:26', '2021-12-19 00:08:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (127, '已经达到报废标准的机动车经大修后可以上路行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第十四条　国家实行机动车强制报废制度，根据机动车的安全技术状况和不同用途，规定不同的报废标准。应当报废的机动车必须及时办理注销登记。达到报废标准的机动车不得上道路行驶。因此，题干表述错误。', 1, 1, '2021-12-19 00:08:26', '2021-12-19 00:08:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (128, '下列哪种证件是驾驶机动车上路行驶应当随车携带？', '', '1', '1', NULL, '机动车登记证', '机动车保险单', '机动车行驶证', '出厂合格证明', 'C', '上道路行驶的机动车应当随车携带：两证、两标、一号牌。根据《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。', 1, 1, '2021-12-19 00:08:26', '2021-12-19 00:08:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (129, '驾驶这种机动车上路行驶属于什么行为？', '/uploadFile/yuanBeiTu29.gif', '1', '1', NULL, '违规行为', '违章行为', '违法行为', '犯罪行为', 'C', '《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。 如图所示机动车，故意遮挡号牌，违反了《道路交通安全法》，属于违法行为。', 1, 1, '2021-12-19 00:08:27', '2021-12-19 00:08:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (130, '下列哪种标志是驾驶机动车上路行驶应当在车上放置的标志？', '', '1', '1', NULL, '产品合格标志', '保持车距标志', '提醒危险标志', '检验合格标志', 'D', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。', 1, 1, '2021-12-19 00:08:27', '2021-12-19 00:08:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (131, '驾驶人在下列哪种情况下不能驾驶机动车？', '', '1', '1', NULL, '饮酒后', '喝茶后', '喝咖啡后', '喝牛奶后', 'A', '《道路交通安全法》第二十二条　机动车驾驶人应当遵守道路交通安全法律、法规的规定，按照操作规范安全驾驶、文明驾驶。 饮酒、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:27', '2021-12-19 00:08:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (132, '这辆在道路上行驶的机动车有下列哪种违法行为？', '/uploadFile/yuanBeiTu32.gif', '1', '1', NULL, '逆向行驶', '未按规定悬挂号牌', '故意遮挡号牌', '占用非机动车道', 'C', '如图所示，机动车号牌被遮挡。根据《道路交通安全法》第十一条规定：机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。', 1, 1, '2021-12-19 00:08:28', '2021-12-19 00:08:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (133, '驾驶机动车上路行驶应当按规定悬挂号牌。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。【两证两标一号牌】', 1, 1, '2021-12-19 00:08:28', '2021-12-19 00:08:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (134, '驾驶这种机动车上路行驶没有违法行为。', '/uploadFile/yuanBeiTu34.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，机动车号牌被遮挡，属于违法行为。根据《道路交通安全违法行为记分分值》规定，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。', 1, 1, '2021-12-19 00:08:28', '2021-12-19 00:08:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (135, '服用国家管制的精神药品可以短途驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第二十二条规定：饮酒、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。本题题干表述有误，因此本题答案应为错误。', 1, 1, '2021-12-19 00:08:29', '2021-12-19 00:08:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (136, '饮酒后只要不影响驾驶操作可以短距离驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第二十二条规定：饮酒、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:29', '2021-12-19 00:08:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (137, '公安交通管理部门对驾驶人的交通违法行为除依法给予行政处罚外，实行下列哪种制度？', '', '1', '1', NULL, '违法登记制度', '奖励里程制度', '累积记分制度', '强制报废制度', 'C', '《道路交通安全法》第二十四条：公安机关交通管理部门对机动车驾驶人违反道路交通安全法律、法规的行为，除依法给予行政处罚外，实行累积记分制度。', 1, 1, '2021-12-19 00:08:29', '2021-12-19 00:08:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (138, '道路交通安全违法行为累积记分的周期是多长时间？', '', '1', '1', NULL, '3个月', '6个月', '12个月', '24个月', 'C', '《道路交通安全法实施条例》第二十三条 公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为除给予行政处罚外，实行道路交通安全违法行为累积记分(以下简称记分)制度，记分周期为12个月。', 1, 1, '2021-12-19 00:08:30', '2021-12-19 00:08:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (139, '公安机关交通管理部门对累积记分达到规定分值的驾驶人怎样处理？', '', '1', '1', NULL, '依法追究刑事责任', '处15日以下拘留', '终生禁驾', '进行法律法规教育，重新考试', 'D', '《道路交通安全法实施条例》第二十三条 对在一个记分周期内记分达到12分的，由公安机关交通管理部门扣留其机动车驾驶证，该机动车驾驶人应当按照规定参加道路交通安全法律、法规的学习并接受考试。考试合格的，记分予以清除，发还机动车驾驶证；考试不合格的，继续参加学习和考试。', 1, 1, '2021-12-19 00:08:30', '2021-12-19 00:08:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (140, '驾驶人出现下列哪种情况，不得驾驶机动车？', '', '1', '1', NULL, '驾驶证丢失、损毁', '驾驶证接近有效期', '记分达到10分', '记分达到6分', 'A', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:30', '2021-12-19 00:08:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (141, '驾驶人在驾驶证丢失后3个月内还可以驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:30', '2021-12-19 00:08:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (142, '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:31', '2021-12-19 00:08:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (143, '驾驶人的驾驶证损毁后不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:31', '2021-12-19 00:08:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (144, '记分满12分的驾驶人拒不参加学习和考试的将被公告驾驶证停止使用。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十五条：机动车驾驶人记分达到12分，拒不参加公安机关交通管理部门通知的学习，也不接受考试的，由公安机关交通管理部门公告其机动车驾驶证停止使用。', 1, 1, '2021-12-19 00:08:31', '2021-12-19 00:08:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (145, '驾驶人的机动车驾驶证被依法扣留、暂扣的情况下不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十八条规定 机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 1, 1, '2021-12-19 00:08:32', '2021-12-19 00:08:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (146, '前方路口这种信号灯亮表示什么意思？', '/uploadFile/yuanBeiTu46.gif', '1', '1', NULL, '路口警示', '禁止通行', '准许通行', '提醒注意', 'B', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 1, 1, '2021-12-19 00:08:32', '2021-12-19 00:08:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (147, '前方路口这种信号灯亮表示什么意思？', '/uploadFile/yuanBeiTu47.gif', '1', '1', NULL, '路口警示', '加速直行', '加速左转', '禁止右转', 'A', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 1, 1, '2021-12-19 00:08:32', '2021-12-19 00:08:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (148, '前方路口这种信号灯亮表示什么意思？', '/uploadFile/yuanBeiTu48.gif', '1', '1', NULL, '路口警示', '禁止通行', '准许通行', '提醒注意', 'C', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 1, 1, '2021-12-19 00:08:33', '2021-12-19 00:08:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (149, '交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十五条，全国实行统一的道路交通信号。交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', 1, 1, '2021-12-19 00:08:33', '2021-12-19 00:08:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (150, '交通标志和交通标线不属于交通信号。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第二十五条，全国实行统一的道路交通信号。交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', 1, 1, '2021-12-19 00:08:33', '2021-12-19 00:08:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (151, '交通信号灯由红灯、绿灯和黄灯组成。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十六条　交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 1, 1, '2021-12-19 00:08:33', '2021-12-19 00:08:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (152, '道路交通标线分为指示标线、警告标线、禁止标线。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第三十条 交通标志分为：指示标志、警告标志、禁令标志、指路标志、旅游区标志、道路施工安全标志和辅助标志。 道路交通标线分为：指示标线、警告标线、禁止标线。', 1, 1, '2021-12-19 00:08:34', '2021-12-19 00:08:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (153, '驾驶机动车在这种道路上如何通行？', '/uploadFile/yuanBeiTu53.gif', '1', '1', NULL, '在道路两边通行', '在道路中间通行', '实行分道通行', '可随意通行', 'B', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。', 1, 1, '2021-12-19 00:08:34', '2021-12-19 00:08:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (154, '道路上划设这种标线的车道内允许下列哪类车辆通行？', '/uploadFile/yuanBeiTu54.gif', '1', '1', NULL, '出租车', '公务用车', '公交车', '私家车', 'C', '如图所示，路面由黄色虚线和“公交专用”文字组成的车道，表示除公交车外，其他车辆及行人不得进入该车道。', 1, 1, '2021-12-19 00:08:34', '2021-12-19 00:08:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (155, '驾驶机动车在路口遇到这种情况如何行驶？', '/uploadFile/yuanBeiTu55.gif', '1', '1', NULL, '可以向右转弯', '靠右侧直行', '遵守交通信号灯', '停车等待', 'D', '', 1, 1, '2021-12-19 00:08:35', '2021-12-19 00:08:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (156, '有这种信号灯的路口允许机动车如何行驶？', '/uploadFile/yuanBeiTu56.gif', '1', '1', NULL, '向左转弯', '直行通过', '向右转弯', '停车等待', 'C', '方向指示信号灯，箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示箭头所指方向车辆起黄灯作用。 如图所示，准许右转弯信号灯，表示允许机动车向右转弯行驶。', 1, 1, '2021-12-19 00:08:35', '2021-12-19 00:08:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (157, '遇到这种情况的路口怎样通过？', '/uploadFile/yuanBeiTu57.gif', '1', '1', NULL, '左转弯加速通过', '加速直行通过', '右转弯加速通过', '确认安全后通过', 'D', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。', 1, 1, '2021-12-19 00:08:35', '2021-12-19 00:08:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (158, '这段道路红车所在车道是什么车道？', '/uploadFile/yuanBeiTu58.gif', '1', '1', NULL, '快速车道', '慢速车道', '专用车道', '应急车道', 'A', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。 如图所示，红色车在最左侧，因此所在的车道是快速车道。', 1, 1, '2021-12-19 00:08:35', '2021-12-19 00:08:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (159, '机动车在道路上变更车道需要注意什么？', '', '1', '1', NULL, '尽快加速进入左侧车道', '不能影响其他车辆正常行驶', '进入左侧车道时适当减速', '开启转向灯迅速向左转向', 'B', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶。 拓展知识： 【变更车道操作要求】变更车道有一定的风险，一定要在确保安全的前提下谨慎变更。需要变更时，提前3秒打开转向灯或利用手势示意，提醒后方来车注意。通过内、外后视镜观察，并向变更车道方向回头观察后方道路交通情况，判断车辆安全距离，控制行驶速度，确认不妨碍本车道后方车辆和即将驶入车道内的其他车辆正常行驶时，安全变更车道，变更车道完毕后关闭转向灯。', 1, 1, '2021-12-19 00:08:36', '2021-12-19 00:08:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (160, '在这种情况下可以借右侧公交车道超车。', '/uploadFile/yuanBeiTu60.gif', '2', '1', NULL, '', '', '', '', 'B', '道路上划设专用车道的，在专用车道内只准许规定的车辆通行，其他车辆不得进入专用车道内行驶。', 1, 1, '2021-12-19 00:08:36', '2021-12-19 00:08:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (161, '在路口遇有交通信号灯和交通警察指挥不一致时，按照交通信号灯通行。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；遇有交通警察现场指挥时，应当按照交通警察的指挥通行；在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。因此，题干表述错误。', 1, 1, '2021-12-19 00:08:36', '2021-12-19 00:08:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (162, '在这种情况下可加速通过交叉路口。', '/uploadFile/yuanBeiTu62.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全实施条例》第三十八条 机动车信号灯和非机动车信号灯表示：黄灯亮时，已越过停止线的车辆可以继续通行；没有越过停止线的车辆需停车等待，不得进入路口，更不得加速抢行通过交叉路口。', 1, 1, '2021-12-19 00:08:37', '2021-12-19 00:08:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (163, '在路口这个位置时可以加速通过路口。', '/uploadFile/yuanBeiTu63.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全实施条例》第三十八条 机动车信号灯和非机动车信号灯表示：红灯亮时，禁止车辆通行，车辆不得越过停止线；右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 1, 1, '2021-12-19 00:08:37', '2021-12-19 00:08:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (164, '驾驶机动车不能进入红色叉形灯或者红色箭头灯亮的车道。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十条 车道信号灯表示：(一)绿色箭头灯亮时，准许本车道车辆按指示方向通行；(二)红色叉形灯或者箭头灯亮时，禁止本车道车辆通行。', 1, 1, '2021-12-19 00:08:37', '2021-12-19 00:08:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (165, '在这段路的最高时速为每小时50公里。', '/uploadFile/yuanBeiTu65.gif', '2', '1', NULL, '', '', '', '', 'B', '【红高蓝低黄建议】白底红圈表示最高限速，蓝色牌子表示最低限速。因此，在这段路的最高限速为80公里/小时，最低限速50公里/小时。', 1, 1, '2021-12-19 00:08:38', '2021-12-19 00:08:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (166, '这辆红色轿车变更车道的方法和路线是正确的。', '/uploadFile/yuanBeiTu66.gif', '2', '1', NULL, '', '', '', '', 'B', '变更车道时，应提前打开转向灯或利用手势示意，提醒后方来车注意，合理判断车辆安全距离，控制行驶速度，确认不妨碍本车道后方车辆和即将驶入车道内的其他车辆正常行驶时，安全变更车道。 如图所示，红车强行变道，方法和路线都是错误的。既没有开启转向灯，也没有合理判断车辆安全距离，妨碍了车道内其他车辆的正常行驶。', 1, 1, '2021-12-19 00:08:38', '2021-12-19 00:08:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (167, '驾驶机动车在道路上向左变更车道时如何使用灯光？', '', '1', '1', NULL, '不用开启转向灯', '提前开启右转向灯', '提前开启左转向灯', '提前开启近光灯', 'C', '驾驶机动车在道路上向左变更车道时，应提前开启左转向灯。根据《道路交通安全法实施条例》第五十七条规定：机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 1, 1, '2021-12-19 00:08:38', '2021-12-19 00:08:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (168, '驾驶机动车在道路上靠路边停车过程中如何使用灯光？', '', '1', '1', NULL, '变换使用远近光灯', '不用指示灯提示', '开启危险报警闪光灯', '提前开启右转向灯', 'D', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 1, 1, '2021-12-19 00:08:38', '2021-12-19 00:08:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (169, '在这种天气条件下行车如何使用灯光？', '/uploadFile/yuanBeiTu69.gif', '1', '1', NULL, '使用近光灯', '不使用灯光', '使用远光灯', '使用雾灯', 'A', '驾驶机动车在雨、雪、沙尘、冰雹等低能见度情况下行驶时，开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，使用近光灯，不得使用远光灯。 【拓展知识】 1，跟车行驶应开启近光灯，帮助驾驶员瞭望前方情况，使驾驶员对前方情况看得清楚一些；2，开启示廓灯和前后位灯，示意自己车辆的存在，使前后行驶的车辆，能够看到自己的具体位置及通行情况，方便他们采取相应的措施。', 1, 1, '2021-12-19 00:08:39', '2021-12-19 00:08:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (170, '在这种天气条件下行车如何使用灯光？', '/uploadFile/yuanBeiTu70.gif', '1', '1', NULL, '使用远光灯', '使用雾灯', '开启右转向灯', '不使用灯光', 'B', '《道路交通安全法实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。如图所示，机动车雾天行驶开启雾灯。', 1, 1, '2021-12-19 00:08:39', '2021-12-19 00:08:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (171, '在这种雨天跟车行驶使用灯光，以下做法正确的是？', '/uploadFile/yuanBeiTu71.gif', '1', '1', NULL, '使用远光灯', '不能使用近光灯', '不能使用远光灯', '使用雾灯', 'C', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。 记住，只要你的车前方有车，不管是相向还是同向，都不能使用远光灯。', 1, 1, '2021-12-19 00:08:39', '2021-12-19 00:08:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (172, '在这种环境下通过路口如何使用灯光？', '/uploadFile/yuanBeiTu72.gif', '1', '1', NULL, '关闭远光灯', '使用危险报警闪光灯', '使用远光灯', '交替使用远近光灯', 'D', '如图所示，夜间通过人行横道时应使用近光灯。根据《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 1, 1, '2021-12-19 00:08:40', '2021-12-19 00:08:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (173, '在这种路段如何行驶？', '/uploadFile/yuanBeiTu73.gif', '1', '1', NULL, '减速鸣喇叭示意', '加速鸣喇叭通过', '在弯道中心转弯', '占对方道路转弯', 'A', '如图所示，右侧是向左急转弯警告标志，表示在急转弯路段。根据《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。', 1, 1, '2021-12-19 00:08:40', '2021-12-19 00:08:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (174, '驾驶机动车在道路上超车时可以不使用转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。 超车必须使用转向灯，否则极易造成严重的交通事故。题目描述有误，本题答案应为错误。', 1, 1, '2021-12-19 00:08:40', '2021-12-19 00:08:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (175, '驾驶机动车在道路上超车完毕驶回原车道时需提前开启右转向灯。', '', '2', '1', NULL, '', '', '', '', 'A', '题干表述正确。根据《道路交通安全法实施条例》第四十七条：机动车超车时，应当：（一）提前开启左转向灯、变换使用远、近光灯或者鸣喇叭；（二）在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。', 1, 1, '2021-12-19 00:08:41', '2021-12-19 00:08:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (176, '驾驶机动车在道路上掉头时提前开启左转向灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： （一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯。 （二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。 因此，本题答案应为正确。', 1, 1, '2021-12-19 00:08:41', '2021-12-19 00:08:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (177, '驾驶机动车在道路上向右变更车道可以不使用转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '为保证安全，驾驶人在变更车道时需要提前打开转向灯示意约3秒以上，确认安全后完成变更车道，随后关闭转向灯。因此，题干表述错误。 根据《道路交通安全法实施条例》第五十七条，机动车应当按照下列规定使用转向灯： （二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 1, 1, '2021-12-19 00:08:41', '2021-12-19 00:08:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (178, '驾驶机动车在沙尘天气条件下行车不用开启前照灯、示廓灯和后位灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 1, 1, '2021-12-19 00:08:42', '2021-12-19 00:08:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (179, '在这种环境里行车使用近光灯。', '/uploadFile/yuanBeiTu79.gif', '2', '1', NULL, '', '', '', '', 'A', '夜间通过人行横道或者没有交通信号灯控制的路口，遇行人通行时，应使用近光灯，同时注意减速慢行，防止其突然横穿。', 1, 1, '2021-12-19 00:08:42', '2021-12-19 00:08:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (180, '驾驶机动车在雾天行车开启雾灯和危险报警闪光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十八条 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 1, 1, '2021-12-19 00:08:42', '2021-12-19 00:08:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (181, '在这种急弯道路上行车应交替使用远近光灯。', '/uploadFile/yuanBeiTu81.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。因此，题干表述正确。', 1, 1, '2021-12-19 00:08:43', '2021-12-19 00:08:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (182, '夜间驾驶机动车通过人行横道时需要交替使用远近光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。因此，题干表述正确。', 1, 1, '2021-12-19 00:08:43', '2021-12-19 00:08:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (183, '驾驶机动车上坡时，在将要到达坡道顶端时要加速并鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。 冲坡不冲顶，题目所说的“到达坡道顶端时要加速”描述错误，本题答案应为错误。', 1, 1, '2021-12-19 00:08:43', '2021-12-19 00:08:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (184, '下列哪种行为会受到200元以上2000元以下罚款，并处吊销机动车驾驶证？', '', '1', '1', NULL, '违反道路通行规定', '超过规定时速50%', '造成交通事故后逃逸', '驾车没带驾驶证', 'B', '《道路交通安全法》第九十九条　有下列行为之一的，由公安机关交通管理部门处二百元以上二千元以下罚款： （一）未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的； （二）将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； （三）造成交通事故后逃逸，尚不构成犯罪的； （四）机动车行驶超过规定时速百分之五十的；（超过规定时速50%） （五）强迫机动车驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车，造成交通事故，尚不构成犯罪的； 。。。 行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证；有第一项、第三项、第五项至第八项情形之一的，可以并处十五日以下拘留。', 1, 1, '2021-12-19 00:08:44', '2021-12-19 00:08:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (185, '在这条高速公路上行驶时的最高速度不能超过多少？', '/uploadFile/yuanBeiTu85.gif', '1', '1', NULL, '100公里/小时', '110公里/小时', '120公里/小时', '90公里/小时', 'B', '如图所示，前方限速标志数值110，表示在这条高速公路上行驶时的最高速度不能超过110公里/小时。 根据《道路交通安全法实施条例》第七十八条规定，道路限速标志标明的车速与车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', 1, 1, '2021-12-19 00:08:44', '2021-12-19 00:08:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (186, '在这段城市道路上行驶的最高速度不能超过多少？', '/uploadFile/yuanBeiTu86.gif', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'A', '根据《道路交通安全法实施条例》第四十五条规定 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 1, '2021-12-19 00:08:45', '2021-12-19 00:08:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (187, '在这条公路上行驶的最高速度不能超过多少？', '/uploadFile/yuanBeiTu87.gif', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'B', '《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。 图中道路，是没有道路中心线的公路，因此最高速度应不超过40公里/小时，本题答案应为B。', 1, 1, '2021-12-19 00:08:45', '2021-12-19 00:08:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (188, '在这条城市道路上行驶的最高速度不能超过多少？', '/uploadFile/yuanBeiTu88.gif', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'C', '如图所示，同方向只有1条机动车道的城市道路，最高速度不能超过50公里/小时。 根据《道路交通安全法》第四十五条规定，在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度：(二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 1, '2021-12-19 00:08:46', '2021-12-19 00:08:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (189, '在这条公路上行驶的最高速度不能超过多少？', '/uploadFile/yuanBeiTu89.gif', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'D', '《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。 这道题看周围环境，路两边全是树木所以是公路。', 1, 1, '2021-12-19 00:08:46', '2021-12-19 00:08:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (190, '在这个弯道上行驶时的最高速度不能超过多少？', '/uploadFile/yuanBeiTu90.gif', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '70公里/小时', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:46', '2021-12-19 00:08:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (191, '驾驶机动车遇到沙尘、冰雹、雨、雾、结冰等气象条件如何行驶？', '', '1', '1', NULL, '按平常速度行驶', '保持匀速行驶', '降低行驶速度', '适当提高车速', 'C', '根据《道路交通安全法》 第四十二条：机动车上道路行驶，不得超过限速标志标明的最高时速。在没有限速标志的路段，应当保持安全车速。　 夜间行驶或者在容易发生危险的路段行驶，以及遇有沙尘、冰雹、雨、雪、雾、结冰等气象条件时，应当降低行驶速度。', 1, 1, '2021-12-19 00:08:47', '2021-12-19 00:08:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (192, '驾驶机动车遇雾、雨、雪等能见度在50米以内时，最高速度不能超过多少？', '', '1', '1', NULL, '70公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', 'D', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时； （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:47', '2021-12-19 00:08:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (193, '驾驶机动车在进出非机动车道时，最高速度不能超过多少？', '', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '60公里/小时', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:47', '2021-12-19 00:08:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (194, '驾驶机动车通过铁路道口时，最高速度不能超过多少？', '', '1', '1', NULL, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', 'C', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:47', '2021-12-19 00:08:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (195, '驾驶机动车通过急弯路时，最高速度不能超过多少？', '', '1', '1', NULL, '20公里/小时', '30公里/小时', '40公里/小时', '50公里/小时', 'B', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:48', '2021-12-19 00:08:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (196, '驾驶机动车通过窄路、窄桥时，最高速度不能超过多少？', '', '1', '1', NULL, '60公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', 'D', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:48', '2021-12-19 00:08:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (197, '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？', '', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '60公里/小时', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:48', '2021-12-19 00:08:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (198, '驾驶机动车在冰雪道路行驶时，最高速度不能超过多少？', '', '1', '1', NULL, '20公里/小时', '30公里/小时', '40公里/小时', '50公里/小时', 'B', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:48', '2021-12-19 00:08:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (199, '驾驶机动车在泥泞道路行驶时，最高速度不能超过多少？', '', '1', '1', NULL, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', 'C', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时；', 1, 1, '2021-12-19 00:08:49', '2021-12-19 00:08:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (410, '周某夜间驾驶大货车在没有路灯的城市道路上以90公里/小时的速度行驶，一直开启远光灯，在通过一窄路时，因加速抢道，导致对面驶来的一辆小客车撞上右侧护栏。周某的主要违法行为是什么？', '', '3', '4', NULL, '超速行驶', '不按规定会车', '疲劳驾驶', '不按规定使用灯光', 'ABD', '周某的主要违法行为有： 1、城市道路上以90公里/小时的速度行驶，属于超速行驶； 2、一直开启远光灯，属于不按规定使用灯光； 3、加速抢道，属于不按规定会车。', 8, 1, '2021-12-19 01:13:29', '2021-12-19 01:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (411, '某日早上6时，冉某驾驶一辆大客车出发，连续行驶至上午11时，在宣汉县境内宣南路1公里处，坠于公路一侧垂直高度8.5米的陡坎下，造成13人死亡、9人受伤。冉某的主要违法行为是什么？', '', '1', '4', NULL, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', 'D', '根据规定：连续开车4小时要停车休息20分钟。本题中冉某已连续开车5个小时了，属于疲劳驾驶。', 8, 1, '2021-12-19 01:13:29', '2021-12-19 01:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (412, '某日13时10分，罗某驾驶一辆中型客车从高速公路0公里处出发，下午14时10分行至该高速公路125公里加200米处时，发生追尾碰撞，机动车驶出西南侧路外边坡，造成11人死亡、2人受伤。罗某的主要违法行为是什么？', '', '1', '4', NULL, '超速行驶', '不按交通标线行驶', '客车超员', '疲劳驾驶', 'A', '《 道路交通安全法》规定，高速公路行驶的中型载客汽车最高速度不得超过100公里/小时。罗某（13:10-14:10）一个小时的驾驶路程是125公里加200米，时速超过100公里/小时，因此，罗某超速行驶。', 8, 1, '2021-12-19 01:13:30', '2021-12-19 01:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (413, '何某驾驶一辆乘载53人的大客车（核载47人），行至宁合高速公路南京境内454公里加100米处，被一辆重型半挂牵引车追尾，导致大客车翻出路侧护栏并起火燃烧，造成17人死亡、27人受伤。何某的主要违法行为是什么？', '', '1', '4', NULL, '超速行驶', '客车超员', '驾驶逾期未年检机动车', '操作不当', 'B', '乘载，是实际载客人数；核载，是规定的载客人数。 （乘载53人）-（核载47人）=（超载6人） 属于客车超员。', 8, 1, '2021-12-19 01:13:30', '2021-12-19 01:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (414, '罗某驾驶大型卧铺客车（乘载44人，核载44人）行至沿河县境内540县道58公里加500米处时，在结冰路面以每小时44公里速度行驶，导致机动车侧滑翻下公路，造成15人死亡、27人受伤。罗某的主要违法行为是什么？', '', '1', '4', NULL, '客车超员', '超速行驶', '疲劳驾驶', '操作不当', 'B', '罗某的主要违法行为有：在结冰路面以每小时44公里的速度行驶，属于超速行驶。', 8, 1, '2021-12-19 01:13:30', '2021-12-19 01:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (415, '徐某驾驶一辆中型客车（乘载27人）行至汤山镇王伏村壶南头路段，在上坡过程中，机动车发生后溜驶出路外坠入落差约80米的山崖，造成11人死亡、7人受伤。徐某的主要违法行为是什么？', '', '1', '4', NULL, '疲劳驾驶', '酒后驾驶', '客车超员', '超速行驶', 'C', '车长小于6米，乘坐人数大于9人且小于20人的汽车称为中型载客汽车。因此，中型客车最多只能乘载19人，题干中徐某驾驶中型客车乘载27人，故徐某的主要违法行为是客车超员。', 8, 1, '2021-12-19 01:13:30', '2021-12-19 01:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (416, '佟某驾驶一辆大客车（乘载54人，核载55人)行至太原境内以45公里的时速通过一处泥泞路段时，机动车侧滑驶出路外坠入深沟，导致14人死亡、40人受伤。佟某的主要违法行为是什么？', '', '1', '4', NULL, '客车超员', '超速行驶', '酒后驾驶', '疲劳驾驶', 'B', '按照规定，泥泞路面最高速度不能超过30公里/小时，题中以45公里的时速通过一处泥泞路段，是超速行驶。', 8, 1, '2021-12-19 01:13:31', '2021-12-19 01:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (417, '郝某驾驶一辆载有84.84吨货物的重型自卸货车（核载15.58吨），行至滦县境内262省道34公里加623米处，与前方同向行驶的一辆载有45.85吨货物的货车（核载1.71吨）追尾碰撞后，侧翻撞向路边人群，造成19人死亡、17人受伤。双方驾驶人共同的违法行为是什么？', '', '1', '4', NULL, '超速行驶', '货车超载', '疲劳驾驶', '酒后驾驶', 'B', '双方驾驶人共同的违法行为是： 郝某：（实载84.84吨）-（核载15.58吨）=（超载69.26吨） 同向货车：（实载45.85吨）-（核载1.71吨）=（超载44.14吨） 都属于货车超载', 8, 1, '2021-12-19 01:13:31', '2021-12-19 01:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (418, '周某驾驶一辆轻型厢式货车（搭载22人）行驶至丙察公路79公里加150米处时，坠入道路一侧山崖，造成12人死亡、10人受伤。周某的主要违法行为是什么？', '', '1', '4', NULL, '驾驶逾期未检验的机动车', '货运机动车载客', '超速行驶', '疲劳驾驶', 'B', '货车只能用来装载货物，搭载22人违法，属于货运机动车载客。', 8, 1, '2021-12-19 01:13:31', '2021-12-19 01:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (419, '赵某（持有A2驾驶证）驾驶大型卧铺客车，行驶至叶城县境内219国道226公里加215米处转弯路段时，坠入道路一侧山沟，致16人死亡，26人受伤。赵某的主要违法行为是什么？', '', '1', '4', NULL, '客车超员', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', 'C', '赵某的主要违法行为有： A2驾驶证，驾驶大型卧铺客车，属于驾驶与准驾车型不符的机动车。', 8, 1, '2021-12-19 01:13:31', '2021-12-19 01:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (420, '叶某驾驶中型厢式货车，行至桃园镇睢桃线3公里加600米弯道路段时，以40公里/小时的速度与王某驾驶的乘载19人正三轮载货摩托车发生正面相撞，造成10人死亡、9人受伤。双方驾驶人的主要违法行为是什么？', '', '3', '4', NULL, '叶某驾驶与准驾车型不符的机动车', '王某驾驶摩托车非法载客', '叶某超速行驶', '王某不按信号灯指示行驶', 'BC', '双方驾驶人的主要违法行为是： 1、弯道限速30公里/小时，叶某以40公里/小时的速度行驶属于超速行驶； 2、王某用载货摩托车载客属于非法载客。', 8, 1, '2021-12-19 01:13:32', '2021-12-19 01:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (421, '某日19时，杨某驾驶大客车，乘载57人（核载55人），连续行驶至次日凌晨1时，在江宁区境内104国道3008公里加110米处，因机动车左前胎爆裂，造成12人死亡、22人受伤的特大交通事故。杨某的主要违法行为是什么？', '', '3', '4', NULL, '疲劳驾驶', '客车超员', '超速行驶', '操作不当', 'AB', '杨某的主要违法行为有： 1、连续驾驶6小时属于疲劳驾驶； 2、大客车核载55人，实际乘载57人，属于客车超员。', 8, 1, '2021-12-19 01:13:32', '2021-12-19 01:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (422, '唐某驾驶一辆大客车，乘载74人（核载30人），以每小时38公里的速度，行至一连续下陡坡转弯路段时，机动车翻入路侧溪水内，造成17人死亡、57人受伤。唐某的主要违法行为是什么？', '', '3', '4', NULL, '酒后驾驶', '客车超员', '疲劳驾驶', '超速行驶', 'BD', '唐某的主要违法行为有： 1、（乘载74人）-（核载30人）=（超载44人），属于客车超员； 2、弯道限速30公里/每小时，唐某以每小时38公里的速度行驶，属于超速行驶。', 8, 1, '2021-12-19 01:13:32', '2021-12-19 01:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (423, '李某驾驶一辆大客车，乘载21人（核载35人），行驶途中察觉制动装置有异常但未处理，行至双岛海湾大桥时时速为50公里（该路段限速40公里），因制动失灵坠入海中，造成13人死亡、8人受伤。李某的主要违法行为是什么？', '', '3', '4', NULL, '超速行驶', '疲劳驾驶', '客车超员', '驾驶具有安全隐患的机动车', 'AD', '李某的主要违法行为有： 1、制动装置有异常，属于驾驶具有安全隐患的机动车； 2、行驶至双岛海湾大桥时时速为50公里（该路段限速40公里），属于超速行驶。', 8, 1, '2021-12-19 01:13:33', '2021-12-19 01:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (424, '吴某驾驶一辆大客车，乘载33人（核载22人），行至163县道7公里加300米处时，机动车失控坠入伏牛山山沟，造成10人死亡、21人受伤。事后经酒精检测，吴某血液酒精含量为26毫克/百毫升。吴某的主要违法行为是什么？', '', '3', '4', NULL, '超速行驶', '客车超员', '疲劳驾驶', '酒后驾驶', 'BD', '吴某的主要违法行为有： 1、血液酒精含量26毫克/百毫升属于酒后驾驶。根据国家质量监督检验检疫局发布的《车辆驾驶醉酒驾车的测试人员血液、呼气酒精含量阈值与检验》规定，该规定指出，“饮酒驾车”是指车辆驾驶人员血液中的酒精含量大于或者等于20mg/100ml，小于80mg/100ml的驾驶行为。“醉酒驾车”是指车辆驾驶人员血液中的酒精含量大于或者等于80mg/100ml的驾驶行为。 2、核载22人，实际乘载了33人，属于客车超员。', 8, 1, '2021-12-19 01:13:33', '2021-12-19 01:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (425, '钱某驾驶大型卧铺客车，乘载45人（核载40人），保持40公里/小时以上的车速行至睢宁县桃园境内连续下坡急转弯路段处，翻下100米深的山崖，造成17人死亡、20人受伤。钱某的主要违法行为是什么？', '', '3', '4', NULL, '驾驶时接听手持电话', '超速行驶', '客车超员', '疲劳驾驶', 'BC', '1、弯道限速30公里/每小时，保持40公里/小时以上的车速是超速行驶。 2、乘载45人(核载40人)，客车超员。', 8, 1, '2021-12-19 01:13:33', '2021-12-19 01:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (426, '某日3时40分，孙某驾驶大客车（乘载55人、核载55人）行至随岳高速公路229公里加300米处，在停车下客过程中，被后方驶来李某驾驶的重型半挂机动车追尾，造成26人死亡，29人受伤。事后查明，李某从昨日18时许出发，途中一直未休息。双方驾驶人的主要违法行为是什么？', '', '3', '4', NULL, '孙某违法停车', '孙某客车超员', '李某超速', '李某疲劳驾驶', 'AD', '双方驾驶人的主要违法行为有： 1、孙某在高速上停车下客，属于违法停车； 2、李某途中一直未休息，属于疲劳驾驶。', 8, 1, '2021-12-19 01:13:33', '2021-12-19 01:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (427, '陶某驾驶中型客车（乘载33人），行至许平南高速公路163公里处时，以120公里/小时的速度与停在最内侧车道上安某驾驶的因事故无法移动的小客车（未设置警示标志）相撞，中型客车撞开右侧护栏侧翻，造成16死亡、15人受伤。双方驾驶人的主要违法行为是什么？', '', '3', '4', NULL, '陶某客车超员', '陶某超速行驶', '安某未按规定设置警示（告）标志', '安某违法停车', 'ABC', '双方驾驶人的主要违法行为有： 1、中型客车允许乘载19人，陶某载客33人，陶某客车超员。 2、在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，陶某超速行驶。 3、安某故障停车未设置警示标志，是未按规定设置警示（告）标志。 【拓展延伸】 很多学员认为安某违法停车，但要注意题干说的是“停在最内侧车道上安某驾驶的因事故无法移动的小客车”，安某的汽车是因为故障无法移动所以才会停在最内侧车道上，并不是主观上的违法停车，注意审题。', 8, 1, '2021-12-19 01:13:34', '2021-12-19 01:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (428, '邹某驾驶大型卧铺客车（核载35人，实载47人），行至京港澳高速公路938公里时，因乘车人携带的大量危险化学品在车厢内突然发生爆燃，造成41人死亡，6人受伤。此事故中的主要违法行为是什么？', '', '3', '4', NULL, '客车超员', '乘车人携带易燃易爆危险物品', '超速行驶', '不按规定停车', 'AB', '事故中的主要违法行为有： 1、大型卧铺客车核载35人，实载47人，属于客车超员。 2、乘车人携带的大量危险化学品，属于乘车人携带易燃易爆危险物品。 此处在港澳高速938公里处指的是事发地点离港澳高速起点938公里，与速度是没有关系的。', 8, 1, '2021-12-19 01:13:34', '2021-12-19 01:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (429, '杨某驾驶改装小型客车（核载9人，实载64人，其中62人为幼儿园学生），行至榆林子镇王伏村处，占用对向车道逆行时与一辆重型自卸货车正面碰撞，造成22人死亡、44人受伤。该起事故中的主要违法行为是什么？', '', '3', '4', NULL, '货车超速行驶', '非法改装机动车', '客车超员', '客车逆向行驶', 'BCD', '该起事故的主要违法行为有： 1、杨某驾驶改装的小型客车，属于非法改装机动车； 2、小型客车核载9人，实载64人，属于超员； 3、占用对向车道逆行时，属于逆向行驶。', 8, 1, '2021-12-19 01:13:34', '2021-12-19 01:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (430, '戚某驾驶大客车，乘载28人（核载55人），由南向北行至一无交通信号控制的交叉路口，以50公里的时速与由东向西行至该路口李某驾驶的重型半挂牵引车（核载40吨，实载55.2吨）侧面相撞，造成12人死亡、17人受伤。此事故中的主要违法行为是什么？', '', '3', '4', NULL, '客车超员', '客车超速行驶', '货车超载', '货车驾驶人经验不足', 'BC', '此事故中的主要违法行为有： 1、机动车通过交叉路口的速度一般不要超过30km／h，戚某以50公里的时速行驶，属于超速行驶。 2、货车核载44吨，实载55.2吨，属于货车超载。', 8, 1, '2021-12-19 01:13:34', '2021-12-19 01:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (431, '彭某驾驶一辆重型半挂牵引车，载运37.7吨货物（核载25吨），行至大广高速公路一下坡路段，追尾碰撞一辆由李某驾驶在应急车道内行驶的重型自卸货车（货箱内装载3.17立方黄土并搭乘24人），造成16人死亡、13人受伤。此事故中的主要违法行为是什么？', '', '3', '4', NULL, '彭某超速行驶', '彭某驾驶机动车超载', '李某在应急车道内行驶', '李某货车车厢内违法载人', 'BCD', '此事故的主要违法行为有： 1、彭某驾驶的半挂牵引车实载37.7吨-核载25吨=超载12.7吨，属于超载； 2、李某驾驶货车在应急车道内行驶，属于违法使用应急车道； 3、李某货箱内装载3.17立方黄土并搭乘24人，属于违法载人。', 8, 1, '2021-12-19 01:13:35', '2021-12-19 01:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (432, '石某驾驶低速载货机动车，运载4.05吨货物（核载1.2吨），行驶至宁津县境内314省道51公里加260米处，在越过道路中心线超越前方同向行驶的机动车时，与对向正常行驶的中型客车（乘载12人，核载11人）正面相撞，造成10人死亡、2人受伤。此事故中的违法行为是什么？', '', '3', '4', NULL, '货车超载', '货车违法超车', '客车超员', '客车驾驶人疲劳驾驶', 'ABC', '此事故中的违法行为有： 1、货车超载：货车运载4.05吨货物（核载1.2吨），超载； 2、货车违法超车：在超车过程中与对面来车有会车可能时，货车应当放弃超车； 3、客车超员：中型客车乘载了12人（核载11人），超员。', 8, 1, '2021-12-19 01:13:35', '2021-12-19 01:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (433, '变更车道前确认后方无来车时可以不开转向灯变道。', '', '2', '4', NULL, '', '', '', '', 'B', '变更车道前必须开启转向灯。《道路交通安全法》第五十七条 机动车应当按照下列规定使用转向灯：(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；(二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 8, 1, '2021-12-19 01:13:35', '2021-12-19 01:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (434, '这个小型客车驾车人有哪些违法行为？', '/uploadFile/yuanBeiTu34.gif', '3', '4', NULL, '接打手持电话', '无证驾驶', '酒后驾驶', '未系安全带', 'ABCD', '违法行为主要有： 1、驾驶人未系安全带； 2、一边开车一边接打手持电话； 3、刚喝完酒，属于酒后驾驶； 4、准备报名学个驾驶本，属于无证驾驶；', 8, 1, '2021-12-19 01:13:35', '2021-12-19 01:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (435, '行车中驾驶人接打手机或发短信有什么危害？', '', '3', '4', NULL, '影响乘车人休息', '分散驾驶注意力', '影响正常驾驶操作', '遇紧急情况反应不及', 'BCD', '行车中驾驶人一边开车一边接打电话或发短信，违背了安全驾驶的“集中注意力”这个黄金法则，对安全行车非常不利。分心驾驶的危害在于：1、驾驶人注意力分散、视线离开路面，无法提前观察或自然忽略道路交通中的信息；2、会引起驾驶姿势或操作动作的变化，不利于安全平稳操纵车辆；3、无法专注于观察和判断道路上的交通情况，遇紧急情况反应不及。', 8, 1, '2021-12-19 01:13:36', '2021-12-19 01:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (436, '下列做法哪些可以有效避免驾驶疲劳？', '', '3', '4', NULL, '连续驾驶不超过4小时', '用餐不宜过饱', '保持良好的睡眠', '餐后适当休息后驾车', 'ABCD', '1、根据规定，连续驾车超过4小时属于疲劳驾驶，司机驾车太长时间很容易疲惫，因此A项“连续驾驶不超过4小时”可以避免驾驶疲劳； 2、当人用餐过饱，很容易发困，不利于驾驶安全，B项“用餐不宜过饱”能够避免驾驶疲劳； 3、保持良好的睡眠，能够让人精力充沛，C项“保持良好的睡眠”也可以避免驾驶疲劳； 4、餐后由于人容易发困，因此适当休息调整状态，精力恢复了再驾车更加安全。D项“餐后适当休息后驾车”可以避免驾驶疲劳。', 8, 1, '2021-12-19 01:13:36', '2021-12-19 01:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (437, '驾驶汽车超速行驶有哪些危害？', '', '3', '4', NULL, '反应距离延长', '视野变窄', '加重事故后果', '制动距离延长', 'ABCD', '超速驾驶是最常见也是最容易引发恶性事故的交通违法行为，违法超速的危害主要表现在以下几方面： 1、超速影响驾驶人的视觉。会使驾驶人视野变窄，视力减弱，反应距离延长，对前方突然出现的险情难以有效处置； 2、超速使制动距离延长。车辆的制动距离主要受车速制约，由于惯性作用，车速越快，制动距离越大，从而增大了事故发生的可能性； 3、超速影响车辆操作稳定性。同等条件下，车速提高1倍，离心力会增大3倍，过大的离心力容易使车辆失控，出现侧翻。', 8, 1, '2021-12-19 01:13:36', '2021-12-19 01:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (438, '赵某（持有A2驾驶证）驾驶大型卧铺客车（核载36人），行驶至叶城县境内某处急弯路段，加速超越前车时，坠入道路一侧山沟，致16人死亡，20人受伤。赵某的主要违法行为是什么？', '', '3', '4', NULL, '在不具备超车条件的急弯路段加速超车', '驾驶逾期未检验的机动车', '驾驶与准驾车型不符的机动车', '疲劳驾驶', 'AC', '赵某的主要违法行为有： 1、在不具备超车条件的急弯路段加速超车 2、驾驶与准驾车型不符的机动车', 8, 1, '2021-12-19 01:13:36', '2021-12-19 01:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (439, '张某驾驶车辆在高速公路上发生故障不能移动，开启危险报警闪光灯后下车，联系朋友李某驾驶私家车帮忙拖曳到应急车道。李某拖曳故障车的过程中，刘某驾驶货运车辆以每小时110公里的速度驶来，导致三车相撞。这起事故中的违法行为有哪些？', '', '3', '4', NULL, '张某疲劳驾驶', '李某用私家车拖曳故障车辆', '刘某超速行驶', '未在故障车辆后设置警示标志', 'BCD', '主要违法行为： 1、张某未在故障车辆后设置警示标志。 2、李某用私家车拖曳故障车辆。机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。 3、刘某货车超速行驶。《道路交通安全法》第七十八条 在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。', 8, 1, '2021-12-19 01:13:37', '2021-12-19 01:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (440, '贾某驾车在高速公路上行驶，遇到大雾，能见度小于50米，贾某开启了雾灯、示廓灯、危险警报灯，以时速40公里行驶，并与同车道保持50米距离，经过三个出口驶离高速公路。贾某的主要违法行为是什么？', '', '3', '4', NULL, '未按规定开启相应的灯光', '超速行驶', '与同车道前车距离不足', '未及时从最近的出口驶离高速公路', 'ABD', '根据规定，雾天能见度小于50米时，要开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口驶离高速公路。 贾某违法行为：1、未按规定开启相应的灯光；2、时速40属于超速；3，未及时驶离高速公路。', 8, 1, '2021-12-19 01:13:37', '2021-12-19 01:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (441, '驾驶人进入驾驶室前，首先要做什么？', '', '1', '4', NULL, '观察机动车周围情况', '不用观察周围情况', '开启车门直接上车', '注意观察天气情况', 'A', '驾驶人在进入驾驶室即车辆起步前，应绕车一周进行检查，确认安全，观察车辆外观有无损毁，周围环境是否安全，车底有无异常情况，打开车门前应观察后方交通情况。', 9, 1, '2021-12-19 01:13:37', '2021-12-19 01:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (442, '出车前对轮胎进行哪些方面的检查？', '', '1', '4', NULL, '什么也不用检查', '轮胎有没有清洗', '备胎在什么位置', '轮胎的紧固和气压', 'D', '检查轮胎的磨损、紧固和气压情况，轮胎磨损超过标准，要及时更换；发现螺栓松动，要进行紧固；气压不足要找出原因，有针对性地处理。', 9, 1, '2021-12-19 01:13:37', '2021-12-19 01:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (443, '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车上道路行驶前，驾驶人要对机动车的安全技术性能进行认真检查，不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车，确保出行前一定都正常。', 9, 1, '2021-12-19 01:13:38', '2021-12-19 01:13:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (444, '行车前应对机动车驾驶室、发动机舱、车外部、轮胎进行检查。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车上道路行驶前，驾驶人要对机动车的安全技术性能进行认真检查，不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车，确保出行前一定都正常。', 9, 1, '2021-12-19 01:13:38', '2021-12-19 01:13:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (445, '事故中造成这个驾驶人致命伤害的原因是什么？', '/uploadFile/yuanBeiTu45.gif', '1', '4', NULL, '没有系安全带', '离转向盘距离过近', '没有握紧转向盘', '安全气囊没有打开', 'A', '图中造成驾驶人致命伤害的原因是没系安全带。座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾乘人员在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', 9, 1, '2021-12-19 01:13:39', '2021-12-19 01:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (446, '机动车在紧急制动时ABS系统会起到什么作用？', '', '1', '4', NULL, '缩短制动距离', '保持转向能力', '减轻制动惯性', '自动控制方向', 'B', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果。', 9, 1, '2021-12-19 01:13:39', '2021-12-19 01:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (447, '驾驶机动车的驾驶人不需要系安全带。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车的驾驶人就是开车的人，即司机，司机是必须要系安全带的，并且研究表明，假设气囊加安全带是100%安全的话，安全带的作用是70%，气囊的作用是30%。', 9, 1, '2021-12-19 01:13:39', '2021-12-19 01:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (448, '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', '', '2', '4', NULL, '', '', '', '', 'A', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 9, 1, '2021-12-19 01:13:39', '2021-12-19 01:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (449, '驾驶有ABS系统的机动车在紧急制动的同时转向可能会发生侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 9, 1, '2021-12-19 01:13:40', '2021-12-19 01:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (450, '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短。', '', '2', '4', NULL, '', '', '', '', 'B', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 9, 1, '2021-12-19 01:13:40', '2021-12-19 01:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (451, '在这种气象条件下起步要注意哪些方面？', '/uploadFile/yuanBeiTu51.gif', '1', '4', NULL, '开启远光灯', '开启前后雾灯', '只能开启左转向灯', '长时间鸣喇叭', 'B', '由图可知是雾天，雾天行车要开启雾灯。', 9, 1, '2021-12-19 01:13:40', '2021-12-19 01:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (452, '在这种环境中安全起步怎样使用灯光？', '/uploadFile/yuanBeiTu52.gif', '1', '4', NULL, '开启远光灯', '只能开启左转向灯', '开启左转向灯、近光灯', '开启危险报警闪光灯', 'C', '夜间起步前，要开启近光灯、左转向灯，仔细观察车辆周边及道路情况，确认安全后再起步。', 9, 1, '2021-12-19 01:13:40', '2021-12-19 01:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (453, '在这种能见度的情况下起步要开启近光灯。', '/uploadFile/yuanBeiTu53.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，驾驶机动车在冰雪等低能见度的天气情况下起步，要开启近光灯，因此本题选择【正确】。', 9, 1, '2021-12-19 01:13:41', '2021-12-19 01:13:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (454, '驾驶机动车在这种环境条件下起步前要开启远光灯。', '/uploadFile/yuanBeiTu54.gif', '2', '4', NULL, '', '', '', '', 'B', '本题考点为，夜间环境条件下车辆起步灯光的正确使用：夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用。1，傍晚黄昏时候，应提前开启前照灯；2，夜间起步前，要开启近光灯，仔细观察车辆周边及道路情况，确认安全后再起步；停车时待车停稳后再关闭灯光。', 9, 1, '2021-12-19 01:13:41', '2021-12-19 01:13:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (455, '驾驶机动车在雨天起步前要使用刮水器。', '', '2', '4', NULL, '', '', '', '', 'A', '雨、雪天气驾驶汽车起步时，要开启近光灯、左转向灯，雨天要使用刮水器，以确保视线清晰。', 9, 1, '2021-12-19 01:13:41', '2021-12-19 01:13:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (456, '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车在路边起步后，要随时注意两侧道路情况，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', 9, 1, '2021-12-19 01:13:42', '2021-12-19 01:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (457, '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', '', '2', '4', NULL, '', '', '', '', 'A', '机动车在路边起步后，要随时注意两侧道路情况，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', 9, 1, '2021-12-19 01:13:42', '2021-12-19 01:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (458, '在这种情况下驾驶人需要注意什么？', '/uploadFile/yuanBeiTu58.gif', '1', '4', NULL, '左侧机动车', '右侧机动车', '后方机动车', '前方机动车', 'A', '如图所示，在这种情况下驾驶人应注意左侧机动车。驾驶机动车汇入主路车流时，要注意观察左侧路况，确认安全后在不影响主路车辆行驶的前提下，向左缓慢汇入。', 9, 1, '2021-12-19 01:13:42', '2021-12-19 01:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (459, '驾驶机动车在有这种标志的路口怎样通过最安全？', '/uploadFile/yuanBeiTu59.gif', '1', '4', NULL, '停车观察路口情况', '加速尽快进入路口', '减速观察左后方情况', '减速缓慢进入路口', 'D', '', 9, 1, '2021-12-19 01:13:43', '2021-12-19 01:13:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (460, '驾驶机动车在有这种标志的路口怎样通过最安全？', '/uploadFile/yuanBeiTu60.gif', '1', '4', NULL, '停车观察路口情况', '加速尽快进入路口', '减速缓慢进入路口', '减速观察左后方情况', 'A', '遇到有停车让行标志的路口，要停车观察主路路口情况，确认安全后再通过。', 9, 1, '2021-12-19 01:13:43', '2021-12-19 01:13:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (461, '驾驶机动车在这种情况下怎样汇入主路车流？', '/uploadFile/yuanBeiTu61.gif', '1', '4', NULL, '加速直接汇入车流', '从主路内灰色车后汇入车流', '从主路内红色车前汇入车流', '开启转向灯直接汇入车流', 'B', '驾驶机动车从辅路汇入主路，遇到左侧车辆较多时，让左侧车辆先行，不要向左突然急加速转向汇入车流。', 9, 1, '2021-12-19 01:13:43', '2021-12-19 01:13:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (462, '驾驶机动车汇入车流时不能影响其他机动车通行。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车汇入车流时，要提前减速，注意观察，不妨碍其他车辆正常行驶的前提下，确认安全逐渐汇入。', 9, 1, '2021-12-19 01:13:44', '2021-12-19 01:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (463, '驾驶机动车从辅路汇入主路车流时要迅速。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车从辅路汇入主路，应缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入主路行驶道路。', 9, 1, '2021-12-19 01:13:44', '2021-12-19 01:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (464, '驾驶机动车向左变更车道遇到这种情况要注意让行。', '/uploadFile/yuanBeiTu64.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶车辆需要变更车道时，要分析道路交通流的状态，正确地选择行驶车道和变更时机，安全变道行驶。如图所示，左侧车道有机动车行驶，此时应注意让行，待左侧车辆通过后，确认安全的情况下再向左变更车道。', 9, 1, '2021-12-19 01:13:44', '2021-12-19 01:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (465, '驾驶机动车遇到这种情况要迅速向左变更车道。', '/uploadFile/yuanBeiTu65.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车向左变更车道时，注意观察前方和左侧车道内的情况，确认安全选择变道时机，不能影响左侧车道内车辆通行。', 9, 1, '2021-12-19 01:13:44', '2021-12-19 01:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (466, '驾驶机动车在该位置不能变更车道。', '/uploadFile/yuanBeiTu66.gif', '2', '4', NULL, '', '', '', '', 'A', '由图可知，前方是实线区，实线区不得变更车道。', 9, 1, '2021-12-19 01:13:45', '2021-12-19 01:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (467, '驾驶机动车可在该路口处向右变更车道。', '/uploadFile/yuanBeiTu67.gif', '2', '4', NULL, '', '', '', '', 'B', '图中车辆已到路口了，且右侧为白色实线，路口150米内和实线区不得变更车道。', 9, 1, '2021-12-19 01:13:45', '2021-12-19 01:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (468, '驾驶机动车在这种道路上怎样会车最安全？', '/uploadFile/yuanBeiTu68.gif', '1', '4', NULL, '靠路中心行驶', '靠路右侧行驶', '在路中间行驶', '靠路左侧行驶', 'B', '《中华人民共和国道路交通安全法实施条例》第四十八条 在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离。因此本题选B。', 9, 1, '2021-12-19 01:13:45', '2021-12-19 01:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (469, '在这种情况下怎样会车最安全？', '/uploadFile/yuanBeiTu69.gif', '1', '4', NULL, '靠中心线行驶', '开前照灯行驶', '向路右侧避让', '向车左侧避让', 'C', '遇到前方车辆越线占道的，应当靠右侧行驶避让，避免发生碰撞。', 9, 1, '2021-12-19 01:13:46', '2021-12-19 01:13:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (470, '驾驶机动车遇到这种情况怎样行驶最安全？', '/uploadFile/yuanBeiTu70.gif', '1', '4', NULL, '减速或停车让行', '紧靠路中心行驶', '保持正常车速', '占对向车道会车', 'A', '行车中遇对向车辆占据本车道逆向行驶时，应减速或停车让行，以免发生剐碰事故。', 9, 1, '2021-12-19 01:13:47', '2021-12-19 01:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (471, '会车中道路一侧有障碍，双方机动车应如何做？', '', '1', '4', NULL, '无障碍一方让对向先行', '速度慢的让速度快的先行', '有障碍的一方让对向先行', '速度快的让速度慢的先行', 'C', '驾驶机动车在有障碍的路段遇对面来车时，有障碍一方让无障碍一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 9, 1, '2021-12-19 01:13:47', '2021-12-19 01:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (472, '机动车在狭窄的坡路会车时，正确的会车方法是什么？', '', '1', '4', NULL, '下坡车让上坡车', '坡顶交会时距离坡顶远的一方让行', '上坡车让下坡车', '下坡车已行至中途而上坡车未上坡时，让上坡车', 'A', '《道路交通安全法实施条例》第四十八条，在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定： (三)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；', 9, 1, '2021-12-19 01:13:47', '2021-12-19 01:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (473, '会车遇到这种情况要低速会车或停车让行。', '/uploadFile/yuanBeiTu73.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，前方是窄桥，而且对向车辆已经在桥上了，所以我们应该先让对方车辆过桥，再接着驶上桥面。 但是在道路变窄之前会车就只需要低速会车，而此时前方车辆已经上桥，有可能出现需要停车让行的情况，因此说“低速会车”或“停车让行”是可以的。', 9, 1, '2021-12-19 01:13:48', '2021-12-19 01:13:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (474, '驾驶机动车遇到这种情况时要向左占道行驶。', '/uploadFile/yuanBeiTu74.gif', '2', '4', NULL, '', '', '', '', 'B', '如图，前方黑车逆行，为了安全，应该靠右减速或停车让行。', 9, 1, '2021-12-19 01:13:49', '2021-12-19 01:13:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (475, '如动画所示，驾驶机动车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu75.gif', '1', '4', NULL, '预留出横向安全距离，减速行驶', '保持正常速度行驶', '临近时紧急制动', '加速通过', 'A', '行车中超越右侧停放的车辆时，为预防其突然起步或开启开车，最有效的方式是预留出横向安全距离，减速行驶。因为长鸣喇叭、加速通过和保持正常速度行驶，都无法预防突然出现的危险。', 9, 1, '2021-12-19 01:13:50', '2021-12-19 01:13:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (476, '如动画所示，驾驶机动车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu76.gif', '1', '4', NULL, '紧急制动', '急转向迅速绕过', '迅速躲避不发生碰撞', '平稳停车', 'D', '在有障碍物的地点会车，要注意观察对面情况，遇对面来车已临近障碍物时，及时停车让对向来车优先通行，会车后再超越障碍。由动画可知，前方路段出现了障碍物，并且左侧车道有车辆，在这样的情况下应当停车。', 9, 1, '2021-12-19 01:13:50', '2021-12-19 01:13:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (477, '驾驶机动车在这种情况下怎样安全行驶？', '/uploadFile/yuanBeiTu77.gif', '1', '4', NULL, '加速抢先绕过障碍物', '占对向车道迫使对向让道', '停车让对向来车优先通行', '鸣喇叭或开启前照灯', 'C', '驾驶机动车在有障碍物的地点会车，要注意观察对面情况，遇对面来车已临近障碍物时，及时停车让对向来车优先通行，会车后再超越障碍。', 9, 1, '2021-12-19 01:13:51', '2021-12-19 01:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (478, '驾驶机动车在这样的路面如何安全行驶？', '/uploadFile/yuanBeiTu78.gif', '1', '4', NULL, '空挡滑行通过', '保持高速通过', '适当加速通过', '低速缓慢通过', 'D', '如图，右侧标志为路面不平，通过这种路面，要低速缓慢通过。', 9, 1, '2021-12-19 01:13:51', '2021-12-19 01:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (479, '驾驶机动车遇到这种情况时对向机动车优先通过。', '/uploadFile/yuanBeiTu79.gif', '2', '4', NULL, '', '', '', '', 'A', '因我方车道有障碍，想要通过必须要借用对向车道，对向车道车辆属于正常行驶，所以我方借用时不能妨碍对向车道车辆的正常行驶，对方车道车辆享有优先通过权。 （有障碍方让无障碍方）', 9, 1, '2021-12-19 01:13:51', '2021-12-19 01:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (480, '驾驶机动车遇到这种情况不要减速。', '/uploadFile/yuanBeiTu80.gif', '2', '4', NULL, '', '', '', '', 'B', '根据规定，有障碍的一方让无障碍的一方。但若有障碍的一方已经驶入有障碍路段而无障碍一方未驶入的，有障碍的一方先行。 如图，对方已行驶至障碍物路段中间，这时要减速停车让对方先行。', 9, 1, '2021-12-19 01:13:52', '2021-12-19 01:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (481, '驾驶机动车遇到这种情况怎样做最安全？', '/uploadFile/yuanBeiTu81.gif', '1', '4', NULL, '尽快加速超越前车', '主动减速放弃超车', '鸣喇叭让前车让路', '开前照灯让对向让路', 'B', '前车与我方车辆较近，且左前方对向有来车，不具备超车条件，应主减速放弃超车，A选项错误。 C和D明显违背了文明行车礼仪，也可以排除。故选择B。', 9, 1, '2021-12-19 01:13:52', '2021-12-19 01:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (482, '驾驶机动车在这种道路上怎样行驶最安全？', '/uploadFile/yuanBeiTu82.gif', '1', '4', NULL, '尽快加速超越前车', '鸣喇叭让前车让路', '保持距离跟车行驶', '从前车的右侧超越', 'C', '不能超车。在没有中心线的道路上超车时，要确认有足够的横向间距且对向无来车。预计在超车过程中与对面来车有会车可能时，应提前减速，保持安全距离跟车行驶，主动放弃超车。如图所示，对向有来车，应放弃超车，保持安全距离跟车行驶。', 9, 1, '2021-12-19 01:13:52', '2021-12-19 01:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (483, '驾驶机动车超车时遇到这样的情况怎样保证安全？', '/uploadFile/yuanBeiTu83.gif', '1', '4', NULL, '减速保持安全距离', '连续鸣喇叭提示', '保持距离加速通过', '占用对向车道超越', 'A', '1、有禁止按喇叭标志，B是错误的。 2、加速通过可能会撞到准备起步的公交车，C是错误的。 3、图中是黄色实线，不可占用对向车道，D是错误的。 （此题陷阱在于题干中的超车，虽然是在超车情况下，但是B、C、D都会是极不安全的行为，是不应该选择的，最安全的就是减速保持安全距离，大家不要被题干迷惑）', 9, 1, '2021-12-19 01:13:53', '2021-12-19 01:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (484, '驾驶机动车遇到这种情况怎样礼让？', '/uploadFile/yuanBeiTu84.gif', '1', '4', NULL, '迅速加速行驶', '紧跟前车行驶', '靠右加速行驶', '靠右减速让行', 'D', 'A与C中的“迅速”和“加速”错误。因为要留出一定的安全距离，B中“紧跟”错误。排除法可以得出答案D。从左后视镜可以看到，后方还有一辆黄色的车，所以必须是靠右减速让行。倡导文明礼让，安全第一。', 9, 1, '2021-12-19 01:13:53', '2021-12-19 01:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (485, '如动画所示，前车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu85.gif', '1', '4', NULL, '迅速减速或紧急制动', '让出适当空间加速行驶', '减速靠道路右侧让行', '靠道路右侧加速行驶', 'C', '由动画可知，行驶中遇后车开启左转向灯发出超车信号时，前车在具备让超条件下，应减速靠右让行，为后车超车留出足够的安全空间。', 9, 1, '2021-12-19 01:13:53', '2021-12-19 01:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (486, '驾驶机动车在这种情况下怎样做？', '/uploadFile/yuanBeiTu86.gif', '1', '4', NULL, '从前车左侧超越', '占对向车道超越', '从前车右侧超越', '跟在前车后行驶', 'D', '路中心黄实线且对向有来车，因此，在如图所示的路况下，应保持安全距离跟车行驶，不得超车，', 9, 1, '2021-12-19 01:13:54', '2021-12-19 01:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (487, '驾驶机动车遇到这种情况怎样行驶？', '/uploadFile/yuanBeiTu87.gif', '1', '4', NULL, '减速让非机动车先行', '连续鸣喇叭告知让道', '从非机动车左侧绕过', '占对向车道加速超越', 'A', '驾驶机动车准备超越前方非机动车时，若非机动车前方有车辆突然停车，造成非机动车占道行驶时，要及时减速让非机动车先行。', 9, 1, '2021-12-19 01:13:54', '2021-12-19 01:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (488, '在这种情况下被超机动车驾驶人怎样应对？', '/uploadFile/yuanBeiTu88.gif', '1', '4', NULL, '鸣喇叭进行警告', '减速或靠右停车', '开远光灯抗议', '加速反超后告诫', 'B', '驾驶机动车遇后方车辆强行超车后，不给留出安全距离便向右变道时，要减速或靠右停车避让，千万不要开赌气车。', 9, 1, '2021-12-19 01:13:55', '2021-12-19 01:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (489, '超车时前方机动车不减速、不让道，驾驶人怎么办？', '', '1', '4', NULL, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', 'C', '超车之前一定要观察好超车条件，能够保证万无一失再开始超越。遇前方车辆不减速、不让道时，应主动减速，保持安全距离，停止继续超车。因为紧随其后、连续鸣喇叭和加速超越，都无法预防突然出现的危险。', 9, 1, '2021-12-19 01:13:55', '2021-12-19 01:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (490, '超车时，发现前方机动车正在超车，驾驶人怎么办？', '', '1', '4', NULL, '紧跟其后，伺机超越', '加速强行超越', '连续鸣喇叭催前车让路', '停止超车，让前方机动车先超车', 'D', '若强行超车，很有可能发生危险。只有在前车完成超车后，才能看清前方道路的状况，此时超车也才较为安全。', 9, 1, '2021-12-19 01:13:55', '2021-12-19 01:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (491, '驾驶机动车在这个路段允许超车。', '/uploadFile/yuanBeiTu91.gif', '2', '4', NULL, '', '', '', '', 'B', '', 9, 1, '2021-12-19 01:13:55', '2021-12-19 01:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (492, '驾驶机动车在这种道路上从前车右侧超越最安全。', '/uploadFile/yuanBeiTu92.gif', '2', '4', NULL, '', '', '', '', 'B', '我国规定，机动车靠右行驶，且车辆均为左舵车，所以右侧盲区相对较大，从而容易与从右侧超越的车辆发生碰撞。正因存在这样的安全隐患，我国交通法禁止右侧超车的行为。 正确的超车方式，一定要从左侧进行，这样后车只需要在往左偏离车道最小的状况下，驾驶员就能观察到前方是否可以超车，更能确保安全。', 9, 1, '2021-12-19 01:13:56', '2021-12-19 01:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (493, '驾驶机动车在这样的道路上只能从左侧超越。', '/uploadFile/yuanBeiTu93.gif', '2', '4', NULL, '', '', '', '', 'A', '在没有中心线的道路上超车时，要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，从被超越车辆的左侧超越。如图所示，有足够的横向间距且对向无来车，符合超车条件。', 9, 1, '2021-12-19 01:13:56', '2021-12-19 01:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (494, '驾驶机动车在这种情况下可以越过中心实线超车。', '/uploadFile/yuanBeiTu94.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，道路中间有黄色实线，不能越线超车；而且前方正在施工，前车必定要向左转的。出于以上两方面考虑，在这种情况下是不应该超车的。', 9, 1, '2021-12-19 01:13:56', '2021-12-19 01:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (495, '驾驶机动车遇到这种情况要主动减速让后车超越。', '/uploadFile/yuanBeiTu95.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车发现后车发出超车信号时，若具备让车条件，主动减速靠右让行，必要时辅以手势示意让超，不得故意不让或让路不让速。', 9, 1, '2021-12-19 01:13:56', '2021-12-19 01:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (496, '机动车在这种情况下可以超车。', '/uploadFile/yuanBeiTu96.gif', '2', '4', NULL, '', '', '', '', 'B', '根据《道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁道路口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。由图可见，车辆进入了隧道，所以这种情况下不允许超车。', 9, 1, '2021-12-19 01:13:57', '2021-12-19 01:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (497, '驾驶机动车在这种道路上超车可借对向车道行驶。', '/uploadFile/yuanBeiTu97.gif', '2', '4', NULL, '', '', '', '', 'B', '不能超。如图所示，路中心黄色实线，不可越线超车。因此，题干表述错误。', 9, 1, '2021-12-19 01:13:57', '2021-12-19 01:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (498, '驾驶机动车在这种情况下不能超车。', '/uploadFile/yuanBeiTu98.gif', '2', '4', NULL, '', '', '', '', 'A', '由图可知，前方是铁道路口，铁道口禁止超车。', 9, 1, '2021-12-19 01:13:57', '2021-12-19 01:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (499, '如动画所示，在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', '/uploadFile/yuanBeiTu99.gif', '2', '4', NULL, '', '', '', '', 'B', '行经交叉路口时，不能超车，而且前方是实线，不可越过实线超车。', 9, 1, '2021-12-19 01:13:58', '2021-12-19 01:13:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1100, '驾驶机动车上道路行驶，不允许超过限速标志标明的最高时速。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十二条：机动车上道路行驶，不得超过限速标志标明的最高时速。在没有限速标志的路段，应当保持安全车速。', 1, 1, '2021-12-19 00:08:49', '2021-12-19 00:08:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1101, '驾驶机动车在没有中心线的城市道路上，最高速度不能超过每小时50公里。', '', '2', '1', NULL, '', '', '', '', 'B', '【无线：城3公4】《道路交通安全法实施条例》第四十五条：在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里。【无线：城3公4】', 1, 1, '2021-12-19 00:08:49', '2021-12-19 00:08:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1102, '驾驶机动车在没有中心线的公路上，最高速度不能超过每小时70公里。', '', '2', '1', NULL, '', '', '', '', 'B', '没有中心线的公路，限速要求≤40公里/小时。《道路交通安全法实施条例》第四十五条：机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 1, 1, '2021-12-19 00:08:49', '2021-12-19 00:08:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1103, '车辆在这种条件的道路上，最高速度不能超过每小时50公里。', '/uploadFile/yuanBeiTu103.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，机动车在冰雪路行驶，因此最高行驶速度不得超过30公里/小时。因此题干表述错误。 机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　 （四）在冰雪、泥泞的道路上行驶时。', 1, 1, '2021-12-19 00:08:50', '2021-12-19 00:08:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1104, '驾驶机动车掉头、转弯、下陡坡时的最高速度不能超过每小时40公里。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里： （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时。 题干描述错误，本题答案为错。', 1, 1, '2021-12-19 00:08:50', '2021-12-19 00:08:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1105, '驾驶机动车通过窄路、窄桥时的最高速度不能超过每小时30公里。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里：　　 （一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时；　　 （二）掉头、转弯、下陡坡时；　　 （三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时；　　 （四）在冰雪、泥泞的道路上行驶时。', 1, 1, '2021-12-19 00:08:50', '2021-12-19 00:08:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1106, '这两辆车发生追尾的主要原因是什么？', '/uploadFile/yuanBeiTu106.gif', '1', '1', NULL, '后车未与前车保持安全距离', '后车超车时距离前车太近', '前车采取制动过急', '前车采取制动时没看后视镜', 'A', '如图所示，后车跟车太近，未与前车保持安全距离，导致追尾。驾驶机动车与同车道行驶的前车，注意保持足以采取紧急制动措施的安全距离。', 1, 1, '2021-12-19 00:08:50', '2021-12-19 00:08:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1107, '驾驶机动车在下列哪种情形下不能超越前车？', '', '1', '1', NULL, '前车减速让行', '前车正在左转弯', '前车靠边停车', '前车正在右转弯', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:50', '2021-12-19 00:08:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1108, '在没有中心线的道路上发现后车发出超车信号时，如果条件许可如何行驶？', '', '1', '1', NULL, '保持原状态行驶', '加速行驶', '迅速停车让行', '降速靠右让路', 'D', '驾驶机动车在没有中心线的道路上发现后车发出超车信号时，若具备让车条件，应及时开启右转向灯，减速靠右让行，必要时辅以手势示意让超，不得故意不让或让路不让速。', 1, 1, '2021-12-19 00:08:51', '2021-12-19 00:08:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1109, '这种情况超车时，从前车的哪一侧超越？', '/uploadFile/yuanBeiTu109.gif', '1', '1', NULL, '从前车的右侧超越', '左右两侧均可超越', '从前车的左侧超越', '从无障碍一侧超越', 'C', '我国规定，机动车靠右行驶，且车辆均为左舵车，所以右侧盲区相对较大，从而容易与从右侧超越的车辆发生碰撞。正因存在这样的安全隐患，我国交通法禁止右侧超车的行为。 正确的超车方式，一定要从左侧进行，这样后车只需要在往左偏离车道最小的状况下，驾驶员就能观察到前方是否可以超车，更能确保安全。', 1, 1, '2021-12-19 00:08:51', '2021-12-19 00:08:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1110, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '', '1', '1', NULL, '正在停车', '减速让行', '正常行驶', '正在超车', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:51', '2021-12-19 00:08:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1111, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '', '1', '1', NULL, '正在停车', '减速让行', '正在掉头', '正常行驶', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:51', '2021-12-19 00:08:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1112, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '执行任务的警车', '大型客货车', '出租汽车', '城市公交车', 'A', '《道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:52', '2021-12-19 00:08:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1113, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '大型客货车', '出租汽车', '执行任务的救护车', '公共汽车', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:52', '2021-12-19 00:08:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1114, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '大型客货车', '执行任务的消防车', '公共汽车', '出租汽车', 'B', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；', 1, 1, '2021-12-19 00:08:52', '2021-12-19 00:08:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1115, '驾驶机动车行经市区下列哪种道路时不得超车？', '', '1', '1', NULL, '主要街道', '单向行驶路段', '单向两条行车道', '交通流量大的路段', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:53', '2021-12-19 00:08:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1116, '驾驶机动车行经下列哪种路段不得超车？', '', '1', '1', NULL, '主要街道', '高架路', '人行横道', '环城高速', 'C', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:53', '2021-12-19 00:08:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1117, '驾驶机动车行经下列哪种路段时不得超车？', '', '1', '1', NULL, '高架路', '交叉路口', '环城高速', '中心街道', 'B', '《道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:53', '2021-12-19 00:08:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1118, '驾驶机动车在下列哪种路段不得超车？', '', '1', '1', NULL, '山区道路', '城市高架路', '城市快速路', '窄桥、弯道', 'D', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:53', '2021-12-19 00:08:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1119, '驾驶机动车在夜间超车时怎样使用灯光？', '', '1', '1', NULL, '变换远、近光灯', '开启雾灯', '开启远光灯', '关闭前大灯', 'A', '《道路交通安全法实施条例》第四十七条规定：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', 1, 1, '2021-12-19 00:08:54', '2021-12-19 00:08:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1120, '遇到这种情况不能超车。', '/uploadFile/yuanBeiTu120.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示前方车辆左转向灯亮，表示前车正在超车，遇到这种情况应放弃超车。 根据《道路交通安全法》第四十三条，同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的；', 1, 1, '2021-12-19 00:08:54', '2021-12-19 00:08:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1121, '驾驶机动车超车应该提前开启左转向灯、变换使用远近光灯或鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', 1, 1, '2021-12-19 00:08:54', '2021-12-19 00:08:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1122, '驾驶机动车超车后立即开启右转向灯驶回原车道。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车超车后，应与被超车辆保持横向安全间距，打开右转向灯，在不影响被超越车辆正常行驶的情况下，逐渐平稳地驶回原车道。因此，题干表述错误。', 1, 1, '2021-12-19 00:08:54', '2021-12-19 00:08:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1123, '在道路上遇到这种情况可以从两侧超车。', '/uploadFile/yuanBeiTu123.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:55', '2021-12-19 00:08:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1124, '在这种情况下可以加速通过人行横道。', '/uploadFile/yuanBeiTu124.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，驾驶机动车看到人行横道前有停止的车辆时，一定要停车，不要盲目通过，更不得加速通过，前车可能是停车避让行人。不要在人行横道及其附近直行超车和变向超车，尤其要提防有些行动缓慢的人可能还滞留在人行横道上。', 1, 1, '2021-12-19 00:08:55', '2021-12-19 00:08:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1125, '遇到这种情况下可以从右侧超车。', '/uploadFile/yuanBeiTu125.gif', '2', '1', NULL, '', '', '', '', 'B', '超车时要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，从被超越车辆的左侧超越。超越后，在不影响被超越车辆正常行驶的情况下，逐渐驶回原车道。不得从被超车辆的右侧超越。', 1, 1, '2021-12-19 00:08:55', '2021-12-19 00:08:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1126, '驾驶机动车行经城市没有列车通过的铁路道口时允许超车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:55', '2021-12-19 00:08:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1127, '驾驶机动车在隧道、陡坡等特殊路段不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 1, 1, '2021-12-19 00:08:56', '2021-12-19 00:08:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1128, '遇到这种情形怎样行驶？', '/uploadFile/yuanBeiTu128.gif', '1', '1', NULL, '停车让对方车辆通过', '开启左转向灯向左行驶', '加速超越障碍后会车', '开前照灯告知对方让行', 'A', '驾驶机动车在有障碍的路段遇对向来车时，有障碍的一方让无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 1, 1, '2021-12-19 00:08:56', '2021-12-19 00:08:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1129, '驾驶机动车在没有道路中心线的狭窄山路怎样会车？', '', '1', '1', NULL, '速度慢的先行', '重车让空车先行', '靠山体的一方先行', '不靠山体的一方先行', 'D', '在狭窄的坡道会车：1，下坡车让上坡车先行。2、如果下坡车已行至中途而上坡车还未上坡时，下坡车先行。在没有道路中心线的狭窄山路会车：不靠山体一方的车辆先行。【狭窄路“靠山让行”】', 1, 1, '2021-12-19 00:08:56', '2021-12-19 00:08:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1130, '夜间在道路上会车时，距离对向来车多远将远光灯改用近光灯？', '', '1', '1', NULL, '不必变换灯光', '150米以外', '100米以内', '50米以内', 'B', '《道路交通安全法》第四十八条：在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定： (一)减速靠右行驶，并与其他车辆、行人保持必要的安全距离； (二)在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行； (三)在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行； (四)在狭窄的山路，不靠山体的一方先行； (五)夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 1, '2021-12-19 00:08:56', '2021-12-19 00:08:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1131, '驾驶机动车在没有中心线的道路上遇相对方向来车时怎样行驶？', '', '1', '1', NULL, '减速靠右行驶', '借非机动车道行驶', '紧靠路边行驶', '靠路中心行驶', 'A', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，减速靠右行驶，并与其他车辆、行人保持必要的安全距离。', 1, 1, '2021-12-19 00:08:57', '2021-12-19 00:08:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1132, '夜间驾驶机动车在窄路、窄桥会车怎样使用灯光？', '', '1', '1', NULL, '关闭所有灯光', '开启近光灯', '关闭前照灯', '开启远光灯', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 1, '2021-12-19 00:08:57', '2021-12-19 00:08:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1133, '遇到这种情况可以优先通行。', '/uploadFile/yuanBeiTu133.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。', 1, 1, '2021-12-19 00:08:57', '2021-12-19 00:08:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1134, '未上坡的车辆遇到这种情况让对向下坡车先行。', '/uploadFile/yuanBeiTu134.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十八条： 在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。如图所示，下坡车已行驶至中途而上坡车未上坡，应让下坡车先行。因此，题干表述正确。', 1, 1, '2021-12-19 00:08:58', '2021-12-19 00:08:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1135, '夜间驾驶机动车在窄路、窄桥会车时正确的做法是使用远光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 1, '2021-12-19 00:08:58', '2021-12-19 00:08:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1136, '如何通过这种交叉路口？', '/uploadFile/yuanBeiTu136.gif', '1', '1', NULL, '鸣笛催促', '保持速度通过', '减速慢行', '加速通过', 'C', '驾驶机动车在没有交通信号的道路遇行人横过道路，要及时减速慢行或停车避让。', 1, 1, '2021-12-19 00:08:58', '2021-12-19 00:08:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1137, '怎样通过这样的路口？', '/uploadFile/yuanBeiTu137.gif', '1', '1', NULL, '不减速通过', '加速尽快通过', '空挡滑行通过', '减速或停车观察', 'D', '驾驶机动车通过无信号灯控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 1, 1, '2021-12-19 00:08:59', '2021-12-19 00:08:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1138, '在这个路口左转弯选择哪条车道？', '/uploadFile/yuanBeiTu138.gif', '1', '1', NULL, '最左侧车道', '中间车道', '最右侧车道', '不用变道', 'A', '路口左转弯应选择左转弯车道。因此，图示机动车应提前变更到最左侧导向箭头为左转弯的车道。', 1, 1, '2021-12-19 00:08:59', '2021-12-19 00:08:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1139, '在这个路口怎样左转弯？', '/uploadFile/yuanBeiTu139.gif', '1', '1', NULL, '靠路口中心点右侧转弯', '靠路口中心点左侧转弯', '不能左转弯', '骑路口中心点转弯', 'B', '左转通过路口，靠路口中心点左侧转弯的法律依据如下： 根据《道路交通安全法》第五十一条　机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： (一)在划有导向车道的路口，按所需行进方向驶入导向车道； (二)准备进入环形路口的让已在路口内的机动车先行； (三)向左转弯时，靠路口中心点左侧转弯。转弯时开启转向灯，夜间行驶开启近光灯；', 1, 1, '2021-12-19 00:08:59', '2021-12-19 00:08:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1140, '进入这个路口应该怎样做？', '/uploadFile/yuanBeiTu140.gif', '1', '1', NULL, '交替变换远近光灯提醒路口内车辆让行', '从路口内车辆前迅速插入', '让已在路口内的车辆先行', '鸣喇叭直接进入路口', 'C', '驾驶机动车准备进入环形路口的，注意观察左侧已在环岛内行驶车辆的动态，让已在路口内的机动车先行。【环岛让行原则】进环岛让出环岛：准备进入环形路口的让已在路口内的机动车先行。', 1, 1, '2021-12-19 00:08:59', '2021-12-19 00:08:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1141, '在路口右转弯遇同车道前车等候放行信号时如何行驶？', '', '1', '1', NULL, '从前车左侧转弯', '从右侧占道转弯', '鸣喇叭让前车让路', '依次停车等候', 'D', '《道路交通安全法实施条例》第五十一条：机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行，向右转弯遇有同车道前车正在等候放行信号时，依次停车等候。', 1, 1, '2021-12-19 00:09:00', '2021-12-19 00:09:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1142, '在这个路口右转弯如何通行？', '/uploadFile/yuanBeiTu142.gif', '1', '1', NULL, '先让对面车左转弯', '直接向右转弯', '抢在对面车前右转弯', '鸣喇叭催促', 'A', '交叉路口通行原则，相对方向行驶的右转弯的机动车让左转弯的车辆先行。如图所示，对向机动车左转弯，因此我方右转弯车辆应先让对面车左转弯行驶。', 1, 1, '2021-12-19 00:09:00', '2021-12-19 00:09:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1143, '在路口直行时，遇这种情形如何通行？', '/uploadFile/yuanBeiTu143.gif', '1', '1', NULL, '让左方道路车辆先行', '让右方道路车辆先行', '直接加速直行通过', '开启危险报警闪光灯通行', 'B', '“让右原则”通俗理解：哪边车的主驾离你近，就要让哪边的车先行。根据《道路交通安全法》第五十二条 机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定： (二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行； 如图所示交叉路口直行时，我方车辆应让右侧路口车辆先行。', 1, 1, '2021-12-19 00:09:00', '2021-12-19 00:09:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1144, '在路口遇这种情形怎样通行？', '/uploadFile/yuanBeiTu144.gif', '1', '1', NULL, '鸣喇叭告知让行', '直接加速转弯', '让左方来车先行', '减速缓慢转弯', 'C', '如图所示，前方路口有减速让行标志和标线，因此应减速让左方来车先行。根据《道路交通安全法实施条例》第五十二条规定，机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，应当遵守下列规定：有交通标志、标线控制的，让优先通行的一方先行。', 1, 1, '2021-12-19 00:09:01', '2021-12-19 00:09:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1145, '驾驶机动车通过没有交通信号的交叉路口怎样行驶？', '', '1', '1', NULL, '减速慢行', '加速通过', '大型车先行', '左侧车辆先行', 'A', '驾驶机动车通过没有交通信号灯控制的路口时，应在距路口50-100米时减速，行至路口时仔细观察左右两侧道路交通情况，减速或停车瞭望，做到“一看，二慢、三通过”，直行车辆优先通行。遇到有停车让行标志的路口，要停车观察主路情况，确认安全后再通过。', 1, 1, '2021-12-19 00:09:01', '2021-12-19 00:09:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1146, '驾驶机动车通过没有交通信号和管理人员的铁路道口怎样通过？', '', '1', '1', NULL, '适当减速通过', '空挡滑行通过', '停车确认安全后通过', '加速尽快通过', 'C', '通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 1, 1, '2021-12-19 00:09:01', '2021-12-19 00:09:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1147, '驾驶机动车通过交叉路口要遵守交通信号。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十四条：机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。', 1, 1, '2021-12-19 00:09:01', '2021-12-19 00:09:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1148, '驾驶机动车在没有交通信号的路口要尽快通过。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车通过没有交通信号灯控制的路口时，应在距路口50-100米时减速，行至路口时仔细观察左右两侧道路交通情况，减速或停车瞭望，做到“一看，二慢、三通过”，直行车辆优先通行。遇到有停车让行标志的路口，要停车观察主路情况，确认安全后再通过。', 1, 1, '2021-12-19 00:09:02', '2021-12-19 00:09:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1149, '在这种情况的铁路道口要加速通过。', '/uploadFile/yuanBeiTu149.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车通过有交通信号控制的铁路道口时，在道口外提前减速、减挡，按照信号灯的指示低速通行，不得在路口内变换档位。遇报警器鸣响或红灯亮时，停车等候，不准抢行通过铁路道口。', 1, 1, '2021-12-19 00:09:02', '2021-12-19 00:09:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1150, '行至这种情况的铁路道口要停车观察。', '/uploadFile/yuanBeiTu150.gif', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 1, 1, '2021-12-19 00:09:02', '2021-12-19 00:09:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1151, '在这种情况的交叉路口转弯要让直行车先行。', '/uploadFile/yuanBeiTu151.gif', '2', '1', NULL, '', '', '', '', 'A', '交叉路口三个优先通行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。', 1, 1, '2021-12-19 00:09:02', '2021-12-19 00:09:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1152, '在交叉路口遇到这种情况享有优先通行权。', '/uploadFile/yuanBeiTu152.gif', '2', '1', NULL, '', '', '', '', 'A', '交叉路口让行原则：1、转弯让直行；2、右转让左转；3、让右原则。即转弯的机动车让直行的车辆先行；右方道路来车先行；右转弯车让左转弯车先行。如图所示，红车右转灯亮表示要右转，我方左转灯亮，右转让左转。因此我方优先通过。 【交叉路口让行原则】 1、小弯让大弯：右转转小弯；左转转大弯，因此右转让左转。 2、转弯让直行 3、让右原则：保护驾驶员原则。', 1, 1, '2021-12-19 00:09:03', '2021-12-19 00:09:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1153, '在交叉路口遇到这种情况享有优先通行权。', '/uploadFile/yuanBeiTu153.gif', '2', '1', NULL, '', '', '', '', 'B', '【直行 >左转>右转】三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。我方是右转，对方左转，故对方优先通过。 【拓展延伸】 题目未说明主体是谁的情况下， 一般默认为主视角车辆(我方车辆)。', 1, 1, '2021-12-19 00:09:03', '2021-12-19 00:09:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1154, '遇有这种排队等候的情形怎么做？', '/uploadFile/yuanBeiTu154.gif', '1', '1', NULL, '依次排队等候', '从右侧借道超越', '从左侧跨越实线超越', '从两侧随意超越', 'A', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 1, 1, '2021-12-19 00:09:03', '2021-12-19 00:09:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1155, '遇到前方车辆缓慢行驶时怎样行驶？', '/uploadFile/yuanBeiTu155.gif', '1', '1', NULL, '从两侧随意超越', '从右侧借道超越', '占对向车道超越', '依次排队行驶', 'D', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 1, 1, '2021-12-19 00:09:04', '2021-12-19 00:09:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1156, '在路口遇到这种情形时怎样做？', '/uploadFile/yuanBeiTu156.gif', '1', '1', NULL, '停在网状线区域内等待', '停在路口以外等待', '跟随前车通过路口', '停在路口内等待', 'B', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。 如图所示，虽然是绿灯，但前方路况拥堵，因此，机动车应该停在路口以外等待。 【拓展知识】', 1, 1, '2021-12-19 00:09:04', '2021-12-19 00:09:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1157, '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？', '', '1', '1', NULL, '从前车右侧路肩进入路口', '从有空隙一侧进入路口', '每车道一辆依次交替驶入路口', '向左变道穿插进入路口', 'C', '《道路交通安全法实施条例》第五十三条：机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆依次交替驶入车道减少后的路口、路段。', 1, 1, '2021-12-19 00:09:04', '2021-12-19 00:09:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1158, '驾驶机动车遇到前方车辆停车排队等候或缓慢行驶时怎么办？', '', '1', '1', NULL, '可借道超车', '占用对面车道', '穿插等候的车辆', '依次行驶', 'D', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待。即便是绿灯亮，也要依次停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。 《道路交通安全法》第四十五条：机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。', 1, 1, '2021-12-19 00:09:05', '2021-12-19 00:09:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1159, '驾驶机动车遇有前方交叉路口交通阻塞时怎么办？', '', '1', '1', NULL, '依次停在路口外等候', '可借对向车道通过', '从前车两侧穿插通过', '进入路口内等候', 'A', '《中华人民共和国道路交通安全法实施条例》第五十三条： 机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。', 1, 1, '2021-12-19 00:09:05', '2021-12-19 00:09:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1160, '驾驶机动车在没有交通信号的路口遇到前方车辆缓慢行驶时要依次交替通行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十五条明确规定：在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当依次交替通行。', 1, 1, '2021-12-19 00:09:05', '2021-12-19 00:09:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1161, '遇到这种情况时，要加速从红车前变更车道。', '/uploadFile/yuanBeiTu161.gif', '2', '1', NULL, '', '', '', '', 'B', '超车时要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，从被超越车辆的左侧超越。超越后，在不影响被超越车辆正常行驶的情况下，逐渐驶回原车道。 图中情景不符合超车条件，不可加速变道超越。', 1, 1, '2021-12-19 00:09:06', '2021-12-19 00:09:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1162, '在交叉路口遇到这种情况时，要在红灯亮以前加速通过路口。', '/uploadFile/yuanBeiTu162.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在黄色信号灯亮的路口，已越过停止线的车辆可以继续通行；没有越过停止线的车辆不得加速抢行通过，要在停止线以外停车等待。 如图所示机动车，在停止线以外，因此应当停车等待，不得加速通过。', 1, 1, '2021-12-19 00:09:06', '2021-12-19 00:09:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1163, '遇到这种情况的路段，可以进入网状线区域内停车等候。', '/uploadFile/yuanBeiTu163.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 1, 1, '2021-12-19 00:09:06', '2021-12-19 00:09:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1164, '这辆小轿车不能在这个位置停车。', '/uploadFile/yuanBeiTu164.gif', '2', '1', NULL, '', '', '', '', 'A', '禁止在人行横道上停放机动车，因此这辆小轿车不能在这个位置停车，题干说法正确。 《道路交通安全法》上有明确的规定： 第五十六条 机动车应当在规定地点停放，禁止在人行横道上停放机动车。 第九十三条 对违反道路交通安全法律、法规关于机动车停放、临时停车规定的，可以指出违法行为，并予以口头警告，令其立即驶离。机动车驾驶人不在现场或者虽在现场但拒绝立即驶离，妨碍其他车辆、行人通行的，处二十元以上二百元以下罚款，并可以将该机动车拖移至不妨碍交通的地点或者公安机关交通管理部门指定的地点停放。', 1, 1, '2021-12-19 00:09:06', '2021-12-19 00:09:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1165, '驾驶机动车遇到这种桥时首先怎样办？', '/uploadFile/yuanBeiTu165.gif', '1', '1', NULL, '保持匀速通过', '尽快加速通过', '低速缓慢通过', '停车察明水情', 'D', '请注意题干关键词，首先。《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 1, '2021-12-19 00:09:07', '2021-12-19 00:09:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1166, '驾驶机动车通过漫水路时要加速行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 1, 1, '2021-12-19 00:09:07', '2021-12-19 00:09:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1167, '驾驶机动车遇到漫水桥时要察明水情确认安全后再低速通过。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。 察明水情确认安全即通过对涉水的深度、水流速度和水底情况进行调查，不可冒险涉水驾驶。涉水中，特别注意减速慢行，不要注视水流的变化，避免中途停留；涉水后，间断轻踏制动踏板，以恢复制动效能。', 1, 1, '2021-12-19 00:09:07', '2021-12-19 00:09:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1168, '遇到这种情形时，应怎么办？', '/uploadFile/yuanBeiTu168.gif', '1', '1', NULL, '从行人前方绕行', '停车让行人先行', '鸣喇叭提醒行人', '从行人后方绕行', 'B', '驾驶机动车遇到行人通过人行横道时，应及时减速停车让行，不得加速从行人两侧绕行通过。', 1, 1, '2021-12-19 00:09:07', '2021-12-19 00:09:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1169, '行经这种交通标线的路段要加速行驶。', '/uploadFile/yuanBeiTu169.gif', '2', '1', NULL, '', '', '', '', 'B', '要减速通过。路面上的白色菱形图案是人行横道预告标识线，提示车辆驾驶人注意道路前方设置了人行横道线，需提前减速慢行。', 1, 1, '2021-12-19 00:09:08', '2021-12-19 00:09:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1170, '遇到这种情形时要停车避让行人。', '/uploadFile/yuanBeiTu170.gif', '2', '1', NULL, '', '', '', '', 'A', '根据《道路交通安全法》第四十七条，机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 1, 1, '2021-12-19 00:09:08', '2021-12-19 00:09:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1171, '在这种路口怎样进行掉头？', '/uploadFile/yuanBeiTu171.gif', '1', '1', NULL, '在人行横道上掉头', '进入路口后掉头', '从右侧车道掉头', '从中心线虚线处掉头', 'D', '路口左侧黄色虚实线，实线一侧禁止车辆越线或压线行驶，虚线一侧准许车辆暂时越线或转弯。路口信号灯绿灯亮时，在不影响过往车辆的情况下，可以从中心线虚线处掉头。', 1, 1, '2021-12-19 00:09:08', '2021-12-19 00:09:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1172, '遇到这种情况的路口，以下做法正确的是什么？', '/uploadFile/yuanBeiTu172.gif', '1', '1', NULL, '沿左侧车道掉头', '该路口不能掉头', '选择中间车道掉头', '在路口内掉头', 'B', '如图所示，前方有禁止掉头标志，因此该路口不能掉头。根据《道路交通安全法实施条例》第四十九条规定，机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 1, '2021-12-19 00:09:08', '2021-12-19 00:09:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1173, '在这个路口可以掉头。', '/uploadFile/yuanBeiTu173.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 1, '2021-12-19 00:09:09', '2021-12-19 00:09:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1174, '在这个路口不能掉头。', '/uploadFile/yuanBeiTu174.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，在设置允许掉头标志和标线的路段，可以掉头。因此，题干表述错误。', 1, 1, '2021-12-19 00:09:09', '2021-12-19 00:09:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1175, '在这段道路上不能掉头。', '/uploadFile/yuanBeiTu175.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 1, '2021-12-19 00:09:09', '2021-12-19 00:09:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1176, '驾驶机动车在铁路道口、桥梁、陡坡、隧道或者容易发生危险的路段不能掉头。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十九条第一款：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 1, 1, '2021-12-19 00:09:10', '2021-12-19 00:09:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1177, '在这段道路上，只要不影响其他车辆通行的前提下可以掉头。', '/uploadFile/yuanBeiTu177.gif', '2', '1', NULL, '', '', '', '', 'A', '黄线用来区分不同方向的车道，一般画在马路正中，好像一条隔离带，把马路隔成两个方向： 1、无论单黄线还是双黄线，看到实线=禁止跨越，比如超车、转弯、掉头等。 2、看到虚线=可越（在保证安全的情况下）。', 1, 1, '2021-12-19 00:09:10', '2021-12-19 00:09:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1178, '这位驾驶人违反法律规定的行为是什么？', '/uploadFile/yuanBeiTu178.gif', '1', '1', NULL, '没按规定握转向盘', '座椅角度不对', '没系安全带', '驾驶姿势不正确', 'C', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。如图所示：上道路行驶的驾驶人没有系安全带，属于违法行为。', 1, 1, '2021-12-19 00:09:10', '2021-12-19 00:09:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1179, '驾驶机动车在上道路行驶前驾驶人要按规定系好安全带。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。 【拓展知识】 座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', 1, 1, '2021-12-19 00:09:11', '2021-12-19 00:09:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1180, '机动车上路行驶时，前排乘车人可不系安全带。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', 1, 1, '2021-12-19 00:09:11', '2021-12-19 00:09:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1181, '机动车行驶中，车上少年儿童可不使用安全带。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', 1, 1, '2021-12-19 00:09:11', '2021-12-19 00:09:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1182, '找出这辆故障车有哪种违法行为？', '/uploadFile/yuanBeiTu182.gif', '1', '1', NULL, '没有设置警告标志', '没有开启危险报警闪光灯', '没有将车停到路边', '没有立即排除故障', 'A', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 1, '2021-12-19 00:09:12', '2021-12-19 00:09:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1183, '机动车在道路上发生故障，需要停车排除时，驾驶人应该怎么办？', '', '1', '1', NULL, '就地停车排除故障', '开启近光灯或雾灯', '将车停到不妨碍交通的地方', '将车停在道路中间', 'C', '机动车在行驶道路上发生故障，应将机动车移至不妨碍交通的地方停放，并要在来车方向的50—100米的地方设置警示标志。', 1, 1, '2021-12-19 00:09:12', '2021-12-19 00:09:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1184, '机动车在道路上发生故障，难以移动时下列做法正确的是什么？', '', '1', '1', NULL, '开启危险报警闪光灯', '开启车上所有灯光', '禁止车上人员下车', '在车前方设置警告标志', 'A', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。【车靠边、人撤离、即报警】', 1, 1, '2021-12-19 00:09:12', '2021-12-19 00:09:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1185, '这辆停在路边的机动车没有违法行为。', '/uploadFile/yuanBeiTu185.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。题目中的汽车没有开启危险报警闪光灯，存在违法行为。', 1, 1, '2021-12-19 00:09:12', '2021-12-19 00:09:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1186, '机动车在道路上发生故障难以移动时要在车后50米以内设置警告标志。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。因此，50米以内做法错误。 【 扩展提示】：高速公路上要在车后150米外设置警告标志。', 1, 1, '2021-12-19 00:09:13', '2021-12-19 00:09:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1187, '机动车在道路上发生轻微交通事故且妨碍交通时，不需移动。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车之间发生道路交通事故，“车能动，人未伤，有保险，车有牌，人有证，没有严重交通违法行为”的交通事故都可以进行快处快赔，发生轻微交通事故后，双方驾驶员应拍照或标明车辆现场位置，并将车辆移到不妨碍交通的地方。', 1, 1, '2021-12-19 00:09:13', '2021-12-19 00:09:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1188, '机动车在夜间道路上发生故障难以移动时要开启危险报警闪光灯、示廓灯、后位灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 1, 1, '2021-12-19 00:09:13', '2021-12-19 00:09:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1189, '在这种情形中前车怎样行驶？', '/uploadFile/yuanBeiTu189.gif', '1', '1', NULL, '正常行驶', '及时让行', '开启危险报警闪光灯行驶', '不得变更车道', 'B', '《道路交通安全法》第五十三条　警车、消防车、救护车、工程救险车执行紧急任务时，可以使用警报器、标志灯具；在确保安全的前提下，不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。【特殊车辆都该让】', 1, 1, '2021-12-19 00:09:14', '2021-12-19 00:09:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1190, '行车中遇到执行紧急任务的消防车、救护车、工程救险车时要及时让行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十三条　警车、消防车、救护车、工程救险车执行紧急任务时，可以使用警报器、标志灯具；在确保安全的前提下，不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。【特殊车辆都该让】', 1, 1, '2021-12-19 00:09:14', '2021-12-19 00:09:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1191, '行车中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十四条：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。【特殊车辆都该让】', 1, 1, '2021-12-19 00:09:14', '2021-12-19 00:09:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1192, '这样在路边临时停放机动车有什么违法行为？', '/uploadFile/yuanBeiTu192.gif', '1', '1', NULL, '在非机动车道停车', '停车占用机动车道', '距离路边超过30厘米', '在有禁停标线路段停车', 'D', '如图所示右侧路缘石上的黄色实线，是禁止停车【标线】，表明此处禁止停、放车辆。因此，这样在路边临时停放机动车，违反了禁停规定，即违法行为是：在有禁停标线路段停车。重点看标线。', 1, 1, '2021-12-19 00:09:14', '2021-12-19 00:09:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1193, '驾驶机动车需要在路边停车时怎样选择停车地点？', '', '1', '1', NULL, '在人行道上停放', '在路边随意停放', '在停车泊位内停放', '靠左侧路边逆向停放', 'C', '机动车停放应当遵守下列规定：（一）在停车场或者交通标志、标线指示的道路停车泊位等规定地点停放。（二）在道路停车泊位内停放时，车身不得超出停车泊位。', 1, 1, '2021-12-19 00:09:15', '2021-12-19 00:09:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1194, '这样在路边临时停放机动车有什么违法行为？', '/uploadFile/yuanBeiTu194.gif', '1', '1', NULL, '在非机动车道停车', '在人行横道上停车', '距离路边超过30厘米', '在有禁停标线路段停车', 'B', '《道路交通安全法实施条例》第六十三条：在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 1, 1, '2021-12-19 00:09:15', '2021-12-19 00:09:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1195, '这样临时停放红色轿车有什么违法行为？', '/uploadFile/yuanBeiTu195.gif', '1', '1', NULL, '距离加油站不到30米', '停车占用非机动车道', '距离路边超过30厘米', '在有禁停标线路段停车', 'A', '图为加油站，规定加油站30米内不得停车。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的车辆以外的车辆，不得停车。', 1, 1, '2021-12-19 00:09:15', '2021-12-19 00:09:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1196, '这样停放机动车有什么违法行为？', '/uploadFile/yuanBeiTu196.gif', '1', '1', NULL, '停车占用人行道', '在公共汽车站停车', '在有禁停标志路段停车', '在非机动车道停车', 'B', '图为公共汽车站。公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点３０米以内的路段，除使用上述设施的以外车辆，不得停车。', 1, 1, '2021-12-19 00:09:16', '2021-12-19 00:09:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1197, '在距这段路多少米以内的路段不能停放机动车？', '/uploadFile/yuanBeiTu197.gif', '1', '1', NULL, '5米以内', '10米以内', '30米以内', '50米以内', 'D', '根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定： （2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；【口5】 （3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', 1, 1, '2021-12-19 00:09:16', '2021-12-19 00:09:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1198, '在道路上临时停车不得妨碍其他车辆和行人通行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十六条：在道路上临时停车的，不得妨碍其他车辆和行人通行。', 1, 1, '2021-12-19 00:09:16', '2021-12-19 00:09:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1199, '驾驶机动车找不到停车位时可以借人行道停放。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法》第五十六条“机动车应当在规定地点停放，禁止在人行道上停放机动车。”', 1, 1, '2021-12-19 00:09:16', '2021-12-19 00:09:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1200, '距离交叉路口50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '【口五站三】距离交叉路口50米内路段，不得停车。根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定： （2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；【口5】 （3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', 1, 1, '2021-12-19 00:09:17', '2021-12-19 00:09:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1201, '这个路段可以在非机动车道上临时停车。', '/uploadFile/yuanBeiTu201.gif', '2', '1', NULL, '', '', '', '', 'B', '在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 1, 1, '2021-12-19 00:09:17', '2021-12-19 00:09:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1202, '社会车辆距离消防栓或者消防队(站)门前30米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '【口五站三】距离消防站门前30米以内路段不得停车。根据《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定： （2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；【口5】 （3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；【站3】', 1, 1, '2021-12-19 00:09:17', '2021-12-19 00:09:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1203, '距离桥梁、陡坡、隧道50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：交叉路口、铁路道口、急弯路、宽度不足４米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 1, 1, '2021-12-19 00:09:17', '2021-12-19 00:09:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1204, '距离宽度不足4米的窄路50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路安全法实施条例》第六十三条，机动车在道路上临时停车应当遵守下列规定： （2）交叉路口、铁路道口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车； （3）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车； 。。。', 1, 1, '2021-12-19 00:09:18', '2021-12-19 00:09:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1205, '机动车停稳前不能开车门和上下人员。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条　机动车在道路上临时停车，应当遵守下列规定： (四)车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行；', 1, 1, '2021-12-19 00:09:18', '2021-12-19 00:09:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1206, '打开机动车车门时，不得妨碍其他车辆和行人通行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第七十七条：乘坐机动车应当遵守下列规定： (一)不得在机动车道上拦乘机动车; (二)在机动车道上不得从机动车左侧上下车; (三)开关车门不得妨碍其他车辆和行人通行; (四)机动车行驶中，不得干扰驾驶，不得将身体任何部分伸出车外，不得跳车; (五)乘坐两轮摩托车应当正向骑坐。', 1, 1, '2021-12-19 00:09:18', '2021-12-19 00:09:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1207, '在这段高速公路上行驶的最高车速是多少？', '/uploadFile/yuanBeiTu207.gif', '1', '1', NULL, '120公里/小时', '100公里/小时', '90公里/小时', '60公里/小时', 'A', '限制速度标志：本标志至前方解除限制速度标志或另一块不同限速值的限制速度标志的路段内，机动车行驶速度不准超过标志所示数值。如图所示数值为120，因此，在这段高速公路上行驶的最高车速是120公里/小时。', 1, 1, '2021-12-19 00:09:19', '2021-12-19 00:09:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1208, '在这段高速公路上行驶的最低车速是多少？', '/uploadFile/yuanBeiTu208.gif', '1', '1', NULL, '50公里/小时', '60公里/小时', '80公里/小时', '100公里/小时', 'B', '在这段高速公路上行驶的最低车速是60公里/小时。', 1, 1, '2021-12-19 00:09:19', '2021-12-19 00:09:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1209, '在这条车道行驶的最低车速是多少？', '/uploadFile/yuanBeiTu209.gif', '1', '1', NULL, '60公里/小时', '90公里/小时', '100公里/小时', '110公里/小时', 'C', '《道路交通安全法实施条例》第七十八条：1、高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 2、同方向有2条车道的，左侧车道的最低车速每小时100公里；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。 如图所示同方向2条车道，且车辆在最左侧，所以最低车速为每小时100公里。 图中白底红圈的120禁令标志，表示高速公路最高限速120公里/小时。', 1, 1, '2021-12-19 00:09:21', '2021-12-19 00:09:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1210, '在这条车道行驶的最低车速是多少？', '/uploadFile/yuanBeiTu210.gif', '1', '1', NULL, '60公里/小时', '90公里/小时', '100公里/小时', '110公里/小时', 'B', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。在高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。 2、同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。 如图所示机动车行驶在中间车道，因此，最低车速是90公里/小时。', 1, 1, '2021-12-19 00:09:22', '2021-12-19 00:09:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1211, '在这条车道行驶的最高车速是多少？', '/uploadFile/yuanBeiTu211.gif', '1', '1', NULL, '120公里/小时', '110公里/小时', '100公里/小时', '90公里/小时', 'D', '图中右侧限度标志标注了最高限速90公里/小时，所以在这条车道行驶的最高车速是90公里/小时。', 1, 1, '2021-12-19 00:09:22', '2021-12-19 00:09:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1212, '驾驶小型载客汽车在高速公路行驶的最低车速为90公里/小时。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 1, 1, '2021-12-19 00:09:22', '2021-12-19 00:09:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1213, '驾驶机动车在高速公路要按照限速标志标明的车速行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '在高速公路行车道行驶，要严格遵守“分道行驶、各行其道”和限速规定，根据车辆行驶速度正确选择行车道，不得随意穿行越线，不准骑、轧车行道分界线，不得占用应急车道。有限速标志的路段，严格将车速控制在限速范围内。限速标志标明的车速与车道行驶车速的规定不一致时，按照限速标志标明的车速行驶。', 1, 1, '2021-12-19 00:09:23', '2021-12-19 00:09:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1214, '在这个位置时怎样使用灯光？', '/uploadFile/yuanBeiTu214.gif', '1', '1', NULL, '开启左转向灯', '开启右转向灯', '开启危险报警闪光灯', '开启前照灯', 'A', '根据《道路交通安全法实施条例》第七十九条：机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 1, 1, '2021-12-19 00:09:23', '2021-12-19 00:09:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1215, '进入减速车道时怎样使用灯光？', '/uploadFile/yuanBeiTu215.gif', '1', '1', NULL, '开启左转向灯', '开启右转向灯', '开启危险报警闪光灯', '开启前照灯', 'B', '向右驶入减速车道，驾驶人应在距出口500米时，打开右转向灯，适时减速，平顺地进入减速车道。', 1, 1, '2021-12-19 00:09:23', '2021-12-19 00:09:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1216, '驾驶小型载客汽车在高速公路上时速超过100公里时的跟车距离是多少？', '', '1', '1', NULL, '保持50米以上', '保持60米以上', '保持80米以上', '保持100米以上', 'D', '《道路交通安全法实施条例》第八十条：机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离；车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 1, '2021-12-19 00:09:23', '2021-12-19 00:09:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1217, '驾驶小型载客汽车在高速公路上时速低于100公里时的最小跟车距离是多少？', '', '1', '1', NULL, '不得少于50米', '不得少于30米', '不得少于20米', '不得少于10米', 'A', '根据《道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离；车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 1, 1, '2021-12-19 00:09:24', '2021-12-19 00:09:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1218, '驾驶机动车在高速公路遇到能见度低于200米的气象条件时，最高车速是多少？', '', '1', '1', NULL, '不得超过100公里/小时', '不得超过90公里/小时', '不得超过80公里/小时', '不得超过60公里/小时', 'D', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 1, '2021-12-19 00:09:24', '2021-12-19 00:09:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1219, '驾驶机动车在高速公路遇到能见度低于100米的气象条件时，最高车速是多少？', '', '1', '1', NULL, '不得超过40公里/小时', '不得超过60公里/小时', '不得超过80公里/小时', '不得超过90公里/小时', 'A', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 1, '2021-12-19 00:09:24', '2021-12-19 00:09:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1220, '驾驶机动车在高速公路遇到能见度低于50米的气象条件时，车速不得超过20公里/小时，还应怎么做？', '', '1', '1', NULL, '进入应急车道行驶', '尽快驶离高速公路', '在路肩低速行驶', '尽快在路边停车', 'B', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：(一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；(二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；(三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 1, 1, '2021-12-19 00:09:24', '2021-12-19 00:09:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1221, '驾驶机动车驶离高速公路时，在这个位置怎样行驶？', '/uploadFile/yuanBeiTu221.gif', '1', '1', NULL, '继续向前行驶', '驶入减速车道', '车速保持100公里/小时', '车速降到40公里/小时以下', 'B', '根据《道路交通安全法实施条例》第七十九条规定，机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。 机动车驶离高速公路时，应当开启右转向灯，驶入减速车道，降低车速后驶离。', 1, 1, '2021-12-19 00:09:25', '2021-12-19 00:09:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1222, '驾驶车辆可以从这个位置直接驶入高速公路行车道。', '/uploadFile/yuanBeiTu222.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第七十九条：机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道，不得从匝道直接驶入行车道。所以本题错在“直接驶入”。', 1, 1, '2021-12-19 00:09:25', '2021-12-19 00:09:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1223, '驾驶车辆驶离高速公路可以从这个位置直接驶入匝道。', '/uploadFile/yuanBeiTu223.gif', '2', '1', NULL, '', '', '', '', 'B', '根据图片可知，箭头所指方向是白色实线，实线禁止跨越，因此，只能继续前行寻找下一个出口，不能从这里直接驶入匝道。', 1, 1, '2021-12-19 00:09:26', '2021-12-19 00:09:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1224, '这辆小型载客汽车进入高速公路行车道的行为是正确的。', '/uploadFile/yuanBeiTu224.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车从匝道驶入高速公路，开启左转向灯，在不妨碍已在高速公路内的车辆正常行驶的情况下驶入加速车道。因此，如图所示机动车驶入行车道的行为是错误的：①直接压实线驶入；②影响左侧车道内其他车辆的正常行驶。题干表述错误。', 1, 1, '2021-12-19 00:09:26', '2021-12-19 00:09:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1225, '这辆小型载客汽车驶离高速公路行车道的方法是正确的。', '/uploadFile/yuanBeiTu225.gif', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车驶离高速公路，打开右转向灯，适当减速，平顺地进入减速车道。如图所示机动车做法正确。', 1, 1, '2021-12-19 00:09:26', '2021-12-19 00:09:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1226, '这辆在高速公路上临时停放的故障车，警告标志应该设置在车后多远处？', '/uploadFile/yuanBeiTu226.gif', '1', '1', NULL, '150米以外', '50～150米', '50～100米', '50米以内', 'A', '《道路交通安全法》第六十八条  机动车在高速公路上发生故障时，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。  ', 1, 1, '2021-12-19 00:09:27', '2021-12-19 00:09:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1227, '机动车在高速公路上发生故障时错误的做法是什么？', '', '1', '1', NULL, '开启危险报警闪光灯', '按规定设置警告标志', '车上人员不能下车', '迅速报警', 'C', '《道路交通安全法》第五十二条：机动车在高速公路上发生故障必须停车时，立即开启危险报警闪光灯，夜间还有开启示廓灯和后位灯，在车后150米以外设置警告标志，车上人员要迅速转移至车右前方护栏以外的安全地带，面向来车方向并迅速拨打紧急电话求援或报警。【车靠边、人撤离、即报警】 综上，车上人员不能下车，做法错误。', 1, 1, '2021-12-19 00:09:27', '2021-12-19 00:09:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1228, '机动车在高速公路上发生故障或交通事故无法正常行驶时由什么车拖曳或牵引？', '', '1', '1', NULL, '过路车', '大客车', '同行车', '清障车', 'D', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；但是，警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。', 1, 1, '2021-12-19 00:09:27', '2021-12-19 00:09:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1229, '机动车在高速公路上发生故障时，在来车方向50至100米处设置警告标志。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 1, '2021-12-19 00:09:27', '2021-12-19 00:09:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1230, '机动车在高速公路上发生故障时，将车上人员迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', '', '2', '1', NULL, '', '', '', '', 'A', '依据《道路交通安全法》第六十八条的规定，机动车在高速公路上发生故障时，对车上人员应当采取的措施：车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 1, 1, '2021-12-19 00:09:28', '2021-12-19 00:09:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1231, '机动车在高速公路上发生交通事故无法正常行驶时，用救援车、清障车拖曳、牵引。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第六十八条： 机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。 所以本题是正确的。', 1, 1, '2021-12-19 00:09:28', '2021-12-19 00:09:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1232, '在道路上发生未造成人员伤亡且无争议的轻微交通事故如何处置？', '', '1', '1', NULL, '保护好现场再协商', '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', 'D', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不立即撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, '2021-12-19 00:09:28', '2021-12-19 00:09:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1233, '驾驶机动车在道路上发生交通事故要立即将车移到路边。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, '2021-12-19 00:09:28', '2021-12-19 00:09:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1234, '驾驶人在发生交通事故后因抢救伤员变动现场时要标明位置。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, '2021-12-19 00:09:29', '2021-12-19 00:09:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1235, '在道路上发生交通事故造成人身伤亡时，要立即抢救受伤人员并迅速报警。', '', '2', '1', NULL, '', '', '', '', 'A', '根据《道路交通安全法》第七十条的规定：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', 1, 1, '2021-12-19 00:09:29', '2021-12-19 00:09:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1236, '驾驶人连续驾驶不得超过多长时间？', '', '1', '1', NULL, '4小时', '6小时', '8小时', '10小时', 'A', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 1, 1, '2021-12-19 00:09:29', '2021-12-19 00:09:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1237, '驾驶人连续驾驶4小时以上，停车休息的时间不得少于多少？', '', '1', '1', NULL, '5分钟', '10分钟', '15分钟', '20分钟', 'D', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 1, 1, '2021-12-19 00:09:29', '2021-12-19 00:09:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1238, '在什么情况下不得行车？', '', '1', '1', NULL, '车窗没关闭', '车门没关闭', '音响没关闭', '顶窗没关闭', 'B', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在车门、车厢没有关好时行车。', 1, 1, '2021-12-19 00:09:30', '2021-12-19 00:09:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1239, '驾驶机动车下陡坡时不得有哪些危险行为？', '', '1', '1', NULL, '提前减挡', '空挡滑行', '低挡行驶', '制动减速', 'B', '驾驶机动车下陡坡行驶，要挂中低速档位，充分利用发动机制动来控制车速，严禁空挡滑行。 【拓展知识-空挡滑行不省油而且危险大】 空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', 1, 1, '2021-12-19 00:09:30', '2021-12-19 00:09:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1240, '在这段道路上一定要减少鸣喇叭的频率。', '/uploadFile/yuanBeiTu240.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，右侧设有禁止停车和禁止鸣喇叭禁令标志，表示禁止车辆鸣喇叭和停放。', 1, 1, '2021-12-19 00:09:30', '2021-12-19 00:09:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1241, '在车门、车厢没有关好时不要驾驶机动车起步。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在车门、车厢没有关好时行车。', 1, 1, '2021-12-19 00:09:30', '2021-12-19 00:09:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1242, '不要在驾驶室的前后窗范围内悬挂和放置妨碍驾驶人视线的物品。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品。', 1, 1, '2021-12-19 00:09:31', '2021-12-19 00:09:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1243, '行车中在道路情况良好的条件下可以观看车载视频。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十二条，驾驶机动车不得有下列行为： (一)在车门、车厢没有关好时行车； (二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品； (三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为； (四)下陡坡时熄火或者空挡滑行； (五)向道路上抛撒物品； (六)驾驶摩托车手离车把或者在车把上悬挂物品； (七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟； (八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 1, '2021-12-19 00:09:31', '2021-12-19 00:09:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1244, '驾驶小型汽车下陡坡时允许熄火滑行。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在下陡坡时熄火或者空挡滑行。', 1, 1, '2021-12-19 00:09:31', '2021-12-19 00:09:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1245, '驾驶机动车时可以向道路上抛撒物品。', '', '2', '1', NULL, '', '', '', '', 'B', '《实施条例》第六十二条，驾驶机动车不得有下列行为： (一)在车门、车厢没有关好时行车； (二)在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品； (三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为； (四)下陡坡时熄火或者空挡滑行； (五)向道路上抛撒物品； (六)驾驶摩托车手离车把或者在车把上悬挂物品； (七)连续驾驶机动车超过4小时未停车休息或者停车休息时间少于20分钟； (八)在禁止鸣喇叭的区域或者路段鸣喇叭。', 1, 1, '2021-12-19 00:09:31', '2021-12-19 00:09:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1246, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失，可能会受到什么刑罚？', '', '1', '1', NULL, '处5年以上徒刑', '处3年以下徒刑或者拘役', '处3年以上徒刑', '处3年以上7年以下徒刑', 'B', '《刑法》第一百三十三条规定：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, '2021-12-19 00:09:32', '2021-12-19 00:09:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1247, '驾驶人违反交通运输管理法规发生重大事故致人重伤、死亡，可能会受到什么刑罚？', '', '1', '1', NULL, '处3年以下徒刑或者拘役', '处3年以上7年以下徒刑', '处5年以上徒刑', '处7年以上徒刑', 'A', '《刑法》第一百三十三条规定：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, '2021-12-19 00:09:32', '2021-12-19 00:09:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1248, '驾驶人违反交通运输管理法规发生重大事故致人重伤的可能判处3年以下徒刑或拘役。', '', '2', '1', NULL, '', '', '', '', 'A', '《刑法》第一百三十三条规定：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, '2021-12-19 00:09:32', '2021-12-19 00:09:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1249, '驾驶人违反交通运输管理法规发生重大事故致人死亡的处3年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '【无逃逸：3年以上】据《刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役。因此，题干表述错。', 1, 1, '2021-12-19 00:09:32', '2021-12-19 00:09:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1250, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失的可能处3年以下徒刑或拘役。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役。', 1, 1, '2021-12-19 00:09:33', '2021-12-19 00:09:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1251, '驾驶人违反交通运输管理法规发生重大事故致人死亡且逃逸的，处多少年有期徒刑？', '', '1', '1', NULL, '7年以上', '3年以下', '3年以上7年以下', '10年以上', 'C', '《刑法》第一百三十三条规定： 1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；【未逃逸】 2、交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；【死亡且逃逸】 3、因逃逸致人死亡的，处七年以上有期徒刑。【逃逸后死亡】', 1, 1, '2021-12-19 00:09:33', '2021-12-19 00:09:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1252, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处多少年有期徒刑？', '', '1', '1', NULL, '2年以下', '3年以下', '7年以下', '7年以上', 'D', '《中华人民共和国刑法》第一百三十三条： 违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役； 交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑； 因逃逸致人死亡的，处七年以上有期徒刑。 在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。 有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 1, 1, '2021-12-19 00:09:33', '2021-12-19 00:09:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1253, '驾驶人违反交通运输管理法规发生重大事故后，逃逸或者有其他特别恶劣情节的，处7年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '【事故后逃逸：3年以上7年以下】《刑法》第一百三十三条规定：1、违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役； 2、交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑； 3、因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, '2021-12-19 00:09:33', '2021-12-19 00:09:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1254, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处3年以上7年以下有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '【逃逸致死：7年以上】发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。', 1, 1, '2021-12-19 00:09:34', '2021-12-19 00:09:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1255, '驾驶机动车在道路上追逐竞驶，情节恶劣，会受到什么处罚？', '', '1', '1', NULL, '处拘役，并处罚金', '处管制，并处罚金', '处1年以上徒刑', '处6个月徒刑', 'A', '《刑法》第一百三十三条 ，在道路上驾驶机动车，有下列情形之一的，处拘役，并处罚金： （一）追逐竞驶，情节恶劣的； （二）醉酒驾驶机动车的； （三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的； （四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。 机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。 有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 1, 1, '2021-12-19 00:09:34', '2021-12-19 00:09:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1256, '驾驶人在道路上驾驶机动车追逐竞驶，情节恶劣的处3年以下有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《刑法》第一百三十三条之一 ，在道路上驾驶机动车，有下列情形之一的，处拘役，并处罚金： （一）追逐竞驶，情节恶劣的； （二）醉酒驾驶机动车的； （三）从事校车业务或者旅客运输，严重超过额定乘员载客，或者严重超过规定时速行驶的； （四）违反危险化学品安全管理规定运输危险化学品，危及公共安全的。 机动车所有人、管理人对前款第三项、第四项行为负有直接责任的，依照前款的规定处罚。 有前两款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 1, 1, '2021-12-19 00:09:34', '2021-12-19 00:09:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1257, '醉酒驾驶机动车在道路上行驶会受到什么处罚？', '', '1', '1', NULL, '处2年以下徒刑', '处拘役，并处罚金', '处2年以上徒刑', '处管制，并处罚金', 'B', '《道路交通安全法》醉酒驾驶机动车辆，吊销驾照，5年内不得重新获取驾照，经过判决后处以拘役，并处罚金。', 1, 1, '2021-12-19 00:09:35', '2021-12-19 00:09:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1258, '驾驶人在道路上醉酒驾驶机动车的处3年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十一条　饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。 醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。', 1, 1, '2021-12-19 00:09:35', '2021-12-19 00:09:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1259, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未悬挂机动车号牌', '未携带身份证', '未携带保险合同', '未放置城市环保标志', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。 故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 1, 1, '2021-12-19 00:09:35', '2021-12-19 00:09:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1260, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未携带身份证', '未放置检验合格标志', '未放置城市环保标志', '未携带机动车登记证书', 'B', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带机动车行驶证、驾驶证的。 【牌检保行驾】即：机动车号牌、检验合格标志、保险标志、行驶证、驾驶证，缺一不可。', 1, 1, '2021-12-19 00:09:35', '2021-12-19 00:09:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1261, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未携带机动车登记证书', '未携带保险合同', '未放置保险标志', '未放置城市环保标志', 'C', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车。 上道路行驶的机动车，两证两标一号牌，缺一不可。 【拓展延伸】 虽然现在部分城市已经实行了电子保单，但是司机拿到电子保单时，也会收到一份交强险纸质保单和交强险标签。驾车到尚未实行电子保单的城市，不放置保险标志的依然会被扣留车辆。建议持有电子保单的司机，也要在车身上放置保险标志。', 1, 1, '2021-12-19 00:09:36', '2021-12-19 00:09:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1262, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '', '1', '1', NULL, '机动车驾驶证', '居民身份证', '从业资格证', '机动车通行证', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带机动车行驶证、驾驶证的。 【牌检保行驾】即：机动车号牌、检验合格标志、保险标志、行驶证、驾驶证，缺一不可。', 1, 1, '2021-12-19 00:09:36', '2021-12-19 00:09:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1263, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '', '1', '1', NULL, '机动车通行证', '居民身份证', '从业资格证', '机动车行驶证', 'D', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带机动车行驶证、驾驶证的。 【牌检保行驾】即：机动车号牌、检验合格标志、保险标志、行驶证、驾驶证，缺一不可。', 1, 1, '2021-12-19 00:09:36', '2021-12-19 00:09:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1264, '对未放置检验合格标志上道路行驶的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 1, 1, '2021-12-19 00:09:36', '2021-12-19 00:09:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1265, '交通警察对未放置保险标志上道路行驶的车辆可依法扣留行驶证。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。 根据规定，对于不放置保险标志上路的处罚，大家注意审题，扣留的是机动车，不是扣留行驶证。因此题干表述错误。', 1, 1, '2021-12-19 00:09:37', '2021-12-19 00:09:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1266, '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 1, 1, '2021-12-19 00:09:37', '2021-12-19 00:09:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1267, '对有使用伪造或变造检验合格标志嫌疑的车辆，交通警察只进行罚款处罚。', '', '2', '1', NULL, '', '', '', '', 'B', '根据《道路交通安全法》第九十六条规定：伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。 使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。 当事人提供相应的合法证明或者补办相应手续的，应当及时退还机动车。', 1, 1, '2021-12-19 00:09:37', '2021-12-19 00:09:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1268, '对使用其他车辆号牌、行驶证的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', 1, 1, '2021-12-19 00:09:38', '2021-12-19 00:09:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1269, '对未按照国家规定投保交强险的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十八条规定，机动车所有人、管理人未按照国家规定投保机动车第三者责任强制保险的，由公安机关交通管理部门扣留车辆至依照规定投保后，并处依照规定投保最低责任限额应缴纳的保险费的二倍罚款。', 1, 1, '2021-12-19 00:09:38', '2021-12-19 00:09:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1270, '对发生道路交通事故需要收集证据的事故车，交通警察可以依法扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：对发生道路交通事故，因收集证据需要的，可以依法扣留事故车辆。', 1, 1, '2021-12-19 00:09:38', '2021-12-19 00:09:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1271, '驾驶人有使用其他车辆号牌、行驶证嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。', 1, 1, '2021-12-19 00:09:39', '2021-12-19 00:09:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1272, '驾驶人有使用其他车辆检验合格标志嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。', 1, 1, '2021-12-19 00:09:39', '2021-12-19 00:09:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1273, '驾驶人有使用其他车辆保险标志嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定，有下列情形之一的，依法扣留车辆：有伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、 保险标志、驾驶证或者使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志嫌疑的。', 1, 1, '2021-12-19 00:09:39', '2021-12-19 00:09:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1274, '驾驶人有哪种情形，交通警察可依法扣留机动车驾驶证？', '', '1', '1', NULL, '饮酒后驾驶机动车', '超过规定速度10%', '疲劳后驾驶机动车', '行车中未系安全带', 'A', '六种情形下可以扣留驾驶证。新法规定，有下列情形之一的，可以扣留机动车驾驶证： (一)饮酒、醉酒后驾驶机动车的； (二)机动车驾驶人将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； (三)机动车行驶超过规定时速百分之五十的； (四)驾驶拼装或者已达到报废标准的机动车的； (五)发生重大交通事故，构成犯罪的； (六)在一个记分周期内累积记分达到12分的。', 1, 1, '2021-12-19 00:09:39', '2021-12-19 00:09:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1275, '驾驶人将机动车交由什么样的人驾驶的，交通警察可依法扣留机动车驾驶证？', '', '1', '1', NULL, '实习期驾驶人', '取得驾驶证的人', '驾驶证被吊销的人', '驾驶证记分达到6分的人', 'C', '有下列情形之一的，可以扣留机动车驾驶证： ①饮酒后驾驶机动车的；②将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； ③机动车行驶超过规定时速50%的； ④驾驶拼装或者已达到报废标准嫌疑的机动车上道路行驶的； ⑤在一个记分周期内累积记分达到12分的。 【拓展知识】 《道路交通安全法》第一百一十条　执行职务的交通警察认为应当对道路交通违法行为人给予暂扣或者吊销机动车驾驶证处罚的，可以先予扣留机动车驾驶证，并在二十四小时内将案件移交公安机关交通管理部门处理。', 1, 1, '2021-12-19 00:09:40', '2021-12-19 00:09:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1276, '驾驶人将机动车交给驾驶证被吊销的人驾驶的，交通警察依法扣留驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '有下列情形之一的，可以扣留机动车驾驶证： ①饮酒后驾驶机动车的；②将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； ③机动车行驶超过规定时速50%的； ④驾驶拼装或者已达到报废标准嫌疑的机动车上道路行驶的； ⑤在一个记分周期内累积记分达到12分的。', 1, 1, '2021-12-19 00:09:40', '2021-12-19 00:09:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1277, '驾驶人将机动车交给驾驶证被暂扣的人驾驶的，交通警察给予口头警告。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十九条：将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处吊销机动车驾驶证。', 1, 1, '2021-12-19 00:09:40', '2021-12-19 00:09:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1278, '驾驶人驾驶有达到报废标准嫌疑机动车上路的，交通警察依法予以拘留。', '', '2', '1', NULL, '', '', '', '', 'B', '应当予以收缴，强制报废，题干描述错误。根据《道路交通安全法》规定，驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 1, 1, '2021-12-19 00:09:41', '2021-12-19 00:09:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1279, '驾驶人在一个记分周期内累积记分达到12分的，交通警察依法扣留驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车有下列情形之一的，依法扣留机动车驾驶证： 1、在一个记分周期内累积记分达12分的； 2、饮酒后驾驶机动车的； 3、醉酒后驾驶机动车的； 4、将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； 5、驾驶拼装或已达报废标准嫌疑的机动车上道路行驶的； 6、机动车行驶超过规定时速50%； 7、发生交通事故构成犯罪的； 8、使用他人机动车驾驶证的。', 1, 1, '2021-12-19 00:09:41', '2021-12-19 00:09:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1280, '驾驶机动车碰撞建筑物、公共设施后可即行撤离现场。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： 　　 （一）造成人员死亡、受伤的； 　　 （二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　 （三）机动车无号牌、无检验合格标志、无保险标志的； 　　 （四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的； 　　 （五）碰撞建筑物、公共设施或者其他设施的；　　 （六）驾驶人无有效机动车驾驶证的； 　　 （七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　 （八）当事人不能自行移动车辆的。 　　 发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 1, '2021-12-19 00:09:41', '2021-12-19 00:09:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1281, '发生交通事故造成人员受伤时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警： （一）造成人员死亡、受伤的； 　　 （二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　 （三）机动车无号牌、无检验合格标志、无保险标志的； 　　 （四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　 （五）碰撞建筑物、公共设施或者其他设施的；　　 （六）驾驶人无有效机动车驾驶证的； 　　 （七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； （八）当事人不能自行移动车辆的。 　　 发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 1, '2021-12-19 00:09:41', '2021-12-19 00:09:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1282, '道路交通事故中，驾驶人有饮酒、醉酒嫌疑时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警：　　 （一）造成人员死亡、受伤的； 　　 （二）发生财产损失事故，当事人对事实或者成因有争议的，以及虽然对事实或者成因无争议，但协商损害赔偿未达成协议的； 　　 （三）机动车无号牌、无检验合格标志、无保险标志的； 　　 （四）载运爆炸物品、易燃易爆化学物品以及毒害性、放射性、腐蚀性、传染病病源体等危险物品车辆的；　　 （五）碰撞建筑物、公共设施或者其他设施的；　　 （六）驾驶人无有效机动车驾驶证的； 　　 （七）驾驶人有饮酒、服用国家管制的精神药品或者麻醉药品嫌疑的； 　　 （八）当事人不能自行移动车辆的。 　　 发生财产损失事故，并具有前款第二项至第五项情形之一，车辆可以移动的，当事人可以在报警后，在确保安全的原则下对现场拍照或者标划停车位置，将车辆移至不妨碍交通的地点等候处理。', 1, 1, '2021-12-19 00:09:42', '2021-12-19 00:09:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1283, '道路交通事故中，机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '无两标两证一号牌的机动车不得上道路行驶。《道路交通事故处理程序规定》 第八条 道路交通事故有下列情形之一的，当事人应当保护现场并立即报警：　　 　　 （三）机动车无号牌、无检验合格标志、无保险标志的； 　', 1, 1, '2021-12-19 00:09:42', '2021-12-19 00:09:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1284, '驾驶机动车发生财产损失交通事故，当事人对事实及成因无争议的可先撤离现场。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 1, '2021-12-19 00:09:42', '2021-12-19 00:09:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1285, '驾驶机动车发生财产损失交通事故后，当事人对事实及成因无争议移动车辆时需要对现场拍照或者标划停车位置。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 1, 1, '2021-12-19 00:09:42', '2021-12-19 00:09:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1286, '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离的，交通警察不可以责令当事人撤离现场。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通事故处理程序规定》第十三条：机动车发生财产损失交通事故，对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 1, '2021-12-19 00:09:44', '2021-12-19 00:09:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1287, '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离造成交通堵塞的，可以对驾驶人处以200元罚款。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 1, 1, '2021-12-19 00:09:44', '2021-12-19 00:09:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1288, '申请小型汽车准驾车型驾驶证的人年龄条件是多少？', '', '1', '1', NULL, '18周岁以上60周岁以下', '18周岁以上不设上限', '21周岁以上50周岁以下', '24周岁以上70周岁以下', 'B', '根据公安部交通管理局发布的新措施，自2020年11月20日起，申请小型汽车、小型自动挡汽车、轻便摩托车驾驶证18周岁以上，取消70周岁的年龄上限。对70周岁以上人员考领驾驶证的，增加记忆力、判断力、反应力等能力测试，且需每年进行一次身体检查，提交体检证明，保证身体条件符合安全驾驶要求。', 1, 1, '2021-12-19 00:09:44', '2021-12-19 00:09:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1289, '3年内有下列哪种行为的人不得申请机动车驾驶证？', '', '1', '1', NULL, '吸烟成瘾', '注射毒品', '注射胰岛素', '酒醉经历', 'B', '《机动车驾驶证申领和使用规定》第十二条：三年内有吸食、注射毒品行为或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的，不得申请机动车驾驶证。', 1, 1, '2021-12-19 00:09:44', '2021-12-19 00:09:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1290, '申请小型汽车驾驶证的，年龄应在18周岁以上70周岁以下。', '', '2', '1', NULL, '', '', '', '', 'B', '根据公安部交通管理局发布的新措施，自2020年11月20日起，申请小型汽车、小型自动挡汽车、轻便摩托车驾驶证18周岁以上，取消70周岁的年龄上限。对70周岁以上人员考领驾驶证的，增加记忆力、判断力、反应力等能力测试，且需每年进行一次身体检查，提交体检证明，保证身体条件符合安全驾驶要求。', 1, 1, '2021-12-19 00:09:45', '2021-12-19 00:09:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1291, '酒后驾驶发生重大交通事故被依法追究刑事责任的人不能申请机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 1, 1, '2021-12-19 00:09:45', '2021-12-19 00:09:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1292, '造成交通事故后逃逸构成犯罪的人不能申请机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第一百零一条 违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。 造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', 1, 1, '2021-12-19 00:09:45', '2021-12-19 00:09:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1293, '驾驶人在驾驶证有效期满前多长时间申请换证？', '', '1', '1', NULL, '30日内', '60日内', '90日内', '6个月内', 'C', '《机动车驾驶证申领和使用规定》第五十七条，机动车驾驶人应当于机动车驾驶证有效期满前九十日内，向机动车驾驶证核发地或者核发地以外的车辆管理所申请换证。', 1, 1, '2021-12-19 00:09:45', '2021-12-19 00:09:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1294, '驾驶人户籍迁出原车辆管理所需要向什么地方的车辆管所提出申请？', '', '1', '1', NULL, '迁出地', '居住地', '所在地', '迁入地', 'D', '《公安部令第123号》第四十九条：机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。', 1, 1, '2021-12-19 00:09:46', '2021-12-19 00:09:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1295, '驾驶证记载的驾驶人信息发生变化的要在多长时间内申请换证？', '', '1', '1', NULL, '60日', '50日', '40日', '30日', 'D', '《机动车驾驶证申领和使用规定》第六十条具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地或者核发地以外的车辆管理所申请换证： （一）在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的； （二）机动车驾驶证损毁无法辨认的。', 1, 1, '2021-12-19 00:09:46', '2021-12-19 00:09:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1296, '驾驶人在驾驶证核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第五十八条，机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。申请时应当填写申请表，提交机动车驾驶人的身份证明和机动车驾驶证，并申报身体条件情况。【车登记，证核发，期满换证居住地】', 1, 1, '2021-12-19 00:09:47', '2021-12-19 00:09:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1297, '道路交通违法行为累积记分周期是多长时间？', '', '1', '1', NULL, '14个月', '12个月', '10个月', '6个月', 'B', '《道路交通安全法实施条例》第二十三条 公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为除给予行政处罚外，实行道路交通安全违法行为累积记分(以下简称记分)制度，记分周期为12个月。', 1, 1, '2021-12-19 00:09:47', '2021-12-19 00:09:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1298, '道路交通安全违法行为累积记分一个周期满分为12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十三条 公安机关交通管理部门对机动车驾驶人的道路交通安全违法行为除给予行政处罚外，实行道路交通安全违法行为累积记分(以下简称记分)制度，记分周期为12个月。', 1, 1, '2021-12-19 00:09:47', '2021-12-19 00:09:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1299, '驾驶人记分没有达到满分，有罚款尚未缴纳的，记分转入下一记分周期。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十四条：机动车驾驶人在一个记分周期内记分未达到12分，所处罚款已经缴纳的，记分予以清除；记分虽未达到12分，但尚有罚款未缴纳的，记分转入下一记分周期。', 1, 1, '2021-12-19 00:09:47', '2021-12-19 00:09:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1300, '持小型汽车驾驶证的驾驶人在下列哪种情况下需要接受审验？', '', '1', '1', NULL, '有效期满换发驾驶证时', '一个记分周期末', '记分周期未满分', '记分周期满12分', 'A', '小型汽车、摩托车等车型驾驶人审验的情形有两种：　　 1、发生交通事故造成人员死亡承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后三十日内到公安交管部门接受审验； 2、驾驶证转到异地或者有效期满换证时，应当到公安交管部门接受审验。　　 需要提醒的是，驾驶人没有按照规定参加审验仍驾驶机动车的，公安交管部门将处200元以上500元以下罚款。', 1, 1, '2021-12-19 00:09:48', '2021-12-19 00:09:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1301, '驾驶人因服兵役、出国（境）等原因无法办理审验时，延期审验期限最长不超过多长时间？', '', '1', '1', NULL, '1年', '2年', '3年', '5年', 'C', '《机动车驾驶证申领和使用规定》第五十条：机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理，延期期限最长不超过三年。延期期间机动车驾驶人不得驾驶机动车。', 1, 1, '2021-12-19 00:09:48', '2021-12-19 00:09:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1302, '小型汽车驾驶人发生交通事故造成人员死亡，承担同等以上责任未被吊销驾驶证的，应当在记分周期结束后30日内接受审验。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第六十条：小型汽车驾驶人发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。因此题干表述正确。', 1, 1, '2021-12-19 00:09:48', '2021-12-19 00:09:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1303, '驾驶人因服兵役、出国（境）等原因延期审验期间不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条 机动车驾驶人因服兵役、出国（境）等原因，无法在规定时间内办理驾驶证期满换证、审验、提交身体条件证明的，可以向机动车驾驶证核发地车辆管理所申请延期办理。申请时应当填写申请表，并提交机动车驾驶人的身份证明、机动车驾驶证和延期事由证明。 延期期限最长不超过三年。【延期期间机动车驾驶人不得驾驶机动车】。', 1, 1, '2021-12-19 00:09:48', '2021-12-19 00:09:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1304, '年龄在70周岁以上的驾驶人多长时间提交一次身体条件证明？', '', '1', '1', NULL, '每3年', '每2年', '每1年', '每6个月', 'C', '修改后的《机动车驾驶证申领和使用规定》(公安部令第139号)中第三十条规定：年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后三十日内，提交县级或者部队团级以上医疗机构出具的有关身体条件的证明。', 1, 1, '2021-12-19 00:09:49', '2021-12-19 00:09:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1305, '大型客车、牵引车、城市公交车、中型客车、大型货车驾驶人应当每两年提交一次身体条件证明。', '', '2', '1', NULL, '', '', '', '', 'B', '公安部123号令已明确规定，大、中型客货车驾驶人每年参加审验，审验时自己申报身体条件情况（每年一次）。因此，题干表述错误。', 1, 1, '2021-12-19 00:09:49', '2021-12-19 00:09:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1306, '机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的多长时间为实习期？', '', '1', '1', NULL, '6个月', '12个月', '3个月', '2年', 'B', '《公安部令第123号》第六十四条：机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 1, 1, '2021-12-19 00:09:49', '2021-12-19 00:09:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1307, '初次申领驾驶证的驾驶人在实习期内可以单独驾驶机动车上高速公路行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《公安部令第123号》第六十五条：驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', 1, 1, '2021-12-19 00:09:50', '2021-12-19 00:09:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1308, '驾驶人在实习期内驾驶机动车时，应当在车身后部粘贴或者悬挂统一式样的实习标志。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十四条：在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂统一式样的实习标志。', 1, 1, '2021-12-19 00:09:50', '2021-12-19 00:09:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1309, '机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车驾驶证申领和使用规定》第七十九条，机动车驾驶人在实习期内发生道路交通安全违法行为被记满12分的，注销其实习的准驾车型驾驶资格。被注销的驾驶资格不属于最高准驾车型的，还应当按照第七十八条第一款规定，注销其最高准驾车型驾驶资格。', 1, 1, '2021-12-19 00:09:50', '2021-12-19 00:09:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1310, '提供虚假材料申领驾驶证的申请人会承担下列哪种法律责任？', '', '1', '1', NULL, '处20元以上200元以下罚款', '取消申领驾驶证资格', '1年内不得再次申领驾驶证', '2年内不能再次申领驾驶证', 'C', '《机动车驾驶证申领和使用规定》第七十八条第一款规定：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。【假1】', 1, 1, '2021-12-19 00:09:50', '2021-12-19 00:09:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1311, '伪造、变造或者使用伪造、变造驾驶证的驾驶人构成犯罪的，将依法追究刑事责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 1, 1, '2021-12-19 00:09:50', '2021-12-19 00:09:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1312, '驾驶人有下列哪种违法行为一次记12分？', '', '1', '1', NULL, '违反交通信号灯', '使用伪造机动车号牌', '违反禁令标志指示', '拨打、接听手机的', 'B', '《道路交通安全违法行为记分分值》机动车驾驶人有下列违法行为之一，一次记12分： （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的； 【拓展知识】 违反道路交通信号灯通行的，一次记6分。 违反禁令标志、禁止标线指示的，一次记3分。 有拨打、接听手持电话等妨碍安全驾驶的行为的，一次记2分。', 1, 1, '2021-12-19 00:09:51', '2021-12-19 00:09:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1313, '驾驶与准驾车型不符的机动车一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '根据公安部《机动车驾驶证申领和使用规定》（附件3：道路交通安全违法行为记分分值），驾驶与准驾车型不符的机动车的，一次应记12分。', 1, 1, '2021-12-19 00:09:51', '2021-12-19 00:09:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1314, '饮酒后驾驶机动车一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '机动车驾驶人有下列违法行为之一，一次记12分： （二）饮酒后驾驶机动车的；', 1, 1, '2021-12-19 00:09:51', '2021-12-19 00:09:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1315, '造成交通事故后逃逸，尚不构成犯罪的一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '机动车驾驶人有下列违法行为之一，一次记12分： （四）造成交通事故后逃逸，尚不构成犯罪的；', 1, 1, '2021-12-19 00:09:52', '2021-12-19 00:09:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1316, '上道路行驶的机动车未悬挂机动车号牌的一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '机动车驾驶人有下列违法行为之一，一次记12分： （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；', 1, 1, '2021-12-19 00:09:52', '2021-12-19 00:09:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1317, '上道路行驶的机动车故意遮挡、污损、不按规定安装机动车号牌的一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '机动车驾驶人有下列违法行为之一，一次记12分： （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；', 1, 1, '2021-12-19 00:09:52', '2021-12-19 00:09:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1318, '使用伪造、变造的机动车号牌一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '机动车驾驶人有下列违法行为之一，一次记12分： （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；', 1, 1, '2021-12-19 00:09:53', '2021-12-19 00:09:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1319, '使用伪造、变造的行驶证一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '机动车驾驶人有下列违法行为之一，一次记12分： （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；', 1, 1, '2021-12-19 00:09:53', '2021-12-19 00:09:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1320, '驾驶人有下列哪种违法行为一次记6分？', '', '1', '1', NULL, '饮酒后驾驶机动车', '使用其他车辆行驶证', '车速超过规定时速50%以上', '违法占用应急车道行驶', 'D', '《道路交通安全法》规定 机动车驾驶人有下列违法行为之一，一次记6分： (八)驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的; (九)低能见度气象条件下，驾驶机动车在高速公路上不按规定行驶的; (十)驾驶机动车运载超限的不可解体的物品，未按指定的时间、路线、速度行驶或者未悬挂明显标志的。', 1, 1, '2021-12-19 00:09:53', '2021-12-19 00:09:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1321, '驾驶人驾驶机动车违反道路交通信号灯通行一次记多少分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'C', '（公安部令123号）机动车驾驶人有下列违法行为之一，一次记6分： (一)机动车驾驶证被暂扣期间驾驶机动车的; (二)驾驶机动车违反道路交通信号灯通行的;', 1, 1, '2021-12-19 00:09:54', '2021-12-19 00:09:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1322, '有下列哪种违法行为的机动车驾驶人将被一次记6分？', '', '1', '1', NULL, '驾驶与准驾车型不符的机动车', '饮酒后驾驶机动车', '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', 'C', '（公安部令123号）机动车驾驶人有下列违法行为之一，一次记6分： (一)机动车驾驶证被暂扣期间驾驶机动车的; (二)驾驶机动车违反道路交通信号灯通行的;', 1, 1, '2021-12-19 00:09:54', '2021-12-19 00:09:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1323, '有下列哪种违法行为的机动车驾驶人将被一次记12分？', '', '1', '1', NULL, '驾驶故意污损号牌的机动车上道路行驶', '机动车驾驶证被暂扣期间驾驶机动车的', '以隐瞒、欺骗手段补领机动车驾驶证的', '驾驶机动车不按照规定避让校车的', 'A', '机动车驾驶人有下列违法行为之一，一次记12分： （一）驾驶与准驾车型不符的机动车的； （二）饮酒后驾驶机动车的； （三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的； （四）造成交通事故后逃逸，尚不构成犯罪的； （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的；', 1, 1, '2021-12-19 00:09:54', '2021-12-19 00:09:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1324, '造成交通事故后逃逸，尚不构成犯罪的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为记分分值》机动车驾驶人有下列违法行为之一，一次记12分： （四）造成交通事故后逃逸，尚不构成犯罪的；', 1, 1, '2021-12-19 00:09:54', '2021-12-19 00:09:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1325, '饮酒后驾驶机动车的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为记分分值》一、机动车驾驶人有下列违法行为之一，一次记12分 ： （一）驾驶与准驾车型不符的机动车的； （二）饮酒后驾驶机动车的； （三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20%以上的； （四）造成交通事故后逃逸，尚不构成犯罪的；', 1, 1, '2021-12-19 00:09:55', '2021-12-19 00:09:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1326, '使用伪造、变造的驾驶证一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为记分分值》机动车驾驶人有下列违法行为之一，一次记12分： （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；', 1, 1, '2021-12-19 00:09:55', '2021-12-19 00:09:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1327, '使用其他机动车号牌、行驶证的一次记3分。', '', '2', '1', NULL, '', '', '', '', 'B', '使用其他机动车号牌、行驶证的一次记12分。根据《道路交通安全法》规定 机动车驾驶人有下列违法行为之一，一次记12分： （一）驾驶与准驾车型不符的机动车的； （二）饮酒后驾驶机动车的； （三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的； （四）造成交通事故后逃逸，尚不构成犯罪的； （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的； （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的；', 1, 1, '2021-12-19 00:09:55', '2021-12-19 00:09:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1328, '驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的一次记6分。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车驾驶人有下列违法行为之一，一次记12分： （一）驾驶与准驾车型不符的机动车的； （二）饮酒后驾驶机动车的； （三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的； （四）造成交通事故后逃逸，尚不构成犯罪的； （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的； （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的； （七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的； 。。。', 1, 1, '2021-12-19 00:09:55', '2021-12-19 00:09:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1329, '违反交通信号灯通行的一次记6分。', '', '2', '1', NULL, '', '', '', '', 'A', '（公安部令123号）机动车驾驶人有下列违法行为之一，一次记6分： (一)机动车驾驶证被暂扣期间驾驶机动车的; (二)驾驶机动车违反道路交通信号灯通行的; (三)驾驶营运客车(不包括公共汽车)、校车载人超过核定人数未达20%的，或者驾驶其他载客汽车载人超过核定人数20%以上的; 。。。', 1, 1, '2021-12-19 00:09:56', '2021-12-19 00:09:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1330, '车速超过规定时速50%以上的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为记分分值》机动车驾驶人有下列违法行为之一，一次记12分： （九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的；', 1, 1, '2021-12-19 00:09:56', '2021-12-19 00:09:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1331, '车速超过规定时速达到50%的一次记3分。', '', '2', '1', NULL, '', '', '', '', 'B', '《公安部令第123号》规定，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 1, 1, '2021-12-19 00:09:56', '2021-12-19 00:09:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1332, '驾驶机动车在高速公路违法占用应急车道行驶的一次记6分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为记分分值》机动车驾驶人有下列违法行为之一，一次记6分： (八)驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的； 。。。', 1, 1, '2021-12-19 00:09:56', '2021-12-19 00:09:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1333, '驾驶机动车不按照规定避让校车的，一次记6分。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》规定，机动车驾驶人有下列违法行为之一，一次记6分： (十四)驾驶机动车不按照规定避让校车的。', 1, 1, '2021-12-19 00:09:57', '2021-12-19 00:09:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1334, '已注册登记的小型载客汽车有下列哪种情形，所有人不需要办理变更登记？', '', '1', '1', NULL, '机动车更换发动机', '加装前后防撞装置', '改变车身颜色', '更换车身或者车架', 'B', '《道路交通安全法实施条例》第六条：已注册登记的机动车有下列情形之一的，机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记： (一)改变机动车车身颜色的； (二)更换发动机的； (三)更换车身或者车架的； (四)因质量有问题，制造厂更换整车的； (五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的； (六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。 注意题目说的是 [不需要办理变更登记的] ，结合法规和题目要求，更换发动机、改变车身颜色、更换车身或者车架都需要办理变更登记，而B项加装前后防撞装置是不需要办理变更登记的，因此本题答案选B。', 1, 1, '2021-12-19 00:09:57', '2021-12-19 00:09:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1335, '机动车达到国家规定的强制报废标准的不能办理注册登记。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车登记规定》第九条，有下列情形之一的，不予办理注册登记： （七）机动车达到国家规定的强制报废标准的；', 1, 1, '2021-12-19 00:09:57', '2021-12-19 00:09:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1336, '机动车行驶证灭失、丢失，机动车所有人要向登记地车辆管理所申请补领、换领。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 1, '2021-12-19 00:09:58', '2021-12-19 00:09:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1337, '机动车号牌损毁，机动车所有人要向登记地车辆管理所申请补领、换领。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车登记规定》（公安部124号令）中第44条 ：机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。', 1, 1, '2021-12-19 00:09:58', '2021-12-19 00:09:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1338, '初次申领的机动车驾驶证的有效期为多少年？', '', '1', '1', NULL, '3年', '5年', '6年', '12年', 'C', '机动车驾驶证有效期首次申领为6年，6年有效期内每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证；在机动车驾驶证的10年有效期内，每个记分周期均未达到12分的，换发长期有效的机动车驾驶证。', 1, 1, '2021-12-19 00:09:59', '2021-12-19 00:09:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1339, '准驾车型为小型汽车的，可以驾驶下列哪种车辆？', '', '1', '1', NULL, '低速载货汽车', '中型客车', '三轮摩托车', '轮式自行机械', 'A', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，低速载货汽车，三轮汽车。', 1, 1, '2021-12-19 00:09:59', '2021-12-19 00:09:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1340, '准驾车型为小型自动挡汽车的，可以驾驶以下哪种车型？', '', '1', '1', NULL, '低速载货汽车', '小型汽车', '二轮摩托车', '轻型自动挡载货', 'D', '小型汽车（C1）准驾车型： 小型、微型载客汽车以及轻型、微型载货汽车；轻、小、微型专项作业车。 小型自动挡汽车的准驾车型只能是自动挡的小型、微型载客汽车以及轻型、微型自动挡载货汽车。', 1, 1, '2021-12-19 00:09:59', '2021-12-19 00:09:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1341, '驾驶人在机动车驾驶证的6年有效期内，每个记分周期均未达到12分的，换发10年有效期的机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第五十六条，机动车驾驶人在机动车驾驶证的六年有效期内，每个记分周期均未记满12分的，换发十年有效期的机动车驾驶证；在机动车驾驶证的十年有效期内，每个记分周期均未记满12分的，换发长期有效的机动车驾驶证。', 1, 1, '2021-12-19 00:09:59', '2021-12-19 00:09:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1342, '机动车驾驶证有效期分为6年、10年、20年。', '', '2', '1', NULL, '', '', '', '', 'B', '《机动车驾驶证申领和使用规定》第十一条，机动车驾驶证有效期分为六年、十年和长期。', 1, 1, '2021-12-19 00:10:00', '2021-12-19 00:10:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1343, '准驾车型为小型汽车的，可以驾驶小型自动挡载客汽车。', '', '2', '1', NULL, '', '', '', '', 'A', '小型汽车（C1）准驾车型：小型、微型载客汽车以及轻型、微型载货汽车；轻型、微型专项作业车，小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车，低速载货汽车，三轮汽车。', 1, 1, '2021-12-19 00:10:00', '2021-12-19 00:10:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1344, '准驾车型为小型自动挡汽车的，可以驾驶低速载货汽车。', '', '2', '1', NULL, '', '', '', '', 'B', '小型自动挡汽车（C2）准驾车型：小型、微型自动挡载客汽车以及轻型、微型自动挡载货汽车。低速载货汽车不在C2驾驶证的使用范围内，因此，题干表述错误。', 1, 1, '2021-12-19 00:10:00', '2021-12-19 00:10:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1345, '初次申领的机动车驾驶证的有效期为6年。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第九条：机动车驾驶证有效期分为六年、十年和长期。', 1, 1, '2021-12-19 00:10:01', '2021-12-19 00:10:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1346, '初次申领的机动车驾驶证的有效期为4年。', '', '2', '1', NULL, '', '', '', '', 'B', '根据规定，初次取得驾驶证的有效期是六年，在六年有效期里的每个记分周期未达到12分的换发十年有效期驾驶证，在十年有效期里的每个记分周期未达到12分的换发长期驾驶证。', 1, 1, '2021-12-19 00:10:01', '2021-12-19 00:10:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1347, '初次申领机动车驾驶证的，可以申请下列哪种准驾车型？', '', '1', '1', NULL, '中型客车', '大型客车', '普通三轮摩托车', '牵引车', 'C', '初次申领机动车驾驶证的，可以申请准驾车型为城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式自行机械车、无轨电车、有轨电车的机动车驾驶证。', 1, 1, '2021-12-19 00:10:01', '2021-12-19 00:10:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1348, '年满20周岁，可以初次申请下列哪种准驾车型？', '', '1', '1', NULL, '大型货车', '大型客车', '中型客车', '牵引车', 'A', '根据公安部交通管理局最新措施 1、申请小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车、轻便摩托车准驾车型的，在18周岁以上; 2、申请低速载货汽车、三轮汽车、普通三轮摩托车、普通二轮摩托车或者轮式自行机械车准驾车型的，在18周岁以上，60周岁以下; 3、申请城市公交车、大型货车、无轨电车或者有轨电车准驾车型的，在20周岁以上，60周岁以下; 4、申请中型客车准驾车型的，在21周岁以上，60周岁以下; 5、申请牵引车准驾车型的，在22周岁以上，60周岁以下; 6、申请大型客车准驾车型的，在22周岁以上，60周岁以下。', 1, 1, '2021-12-19 00:10:01', '2021-12-19 00:10:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1349, '小型手动挡汽车科目二考试内容包括倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第二十五条：科目二考试内容包括小型汽车、小型自动挡汽车、残疾人专用小型自动挡载客汽车和低速载货汽车考试倒车入库、坡道定点停车和起步、侧方停车、曲线行驶、直角转弯。省级公安机关交通管理部门可以根据实际增加考试内容。请注意：2021年6月1日起，小型自动挡汽车（C2）取消科目二考试中的坡道定点停车和起步项目；手动挡汽车依然考5项。', 1, 1, '2021-12-19 00:10:02', '2021-12-19 00:10:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1350, '科目三考试分为道路驾驶技能考试和安全文明驾驶常识考试两部分。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车驾驶人考试内容分为道路交通安全法律、法规和相关知识考试科目（以下简称“科目一”）、场地驾驶技能考试科目（以下简称“科目二”）、道路驾驶技能和安全文明驾驶常识考试科目（以下简称“科目三”）。', 1, 1, '2021-12-19 00:10:02', '2021-12-19 00:10:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1351, '科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到80和90分的为合格。', '', '2', '1', NULL, '', '', '', '', 'B', '根据《机动车驾驶证申领和使用规定》第三十条，各科目考试的合格标准为： （一）科目一考试满分为100分，成绩达到90分的为合格； （二）科目二考试满分为100分，考试大型客车、牵引车、城市公交车、中型客车、大型货车准驾车型的，成绩达到90分的为合格，其他准驾车型的成绩达到80分的为合格； （三）科目三道路驾驶技能和安全文明驾驶常识考试满分分别为100分，成绩分别达到90分的为合格。', 1, 1, '2021-12-19 00:10:02', '2021-12-19 00:10:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1352, '在驾驶技能准考证明的有效期内，科目二和科目三道路驾驶技能考试预约次数不得超过多少次？', '', '1', '1', NULL, '3次', '4次', '5次', '6次', 'C', '在驾驶技能准考证明有效期内，科目二和科目三道路驾驶技能考试预约考试的次数不得超过五次。第五次预约考试仍不合格的，已考试合格的其他科目成绩作废。', 1, 1, '2021-12-19 00:10:02', '2021-12-19 00:10:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1353, '驾驶技能准考证明的有效期是多久？', '', '1', '1', NULL, '1年', '2年', '3年', '4年', 'C', '驾驶技能准考证明的有效期为三年，申请人应当在有效期内完成科目二和科目三考试。未在有效期内完成考试的，已考试合格的科目成绩作废。', 1, 1, '2021-12-19 00:10:03', '2021-12-19 00:10:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1354, '申请人因故不能按照预约时间参加考试的，应当提前一日申请取消预约，对申请人未按照预约考试时间参加考试的，判定该次考试不合格。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第四十三条，申请人因故不能按照预约时间参加考试的，应当提前一日申请取消预约。对申请人未按照预约考试时间参加考试的，判定该次考试不合格。', 1, 1, '2021-12-19 00:10:03', '2021-12-19 00:10:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1355, '机动车驾驶证遗失、损毁无法辨认时，机动车驾驶人应当向机动车驾驶证核发地车辆管理所申请补发。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第六十条具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地车辆管理所申请换证： (一)在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的; (二)机动车驾驶证损毁无法辨认的。', 1, 1, '2021-12-19 00:10:03', '2021-12-19 00:10:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1356, '有吸食、注射毒品后驾驶车辆行为的机动车驾驶人，不会被注销驾驶证。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证： （一）死亡的； （二）提出注销申请的； （三）丧失民事行为能力，监护人提出注销申请的； （四）身体条件不适合驾驶机动车的； （五）有器质性心脏病、癫痫病、美尼尔氏症、眩晕症、癔病、震颤麻痹、精神病、痴呆以及影响肢体活动的神经系统疾病等妨碍安全驾驶疾病的； （六）被查获有吸食、注射毒品后驾驶机动车行为，正在执行社区戒毒、强制隔离戒毒、社区康复措施，或者长期服用依赖性精神药品成瘾尚未戒除的；', 1, 1, '2021-12-19 00:10:04', '2021-12-19 00:10:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1357, '正在执行社区戒毒、强制隔离戒毒、社区康复措施，车辆管理所将注销其驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证： （六）被查获有吸食、注射毒品后驾驶机动车行为，正在执行社区戒毒、强制隔离戒毒、社区康复措施，或者长期服用依赖性精神药品成瘾尚未戒除的。', 1, 1, '2021-12-19 00:10:04', '2021-12-19 00:10:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1358, '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向哪个部门申请补领、换领。', '', '1', '1', NULL, '住地交警支队车辆管理所', '驾驶证核发地车辆管理所', '登记地车辆管理所', '当地公安局', 'C', '根据《机动车登记规定》第四十四条的规定，机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。', 1, 1, '2021-12-19 00:10:05', '2021-12-19 00:10:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1359, '机动车登记证书、号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向居住地车辆管理所申请补领、换领。', '', '2', '1', NULL, '', '', '', '', 'B', '根据《机动车登记规定》第四十四条的规定：机动车号牌、行驶证灭失、丢失或者损毁的，机动车所有人应当向登记地车辆管理所申请补领、换领。申请时，机动车所有人应当填写申请表并提交身份证明。 本题题干说的是“居住地车辆管理所”，是不正确的，本题答案应为错误。', 1, 1, '2021-12-19 00:10:05', '2021-12-19 00:10:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1360, '机动车购买后尚未注册登记，需要临时上道路行驶的，可以凭什么临时上道路行驶？', '', '1', '1', NULL, '合法来源凭证', '临时行驶车号牌', '借用的机动车号牌', '法人单位证明', 'B', '《道路交通安全法》第八条：国家对机动车实行登记制度。机动车经公安机关交通管理部门登记后，方可上道路行驶。尚未登记的机动车，需要临时上道路行驶的，应当取得临时通行牌证。', 1, 1, '2021-12-19 00:10:05', '2021-12-19 00:10:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1361, '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的（被撤销的），申请人在多长时间内不得再次申领机动车驾驶证。', '', '1', '1', NULL, '6个月', '1年', '2年', '3年', 'D', '《机动车驾驶证申领和使用规定》第八十八条，申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，申请人在三年内不得再次申领机动车驾驶证。【骗3】', 1, 1, '2021-12-19 00:10:05', '2021-12-19 00:10:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1362, '申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效。', '', '2', '1', NULL, '', '', '', '', 'A', '申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。', 1, 1, '2021-12-19 00:10:06', '2021-12-19 00:10:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1363, '驾驶机动车跨越双实线行驶属于什么行为？', '', '1', '1', NULL, '违章行为', '违法行为', '过失行为', '违规行为', 'B', '《道路交通安全法》第四十四条　机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。 路面双实线属于禁止标线，机动车禁止跨越。因此，违反道路交通安全法的行为属于违法行为。', 1, 1, '2021-12-19 00:10:06', '2021-12-19 00:10:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1364, '驾驶机动车在人行横道上临时停车属于违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》规定机动车禁止在人行横道线上停车，通过人行横道时应减速慢行，遇到行人需停车礼让。违反交通安全法就是违法行为。', 1, 1, '2021-12-19 00:10:06', '2021-12-19 00:10:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1365, '在行驶过程中，机动车驾驶人要注意与前车保持安全距离。', '', '2', '1', NULL, '', '', '', '', 'A', '后车与前车保持安全距离是很重要的，因为安全距离很小的话，在刹车的时候，由于制动距离不够，就会发生碰撞。所以要保持足够的安全距离。', 1, 1, '2021-12-19 00:10:06', '2021-12-19 00:10:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1366, '机动车、非机动车和行人实行分道行驶，是为了规范交通秩序，提高通行效率。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。规范交通秩序，提高通行效率。', 1, 1, '2021-12-19 00:10:07', '2021-12-19 00:10:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1367, '专用车道规定的专用使用时间之外，其他车辆可以进入专用车道行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '在规定时间内，道路划设为专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶；在规定时间之外，其他车辆可以行驶。', 1, 1, '2021-12-19 00:10:07', '2021-12-19 00:10:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1368, '其他车辆不准进入专用车道行驶，其目的是为了不影响专用车的正常通行。', '', '2', '1', NULL, '', '', '', '', 'A', '其他车辆不准进入专用车道行驶，其目的是为了不影响专用车的正常通行。根据《道路交通安全法》 第三十七条：道路划设专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶。', 1, 1, '2021-12-19 00:10:07', '2021-12-19 00:10:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1369, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪。', '', '1', '1', NULL, '未及时报警', '未抢救受伤人员', '严重超载驾驶的', '未带驾驶证', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以交通肇事罪定罪处罚： (一)酒后、吸食毒品后驾驶机动车辆的; (二)无驾驶资格驾驶机动车辆的; (三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; (四)明知是无牌证或者已报废的机动车辆而驾驶的; (五)严重超载驾驶的; (六)为逃避法律追究逃离事故现场的。', 1, 1, '2021-12-19 00:10:08', '2021-12-19 00:10:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1370, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪。', '', '1', '1', NULL, '未报警', '未抢救受伤人员', '酒后、吸食毒品后驾驶机动车辆的', '未带驾驶证', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以交通肇事罪定罪处罚： (一)酒后、吸食毒品后驾驶机动车辆的; (二)无驾驶资格驾驶机动车辆的; (三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; (四)明知是无牌证或者已报废的机动车辆而驾驶的; (五)严重超载驾驶的; (六)为逃避法律追究逃离事故现场的。', 1, 1, '2021-12-19 00:10:08', '2021-12-19 00:10:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1371, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪。', '', '1', '1', NULL, '未带驾驶证', '未报警', '无驾驶资格驾驶机动车辆的', '未抢救受伤人员', 'C', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以交通肇事罪定罪处罚： (一)酒后、吸食毒品后驾驶机动车辆的; (二)无驾驶资格驾驶机动车辆的; (三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; (四)明知是无牌证或者已报废的机动车辆而驾驶的; (五)严重超载驾驶的; (六)为逃避法律追究逃离事故现场的。', 1, 1, '2021-12-19 00:10:08', '2021-12-19 00:10:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1372, '交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列哪种行为的，构成交通肇事罪。', '', '1', '1', NULL, '未抢救受伤人员', '未带驾驶证', '未报警', '为逃避法律追究逃离事故现场的', 'D', '根据《关于审理交通肇事刑事案件具体应用法律若干问题的解释》：交通肇事致一人以上重伤，负事故全部或者主要责任，并具有下列情形之一的，以交通肇事罪定罪处罚： (一)酒后、吸食毒品后驾驶机动车辆的; (二)无驾驶资格驾驶机动车辆的; (三)明知是安全装置不全或者安全机件失灵的机动车辆而驾驶的; (四)明知是无牌证或者已报废的机动车辆而驾驶的; (五)严重超载驾驶的; (六)为逃避法律追究逃离事故现场的。', 1, 1, '2021-12-19 00:10:09', '2021-12-19 00:10:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1373, '年龄在70周岁以上，在一个记分周期结束后一年内未提交身体条件证明的，其机动车驾驶证将会被车辆管理所注销。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第三十条规定：年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后三十日内，提交县级或者部队团级以上医疗机构出具的有关身体条件的证明。在一个记分周期结束后一年内未提交身体条件证明的，车辆管理所应当注销其机动车驾驶证。', 1, 1, '2021-12-19 00:10:09', '2021-12-19 00:10:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1374, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人从业单位等信息发生变化的，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第八十条，机动车驾驶人联系电话、联系地址等信息发生变化，以及持有大型客车、牵引车、城市公交车、中型客车、大型货车准驾车型驾驶证的驾驶人从业单位等信息发生变化，应在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', 1, 1, '2021-12-19 00:10:09', '2021-12-19 00:10:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1375, '持有小型汽车驾驶证的驾驶人，发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验，同时应当申报身体条件情况。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》（公安部令第123号）第六十条 小型汽车驾驶人发生交通事故造成人员死亡承担同等以上责任未被吊销机动车驾驶证的，应当在本记分周期结束后三十日内到公安机关交通管理部门接受审验。 机动车驾驶人可以在机动车驾驶证核发地或者核发地以外的地方参加审验、提交身体条件证明。', 1, 1, '2021-12-19 00:10:09', '2021-12-19 00:10:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1376, '发动机号码、车辆识别代号因磨损、锈蚀、事故等原因辨认不清或者损坏的，可以向登记地车辆管理所申请备案。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车登记规定》：发动机号码、车辆识别代号因磨损、锈蚀、事故等原因辨认不清或者损坏的，可以向登记地车辆管理所申请备案。', 1, 1, '2021-12-19 00:10:10', '2021-12-19 00:10:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1377, '机动车登记证书丢失后应及时补办，避免被不法分子利用。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车登记证书丢失后，一旦被他人非法利用很容易造成重大损失，一定要在丢失后及时补办。', 1, 1, '2021-12-19 00:10:10', '2021-12-19 00:10:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1378, '驾驶机动车发生交通事故未造成人身伤亡的，责任明确双方无争议时，应当如何处置？', '', '1', '1', NULL, '保护好现场再协商', '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', 'D', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。', 1, 1, '2021-12-19 00:10:10', '2021-12-19 00:10:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1379, '驾驶机动车发生交通事故后当事人故意破坏、伪造现场、毁灭证据的，应当承担什么责任？', '', '1', '1', NULL, '主要责任', '次要责任', '同等责任', '全部责任', 'D', '发生交通事故后当事人逃逸的，故意破坏、伪造现场、毁灭证据的，应当承担全部责任。', 1, 1, '2021-12-19 00:10:11', '2021-12-19 00:10:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1380, '非机动车驾驶人、行人故意碰撞机动车造成交通事故的，机动车一方不承担赔偿责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十六条：明确规定交通事故的损失是由非机动车驾驶人、行人故意碰撞机动车造成的，机动车一方不承担赔偿责任。', 1, 1, '2021-12-19 00:10:11', '2021-12-19 00:10:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1381, '如图所示，红圈中标记车辆使用灯光的方法是正确的。', '/uploadFile/yuanBeiTu381.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十八条规定：在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯。 因此，红圈中标记车辆使用远光灯的做法是错误的。', 1, 1, '2021-12-19 00:10:11', '2021-12-19 00:10:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1382, '驾驶机动车超车时，可以鸣喇叭替代开启转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十七条 机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下，应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。', 1, 1, '2021-12-19 00:10:11', '2021-12-19 00:10:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1383, '驾驶机动车变更车道时，以下做法正确的是什么？', '', '1', '1', NULL, '开启转向灯的同时变更车道', '在道路同方向划有2条以上机动车道的，不得影响相关车道内行驶的机动车的正常行驶', '在车辆较少路段，可以随意变更车道', '遇前方道路拥堵，可以向应急车道变更', 'B', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶。', 1, 1, '2021-12-19 00:10:12', '2021-12-19 00:10:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1384, '驾驶机动车应在变更车道的同时开启转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车在变更车道时，应当提前开启转向灯，夜间还需变换使用远、近光灯或者鸣喇叭；在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶，安全变更车道后关闭转向灯。', 1, 1, '2021-12-19 00:10:12', '2021-12-19 00:10:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1385, '如图所示，A车可以从左侧超越B车。', '/uploadFile/yuanBeiTu385.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，路中心黄色虚实线，实线一侧禁止车辆越线或压线行驶。因此，A车不具备超车条件，不可以从左侧超越B车。', 1, 1, '2021-12-19 00:10:12', '2021-12-19 00:10:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1386, '如图所示，驾驶机动车行驶至桥梁涵洞时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu386.gif', '1', '1', NULL, '加速，在对向车到达前通过', '减速靠右通过', '保持原速继续正常行驶', '鸣喇叭后加速通过', 'B', '如图所示，一辆车已经行至涵洞里了，而涵洞较窄，所以我方车辆应该减速靠右让对向车辆先行。', 1, 1, '2021-12-19 00:10:13', '2021-12-19 00:10:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1387, '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？', '/uploadFile/yuanBeiTu387.gif', '1', '1', NULL, '减速慢行、鸣喇叭示意', '为拓宽视野，临时占用左侧车道行驶', '加速行驶', '停车观察', 'A', '根据《道路交通安全法实施条例》第五十九条规定：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时，应当减速慢行，并鸣喇叭示意。', 1, 1, '2021-12-19 00:10:13', '2021-12-19 00:10:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1388, '驾驶机动车在隧道内行驶时，可以临时停车。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车在交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 1, 1, '2021-12-19 00:10:13', '2021-12-19 00:10:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1389, '发生交通事故后，当事人故意破坏、伪造现场、毁灭证据的，承担全部责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第九十二条 发生交通事故后当事人逃逸的，逃逸的当事人承担全部责任。但是，有证据证明对方当事人也有过错的，可以减轻责任。当事人故意破坏、伪造现场、毁灭证据的，承担全部责任。', 1, 1, '2021-12-19 00:10:13', '2021-12-19 00:10:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1390, '机动车驾驶证补领后，以下说法正确的是什么？', '', '1', '1', NULL, '原驾驶证继续使用', '原驾驶证作废，不得继续使用', '原驾驶证特殊情况下使用', '替换使用', 'B', '《机动车驾驶证申领和使用规定》：机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', 1, 1, '2021-12-19 00:10:14', '2021-12-19 00:10:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1391, '机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》：机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。', 1, 1, '2021-12-19 00:10:14', '2021-12-19 00:10:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1392, '超过机动车驾驶证有效期一年以上未换证被注销，但未超过2年的，机动车驾驶人应当如何恢复驾驶资格？', '', '1', '1', NULL, '参加道路交通安全法律、法规和相关知识考试合格后', '参加场地考试合格后', '参加道路驾驶技能考试合格后', '参加安全文明驾驶常识考试合格后', 'A', '《机动车驾驶证申领和使用规定》第七十七条机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证：（七）超过机动车驾驶证有效期一年以上未换证的； 有第一款第七项情形被注销机动车驾驶证未超过二年的，机动车驾驶人参加道路交通安全法律、法规和相关知识考试合格后，可以恢复驾驶资格。', 1, 1, '2021-12-19 00:10:14', '2021-12-19 00:10:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1393, '已注册登记的机动车达到国家规定的强制报废标准的，应当向登记地车辆管理所申请注销登记。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法》第二章第十四条规定：应当报废的机动车必须及时办理注销登记。达到报废标准的机动车不得上道路行驶。报废的大型客、货车及其他营运车辆应当在公安机关交通管理部门的监督下解体。', 1, 1, '2021-12-19 00:10:14', '2021-12-19 00:10:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1394, '存在以下哪种行为的申请人在一年内不得再次申领机动车驾驶证？', '', '1', '1', NULL, '在考试过程中出现身体不适', '在考试过程中有舞弊行为', '不能按照教学大纲认真练习驾驶技能', '未参加理论培训', 'B', '《机动车驾驶证申领和使用规定》第六章第七十八条：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。', 1, 1, '2021-12-19 00:10:15', '2021-12-19 00:10:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1395, '驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第五十八条，机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。申请时应当填写申请表，提交机动车驾驶人的身份证明和机动车驾驶证，并申报身体条件情况。', 1, 1, '2021-12-19 00:10:15', '2021-12-19 00:10:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1396, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，记分周期内有记分的，应当在记分周期结束后三十日内到公安机关交通管理部门接受审验，同时还应当申报身体条件情况。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》规定：持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在每个记分周期结束后三十日内到公安机关交通管理部门接受审验。但在一个记分周期内没有记分记录的，免予本记分周期审验。', 1, 1, '2021-12-19 00:10:15', '2021-12-19 00:10:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1397, '如图所示，夜间驾驶机动车遇对方使用远光灯，无法看清前方路况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu397.gif', '1', '1', NULL, '保持行驶方向和车速不变', '自己也打开远光灯行驶', '降低车速，谨慎会车', '加速通过，尽快摆脱眩目光线', 'C', '夜间会车遇对向车辆使用远光灯，无法看清前方路况时，要及时减速靠右侧行驶或停车让行，以防发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 1, 1, '2021-12-19 00:10:16', '2021-12-19 00:10:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1398, '如图所示，驾驶机动车经过这种道路时，如果前方没有其他交通参与者，可在道路上随意通行。', '/uploadFile/yuanBeiTu398.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。 因此，驾驶机动车在如图所示道路上随意通行做法错误。', 1, 1, '2021-12-19 00:10:16', '2021-12-19 00:10:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1399, '如图所示，驾驶机动车直行遇前方道路堵塞时，车辆可以在黄色网格线区域临时停车等待，但不得在人行横道停车。', '/uploadFile/yuanBeiTu399.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车遇有路口堵塞时，车辆既不可在黄色网格线区域临时停车等待，也不可在人行横道停车。即便是绿灯亮，也要在路口外停车等待，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 1, 1, '2021-12-19 00:10:16', '2021-12-19 00:10:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1400, '如图所示，驾驶机动车在路口遇到这种交通信号时，右转弯的车辆在不妨碍被放行的车辆、行人的情况下，可以通行。', '/uploadFile/yuanBeiTu400.gif', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车在路口遇到这种圆形交通信号灯时，右转弯的车辆在不妨碍被放行的车辆、行人的情况下，可以通行。', 1, 1, '2021-12-19 00:10:17', '2021-12-19 00:10:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1401, '如图所示，驾驶机动车遇到右侧车辆强行变道，应减速慢行，让右前方车辆顺利变道。', '/uploadFile/yuanBeiTu401.gif', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车遇右侧车辆强行变道，占据自己车道时，要减速或靠右停车避让，千万不要开赌气车。', 1, 1, '2021-12-19 00:10:17', '2021-12-19 00:10:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1402, '驾驶机动车行经漫水路或者漫水桥时，应当停车察明水情。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通管理条例》的第四十六条规定：车辆行经漫水路或漫水桥时,必须停车察明水情，确认安全后，低速通过。一定要察明水情，不要冒险行车！', 1, 1, '2021-12-19 00:10:17', '2021-12-19 00:10:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1403, '如图所示，驾驶机动车遇到这种情况，以下做法正确的是什么？', '/uploadFile/yuanBeiTu403.gif', '1', '1', NULL, '长鸣喇叭催促行人快速通过', '开启远光灯警示行人有车辆驶近', '降低行驶速度，避让行人', '适当加速从行人前方绕行', 'C', '机动车进出道路，遇行人横穿道路时，应当及时减速或停车避让。', 1, 1, '2021-12-19 00:10:17', '2021-12-19 00:10:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1404, '当驾驶人的血液中酒精含量为100毫克/100毫升时，属于醉酒驾驶。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶人的血液中酒精含量在80毫克/100毫升（含）以上属于醉酒驾驶。100毫克/100毫升高于80毫克/100毫升，按规定属于醉酒驾驶。题干描述正确，本题答案为对。', 1, 1, '2021-12-19 00:10:18', '2021-12-19 00:10:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1405, '驾驶机动车在道路上发生交通事故，任何情况下都应标明现场位置后，先行撤离现场。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第七十条　在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, '2021-12-19 00:10:18', '2021-12-19 00:10:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1406, '申请人有下列哪种行为，三年内不得再次申领机动车驾驶证？', '', '1', '1', NULL, '实习期记满12分，注销驾驶证的', '申请人在考试过程中有舞弊行为的', '申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的', '申请人未能在培训过程中认真练习的', 'C', '《机动车驾驶证申领和使用规定》第八十八条，申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，申请人在三年内不得再次申领机动车驾驶证。【骗3】', 1, 1, '2021-12-19 00:10:18', '2021-12-19 00:10:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1407, '申请人患有精神病的，可以申领机动车驾驶证，但是在发病期间不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《机动车驾驶证申领和使用规定》第十二条有下列情形之一的，不得申请机动车驾驶证：(一)有器质性心脏病、癫痫病、美尼尔氏症、眩晕症、癔病、震颤麻痹、精神病、痴呆以及影响肢体活动的神经系统疾病等妨碍安全驾驶疾病的;(二)三年内有吸食、注射毒品行为或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的;(三)造成交通事故后逃逸构成犯罪的;（四）申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的；(五)醉酒驾驶营运机动车依法被吊销机动车驾驶证未满十年的。', 1, 1, '2021-12-19 00:10:18', '2021-12-19 00:10:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1408, '在暂住地初次申领机动车驾驶证的，不能直接申领大型货车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'B', '根据《机动车驾驶证申领和使用规定》第十四条规定：初次申领机动车驾驶证的，可以申请准驾车型为城市公交车、大型货车、小型汽车、小型自动挡汽车、低速载货汽车、三轮汽车、残疾人专用小型自动挡载客汽车、普通三轮摩托车、普通二轮摩托车、轻便摩托车、轮式自行机械车、无轨电车、有轨电车的机动车驾驶证。因此，题干表述错误。', 1, 1, '2021-12-19 00:10:19', '2021-12-19 00:10:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1409, '机动车驾驶证有效期超过一年以上未换证的，驾驶证将被注销。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第四十二条机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证:(一)死亡的;(二)身体条件不适合驾驶机动车的;(三)提出注销申请的;(四)丧失民事行为能力，监护人提出注销申请的;(五)超过机动车驾驶证有效期一年以上未换证的。', 1, 1, '2021-12-19 00:10:19', '2021-12-19 00:10:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1410, '驾驶证审验内容不包括以下哪一项？', '', '1', '1', NULL, '道路交通安全违法行为、交通事故处理情况', '身体条件情况', '道路交通安全违法行为记分及记满12分后参加学习和考试情况', '机动车检验情况', 'D', '驾驶证审验内容包括：道路交通安全违法行为、交通事故处理情况；身体条件情况；道路交通安全违法行为记分及记满12分后参加学习和考试情况。', 1, 1, '2021-12-19 00:10:19', '2021-12-19 00:10:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1411, '驾驶机动车在道路上发生交通事故，当事人不能自行移动车辆的，应当保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条，在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', 1, 1, '2021-12-19 00:10:20', '2021-12-19 00:10:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1412, '在交叉路口、隧道内均不能倒车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通交通法》规定：机动车在交叉路口、隧道内均不能倒车。', 1, 1, '2021-12-19 00:10:20', '2021-12-19 00:10:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1413, '驾驶机动车发生以下交通事故，哪种情况适用自行协商解决？', '', '1', '1', NULL, '对方饮酒的', '对事实及成因有争议的', '未造成人身伤亡，对事实及成因无争议的', '造成人身伤亡的', 'C', '《道路交通安全法实施条例》第八十六条规定 机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场，自行协商损害赔偿事宜。当事人对交通事故事实及成因有争议的，应当迅速报警。', 1, 1, '2021-12-19 00:10:20', '2021-12-19 00:10:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1414, '夜间驾驶机动车在没有中心隔离设施或者没有中心线的道路上行驶，以下哪种情况下应当改用近光灯？', '', '1', '1', NULL, '接近没有交通信号灯控制的交叉路口时', '与对向机动车会车时', '接近人行横道时', '城市道路照明条件不良时', 'B', '《道路交通安全法实施条例》第四十八条 在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：(五)夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 1, 1, '2021-12-19 00:10:20', '2021-12-19 00:10:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1415, '如图所示，驾驶机动车遇前方车流行驶缓慢时，借用公交专用道超车是正确的。', '/uploadFile/yuanBeiTu415.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车遇有前方机动车停车排队等候或者缓慢行驶的，应当依次排队等候或缓慢行驶，不得借公交专用道超车。 《道路交通安全法》第三十七条　道路划设专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶。', 1, 1, '2021-12-19 00:10:21', '2021-12-19 00:10:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1416, '如图所示，驾驶机动车行驶至车道减少的路段时，遇前方机动车排队等候或行驶缓慢时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu416.gif', '1', '1', NULL, '右侧车让左侧车先行', '每车道一辆依次交替驶入左侧车道', '左侧车让右侧车先行', '右侧车寻找空隙提前进入左侧车道', 'B', '如图所示，前方有右侧变窄警告标志，表示前方右侧车行道或路面变窄。因此正确的做法是：每车道一辆依次交替驶入左侧车道。 《道路交通安全法》第四十五条，在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当依次交替通行。', 1, 1, '2021-12-19 00:10:21', '2021-12-19 00:10:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1417, '机动车驾驶证遗失的，机动车驾驶人应当向哪里的车辆管理所申请补发？', '', '1', '1', NULL, '核发地', '暂住地', '居住地', '户籍地', 'A', '根据 《机动车驾驶证申领和使用规定》第六十三条 机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地或者核发地以外的车辆管理所申请补发。', 1, 1, '2021-12-19 00:10:21', '2021-12-19 00:10:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1418, '机动车驾驶证被依法扣押、扣留、暂扣期间能否申请补发？', '', '1', '1', NULL, '可以申请', '扣留期间可以临时申请', '暂扣期间可以临时申请', '不得申请补发', 'D', '《机动车驾驶证申领和使用规定》第六十三条机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地或者核发地以外的车辆管理所申请补发。 机动车驾驶证被依法扣押、扣留或者暂扣期间，机动车驾驶人不得申请补发。', 1, 1, '2021-12-19 00:10:21', '2021-12-19 00:10:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1419, '驾驶证丢失后，驾驶人可以继续驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶证丢失后，驾驶人不可继续驾驶机动车，应该向核发地车辆管理所申请补发。', 1, 1, '2021-12-19 00:10:22', '2021-12-19 00:10:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1420, '已注册登记的机动车，改变机动车车身颜色的应到公安交通管理部门申请变更登记。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路运输条例》规定：已注册登记的机动车，改变机动车车身颜色的应到公安交通管理部门申请变更登记。', 1, 1, '2021-12-19 00:10:22', '2021-12-19 00:10:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1421, '如图所示，驾驶机动车经过这种道路时，应降低车速在道路中间通行。', '/uploadFile/yuanBeiTu421.gif', '2', '1', NULL, '', '', '', '', 'A', '图中道路两侧都有行人，驾驶机动车经过这种道路时，为了保障行人安全，需要降低车速在道路中间通行。', 1, 1, '2021-12-19 00:10:22', '2021-12-19 00:10:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1422, '如图所示，驾驶机动车遇到这种情况，可以轻按喇叭提醒前方非机动车和行人后方有来车。', '/uploadFile/yuanBeiTu422.gif', '2', '1', NULL, '', '', '', '', 'A', '行车中遇前方有行人和非机动车占道行驶，当超越他们时，应当提前请按喇叭提示，减速慢行，并随时准备停车。', 1, 1, '2021-12-19 00:10:23', '2021-12-19 00:10:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1423, '驾驶机动车通过未设置交通信号灯的交叉路口时，下列说法错误的是什么？', '', '1', '1', NULL, '转弯的机动车让直行的车辆、行人先行', '没有交通标志、标线控制时，在进入路口前停车瞭望，让右方道路的来车先行', '相对方向行驶的右转弯机动车让左转弯的车辆先行', '相对方向行驶的左转弯机动车让右转弯的车辆先行', 'D', '请注意：题干要求选出说法错误的选项。根据《道路交通安全法实施条例》第五十二条规定，驾驶机动车通过未设置交通信号灯的交叉路口时：转弯的机动车应当让直行的车辆、行人先行；没有交通标志、标线控制时，在进入路口前停车瞭望，让右方道路的来车先行；相对方向行驶的右转弯机动车让左转弯的车辆先行。【谨记：小弯让大弯；转弯让直行】', 1, 1, '2021-12-19 00:10:23', '2021-12-19 00:10:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1424, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu424.gif', '1', '1', NULL, '应停车察明水情，确认安全后，低速通过', '应停车察明水情，确认安全后，快速通过', '应减速观察水情，然后加速行驶通过', '可随意通行', 'A', '如图所示，过水路面警告标志，提醒前方是过水路面或漫水桥路段，应停车察明水情，确认安全后，低速通过。', 1, 1, '2021-12-19 00:10:23', '2021-12-19 00:10:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1425, '以下哪项行为可构成危险驾驶罪？', '', '1', '1', NULL, '闯红灯', '无证驾驶', '疲劳驾驶', '醉驾', 'D', '根据《刑法》第一百三十三条的规定：危险驾驶罪是指在道路上驾驶机动车追逐竞驶，情节恶劣的；醉酒驾驶机动车的；从事校车业务或者旅客运输，严重超过定额乘员载客，或者严重超过规定时速行驶的；违反危险化学品安全管理规定运输危险化学品，危害公共安全的行为。', 1, 1, '2021-12-19 00:10:23', '2021-12-19 00:10:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1426, '机动车发生轻微财产损失的交通事故，对应当自行撤离现场而未撤离的，交通警察有权责令当事人撤离现场。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法》中第七十条第二、三款规定： 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 机动车发生轻微财产损失的交通事故，对应当自行撤离现场而未撤离的，交通警察有权责令当事人撤离现场。', 1, 1, '2021-12-19 00:10:24', '2021-12-19 00:10:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1427, '实习期驾驶人驾驶机动车上高速公路行驶，以下做法正确的是什么？', '', '1', '1', NULL, '任何情况下都不允许上高速', '不需要其他人员陪同', '需要持有相应或者更高准驾车型驾驶证三年以上的驾驶人陪同', '需要持有相应或者更高准驾车型驾驶证、同在实习期内的驾驶人陪同', 'C', '《机动车驾驶证申领和使用规定》第七十五条规定：驾驶人在实习期内驾驶机动车上高速公路行驶,应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。其中,驾驶残疾人专用小型自动挡载客汽车的,可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', 1, 1, '2021-12-19 00:10:24', '2021-12-19 00:10:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1428, '年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查的目的是什么？', '', '1', '1', NULL, '体现对老年人的关心', '例行程序仅供参考', '检查是否患有老年常见病', '检查是否患有妨碍安全驾驶的疾病', 'D', '为了确保高龄驾驶员没有患有妨碍安全驾驶的疾病，消除潜在的道路交通安全隐患，需要每年提交身体报告。', 1, 1, '2021-12-19 00:10:24', '2021-12-19 00:10:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1429, '年龄在50周岁以上的机动车驾驶人，应当每年进行一次身体检查，并向公安机关交通管理部门申报身体条件情况。', '', '2', '1', NULL, '', '', '', '', 'B', '修改后的《机动车驾驶证申领和使用规定》(公安部令第139号)中第三十条规定：年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后三十日内，提交符合健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心等医疗机构出具的有关身体条件的证明。', 1, 1, '2021-12-19 00:10:24', '2021-12-19 00:10:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1430, '机动车驾驶人联系电话、联系地址等信息发生变化，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', '', '2', '1', NULL, '', '', '', '', 'A', '在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的；机动车驾驶证损毁无法辨认的，机动车驾驶人应当在30日内到机动车驾驶证核发地或者核发地以外的车辆管理所备案申请换证。', 1, 1, '2021-12-19 00:10:24', '2021-12-19 00:10:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1431, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、从业单位等信息发生变化未及时申报变更信息的，公安机关交通管理部门处二十元以上二百元以下罚款。', '', '2', '1', NULL, '', '', '', '', 'A', '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人联系电话、从业单位等信息发生变化未及时申报变更信息的，公安机关交通管理部门处二十元以上二百元以下罚款。机动车驾驶人联系电话、联系地址等信息发生变化，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', 1, 1, '2021-12-19 00:10:25', '2021-12-19 00:10:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1432, '已注册登记的机动车，机动车所有人住所在车辆管理所管辖区域内迁移或者机动车所有人姓名（单位名称）、联系方式变更的，应当向登记地车辆管理所备案。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车登记规定》第十七条规定：已注册登记的机动车，机动车所有人住所在车辆管理所管辖区域内迁移或者机动车所有人姓名(单位名称)、联系方式变更的，应当向登记地车辆管理所备案。', 1, 1, '2021-12-19 00:10:25', '2021-12-19 00:10:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1433, '驾驶机动车发生交通事故，仅造成财产损失的，但是对交通事故事实及成因有争议的，应当怎么处理？', '', '1', '1', NULL, '迅速报警', '占道继续和对方争辩', '找中间人帮忙解决', '自行协商损害赔偿事宜', 'A', '对于事故成因不明的交通事故，应及时报警，交由警察处理。', 1, 1, '2021-12-19 00:10:25', '2021-12-19 00:10:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1434, '如图所示，驾驶机动车驶出这个路口时应当怎样使用灯光?', '/uploadFile/yuanBeiTu434.gif', '1', '1', NULL, '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', 'A', '驾驶机动车驶出环岛前，打开右转向灯，注意观察右侧车辆、行人的动态。在有两条或两条以上车道的环岛驶出时，应提前开启右转向灯变更至外侧车道，严禁直接从内侧车道驶出环岛。因此，图中机动车，驶出环岛时应当开启右转向灯。', 1, 1, '2021-12-19 00:10:25', '2021-12-19 00:10:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1435, '驾驶机动车在道路上掉头时，应当提前开启左转向灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十七条机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 1, 1, '2021-12-19 00:10:26', '2021-12-19 00:10:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1436, '驾驶机动车在交叉路口前变更车道时，应在进入实线区后，开启转向灯，变更车道。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在交叉路口前变更车道时，需按导向箭头的指示在虚线区变更车道，进入实线区后不能变更车道。', 1, 1, '2021-12-19 00:10:26', '2021-12-19 00:10:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1437, '如图所示，A车具有优先通行权。', '/uploadFile/yuanBeiTu437.gif', '2', '1', NULL, '', '', '', '', 'A', '根据《道路交通安全法实施条例》：第五十二条 机动车通过没有交通信号灯控制也没有交通警察指挥的交叉路口，应当遵守下列规定： （一）有交通标志、标线控制的，让优先通行的一方先行； （二）没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行； （三）转弯的机动车让直行的车辆先行； （四）相对方向行驶的右转弯的机动车让左转弯的车辆先行。 根据“路口直行，右方道路车辆先行”通行原则，A车在右方道路，故A车具有优先通行权。', 1, 1, '2021-12-19 00:10:26', '2021-12-19 00:10:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1438, '如图所示，驾驶机动车在路口前遇到这种情况时，A车具有优先通行权。', '/uploadFile/yuanBeiTu438.gif', '2', '1', NULL, '', '', '', '', 'A', '道路上行驶时，右转弯让左转弯，转弯让直行。因此图中的A车具有优先通行权。', 1, 1, '2021-12-19 00:10:27', '2021-12-19 00:10:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1439, '驾驶机动车遇前方交叉路口交通阻塞时，路口内无网状线的，可停在路口内等候。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十三条：机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。', 1, 1, '2021-12-19 00:10:27', '2021-12-19 00:10:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1440, '驾驶机动车在高速公路上行驶，错过出口时，如果确认后方无来车，可以倒回出口驶离高速公路。', '', '2', '1', NULL, '', '', '', '', 'B', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，不得紧急制动；即便是刚刚错过出口且距下一出口很远，也不得掉头逆行或倒回出口。', 1, 1, '2021-12-19 00:10:27', '2021-12-19 00:10:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1441, '发生交通事故时，下列哪种情况下当事人应当保护现场并立即报警？', '', '1', '1', NULL, '未造成人员伤亡的', '未发生财产损失事故', '未损害公共设施及建筑物的', '驾驶人有酒后驾驶嫌疑的', 'D', '酒后驾驶属违法行为，应及时报警。', 1, 1, '2021-12-19 00:10:27', '2021-12-19 00:10:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1442, '下列哪种情况可以向机动车驾驶证核发地车辆管理所申请补发?', '', '1', '1', NULL, '驾驶证被扣押', '驾驶证被扣留', '驾驶证遗失', '驾驶证被暂扣', 'C', '《机动车驾驶证申领和使用规定》第六十三条机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地或者核发地以外的车辆管理所申请补发。申请时应当填写申请表，并提交以下证明、凭证： （一）机动车驾驶人的身份证明； （二）机动车驾驶证遗失的书面声明。 符合规定的，车辆管理所应当在一日内补发机动车驾驶证。 机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。 机动车驾驶证被依法扣押、扣留或者暂扣期间，机动车驾驶人不得申请补发。', 1, 1, '2021-12-19 00:10:28', '2021-12-19 00:10:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1443, '机动车驾驶人补领机动车驾驶证后，使用原机动车驾驶证驾驶的，除由公安机关交通管理部门收回原机动车驾驶证外，还应当受到何种处罚？', '', '1', '1', NULL, '吊销驾驶证', '拘留驾驶人', '警告', '罚款', 'D', '《机动车驾驶证申领和使用规定》第九十四条，机动车驾驶人有下列行为之一的，由公安机关交通管理部门处二十元以上二百元以下罚款： （一）机动车驾驶人补领机动车驾驶证后，继续使用原机动车驾驶证的；', 1, 1, '2021-12-19 00:10:28', '2021-12-19 00:10:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1444, '机动车在抵押登记、质押备案期间不可以办理转移登记。', '', '2', '1', NULL, '', '', '', '', 'A', '第二十条有下列情形之一的，不予办理转移登记：(一)机动车与该车档案记载内容不一致的；(二)属于海关监管的机动车，海关未解除监管或者批准转让的；(三)机动车在抵押登记、质押备案期间的；(四)有本规定第九条第(一)项、第(二)项、第(七)项、第(八)项、第(九)项规定情形的。', 1, 1, '2021-12-19 00:10:28', '2021-12-19 00:10:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1445, '机动车所有人申请转移登记前，应当将涉及该车的道路交通安全违法行为和交通事故处理完毕。', '', '2', '1', NULL, '', '', '', '', 'A', '已注册登记的机动车所有权发生转移的，现机动车所有人应当自机动车交付之日起三十日内向登记地车辆管理所申请转移登记。机动车所有人申请转移登记前，应当将涉及该车的道路交通安全违法行为和交通事故处理完毕。', 1, 1, '2021-12-19 00:10:28', '2021-12-19 00:10:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1446, '经购买、调拨、赠予等方式获得机动车后尚未注册登记的，向车辆管理所申领临时行驶车号牌后，方可临时上道路行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '遇有下列情形之一，需要临时上道路行驶的，机动车所有人应当向车辆管理所申领临时行驶车号牌： 1、未销售的； 2、购买、调拨、赠予等方式获得机动车后尚未注册登记的； 3、进行科研、定型试验的； 4、因轴荷、总质量、外廓尺寸超出国家标准不予办理注册登记的特型机动车。', 1, 1, '2021-12-19 00:10:28', '2021-12-19 00:10:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1447, '驾驶机动车与行人之间发生交通事故造成人身伤亡、财产损失的，机动车一方没有过错的，不承担赔偿责任。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通事故》第七十六条 机动车发生交通事故造成人身伤亡、财产损失的，由保险公司在机动车第三者责任强制保险责任限额范围内予以赔偿。超过责任限额的部分，按照下列方式承担赔偿责任：（一）机动车之间发生交通事故的，由有过错的一方承担责任；双方都有过错的，按照各自过错的比例分担责任。（二）机动车与非机动车驾驶人、行人之间发生交通事故的，由机动车一方承担责任；但是，有证据证明非机动车驾驶人、行人违反道路交通安全法律、法规，机动车驾驶人已经采取必要处置措施的，减轻机动车一方的责任。交通事故的损失是由非机动车驾驶人、行人故意造成的，机动车一方不承担责任。', 1, 1, '2021-12-19 00:10:29', '2021-12-19 00:10:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1448, '如图所示，驾驶机动车行驶至此位置时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu448.gif', '1', '1', NULL, '观察左侧无车后，可以左转', '从该处直接左转', '不得左转，应当直行', '倒车退到虚线处换到左转车道', 'C', '上道路行驶的机动车，应按道路交通标志、标线的指示通行。如图所示，驾驶机动车行驶在路面导向箭头为直行的车道上，因此只能直行通过路口。', 1, 1, '2021-12-19 00:10:29', '2021-12-19 00:10:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1449, '发生无人员伤亡的、财产轻微损失的交通事故后，以下做法正确的是什么？', '', '1', '1', NULL, '必须报警，等候警察处理', '开车离开现场', '确保安全的情况下，对现场拍照，然后将车辆移至路边等不妨碍交通的地点', '停在现场保持不动', 'C', '《中华人民共和国道路交通安全法实施条例》第八十六条规定:“机动车与机动车、机动车与非机动车在道路上发生未造成人身伤亡的交通事故，当事人对事实及成因无争议的，在记录交通事故的时间、地点、对方当事人的姓名和联系方式、机动车牌号、驾驶证号、保险凭证号、碰撞部位，并共同签名后，撤离现场，自行协商损害赔偿事宜。当事人对交通事故事实及成因有争议的，应当迅速报警。”', 1, 1, '2021-12-19 00:10:29', '2021-12-19 00:10:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1450, '以下不属于机动车驾驶证审验内容的是什么？', '', '1', '1', NULL, '道路交通安全违法行为、交通事故处理情况', '驾驶人身体条件', '记满12分后参加学习和考试情况', '驾驶车辆累计行驶里程', 'D', '机动车驾驶证审验内容：（一）道路交通安全违法行为、交通事故处理情况；（二）身体条件情况；（三）道路交通安全违法行为记分及记满12分后参加学习和考试情况。驾驶车辆累计行驶里程不在审验范围内。', 1, 1, '2021-12-19 00:10:29', '2021-12-19 00:10:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1451, '已注册登记的机动车，改变车身颜色，机动车所有人不需要向登记地车辆管理所申请变更登记。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六条，已注册登记的机动车有下列情形之一的，机动车所有人应当向登记该机动车的公安机关交通管理部门申请变更登记： (一)改变机动车车身颜色的； (二)更换发动机的； (三)更换车身或者车架的； (四)因质量有问题，制造厂更换整车的； (五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车的； (六)机动车所有人的住所迁出或者迁入公安机关交通管理部门管辖区域的。', 1, 1, '2021-12-19 00:10:30', '2021-12-19 00:10:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1452, '驾驶机动车在高速公路上行驶，遇低能见度气象条件时，能见度在200米以下，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？', '', '1', '1', NULL, '60，100', '70，100', '40，80', '30，80', 'A', '《道路交通安全法实施条例》第八十一条 机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定： (一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离； (二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； (三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。 遇有前款规定情形时，高速公路管理部门应当通过显示屏等方式发布速度限制、保持车距等提示信息。', 1, 1, '2021-12-19 00:10:30', '2021-12-19 00:10:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1453, '如图所示，驾驶机动车遇左侧车道有车辆正在超车时，可以迅速变道，伺机反超。', '/uploadFile/yuanBeiTu453.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车遇左侧车道有车辆正在超车时，应减速或靠右停车避让，千万不要开赌气车。', 1, 1, '2021-12-19 00:10:30', '2021-12-19 00:10:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1454, '如图所示，驾驶机动车遇到这种情况，不仅要控制车辆留出会车空间，而且要注意与右侧的儿童保持足够的安全距离。', '/uploadFile/yuanBeiTu454.gif', '2', '1', NULL, '', '', '', '', 'A', '行车中，要注意礼让儿童，发现儿童在路边玩耍、嬉笑打闹时，应当减速慢行，随时注意观察儿童动态，发现异常要及时停车避让。如图所示路况，不但要控制车辆留出会车空间，还要注意与右侧的儿童保持足够的安全距离。', 1, 1, '2021-12-19 00:10:31', '2021-12-19 00:10:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1455, '机动车驾驶人血液中酒精含量大于或者等于多少可认定为醉驾？', '', '1', '1', NULL, '20毫克/100毫升', '60毫克/100毫升', '80毫克/100毫升', '50毫克/100毫升', 'C', '当驾驶人血液中的酒精含量大于或者等于20毫克/100毫升，小于80毫克/100毫升时，就是酒后驾驶；当驾驶人血液中的酒精含量大于或者等于80毫克/100毫升时，就是醉酒驾驶。醉酒驾驶触犯了刑法，是会受到刑事处罚的。【20/80分酒醉：20酒驾；80醉驾】', 1, 1, '2021-12-19 00:10:31', '2021-12-19 00:10:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1456, '如图所示，驾驶机动车在这种情况下，当C车减速让超车时，A车应该如何行驶？', '/uploadFile/yuanBeiTu456.gif', '1', '1', NULL, '放弃超越C车', '加速超越C车', '鸣喇叭示意B车让行后超车', '直接向左变更车道，迫使B车让行', 'A', '超车之前一定要观察好超车条件，能够保证万无一失再开始超越，以避免在超车时形成“三点一线”的状况，也就是说本车、被超车与对向来车千万不能在一条横向的直线上。一旦发生这种情况，被夹在两辆车中间完全没有躲避的条件，很容易发生剐碰事故。如图所示，虽然C车减速让超，但对向有B车，如果A车超车，ABC三车将会形成“三点一线”的状况非常危险，因此，在这种情况下A车应放弃超车。', 1, 1, '2021-12-19 00:10:31', '2021-12-19 00:10:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1457, '如图所示，夜间驾驶机动车与同方向行驶的前车距离较近时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu457.gif', '1', '1', NULL, '使用远光灯，有利于观察路面情况', '禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目', '使用远光灯，有利于告知前方驾驶人后方有来车', '禁止使用远光灯，避免灯光照射至前车后视镜造成自己眩目', 'B', '夜间驾驶机动车与同方向行驶的前车距离较近时，禁止使用远光灯，避免灯光照射至前车后视镜造成前车驾驶人眩目。', 1, 1, '2021-12-19 00:10:31', '2021-12-19 00:10:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1458, '道路没有划分机动车道、非机动车道和人行道的，以下说法正确的是什么？', '', '1', '1', NULL, '机动车在道路左侧通行，非机动车和行人随意通行', '机动车在道路左侧通行，非机动车和行人在道路两侧通行', '机动车在道路中间通行，非机动车和行人在道路两侧通行', '机动车、非机动车和行人可随意通行', 'C', '《道路交通安全法》第三十五条　机动车、非机动车实行右侧通行。第三十六条　根据道路要求和通行需要，道路区分为机动行车道、非机动行车道和人行道的，机动车、非机动车、路人实行分道通行。 无区分机动行车道、非机动行车道和人行道的，机动车在道路中间通行，非机动车和路人在道路两侧通行。', 1, 1, '2021-12-19 00:10:32', '2021-12-19 00:10:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1459, '驾驶机动车经过无划分车道的道路时，可以随意通行。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第三十六条：根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。 因此，随意通行做法错误。', 1, 1, '2021-12-19 00:10:32', '2021-12-19 00:10:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1460, '驾驶机动车在路口右转弯时，应提前开启右转向灯，不受信号灯限制，不受车速限制，迅速通过，防止路口堵塞。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在路口右转弯时，应提前开启右转向灯，注意观察后方和右转弯方向道路交通动态，同时观察右转弯的车辆和行人。右转弯遇圆形红灯亮时，可减速靠右侧转弯通过，但不应影响被放行方向的其他车辆和行人通行。 【拓展知识】 1，信号灯是圆形时，右转弯不受信号灯的限制，但不得影响被放行方向的其他车辆和行人通行； 2，信号灯是箭头形状时，红灯停，绿灯行。', 1, 1, '2021-12-19 00:10:32', '2021-12-19 00:10:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1461, '驾驶机动车遇到前方道路拥堵时，可以借用无人通行的非机动车道行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '遇到道路拥堵，应该按顺序排队等待，不得借用非机动车道行驶。', 1, 1, '2021-12-19 00:10:33', '2021-12-19 00:10:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1462, '如图所示，红圈标注的深色车辆的做法是违法的。', '/uploadFile/yuanBeiTu462.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，红圈标注的深色车辆骑轧道路中心线行驶，影响其他车辆行驶，极易引发交通事故，是违反道路交通安全法的行为。，', 1, 1, '2021-12-19 00:10:33', '2021-12-19 00:10:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1463, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu463.gif', '1', '1', NULL, '应减速观察水情，然后加速行驶通过', '应停车察明水情，确认安全后，低速通过', '应停车察明水情，确认安全后，快速通过', '可随意通行', 'B', '为了保障安全。机动车在有积水较深的桥下通过行车时，应当停车察明水情，确认安全后，低速通过。', 1, 1, '2021-12-19 00:10:33', '2021-12-19 00:10:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1464, '驾驶机动车行经漫水路或者漫水桥时，应当停车察明水情，快速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通管理条例》的第四十六条规定：车辆行经漫水路或漫水桥时,必须停车察明水情,确认安全后，低速通过。', 1, 1, '2021-12-19 00:10:33', '2021-12-19 00:10:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1465, '关于醉酒驾驶机动车的处罚，以下说法错误的是什么？', '', '1', '1', NULL, '公安机关交通管理部门约束至酒醒', '吊销驾驶证', '五年内不得重新取得机动车驾驶证', '记6分', 'D', '请注意，题干要求选出说法错误的选项。根据《道路交通安全法》第九十一条规定，醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。 《道路交通安全违法行为记分分值》一、机动车驾驶人有下列违法行为之一，一次记12分 ： （二）饮酒后驾驶机动车的；', 1, 1, '2021-12-19 00:10:34', '2021-12-19 00:10:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1466, '驾驶机动车在道路上发生交通事故造成人身伤亡的，驾驶人必须报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条规定：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', 1, 1, '2021-12-19 00:10:34', '2021-12-19 00:10:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1467, '两辆机动车发生轻微碰擦事故后，为保证理赔，必须等保险公司人员到场鉴定后才能撤离现场。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》中第七十条，在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 1, 1, '2021-12-19 00:10:34', '2021-12-19 00:10:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1468, '以下哪种身体条件，不可以申请机动车驾驶证?', '', '1', '1', NULL, '糖尿病', '红绿色盲', '高血压', '怀孕', 'B', '红绿色盲会影响观察红绿灯，而我们在路上开车一般都是根据交通信号灯行驶的，所以不能申请驾照。', 1, 1, '2021-12-19 00:10:34', '2021-12-19 00:10:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1469, '申请人患有癫痫病的，可以申领机动车驾驶证，但是驾驶时必须有人陪同。', '', '2', '1', NULL, '', '', '', '', 'B', '申请人患有癫痫病的，因为不知道发病时间，所以驾驶车辆是存在安全隐患的，所以不能申请。', 1, 1, '2021-12-19 00:10:35', '2021-12-19 00:10:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1470, '自愿降级的驾驶人需要到车辆管理所申请换领驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '自愿降级的驾驶人，由本人带驾驶证、身份证去车管所提出申请，并换领驾驶证。', 1, 1, '2021-12-19 00:10:35', '2021-12-19 00:10:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1471, '补领机动车驾驶证应到以下哪个地方办理？', '', '1', '1', NULL, '所学驾校', '驾驶证核发地车辆管理所', '派出所', '全国任何地方公安机关交通管理部门', 'B', '《机动车驾驶证申领和使用规定》第六十三条机动车驾驶证遗失的，机动车驾驶人应当向机动车驾驶证核发地或者核发地以外的车辆管理所申请补发。申请时应当填写申请表，并提交以下证明、凭证： （一）机动车驾驶人的身份证明； （二）机动车驾驶证遗失的书面声明。 符合规定的，车辆管理所应当在一日内补发机动车驾驶证。 机动车驾驶人补领机动车驾驶证后，原机动车驾驶证作废，不得继续使用。 机动车驾驶证被依法扣押、扣留或者暂扣期间，机动车驾驶人不得申请补发。', 1, 1, '2021-12-19 00:10:35', '2021-12-19 00:10:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1472, '机动车驾驶人驾驶证有效期满换领驾驶证时，须提交县级以上医疗机构出具的身体条件证明。', '', '2', '1', NULL, '', '', '', '', 'B', '根据公安部交通管理局发布的新措施，自2020年11月20日起，扩大体检医疗机构范围，对申请机动车驾驶证、办理机动车驾驶证业务需要提交身体条件证明的，体检医疗机构由县级或者部队团级以上医疗机构扩大到符合健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心等医疗机构。 因此，必须提交县级以上医疗机构出具的身体条件证明，说法错误。', 1, 1, '2021-12-19 00:10:35', '2021-12-19 00:10:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1473, '年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查。', '', '2', '1', NULL, '', '', '', '', 'A', '修改后的《机动车驾驶证申领和使用规定》(公安部令第139号)中第三十条规定：年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后三十日内，提交符合健康体检资质的二级以上医院、乡镇卫生院、社区卫生服务中心等医疗机构出具的有关身体条件的证明。', 1, 1, '2021-12-19 00:10:36', '2021-12-19 00:10:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1474, '驾驶人吸食或注射毒品后驾驶机动车的，一经查获，其驾驶证将被注销。', '', '2', '1', NULL, '', '', '', '', 'A', '《驾驶证的申领和使用》第七十七条机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证： 　　（一）死亡的； 　　（二）提出注销申请的； 　　（三）丧失民事行为能力，监护人提出注销申请的； 　　（四）身体条件不适合驾驶机动车的； 　　（五）有器质性心脏病、癫痫病、美尼尔氏症、眩晕症、癔病、震颤麻痹、精神病、痴呆以及影响肢体活动的神经系统疾病等妨碍安全驾驶疾病的； 　　（六）被查获有吸食、注射毒品后驾驶机动车行为，正在执行社区戒毒、强制隔离戒毒、社区康复措施，或者长期服用依赖性精神药品成瘾尚未戒除的；', 1, 1, '2021-12-19 00:10:36', '2021-12-19 00:10:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1475, '遇到这种单方交通事故，应如何处理？', '/uploadFile/yuanBeiTu475.gif', '1', '1', NULL, '不用报警', '报警', '直接联系路政部门进行理赔', '直接联系绿化部门', 'B', '如图所示，机动车碰撞道路防护栏，应立即报警处理。根据《道路交通安全法》道路交通事故有下列情形之一的，当事人应当保护现场并立即报警：5、碰撞建筑物、公共设施或者其他设施的；', 1, 1, '2021-12-19 00:10:36', '2021-12-19 00:10:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1476, '机动车之间发生交通事故，不管是否有人员伤亡，只要双方当事人同意，都可自行协商解决。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第七十条：在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；造成人身伤亡的，应立即报警由交警认定事故中是否存在酒驾等刑事犯罪行为。', 1, 1, '2021-12-19 00:10:37', '2021-12-19 00:10:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1477, '事故报警时，要向交警提供事故地点、人员伤情、车辆号牌等信息，协助交警快速定位到达现场。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》规定：发生事故报警时，要向交警提供事故地点、人员伤情、车辆号牌等信息，协助交警快速定位到达现场。', 1, 1, '2021-12-19 00:10:37', '2021-12-19 00:10:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1478, '假如您在高速公路上不小心错过了准备驶出的路口，正确的操作应该是？', '', '1', '1', NULL, '紧急刹车，倒车至想要驶出的路口', '继续前行，到下一出口驶离高速公路掉头', '在应急停车道上停车，等待车辆较少的时候再伺机倒车', '借用应急停车道进行掉头，逆向行驶', 'B', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，不得紧急制动；即便是刚刚错过出口且距下一出口很远，也不得掉头逆行或倒回出口。', 1, 1, '2021-12-19 00:10:37', '2021-12-19 00:10:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1479, '下列高速公路交通标志与其含义对应的正确的一项是？', '/uploadFile/yuanBeiTu479.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'C', '图1：高速公路终点；图2：高速公路服务区预告；图3：高速公路紧急停车带；图4：紧急电话。因此，图3的标志与含义对应。', 1, 1, '2021-12-19 00:10:37', '2021-12-19 00:10:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1480, '驾驶车辆进入高速公路加速车道后，须尽快将车速提高到每小时60公里以上的原因是什么？', '', '1', '1', NULL, '以防被其他车辆超过', '以防后方车辆发生追尾事故', '以防汇入车流时影响主线车道上行驶的车辆', '以防违反最低限速要求受到处罚', 'C', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 因此，驾驶机动车从匝道驶入加速车道后，尽快将车速提高到60公里/小时以上，以防汇入车流时影响主线车道上行驶的车辆，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机，不得在加速车道内紧急制动或停车。', 1, 1, '2021-12-19 00:10:38', '2021-12-19 00:10:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1481, '在高速公路上开车遇到图中所示的情况时，以下操作不正确的是什么？', '/uploadFile/yuanBeiTu481.gif', '1', '1', NULL, '应该打开雾灯、近光灯、示廓灯、前后位灯，危险报警灯光', '能见度低，应该与同车道前车间距保持一定距离', '降低车速，防止紧急情况下无法及时制动', '继续维持高速行驶，防止后面车辆堵塞', 'D', '如图所示，浓雾天行车应开启雾灯、近光灯、示廓灯以便看清前方路况及让其他车辆驾驶人发现自己，同时开启危险报警闪光灯，不能使用远光灯。将车速控制在能及时停车的范围内，与同车道前车保持足够的安全距离，谨慎缓慢行驶。 请注意：题干要求选出操作不正确的选项，因此继续维持高速行驶，做法错误。', 1, 1, '2021-12-19 00:10:38', '2021-12-19 00:10:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1482, '雪天在高速公路上驾驶时，关于安全车距错误的说法是什么？', '', '1', '1', NULL, '雪天路滑，制动距离比干燥柏油路更长', '雪天能见度低，应该根据能见度控制安全距离', '能见度小于200m时，与前车至少保持50m的安全距离', '能见度小于50m时，应该驶离高速公路', 'C', '请注意，题干要求选出说法错误的选项。雪天在高速公路上驾驶时，积雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向，应根据能见度控制安全距离，低速行驶。 《道路交通安全法实施条例》第八十一条 机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定： (一)能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离； (二)能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； (三)能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。 综上，“能见度200m时，与前车保持50m的安全距离”说法错误。', 1, 1, '2021-12-19 00:10:38', '2021-12-19 00:10:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1483, '高速公路上同时有最高和最低速度限制，因为过快或者过慢都容易导致追尾。', '', '2', '1', NULL, '', '', '', '', 'A', '过快容易与前车追尾，过慢容易与后车追尾，题干所说的是正确的。', 1, 1, '2021-12-19 00:10:38', '2021-12-19 00:10:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1484, '驾驶人在实习期内驾驶机动车上高速公路行驶，应由持相应或者更高准驾车型驾驶证一年以上的驾驶人陪同。', '', '2', '1', NULL, '', '', '', '', 'B', '《机动车驾驶证申领和使用规定》第七十五条 驾驶人在实习期内驾驶机动车上高速公路行驶，应当由持相应或者更高准驾车型驾驶证三年以上的驾驶人陪同。其中，驾驶残疾人专用小型自动挡载客汽车的，可以由持有小型自动挡载客汽车以上准驾车型驾驶证的驾驶人陪同。', 1, 1, '2021-12-19 00:10:39', '2021-12-19 00:10:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1485, '在高速公路上长期骑、轧车行道分界线行驶，会同时占用两个车道，导致后方车辆行驶困难，易引发交通事故。', '', '2', '1', NULL, '', '', '', '', 'A', '高速公路行车不得随意穿行越线，不准骑、轧车行道分界线，不得占用应急车道。机动车同时占用两个车道行驶，会导致后方车辆行驶困难，从而引发交通事故。', 1, 1, '2021-12-19 00:10:39', '2021-12-19 00:10:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1486, '车辆在高速公路发生故障不能移动时，驾驶人这种尝试排除故障的做法是否正确？', '/uploadFile/yuanBeiTu486.gif', '2', '1', NULL, '', '', '', '', 'B', '车辆在高速公路发生故障不能移动时，应①开启危险报警闪光灯（夜间还要开启示廓灯和后位灯），②在车后150米以外设置警告标志，③车上人员要迅速转移至车右前方护栏以外的安全地带，④面向来车方向并迅速拨打紧急电话求援或报警。因此，如图所示，驾驶人的处理方式错误。', 1, 1, '2021-12-19 00:10:39', '2021-12-19 00:10:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1487, '下列做法是否正确？', '/uploadFile/yuanBeiTu487.gif', '2', '1', NULL, '', '', '', '', 'B', '根据《道路交通安全法实施条例》的有关规定，机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引，牵引车和被牵引车均应当开启危险报警闪光灯，在最右侧慢车道行驶。 图中用普通小车做牵引车是不对的，根据法规必须使用救援车、清障车；且应该在最右侧慢车道行驶，因此图中做法错误。', 1, 1, '2021-12-19 00:10:39', '2021-12-19 00:10:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1488, '高速公路上车辆发生故障后，开启危险报警闪光灯和摆放警告标志是为了向其他车辆求助。', '', '2', '1', NULL, '', '', '', '', 'B', '高速公路上车辆发生故障后，要开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，主要用于提醒其他车辆注意到本车发生的特殊情况，起到警示作用。', 1, 1, '2021-12-19 00:10:40', '2021-12-19 00:10:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1489, '高速公路上车辆发生故障后，开启危险报警闪光灯和摆放警告标志的作用是警告后续车辆注意避让。', '', '2', '1', NULL, '', '', '', '', 'A', '高速公路上车辆发生故障后，要开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，主要用于提醒其他车辆注意到本车发生的特殊情况，起到警示作用。', 1, 1, '2021-12-19 00:10:40', '2021-12-19 00:10:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1490, '车辆发生故障无法移动时，以下做法是否正确？', '/uploadFile/yuanBeiTu490.gif', '2', '1', NULL, '', '', '', '', 'B', '危险警告标志应放置在故障车车后的150米以外，图中危险警告标志牌放在车前。做法错误。', 1, 1, '2021-12-19 00:10:40', '2021-12-19 00:10:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1491, '驾驶机动车在高速公路上发生故障，需要停车排除故障时，若能将机动车移至应急车道内，则不需要开启危险报警闪光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在高速公路上发生故障，需要停车排除故障时，应将车停住紧急停车带或应急车道内，停车后应①立即开启危险报警闪光灯（夜间还要开启示廓灯和后位灯），②在车后150米以外设置警告标志，③车上人员要迅速转移至车右前方护栏以外的安全地带，④面向来车方向并迅速拨打紧急电话求援或报警。', 1, 1, '2021-12-19 00:10:41', '2021-12-19 00:10:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1492, '车辆发生故障而无法移动时，首先应在车辆后方50-150米处放置危险警告标志，防止后车追尾。', '', '2', '1', NULL, '', '', '', '', 'B', '车辆发生故障无法移动时，应当首先开启危险报警闪光灯。根据《道路交通安全法》规定，机动车在路上发生故障时，应当采取以下措施：1、驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放；2、难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离。普通道路上应把警告标志设置在车后50米-100米处，高速公路上应把警告标志设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。 因此，应首先开启危险报警闪光灯，而不是首先放置警告标志做法。', 1, 1, '2021-12-19 00:10:41', '2021-12-19 00:10:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1493, '对驾驶过程中接打手机的看法正确的是？', '', '1', '1', NULL, '开车过程中不主动打电话，但是有重要电话打进来是可以边开车边接听手持电话的', '根据驾龄和驾车技术，经验丰富的驾驶人可以在驾驶过程中接打手持电话', '在车流量不大的道路上驾驶时，短时接听手持电话是可以的', '开车需要接打电话时，应该先找到安全的地方停车再操作', 'D', '《中华人民共和国道路交通安全法实施条例》第六十二条 （三）项明确规定：“驾驶机动车不得有下列行为：拨打接听手持电话的行为”，违者罚款200元，扣2分。开车需要接打电话时，应该先找到安全的地方停车再操作', 1, 1, '2021-12-19 00:10:41', '2021-12-19 00:10:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1494, '蓝色车辆遇到图中的情形时，下列做法正确的是？', '/uploadFile/yuanBeiTu494.gif', '1', '1', NULL, '按照前方交通信号灯指示直接通行', '鸣喇叭提醒，让学生队伍中空出一个缺口，从缺口中穿行过去', '停车等待，直到学生队伍完全通过', '鸣喇叭，催促还未通过的学生加快速度通过', 'C', '按规定，驾驶机动车遇到人行横道上有行人通过时需要停车等待。行人的流动很随意，需要谨慎对待。', 1, 1, '2021-12-19 00:10:42', '2021-12-19 00:10:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1495, '驾驶车辆时在道路上抛撒物品，以下说法不正确的是？', '', '1', '1', NULL, '抛撒纸张等轻质物品会阻挡驾驶人视线，分散驾驶人注意力', '有可能引起其他驾驶人紧急躲避等应激反应，进而引发事故', '破坏环境，影响环境整洁，甚至造成路面的损坏', '保持车内整洁，减少燃油消耗', 'D', '驾驶车辆时在道路上抛撒物品，容易阻挡驾驶员视线、引起其他驾驶人紧急躲避等应激反应，也会污染环境造成路面损坏。与车内卫生和燃油消耗并无直接影响，因此不正确。', 1, 1, '2021-12-19 00:10:42', '2021-12-19 00:10:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1496, '下面关于下坡熄火滑行的说法错误的是？', '', '1', '1', NULL, '对于采用真空助力刹车系统的车辆而言，下坡时的熄火会使刹车系统失效', '对于采用了助力转向系统的车辆而言，下坡时熄火会使转向盘变重，难以控制', '下坡道熄火时，车辆不能使用发动机制动', '下坡滑行是利用坡道的位能推动汽车前进，发动机不工作，可以节油，应大力提倡', 'D', '注意，题目要求选出说法【错误】的选项，“下坡滑行是利用坡道的位能推动汽车前进，发动机不工作，可以节油，应【大力提倡】”，说法错误，因为下坡空挡滑行不省油而且危险大，不应该得到大力提倡。 空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。', 1, 1, '2021-12-19 00:10:42', '2021-12-19 00:10:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1497, '如图所示，当机动车行驶至交叉口时的做法是正确的。', '/uploadFile/yuanBeiTu497.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，A车行驶在右转弯车道，且信号灯为绿灯，因此，A车的做法正确。', 1, 1, '2021-12-19 00:10:42', '2021-12-19 00:10:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1498, '当后排座位没有人乘坐时，后车门未关好就起步也是可以的。', '', '2', '1', NULL, '', '', '', '', 'B', '应该把所有车门关好再起步，题干所说是错误的。', 1, 1, '2021-12-19 00:10:43', '2021-12-19 00:10:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1499, '车辆后备箱门未关好，是可以上路行驶的。', '/uploadFile/yuanBeiTu499.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车禁止在车门、车厢没有关好时行车。', 1, 1, '2021-12-19 00:10:43', '2021-12-19 00:10:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1500, '下列哪个交通标志表示不能停车？', '/uploadFile/yuanBeiTu500.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '图1：禁止长时停车；图2：禁止停车；图3：停车检查；图4：停车让行。', 1, 1, '2021-12-19 00:10:43', '2021-12-19 00:10:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1501, '图中深色车辆在该地点临时停车是可以的。', '/uploadFile/yuanBeiTu501.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车不得在人行横道上停车，因此图中深色车辆临时停车是错误的，不可以的。', 1, 1, '2021-12-19 00:10:44', '2021-12-19 00:10:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1502, '图中标注车辆在该地点停车是可以的。', '/uploadFile/yuanBeiTu502.gif', '2', '1', NULL, '', '', '', '', 'B', '公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，不得停车。', 1, 1, '2021-12-19 00:10:44', '2021-12-19 00:10:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1503, '图中小型汽车的停车地点是正确的。', '/uploadFile/yuanBeiTu503.gif', '2', '1', NULL, '', '', '', '', 'B', '公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，不得停车。', 1, 1, '2021-12-19 00:10:44', '2021-12-19 00:10:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1504, '如图所示，A车在此处临时停车是可以的。', '/uploadFile/yuanBeiTu504.gif', '2', '1', NULL, '', '', '', '', 'B', '交叉路口、铁道路口、急转弯、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。图上的车离路口只有30米，是不能在此停车的。', 1, 1, '2021-12-19 00:10:44', '2021-12-19 00:10:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1505, '如图所示，A车在此处临时停车是可以的。', '/uploadFile/yuanBeiTu505.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，图中A车停在距离公交车车站30米以外的位置，因此做法正确。根据《道路交通安全法实施条例》第六十三条规定：机动车在道路上临时停车，应当遵守下列规定：(二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；【口5】 (三)公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车。【站3】', 1, 1, '2021-12-19 00:10:45', '2021-12-19 00:10:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1506, '驾驶机动车在路口直行遇到这种信号灯应该怎样行驶？', '/uploadFile/yuanBeiTu506.gif', '1', '1', NULL, '不得越过停止线', '加速直行通过', '左转弯行驶', '进入路口等待', 'A', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 红灯亮时，禁止车辆通行，车辆不得越过停止线，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 2, 1, '2021-12-19 00:10:45', '2021-12-19 00:10:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1507, '驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过。', '/uploadFile/yuanBeiTu507.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 红灯亮时，禁止车辆通行，车辆不得越过停止线。 《道路交通安全法实施条例》第三十八条 机动车信号灯和非机动车信号灯表示： (一)绿灯亮时，准许车辆通行，但转弯的车辆不得妨碍被放行的直行车辆、行人通行； (二)黄灯亮时，已越过停止线的车辆可以继续通行； (三)红灯亮时，禁止车辆通行。 在未设置非机动车信号灯和人行横道信号灯的路口，非机动车和行人应当按照机动车信号灯的表示通行。 红灯亮时，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 2, 1, '2021-12-19 00:10:45', '2021-12-19 00:10:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1508, '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '/uploadFile/yuanBeiTu508.gif', '2', '1', NULL, '', '', '', '', 'A', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 红灯亮时，禁止车辆通行，车辆不得越过停止线，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 2, 1, '2021-12-19 00:10:45', '2021-12-19 00:10:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1509, '驾驶机动车在路口遇到这种信号灯禁止通行。', '/uploadFile/yuanBeiTu509.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 绿灯亮时，准许车辆通行，但转弯的车辆不得妨碍被放行的直行车辆、行人通行。', 2, 1, '2021-12-19 00:10:46', '2021-12-19 00:10:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1510, '绿灯亮表示前方路口允许机动车通行。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车信号灯（圆形信号灯）有红、黄、绿三种颜色。 绿灯亮时，准许车辆通行，但转弯的车辆不得妨碍被放行的直行车辆、行人通行。', 2, 1, '2021-12-19 00:10:46', '2021-12-19 00:10:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1511, '驾驶机动车在路口遇到这种信号灯亮时，要在停止线前停车瞭望。', '/uploadFile/yuanBeiTu511.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： (一)在划有导向车道的路口，按所需行进方向驶入导向车道； (二)准备进入环形路口的让已在路口内的机动车先行； (三)向左转弯时，靠路口中心点左侧转弯。转弯时开启转向灯，夜间行驶开启近光灯； (四)遇放行信号时，依次通过； (五)遇停止信号时，依次停在停止线以外。没有停止线的，停在路口以外； (六)向右转弯遇有同车道前车正在等候放行信号时，依次停车等候； (七)在没有方向指示信号灯的交叉路口，转弯的机动车让直行的车辆、行人先行。相对方向行驶的右转弯机动车让左转弯车辆先行。', 2, 1, '2021-12-19 00:10:47', '2021-12-19 00:10:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1512, '驾驶机动车在路口遇到这种信号灯亮时，不能右转弯。', '/uploadFile/yuanBeiTu512.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： (一)在划有导向车道的路口，按所需行进方向驶入导向车道； (二)准备进入环形路口的让已在路口内的机动车先行； (三)向左转弯时，靠路口中心点左侧转弯。转弯时开启转向灯，夜间行驶开启近光灯； (四)遇放行信号时，依次通过；', 2, 1, '2021-12-19 00:10:47', '2021-12-19 00:10:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1513, '驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯。', '/uploadFile/yuanBeiTu513.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： (七)在没有方向指示信号灯的交叉路口，转弯的机动车让直行的车辆、行人先行。相对方向行驶的右转弯机动车让左转弯车辆先行。 如图所示，对面车辆没有开启转向灯，可判断其为直行车辆；我方车辆左转向灯亮，准备左转弯行驶；因此，应当让对面直行车先行。', 2, 1, '2021-12-19 00:10:47', '2021-12-19 00:10:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1514, '驾驶机动车在路口遇到这种信号灯表示什么意思？', '/uploadFile/yuanBeiTu514.gif', '1', '1', NULL, '路口警示', '禁止右转', '准许直行', '加速通过', 'A', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', 2, 1, '2021-12-19 00:10:48', '2021-12-19 00:10:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1515, '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '/uploadFile/yuanBeiTu515.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 2, 1, '2021-12-19 00:10:48', '2021-12-19 00:10:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1516, '驾驶机动车在前方路口不能右转弯。', '/uploadFile/yuanBeiTu516.gif', '2', '1', NULL, '', '', '', '', 'B', '图中黄色信号灯是闪光警告信号灯，提示车辆、行人通行时注意瞭望，确认安全后通过。因此驾驶机动车在前方路口可以右转弯。', 2, 1, '2021-12-19 00:10:48', '2021-12-19 00:10:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1517, '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', '/uploadFile/yuanBeiTu517.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；没有越过停止线的车辆需停车等待，不得进入路口，更不得加速强行通过交叉路口。 图中机动车的位置已越过停止线，因此可以继续通行。', 2, 1, '2021-12-19 00:10:49', '2021-12-19 00:10:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1518, '遇到这种情况时怎样行驶？', '/uploadFile/yuanBeiTu518.gif', '1', '1', NULL, '禁止车辆在两侧车道通行', '减速进入两侧车道行驶', '进入右侧车道行驶', '加速进入两侧车道行驶', 'A', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 如图所示：两侧车道红色叉形灯亮，因此禁止通行。', 2, 1, '2021-12-19 00:10:49', '2021-12-19 00:10:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1519, '遇到这种情况时，中间车道不允许车辆通行。', '/uploadFile/yuanBeiTu519.gif', '2', '1', NULL, '', '', '', '', 'A', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 如图所示，中间车道红色叉形灯亮，表示禁止车辆通行。', 2, 1, '2021-12-19 00:10:49', '2021-12-19 00:10:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1520, '这辆红色轿车可以在该车道行驶。', '/uploadFile/yuanBeiTu520.gif', '2', '1', NULL, '', '', '', '', 'B', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 如图所示：红色轿车行驶的车道，红色叉形灯亮，表示该车道禁止车辆通行。', 2, 1, '2021-12-19 00:10:49', '2021-12-19 00:10:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1521, '驾驶机动车要选择绿色箭头灯亮的车道行驶。', '/uploadFile/yuanBeiTu521.gif', '2', '1', NULL, '', '', '', '', 'A', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。', 2, 1, '2021-12-19 00:10:50', '2021-12-19 00:10:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1522, '这个路口允许车辆怎样行驶？', '/uploadFile/yuanBeiTu522.gif', '1', '1', NULL, '向左、向右转弯', '直行或向左转弯', '向左转弯', '直行或向右转弯', 'D', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，绿色箭头灯向上和向右，表示允许车辆直行或向右转弯行驶。', 2, 1, '2021-12-19 00:10:50', '2021-12-19 00:10:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1523, '这个路口允许车辆怎样行驶？', '/uploadFile/yuanBeiTu523.gif', '1', '1', NULL, '向左转弯', '直行', '直行或向右转弯', '向右转弯', 'A', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，绿色箭头灯向左，表示允许车辆向左转弯通行。', 2, 1, '2021-12-19 00:10:51', '2021-12-19 00:10:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1524, '这个路口允许车辆怎样行驶？', '/uploadFile/yuanBeiTu524.gif', '1', '1', NULL, '直行', '向右转弯', '直行或向左转弯', '向左转弯', 'B', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，绿色箭头灯向右，表示允许车辆向右转弯行驶。', 2, 1, '2021-12-19 00:10:51', '2021-12-19 00:10:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1525, '驾驶机动车在这种情况下不能左转弯。', '/uploadFile/yuanBeiTu525.gif', '2', '1', NULL, '', '', '', '', 'A', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，红色箭头灯向左，表示禁止车辆左转弯行驶。', 2, 1, '2021-12-19 00:10:51', '2021-12-19 00:10:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1526, '驾驶机动车在这种情况下可以右转弯。', '/uploadFile/yuanBeiTu526.gif', '2', '1', NULL, '', '', '', '', 'B', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，红色箭头灯向上和向右，表示禁止车辆直行和右转弯行驶。', 2, 1, '2021-12-19 00:10:51', '2021-12-19 00:10:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1527, '驾驶机动车在这种情况下不能直行和左转弯。', '/uploadFile/yuanBeiTu527.gif', '2', '1', NULL, '', '', '', '', 'A', '方向信号灯箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示，红色箭头灯向上和向左，表示禁止车辆直行和左转弯行驶。', 2, 1, '2021-12-19 00:10:52', '2021-12-19 00:10:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1528, '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '/uploadFile/yuanBeiTu528.gif', '1', '1', NULL, '尽快加速通过', '靠边停车等待', '注意瞭望安全通过', '紧急制动', 'C', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。', 2, 1, '2021-12-19 00:10:52', '2021-12-19 00:10:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1529, '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '', '2', '1', NULL, '', '', '', '', 'A', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。', 2, 1, '2021-12-19 00:10:52', '2021-12-19 00:10:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1530, '黄灯持续闪烁，表示机动车可以加速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。因此，加速通过做法错误。', 2, 1, '2021-12-19 00:10:53', '2021-12-19 00:10:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1531, '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '/uploadFile/yuanBeiTu531.gif', '1', '1', NULL, '边观察边缓慢通过', '不换挡加速通过', '在火车到来前通过', '不得越过停止线', 'D', '道路与铁路平面交叉道口有两个红灯交替闪烁或一个红灯亮时，表示火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线。红灯熄灭时，表示允许车辆行人通行。', 2, 1, '2021-12-19 00:10:53', '2021-12-19 00:10:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1532, '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '/uploadFile/yuanBeiTu532.gif', '2', '1', NULL, '', '', '', '', 'A', '道路与铁路平面交叉道口有两个红灯交替闪烁或一个红灯亮时，表示火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线。红灯熄灭时，表示允许车辆行人通行。', 2, 1, '2021-12-19 00:10:53', '2021-12-19 00:10:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1533, '在道路与铁路道口遇到一个红灯亮时要尽快通过道口。', '/uploadFile/yuanBeiTu533.gif', '2', '1', NULL, '', '', '', '', 'B', '道路与铁路平面交叉道口有两个红灯交替闪烁或一个红灯亮时，表示火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线。红灯熄灭时，表示允许车辆行人通行。', 2, 1, '2021-12-19 00:10:54', '2021-12-19 00:10:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1534, '这属于哪一种标志？', '/uploadFile/yuanBeiTu534.gif', '1', '1', NULL, '警告标志', '指路标志', '指示标志', '禁令标志', 'A', '红色是禁令标志，黄色是警告标志，蓝色是指示标志与普通路段的指路标志，绿色是高速公路的指路标志，棕色是旅游标志。', 2, 1, '2021-12-19 00:10:54', '2021-12-19 00:10:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1535, '这个标志是何含义？', '/uploadFile/yuanBeiTu535.gif', '1', '1', NULL, 'T形交叉路口', 'Y形交叉路口', '十字交叉路口', '环形交叉路口', 'C', '十字交叉路口：警告前方道路有十字交叉路口，应谨慎慢行，注意横向车辆。', 2, 1, '2021-12-19 00:10:54', '2021-12-19 00:10:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1536, '这个标志是何含义？', '/uploadFile/yuanBeiTu536.gif', '1', '1', NULL, 'T形交叉路口', 'Y形交叉路口', '十字交叉路口', '环形交叉路口', 'A', 'T形交叉路口：警告前方道路有与上述图形相符的T形交叉路口，应谨慎慢行，注意横向车辆。', 2, 1, '2021-12-19 00:10:54', '2021-12-19 00:10:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1537, '这个标志是何含义？', '/uploadFile/yuanBeiTu537.gif', '1', '1', NULL, '向左急转弯', '向右急转弯', '向右绕行', '连续弯路', 'B', '【一急二反三连续】一个弯表示急转弯，两个弯表示反向弯路，三个弯表示连续弯路。', 2, 1, '2021-12-19 00:10:55', '2021-12-19 00:10:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1538, '这个标志是何含义？', '/uploadFile/yuanBeiTu538.gif', '1', '1', NULL, '向左急转弯', '向右急转弯', '向左绕行', '连续弯路', 'A', '【一急二反三连续】一个弯表示急转弯，两个弯表示反向弯路，三个弯表示连续弯路。', 2, 1, '2021-12-19 00:10:55', '2021-12-19 00:10:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1539, '这是什么交通标志？', '/uploadFile/yuanBeiTu539.gif', '1', '1', NULL, '易滑路段', '急转弯路', '反向弯路', '连续弯路', 'C', '【一急二反三连续】一个弯表示急转弯，两个弯表示反向弯路，三个弯表示连续弯路。', 2, 1, '2021-12-19 00:10:55', '2021-12-19 00:10:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1540, '这是什么交通标志？', '/uploadFile/yuanBeiTu540.gif', '1', '1', NULL, '易滑路段', '急转弯路', '反向弯路', '连续弯路', 'D', '', 2, 1, '2021-12-19 00:10:56', '2021-12-19 00:10:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1541, '这个标志是何含义？', '/uploadFile/yuanBeiTu541.gif', '1', '1', NULL, '堤坝路', '上陡坡', '连续上坡', '下陡坡', 'B', '', 2, 1, '2021-12-19 00:10:56', '2021-12-19 00:10:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1542, '这个标志是何含义？', '/uploadFile/yuanBeiTu542.gif', '1', '1', NULL, '堤坝路', '上陡坡', '下陡坡', '连续上坡', 'C', '', 2, 1, '2021-12-19 00:10:56', '2021-12-19 00:10:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1543, '这个标志是何含义？', '/uploadFile/yuanBeiTu543.gif', '1', '1', NULL, '连续上坡', '上陡坡', '下陡坡', '连续下坡', 'D', '连续下坡：提醒前方有两个及以上的连续下坡路段，应小心驾驶。', 2, 1, '2021-12-19 00:10:56', '2021-12-19 00:10:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1544, '这是什么交通标志？', '/uploadFile/yuanBeiTu544.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'A', '', 2, 1, '2021-12-19 00:10:57', '2021-12-19 00:10:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1545, '这是什么交通标志？', '/uploadFile/yuanBeiTu545.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'B', '', 2, 1, '2021-12-19 00:10:57', '2021-12-19 00:10:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1546, '这是什么交通标志？', '/uploadFile/yuanBeiTu546.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'C', '', 2, 1, '2021-12-19 00:10:57', '2021-12-19 00:10:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1547, '这个标志是何含义？', '/uploadFile/yuanBeiTu547.gif', '1', '1', NULL, '窄路', '右侧变窄', '左侧变窄', '窄桥', 'D', '窄桥：用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6m的桥梁以前适当位置。', 2, 1, '2021-12-19 00:10:57', '2021-12-19 00:10:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1548, '这个标志是何含义？', '/uploadFile/yuanBeiTu548.gif', '1', '1', NULL, '双向交通', '分离式道路', '潮汐车道', '减速让行', 'A', '', 2, 1, '2021-12-19 00:10:58', '2021-12-19 00:10:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1549, '这个标志是何含义？', '/uploadFile/yuanBeiTu549.gif', '1', '1', NULL, '人行横道', '注意行人', '注意儿童', '学校区域', 'B', '', 2, 1, '2021-12-19 00:10:58', '2021-12-19 00:10:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1550, '这个标志是何含义？', '/uploadFile/yuanBeiTu550.gif', '1', '1', NULL, '注意行人', '人行横道', '注意儿童', '学校区域', 'C', '', 2, 1, '2021-12-19 00:10:58', '2021-12-19 00:10:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1551, '这个标志是何含义？', '/uploadFile/yuanBeiTu551.gif', '1', '1', NULL, '大型畜牧场', '野生动物保护区', '注意野生动物', '注意牲畜', 'D', '', 2, 1, '2021-12-19 00:10:59', '2021-12-19 00:10:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1552, '这个标志是何含义？', '/uploadFile/yuanBeiTu552.gif', '1', '1', NULL, '注意野生动物', '注意牲畜', '动物公园', '开放的牧区', 'A', '', 2, 1, '2021-12-19 00:10:59', '2021-12-19 00:10:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1553, '这个标志是何含义？', '/uploadFile/yuanBeiTu553.gif', '1', '1', NULL, '交叉路口', '注意信号灯', '注意行人', '人行横道灯', 'B', '注意信号灯：警告前方路段设有信号灯，应按信号灯指示行车。', 2, 1, '2021-12-19 00:10:59', '2021-12-19 00:10:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1554, '这个标志是何含义？', '/uploadFile/yuanBeiTu554.gif', '1', '1', NULL, '傍山险路', '悬崖路段', '注意落石', '危险路段', 'C', '', 2, 1, '2021-12-19 00:10:59', '2021-12-19 00:10:59', NULL, NULL, '0');
