INSERT INTO `subject` VALUES (1556, '这个标志是何含义？', '/uploadFile/yuanBeiTu556.gif', '1', '1', NULL, '急转弯路', '易滑路段', '试车路段', '曲线路段', 'B', '', 2, 1, '2021-12-19 00:11:00', '2021-12-19 00:11:00', NULL, NULL, '0');
