INSERT INTO `subject` VALUES (1558, '这个标志是何含义？', '/uploadFile/yuanBeiTu558.gif', '1', '1', NULL, '堤坝路', '临崖路', '易滑路', '傍水路', 'A', '', 2, 1, '2021-12-19 00:11:01', '2021-12-19 00:11:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1559, '这个标志是何含义？', '/uploadFile/yuanBeiTu559.gif', '1', '1', NULL, '注意行人', '有人行横道', '村庄或集镇', '有小学校', 'C', '村庄：提醒前方是紧靠村庄、集镇且视线不良的路段，应小心驾驶。', 2, 1, '2021-12-19 00:11:01', '2021-12-19 00:11:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1560, '这个标志是何含义？', '/uploadFile/yuanBeiTu560.gif', '1', '1', NULL, '涵洞', '水渠', '桥梁', '隧道', 'D', '隧道：提醒前方是双向行驶并且照明不好的隧道，应注意慢行。', 2, 1, '2021-12-19 00:11:01', '2021-12-19 00:11:01', NULL, NULL, '0');
