INSERT INTO `subject` VALUES (1567, '这个标志是何含义？', '/uploadFile/yuanBeiTu567.gif', '1', '1', NULL, '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', 'B', '', 2, 1, '2021-12-19 00:11:03', '2021-12-19 00:11:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1568, '这个标志是何含义？', '/uploadFile/yuanBeiTu568.gif', '1', '1', NULL, '多股铁路与道路相交', '有人看守铁路道口', '无人看守铁路道口', '注意长时鸣喇叭', 'C', '', 2, 1, '2021-12-19 00:11:03', '2021-12-19 00:11:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1569, '这个标志是何含义？', '/uploadFile/yuanBeiTu569.gif', '1', '1', NULL, '注意避让火车', '有人看守铁路道口', '无人看守铁路道口', '多股铁路与道路相交', 'D', '叉形符号：表示多股铁路与道路相交，设在铁路道口标志上方。', 2, 1, '2021-12-19 00:11:03', '2021-12-19 00:11:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1570, '这个标志是何含义？', '/uploadFile/yuanBeiTu570.gif', '1', '1', NULL, '距无人看守铁路道口50米', '距有人看守铁路道口50米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', 'A', '', 2, 1, '2021-12-19 00:11:04', '2021-12-19 00:11:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1571, '这个标志是何含义？', '/uploadFile/yuanBeiTu571.gif', '1', '1', NULL, '距无人看守铁路道口50米', '距有人看守铁路道口50米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', 'C', '', 2, 1, '2021-12-19 00:11:04', '2021-12-19 00:11:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1572, '这个标志是何含义？', '/uploadFile/yuanBeiTu572.gif', '1', '1', NULL, '距有人看守铁路道口150米', '距无人看守铁路道口150米', '距无人看守铁路道口100米', '距有人看守铁路道口100米', 'B', '', 2, 1, '2021-12-19 00:11:04', '2021-12-19 00:11:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1573, '这个标志是何含义？', '/uploadFile/yuanBeiTu573.gif', '1', '1', NULL, '避让非机动车', '非机动车道', '禁止非机动车通行', '注意非机动车', 'D', '', 2, 1, '2021-12-19 00:11:05', '2021-12-19 00:11:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1574, '这个标志是何含义？', '/uploadFile/yuanBeiTu574.gif', '1', '1', NULL, '注意残疾人', '残疾人出入口', '残疾人休息处', '残疾人专用通道', 'A', '注意残疾人：提醒前方是残疾人经常出入地点，应减速慢行，注意残疾人。', 2, 1, '2021-12-19 00:11:05', '2021-12-19 00:11:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1575, '这个标志是何含义？', '/uploadFile/yuanBeiTu575.gif', '1', '1', NULL, '施工路段', '事故易发路段', '减速慢行路段', '拥堵路段', 'B', '', 2, 1, '2021-12-19 00:11:05', '2021-12-19 00:11:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1576, '这个标志是何含义？', '/uploadFile/yuanBeiTu576.gif', '1', '1', NULL, '施工路段', '车多路段', '慢行', '拥堵路段', 'C', '慢行：提醒前方道路发生特殊情况，影响行车安全，需减速慢行。', 2, 1, '2021-12-19 00:11:05', '2021-12-19 00:11:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1577, '这个标志是何含义？', '/uploadFile/yuanBeiTu577.gif', '1', '1', NULL, '施工路段绕行', '双向交通', '注意危险', '左右绕行', 'D', '注意障碍物：：告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 2, 1, '2021-12-19 00:11:06', '2021-12-19 00:11:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1578, '这个标志是何含义？', '/uploadFile/yuanBeiTu578.gif', '1', '1', NULL, '左侧绕行', '单向通行', '注意危险', '右侧绕行', 'A', '左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 2, 1, '2021-12-19 00:11:06', '2021-12-19 00:11:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1579, '这个标志是何含义？', '/uploadFile/yuanBeiTu579.gif', '1', '1', NULL, '单向通行', '右侧绕行', '注意危险', '左侧绕行', 'B', '右侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', 2, 1, '2021-12-19 00:11:06', '2021-12-19 00:11:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1580, '这个标志是何含义？', '/uploadFile/yuanBeiTu580.gif', '1', '1', NULL, '事故多发路段', '减速慢行', '注意危险', '拥堵路段', 'C', '', 2, 1, '2021-12-19 00:11:07', '2021-12-19 00:11:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1581, '这个标志是何含义？', '/uploadFile/yuanBeiTu581.gif', '1', '1', NULL, '塌方路段', '施工路段', '前方工厂', '道路堵塞', 'B', '施工：告示前方道路施工，车辆应减速慢行或绕道行驶。', 2, 1, '2021-12-19 00:11:07', '2021-12-19 00:11:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1582, '这个标志是何含义？', '/uploadFile/yuanBeiTu582.gif', '1', '1', NULL, '建议速度', '最低速度', '最高速度', '限制速度', 'A', '', 2, 1, '2021-12-19 00:11:07', '2021-12-19 00:11:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1583, '这个标志是何含义？', '/uploadFile/yuanBeiTu583.gif', '1', '1', NULL, '隧道开远光灯', '隧道减速', '隧道开灯', '隧道开示宽灯', 'C', '隧道开车灯：警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。', 2, 1, '2021-12-19 00:11:08', '2021-12-19 00:11:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1584, '这个标志是何含义？', '/uploadFile/yuanBeiTu584.gif', '1', '1', NULL, '注意双向行驶', '靠两侧行驶', '可变车道', '注意潮汐车道', 'D', '注意潮汐车道：警告前方为潮汐车道，即前方车道上车辆行驶方向可随交通管理需要变化。', 2, 1, '2021-12-19 00:11:08', '2021-12-19 00:11:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1585, '这个标志是何含义？', '/uploadFile/yuanBeiTu585.gif', '1', '1', NULL, '注意保持车距', '车距确认路段', '车速测试路段', '两侧变窄路段', 'A', '注意保持车距：警告前方路段经常发生车辆追尾事故，应注意和前车保持安全距离。', 2, 1, '2021-12-19 00:11:08', '2021-12-19 00:11:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1586, '这个标志是何含义？', '/uploadFile/yuanBeiTu586.gif', '1', '1', NULL, '注意交互式道路', '注意分离式道路', '平面交叉路口', '环形平面交叉', 'B', '如图所示，警告标志【注意分离式道路】：十字平面交叉，警告前方平面交叉的被交道路是分离式道路，即左、右行车道分开修建，需按标志指示方向行车。', 2, 1, '2021-12-19 00:11:08', '2021-12-19 00:11:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1587, '这个标志是何含义？', '/uploadFile/yuanBeiTu587.gif', '1', '1', NULL, 'Y形交叉口', '主路让行', '注意分流', '注意合流', 'D', '注意合流：警告前方有车辆汇合进来，需提高警惕。', 2, 1, '2021-12-19 00:11:09', '2021-12-19 00:11:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1588, '这个标志是何含义？', '/uploadFile/yuanBeiTu588.gif', '1', '1', NULL, '避险车道', '应急车道', '路肩', '急弯道', 'A', '避险车道：提醒前方道路设置了避险车道，即供速度失控车辆驶离原车道，进行安全减速的专用车道。', 2, 1, '2021-12-19 00:11:09', '2021-12-19 00:11:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1589, '这属于哪一种标志？', '/uploadFile/yuanBeiTu589.gif', '1', '1', NULL, '警告标志', '禁令标志', '指示标志', '指路标志', 'B', '黄色-警示、红色-禁令、蓝色-指示指路，黑色-辅助。禁令标志的颜色为白底、红圈、红杠、黑图形；图形压杠；形状为圆形。个别标志例外。', 2, 1, '2021-12-19 00:11:09', '2021-12-19 00:11:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1590, '禁令标志的作用是什么？', '', '1', '1', NULL, '禁止或限制行为', '告知方向信息', '指示车辆行进', '警告前方危险', 'A', '禁令标志的作用是禁止、限制及相应解除，道路使用者应严格遵守。', 2, 1, '2021-12-19 00:11:09', '2021-12-19 00:11:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1591, '这个标志是何含义？', '/uploadFile/yuanBeiTu591.gif', '1', '1', NULL, '不准车辆驶入', '不准长时间停车', '停车让行', '不准临时停车', 'C', '', 2, 1, '2021-12-19 00:11:10', '2021-12-19 00:11:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1592, '这个标志是何含义？', '/uploadFile/yuanBeiTu592.gif', '1', '1', NULL, '不准让行', '会车让行', '停车让行', '减速让行', 'D', '', 2, 1, '2021-12-19 00:11:10', '2021-12-19 00:11:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1593, '这个标志是何含义？', '/uploadFile/yuanBeiTu593.gif', '1', '1', NULL, '会车时停车让对方车先行', '前方是双向通行路段', '右侧道路禁止车通行', '会车时停车让右侧车先行', 'A', '', 2, 1, '2021-12-19 00:11:10', '2021-12-19 00:11:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1594, '这个标志是何含义？', '/uploadFile/yuanBeiTu594.gif', '1', '1', NULL, '禁止驶入', '禁止通行', '减速行驶', '限时进入', 'B', '', 2, 1, '2021-12-19 00:11:11', '2021-12-19 00:11:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1595, '这个标志提示哪种车型禁止通行？', '/uploadFile/yuanBeiTu595.gif', '1', '1', NULL, '中型客车', '小型货车', '各种车辆', '小型客车', 'D', '禁止小型客车驶入：前方路段禁止小型客车（含小轿车、7座以下商务车、11座以下面包车）驶入。', 2, 1, '2021-12-19 00:11:11', '2021-12-19 00:11:11', NULL, NULL, '0');
