INSERT INTO `subject` VALUES (1638, '这个标志是何含义？', '/uploadFile/yuanBeiTu638.gif', '1', '1', NULL, '直行车道', '右转车道', '掉头车道', '左转车道', 'A', '', 2, 1, '2021-12-19 00:11:24', '2021-12-19 00:11:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1639, '这个标志是何含义？', '/uploadFile/yuanBeiTu639.gif', '1', '1', NULL, '直行和左转车道', '直行和辅路出口车道', '直行和右转合用车道', '分向行驶车道', 'C', '', 2, 1, '2021-12-19 00:11:24', '2021-12-19 00:11:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1640, '这个标志是何含义？', '/uploadFile/yuanBeiTu640.gif', '1', '1', NULL, '直行和左转合用车道', '直行和掉头合用车道', '直行和右转车道', '分向行驶车道', 'A', '', 2, 1, '2021-12-19 00:11:24', '2021-12-19 00:11:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1641, '这个标志是何含义？', '/uploadFile/yuanBeiTu641.gif', '1', '1', NULL, '左转车道', '掉头车道', '绕行车道', '分向车道', 'B', '掉头车道：所指示的车道为掉头车道。', 2, 1, '2021-12-19 00:11:25', '2021-12-19 00:11:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1642, '这个标志是何含义？', '/uploadFile/yuanBeiTu642.gif', '1', '1', NULL, '直行和左转合用车道', '禁止左转和掉头车道', '掉头和左转合用车道', '分向行驶车道', 'C', '掉头和左转合用车道：所指示的车道为掉头和左转合用车道。', 2, 1, '2021-12-19 00:11:25', '2021-12-19 00:11:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1643, '这个标志是何含义？', '/uploadFile/yuanBeiTu643.gif', '1', '1', NULL, '直线行驶车道', '左转行驶车道', '右转行驶车道', '分向行驶车道', 'D', '分向行驶车道：各车道车辆应按照标志指示方向行驶。', 2, 1, '2021-12-19 00:11:25', '2021-12-19 00:11:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1644, '这个标志是何含义？', '/uploadFile/yuanBeiTu644.gif', '1', '1', NULL, '公交线路专用车道', '大型客车专用车道', '快速公交系统专用车道', '多乘员车辆专用车道', 'A', '', 2, 1, '2021-12-19 00:11:26', '2021-12-19 00:11:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1645, '这个标志是何含义？', '/uploadFile/yuanBeiTu645.gif', '1', '1', NULL, '禁止小型车行驶', '机动车行驶', '只准小型车行驶', '不准小型车通行', 'B', '', 2, 1, '2021-12-19 00:11:26', '2021-12-19 00:11:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1646, '这个标志是何含义？', '/uploadFile/yuanBeiTu646.gif', '1', '1', NULL, '小型车车道', '小型车专用车道', '机动车车道', '多乘员车辆专用车道', 'C', '', 2, 1, '2021-12-19 00:11:26', '2021-12-19 00:11:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1647, '这个标志是何含义？', '/uploadFile/yuanBeiTu647.gif', '1', '1', NULL, '非机动车停车位', '电动自行车行驶', '非机动车停放区', '非机动车行驶', 'D', '', 2, 1, '2021-12-19 00:11:26', '2021-12-19 00:11:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1648, '这个标志是何含义？', '/uploadFile/yuanBeiTu648.gif', '1', '1', NULL, '非机动车车道', '禁止自行车通行车道', '自行车专用车道', '停放自行车路段', 'A', '非机动车车道：所指示车道只供非机动车行驶。指示标志中两侧的虚线表示车道，通常为蓝底方形。', 2, 1, '2021-12-19 00:11:27', '2021-12-19 00:11:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1649, '这个标志是何含义？', '/uploadFile/yuanBeiTu649.gif', '1', '1', NULL, '公交车专用车道', 'BRT车辆专用车道', '大型客车专用车道', '多乘员车专用车道', 'B', '', 2, 1, '2021-12-19 00:11:27', '2021-12-19 00:11:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1650, '这个标志是何含义？', '/uploadFile/yuanBeiTu650.gif', '1', '1', NULL, '小型汽车专用车道', '机动车专用车道', '多乘员车辆专用车道', '出租汽车专用车道', 'C', '此标志为多乘员车辆专用车道，比机动车专用车道标志上的小车多了2个小人，所以它是多乘员车辆专用车道。', 2, 1, '2021-12-19 00:11:27', '2021-12-19 00:11:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1651, '这个标志是何含义？', '/uploadFile/yuanBeiTu651.gif', '1', '1', NULL, '掉头', '倒车', '左转', '绕行', 'A', '', 2, 1, '2021-12-19 00:11:28', '2021-12-19 00:11:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1652, '这属于哪一类标志？', '/uploadFile/yuanBeiTu652.gif', '1', '1', NULL, '警告标志', '禁令标志', '指示标志', '指路标志', 'D', '指路标志：蓝底、白图形、白边框、蓝色衬边；形状为长方形和正方形，个别标志例外；作用是指引道路信息，为驾驶人传递道路方向、地点、距离等信息。 指路标志一般由以下几点信息组成：1、有文字表示地点；2、有箭头表示道路方向；3、有数字表示距离。', 2, 1, '2021-12-19 00:11:28', '2021-12-19 00:11:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1653, '指路标志的作用是什么？', '', '1', '1', NULL, '限制车辆通行', '提示限速信息', '提供方向信息', '警告前方危险', 'C', '指路标志的作用是指引道路信息，为驾驶人传递道路方向、地点、距离等信息。', 2, 1, '2021-12-19 00:11:28', '2021-12-19 00:11:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1654, '这个标志是何含义？', '/uploadFile/yuanBeiTu654.gif', '1', '1', NULL, '交叉路口预告', '车道方向预告', '分道信息预告', '分岔处预告', 'A', '指路标志【大交通量的四车道以上公路交叉路口预告】：预告前方交叉路口形式、交叉公路的编号或交叉公路的名称、通往方向信息、地理方向信息以及距前方交叉路口的距离。 如图所示：预告距离前方500米处的交叉路口，左转弯行驶通往远大路；右转弯行驶通往林园路；直行通往知泉路。', 2, 1, '2021-12-19 00:11:29', '2021-12-19 00:11:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1655, '这个标志是何含义？', '/uploadFile/yuanBeiTu655.gif', '1', '1', NULL, '地点和距离预告', '十字交叉路口预告', '分道信息预告', '道路分岔处预告', 'B', '', 2, 1, '2021-12-19 00:11:29', '2021-12-19 00:11:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1656, '这个标志是何含义？', '/uploadFile/yuanBeiTu656.gif', '1', '1', NULL, 'Y形交叉路口预告', '十字交叉路口预告', '丁字交叉路口预告', '道路分叉处预告', 'C', '', 2, 1, '2021-12-19 00:11:30', '2021-12-19 00:11:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1657, '这个标志是何含义？', '/uploadFile/yuanBeiTu657.gif', '1', '1', NULL, '环形交叉路口预告', '十字交叉路口预告', '丁字交叉路口预告', 'Y形交叉路口预告', 'D', '', 2, 1, '2021-12-19 00:11:30', '2021-12-19 00:11:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1658, '这个标志是何含义？', '/uploadFile/yuanBeiTu658.gif', '1', '1', NULL, '环形交叉路口预告', '十字交叉路口预告', '互通立体交叉预告', 'Y形交叉路口预告', 'A', '', 2, 1, '2021-12-19 00:11:30', '2021-12-19 00:11:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1659, '这个标志是何含义？', '/uploadFile/yuanBeiTu659.gif', '1', '1', NULL, '十字交叉路口预告', '互通式立体交叉预告', 'Y形交叉路口预告', '环形交叉路口预告', 'B', '', 2, 1, '2021-12-19 00:11:30', '2021-12-19 00:11:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1660, '这个标志是何含义？', '/uploadFile/yuanBeiTu660.gif', '1', '1', NULL, '国道编号', '省道编号', '县道编号', '乡道编号', 'A', '', 2, 1, '2021-12-19 00:11:31', '2021-12-19 00:11:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1661, '这个标志是何含义？', '/uploadFile/yuanBeiTu661.gif', '1', '1', NULL, '国道编号', '省道编号', '县道编号', '乡道编号', 'B', '', 2, 1, '2021-12-19 00:11:31', '2021-12-19 00:11:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1662, '这个标志是何含义？', '/uploadFile/yuanBeiTu662.gif', '1', '1', NULL, '国道编号', '省道编号', '县道编号', '乡道编号', 'C', '', 2, 1, '2021-12-19 00:11:31', '2021-12-19 00:11:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1663, '这个标志是何含义？', '/uploadFile/yuanBeiTu663.gif', '1', '1', NULL, '国道编号', '省道编号', '县道编号', '乡道编号', 'D', '', 2, 1, '2021-12-19 00:11:31', '2021-12-19 00:11:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1664, '这个标志是何含义？', '/uploadFile/yuanBeiTu664.gif', '1', '1', NULL, '地点距离', '行驶路线', '行驶方向', '终点地名', 'A', '地点距离：指示前方道路要经过的重要公路编号、道路名、地名和距离。', 2, 1, '2021-12-19 00:11:32', '2021-12-19 00:11:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1665, '这个标志是何含义？', '/uploadFile/yuanBeiTu665.gif', '1', '1', NULL, '室内停车场', '露天停车场', '专用停车场', '内部停车场', 'B', '', 2, 1, '2021-12-19 00:11:32', '2021-12-19 00:11:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1666, '这个标志是何含义？', '/uploadFile/yuanBeiTu666.gif', '1', '1', NULL, '专用停车场', '露天停车场', '室内停车场', '内部停车场', 'C', '', 2, 1, '2021-12-19 00:11:32', '2021-12-19 00:11:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1667, '这个标志是何含义？', '/uploadFile/yuanBeiTu667.gif', '1', '1', NULL, '紧急停车带', '露天停车场', '停车位', '错车道', 'D', '', 2, 1, '2021-12-19 00:11:32', '2021-12-19 00:11:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1668, '这个标志是何含义？', '/uploadFile/yuanBeiTu668.gif', '1', '1', NULL, '观景台', '停车场', '休息区', '停车位', 'A', '', 2, 1, '2021-12-19 00:11:33', '2021-12-19 00:11:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1669, '这个标志是何含义？', '/uploadFile/yuanBeiTu669.gif', '1', '1', NULL, '横过道路设施', '应急避难场所', '生活服务区', '行人专用通道', 'B', '应急避难设施（场所）：附近有应急避难场所、隧道等设施的疏散通道或其他应急避难设施。', 2, 1, '2021-12-19 00:11:33', '2021-12-19 00:11:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1670, '这个标志是何含义？', '/uploadFile/yuanBeiTu670.gif', '1', '1', NULL, '停车场', '观景台', '休息区', '服务区', 'C', '', 2, 1, '2021-12-19 00:11:33', '2021-12-19 00:11:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1671, '这个标志是何含义？', '/uploadFile/yuanBeiTu671.gif', '1', '1', NULL, '禁止左转', '此路不通', '禁止通行', '超高绕行', 'D', '绕行：前方道路实施交通管制，车辆需按照标志所指示的路线绕行。如图所示，高度超过4米的车辆需按箭头指引的路线绕行通过，因此是超高绕行。', 2, 1, '2021-12-19 00:11:33', '2021-12-19 00:11:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1672, '这个标志是何含义？', '/uploadFile/yuanBeiTu672.gif', '1', '1', NULL, '此路不通', 'T型路口', '分流路口', '减速通行', 'A', '此路不通指路标志，指示前方道路无出口，不能通行。', 2, 1, '2021-12-19 00:11:34', '2021-12-19 00:11:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1673, '这个标志是何含义？', '/uploadFile/yuanBeiTu673.gif', '1', '1', NULL, '向左变道', '车道数变少', '合流处', '应急车道', 'B', '', 2, 1, '2021-12-19 00:11:34', '2021-12-19 00:11:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1674, '这个标志是何含义？', '/uploadFile/yuanBeiTu674.gif', '1', '1', NULL, '向右变道', '分流处', '车道数增加', '路面变宽', 'C', '', 2, 1, '2021-12-19 00:11:34', '2021-12-19 00:11:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1675, '这个标志是何含义？', '/uploadFile/yuanBeiTu675.gif', '1', '1', NULL, '减速拍照区', '道路流量监测', '全路段抓拍', '交通监控设备', 'D', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人本路段设置了图像采集等交通监控设备。', 2, 1, '2021-12-19 00:11:34', '2021-12-19 00:11:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1676, '这个标志是何含义？', '/uploadFile/yuanBeiTu676.gif', '1', '1', NULL, '隧道出口距离', '隧道入口距离', '隧道跟车距离', '隧道总长度', 'A', '隧道出口距离预告指路标志，用于指示到前方隧道出口的距离。 【拓展延伸】 隧道出口距离标志，表示设置标志的地方距离出口还有多少米，不一定设置在隧道入口处。因此隧道出口距离不能等同于隧道总长度，请注意。', 2, 1, '2021-12-19 00:11:35', '2021-12-19 00:11:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1677, '这个标志是何含义？', '/uploadFile/yuanBeiTu677.gif', '1', '1', NULL, '转弯诱导标志', '线形诱导标志', '合流诱导标志', '分流诱导标志', 'B', '线形诱导标组合使用指路标志，引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，注意谨慎驾驶。 线形诱导标的颜色规定为：指示性线形诱导标一般道路为蓝底白图案,高速公路为绿底白图案，用以提供一般性行驶指示;警告性线形诱导标为红底白图案，可使车辆驾驶人提高警觉，并准备防范应变之措施。', 2, 1, '2021-12-19 00:11:35', '2021-12-19 00:11:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1678, '这个标志是何含义？', '/uploadFile/yuanBeiTu678.gif', '1', '1', NULL, '左侧通行', '右侧通行', '两侧通行', '不准通行', 'C', '线形诱导标指路标志，引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，谨慎驾驶。', 2, 1, '2021-12-19 00:11:35', '2021-12-19 00:11:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1679, '这个标志是何含义？', '/uploadFile/yuanBeiTu679.gif', '1', '1', NULL, '左侧通行', '不准通行', '两侧通行', '右侧通行', 'D', '', 2, 1, '2021-12-19 00:11:35', '2021-12-19 00:11:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1680, '这个标志是何含义？', '/uploadFile/yuanBeiTu680.gif', '1', '1', NULL, '左侧通行', '不准通行', '两侧通行', '右侧通行', 'A', '', 2, 1, '2021-12-19 00:11:36', '2021-12-19 00:11:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1681, '这个标志是何含义？', '/uploadFile/yuanBeiTu681.gif', '1', '1', NULL, '高速公路终点预告', '高速公路入口预告', '高速公路起点预告', '高速公路出口预告', 'B', '【绿底标志在高速，看图识字就可以】入口预告：预告前方进入高速公路或城市快速路入口的距离及行车方向。', 2, 1, '2021-12-19 00:11:36', '2021-12-19 00:11:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1682, '这个标志是何含义？', '/uploadFile/yuanBeiTu682.gif', '1', '1', NULL, '高速公路右侧出口预告', '高速公路下一出口预告', '高速公路地点、方向预告', '高速公路左侧出口预告', 'C', '', 2, 1, '2021-12-19 00:11:36', '2021-12-19 00:11:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1683, '这个标志是何含义？', '/uploadFile/yuanBeiTu683.gif', '1', '1', NULL, '高速公路终点地名预告', '高速公路行驶路线预告', '高速公路行驶方向预告', '高速公路地点距离预告', 'D', '', 2, 1, '2021-12-19 00:11:37', '2021-12-19 00:11:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1684, '这个标志是何含义？', '/uploadFile/yuanBeiTu684.gif', '1', '1', NULL, '高速公路界牌编号', '高速公路里程编号', '高速公路命名编号', '高速公路路段编号', 'C', '如图所示编号，指示高速公路命名编号，作为指路标志的路线信息。字母G，为“国”字拼音的首字母。', 2, 1, '2021-12-19 00:11:37', '2021-12-19 00:11:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1685, '这个标志是何含义？', '/uploadFile/yuanBeiTu685.gif', '1', '1', NULL, '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', 'A', '下一出口预告：预告下一出口的信息和距离。', 2, 1, '2021-12-19 00:11:37', '2021-12-19 00:11:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1686, '这个标志是何含义？', '/uploadFile/yuanBeiTu686.gif', '1', '1', NULL, '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', 'B', '', 2, 1, '2021-12-19 00:11:37', '2021-12-19 00:11:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1687, '这个标志是何含义？', '/uploadFile/yuanBeiTu687.gif', '1', '1', NULL, '高速公路下一出口预告', '高速公路右侧出口预告', '高速公路目的地预告', '高速公路左侧出口预告', 'D', '', 2, 1, '2021-12-19 00:11:38', '2021-12-19 00:11:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1688, '这个标志是何含义？', '/uploadFile/yuanBeiTu688.gif', '1', '1', NULL, '高速公路起点', '高速公路出口', '高速公路入口', '高速公路终点', 'A', '', 2, 1, '2021-12-19 00:11:38', '2021-12-19 00:11:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1689, '这个标志是何含义？', '/uploadFile/yuanBeiTu689.gif', '1', '1', NULL, '高速公路起点预告', '高速公路出口预告', '高速公路入口预告', '高速公路终点预告', 'D', '终点预告：预告高速公路或城市快速路终点的距离。', 2, 1, '2021-12-19 00:11:38', '2021-12-19 00:11:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1690, '这个标志是何含义？', '/uploadFile/yuanBeiTu690.gif', '1', '1', NULL, '高速公路报警电话号码', '高速公路交通广播频率', '高速公路救援电话号码', '高速公路服务电话号码', 'B', '道路交通信息：指示高速公路或城市快速路上收听交通信息广播的频率。', 2, 1, '2021-12-19 00:11:38', '2021-12-19 00:11:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1691, '这个标志是何含义？', '/uploadFile/yuanBeiTu691.gif', '1', '1', NULL, '停车领卡', '停车缴费', '停车检查', 'ETC通道', 'A', '', 2, 1, '2021-12-19 00:11:39', '2021-12-19 00:11:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1692, '这个标志是何含义？', '/uploadFile/yuanBeiTu692.gif', '1', '1', NULL, '高速公路领卡处', '高速公路收费处', '高速公路检查站', '设有ETC的收费站', 'D', 'ETC = Electronic Toll Collection，电子不停车收费系统。 是指车辆在通过收费站时，通过车载设备实现车辆识别、信息写入（入口）并自动从预先绑定的IC卡或银行帐户上扣除相应资金（出口），是国际上正在努力开发并推广普及的一种用于道路、大桥和隧道的电子收费系统。 图中标有“ETC”、“收费”字样，可以判断是设有ETC的收费站。', 2, 1, '2021-12-19 00:11:39', '2021-12-19 00:11:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1693, '这个标志是何含义？', '/uploadFile/yuanBeiTu693.gif', '1', '1', NULL, '高速公路ETC车道', '高速公路缴费车道', '高速公路检查车道', '高速公路领卡车道', 'A', 'ETC车道指示：指示前方收费站的电子不停车收费车道，黄色箭头表示ETC车辆的行驶方向。', 2, 1, '2021-12-19 00:11:39', '2021-12-19 00:11:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1694, '这个标志是何含义？', '/uploadFile/yuanBeiTu694.gif', '1', '1', NULL, '高速公路特殊天气最高速度', '高速公路特殊天气建议速度', '高速公路特殊天气最低速度', '高速公路特殊天气平均速度', 'B', '高速公路特殊天气建议速度指路标志，提醒车辆驾驶人在雨、雪、雾等特殊天气下，根据能看清楚道路两侧的白色半圆状车距确认线数量来确定相应的建议行驶速度。', 2, 1, '2021-12-19 00:11:39', '2021-12-19 00:11:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1695, '这个标志是何含义？', '/uploadFile/yuanBeiTu695.gif', '1', '1', NULL, '高速公路公用电话', '高速公路报警电话', '高速公路紧急电话', '高速公路救援电话', 'C', '', 2, 1, '2021-12-19 00:11:40', '2021-12-19 00:11:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1696, '这个标志是何含义？', '/uploadFile/yuanBeiTu696.gif', '1', '1', NULL, '高速公路公用电话', '高速公路报警电话', '高速公路紧急电话', '高速公路救援电话', 'D', '', 2, 1, '2021-12-19 00:11:40', '2021-12-19 00:11:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1697, '这个标志是何含义？', '/uploadFile/yuanBeiTu697.gif', '1', '1', NULL, '高速公路紧急停车带', '高速公路避让处所', '高速公路停车区', '高速公路客车站', 'A', '紧急停车带：前方路段设有紧急停车的位置。设在高速公路和一级公路上，供车辆发生故障或其他原因紧急停车使用的临时停车地带。 紧急停车带只供紧急情况下使用，不得无故占用紧急停车带。', 2, 1, '2021-12-19 00:11:40', '2021-12-19 00:11:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1698, '这个标志是何含义？', '/uploadFile/yuanBeiTu698.gif', '1', '1', NULL, '高速公路服务区', '高速公路加油站', '高速公路休息区', '高速公路客车站', 'B', '高速公路【加油站】：前方有通往加油站的入口，指示加油站的位置。', 2, 1, '2021-12-19 00:11:40', '2021-12-19 00:11:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1699, '这个标志是何含义？', '/uploadFile/yuanBeiTu699.gif', '1', '1', NULL, '高速公路收费站预告', '高速公路服务区预告', '高速公路避险处预告', '高速公路客车站预告', 'B', '服务区预告：预告前方服务区的位置和服务区能提供的服务，服务区一般可提供停车、加油、维修、餐饮、超市、住宿等综合性服务。', 2, 1, '2021-12-19 00:11:41', '2021-12-19 00:11:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1700, '这个标志是何含义？', '/uploadFile/yuanBeiTu700.gif', '1', '1', NULL, '高速公路停车区预告', '高速公路避险处预告', '高速公路服务区预告', '高速公路停车场预告', 'A', '', 2, 1, '2021-12-19 00:11:41', '2021-12-19 00:11:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1701, '这个标志预告什么？', '/uploadFile/yuanBeiTu701.gif', '1', '1', NULL, '高速公路服务区预告', '高速公路避险处预告', '高速公路客车站预告', '高速公路停车场预告', 'D', '', 2, 1, '2021-12-19 00:11:41', '2021-12-19 00:11:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1702, '这属于哪一种标志？', '/uploadFile/yuanBeiTu702.gif', '1', '1', NULL, '旅游区标志', '作业区标志', '告示标志', '高速公路标志', 'A', '旅游区标志：颜色为棕底、白字（图形）、白边框、棕色衬边；形状为矩形。', 2, 1, '2021-12-19 00:11:41', '2021-12-19 00:11:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1703, '这个标志是何含义？', '/uploadFile/yuanBeiTu703.gif', '1', '1', NULL, '旅游区类别', '旅游区距离', '旅游区方向', '旅游区符号', 'B', '', 2, 1, '2021-12-19 00:11:42', '2021-12-19 00:11:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1704, '这个标志是何含义？', '/uploadFile/yuanBeiTu704.gif', '1', '1', NULL, '旅游区类别', '旅游区距离', '旅游区方向', '旅游区符号', 'C', '', 2, 1, '2021-12-19 00:11:42', '2021-12-19 00:11:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1705, '路中心黄色虚线属于哪一类标线？', '/uploadFile/yuanBeiTu705.gif', '1', '1', NULL, '指示标线', '禁止标线', '警告标志', '辅助标线', 'A', '道路中心黄色虚线 ，用于分隔对向行驶的交通流 ，属指示标线，因此本题应该选A。', 2, 1, '2021-12-19 00:11:43', '2021-12-19 00:11:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1706, '指示标线的作用是什么？', '', '1', '1', NULL, '禁止通行', '指示通行', '限制通行', '警告提醒', 'B', '指示标线是指示通行的，具体的红色标志表示禁止、限制；黄色标志表示警告。', 2, 1, '2021-12-19 00:11:43', '2021-12-19 00:11:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1707, '路中白色虚线是什么标线？', '/uploadFile/yuanBeiTu707.gif', '1', '1', NULL, '禁止跨越对向车道中心线', '限制跨越对向车道中心线', '可跨越同向车道中心线', '单向行驶车道分界中心线', 'C', '可跨越同向车行道分界线：道路中线为白色虚线，用于分隔同向行驶的交通流，在保证安全的情况下，允许车辆短时越线行驶。', 2, 1, '2021-12-19 00:11:43', '2021-12-19 00:11:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1708, '路中黄色分界线的作用是什么？', '/uploadFile/yuanBeiTu708.gif', '1', '1', NULL, '分隔同向行驶的交通流', '禁止跨越对向行车道', '允许在左侧车道行驶', '分隔对向行驶的交通流', 'D', '可跨越对向车行道分界线：道路中线为黄色虚线属于指示标线，用于分隔对向行驶的交通流。车辆在保证安全的情况下，可以越线超车或转弯。', 2, 1, '2021-12-19 00:11:46', '2021-12-19 00:11:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1709, '路中两条双黄色虚线是什么标线？', '/uploadFile/yuanBeiTu709.gif', '1', '1', NULL, '单向分道线', '可跨越分道线', '潮汐车道线', '双向分道线', 'C', '路中两条双黄色虚线是潮汐车道线。潮汐车道行车方向可随需要变化，以双黄虚线作为指示标线来指示潮汐车道的位置。 拓展知识： “潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数，提高车道使用效率。', 2, 1, '2021-12-19 00:11:46', '2021-12-19 00:11:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1710, '路两侧的车行道边缘白色实线是什么含义？', '/uploadFile/yuanBeiTu710.gif', '1', '1', NULL, '机动车可临时跨越', '非机动车可临时跨越', '车辆可临时跨越', '禁止车辆跨越', 'D', '车行道边缘白色实线：用白色实线划分机动车道与非机动车道分界（简称机非分界），禁止车辆跨越车行道边缘或机非分界。', 2, 1, '2021-12-19 00:11:46', '2021-12-19 00:11:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1711, '路右侧车行道边缘白色虚线是什么含义？', '/uploadFile/yuanBeiTu711.gif', '1', '1', NULL, '车辆可临时越线行驶', '车辆禁止越线行驶', '应急车道分界线', '人行横道分界线', 'A', '车行道边缘白色虚线：用白色虚线划分机非分界，允许车辆临时越线行驶，但应避让其他正常行驶的车辆、非机动车和行人。', 2, 1, '2021-12-19 00:11:47', '2021-12-19 00:11:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1712, '图中圈内两条白色虚线是什么标线？', '/uploadFile/yuanBeiTu712.gif', '1', '1', NULL, '小型车转弯线', '掉头引导线', '左转弯待转区线', '交叉路停车线', 'C', '左弯待转区线： 在左转弯专用车道前端，伸入交叉路口内，以白色虚线划分的位置，指示左转弯车辆在直行时段进入待转区等待左转，但不得妨碍对向直行车辆的正常行驶。', 2, 1, '2021-12-19 00:11:47', '2021-12-19 00:11:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1713, '图中圈内白色虚线是什么标线？', '/uploadFile/yuanBeiTu713.gif', '1', '1', NULL, '小型车转弯线', '车道连接线', '非机动车引导线', '路口导向线', 'D', '路口导向线：可辅助车辆在路口行驶和转向。连接同向车行道分界线或机非分界线的路口导向线为白色圆曲（或直）虚线；连接对向车行道分界线的路口导向线为黄色圆曲（或直）虚线。', 2, 1, '2021-12-19 00:11:47', '2021-12-19 00:11:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1714, '图中圈内黄色虚线是什么标线？', '/uploadFile/yuanBeiTu714.gif', '1', '1', NULL, '路口导向线', '非机动车引导线', '车道连接线', '小型车转弯线', 'A', '路口导向线：可辅助车辆在路口行驶和转向。连接同向车行道分界线或机非分界线的路口导向线为白色圆曲（或直）虚线；连接对向车行道分界线的路口导向线为黄色圆曲（或直）虚线。', 2, 1, '2021-12-19 00:11:47', '2021-12-19 00:11:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1715, '图中圈内白色实线是什么标线？', '/uploadFile/yuanBeiTu715.gif', '1', '1', NULL, '可变导向车道线', '导向车道线', '方向引导线', '单向行驶线', 'B', '有固定导向箭头的车道线，叫做导向车道线。设置于路口驶入段的车行道分界线，车辆在该车道内应按车道行驶方向标志显示的指向行驶。具有固定行驶方向的为导向车道线；可根据信号灯或路面标志牌指示改变行驶方向的为可变导向车道线。', 2, 1, '2021-12-19 00:11:48', '2021-12-19 00:11:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1716, '图中圈内的锯齿状白色实线是什么标线？', '/uploadFile/yuanBeiTu716.gif', '1', '1', NULL, '导向车道线', '方向引导线', '可变导向车道线', '单向行驶线', 'C', '可变导向车道线：顾名思义是能随时根据交通流量更改指示方向的车道，需按照对应的车道信号灯或路面标志牌的指示行驶。', 2, 1, '2021-12-19 00:11:48', '2021-12-19 00:11:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1717, '图中圈内的路面标记是什么标线？', '/uploadFile/yuanBeiTu717.gif', '1', '1', NULL, '路口示意线', '停车让行线', '减速让行线', '人行横道线', 'D', '人行横道线（斑马线）：白色平行粗实线，标示一定条件下准许行人横穿道路的路径，同时警示机动车驾驶人注意行人及非机动车过街。机动车禁止在人行横道线上停车，通过人行横道时应减速慢行，遇到行人需停车礼让。', 2, 1, '2021-12-19 00:11:48', '2021-12-19 00:11:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1718, '这个地面标记是什么标线？', '/uploadFile/yuanBeiTu718.gif', '1', '1', NULL, '人行横道预告', '交叉路口预告', '减速让行预告', '停车让行预告', 'A', '人行横道预告标识线：白色菱形图案，提示车辆驾驶人注意道路前方设置了人行横道线，需提前减速慢行。', 2, 1, '2021-12-19 00:11:48', '2021-12-19 00:11:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1719, '图中圈内的白色折线是什么标线？', '/uploadFile/yuanBeiTu719.gif', '1', '1', NULL, '减速行驶线', '车距确认线', '车速确认线', '路口减速线', 'B', '白色折线车距确认线：以白色折线作为车距确认标线，两道为一组，每组间隔50米，作为车辆驾驶人保持行车安全距离的参考。', 2, 1, '2021-12-19 00:11:49', '2021-12-19 00:11:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1720, '图中圈内的白色半圆状标记是什么标线？', '/uploadFile/yuanBeiTu720.gif', '1', '1', NULL, '减速行驶线', '车速确认线', '车距确认线', '路口减速线', 'C', '白色半圆状车距确认线：以白色半圆状作为车距确认线，设置在气象条件复杂、影响安全行车的路段两侧，每隔50米一个，作为车辆驾驶人保持行车安全距离的参考。', 2, 1, '2021-12-19 00:11:49', '2021-12-19 00:11:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1721, '路面由白色虚线和三角地带标线组成的是什么标线？', '/uploadFile/yuanBeiTu721.gif', '1', '1', NULL, '道路入口标线', '可跨越式分道线', '道路出口减速线', '道路出口标线', 'D', '图中是道路出口标线，一般由出口的纵向标线和三角地带标线组成。用于引导驶出车辆的运行轨迹，使车辆安全交汇，减少与突出缘石碰撞的可能。', 2, 1, '2021-12-19 00:11:49', '2021-12-19 00:11:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1722, '路面上白色虚线和三角地带标线组成的是什么标线？', '/uploadFile/yuanBeiTu722.gif', '1', '1', NULL, '道路入口标线', '可跨越式分道线', '道路入口减速线', '道路出口标线', 'A', '道路入口标线为白色，一般由入口的纵向标线和三角地带标线组成。用于引导驶入车辆的运行轨迹，使车辆安全交汇，减少与突出缘石碰撞的可能。', 2, 1, '2021-12-19 00:11:50', '2021-12-19 00:11:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1723, '这种白色矩形标线框含义是什么？', '/uploadFile/yuanBeiTu723.gif', '1', '1', NULL, '出租车专用上下客停车位', '平行式停车位', '倾斜式停车位', '垂直式停车位', 'B', '平行式停车位标线：车辆平行于通道方向停放在停车位内。', 2, 1, '2021-12-19 00:11:50', '2021-12-19 00:11:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1724, '这种停车标线含义是什么？', '/uploadFile/yuanBeiTu724.gif', '1', '1', NULL, '专用待客停车位', '专用上下客停车位', '固定停车方向停车位', '机动车限时停车位', 'C', '固定停车方向停车位标线：停车位对车辆停车方向有特殊要求，箭头所指方向标示停车后车头的朝向。', 2, 1, '2021-12-19 00:11:50', '2021-12-19 00:11:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1725, '这种白色矩形标线框含义是什么？', '/uploadFile/yuanBeiTu725.gif', '1', '1', NULL, '长时停车位', '限时停车位', '专用停车位', '免费停车位', 'B', '机动车限时停车位标线：标示机动车只能在停车位内标注的时段停放，其他时段禁止停放。', 2, 1, '2021-12-19 00:11:50', '2021-12-19 00:11:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1726, '红色圆圈内标线含义是什么？', '/uploadFile/yuanBeiTu726.gif', '1', '1', NULL, '临时停靠站', '港湾式停靠站', '应急停车带', '公交车停靠站', 'B', '港湾式停靠站标线：标示车辆通向专门的分离引道的路径和停靠位置，由渐变段引道白色虚线、正常段外缘白色实线或白色填充线组成。', 2, 1, '2021-12-19 00:11:51', '2021-12-19 00:11:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1727, '红色圆圈内标线含义是什么？', '/uploadFile/yuanBeiTu727.gif', '1', '1', NULL, '临时停靠站', '大客车停靠站', '公交车停靠站', '应急停车带', 'C', '车种专用港湾式停靠站标线：与停靠站中间标注的车辆类型文字（如公交车、校车）相符的特定车辆停靠，除特定车辆外，其他车辆不得在此区域停留。图中标注公交车，因此是公交车停靠站。', 2, 1, '2021-12-19 00:11:51', '2021-12-19 00:11:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1728, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu728.gif', '1', '1', NULL, '指示车道', '指示禁行', '指示合流', '指示直行', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向前方，表示直行。', 2, 1, '2021-12-19 00:11:51', '2021-12-19 00:11:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1729, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu729.gif', '1', '1', NULL, '指示向左转弯或掉头', '指示直行或向左变道', '指示直行或左转弯', '指示直行或掉头', 'C', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向前和指向左，表示前方路口可以直行或左转。', 2, 1, '2021-12-19 00:11:52', '2021-12-19 00:11:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1730, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu730.gif', '1', '1', NULL, '指示向右转弯或掉头', '指示直行或右转弯', '指示直行或向右变道', '指示直行或掉头', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向前和指向右，表示直行或右转。', 2, 1, '2021-12-19 00:11:52', '2021-12-19 00:11:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1731, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu731.gif', '1', '1', NULL, '指示前方右转弯', '指示向左变道', '指示前方直行', '指示前方左转弯', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向左，表示左转。', 2, 1, '2021-12-19 00:11:52', '2021-12-19 00:11:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1732, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu732.gif', '1', '1', NULL, '指示前方右转弯', '指示前方掉头', '指示前方直行', '指示向左变道', 'A', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向右，表示右转。', 2, 1, '2021-12-19 00:11:52', '2021-12-19 00:11:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1733, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu733.gif', '1', '1', NULL, '指示前方右转', '指示前方掉头', '指示前方直行', '指示向左变道', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向反向，表示掉头。', 2, 1, '2021-12-19 00:11:53', '2021-12-19 00:11:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1734, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu734.gif', '1', '1', NULL, '指示前方可左转或掉头', '指示前方可直行或左转', '指示前方可直行或掉头', '指示前方直行向左变道', 'C', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向前和指向反向，表示直行或掉头。', 2, 1, '2021-12-19 00:11:53', '2021-12-19 00:11:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1735, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu735.gif', '1', '1', NULL, '指示前方可直行或向左变道', '指示前方可直行或左转', '指示前方可直行或掉头', '指示前方可左转或掉头', 'D', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向反向和指向左，表示左转或掉头。', 2, 1, '2021-12-19 00:11:53', '2021-12-19 00:11:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1736, '这个导向箭头是何含义？', '/uploadFile/yuanBeiTu736.gif', '1', '1', NULL, '提示前方有左弯或需向左合流', '提示前方有右弯或需向右合流', '提示前方右侧有障碍需向左合流', '提示前方有左弯或需向左绕行', 'A', '导向箭头颜色为白色，用以指示车辆的行驶方向，箭头提示前方道路有左弯或需向左合流。', 2, 1, '2021-12-19 00:11:53', '2021-12-19 00:11:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1737, '路面上导向箭头是何含义？', '/uploadFile/yuanBeiTu737.gif', '1', '1', NULL, '提示前方有左弯或需向左合流', '提示前方有右弯或需向右合流', '提示前方有障碍需向左合流', '提示前方有左弯或需向左绕行', 'B', '导向箭头颜色为白色，用以指示车辆的行驶方向，箭头提示前方道路有右弯或需向右合流。', 2, 1, '2021-12-19 00:11:54', '2021-12-19 00:11:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1738, '路面上导向箭头是何含义？', '/uploadFile/yuanBeiTu738.gif', '1', '1', NULL, '指示前方道路是Y型交叉口', '指示前方道路是分离式道路', '指示前方道路仅可左右转弯', '指示前方道路需向左右合流', 'C', '导向箭头颜色为白色，用以指示车辆的行驶方向。箭头指向左和指向右，表示左右转弯。', 2, 1, '2021-12-19 00:11:54', '2021-12-19 00:11:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1739, '这个路面数字标记是何含义？', '/uploadFile/yuanBeiTu739.gif', '1', '1', NULL, '保持车距标记', '最小间距标记', '速度限制标记', '道路编号标记', 'C', '路面限速标记字符：白色字符表示相应车行道上车辆行驶的最低限速值；黄色字符表示相应车行道上车辆行驶的最高限速值。', 2, 1, '2021-12-19 00:11:54', '2021-12-19 00:11:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1740, '这个路面标记是何含义？', '/uploadFile/yuanBeiTu740.gif', '1', '1', NULL, '最低限速为100公里/小时', '平均速度为100公里/小时', '解除100公里/小时限速', '最高限速为100公里/小时', 'D', '路面限速标记字符：白色字符表示相应车行道上车辆行驶的最低限速值；黄色字符表示相应车行道上车辆行驶的最高限速值。', 2, 1, '2021-12-19 00:11:55', '2021-12-19 00:11:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1741, '这个路面标记是何含义？', '/uploadFile/yuanBeiTu741.gif', '1', '1', NULL, '最低限速为80公里/小时', '平均速度为80公里/小时', '解除80公里/小时限速', '最高限速为80公里/小时', 'A', '路面限速标记字符：白色字符表示相应车行道上车辆行驶的最低限速值；黄色字符表示相应车行道上车辆行驶的最高限速值。', 2, 1, '2021-12-19 00:11:55', '2021-12-19 00:11:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1742, '这个路面标记是何含义？', '/uploadFile/yuanBeiTu742.gif', '1', '1', NULL, '自行车专用道', '非机动车道', '摩托车专用道', '电瓶车专用道', 'B', '非机动车路面标记：施划于车道起点或车道中，表示该车道为非机动车道，禁止机动车占道通行。', 2, 1, '2021-12-19 00:11:55', '2021-12-19 00:11:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1743, '路中心的双黄实线属于哪一类标线？', '/uploadFile/yuanBeiTu743.gif', '1', '1', NULL, '指示标线', '辅助标线', '警告标志', '禁止标线', 'D', '路中心的双黄实线属于禁止标线，用于分隔对向行驶的交通流，并禁止双方向车辆越线或压线行驶。', 2, 1, '2021-12-19 00:11:55', '2021-12-19 00:11:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1744, '路中心双黄实线是何含义？', '/uploadFile/yuanBeiTu744.gif', '1', '1', NULL, '可跨越对向车道分界线', '禁止跨越对向车行道分界线', '双侧可跨越同向车道分界线', '单向行驶车道分界线', 'B', '双黄实线禁止跨越对向车行道分界线：道路中线为双黄实线，用于分隔对向行驶的交通流，并禁止双方向车辆越线或压线行驶。', 2, 1, '2021-12-19 00:11:56', '2021-12-19 00:11:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1745, '路中心黄色虚实线是何含义？', '/uploadFile/yuanBeiTu745.gif', '1', '1', NULL, '实线一侧禁止越线', '虚线一侧禁止越线', '实线一侧允许越线', '两侧均可越线行驶', 'A', '黄色虚实线禁止跨越对向车行道分界线：实线一侧禁止车辆越线或压线行驶，虚线一侧准许车辆暂时越线或转弯。越线行驶的车辆应避让正常行驶的车辆。', 2, 1, '2021-12-19 00:11:56', '2021-12-19 00:11:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1746, '路中心的黄色斜线填充是何含义？', '/uploadFile/yuanBeiTu746.gif', '1', '1', NULL, '可跨越对向车道分界线', '双侧可跨越同向车道分界线', '禁止跨越对向车行道分界线', '单向行驶车道分界线', 'C', '黄色斜线填充的双黄实线，禁止跨越对向车行道分界线。', 2, 1, '2021-12-19 00:11:56', '2021-12-19 00:11:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1747, '路中心白色实线是何含义？', '/uploadFile/yuanBeiTu747.gif', '1', '1', NULL, '禁止跨越对向车行道分界线', '双侧可跨越同向车道分界线', '禁止跨越同向车行道分界线', '单侧可跨越同向车道分界线', 'C', '禁止跨越同向车行道分界线：白色实线，禁止车辆跨越车行道分界线进行变换车道或借道超车。', 2, 1, '2021-12-19 00:11:57', '2021-12-19 00:11:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1748, '路缘石上的黄色虚线是何含义？', '/uploadFile/yuanBeiTu748.gif', '1', '1', NULL, '禁止临时停车', '禁止上下人员', '禁止长时停车', '禁止装卸货物', 'C', '路缘石上的黄色虚线，禁止路边长时间停、放车辆，但一般情况下允许装卸货物或上下人员等的临时停放。 黄色实线，禁止路边停、放车辆。', 2, 1, '2021-12-19 00:11:57', '2021-12-19 00:11:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1749, '路缘石上的黄色实线是何含义？', '/uploadFile/yuanBeiTu749.gif', '1', '1', NULL, '仅允许上下人员', '仅允许装卸货物', '禁止长时间停车', '禁止停放车辆', 'D', '路缘石上的黄色实线，禁止路边停、放车辆； 黄色虚线，禁止路边长时间停、放车辆，但一般情况下允许装卸货物或上下人员等的临时停放。', 2, 1, '2021-12-19 00:11:57', '2021-12-19 00:11:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1750, '图中圈内白色横实线是何含义？', '/uploadFile/yuanBeiTu750.gif', '1', '1', NULL, '停止线', '让行线', '减速线', '待转线', 'A', '停止线：信号灯路口的白色横实线，表示车辆等待放行信号或者停车让行的停车位置的路面标线，并且不能超过或压下该线。 【拓展知识】 以停止线为界，停止线到路口的区域称为停止线以内，停止线与车道间的区域称为停止线以外。', 2, 1, '2021-12-19 00:11:57', '2021-12-19 00:11:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1751, '路口最前端的双白实线是什么含义？', '/uploadFile/yuanBeiTu751.gif', '1', '1', NULL, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', 'B', '停车让行线：两条平行白色实线和一个白色“停”字，表示车辆在此路口应停车让干道车辆先行。', 2, 1, '2021-12-19 00:11:58', '2021-12-19 00:11:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1752, '路口最前端的双白虚线是什么含义？', '/uploadFile/yuanBeiTu752.gif', '1', '1', NULL, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', 'C', '减速让行线：两条平行白色虚线和一个白色倒三角形，表示车辆在此路口应减速让干道车辆先行。', 2, 1, '2021-12-19 00:11:58', '2021-12-19 00:11:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1753, '图中圈内三角填充区域是什么标线？', '/uploadFile/yuanBeiTu753.gif', '1', '1', NULL, '网状线', '停车线', '减速线', '导流线', 'D', '导流线的颜色为白色，起到引导和分流车辆的作用。与道路中心线相连时，也可用黄色。标线形式可分为单实线、V形线和斜纹线三种。车辆在交叉口需按规定的路线行驶，不得压线或越线行驶。', 2, 1, '2021-12-19 00:11:58', '2021-12-19 00:11:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1754, '这个路面标记是什么标线？', '/uploadFile/yuanBeiTu754.gif', '1', '1', NULL, '禁驶区', '网状线', '导流线', '中心圈', 'D', '菱形中心圈标线，设在平面交叉路口的中心，用以区分车辆大、小转弯或作为交叉口车辆左右转弯的指示，车辆不得压线行驶。', 2, 1, '2021-12-19 00:11:58', '2021-12-19 00:11:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1755, '这个路面标记是什么标线？', '/uploadFile/yuanBeiTu755.gif', '1', '1', NULL, '禁驶区', '网状线', '中心圈', '导流线', 'C', '圆形中心圈，设置在交叉路口中心的白色圆形或菱形区域，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。', 2, 1, '2021-12-19 00:11:59', '2021-12-19 00:11:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1756, '图中路口中央黄色路面标记是什么标线？', '/uploadFile/yuanBeiTu756.gif', '1', '1', NULL, '中心圈', '导流线', '网状线', '停车区', 'C', '网状线：严格禁止一切车辆长时或临时停车，防止交通阻塞。', 2, 1, '2021-12-19 00:11:59', '2021-12-19 00:11:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1757, '图中圈内两条黄色虚线间的区域是何含义？', '/uploadFile/yuanBeiTu757.gif', '1', '1', NULL, '大客车专用车道', '营运客车专用车道', '出租车专用车道', '公交专用车道', 'D', '公交车专用车道线：由黄色虚线及白色文字组成，表示除公交车外，其他车辆及行人不得进入该车道。', 2, 1, '2021-12-19 00:11:59', '2021-12-19 00:11:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1758, '道路最左侧白色虚线区域是何含义？', '/uploadFile/yuanBeiTu758.gif', '1', '1', NULL, '多乘员车辆专用车道', '小型客车专用车道', '未载客出租车专用车道', '大型客车专用车道', 'A', '多乘员车辆专用车道线：由白色虚线及白色文字“多乘员专用”组成，表示该车道为有多个乘车人的多乘员车辆专用的车道，未载乘客或乘员数未达规定的车辆不得入内行驶。', 2, 1, '2021-12-19 00:11:59', '2021-12-19 00:11:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1759, '路面上的黄色标记是何含义？', '/uploadFile/yuanBeiTu759.gif', '1', '1', NULL, '禁止转弯', '禁止掉头', '允许掉头', '禁止直行', 'B', '禁止掉头标记：由黄色导向箭头和黄色叉形标记左右组合而成，黄色叉形标记位于左侧，表示本路口或区间禁止车辆掉头。', 2, 1, '2021-12-19 00:12:00', '2021-12-19 00:12:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1760, '路面上的黄色标记是何含义？', '/uploadFile/yuanBeiTu760.gif', '1', '1', NULL, '禁止左转', '禁止掉头', '禁止右转', '禁止直行', 'C', '箭头指向右，表示禁止右转。由黄色导向箭头和黄色叉形标记左右组合而成的禁止转弯标记，黄色叉形标记位于左侧，表示本路口或区间禁止车辆转弯。', 2, 1, '2021-12-19 00:12:00', '2021-12-19 00:12:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1761, '路面上的白色标线是何含义？', '/uploadFile/yuanBeiTu761.gif', '1', '1', NULL, '车行道横向减速标线', '道路施工提示标线', '车行道纵向减速标线', '车道变少提示标线', 'A', '车行道横向减速标线：警告车辆驾驶人本车道需减速慢行。', 2, 1, '2021-12-19 00:12:00', '2021-12-19 00:12:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1762, '路面上的黄色标线是何含义？', '/uploadFile/yuanBeiTu762.gif', '1', '1', NULL, '车行道变多标线', '路面宽度渐变标线', '接近障碍物标线', '施工路段提示线', 'B', '三车行道变为双车行道渐变段标线（路面宽度渐变）：警告车辆驾驶人路宽或车道数变化，应谨慎行车，并禁止超车。', 2, 1, '2021-12-19 00:12:00', '2021-12-19 00:12:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1763, '路面上的黄色填充标线是何含义？', '/uploadFile/yuanBeiTu763.gif', '1', '1', NULL, '接近移动障碍物标线', '加宽隔离带标线', '接近障碍物标线', '接近狭窄路面标线', 'C', '接近障碍物标线：指示路面有固定性障碍物，警告车辆驾驶人谨慎行车，引导交通流顺畅驶离障碍物区域。', 2, 1, '2021-12-19 00:12:01', '2021-12-19 00:12:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1764, '这种黄黑相间的倾斜线条是什么标记？', '/uploadFile/yuanBeiTu764.gif', '1', '1', NULL, '减速标记', '实体标记', '突起标记', '立面标记', 'D', '黄黑相间的斜纹线条标记是立面标记警告标线，提醒驾驶人注意，在车行道或近旁有高出路面的构造物。', 2, 1, '2021-12-19 00:12:01', '2021-12-19 00:12:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1765, '路面上的菱形块虚线是何含义？', '/uploadFile/yuanBeiTu765.gif', '1', '1', NULL, '车行道纵向减速标线', '道路施工提示标线', '车行道横向减速标线', '车道变少提示标线', 'A', '车行道纵向减速标线：警告车辆驾驶人本车道需减速慢行。', 2, 1, '2021-12-19 00:12:01', '2021-12-19 00:12:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1766, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu766.gif', '1', '1', NULL, '左转弯信号', '停止信号', '右转弯信号', '靠边停车信号', 'B', '', 2, 1, '2021-12-19 00:12:01', '2021-12-19 00:12:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1767, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu767.gif', '1', '1', NULL, '直行信号', '转弯信号', '停止信号', '靠边停车信号', 'A', '', 2, 1, '2021-12-19 00:12:02', '2021-12-19 00:12:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1768, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu768.gif', '1', '1', NULL, '靠边停车信号', '左转弯待转信号', '左转弯信号', '右转弯信号', 'C', '', 2, 1, '2021-12-19 00:12:02', '2021-12-19 00:12:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1769, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu769.gif', '1', '1', NULL, '靠边停车信号', '左转弯待转信号', '减速慢行信号', '左转弯信号', 'D', '', 2, 1, '2021-12-19 00:12:02', '2021-12-19 00:12:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1770, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu770.gif', '1', '1', NULL, '左转弯待转信号', '左转弯信号', '减速慢行信号', '右转弯信号', 'A', '', 2, 1, '2021-12-19 00:12:03', '2021-12-19 00:12:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1771, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu771.gif', '1', '1', NULL, '靠边停车信号', '减速慢行信号', '停止信号', '右转弯信号', 'D', '', 2, 1, '2021-12-19 00:12:03', '2021-12-19 00:12:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1772, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu772.gif', '1', '1', NULL, '右转弯信号', '减速慢行信号', '左转弯待转信号', '靠边停车信号', 'A', '', 2, 1, '2021-12-19 00:12:03', '2021-12-19 00:12:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1773, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu773.gif', '1', '1', NULL, '右转弯信号', '减速慢行信号', '变道信号', '靠边停车信号', 'C', '', 2, 1, '2021-12-19 00:12:03', '2021-12-19 00:12:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1774, '这一组交通警察手势是什么信号？', '/uploadFile/yuanBeiTu774.gif', '1', '1', NULL, '靠边停车信号', '减速慢行信号', '变道信号', '右转弯信号', 'B', '', 2, 1, '2021-12-19 00:12:04', '2021-12-19 00:12:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1775, '这个标志是何含义？', '/uploadFile/yuanBeiTu775.gif', '1', '1', NULL, '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', 'B', '', 2, 1, '2021-12-19 00:12:04', '2021-12-19 00:12:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1776, '这个标志是何含义？', '/uploadFile/yuanBeiTu776.gif', '1', '1', NULL, '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', 'C', '', 2, 1, '2021-12-19 00:12:04', '2021-12-19 00:12:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1777, '这个标志是何含义？', '/uploadFile/yuanBeiTu777.gif', '1', '1', NULL, '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续下坡路段', 'D', '连续下坡：提醒前方有两个及以上的连续下坡路段，应小心驾驶。', 2, 1, '2021-12-19 00:12:04', '2021-12-19 00:12:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1778, '这种标志的作用是警告车辆驾驶人前方有危险，谨慎通行。', '/uploadFile/yuanBeiTu778.gif', '2', '1', NULL, '', '', '', '', 'A', '警告标志的作用是警告车辆驾驶人、行人前方有危险，道路使用者需谨慎通过。警告标志的颜色为黄底、黑边、黑图形；形状为等边三角形（顶角朝上）或矩形。个别标志例外。因此，题干表述正确。', 2, 1, '2021-12-19 00:12:05', '2021-12-19 00:12:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1779, '这个标志的作用是用以警告车辆驾驶人谨慎慢行，注意横向来车。', '/uploadFile/yuanBeiTu779.gif', '2', '1', NULL, '', '', '', '', 'A', '【十字交叉路口】警告标志，警告前方道路有十字交叉路口，应谨慎慢行，注意横向车辆。', 2, 1, '2021-12-19 00:12:05', '2021-12-19 00:12:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1780, '这个标志的含义是前方即将行驶至Y形交叉路口。', '/uploadFile/yuanBeiTu780.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:05', '2021-12-19 00:12:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1781, '这个标志的含义是警告前方道路有障碍物，车辆减速绕行。', '/uploadFile/yuanBeiTu781.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:05', '2021-12-19 00:12:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1782, '这个标志的含义是警告前方出现向左的急转弯路。', '/uploadFile/yuanBeiTu782.gif', '2', '1', NULL, '', '', '', '', 'A', '【一急二反三连续】一个弯表示急转弯，两个弯表示反向弯路，三个弯表示连续弯路。', 2, 1, '2021-12-19 00:12:06', '2021-12-19 00:12:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1783, '这个标志的含义是警告前方道路易滑，注意慢行。', '/uploadFile/yuanBeiTu783.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:06', '2021-12-19 00:12:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1784, '这个标志的含义是警告前方有两个相邻的反向转弯道路。', '/uploadFile/yuanBeiTu784.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:06', '2021-12-19 00:12:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1785, '这个标志的含义是提醒前方桥面宽度变窄。', '/uploadFile/yuanBeiTu785.gif', '2', '1', NULL, '', '', '', '', 'B', '【上窄下宽是窄路，中间窄是窄桥】图中标志为两侧变窄警告标志，是路面变窄不是桥面变窄。常设在两侧变窄道路，提示司机看到此标志时应提前减速，前方道路即将变窄，车辆密集度增加，行使空间降低，易发生车祸，所以请一定要提前减速。', 2, 1, '2021-12-19 00:12:06', '2021-12-19 00:12:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1786, '这个标志的含义是提醒前方右侧行车道或路面变窄。', '/uploadFile/yuanBeiTu786.gif', '2', '1', NULL, '', '', '', '', 'A', '', 2, 1, '2021-12-19 00:12:07', '2021-12-19 00:12:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1787, '这个标志的含义是提醒前方左侧行车道或路面变窄。', '/uploadFile/yuanBeiTu787.gif', '2', '1', NULL, '', '', '', '', 'A', '', 2, 1, '2021-12-19 00:12:07', '2021-12-19 00:12:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1788, '这个标志的含义是提醒前方两侧行车道或路面变窄。', '/uploadFile/yuanBeiTu788.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:07', '2021-12-19 00:12:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1789, '这个标志的含义是提醒前方道路变为不分离双向行驶路段。', '/uploadFile/yuanBeiTu789.gif', '2', '1', NULL, '', '', '', '', 'A', '双向交通警告标志，提醒前方是不分离双向行驶路段，应注意会车。因此，题干表述正确。', 2, 1, '2021-12-19 00:12:07', '2021-12-19 00:12:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1790, '这个标志的含义是警告车辆驾驶人前方是人行横道。', '/uploadFile/yuanBeiTu790.gif', '2', '1', NULL, '', '', '', '', 'B', '请注意！黄底黑字三角形，表示警告、注意、提醒，不要混淆成指示。如图所示注意行人标志，警告前方道路设有人行横道线，应减速慢行，注意行人。此标志通常设置在行人密集，或者有人行横道但是不易被发现的区域，提示司机注意行人。', 2, 1, '2021-12-19 00:12:08', '2021-12-19 00:12:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1791, '这个标志的含义是警告车辆驾驶人前方是学校区域。', '/uploadFile/yuanBeiTu791.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:08', '2021-12-19 00:12:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1792, '这个标志的含义是警告车辆驾驶人注意前方设有信号灯。', '/uploadFile/yuanBeiTu792.gif', '2', '1', NULL, '', '', '', '', 'A', '注意信号灯：警告前方路段设有信号灯，应按信号灯指示行车。', 2, 1, '2021-12-19 00:12:08', '2021-12-19 00:12:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1793, '这个标志的含义是提醒车辆驾驶人前方是傍山险路路段。', '/uploadFile/yuanBeiTu793.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:08', '2021-12-19 00:12:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1794, '这个标志的含义是提醒车辆驾驶人前方有很强的侧向风。', '/uploadFile/yuanBeiTu794.gif', '2', '1', NULL, '', '', '', '', 'A', '注意横风：提醒前方路段经常有很强的侧向风，应小心驾驶。', 2, 1, '2021-12-19 00:12:09', '2021-12-19 00:12:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1795, '这个标志的含义是提醒车辆驾驶人前方是急转弯路段。', '/uploadFile/yuanBeiTu795.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:09', '2021-12-19 00:12:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1796, '这个标志的含义是提醒车辆驾驶人前方是堤坝路段。', '/uploadFile/yuanBeiTu796.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，傍山险路警告标志，提醒车辆驾驶人前方是傍山险路，应小心驾驶。', 2, 1, '2021-12-19 00:12:09', '2021-12-19 00:12:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1797, '这个标志的含义是提醒车辆驾驶人前方路段通过村庄或集镇。', '/uploadFile/yuanBeiTu797.gif', '2', '1', NULL, '', '', '', '', 'A', '村庄：提醒前方是紧靠村庄、集镇且视线不良的路段，应小心驾驶。', 2, 1, '2021-12-19 00:12:10', '2021-12-19 00:12:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1798, '这个标志的含义是提醒车辆驾驶人前方是单向行驶并且照明不好的涵洞。', '/uploadFile/yuanBeiTu798.gif', '2', '1', NULL, '', '', '', '', 'B', '注意隧道警告标志，提醒前方是双向行驶并且照明不好的隧道，应注意慢行。因此，题干表述错误。', 2, 1, '2021-12-19 00:12:10', '2021-12-19 00:12:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1799, '这个标志的含义是提醒车辆驾驶人前方是车辆渡口。', '/uploadFile/yuanBeiTu799.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示渡口警告标志：提醒前方是车辆渡口，应谨慎驾驶。', 2, 1, '2021-12-19 00:12:10', '2021-12-19 00:12:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1800, '这个标志的含义是提醒车辆驾驶人前方是桥头跳车较严重的路段。', '/uploadFile/yuanBeiTu800.gif', '2', '1', NULL, '', '', '', '', 'B', '驼峰桥：提醒前方是拱度很大、影响视距的驼峰桥，应谨慎驾驶。因此，题干表述错误。', 2, 1, '2021-12-19 00:12:10', '2021-12-19 00:12:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1801, '这个标志的含义是提醒车辆驾驶人前方路面颠簸或有桥头跳车现象。', '/uploadFile/yuanBeiTu801.gif', '2', '1', NULL, '', '', '', '', 'A', '', 2, 1, '2021-12-19 00:12:11', '2021-12-19 00:12:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1802, '这个标志的含义是提醒车辆驾驶人前方是过水路面或漫水桥路段。', '/uploadFile/yuanBeiTu802.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，过水路面（漫水桥）：提醒车辆驾驶人前方是过水路面或漫水桥路段，应停车察明水情，确认安全后，低速通过。', 2, 1, '2021-12-19 00:12:11', '2021-12-19 00:12:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1803, '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', '/uploadFile/yuanBeiTu803.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:11', '2021-12-19 00:12:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1804, '这个标志的含义是提醒车辆驾驶人前方是无人看守铁路道口。', '/uploadFile/yuanBeiTu804.gif', '2', '1', NULL, '', '', '', '', 'A', '', 2, 1, '2021-12-19 00:12:11', '2021-12-19 00:12:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1805, '这个标志的含义是提醒车辆驾驶人前方是非机动车道。', '/uploadFile/yuanBeiTu805.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:12', '2021-12-19 00:12:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1806, '这个标志的含义是告示前方是拥堵路段，注意减速慢行。', '/uploadFile/yuanBeiTu806.gif', '2', '1', NULL, '', '', '', '', 'B', '图中是事故多发地段警告标志。用以告示前方道路为事故易发路段，谨慎驾驶。设在交通事故易发路段以前适当位置。题目描述错误，本题答案为错。', 2, 1, '2021-12-19 00:12:12', '2021-12-19 00:12:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1807, '这个标志的含义是告示前方道路施工，车辆左右绕行。', '/uploadFile/yuanBeiTu807.gif', '2', '1', NULL, '', '', '', '', 'B', '图中是 [左右绕行] 标志。用以告示前方道路有障碍物，车辆应按标志指示减速慢行。设在道路障碍物前适当位置。因为前方有障碍物才要左右绕行，没有任何和道路施工相关的信息提示，因此并非因为道路施工而绕行，本题错在“道路施工标志”。', 2, 1, '2021-12-19 00:12:12', '2021-12-19 00:12:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1808, '这个标志的含义是告示前方道路有障碍物，车辆左侧绕行。', '/uploadFile/yuanBeiTu808.gif', '2', '1', NULL, '', '', '', '', 'A', '注意障碍物警告标志—左侧绕行：告示前方道路有障碍物，应按标志指示减速慢行，从左侧绕行通过。', 2, 1, '2021-12-19 00:12:12', '2021-12-19 00:12:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1809, '这个标志的含义是告示前方道路是单向通行路段。', '/uploadFile/yuanBeiTu809.gif', '2', '1', NULL, '', '', '', '', 'B', '右侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', 2, 1, '2021-12-19 00:12:13', '2021-12-19 00:12:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1810, '这个标志的含义是告示前方是塌方路段，车辆应绕道行驶。', '/uploadFile/yuanBeiTu810.gif', '2', '1', NULL, '', '', '', '', 'B', '【施工】 警告标志：用以告示前方道路施工，车辆应减速慢行或绕道行驶。该标志可以作为临时标志支设在施工路段以前适当位置。因此，题干表述错误。', 2, 1, '2021-12-19 00:12:13', '2021-12-19 00:12:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1811, '这个标志的含义是告示车辆驾驶人应慢行或停车，确保干道车辆优先。', '/uploadFile/yuanBeiTu811.gif', '2', '1', NULL, '', '', '', '', 'A', '减速让行标志，告示车辆驾驶人必须慢行或停车，观察干道行车情况，在确保干道车辆优先通行且安全的前提下，方可进入路口。因此，题干表述正确。', 2, 1, '2021-12-19 00:12:13', '2021-12-19 00:12:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1812, '这个标志的含义是表示车辆会车时，对方车辆应停车让行。', '/uploadFile/yuanBeiTu812.gif', '2', '1', NULL, '', '', '', '', 'B', '会车让行：表示车辆会车时，我方车辆必须停车让对方车辆先行。因此，题干表述错误。', 2, 1, '2021-12-19 00:12:14', '2021-12-19 00:12:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1813, '这个标志的含义是指示此处设有室内停车场。', '/uploadFile/yuanBeiTu813.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:14', '2021-12-19 00:12:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1814, '这个标志的含义是指示此处设有室内停车场。', '/uploadFile/yuanBeiTu814.gif', '2', '1', NULL, '', '', '', '', 'A', '', 2, 1, '2021-12-19 00:12:14', '2021-12-19 00:12:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1815, '路中心黄色虚线的含义是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '/uploadFile/yuanBeiTu815.gif', '2', '1', NULL, '', '', '', '', 'A', '可跨越对向车行道分界线：道路中线为黄色虚线属于指示标线，用于分隔对向行驶的交通流。车辆在保证安全的情况下，可以越线超车或转弯。', 2, 1, '2021-12-19 00:12:14', '2021-12-19 00:12:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1816, '这个地面标记的含义是预告前方设有交叉路口。', '/uploadFile/yuanBeiTu816.gif', '2', '1', NULL, '', '', '', '', 'B', '白色菱形图案是人行横道预告标识线，指示车辆驾驶人注意道路前方设置了人行横道线，需提前减速慢行。', 2, 1, '2021-12-19 00:12:15', '2021-12-19 00:12:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1817, '路中心的双黄实线作用是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '/uploadFile/yuanBeiTu817.gif', '2', '1', NULL, '', '', '', '', 'B', '双黄实线禁止跨越对向车行道分界线：道路中心为双黄实线，用于分隔对向行驶的交通流，并禁止双方向车辆越线或压线行驶。', 2, 1, '2021-12-19 00:12:15', '2021-12-19 00:12:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1818, '驾驶的车辆正在被其他车辆超越时，若此时后方有跟随行驶的车辆，应怎样做？', '', '1', '1', NULL, '继续加速行驶', '稍向右侧行驶，保证横向安全距离', '靠道路中心行驶', '加速向右侧让路', 'B', '稍向右侧减速慢行，和前车、超车的车辆保持一定的安全距离。', 2, 1, '2021-12-19 00:12:15', '2021-12-19 00:12:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1819, '如图所示，在这种情况下遇到红灯交替闪烁时，要尽快通过道口。', '/uploadFile/yuanBeiTu819.gif', '2', '1', NULL, '', '', '', '', 'B', '红灯交替闪烁，说明此时情况很危险，火车马上就要来了，应该等火车通过后再通过。', 2, 1, '2021-12-19 00:12:15', '2021-12-19 00:12:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1820, '如图所示，以下哪种情况可以超车。', '/uploadFile/yuanBeiTu820.gif', '1', '1', NULL, 'C图', 'D图', 'B图', 'A图', 'A', '如图所示路况，安全超车需要满足两个条件：1、确认安全，即对向车道无来车；2、虚实线，实线一侧禁止车辆越线或压线行驶，虚线一侧准许车辆暂时越线。因此，C图所示的情况下可以超车。 请注意：图片的ABCD并不是对应选项的ABCD。', 2, 1, '2021-12-19 00:12:16', '2021-12-19 00:12:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1821, '如图所示，这种情况下只要后方、对向无来车，可以掉头。', '/uploadFile/yuanBeiTu821.gif', '2', '1', NULL, '', '', '', '', 'A', '【可跨越对向车行道分界线】道路中线为黄色虚线，用于分隔对向行驶的交通流。车辆在保证安全的情况下，可以越线超车、掉头或转弯。', 2, 1, '2021-12-19 00:12:16', '2021-12-19 00:12:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1822, '在没有交通信号指示的交叉路口，转弯的机动车让直行的车辆和行人先行。', '', '2', '1', NULL, '', '', '', '', 'A', '通过无交通信号灯的交叉路口，①右方道路来车先行；②转弯的机动车让直行的车辆先行；③相对方向行驶的右转弯的机动车让左转弯的车辆先行。', 2, 1, '2021-12-19 00:12:16', '2021-12-19 00:12:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1823, '如图所示，B车具有优先通行权。', '/uploadFile/yuanBeiTu823.gif', '2', '1', NULL, '', '', '', '', 'B', '【转弯让直行】如图所示：A车直行，B车右转弯，因此，A车具有优先通行权。 《道路交通安全法实施条例》第五十二条 机动车通过交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定： (一)有交通标志、标线控制的，让优先通行的一方先行； (二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；【让右原则】 (三)转弯的机动车让直行的车辆先行；【转弯让直行】 (四)相对方向行驶的右转弯的机动车让左转弯的车辆先行。【右转让左转】', 2, 1, '2021-12-19 00:12:16', '2021-12-19 00:12:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1824, '如图所示，在这种情况下只要后方、对向无来车，可以掉头。', '/uploadFile/yuanBeiTu824.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，路面黄色实线，是禁止标线，禁止跨越对向车行道分界线，因此禁止车辆越线掉头行驶。', 2, 1, '2021-12-19 00:12:17', '2021-12-19 00:12:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1825, '如图所示，A车具有优先通行权。', '/uploadFile/yuanBeiTu825.gif', '2', '1', NULL, '', '', '', '', 'B', '三个先行原则：转弯的机动车让直行的车辆先行，右方道路来车先行，右转弯车让左转弯车先行。图中，A车右转弯；B车左转弯，因此，A车应当让B车先行，即B车具有优先通行权。', 2, 1, '2021-12-19 00:12:17', '2021-12-19 00:12:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1826, '如图所示，A车在此时进入左侧车道是因为进入实线区不得变更车道。', '/uploadFile/yuanBeiTu826.gif', '2', '1', NULL, '', '', '', '', 'A', '变更车道时，需按导向箭头的指示在虚线区变更车道，进入实线区后不能变更车道。如图所示，A车选择此时变更到左侧车道，是因为此时正处在白色虚线区；如果这个时候不变更车道，继续向前行驶进入实线区后，就不能变更到左侧掉头车道了。题目表述的是A车选择此时进入左侧车道的原因，故说法正确。', 2, 1, '2021-12-19 00:12:17', '2021-12-19 00:12:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1827, '如图所示，在同向三车道高速公路上行车，车速每小时115公里应在哪条行车道上行驶？', '/uploadFile/yuanBeiTu827.gif', '1', '1', NULL, '最右侧行车道', '最左侧行车道', '中间行车道', '哪条都行', 'B', '如图上面的绿色标志，左侧限速在100-120，中间是80-100，右侧是60-80，车速每小时115公里，应该在左侧车道。', 2, 1, '2021-12-19 00:12:17', '2021-12-19 00:12:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1828, '驾驶机动车变更车道为什么要提前开启转向灯？', '', '1', '1', NULL, '开阔视野，便于观察路面情况', '提示前车让行', '提示行人让行', '提示其他车辆我方准备变更车道', 'D', '转向灯相当于一个信号，传达自己车辆动向的信息，提前开启转向灯，提醒后车我方车辆动向，注意减速慢行。', 2, 1, '2021-12-19 00:12:18', '2021-12-19 00:12:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1829, '如图所示，通过有这个标志的路口时应该减速让行。', '/uploadFile/yuanBeiTu829.gif', '2', '1', NULL, '', '', '', '', 'B', '图中路口双白实线和路边停车让行标志，表示车辆必须在停止线以外停车观察主路情况，确认安全后再通行。因此，题干表述错误。 【拓展延伸】', 2, 1, '2021-12-19 00:12:18', '2021-12-19 00:12:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1830, '如图所示，当A车后方有执行任务的救护车驶来时以下做法正确的是什么？', '/uploadFile/yuanBeiTu830.gif', '1', '1', NULL, '不必理会，继续行驶', '靠右减速让路', '向左转弯让路', '立即停车让路', 'B', '行车中遇到执行任务的消防车、救护车、工程救险车时要及时让行。', 2, 1, '2021-12-19 00:12:18', '2021-12-19 00:12:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1831, '机动车向左转弯、向左变更车道、驶离停车地点或者掉头时，提前开启左转向灯是为了什么？', '', '1', '1', NULL, '提示前车，将要向左变更行驶路线', '提示后车，将要向右变更行驶路线', '提示后车，将要向左变更行驶路线', '提示前车，将要向右变更行驶路线', 'C', '开启转向灯，主要是为了提醒后车，开启左转向灯，当然是向左变更行驶路线了。', 2, 1, '2021-12-19 00:12:18', '2021-12-19 00:12:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1832, '如图所示，A车具有优先通行权。', '/uploadFile/yuanBeiTu832.gif', '2', '1', NULL, '', '', '', '', 'A', '【转弯让直行】如图所示：A车直行，B车左转弯，因此，A车具有优先通行权。 《道路交通安全法实施条例》第五十二条 机动车通过交叉路口，除应当遵守第五十一条第(二)项、第(三)项的规定外，还应当遵守下列规定： (一)有交通标志、标线控制的，让优先通行的一方先行； (二)没有交通标志、标线控制的，在进入路口前停车瞭望，让右方道路的来车先行；【让右原则】 (三)转弯的机动车让直行的车辆先行；【转弯让直行】 (四)相对方向行驶的右转弯的机动车让左转弯的车辆先行。【右转让左转】', 2, 1, '2021-12-19 00:12:19', '2021-12-19 00:12:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1833, '如图所示，在这种情况下通过路口，交替使用远近光灯的目的是什么？', '/uploadFile/yuanBeiTu833.gif', '1', '1', NULL, '检查灯光是否能正常使用', '提示其他交通参与者注意来车', '准备变更车道', '超车前提示前车', 'B', '如图所示，驾驶机动车通过没有交通信号灯控制的路口时，应当交替使用远近光灯示意道路其他交通参与者注意来车。', 2, 1, '2021-12-19 00:12:19', '2021-12-19 00:12:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1834, '如图所示，在这种情况下跟车行驶，不能使用远光灯的原因是什么？', '/uploadFile/yuanBeiTu834.gif', '1', '1', NULL, '不利于看清远方的路况', '会影响自己的视线', '会影响前车驾驶人的视线', '不利于看清车前的路况', 'C', '夜间不按规定使用灯光，尤其是跟车行驶不关闭远光灯，会使得前车驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操纵失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 2, 1, '2021-12-19 00:12:19', '2021-12-19 00:12:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1835, '在超越前车时，提前开启左转向灯，变换使用远、近光灯或者鸣喇叭是为了什么？', '', '1', '1', NULL, '提醒后车以及前车', '提醒行人', '仅提醒后车', '仅提醒前车', 'A', '超车时交替使用远近光灯或者鸣喇叭是为了提醒前车，开启左转向灯是为了提醒后车，所以是为了提醒后车和前车。', 2, 1, '2021-12-19 00:12:19', '2021-12-19 00:12:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1836, '机动车驶近急弯、坡道顶端等影响安全视距的路段时，减速慢行并鸣喇叭示意是为了什么？', '', '1', '1', NULL, '测试喇叭是否能正常使用', '提示前车后方车辆准备超车', '提示对向交通参与者我方有来车', '避免行至坡道顶端车辆动力不足', 'C', '视线受阻，彼此看不到，所以就要靠喇叭来提醒，路上的行人或者车辆，我方有来车需要注意安全。', 2, 1, '2021-12-19 00:12:20', '2021-12-19 00:12:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1837, '夜间行车，需要超车时，变换远近光灯示意是为了提示前车。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间超车，由于光线差、驾驶员视野变小，仅仅通过打左转向灯有时候很难让前车注意到后车发出的超车信号。为了保证安全，可以在需要超车时，变换远近光灯提示前车注意避让。题目描述无误，本题答案为对。', 2, 1, '2021-12-19 00:12:20', '2021-12-19 00:12:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1838, '如图所示，在这种情况下驶近路口，车辆可以怎么行驶。', '/uploadFile/yuanBeiTu838.gif', '1', '1', NULL, '只能直行', '左转或者直行', '左转或右转', '直行或右转', 'B', '注意观察地面车道箭头和信号灯。如图所示，1、机动车所在车道的箭头指向左侧和前方，表示允许机动车左转或直行；2、前方车道信号灯显示绿灯，表示允许通行，因此，车辆可以左转或直行行驶。', 2, 1, '2021-12-19 00:12:20', '2021-12-19 00:12:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1839, '关于机动车灯光的使用，以下说法正确的是什么？', '', '1', '1', NULL, '夜间驾驶机动车在照明条件良好的路段必须使用远光灯', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光', '机动车灯光一个重要的作用是提示其他机动车驾驶人和行人', '机动车灯光的作用仅仅是为了在夜间照明', 'C', '对于车辆来说，车灯就好比人的眼睛一样重要，正常的开启或关闭关乎到驾驶人能否安全驾驶车辆到达目的地。简单的说，灯光有照明和信号两方面作用，一个是为驾驶员提供照明，在天气不好或是阴天的时候提供良好的视野；另一个重要作用是起到警示的作用，为的是提醒其余车辆与人员。', 2, 1, '2021-12-19 00:12:20', '2021-12-19 00:12:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1840, '如图所示，A车要在前方掉头行驶，可以在此处变换车道，进入左侧车道准备掉头。', '/uploadFile/yuanBeiTu840.gif', '2', '1', NULL, '', '', '', '', 'B', '图中虚线箭头代表的是车辆行驶路线，仔细观察虚线路径，可以看到变道时压到了白色实线。变道是不能压线的，因此图中A车要在前方掉头行驶，是不能在此处变换车道的。题目描述错误，本题答案为错。', 2, 1, '2021-12-19 00:12:21', '2021-12-19 00:12:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1841, '如图所示，通过有这个标志的路口时无需减速。', '/uploadFile/yuanBeiTu841.gif', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车通过路口时，遇到有减速让行标志的路口，要减速让行、缓慢通过。', 2, 1, '2021-12-19 00:12:21', '2021-12-19 00:12:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1842, '如图所示，在这种情况下，您应该轻踩制动踏板减速。', '/uploadFile/yuanBeiTu842.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十二条：机动车上道路行驶，不得超过限速标志标明的最高时速。 如图所示，道路最高限速60公里/小时，速度仪表盘显示速度大于60公里/小时，因此，应当轻踏制动踏板减速行驶。', 2, 1, '2021-12-19 00:12:21', '2021-12-19 00:12:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1843, '如图所示，这个标志设置在有人看守的铁路道口，提示驾驶人距有人看守的铁路道口的距离还有100米。', '/uploadFile/yuanBeiTu843.gif', '2', '1', NULL, '', '', '', '', 'B', '', 2, 1, '2021-12-19 00:12:22', '2021-12-19 00:12:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1844, '如图所示，铁路道口设置这个标志，是提示驾驶人前方路口有单股铁道。', '/uploadFile/yuanBeiTu844.gif', '2', '1', NULL, '', '', '', '', 'B', '叉形符号：表示多股铁路与道路相交，设在铁路道口标志上方。', 2, 1, '2021-12-19 00:12:22', '2021-12-19 00:12:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1845, '路口转弯过程中，持续开启转向灯，主要是因为什么？', '', '1', '1', NULL, '让其他驾驶人知道您正在转弯', '完成转弯动作前，关闭转向灯会对车辆造成损害', '让其他驾驶人知道您正在超车', '完成转弯动作前，关闭转向灯是习惯动作', 'A', '路口转弯过程中，持续开启转向灯，是为了提醒别的行人和车辆此车正在转弯，注意安全。直至转弯动作完成后，才能关闭转向灯。', 2, 1, '2021-12-19 00:12:22', '2021-12-19 00:12:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1846, '这属于哪一种标志？', '/uploadFile/yuanBeiTu846.gif', '1', '1', NULL, '禁令标志', '指示标志', '指路标志', '警告标志', 'A', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，禁令标志的颜色除个别标志外，通常为白底、红圈、红杠、黑图形，图形压杠；禁令标志的形状为圆形、八角形、顶角向下的等边三角形。 如图所示的禁令标志，分别是停车让行、减速让行、禁止小型客车驶入。', 2, 1, '2021-12-19 00:12:22', '2021-12-19 00:12:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1847, '这属于哪一种标志？', '/uploadFile/yuanBeiTu847.gif', '1', '1', NULL, '指路标志', '指示标志', '禁令标志', '警告标志', 'B', '【蓝底白图是指示，不要混淆成警告】指示标志的颜色为蓝底、白图案；形状分为圆形、长方形和正方形。', 2, 1, '2021-12-19 00:12:22', '2021-12-19 00:12:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1848, '这属于哪一类标志？', '/uploadFile/yuanBeiTu848.gif', '1', '1', NULL, '指路标志', '指示标志', '禁令标志', '警告标志', 'A', '指路标志的特征：一般道路上的指路标志为蓝底、白图形、白边框、蓝色衬边；高速公路和城市快速路指路标志为绿底、白图形、白边框、绿色衬边。 指路标志形状：长方形和正方形，个别标志例外。 指路标志的作用：指引道路信息，为驾驶人传递道路方向、地点。距离等信息。 图中指路标志，从左到右依次为：交叉路口预告、国道编号、高速公路地点距离。', 2, 1, '2021-12-19 00:12:23', '2021-12-19 00:12:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1849, '这属于哪一种标志？', '/uploadFile/yuanBeiTu849.gif', '1', '1', NULL, '作业区标志', '告示标志', '高速公路标志', '旅游区标志', 'D', '旅游区标志的颜色为棕底、白字（图形）、白边框、棕色衬边，形状为矩形；旅游区标志的作用是方便旅游者识别通往旅游区的方向和距离，了解旅游项目的类别。', 2, 1, '2021-12-19 00:12:23', '2021-12-19 00:12:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1850, '这个标志是何含义？', '/uploadFile/yuanBeiTu850.gif', '1', '1', NULL, '提醒车辆驾驶人前方道路沿水库、湖泊、河流', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', 'B', '', 2, 1, '2021-12-19 00:12:23', '2021-12-19 00:12:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1851, '遇到下列哪个标志，你不需要主动让行？', '/uploadFile/yuanBeiTu851.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '图1：八角停车让行；图2：三角减速让行；图3：红色箭头向上，会车让行；图4：红色箭头向下，会车先行。因此不需要主动让行的是图4，先行。', 2, 1, '2021-12-19 00:12:24', '2021-12-19 00:12:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1852, '以下交通标志中，表示禁止一切车辆和行人通行的是？', '/uploadFile/yuanBeiTu852.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '图1是禁止通行标志，表示禁止一切车辆和行人通行，此标志设在禁止通行的道路入口处； 图2是禁止驶入标志，表示禁止一切车辆驶入，此标志设在单行路的出口处或禁止驶入的路段入口； 图3是禁止机动车驶入标志，表示禁止各类机动车驶入，此标志设在禁止机动车通行路段的入口处； 图4是禁止直行标志，表示前方路口禁止一切车辆直行。 所以该题选择图1。 更多禁令标志详解，可以进入驾驶员考试网《交通标志》频道查看', 2, 1, '2021-12-19 00:12:24', '2021-12-19 00:12:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1853, '以下交通标志表示的含义是什么', '/uploadFile/yuanBeiTu853.gif', '1', '1', NULL, '禁止机动车驶入', '禁止小客车驶入', '禁止所有车辆驶入', '禁止非机动车驶入', 'A', '', 2, 1, '2021-12-19 00:12:24', '2021-12-19 00:12:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1854, '下列哪个标志禁止一切车辆长时间停放，临时停车不受限制。', '/uploadFile/yuanBeiTu854.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '图1表示禁止停车；图2表示停车让行；图3表示禁止驶入；图4表示禁止长时停车。', 2, 1, '2021-12-19 00:12:25', '2021-12-19 00:12:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1855, '遇到这个标志，您不可以左转，但是可以掉头。', '/uploadFile/yuanBeiTu855.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通全法实施条例》第四十九条 机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 2, 1, '2021-12-19 00:12:25', '2021-12-19 00:12:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1856, '以下交通标志表示除小客车和货车外，其他车辆可以直行。', '/uploadFile/yuanBeiTu856.gif', '2', '1', NULL, '', '', '', '', 'A', '图片中的标志是一个组合标志，位于上方的是组合中的主要标志—【禁止直行】标志，位于下方的是辅助标志，辅助说明具体禁止哪种车辆直行。我们可以看到辅助标志中有货车和小客车，因此组合到一起就是表示小客车和货车禁止直行（也表示除小客车和货车外，其他车辆可以直行）。题干描述正确，因此本题答案为正确。 拓展知识： 小客车又称“轿车”。一般指乘坐9人以下的小型轻便载客汽车。小客车是小型车大范畴的统称，并非专指运营载客汽车。根据车厢结构的不同，小客车可以分为：固定车顶的轿车、活络车顶的敞逢车等。', 2, 1, '2021-12-19 00:12:25', '2021-12-19 00:12:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1857, '以下哪个标志，表示干路先行？', '/uploadFile/yuanBeiTu857.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '图1表示单行路（直行）；图2表示直行；图3表示直行车道；图4表示路口优先通行。', 2, 1, '2021-12-19 00:12:25', '2021-12-19 00:12:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1858, '以下交通标志表示单行线的是哪一项？', '/uploadFile/yuanBeiTu858.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '图1表示单行路（直行）；图2表示直行车道；图3表示路口优先通行；图4表示靠右侧道路行驶。', 2, 1, '2021-12-19 00:12:26', '2021-12-19 00:12:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1859, '下列哪个标志，指示车辆直行和右转合用车道？', '/uploadFile/yuanBeiTu859.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '图1：直行和右转合用车道；图2：干路先行；图3：右转车道；图4：分向行驶车道。', 2, 1, '2021-12-19 00:12:26', '2021-12-19 00:12:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1860, '下列哪个标志为最低限速标志？', '/uploadFile/yuanBeiTu860.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '图1表示鸣喇叭；图2表示最低限速；图3表示机动车行驶标志；图4表示非机动车行驶标志。', 2, 1, '2021-12-19 00:12:26', '2021-12-19 00:12:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1861, '下列哪个表示一般道路车道数变少？', '/uploadFile/yuanBeiTu861.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '图1表示错车道；图2表示车道数变少；图3表示车道数增加；图4表示隧道出口距离预告。', 2, 1, '2021-12-19 00:12:26', '2021-12-19 00:12:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1862, '当驾驶员看到以下标志时，需减速慢行，是因为什么？', '/uploadFile/yuanBeiTu862.gif', '1', '1', NULL, '前方车行道或路面变窄', '前方有弯道', '前方车流量较大', '前方有窄桥', 'A', '警告标志右侧变窄，行车注意安全。用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6m以下的路段起点前方。', 2, 1, '2021-12-19 00:12:27', '2021-12-19 00:12:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1863, '下列哪个标志提示驾驶人下陡坡？', '/uploadFile/yuanBeiTu863.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '图1是向左急转弯标志，警告驾驶人减速慢行；图2是连续弯路标志，警告驾驶人减速慢行；图3是反向弯路标志，警告驾驶人减速慢行；图4是下坡路标志，提醒驾驶人下陡坡小心驾驶。', 2, 1, '2021-12-19 00:12:27', '2021-12-19 00:12:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1864, '下列哪个标志提示驾驶人连续弯路？', '/uploadFile/yuanBeiTu864.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '图1表示向左急转弯，警告驾驶人减速慢行；图2表示连续弯路，警告驾驶人减速慢行；图3表示反向弯路，警告驾驶人减速慢行；图4表示环形交叉路口，警告驾驶人谨慎慢行，注意横向来车。', 2, 1, '2021-12-19 00:12:27', '2021-12-19 00:12:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1865, '看到这个标志的时候，您应该开启前照灯。', '/uploadFile/yuanBeiTu865.gif', '2', '1', NULL, '', '', '', '', 'A', '隧道开车灯：警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。', 2, 1, '2021-12-19 00:12:27', '2021-12-19 00:12:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1866, '遇到这个标志时，您应该主动确认您与前车之间的距离。', '/uploadFile/yuanBeiTu866.gif', '2', '1', NULL, '', '', '', '', 'A', '图片是注意保持车距警告标志：警告前方路段经常发生车辆追尾事故，应注意和前车保持安全距离。', 2, 1, '2021-12-19 00:12:28', '2021-12-19 00:12:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1867, '在下图所示的交通事故中，有关事故责任认定，正确的说法是什么？', '/uploadFile/yuanBeiTu867.gif', '1', '1', NULL, 'B车闯红灯，所以B负全责', 'B车可以右转，但不得妨碍被放行的直行车辆，所以B车负全责', '直行车辆不得妨碍右转车辆，所以A车负全责', '右侧方向的车辆具有优先通行权，故A车负全责', 'B', '路口遇到圆形的红绿灯变成红灯时，在不妨碍被放行的车辆、行人通行的情况下，车辆可以直接右转，故B车应该在不妨碍直行的A车正常通行的情况下右转。图中B车与A车相撞，B车明显影响了直行车的正常通行，所以B车负全责。', 2, 1, '2021-12-19 00:12:28', '2021-12-19 00:12:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1868, '在图中所示的交通事故中，有关事故责任认定，正确的说法是？', '/uploadFile/yuanBeiTu868.gif', '1', '1', NULL, 'B车违反交通信号，所以B负全责', 'B车不得妨碍被放行的直行车辆，所以B车负全责', '直行车辆不得妨碍左转车辆，所以A车负全责', '右侧方向的车辆具有优先通行权，故B车负全责', 'B', 'A车是直行车道享有最高通行权利，而B车与A车相撞，明显影响了直行车的正常同行，所以B车负全责。', 2, 1, '2021-12-19 00:12:29', '2021-12-19 00:12:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1869, '雨天对安全行车的主要影响是什么？', '', '1', '1', NULL, '电器设备易受潮短路', '路面湿滑，视线受阻', '发动机易熄火', '行驶阻力增大', 'B', '雨天行驶，视线受阻、路面湿滑，车轮的附着力随车速的增加急剧变小，车轮很容易发生侧滑，当车辆高速行驶时也易发生“水滑”现象，因此一定要合理控制车速。', 3, 1, '2021-12-19 00:12:29', '2021-12-19 00:12:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1870, '下雨后路面湿滑，车辆行驶中紧急制动时，容易导致什么？', '', '1', '1', NULL, '引起发动机熄火', '不被其他车辆驾驶人发现', '因视线模糊而撞车', '发生侧滑、引发交通事故', 'D', '下雨后路面湿滑，车辆行驶中一定要合理控制车速，充分利用发动机制动减速，不要急转向或紧急制动，以免发生侧滑，引发交通事故。', 3, 1, '2021-12-19 00:12:29', '2021-12-19 00:12:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1871, '雾天对安全行车的主要影响是什么？', '', '1', '1', NULL, '发动机易熄火', '易发生侧滑', '行驶阻力增大', '能见度低，视线不清', 'D', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄；浓雾天时方向难辨，行进中很难看清前方障碍（如慢行车、行人、故障车、凹坑等），容易发生交通事故。', 3, 1, '2021-12-19 00:12:29', '2021-12-19 00:12:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1872, '行人参与道路交通的主要特点是什么？', '', '1', '1', NULL, '行动迟缓', '喜欢聚集、围观', '行走随意性大，方向多变', '以上都是', 'C', '道路上的行人各有差异，但行人的主要特点是行走随意性大，方向多变，很容易引起险情；而行动迟缓、喜欢聚集、围观等是个别行人的特点不是主要的特点。', 3, 1, '2021-12-19 00:12:30', '2021-12-19 00:12:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1873, '夜间道路环境对安全行车的主要影响是什么？', '', '1', '1', NULL, '能见度低、不利于观察道路交通情况', '路面复杂多变', '驾驶人体力下降', '驾驶人易产生冲动、幻觉', 'A', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄，不利于观察道路交通情况；同时驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', 3, 1, '2021-12-19 00:12:30', '2021-12-19 00:12:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1874, '夜间驾驶人对物体的观察明显比白天差，视距会有什么变化？', '', '1', '1', NULL, '变长', '变短', '不变', '无规律', 'B', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', 3, 1, '2021-12-19 00:12:30', '2021-12-19 00:12:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1875, '冰雪道路对安全行车的主要影响是什么？', '', '1', '1', NULL, '电器设备易受潮短路', '能见度降低，视野模糊', '行驶阻力增大', '制动性能差，方向易跑偏', 'D', '车辆在冰雪道路上行驶，车辆制度性能差，车辆很容易侧滑跑偏。因路面湿滑，汽车轮胎摩擦力减少、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 3, 1, '2021-12-19 00:12:30', '2021-12-19 00:12:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1876, '冰雪路行车时应注意什么？', '', '1', '1', NULL, '制动距离延长', '抗滑能力变大', '路面附着力增大', '制动性能没有变化', 'A', '车辆在冰雪道路上行驶，车辆制度性能差，车辆很容易侧滑跑偏。因路面湿滑，汽车轮胎摩擦力减少、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 3, 1, '2021-12-19 00:12:31', '2021-12-19 00:12:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1877, '泥泞道路对安全行车的主要影响是什么？', '', '1', '1', NULL, '行驶阻力变小', '车轮极易滑转和侧滑', '能见度低，视野模糊', '路面附着力增大', 'B', '泥泞路路面特别松软、粘稠，行驶阻力大，附着力减小，车轮易滑转和侧滑，容易陷车。', 3, 1, '2021-12-19 00:12:31', '2021-12-19 00:12:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1878, '水淹路面影响行车安全，不易通行的原因是什么？', '', '1', '1', NULL, '无法观察到暗坑和凸起的路面', '路面附着力增大', '能见度低，视野模糊', '日光反射阻挡视线', 'A', '水淹路面无法观察到暗坑和凸起的路面，因此，不易通行，以免影响行车安全。', 3, 1, '2021-12-19 00:12:31', '2021-12-19 00:12:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1879, '山区道路对安全行车的主要影响是什么？', '', '1', '1', NULL, '道路标志少', '交通情况单一', '坡长弯急，视距不足', '车流密度大', 'C', '山区道路大多依山傍崖、陡坡路窄、急弯、隧道等危险路段多，车流海拔高的山区气候多变，坡长急弯还会造成视距不足，山口的横风也会引起车辆行驶位置偏移等。', 3, 1, '2021-12-19 00:12:31', '2021-12-19 00:12:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1880, '行车中突遇对方车辆强行超车，占据自己车道，正确的做法是什么？', '', '1', '1', NULL, '加速行驶', '尽可能减速避让，直至停车', '保持原车速行驶', '挡住其去路', 'B', '驾驶机动车遇后方强行超车后，占据自己车道时，要减速或靠右停车避让，千万不要开赌气车。', 3, 1, '2021-12-19 00:12:32', '2021-12-19 00:12:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1881, '夜间行车，驾驶人视距变短，影响观察，同时注意力高度集中，易产生疲劳。', '', '2', '1', NULL, '', '', '', '', 'A', '高度集中注意力，比较费神，时间久了就会疲倦。', 3, 1, '2021-12-19 00:12:32', '2021-12-19 00:12:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1882, '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人目眩而产生错觉。', '', '2', '1', NULL, '', '', '', '', 'A', '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人目眩而产生错觉，为预防积雪反射引起炫目，驾驶人可以佩戴墨镜。', 3, 1, '2021-12-19 00:12:32', '2021-12-19 00:12:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1883, '在冰雪道路上行车时，车辆的稳定性降低，加速过急时车轮极易空转或溜滑。', '', '2', '1', NULL, '', '', '', '', 'A', '【冰雪道路对安全行车的影响】 车辆在冰雪道路上行驶，车辆制度性能差，车辆很容易侧滑跑偏。因路面湿滑，汽车轮胎摩擦力减少、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 3, 1, '2021-12-19 00:12:32', '2021-12-19 00:12:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1884, '在泥泞路上制动时，车轮易发生侧滑或甩尾，导致交通事故。', '', '2', '1', NULL, '', '', '', '', 'A', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞路段时，要停车查看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 3, 1, '2021-12-19 00:12:33', '2021-12-19 00:12:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1885, '漫水道路行车时，应挂高速档，快速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '漫水道路行车时，应挂低速挡保持车辆足够动力，低速缓慢驶入水中，稳住方向、匀速一气通过，中途不要换挡、停车和急转向。', 3, 1, '2021-12-19 00:12:33', '2021-12-19 00:12:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1886, '行车中突遇对向车辆强行超车，占据自己车道时，可不予避让，迫使对方让路。', '', '2', '1', NULL, '', '', '', '', 'B', '行车中遇后方车辆强行超车后，不给留安全距离便向右变道时，要减速或靠右停车避让，千万不要开赌气车。', 3, 1, '2021-12-19 00:12:33', '2021-12-19 00:12:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1887, '行车中遇有前方发生交通事故，需要帮助时，应怎样做？', '', '1', '1', NULL, '尽量绕道躲避', '立即报警，停车观望', '协助保护现场，并立即报警', '加速通过，不予理睬', 'C', '助人为乐是中华民族的传统美德，也是文明驾驶的重要体现，驾车过程中如发现他人遇到困难，应积极主动伸出援助之手，为他人排忧解难。遇到交通事故需要帮助时，应减速停车，协助保护事故现场，并立即报警。如果伤者需要抢救，应及时拨打急救电话或送伤者去医院。', 3, 1, '2021-12-19 00:12:33', '2021-12-19 00:12:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1888, '行车中遇交通事故受伤者需要抢救时，应怎样做？', '', '1', '1', NULL, '及时将伤者送医院抢救或拨打急救电话', '尽量避开，少惹麻烦', '绕过现场行驶', '借故避开现场', 'A', '行车中遇到交通事故需要帮助时，应减速停车，协助保护事故现场，并立即报警。如果伤者需要抢救，应及时拨打急救电话或送伤者去医院。', 3, 1, '2021-12-19 00:12:34', '2021-12-19 00:12:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1889, '行车中遇到对向来车占道行驶，应怎样做？', '', '1', '1', NULL, '紧靠道路中心行驶', '主动给对方让行', '用大灯警示对方', '逼对方靠右行驶', 'B', '记住文明驾驶，礼貌让行。不开赌气车，安全第一。', 3, 1, '2021-12-19 00:12:34', '2021-12-19 00:12:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1890, '行车中发现前方道路拥堵时，应怎样做？', '', '1', '1', NULL, '寻找机会超越前车', '从车辆空间穿插通过', '减速停车，依次排队等候', '鸣喇叭催促', 'C', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 3, 1, '2021-12-19 00:12:34', '2021-12-19 00:12:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1891, '会车中遇到对方来车行进有困难需借道时，应怎样做？', '', '1', '1', NULL, '不侵占对方道路，正常行驶', '示意对方停车让行', '靠右侧加速行驶', '尽量礼让对方先行', 'D', '【礼让三先】会车中遇到对方来车行进有困难需借道时，尽量礼让让对方先行。', 3, 1, '2021-12-19 00:12:34', '2021-12-19 00:12:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1892, '行车中遇到后方车辆要求超车时，应怎样做？', '', '1', '1', NULL, '及时减速、观察后靠右行驶让行', '保持原有车速行驶', '靠右侧加速行驶', '不让行', 'A', '行车中遇后方车辆发出超车信号时，若具备让车条件，及时开启右转向灯，减速靠右让行，必要时辅以手势示意让超，不得故意不让或让路不让速。', 3, 1, '2021-12-19 00:12:35', '2021-12-19 00:12:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1893, '驾驶人在超车时，前方车辆不减速、不让道，应怎样做？', '', '1', '1', NULL, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', 'C', '超车之前一定要观察好超车条件，能够保证万无一失再开始超越。遇前方车辆不减速、不让道时，应主动减速，保持安全距离，停止继续超车。因为紧随其后、连续鸣喇叭和加速超越，都无法预防突然出现的危险。', 3, 1, '2021-12-19 00:12:35', '2021-12-19 00:12:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1894, '驾驶人在行车中经过积水路面时，应怎样做？', '', '1', '1', NULL, '减速慢行', '保持正常车速通过', '空挡滑行通过', '加速通过', 'A', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 3, 1, '2021-12-19 00:12:35', '2021-12-19 00:12:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1895, '发现前方道路堵塞，正确的做法是什么？', '', '1', '1', NULL, '继续穿插绕行', '选择空挡逐车超越', '鸣喇叭示意前方车辆快速行驶', '按顺序停车等候', 'D', '如果遇到前方拥堵，应该按顺序停车等候。随便加塞或者鸣喇叭都不是文明行车行为。', 3, 1, '2021-12-19 00:12:36', '2021-12-19 00:12:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1896, '车辆在拥挤路段低速行驶时，遇其他车辆强行插队，应怎样做？', '', '1', '1', NULL, '鸣喇叭警告，不得进入', '加速行驶，紧跟前车，不让其进入', '主动礼让，确保行车安全', '挤靠“加塞”车辆，逼其离开', 'C', '车辆在拥挤路段低速行驶时，遇其他车辆强行插队，应主动礼让，确保行车安全，不得加速行驶不让其进入或挤靠“加塞”车辆。', 3, 1, '2021-12-19 00:12:36', '2021-12-19 00:12:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1897, '当驾驶车辆行经两侧有行人且有积水的路面时，应怎样做？', '', '1', '1', NULL, '加速通过', '正常行驶', '减速慢行', '连续鸣喇叭', 'C', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 3, 1, '2021-12-19 00:12:36', '2021-12-19 00:12:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1898, '当驾驶车辆行经两侧有非机动车行驶且有积水的路面时，应怎样做？', '', '1', '1', NULL, '减速慢行', '正常行驶', '加速通过', '连续鸣喇叭', 'A', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 3, 1, '2021-12-19 00:12:36', '2021-12-19 00:12:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1899, '一个合格的驾驶人，不仅表现在技术的娴熟上，更重要的是应该具有良好的驾驶行为习惯和道德修养。', '', '2', '1', NULL, '', '', '', '', 'A', '娴熟的技术固然重要，但没有良好的驾驶习惯和道德修养，同样会破坏正常的交通秩序。因此合格驾驶员应该具备良好的驾驶行为习惯和道德修养。', 3, 1, '2021-12-19 00:12:37', '2021-12-19 00:12:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1900, '驾驶车辆在道路上行驶时，应当按照规定的速度安全行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '安全驾驶，请驾驶员按照规定的速度行驶。', 3, 1, '2021-12-19 00:12:37', '2021-12-19 00:12:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1901, '驾驶人一边驾车，一边打手持电话是违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条 驾驶机动车不得有下列行为：(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为； 【拓展知识】 驾驶人一边驾驶一边做或想其他使注意力分散的事情，违背了安全驾驶的“集中注意力”这个黄金原则，对安全行车非常不利。', 3, 1, '2021-12-19 00:12:37', '2021-12-19 00:12:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1902, '在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', '', '2', '1', NULL, '', '', '', '', 'B', '实线不能跨越，因此不能越实线超车，本题错误。', 3, 1, '2021-12-19 00:12:37', '2021-12-19 00:12:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1903, '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '', '2', '1', NULL, '', '', '', '', 'B', '跟车行驶时，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，控制好与前车的安全距离，是避免发生追尾、剐碰等事故的前提。', 3, 1, '2021-12-19 00:12:38', '2021-12-19 00:12:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1904, '谨慎驾驶的三个原则是集中注意力、仔细观察和提前预防。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车驾驶人应牢记谨慎驾驶的三条黄金原则：1、集中注意力；2、仔细观察；3、提前预防。', 3, 1, '2021-12-19 00:12:38', '2021-12-19 00:12:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1905, '遇到路口情况复杂时，应做到“宁停三分，不抢一秒”。', '', '2', '1', NULL, '', '', '', '', 'A', '文明驾车，安全行驶。遇到路口情况复杂时，应做到“宁停三分，不抢一秒”。', 3, 1, '2021-12-19 00:12:38', '2021-12-19 00:12:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1906, '行车中要文明驾驶，礼让行车，做到不开英雄车、冒险车、赌气车和带病车。', '', '2', '1', NULL, '', '', '', '', 'A', '文明驾驶要做到“礼让三先”：先慢、先让、先停。驾驶人要加强个人修养，注重行车中的礼让。自己首先要做到不开“冒险车”、“英雄车”、“带病车”；对他人的不良驾驶行为做到宽容、大度、礼让，正确处理好有理与无理的关系，不开“斗气车”，保持冷静的心态，宁可“有理让无理，不可无理对无理”。’', 3, 1, '2021-12-19 00:12:38', '2021-12-19 00:12:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1907, '驾驶人在观察后方无来车的情况下，未开转向灯就变更车道也是合理的。', '', '2', '1', NULL, '', '', '', '', 'B', '这是错误的行车方法，为保证安全，驾驶人在变更车道时需要提前打开转向灯示意约3秒以上，确认安全后完成变更车道，随后关闭转向灯。', 3, 1, '2021-12-19 00:12:39', '2021-12-19 00:12:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1908, '女驾驶人穿高跟鞋驾驶车辆，不利于安全行车。', '', '2', '1', NULL, '', '', '', '', 'A', '穿高跟鞋踩制动踏板，会受力不均，同时也有可能会造成鞋跟卡在油门或者制动踏板的中间位置无法及时刹车，造成交通事故。', 3, 1, '2021-12-19 00:12:39', '2021-12-19 00:12:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1909, '驾驶车辆时，长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，是一种驾驶陋习。', '', '2', '1', NULL, '', '', '', '', 'A', '长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆，是一种妨碍安全驾驶的行为，如果出现紧急情况，驾驶员很难做出良好及时的应对，属于驾驶陋习。', 3, 1, '2021-12-19 00:12:39', '2021-12-19 00:12:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1910, '驾驶人一边驾车，一边吸烟对安全行车无影响。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶人一边驾车，一边吸烟属于分心驾驶，对安全行车非常不利。题目描述错误。 分心驾驶，就是驾驶人一边驾驶一边做或想其他使注意力分散的事情，这违背了安全驾驶的“集中注意力”这个黄金原则，对安全行车非常不利。 分心驾驶的危害在于：1、驾驶人注意力分散、视线离开路面，无法提前观察或自然忽略道路交通中的信息；2、引起驾驶姿势或操作动作的变化，不利于安全平稳操纵车辆。', 3, 1, '2021-12-19 00:12:39', '2021-12-19 00:12:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1911, '在狭窄的路段会车时，应做到礼让三先：先慢、先让、先停。', '', '2', '1', NULL, '', '', '', '', 'A', '在狭窄的路段会车时，应做到礼让三先，即先慢、先让、先停，以保证行车安全。', 3, 1, '2021-12-19 00:12:40', '2021-12-19 00:12:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1912, '行车中需要借道绕过前方障碍物，但对向来车已接近障碍物时，应怎样做？', '', '1', '1', NULL, '降低速度或停车，让对向来车优先通行', '加速提前抢过', '鸣喇叭示意对向车辆让道', '迅速占用车道，迫使对向来车停车让道', 'A', '驾驶机动车在有障碍的路段遇对面来车时，有障碍一方让无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。因此，当需要借道绕过障碍物，但对向来车已接近障碍物时，应降低速度或停车，让对向来车优先通行。', 3, 1, '2021-12-19 00:12:40', '2021-12-19 00:12:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1913, '驾驶车辆在交叉路口前变更车道时，应怎样驶入要变更的车道？', '', '1', '1', NULL, '在路口前实线区内根据需要', '进入路口实线区内', '在虚线区按导向箭头指示', '在路口停止线前', 'C', '驾驶机动车在交叉路口前需要变更车道时，需按导向箭头的指示在虚线区变更车道，进入实线区后不能变更车道。', 3, 1, '2021-12-19 00:12:40', '2021-12-19 00:12:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1914, '车辆驶近人行横道时，应怎样做？', '', '1', '1', NULL, '加速通过', '立即停车', '鸣喇叭示意行人让道', '先减速注意观察行人、非机动车动态，确认安全后再通过', 'D', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人、非机动车通行，随时准备停车礼让行人。', 3, 1, '2021-12-19 00:12:40', '2021-12-19 00:12:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1915, '车辆临时靠边停车后准备起步时，应先怎样做？', '', '1', '1', NULL, '加油起步', '鸣喇叭', '观察周围交通情况', '提高发动机转速', 'C', '车辆起步时，观察内、外后视镜，侧头观察左后方交通情况，开启左转向灯，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', 3, 1, '2021-12-19 00:12:41', '2021-12-19 00:12:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1916, '行驶车道绿灯亮时，但车辆前方人行横道仍有行人行走，应怎样做？', '', '1', '1', NULL, '直接起步通过', '起步后从行人后方绕过', '起步后从行人前方绕过', '等行人通过后再起步', 'D', '行驶车道绿灯亮时，但车辆前方人行横道仍有行人行走，应等行人通过后再起步。即使行人出现不遵守交通规则的行为，驾驶人也要做到文明礼让，优先保证行人的安全。', 3, 1, '2021-12-19 00:12:41', '2021-12-19 00:12:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1917, '在一般道路倒车时，若发现有过往车辆通过，应怎样做？', '', '1', '1', NULL, '继续倒车', '鸣喇叭示意', '主动停车避让', '加速倒车', 'C', '倒车时，若发现有过往车辆通过，应主动停车避让。', 3, 1, '2021-12-19 00:12:41', '2021-12-19 00:12:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1918, '会车前选择的交会位置不理想时，应怎样做？', '', '1', '1', NULL, '加速选择理想位置', '减速、低速会车或停车让行', '向左占道，让对方减速让行', '打开前照灯，示意对方停车让行', 'B', '发现交会位置不理想时，应减速或停车让行，以寻找下一个理想会车位置。', 3, 1, '2021-12-19 00:12:41', '2021-12-19 00:12:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1919, '进入左侧道路超车，无法保证与正常行驶前车的横向安全间距时，应怎样做？', '', '1', '1', NULL, '加速超越', '并行一段距离后再超越', '放弃超车', '谨慎超越', 'C', '没有超车条件，放弃超车是最安全的行为。', 3, 1, '2021-12-19 00:12:42', '2021-12-19 00:12:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1920, '驾驶的车辆正在被其他车辆超越时，应怎样做？', '', '1', '1', NULL, '靠道路中心行驶', '加速让路', '继续加速行驶', '减速，靠右侧行驶', 'D', '超车从左边，如果我们正在被其他车辆超车，应该靠右边减速让行。安全第一。', 3, 1, '2021-12-19 00:12:42', '2021-12-19 00:12:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1921, '遇后车发出超车信号后，只要具备让超条件应怎样做？', '', '1', '1', NULL, '迅速减速或紧急制动', '让出适当空间加速行驶', '主动减速并靠右侧行驶', '靠道路右侧加速行驶', 'C', '文明礼让，和谐驾驶。如果遇到后车发出超车信号，我们应该主动减速并靠右侧行驶。', 3, 1, '2021-12-19 00:12:42', '2021-12-19 00:12:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1922, '驾驶车辆行至道路急转弯处，应怎样做？', '', '1', '1', NULL, '借对向车道行驶', '急剧制动低速通过', '靠弯道外侧行驶', '充分减速并靠右侧行驶', 'D', '驾驶车辆行至急弯路时，做到“减速、鸣号、靠右行”，在距弯道约150米处，交替使用远、近光灯示意；转弯时要关闭远光灯，开启近光灯，降低车速并且靠右侧行驶，同时随时做好停车准备。', 3, 1, '2021-12-19 00:12:42', '2021-12-19 00:12:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1923, '山区道路车辆进入弯道前，在对面没有来车的情况下，应怎样做？', '', '1', '1', NULL, '应“减速、鸣喇叭、靠右行”', '可靠弯道外侧行驶', '可短时间借用对方的车道', '可加速沿弯道切线方向通过', 'A', '山区弯道较多，要注意观察，提前发现急弯路警告标志，做到“减速、鸣号、靠右行”。通过连续急弯路时，要注意靠右侧减速慢行，不要占对向车道', 3, 1, '2021-12-19 00:12:43', '2021-12-19 00:12:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1924, '在堵车的交叉路口绿灯亮时，车辆应怎样做？', '', '1', '1', NULL, '可直接驶入交叉路口', '不能驶入交叉路口', '在保证安全的情况下驶入交叉路口', '可借对向车道通过路口', 'B', '驾驶机动车遇到路口交通阻塞时，即便是绿灯亮，也要停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。', 3, 1, '2021-12-19 00:12:43', '2021-12-19 00:12:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1925, '驾驶车辆通过无人看守的铁路道口时，应怎样做？', '', '1', '1', NULL, '加速通过', '减速通过', '匀速通过', '一停、二看、三通过', 'D', '通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 3, 1, '2021-12-19 00:12:43', '2021-12-19 00:12:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1926, '驾驶车辆驶入铁路道口前减速降挡，进入道口后应怎样做？', '', '1', '1', NULL, '不能变换挡位', '可以变换挡位', '可换为高挡', '停车观察', 'A', '驾驶车辆驶入铁路道口，在道口外提前减速、减挡，不得在路口内变换档位。', 3, 1, '2021-12-19 00:12:43', '2021-12-19 00:12:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1927, '行车中超越右侧停放的车辆时，为预防其突然起步或开启车门，应怎样做？', '', '1', '1', NULL, '预留出横向安全距离，减速行驶', '保持正常速度行驶', '长鸣喇叭', '加速通过', 'A', '行车中超越右侧停放的车辆时，为预防其突然起步或开启开车，最有效的方式是预留出横向安全距离，减速行驶。因为长鸣喇叭、加速通过和保持正常速度行驶，都无法预防突然出现的危险。', 3, 1, '2021-12-19 00:12:44', '2021-12-19 00:12:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1928, '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？', '', '1', '1', NULL, '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '立即变道绕过行人', 'A', '机动车进出道路或者在没有交通信号、人行横道的路段行驶，应当减速或停车避让横过道路的行人。', 3, 1, '2021-12-19 00:12:44', '2021-12-19 00:12:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1929, '行车中遇有非机动车准备绕过停放的车辆时，应怎样做？', '', '1', '1', NULL, '鸣喇叭示意其让道', '让其先行', '加速绕过', '紧随其后鸣喇叭', 'B', '行车中遇有非机动车准备绕过停放的车辆或抢道行驶时，驾驶人应主动减速让行。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', 3, 1, '2021-12-19 00:12:44', '2021-12-19 00:12:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1930, '行车中，遇非机动车抢行时，应怎样做？', '', '1', '1', NULL, '鸣喇叭警告', '加速通过', '减速让行', '临近时突然加速', 'C', '行车中遇有非机动车抢行时，驾驶人应主动减速让行。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', 3, 1, '2021-12-19 00:12:44', '2021-12-19 00:12:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1931, '行车中遇抢救伤员的救护车从本车道逆向驶来时，应怎样做？', '', '1', '1', NULL, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', 'A', '行车中遇到抢救伤员的救护车逆向驶来时，，应当靠边减速或停车让行，确保救护车通行无阻。', 3, 1, '2021-12-19 00:12:44', '2021-12-19 00:12:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1932, '行车中遇儿童时，应怎样做？', '', '1', '1', NULL, '长鸣喇叭催促', '减速慢行，必要时停车避让', '迅速从一侧通过', '加速绕行', 'B', '行车中，要注意礼让儿童，发现儿童在路边玩耍、嬉戏打闹时，应当减速慢行，随时注意观察儿童动态，发现异常要及时停车避让。', 3, 1, '2021-12-19 00:12:45', '2021-12-19 00:12:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1933, '行车中遇列队横过道路的学生时，应怎样做？', '', '1', '1', NULL, '提前加速抢行', '停车让行', '降低车速、缓慢通过', '连续鸣喇叭催促', 'B', '驾驶机动车遇列队横过道路时，应及时停车礼让，待列队学生全部通过后再通过。不可加速抢行、从他们中间穿行或从两侧绕行通过，避免发生事故。', 3, 1, '2021-12-19 00:12:45', '2021-12-19 00:12:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1934, '车辆通过凹凸路面时，应怎样做？', '', '1', '1', NULL, '低速缓慢平稳通过', '依靠惯性加速冲过', '挂空挡滑行驶过', '保持原速通过', 'A', '遇到凹凸不平的路面，要减速缓慢通过，安全第一。', 3, 1, '2021-12-19 00:12:45', '2021-12-19 00:12:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1935, '行车中超越同向行驶的自行车时，应怎样做？', '', '1', '1', NULL, '让自行车先行', '注意观察动态，减速慢行，留有足够的安全距离', '连续鸣喇叭提醒其让路', '持续鸣喇叭并加速超越', 'B', '驾驶机动车遇行人和非机动车肯定要减速慢行，否则会容易撞上，但“慢行”相对机动车的快行是慢的，而相对自行车来的速度来说还是比较快的。并且超车必须要确保安全距离（左右距离），防止与自行车相撞。', 3, 1, '2021-12-19 00:12:45', '2021-12-19 00:12:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1936, '夜间驾驶车辆遇自行车对向驶来时，应怎样做？', '', '1', '1', NULL, '连续变换远、近光灯', '不断鸣喇叭', '使用远光灯', '使用近光灯，减速或停车避让', 'D', '安全行车，文明礼让。夜间驾驶机动车遇非机动车对向驶来时，应使用近光灯，减速慢行，必要的时候停车避让。交替使用远近光灯强光会使对方看不清道路，导致危险。', 3, 1, '2021-12-19 00:12:46', '2021-12-19 00:12:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1937, '车辆在主干道上行驶，驶近主支干道交汇处时，为防止与从支路突然驶入的车辆相撞，应怎样做？', '', '1', '1', NULL, '提前减速、观察，谨慎驾驶', '保持正常速度行驶', '鸣喇叭，迅速通过', '提前加速通过', 'A', '车辆在主干道上行驶，驶近主支干道交汇处时，应提前减速、观察并谨慎驾驶，以防与从支路突然驶入的车辆发生碰撞事故。', 3, 1, '2021-12-19 00:12:46', '2021-12-19 00:12:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1938, '车辆在交叉路口有优先通行权的，遇有车辆抢行时，应怎样做？', '', '1', '1', NULL, '抢行通过', '提前加速通过', '按优先权规定正常行驶不予避让', '减速避让，必要时停车让行', 'D', '通过交叉路口时要注意减速避让正在通行的车辆和行人，随时做好停车让行的准备。即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', 3, 1, '2021-12-19 00:12:46', '2021-12-19 00:12:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1939, '机动车在道路边临时停车时，应怎样做？', '', '1', '1', NULL, '可逆向停放', '可并列停放', '不得逆向或并列停放', '只要出去方便，可随意停放', 'C', '在道路上临时停车时，不得妨碍其他机动车和行人通行，应按指定的位置停放，不得在道路两侧并列停放或逆向停放。', 3, 1, '2021-12-19 00:12:47', '2021-12-19 00:12:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1940, '车辆在雨天临时停车时，应开启什么灯？', '', '1', '1', NULL, '前后雾灯', '危险报警闪光灯', '前大灯', '倒车灯', 'B', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯和危险报警闪光灯。', 3, 1, '2021-12-19 00:12:47', '2021-12-19 00:12:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1941, '车辆在雪天临时停车时，应开启什么灯？', '', '1', '1', NULL, '前后雾灯', '倒车灯', '前大灯', '危险报警闪光灯', 'D', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯和危险报警闪光灯。', 3, 1, '2021-12-19 00:12:47', '2021-12-19 00:12:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1942, '驾驶人行车中看到注意儿童标志的时候，应怎样做？', '', '1', '1', NULL, '加速行驶', '绕道行驶', '谨慎选择行车速度', '保持正常车速行驶', 'C', '行车中看到注意儿童标志时，应谨慎行车，减速慢行，注意儿童。', 3, 1, '2021-12-19 00:12:47', '2021-12-19 00:12:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1943, '车辆驶近停在车站的公交车辆时，为预防公交车突然起步或行人从车前穿出，应怎样做？', '', '1', '1', NULL, '减速，保持足够间距，随时准备停车', '保持正常车速行驶', '随时准备紧急制动', '鸣喇叭提醒，加速通过', 'A', '驾驶机动车驶近停有公交车的车站时，要减速慢行，与公交车保持较大的安全间距，预防公交车突然起步或车站上下车的乘客从车前、车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。', 3, 1, '2021-12-19 00:12:48', '2021-12-19 00:12:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1944, '雨天行车，遇撑雨伞和穿雨衣的行人在公路上行走时，应怎样做？', '', '1', '1', NULL, '以正常速度行驶', '持续鸣喇叭示意其让道', '加速绕行', '提前鸣喇叭，并适当降低车速', 'D', '下雨天遇撑雨伞和穿雨衣的行人在道路上行走时，要提前鸣喇叭进行提示，并适当降低车速，注意观察其动态，保持一定的距离通过，同时要随时准备应付突发状况。', 3, 1, '2021-12-19 00:12:48', '2021-12-19 00:12:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1945, '车辆行至交叉路口，遇有转弯的车辆抢行，应怎样做？', '', '1', '1', NULL, '停车避让', '保持正常车速行驶', '提高车速抢先通过', '鸣喇叭抢先通过', 'A', '通过交叉路口时要注意避让正在通行的车辆和行人，随时做好停车避让的准备。即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', 3, 1, '2021-12-19 00:12:48', '2021-12-19 00:12:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1946, '驾驶车辆变更车道时，应提前开启转向灯，注意观察，保持安全距离，驶入要变更的车道。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车在变更车道时，应当提前开启转向灯，夜间还需变换使用远、近光灯或者鸣喇叭；在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶，安全变更车道后关闭转向灯。', 3, 1, '2021-12-19 00:12:49', '2021-12-19 00:12:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1947, '驾驶车辆向右变更车道时，应提前开启右转向灯，注意观察，在确保安全的情况下，驶入要变更的车道。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车向右变更车道时，应提前开启右转向灯，通过内外后视镜观察后方车辆和右侧车道的交通状况，在确保安全的情况下，驶入要变更的车道。', 3, 1, '2021-12-19 00:12:49', '2021-12-19 00:12:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1948, '变更车道时只需开启转向灯，便可迅速转向驶入相应的行车道。', '', '2', '1', NULL, '', '', '', '', 'B', '题目错在迅速转向。为保证安全，驾驶人在变更车道前打开转向灯示意约3秒以上，先通过内外后视镜观察后方车辆和将驶入车道的交通状况；车辆的侧方存在盲区，因此在确认自己的前方安全后需再次侧头观察驶入车道一侧有无他车，判断有无变更车道的条件，正确的选择行驶车道和变更时机，安全变道行驶。 因此，变更车道时提前开启转向灯提醒后车，然后观察有无变更车道的条件，在确保安全的情况下，保持安全距离变更车道，不可迅速变更车道。', 3, 1, '2021-12-19 00:12:49', '2021-12-19 00:12:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1949, '驾驶车辆汇入车流时，应提前开启转向灯，保持直线行驶，通过后视镜观察左右情况，确认安全后汇入合流。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车汇入车流时，要通过后视镜随时注意观察两侧道路情况，提前开启转向灯，保持直线行驶，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', 3, 1, '2021-12-19 00:12:50', '2021-12-19 00:12:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1950, '变更车道时，应开启转向灯，迅速驶入侧方车道。', '', '2', '1', NULL, '', '', '', '', 'B', '本题错在“迅速”两字，且没有观察。为保证安全，驾驶人在变更车道前打开转向灯示意约3秒以上，先通过内外后视镜观察后方车辆和将驶入车道的交通状况；车辆的侧方存在盲区，因此在确认自己的前方安全后需再次侧头观察驶入车道一侧有无他车，判断有无变更车道的条件，正确的选择行驶车道和变更时机，安全变道行驶。 因此，变更车道时提前开启转向灯提醒后车，然后观察有无变更车道的条件，在确保安全的情况下，保持安全距离变更车道，不可迅速变更车道。', 3, 1, '2021-12-19 00:12:50', '2021-12-19 00:12:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1951, '行车中从其他道路汇入车流前，应注意观察侧后方车辆的动态。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车从其他道路汇入车流不得影响车道内其他车辆的正常行驶，因此，汇入车流前，应注意观察侧后方车辆的动态，要分析道路交通流的状态，正确地选择汇入时机，安全汇入。', 3, 1, '2021-12-19 00:12:50', '2021-12-19 00:12:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1952, '驾驶车辆通过人行横道线时，应注意礼让行人。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人。', 3, 1, '2021-12-19 00:12:50', '2021-12-19 00:12:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1953, '车辆起步前，驾驶人应对车辆周围交通情况进行观察，确认安全时再开始起步。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆起步前，驾驶人应绕车一周进行检查，确认安全，观察车辆外观有无损毁，周围环境是否安全，车底有无异常情况。打开车门前应观察后方交通情况。', 3, 1, '2021-12-19 00:12:51', '2021-12-19 00:12:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1954, '车辆在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车在路边起步后，要随时注意两侧道路情况，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。', 3, 1, '2021-12-19 00:12:51', '2021-12-19 00:12:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1955, '倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，随时做好停车准备。', '', '2', '1', NULL, '', '', '', '', 'A', '安全倒车，倒车前应仔细观察倒车路线，确认具备安全倒车条件后方可倒车。倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，并随时注意车头两侧的安全空间位置，随时做好停车准备，以免因转向角度过大而发生剐蹭事故。因此，题干表述正确。', 3, 1, '2021-12-19 00:12:51', '2021-12-19 00:12:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1956, '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '', '2', '1', NULL, '', '', '', '', 'B', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 3, 1, '2021-12-19 00:12:51', '2021-12-19 00:12:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1957, '通过隧道时，不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》 第四十三条 同车道行驶的机动车，有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 3, 1, '2021-12-19 00:12:52', '2021-12-19 00:12:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1958, '通过铁路道口时，不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》 第四十三条 同车道行驶的机动车，有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 3, 1, '2021-12-19 00:12:52', '2021-12-19 00:12:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1959, '通过急转弯路段时，在车辆较少的情况下可以超车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》 第四十三条 同车道行驶的机动车，……有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。', 3, 1, '2021-12-19 00:12:52', '2021-12-19 00:12:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1960, '通过窄路、窄桥时，不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条，同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：（四）行经交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者市区交通流量大的路段等没有超车条件的。', 3, 1, '2021-12-19 00:12:52', '2021-12-19 00:12:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1961, '车辆转弯时应沿道路右侧行驶，不要侵占对方的车道，做到“左转转大弯，右转转小弯”。', '', '2', '1', NULL, '', '', '', '', 'A', '由于我国规定机动车要靠右行驶，无论在十字路口，弯路都是各行其路，所以左转弧度大，右转弧度就小。右转转小弯左转转大弯，就能最大程度避免与对方来车发生碰撞。', 3, 1, '2021-12-19 00:12:53', '2021-12-19 00:12:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1962, '驾驶车辆进入交叉路口前，应降低行驶速度，注意观察，确认安全。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车进入交叉路口前，应减速慢行，注意观察左、右方交通情况，确认安全，切勿抢行通过。', 3, 1, '2021-12-19 00:12:53', '2021-12-19 00:12:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1963, '车辆通过铁道路口时，应用低速挡安全通过，中途不得换挡，以避免发动机熄火。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆通过铁路道口时，在道口外提前减速、减挡，低速通行，不得在路口内变换档位。', 3, 1, '2021-12-19 00:12:53', '2021-12-19 00:12:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1964, '行车中，发现行人突然横过道路时，应迅速减速避让。', '', '2', '1', NULL, '', '', '', '', 'A', '道路上的行人各有差异，但行人的主要特点是行走随意性大，方向多变，很容易引起险情。由于行人缺少相应的保护，很容易受到伤害，道路通行中应当坚持行人优先的原则，即使行人出现不遵守交通规则的行为，驾驶人也要做到减速礼让，优先保证行人的安全。', 3, 1, '2021-12-19 00:12:53', '2021-12-19 00:12:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1965, '当行人出现交通安全违法行为时，车辆可以不给行人让行。', '', '2', '1', NULL, '', '', '', '', 'B', '由于行人缺少相应的保护，很容易受到伤害，道路通行中应当坚持行人优先的原则，即使行人出现不遵守交通规则的行为，驾驶人也要做到文明礼让，优先保证行人的安全。', 3, 1, '2021-12-19 00:12:54', '2021-12-19 00:12:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1966, '车辆在交叉路口绿灯亮后，遇非机动车抢道行驶时，可以不让行。', '', '2', '1', NULL, '', '', '', '', 'B', '路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险，应注意避让行人和非机动车，随时做好停车的准备，即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', 3, 1, '2021-12-19 00:12:54', '2021-12-19 00:12:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1967, '掉头过程中，应严格控制车速，仔细观察道路前后方情况，确认安全后方可前进或倒车。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车行驶过程中，需要掉头时，观察前、后方交通情况，确认安全后减速或停车，开启左转向灯，正确选择掉头地点和时机进行掉头。在没有掉头成功的情况下可以倒车调整，但不要妨碍其他车辆和行人的正常通行。', 3, 1, '2021-12-19 00:12:54', '2021-12-19 00:12:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1968, '行车中遇残疾人影响通行时，应主动减速礼让。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车遇到行动不便的行人横过道路时，要主动减速或停车礼让。', 3, 1, '2021-12-19 00:12:55', '2021-12-19 00:12:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1969, '设有安全带装置的车辆，应要求车内乘员系安全带。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十一条：机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。 【拓展知识】 座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', 3, 1, '2021-12-19 00:12:55', '2021-12-19 00:12:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1970, '行车中前方遇自行车影响通行时，可鸣喇叭提示，加速绕行。', '', '2', '1', NULL, '', '', '', '', 'B', '本题错在加速绕行，鸣喇叭提示是对的。应当减速与其保持安全距离，道路宽度允许通过时谨慎通过。因此本题错了。', 3, 1, '2021-12-19 00:12:55', '2021-12-19 00:12:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1971, '机动车在环形路口内行驶，遇有其他车辆强行驶入时，只要有优先权就可以不避让。', '', '2', '1', NULL, '', '', '', '', 'B', '通过环岛时一定要提前用信号告知自己的行驶方向，减速慢行，保持安全间距，随时预防其他车辆的异常行为。遇强行驶入的机动车，即使有优先通行权，为确保行车安全，也要注意避让。', 3, 1, '2021-12-19 00:12:56', '2021-12-19 00:12:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1972, '车辆行至交叉路口时，左转弯车辆在任何时段都可以进入左转弯待转区。', '', '2', '1', NULL, '', '', '', '', 'B', '有左转弯待转区线的路口，应在直行绿灯亮时进入待转区等待放行信号。', 3, 1, '2021-12-19 00:12:56', '2021-12-19 00:12:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1973, '车辆行至急转弯处时，应减速并靠右侧行驶，防止与越过弯道中心线的对方车辆相撞。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车行至急转弯处时，要注意靠右侧减速慢行，防止与越过弯道中心线的对方车辆相撞。', 3, 1, '2021-12-19 00:12:56', '2021-12-19 00:12:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1974, '车辆长时间停放时，应选择停车场停车。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆停放时间较长时，选择停车场或准许长时间停放车辆的地点，在规定的位置内依次停放。车辆长期停放时，尽量停入车库。不准在车行道、人行道和设有禁止长时停放标志的地点停放。', 3, 1, '2021-12-19 00:12:56', '2021-12-19 00:12:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1975, '车辆通过学校和小区应注意观察标志标线，低速行驶，不要鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车通过学校和居民小区时，一定要及时减速，注意观察道路两侧及周围的情况，遵守标志标线的规定，低速行驶，不得鸣喇叭和加速抢行。', 3, 1, '2021-12-19 00:12:57', '2021-12-19 00:12:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1976, '驶入高速公路的收费口时，应选择怎样的入口？', '', '1', '1', NULL, '车辆多', '红灯亮', '暂停服务', '绿灯亮', 'D', '驾驶机动车驶入高速公路收费站时，应严格遵守限速规定，选择通道上方绿色灯亮且车辆较少的收费口，依次排队通过，切勿争道抢行。', 3, 1, '2021-12-19 00:12:57', '2021-12-19 00:12:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1977, '驾驶车辆进入高速公路加速车道后，应尽快将车速提高到每小时多少公里以上？', '', '1', '1', NULL, '30', '40', '50', '60', 'D', '《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 因此，驾驶机动车从匝道驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，以防汇入车流时影响主线车道上行驶的车辆，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。', 3, 1, '2021-12-19 00:12:57', '2021-12-19 00:12:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1978, '高速公路上行车，如果因疏忽驶过出口，应怎样做？', '', '1', '1', NULL, '在原地倒车驶回', '继续向前行驶，寻找下一个出口', '立即停车', '在原地掉头', 'B', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，不得紧急制动；即便是刚刚错过出口且距下一出口很远，也不得掉头逆行或倒回出口。', 3, 1, '2021-12-19 00:12:57', '2021-12-19 00:12:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1979, '车辆因故障必须在高速公路停车时，应在车后方多少米以外设置故障警告标志？', '', '1', '1', NULL, '25', '150', '100', '50', 'B', '《道路交通安全法》第六十八条：机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。', 3, 1, '2021-12-19 00:12:58', '2021-12-19 00:12:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1980, '在标志、标线齐全的高速公路上行车，应当按照什么规定的车道和车速行驶？', '', '1', '1', NULL, '《道路交通安全法》', '标志或标线', '车辆说明书', '地方法规', 'B', '标志标线齐全的高速公路上应按照标志标线规定的车道和车速行车。行车时遵循的指示按以下顺序优先级依次递增：交通安全法-地方法规-明确的标志标线-交警指挥。原因是适用范围大的法律不一定适用于所有路况，需要更有针对性的指示来告诉行驶在该路段的驾驶员该如何行驶，以此来维护道路的安全和有序。', 3, 1, '2021-12-19 00:12:58', '2021-12-19 00:12:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1981, '机动车在高速公路行驶，下列做法正确的是？', '', '1', '1', NULL, '可在路肩停车上下人员', '可在紧急停车带停车装卸货物', '可在减速车道或加速车道上超车、停车', '非紧急情况时不得在应急车道行驶或者停车', 'D', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，不得有下列行为： (一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； (二)在匝道、加速车道或者减速车道上超车； (三)骑、轧车行道分界线或者在路肩上行驶； (四)非紧急情况时在应急车道行驶或者停车； (五)试车或者学习驾驶机动车。', 3, 1, '2021-12-19 00:12:58', '2021-12-19 00:12:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1982, '在同向4车道高速公路上行车，车速高于每小时110公里的车辆应在哪条车道上行驶？', '', '1', '1', NULL, '最左侧', '第二条', '第三条', '最右侧', 'A', '最左侧为快车道，最低限速110公里/小时，因此高于110的车辆应在最左侧车道上行驶。 根据《道路交通安全法实施条例》第七十八条 高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 同方向有2条车道的，左侧车道的最低车速为每小时100公里；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', 3, 1, '2021-12-19 00:12:58', '2021-12-19 00:12:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1983, '车辆驶入高速公路匝道后，迅速将车速提高到每小时60公里以上。', '', '2', '1', NULL, '', '', '', '', 'B', '车辆进入匝道后要尽快提高车速，但不能超过交通标志限定的速度。本题错在“迅速”。 《高速公路交通管理办法》第十二条 机动车进入高速公路起点后，应当尽快将车速提高到60公里以上。从匝道入口进入高速公路的车辆，必须在加速车道上提高车速，并开启左转向灯。驶入行车道时，不准妨碍其他车辆的正常行驶。', 3, 1, '2021-12-19 00:12:59', '2021-12-19 00:12:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1984, '车辆在高速公路匝道上可以停车。', '', '2', '1', NULL, '', '', '', '', 'B', '车辆进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，不得超车、停车，也不得未经减速车道直接驶入匝道。', 3, 1, '2021-12-19 00:12:59', '2021-12-19 00:12:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1985, '车辆不得在高速公路匝道上掉头。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车在高速公路上行驶规定：在匝道内不许超车、掉头、停车和倒车。', 3, 1, '2021-12-19 00:12:59', '2021-12-19 00:12:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1986, '车辆不得在高速公路匝道上倒车。', '', '2', '1', NULL, '', '', '', '', 'A', '《高速公路交通管理办法》第十七条 机动车在高速公路上正常行驶时，必须遵循下列规定： （一）不准倒车、逆行，不准穿越中央分隔带掉头或者转弯； （二）不准进行试车和学习驾驶机动车； （三）不准在匝道、加速车道或者减速车道上倒车、超车、停车； （四）不准骑、压车道分界线和在超车道上连续行驶； （五）不准右侧超车； （六）除遇障碍，发生故障等必须停车的情况外，不准随意停车、停车上下人员或者装卸货物； （七）除因停车驶入或者驶出紧急停车带和路肩外，不准在紧急停车带和路肩上行车。', 3, 1, '2021-12-19 00:12:59', '2021-12-19 00:12:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1987, '车辆在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶车辆从匝道进入高速公路加速车道后，打开左转向灯或者不打开均可，尽快将车速提高到每小时60公里以上，并仔细观察车道上车辆行驶的情况，选择驶入行车道的时机。不准在加速车道紧急制动或停车。行车道车辆稀少时，可以正常行驶车辆后驶入行车道；遇高速公路正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道。因此，直接驶入行车道做法错误。', 3, 1, '2021-12-19 00:13:00', '2021-12-19 00:13:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1988, '车辆应靠高速公路右侧的路肩上行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车在高速公路上行使，不得在路肩上行驶。 根据《道路交通安全法实施条例》第八十二条规定，机动车在高速公路上行驶，不得有下列行为： (一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； (二)在匝道、加速车道或者减速车道上超车； (三)骑、轧车行道分界线或者在路肩上行驶； (四)非紧急情况时在应急车道行驶或者停车； (五)试车或者学习驾驶机动车。', 3, 1, '2021-12-19 00:13:00', '2021-12-19 00:13:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1989, '车辆在高速公路以每小时100公里的速度行驶时，距同车道前车100米以上为安全距离。', '', '2', '1', NULL, '', '', '', '', 'A', '根据《道路交通安全法实施条例》第八十条 机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离，车速低于每小时100公里时，与同车道前车距离可以适当缩短，但最小距离不得少于50米。', 3, 1, '2021-12-19 00:13:00', '2021-12-19 00:13:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1990, '车辆在高速公路上行车，可以频繁地变更车道。', '', '2', '1', NULL, '', '', '', '', 'B', '每变更一次车道，就会隐含一次风险。因此，高速公路行车，不得频繁变更车道。 频繁变更车道的危害：变更车道时，不观察车辆两侧和后方道路交通情况，不开启转向灯，随意频繁变更车道或强行突然变道，连续侵占正常通行车辆的行驶路线会严重扰乱道路通行秩序，影响其他车辆的正常通行，容易导致道路拥堵和剐蹭、碰撞事故的发生。', 3, 1, '2021-12-19 00:13:01', '2021-12-19 00:13:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1991, '车辆驶离高速公路时，应当经减速车道减速后进入匝道。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆驶离高速公路进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，也不得未经减速车道直接驶入匝道。', 3, 1, '2021-12-19 00:13:01', '2021-12-19 00:13:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1992, '在高速公路变更车道时，应提前开启转向灯，观察情况，确认安全后，驶入需要变更的车道。', '', '2', '1', NULL, '', '', '', '', 'A', '高速公路行车，不得频繁变更车道。确需变更车道时，应提前观察将驶入车道的交通情况，在不影响其他车辆正常行驶且确认安全的情况下，开启转向灯，缓慢转向，加速变道。', 3, 1, '2021-12-19 00:13:01', '2021-12-19 00:13:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1993, '高速公路因发生事故造成堵塞时，可在右侧紧急停车带或路肩行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，不得有下列行为： (一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； (二)在匝道、加速车道或者减速车道上超车； (三)骑、轧车行道分界线或者在路肩上行驶； (四)非紧急情况时在应急车道行驶或者停车；', 3, 1, '2021-12-19 00:13:01', '2021-12-19 00:13:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1994, '行驶在高速公路上遇大雾视线受阻时，应当立即紧急制动停车。', '', '2', '1', NULL, '', '', '', '', 'B', '行驶在高速公路遇大雾视线受阻时，应开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过20公里/小时，并从最近的出口尽快驶离高速公路。 高速公路停车应选择到服务区停车，不得在行车道内停车。', 3, 1, '2021-12-19 00:13:02', '2021-12-19 00:13:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1995, '机动车在高速公路上遇前方交通受阻时，应当跟随前车顺序排队，并立即开启危险报警闪光灯，防止追尾。', '', '2', '1', NULL, '', '', '', '', 'A', '开启危险警报闪光灯是为了提醒后车：前方有故障，请减速慢行，谨防追尾。', 3, 1, '2021-12-19 00:13:02', '2021-12-19 00:13:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1996, '在高速公路上遇分流交通管制时，可不驶出高速公路，就地靠边停靠等待管制结束后继续前行。', '', '2', '1', NULL, '', '', '', '', 'B', '在高速公路上遇到分流交通管制时，应听从交警指挥，按指示通行，不得就地靠边停车等待，以免造成更大的交通堵塞。', 3, 1, '2021-12-19 00:13:02', '2021-12-19 00:13:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1997, '小型客车行驶在平坦的高速公路上，突然有颠簸感觉时，应迅速降低车速，防止爆胎。', '', '2', '1', NULL, '', '', '', '', 'A', '高速行车时，如果车身突然出现颠簸现象，则有可能存在轮胎漏气问题，应迅速反应，及时降低车速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不急导致追尾事故。请注意：这里的“迅速”指的是迅速反应减速行驶，不是指紧急停车的意思。', 3, 1, '2021-12-19 00:13:02', '2021-12-19 00:13:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1998, '在高速公路上行驶感觉疲劳时，应立即停车休息。', '', '2', '1', NULL, '', '', '', '', 'B', '在高速公路上行驶感觉疲困时，应该找到最近的服务区停车休息，不可在高速上停车休息。', 3, 1, '2021-12-19 00:13:03', '2021-12-19 00:13:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (1999, '《道路交通安全法实施条例》规定，高速公路上最高时速不得超过120公里。因此在高速公路上行驶只要时速不超过120公里就不违法。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第七十八条，高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。因此，题干表述错误。', 3, 1, '2021-12-19 00:13:03', '2021-12-19 00:13:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4100, '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '', '2', '4', NULL, '', '', '', '', 'B', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 9, 1, '2021-12-19 01:13:58', '2021-12-19 01:13:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4101, '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', '', '2', '4', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。因此此题是错的。', 9, 1, '2021-12-19 01:13:59', '2021-12-19 01:13:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4102, '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第四章第三十七条，道路划设专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶。因此题干表述错误。', 9, 1, '2021-12-19 01:13:59', '2021-12-19 01:13:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4103, '机动车行经交叉路口，不得超车。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 因此，题干表述正确。', 9, 1, '2021-12-19 01:13:59', '2021-12-19 01:13:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4104, '驾乘人员下车时要怎样做以保证安全？', '', '1', '4', NULL, '停车后立即开门下车', '观察前方交通情况', '先开车门再观察侧后情况', '先观察侧后情况，再缓开车门', 'D', '机动车驾驶人及乘车人下车时，先观察侧后方和左侧交通情况，确认安全后，再用远离车门一侧的手缓开车门，以免开车门时妨碍其他车辆记行人通行。', 9, 1, '2021-12-19 01:13:59', '2021-12-19 01:13:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4105, '机动车在雨天临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯', '危险报警闪光灯', '前大灯', '倒车灯', 'B', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯和危险报警闪光灯。', 9, 1, '2021-12-19 01:14:00', '2021-12-19 01:14:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4106, '机动车在雾天临时停车时，应开启什么灯？', '', '1', '4', NULL, '危险报警闪光灯、示廓灯和后位灯', '左转向灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'A', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯、危险报警闪光灯和后位灯。', 9, 1, '2021-12-19 01:14:00', '2021-12-19 01:14:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4107, '机动车在夜间临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'C', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯、危险报警闪光灯和后位灯。', 9, 1, '2021-12-19 01:14:00', '2021-12-19 01:14:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4108, '机动车在雪天临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', 'D', '下雪天开车危险，临时停车时，应当开启危险报警闪光灯、示廓灯和后位灯，提醒其他车辆注意。', 9, 1, '2021-12-19 01:14:01', '2021-12-19 01:14:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4109, '机动车停车的错误做法是什么？', '', '1', '4', NULL, '应当在规定地点停放', '禁止在人行道上停放', '在道路上临时停车时，不得妨碍其他机动车和行人通行', '可以停放在非机动车道上', 'D', '机动车停车的正确做法：①驾驶机动车停车，要在规定地点停放。 ②需要在路边停车时，选择在停车泊位内停放。 ③在道路上临时停车，不得妨碍其他车辆和行人通行。 ④在没有施划停车泊位的道路上，路边停车要紧靠道路右侧，按顺行方向停放，车身距道路边缘不超过30厘米，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离。 ⑤遇机动车故障或交通事故停车时，车辆难以移动的，应当开启危险报警闪光灯；夜间须开启危险报警闪光灯、示廓灯和后位灯。 请注意，题干要求选出机动车停车的错误做法，因此，停放在非机动车道上做法错误。', 9, 1, '2021-12-19 01:14:01', '2021-12-19 01:14:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4110, '在这个区域内可以临时停车。', '/uploadFile/yuanBeiTu110.gif', '2', '4', NULL, '', '', '', '', 'B', '', 9, 1, '2021-12-19 01:14:01', '2021-12-19 01:14:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4111, '在这个区域内不允许长时间停放机动车。', '/uploadFile/yuanBeiTu111.gif', '2', '4', NULL, '', '', '', '', 'A', '', 9, 1, '2021-12-19 01:14:02', '2021-12-19 01:14:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4112, '驾驶机动车在前方路口怎样掉头？', '/uploadFile/yuanBeiTu112.gif', '1', '4', NULL, '经左转弯待转区进行掉头', '在路口虚线处进行掉头', '左转信号灯亮时方可掉头', '直行信号灯亮时方可掉头', 'B', '如图所示，车行道中心线是用来分隔对向行驶车辆的，在设有允许掉头标志和标线的路口，车辆可从中心线虚线处掉头，无须经过人行横道或进入路口。注意此时掉头不受信号灯的控制，但是要注意避让正常直行的车辆。', 9, 1, '2021-12-19 01:14:02', '2021-12-19 01:14:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4113, '驾驶机动车在前方路口掉头前先进入左转直行车道。', '/uploadFile/yuanBeiTu113.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，最左侧是掉头车道，因此机动车应提前进入最左侧掉头车道，从虚线处掉头，不应在左转直行车道掉头。', 9, 1, '2021-12-19 01:14:03', '2021-12-19 01:14:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4114, '驾驶机动车在这个路口允许掉头。', '/uploadFile/yuanBeiTu114.gif', '2', '4', NULL, '', '', '', '', 'B', '如图，左侧标志是禁止左转，加上前方是双黄实线，不能掉头。', 9, 1, '2021-12-19 01:14:03', '2021-12-19 01:14:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4115, '驾驶机动车进入左侧车道可以掉头。', '/uploadFile/yuanBeiTu115.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，车行道中心线是用来分隔对向行驶车辆的，车辆可从中心线虚线处掉头，无须经过人行横道或进入路口。注意此时掉头虽然不受信号灯的控制，但是要注意避让正常直行的车辆。', 9, 1, '2021-12-19 01:14:03', '2021-12-19 01:14:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4116, '驾驶机动车需要掉头时，只要不影响正常交通可以在虚线处掉头。', '/uploadFile/yuanBeiTu116.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，道路中心线是黄色虚线，虚线允许临时跨越，因此机动车需要掉头时，只要不影响正常交通（即确保安全）的前提下是可以在虚线处进行掉头的。', 9, 1, '2021-12-19 01:14:04', '2021-12-19 01:14:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4117, '驾驶机动车在该处不影响行人正常通行的情况下可以掉头。', '/uploadFile/yuanBeiTu117.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十九条 机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 9, 1, '2021-12-19 01:14:04', '2021-12-19 01:14:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4118, '驾驶机动车在这个路口可以沿掉头车道直接掉头。', '/uploadFile/yuanBeiTu118.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，最左侧掉头信号灯显示红灯，表示禁止掉头，当掉头信号灯为绿灯时，方可掉头。', 9, 1, '2021-12-19 01:14:04', '2021-12-19 01:14:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4119, '机动车倒车时遇到这种情况怎样做以保证安全？', '/uploadFile/yuanBeiTu119.gif', '1', '4', NULL, '低速缓慢倒车', '主动停车避让', '连续鸣喇叭示意', '向右转向倒车', 'B', '机动车倒车时，若发现有过往车辆通过，应主动停车避让。', 9, 1, '2021-12-19 01:14:05', '2021-12-19 01:14:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4120, '发生该事故的主要原因是驾驶人倒车前没有进行安全确认。', '/uploadFile/yuanBeiTu120.gif', '2', '4', NULL, '', '', '', '', 'A', '倒车前，应仔细观察倒车路线，确认具备安全倒车条件后方可倒车。倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，并随时注意车头两侧的安全空间位置，随时做好停车准备，以免因转向角度过大而发生剐蹭事故。', 9, 1, '2021-12-19 01:14:05', '2021-12-19 01:14:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4121, '机动车倒车时，后方道路条件较好的，应加速倒车，迅速完成操作。', '', '2', '4', NULL, '', '', '', '', 'B', '安全倒车，倒车前应仔细观察倒车路线，确认具备安全倒车条件后方可倒车。倒车过程中要缓慢行驶，注意观察车辆两侧和后方的情况，并随时注意车头两侧的安全空间位置，随时做好停车准备，以免因转向角度过大而发生剐蹭事故。', 9, 1, '2021-12-19 01:14:05', '2021-12-19 01:14:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4122, '机动车可以选择交叉路口进行倒车。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十条规定：机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 9, 1, '2021-12-19 01:14:06', '2021-12-19 01:14:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4123, '机动车不得在隧道中倒车。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十条 机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 9, 1, '2021-12-19 01:14:06', '2021-12-19 01:14:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4124, '夜间驾驶机动车在窄路或者窄桥遇自行车对向驶来时，要怎样使用灯光？', '', '1', '4', NULL, '连续变换远、近光灯', '使用示廓灯', '使用远光灯', '使用近光灯', 'D', '《道路交通安全法实施条例》第四十八条：在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 9, 1, '2021-12-19 01:14:06', '2021-12-19 01:14:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4125, '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', '', '1', '4', NULL, '前后雾灯', '近光灯', '远光灯', '危险报警闪光灯', 'B', '夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用，通过照明条件良好的路段时，使用近光灯。', 9, 1, '2021-12-19 01:14:06', '2021-12-19 01:14:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4126, '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？', '', '1', '4', NULL, '使用远光灯', '使用近光灯', '使用危险报警闪光灯', '交替使用远近光灯示意', 'D', '《道路交通安全法实施条例》第五十九条 机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 9, 1, '2021-12-19 01:14:07', '2021-12-19 01:14:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4127, '夜间驾驶机动车遇到这种情况怎样使用灯光？', '/uploadFile/yuanBeiTu127.gif', '1', '4', NULL, '临近时关闭前照灯', '使用近光灯', '使用远光灯', '提前关闭所有灯光', 'B', '夜间驾驶机动车在照明良好的路段，使用近光灯。', 9, 1, '2021-12-19 01:14:08', '2021-12-19 01:14:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4128, '夜间驾驶机动车在照明条件良好的路段跟车行驶怎样使用灯光？', '', '1', '4', NULL, '关闭前照灯', '使用远光灯', '关闭所有车灯', '使用近光灯', 'D', '夜间跟车行驶时，要使用近光灯，保持安全距离，注意观察前车信号灯的变化，随时做好减速或停车的准备。跟车时，不得使用远光灯，同时要提防路侧黑暗处的行人，防止其突然横穿。', 9, 1, '2021-12-19 01:14:08', '2021-12-19 01:14:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4129, '机动车驶入双向行驶隧道前，要如何使用灯光？', '', '1', '4', NULL, '开启危险报警闪光灯', '开启远光灯', '开启雾灯', '开启近光灯', 'D', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', 9, 1, '2021-12-19 01:14:09', '2021-12-19 01:14:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4130, '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '', '2', '4', NULL, '', '', '', '', 'B', '夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用。在照明条件良好的路段，使用近光灯。', 9, 1, '2021-12-19 01:14:09', '2021-12-19 01:14:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4131, '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', '', '2', '4', NULL, '', '', '', '', 'A', '在行驶过程中要留意与前后车的距离，因此若是贸然使用制动，很容易使后车来不及反应发生追尾，所以本题正确。', 9, 1, '2021-12-19 01:14:09', '2021-12-19 01:14:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4132, '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '', '2', '4', NULL, '', '', '', '', 'B', '跟车行驶时，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，控制好与前车的安全距离，是避免发生追尾、剐碰等事故的前提。', 9, 1, '2021-12-19 01:14:09', '2021-12-19 01:14:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4133, '如动画所示，驾驶机动车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu133.gif', '1', '4', NULL, '借对向车道行驶', '急剧制动低速通过', '靠弯道外侧行驶', '充分减速并靠右侧行驶', 'D', '驾驶机动车通过弯道时，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”，不要占对向车道。', 9, 1, '2021-12-19 01:14:10', '2021-12-19 01:14:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4134, '驾驶机动车遇到这种情况怎样做行驶最安全？', '/uploadFile/yuanBeiTu134.gif', '1', '4', NULL, '鸣喇叭或开前照灯', '减速靠右侧行驶', '尽量靠路中心行驶', '沿道路左侧行驶', 'B', '如图，前方弯道，且对向车道有多辆车辆驶来，因视线遮挡我方无法获知前方道路情况及是否还有车辆驶来，因此应当减速靠右侧行驶，以免发生碰撞。', 9, 1, '2021-12-19 01:14:10', '2021-12-19 01:14:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4135, '在这种条件的道路上怎样行驶才安全？', '/uploadFile/yuanBeiTu135.gif', '1', '4', NULL, '靠路左侧转大弯', '靠弯路中心转弯', '靠路右侧转小弯', '借对向车道转弯', 'C', '如图，前方是向右急转弯。靠右转弯是小弯，靠左转弯是大弯。通过弯道应鸣喇叭、减速、靠右行。', 9, 1, '2021-12-19 01:14:10', '2021-12-19 01:14:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4136, '在这种条件的道路上怎样安全行驶？', '/uploadFile/yuanBeiTu136.gif', '1', '4', NULL, '靠路右侧转小弯', '靠弯路中心转弯', '借对向车道转弯', '靠路右侧转大弯', 'D', '如图所示，驾驶机动车通过向左急弯路时，应注意靠路右侧转大弯减速慢行，不得靠道路中心或占对向车道行驶，以防对向来车时发生剐碰事故。', 9, 1, '2021-12-19 01:14:11', '2021-12-19 01:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4137, '驾驶机动车在这种条件的弯道处怎样转弯最安全？', '/uploadFile/yuanBeiTu137.gif', '1', '4', NULL, '减速靠右侧行驶', '骑轧路中心行驶', '靠弯道外侧行驶', '借对向车道行驶', 'A', '驾驶机动车通过弯道时，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”，不要占对向车道。', 9, 1, '2021-12-19 01:14:11', '2021-12-19 01:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4138, '机动车遇有急弯路时要在进入弯路后减速。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车遇到急弯路时应当提前减速慢行，而不能再进入弯路后减速。如果进入弯路后减速，很容易发生侧滑或侧翻，十分危险。因此题干表述错误，应进入弯路前就要减速。', 9, 1, '2021-12-19 01:14:11', '2021-12-19 01:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4139, '机动车行驶至转弯路段时，易引发事故的驾驶行为有什么？', '', '3', '4', NULL, '机动车占对向道行驶', '在弯道内急转转向盘', '在驶入弯道前不减速', '机动车靠路右侧行驶', 'ABC', '驾驶机动车在转弯路段行驶时，要注意观察，按照导向箭头的指示减速靠右行驶，不得急转转向盘、加速或占道行驶，以免发生交通事故。', 9, 1, '2021-12-19 01:14:12', '2021-12-19 01:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4140, '如动画所示，驾驶机动车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu140.gif', '1', '4', NULL, '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '立即变道绕过行人', 'A', '由动画可知，驾驶机动车遇行人横穿道路时，应减速或停车让行人先行。', 9, 1, '2021-12-19 01:14:12', '2021-12-19 01:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4141, '驾驶机动车直行通过前方路口怎样行驶？', '/uploadFile/yuanBeiTu141.gif', '1', '4', NULL, '接近路口时减速慢行', '进入路口后再减速慢行', '可以不减速直接通过', '提前加速通过交叉路口', 'A', '前方是没有交通信号灯的交叉路口，应当在接近路口时提前减速慢行，确认安全后通过。', 9, 1, '2021-12-19 01:14:12', '2021-12-19 01:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4142, '驾驶机动车在这个路口怎样左转弯行驶？', '/uploadFile/yuanBeiTu142.gif', '1', '4', NULL, '沿直行车道左转', '进入左转弯待转区', '进入直行等待区', '沿左车道左转弯', 'B', '设有左转弯待转区的路口，指示左转弯车辆在直行时段（直行绿灯）进入左转弯待转区等待左转，但不得妨碍对向直行车辆的正常行驶。', 9, 1, '2021-12-19 01:14:12', '2021-12-19 01:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4143, '驾驶机动车在这个路口怎样右转弯行驶？', '/uploadFile/yuanBeiTu143.gif', '1', '4', NULL, '沿直行车道右转弯', '停止线前停车等待', '沿右侧道路右转弯', '借非机动车道右转', 'C', '如图所示，机动车信号灯红灯亮时，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 9, 1, '2021-12-19 01:14:13', '2021-12-19 01:14:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4144, '驾驶机动车在路口直行遇到这种情况怎么办？', '/uploadFile/yuanBeiTu144.gif', '1', '4', NULL, '鸣喇叭示意其让行', '加速从车前通过', '开前照灯示意其让行', '减速或停车让行', 'D', '交叉路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险。遇对向左转弯车辆强行左转时，应减速或停车让行。', 9, 1, '2021-12-19 01:14:14', '2021-12-19 01:14:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4145, '驾驶机动车在路口遇到这种情况的行人怎么办？', '/uploadFile/yuanBeiTu145.gif', '1', '4', NULL, '及时减速停车让行', '鸣喇叭示意其让道', '加速从行人前通过', '开前照灯示意其让道', 'A', '道路通行中坚持“行人优先”原则，驾驶机动车在路口遇行人横穿马路时，应及时减速，停车让行。', 9, 1, '2021-12-19 01:14:14', '2021-12-19 01:14:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4146, '驾驶机动车进入这个路口怎样使用灯光？', '/uploadFile/yuanBeiTu146.gif', '1', '4', NULL, '开启右转向灯', '开启危险报警闪光灯', '不用开启转向灯', '开启左转向灯', 'C', '由图可知，前方是向右环形路段，因此向右行驶时不用开启转向灯，注意观察已在环岛内行驶车辆的动态，适时汇入车流。', 9, 1, '2021-12-19 01:14:15', '2021-12-19 01:14:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4147, '驾驶机动车驶出这个环岛路口怎样使用灯光？', '/uploadFile/yuanBeiTu147.gif', '1', '4', NULL, '开启左转向灯', '开启报警闪光灯', '不用开转向灯', '开启右转向灯', 'D', '驶近环岛时，注意观察左侧已在环岛内行驶车辆的动态，适时汇入车流，必要时减速或停车让行；驶出环岛前，打开右转向灯，注意观察右侧车辆、行人的动态。', 9, 1, '2021-12-19 01:14:25', '2021-12-19 01:14:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4148, '驾驶机动车驶近前方主支干道交汇处要注意什么？', '/uploadFile/yuanBeiTu148.gif', '1', '4', NULL, '提前减速，注意机动车', '保持正常速度行驶', '鸣喇叭，迅速通过', '提前加速，快速通过', 'A', '由图可知，前方会有车辆从支路汇入主路，因此为了避免支路的车辆汇入时发生碰撞，所以要减速慢行，并注意周围的机动车，因此本题选A。', 9, 1, '2021-12-19 01:14:25', '2021-12-19 01:14:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4149, '如动画所示，驾驶人的行为是正确的。', '/uploadFile/yuanBeiTu149.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在有导向箭头的车道行驶时，一定要按导向箭头的指示行驶，如动画所示，黄色小车在左转车道，只能向左转弯行驶，不可以右转弯行驶。因此，动画中驾驶人的行为是错误的，违反道路交通标线的通行规定。', 9, 1, '2021-12-19 01:14:26', '2021-12-19 01:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4150, '驾驶机动车通过这个路口要注意观察左侧情况。', '/uploadFile/yuanBeiTu150.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在没有交通信号灯控制的路口直行，应在距路口50-100米时减速，行至路口时仔细观察左、右两侧道路交通情况，减速或停车瞭望，做到“一看，二慢，三通过”。因此，题干表述错误，不应只观察左侧情况。', 9, 1, '2021-12-19 01:14:26', '2021-12-19 01:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4151, '驾驶机动车在交叉路口遇到这种情况可以不让行。', '/uploadFile/yuanBeiTu151.gif', '2', '4', NULL, '', '', '', '', 'B', '路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险，应注意避让其他车辆及行人，随时做好停车让行的准备，即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', 9, 1, '2021-12-19 01:14:26', '2021-12-19 01:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4152, '驾驶机动车在这个路口右转弯可以不变更车道。', '/uploadFile/yuanBeiTu152.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过路口时，要仔细观察路口两侧的交通情况，根据车辆行驶方向提前变更至相应的车道，路口右转弯时，提前开启右转向灯，进入右转弯车道，完成右转弯。', 9, 1, '2021-12-19 01:14:27', '2021-12-19 01:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4153, '驾驶机动车在这个路口可以直接向右转弯。', '/uploadFile/yuanBeiTu153.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过设有箭头信号灯的路口右转弯行驶时，要注意观察信号灯的指示方向，当本车道对应的绿色箭头信号灯亮时，可以通过；红色箭头信号灯亮时，禁止通行。', 9, 1, '2021-12-19 01:14:27', '2021-12-19 01:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4154, '在这个路口左转弯要靠路口中心点左侧转弯。', '/uploadFile/yuanBeiTu154.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在设有中心圈的路口左转弯时，要沿中心圈左侧向左行驶，不得轧线或从右侧向左行驶。', 9, 1, '2021-12-19 01:14:27', '2021-12-19 01:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4155, '驾驶机动车在这个路口左转弯要提前按导向箭头指示向左变更车道。', '/uploadFile/yuanBeiTu155.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在有导向箭头的路口左转弯时，要提前按导向箭头指示向左变更车道。在变更车道时，注意观察前方和左侧车道内的情况，不能影响左侧车道内车辆通行。', 9, 1, '2021-12-19 01:14:28', '2021-12-19 01:14:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4156, '驾驶机动车此时可以加速通过路口。', '/uploadFile/yuanBeiTu156.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知，此时信号灯已经变为黄色，即将转变为红色，此时应当在停车线前停车。否则就是闯黄灯的行为了。而且人行道上还有行人在过马路，所以如果加速通过，容易撞到行人造成事故，因此本题是错误的。', 9, 1, '2021-12-19 01:14:28', '2021-12-19 01:14:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4157, '驾驶机动车在这个路口右转弯时要避让非机动车。', '/uploadFile/yuanBeiTu157.gif', '2', '4', NULL, '', '', '', '', 'A', '右转弯时，在非机动车道有非机动车正在前进，此时应当注意避让，待非机动车通过后再转弯，因此题干表述正确。', 9, 1, '2021-12-19 01:14:28', '2021-12-19 01:14:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4158, '驾驶机动车怎样通过这个铁路道口？', '/uploadFile/yuanBeiTu158.gif', '1', '4', NULL, '换入空档，滑行通过', '一停、二看、三通过', '加速、观察、快通过', '减速、观察、慢通过', 'B', '驾驶机动车通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）', 9, 1, '2021-12-19 01:14:29', '2021-12-19 01:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4159, '驾驶机动车怎样安全通过铁路道口？', '/uploadFile/yuanBeiTu159.gif', '1', '4', NULL, '换空挡利用惯性通过', '进入道口后换低速挡', '进入道口前减速减挡', '道口内停车左右观察', 'C', '驾驶机动车通过有交通信号控制的铁路道口时，在道口外提前减速、减挡，按照信号灯的指示低速通行，不得在路口内变换档位。遇报警器鸣响或红灯亮时，停车等候，不准抢行通过铁路道口。', 9, 1, '2021-12-19 01:14:29', '2021-12-19 01:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4160, '如动画所示，机动车通过铁路道口的做法是正确的。', '/uploadFile/yuanBeiTu160.gif', '2', '4', NULL, '', '', '', '', 'A', '由动画可知，前方是无人看守的铁路道口，并且设有停车让行标志，因此机动车在停止线外停车让行的做法是正确的。', 9, 1, '2021-12-19 01:14:29', '2021-12-19 01:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4161, '驾驶机动车不能快速通过这种情况的铁路道口。', '/uploadFile/yuanBeiTu161.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过无信号控制或无人看守的铁路道口时，要在道口外停车观察，做到一停（在停止线以外停车）、二看（观察左右是否有驶来的列车）、三通过（确认安全后，低速通过）。', 9, 1, '2021-12-19 01:14:30', '2021-12-19 01:14:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4162, '驾驶机动车通过这个铁路道口时要减速停车。', '/uploadFile/yuanBeiTu162.gif', '2', '4', NULL, '', '', '', '', 'A', '道路与铁路平面交叉道口有两个红灯交替闪烁或者一个红灯亮时，表述火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线。因此，题干表述正确。', 9, 1, '2021-12-19 01:14:30', '2021-12-19 01:14:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4163, '如动画所示，驾驶机动车遇到这种情况要如何处置？', '/uploadFile/yuanBeiTu163.gif', '1', '4', NULL, '加速通过', '立即停车', '鸣喇叭示意行人让道', '先注意观察行人、非机动车动态，再通过', 'D', '驾驶机动车接近人行横线时，提前减速观察，注意观察人行横道左右两侧是否有行人、非机动车通行，随时准备停车礼让行人，确认安全后再通过。', 9, 1, '2021-12-19 01:14:31', '2021-12-19 01:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4164, '驾驶机动车在这个位置怎样安全通过？', '/uploadFile/yuanBeiTu164.gif', '1', '4', NULL, '加速从行人前通过', '从行人后绕行通过', '减速、鸣喇叭示意', '停车等待行人通过', 'D', '道路通行中坚持行人优先原则，驾驶机动车接近人行横道线遇行人通过人行横道时，即使是绿灯亮，也应及时停车让行。', 9, 1, '2021-12-19 01:14:31', '2021-12-19 01:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4165, '驾驶机动车遇到这种情况的人行横道怎样通过？', '/uploadFile/yuanBeiTu165.gif', '1', '4', NULL, '减速通过', '加速通过', '鸣喇叭通过', '紧急制动', 'A', '机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。', 9, 1, '2021-12-19 01:14:31', '2021-12-19 01:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4166, '造成这起事故的主要原因是行人从车前横穿。', '/uploadFile/yuanBeiTu166.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第四章道路通行规定 第二节机动车通行规定 ：第四十七条　机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。 如图所示，机动车遇行人正在通过人行横道时，应及时停车礼让，不得抢行。因此，造成这起事故的主要原因是机动车违反道路通行规定。', 9, 1, '2021-12-19 01:14:32', '2021-12-19 01:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4167, '驾驶机动车遇到这种情况的人行横道线可以加速通过。', '/uploadFile/yuanBeiTu167.gif', '2', '4', NULL, '', '', '', '', 'B', '机动车行经人行横道时，应当减速行驶，注意观察人行横道左右两侧行人动态，随时准备停车礼让行人。', 9, 1, '2021-12-19 01:14:32', '2021-12-19 01:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4168, '驾驶机动车在人行横道前遇到这种情况一定要减速慢行。', '/uploadFile/yuanBeiTu168.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，右侧车道机动车停车礼让行人。因此驾驶机动车在人行横道前遇到有停止的车辆时，一定要停车，不要盲目通过，前车可能是停车避让行人。', 9, 1, '2021-12-19 01:14:32', '2021-12-19 01:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4169, '驾驶机动车看到路边有这种标志时怎样行驶？', '/uploadFile/yuanBeiTu169.gif', '1', '4', NULL, '采取紧急制动', '减速注意观察', '断续鸣喇叭', '做好绕行准备', 'B', '驾驶车辆行至学校附近或有注意儿童标志的路段时，一定要及时减速，注意观察道路两侧及周围的情况，时刻提防学生横过道路。', 9, 1, '2021-12-19 01:14:33', '2021-12-19 01:14:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4170, '驾驶机动车在学校门口遇到这种情况怎样行驶？', '/uploadFile/yuanBeiTu170.gif', '1', '4', NULL, '从列队前方绕过', '减速慢行通过', '及时停车让行', '从列队空隙穿过', 'C', '驾驶机动车在学校区域，遇到上学或放学时段，随时准备避让横过道路的学生和儿童。遇学生或儿童横过道路时，应及时停车礼让，不可从他们中间穿行或从两侧绕行通过，避免发生事故。', 9, 1, '2021-12-19 01:14:33', '2021-12-19 01:14:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4171, '驾驶机动车在居民小区看到这种情况怎样安全行驶？', '/uploadFile/yuanBeiTu171.gif', '1', '4', NULL, '鸣喇叭提示行人', '加速，尽快通过', '保持正常行驶', '减速，准备停车', 'D', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。', 9, 1, '2021-12-19 01:14:34', '2021-12-19 01:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4172, '驾驶机动车通过居民小区遇到这种情况怎样处置？', '/uploadFile/yuanBeiTu172.gif', '1', '4', NULL, '立即停车', '加速通过', '连续鸣喇叭', '减速慢行', 'A', '驾驶机动车通过居民小区，看到突然出现的皮球，要注意观察两侧情况立即停车，以防碰撞跑出来追逐皮球的儿童。', 9, 1, '2021-12-19 01:14:34', '2021-12-19 01:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4173, '驾驶机动车看到这个标志时要及时减速。', '/uploadFile/yuanBeiTu173.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在学校区域，应注意观察，减速慢行；遇到上学或放学时段，随时准备避让横过道路的学生和儿童。遇学生或儿童横过道路时，应及时停车礼让，不可从他们中间穿行或从两侧绕行通过，避免发生事故。', 9, 1, '2021-12-19 01:14:34', '2021-12-19 01:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4174, '在学校门口遇到这种情况要做好随时停车的准备。', '/uploadFile/yuanBeiTu174.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶车辆行至学校附近或有注意儿童标志的路段时，一定要及时减速，注意观察道路两侧及周围的情况，时刻提防学生横过道路。', 9, 1, '2021-12-19 01:14:34', '2021-12-19 01:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4175, '驾驶机动车在学校附近遇到这种情况要尽快加速通过。', '/uploadFile/yuanBeiTu175.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，此为学校区域，人行道上有学生正欲过马路，应减速慢行，必要时停车避让。', 9, 1, '2021-12-19 01:14:35', '2021-12-19 01:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4176, '驾驶机动车在居民小区遇到这种情形要连续鸣喇叭。', '/uploadFile/yuanBeiTu176.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。', 9, 1, '2021-12-19 01:14:35', '2021-12-19 01:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4177, '驾驶机动车在居民小区遇到这种情形要紧跟其后行驶。', '/uploadFile/yuanBeiTu177.gif', '2', '4', NULL, '', '', '', '', 'B', '居民小区通行时，要注意避让行人，遇两侧有行人或行人占道行走时，要与行人保持安全距离低速行驶，待行人让路后再通过。因此，紧随其后做法错误。', 9, 1, '2021-12-19 01:14:35', '2021-12-19 01:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4178, '驾驶机动车进入居民小区不能超过限速标志限定的速度行驶。', '/uploadFile/yuanBeiTu178.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。 由图可知，进入此小区车速限制在5公里/小时，也就是说不能超过5公里/小时，因此本题正确。', 9, 1, '2021-12-19 01:14:36', '2021-12-19 01:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4179, '驾驶机动车在小区内遇到这样的情况要在自行车前加速通过。', '/uploadFile/yuanBeiTu179.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知，前方是丁字路口，且横向道路有骑自行车的人穿出。遇到这种情况应当让自行车先行，减速慢行注意避让。因此本题错误。', 9, 1, '2021-12-19 01:14:36', '2021-12-19 01:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4180, '驾驶机动车通过学校时要注意什么？', '', '3', '4', NULL, '观察标志标线', '减速慢行', '不要鸣喇叭', '快速通过', 'ABC', '学校区域禁鸣，驾驶机动车通过学校区域，一定要及时减速，注意观察标志标线及道路两侧、周围情况，时刻提防学生横过道路。', 9, 1, '2021-12-19 01:14:36', '2021-12-19 01:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4181, '驾驶机动车在这种情况下注意什么？', '/uploadFile/yuanBeiTu181.gif', '1', '4', NULL, '行人从车后穿出', '行人从车前穿出', '公交车突然倒车', '公交车突然起步', 'B', '驾驶机动车超越停在公交车站内的车辆时，要减速慢行，与公交车保持较大的安全间距，预防车站上下车的乘客从车前或车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。如图所示，应注意车前的行人穿出。', 9, 1, '2021-12-19 01:14:37', '2021-12-19 01:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4182, '在这种公交车站怎样预防公交车突然起步？', '/uploadFile/yuanBeiTu182.gif', '1', '4', NULL, '在公交车后停车', '迅速超越公交车', '减速，缓慢超越', '连续鸣喇叭提醒', 'C', '驾驶机动车驶近停有公交车的车站时，要减速慢行，与公交车保持较大的安全间距，预防公交车突然起步或车站上下车的乘客从车前、车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。【注意区分：机动车遇校车VS公交车时的行驶规定】', 9, 1, '2021-12-19 01:14:37', '2021-12-19 01:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4183, '驾驶机动车遇到这种情况怎样行驶？', '/uploadFile/yuanBeiTu183.gif', '1', '4', NULL, '加速从左侧超越', '连续鸣喇叭告知', '紧跟在自行车后', '减速避让自行车', 'D', '驾驶机动车在公交车站，遇有非机动车超越公交车时，要减速避让，与其保持较大的安全间距，预防公交车突然起步或非机动车突然摔倒。', 9, 1, '2021-12-19 01:14:38', '2021-12-19 01:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4184, '驾驶机动车在这种情况下可以占用公交车站临时停车。', '/uploadFile/yuanBeiTu184.gif', '2', '4', NULL, '', '', '', '', 'B', '根据《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：（三）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，不得停车。因此本题错误。请注意：“这种情况下可以”，不是“这种情况不可以”。', 9, 1, '2021-12-19 01:14:38', '2021-12-19 01:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4185, '驾驶机动车在公交车站遇到这种情况要迅速停车让行。', '/uploadFile/yuanBeiTu185.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过公交车站时，要减速慢行，与公交车保持较大的安全间距，预防车站上下车的乘客，同时应特别注意公交车前后是否有行人横穿道路，做好随时停车的准备。', 9, 1, '2021-12-19 01:14:39', '2021-12-19 01:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4186, '使用已有裂纹或损伤的轮胎容易引起什么后果？', '', '1', '4', NULL, '向一侧偏驶', '爆胎', '转向困难', '行驶阻力增大', 'B', '使用已经有裂纹或损伤的轮胎行驶，造成气压不稳容易爆胎。', 9, 1, '2021-12-19 01:14:39', '2021-12-19 01:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4187, '汽车的专用备胎可作为正常轮胎长期使用。', '', '2', '4', NULL, '', '', '', '', 'B', '车辆配备的专用备胎不能作为正常轮胎长时间使用，只能在发生爆胎或轮胎漏气时临时应急使用。使用备胎时，行驶速度不能过快，而且要尽快将车开到最近的修理厂进行维修，换上正常的轮胎。', 9, 1, '2021-12-19 01:14:39', '2021-12-19 01:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4188, '怎样调整汽车座椅安全头枕的高度？', '', '1', '4', NULL, '调整到头枕中心对正颈部', '调整到头枕中心与颈部平齐', '调整到头枕中心高出头顶', '调整到头枕中心能支撑头部', 'D', '安全头枕高度的调整方法：起步前将保护颈部的头枕调整到合适位置（能支撑自己头部的高度），即头枕顶部与头平齐，头枕中央对准脑后部。安全头枕：1、正对脑后部；2、支撑头部；3、保护颈部。', 9, 1, '2021-12-19 01:14:40', '2021-12-19 01:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4189, '驾驶装有ABS系统的汽车怎样采取紧急制动？', '', '1', '4', NULL, '用力踏制动踏板', '间歇踏制动踏板', '缓慢踏制动踏版', '逐渐踏下制动踏板', 'A', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 9, 1, '2021-12-19 01:14:40', '2021-12-19 01:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4190, '车辆发生碰撞时，关于安全带作用的说法错误的是什么？', '', '3', '4', NULL, '保护颈部不受伤害', '减轻驾乘人员受伤程度', '减轻驾驶人疲劳', '保持正确驾驶姿势', 'ACD', '座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。 请注意，题干要求选出说法错误的选项，因此，保护颈部不受伤害、减轻驾驶人疲劳及保持正确驾驶姿势说法错误。', 9, 1, '2021-12-19 01:14:40', '2021-12-19 01:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4191, '驾驶汽车不系安全带在遇紧急制动或发生碰撞时可能会发生哪些危险？', '', '3', '4', NULL, '撞击风窗玻璃', '减少人员伤亡', '被甩出车外', '造成胸部损伤', 'ACD', '座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。因此，不系安全带在遇到紧急制动或发生碰撞时可能会发生撞击风窗玻璃、被甩出车外、造成胸部损伤等危险。', 9, 1, '2021-12-19 01:14:41', '2021-12-19 01:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4192, '安全头枕要调整到与颈部平齐的高度。', '', '2', '4', NULL, '', '', '', '', 'B', '座椅上头枕的主要作用是汽车被追尾时，有效保护驾乘人的颈椎。调整头枕高度时，保持头枕中心与后脑中心平齐，才能发挥保护作用。', 9, 1, '2021-12-19 01:14:41', '2021-12-19 01:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4193, '驾驶装有安全气囊的汽车可以不系安全带。', '', '2', '4', NULL, '', '', '', '', 'B', '安全气囊与安全带要配套使用，如果不系安全带，气囊引爆时会对驾乘人员头部和颈部造成严重伤害，这种伤害对儿童可能是致命的。', 9, 1, '2021-12-19 01:14:41', '2021-12-19 01:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4194, '驾驶汽车在雨天起步前要使用刮水器。', '/uploadFile/yuanBeiTu194.gif', '2', '4', NULL, '', '', '', '', 'A', '雨、雪天气驾驶汽车起步时，要开启近光灯、左转向灯，雨天要使用刮水器，以确保驾驶人视线清晰。', 9, 1, '2021-12-19 01:14:41', '2021-12-19 01:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4195, '驾驶汽车在非禁鸣路段，遇复杂交通情况时可合理使用喇叭。', '', '2', '4', NULL, '', '', '', '', 'A', '非禁鸣路段，驾驶机动车驶近急弯、坡道顶端等影响安全视距的路段以及超车或者遇有紧急情况时应合理使用喇叭，减速慢行并鸣喇叭示意。', 9, 1, '2021-12-19 01:14:42', '2021-12-19 01:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4196, '在这种情况下从主路进入辅路怎样汇入车流？', '/uploadFile/yuanBeiTu196.gif', '1', '4', NULL, '注意观察减速慢行', '加速进入辅路行驶', '从红车后汇入车流', '从红车前汇入车流', 'A', '驾驶机动车从辅路汇入主路，从主路汇入辅路时，要提前减速，注意观察右侧车辆是否让行，确认安全后，逐渐汇入。', 9, 1, '2021-12-19 01:14:42', '2021-12-19 01:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4197, '驾驶汽车从支线道路怎样安全汇入主干道车流？', '', '3', '4', NULL, '提前开启左转向灯', '仔细观察主干道内情况', '确认安全后汇入车流', '加速直接汇入车流', 'ABC', '驾驶机动车从辅路汇入主路，要随时注意观察主路车流情况，提前开启左转向灯，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐汇入车流，不得加速转向直接汇入。', 9, 1, '2021-12-19 01:14:42', '2021-12-19 01:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4198, '这种情况下怎样安全驾驶？', '/uploadFile/yuanBeiTu198.gif', '3', '4', NULL, '提前减速行驶', '观察交汇处车辆', '提前加速通过', '谨慎驾驶通过', 'ABD', '驾驶机动车汇入车流时，要减速慢行，通过后视镜随时注意观察两侧道路情况，提前开启转向灯，保持直线行驶，在不影响其他车辆通行的前提下，谨慎驾驶逐渐驶入正常行驶道路。', 9, 1, '2021-12-19 01:14:42', '2021-12-19 01:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4199, '在道路上行车时，安全跟车距离无需随着速度变化而变化。', '', '2', '4', NULL, '', '', '', '', 'B', '安全跟车距离需要随着速度变化而变化，车速越高，紧急制动距离越大，跟车的安全距离要随之增大，这样才能保证安全。因此，题干表述错误。', 9, 1, '2021-12-19 01:14:43', '2021-12-19 01:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4200, '在道路上怎样安全跟车行驶？', '', '3', '4', NULL, '注意观察前车动态', '随时做好减速准备', '尽量靠路左侧行驶', '保持安全距离', 'ABD', '跟车行驶，注意观察前车动态，不能将注视点固定在前车上，要随时观察前方两到三辆车的动态。随时做好减速准备，同时必须与前车保持安全的跟车距离，这是避免发生追尾、剐碰等事故的前提。', 9, 1, '2021-12-19 01:14:43', '2021-12-19 01:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4201, '行车中变更车道不需要提前开启转向灯。', '', '2', '4', NULL, '', '', '', '', 'B', '变更车道一定要提前开启转向灯。', 9, 1, '2021-12-19 01:14:43', '2021-12-19 01:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4202, '在前方交叉路口直行时，要提前在虚线区按导向箭头指示向右变更车道。', '/uploadFile/yuanBeiTu202.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在有导向箭头的路口直行时，要提前按导向箭头指示行驶。如图所示，右侧车道是直行和右转弯车道，因此应提前在虚线区按导向箭头指示向右变更车道。', 9, 1, '2021-12-19 01:14:44', '2021-12-19 01:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4203, '驾驶汽车怎样向左安全变更车道？', '', '3', '4', NULL, '观察左侧道路情况', '打开左转向灯', '不得影响正常通行车辆', '迅速向左变道', 'ABC', '安全变更车道操作程序：1、变更车道前，应通过内、外后视镜观察与判断，观察车辆后方、侧方和准备变更车道上的交通情况。2、确认安全后，开启转向灯示意，在不妨碍车道内车辆正常行驶的情况下，逐渐（不是迅速）将车辆变更到所需车道，然后关闭转向灯。', 9, 1, '2021-12-19 01:14:44', '2021-12-19 01:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4204, '驾驶汽车频繁变更车道有哪些危害？', '', '3', '4', NULL, '扰乱交通秩序', '易导致爆胎', '影响正常通行', '易引发交通事故', 'ACD', '【频繁变更车道的危害】变更车道时，不观察车辆两侧和后方道路交通情况，不开启转向灯，随意频繁变更车道或强行突然变道，连续侵占正常通行车辆的行驶路线会严重扰乱道路通行秩序，影响其他车辆的正常通行，容易导致道路拥堵和剐蹭、碰撞事故的发生。', 9, 1, '2021-12-19 01:14:44', '2021-12-19 01:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4205, '下列关于驾驶汽车突然变道加塞说法正确的是什么？', '', '3', '4', NULL, '缓解交通拥堵', '易引发交通事故', '提高通行效率', '造成道路拥堵', 'BD', '驾驶机动车突然变道加塞，会扰乱道路交通秩序，阻碍同方向车辆的正常行驶，造成道路拥堵，甚至导致交通事故。', 9, 1, '2021-12-19 01:14:44', '2021-12-19 01:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4206, '在这种情况下怎样安全会车？', '/uploadFile/yuanBeiTu206.gif', '3', '4', NULL, '加速绕过障碍物', '向左占道行驶', '停车让对方先行', '提前减速让行', 'CD', '驾驶机动车在有障碍物的地点会车，要注意观察对面情况，遇对面来车已临近障碍物时，及时停车让对向来车优先通行，会车后再超越障碍。', 9, 1, '2021-12-19 01:14:45', '2021-12-19 01:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4207, '在这种情况下怎样安全会车？', '/uploadFile/yuanBeiTu207.gif', '3', '4', NULL, '加速缩短会车时间', '减速靠右行驶', '保持安全间距', '注意避让非机动车', 'BCD', '如图所示，在没有中心隔离设施或中心线的道路上会车时，应注意减速靠右行驶，与对方车辆保持横向安全间距，同时注意避让前方非机动车。', 9, 1, '2021-12-19 01:14:45', '2021-12-19 01:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4208, '在这种没有中心线的弯道上怎样安全会车？', '/uploadFile/yuanBeiTu208.gif', '3', '4', NULL, '紧靠道路中心', '紧靠道路右侧', '保持安全间距', '降低车速行驶', 'BCD', '在如图所示没有中心线的弯道上会车时，应主动减速靠右行驶，并与对向来车保持较大的安全距离，不得加速或靠道路中心行驶，防止发生剐碰事故。', 9, 1, '2021-12-19 01:14:46', '2021-12-19 01:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4209, '驾驶汽车遇雨、雪、雾等视线不清或路面较滑时怎样安全会车？', '', '3', '4', NULL, '降低车速行驶', '加大横向间距', '应当加速行驶', '必要时停车避让', 'ABD', '雨雪雾等视线不清或路面较滑，对驾驶人的判断和操作产生不利的影响，会车时应降低车速行驶，同时要保持足够的安全距离低速通过，必要时停车避让，不得抢行或加速绕行。', 9, 1, '2021-12-19 01:14:46', '2021-12-19 01:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4210, '如图所示，行驶过程中遇前方有障碍物的情况怎么办？', '/uploadFile/yuanBeiTu210.gif', '1', '4', NULL, '减速靠右行驶', '抢在绿车前绕过障碍', '开启左转向灯', '借对向车道绕过障碍', 'A', '如图所示，车道前方遇障碍物，应变更到右侧车道通过，但此时右前方有车，因此减速，在绿车后靠右行驶即可。', 9, 1, '2021-12-19 01:14:47', '2021-12-19 01:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4211, '驾驶汽车行车中怎样选择超车路段？', '', '3', '4', NULL, '视线良好', '对面无来车', '路面无障碍物', '道路宽直', 'ABCD', '驾驶汽车行车中【视线良好】【对面无来车】【路面无障碍物】【道路宽直】选择路段超车，保障安全行驶。不过真实环境中，开车时也要先观察路况，确保安全的情况下再超车。', 9, 1, '2021-12-19 01:14:47', '2021-12-19 01:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4212, '驾驶人下车前要观察后视镜和侧头观察左后侧情况。', '', '2', '4', NULL, '', '', '', '', 'A', '下车前，要观察前方，通过内外后视镜并侧头观察左后方交通情况，确认无行人、非机动车和其他车辆临近。', 9, 1, '2021-12-19 01:14:47', '2021-12-19 01:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4213, '在道路上停车时要尽量避开坡道、积水、结冰或松软路面。', '', '2', '4', NULL, '', '', '', '', 'A', '在道路上停车时，应选择允许停车的安全地段，尽量避开坡道、积水、结冰或松软路面，防止路面垮塌，发生危险。', 9, 1, '2021-12-19 01:14:47', '2021-12-19 01:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4214, '这辆小型汽车驾驶人错在哪里？', '/uploadFile/yuanBeiTu214.gif', '1', '4', NULL, '没有开启转向灯', '没有鸣喇叭警示', '未观察左后方情况', '驾驶人没有错误', 'C', '这辆小型汽车驾驶人错在没有观察左后方情况。 停车后，应先观察侧后方和左侧交通情况，确认安全后，再缓开车门，以免开车门时妨碍其他车辆及行人通行。', 9, 1, '2021-12-19 01:14:48', '2021-12-19 01:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4215, '驾驶人在下车前应注意什么？', '', '3', '4', NULL, '车门开的幅度不要过大', '开门下车动作要迅速', '仔细观察左后方情况', '开车门的动作要缓慢', 'ACD', '驾驶人在下车前，先观察侧后方和左侧交通情况，确认安全后，再缓开车门，车门开的幅度不要过大，以免开车门时妨碍其他车辆及行人通行', 9, 1, '2021-12-19 01:14:48', '2021-12-19 01:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4216, '驾驶汽车在道路上临时停车怎样选择停车路段和地点？', '', '3', '4', NULL, '路面平坦坚实', '可以随意停放', '无禁止停车标志', '不妨碍交通', 'ACD', '停车要选择路面平坦坚实，不妨碍交通又无禁止停车标志标线的路段或地点，不得在设有禁止停车标志标线的路段停车。', 9, 1, '2021-12-19 01:14:48', '2021-12-19 01:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4217, '遇到这种情况的骑车人可以借对向车道超越。', '/uploadFile/yuanBeiTu217.gif', '2', '4', NULL, '', '', '', '', 'B', '不能超。因为：1、道路交通安全法规定，行经弯道路段不得超车；2、路中心黄实线，禁止越线。', 9, 1, '2021-12-19 01:14:48', '2021-12-19 01:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4218, '驾驶汽车通过连续弯道时，尽量靠弯道右侧行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '山区弯道较多，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”；通过连续急弯路时，要注意靠右侧减速慢行，不要占对向车道。', 9, 1, '2021-12-19 01:14:49', '2021-12-19 01:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4219, '怎样安全通过这种较窄的弯道？', '/uploadFile/yuanBeiTu219.gif', '3', '4', NULL, '沿道路右侧行驶', '挂低速档减速通过', '沿道路左侧行驶', '挂高速档加速通过', 'AB', '驾驶机动车遇到上陡坡的弯道时，要在坡底减速减挡，保持不换挡能一气爬到坡顶的动力；进入弯道前，适当减速，靠右行驶，并鸣喇叭示意。路面较窄时，还要时刻注意观察对向交通情况，做好随时停车的准备。', 9, 1, '2021-12-19 01:14:49', '2021-12-19 01:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4220, '驾驶汽车在道路急转弯处怎样行驶？', '', '3', '4', NULL, '减速靠路右侧行驶', '不能占用对方车道', '注意对面来车', '鸣喇叭示意', 'ABCD', '山区弯道较多，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”，通过连续急弯路时，要注意靠右侧减速慢行，不要占对向车道。', 9, 1, '2021-12-19 01:14:49', '2021-12-19 01:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4221, '驾驶汽车在转弯路段易引发事故的驾驶行为有哪些？', '', '3', '4', NULL, '占道行驶', '急转转向盘', '弯道前不减速', '靠路右侧行驶', 'ABC', '驾驶机动车在转弯路段行驶时，要注意观察，按照导向箭头的指示减速靠右行驶，不得急转转向盘、加速或占道行驶，以免发生交通事故。', 9, 1, '2021-12-19 01:14:50', '2021-12-19 01:14:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4222, '在这种路口遇到行人突然横穿怎么办？', '/uploadFile/yuanBeiTu222.gif', '1', '4', NULL, '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '向右变道绕过行人', 'A', '机动车进出道路，遇行人横穿道路时，应当及时减速或停车避让。', 9, 1, '2021-12-19 01:14:51', '2021-12-19 01:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4223, '如图所示，遇有这种情况要停车让行。', '/uploadFile/yuanBeiTu223.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，驾驶机动车看到人行横道前有停止的车辆时，一定要停车，不要盲目通过，更不得加速通过，前车可能是停车避让行人。不要在人行横道及其附近直行超车和变向超车，尤其要提防有些行动缓慢的人可能还滞留在人行横道上。', 9, 1, '2021-12-19 01:14:51', '2021-12-19 01:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4224, '如图所示，接近人行横道线时怎样安全行驶？', '/uploadFile/yuanBeiTu224.gif', '3', '4', NULL, '提前减速观察', '注意避让行人', '随时准备停车', '抢先加速通过', 'ABC', '驾驶机动车接近人行横线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人。如图所示，遇非机动车通过人行横道时，应及时停车让行，不得抢行或绕行。', 9, 1, '2021-12-19 01:14:51', '2021-12-19 01:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4225, '驾驶汽车通过人行横道时要注意下列哪些情况？', '', '3', '4', NULL, '突然横穿的儿童', '急速通过的自行车', '缓慢通过的行人', '准备横过的行人', 'ABCD', '驾驶汽车通过人行横道时要注意【突然横穿的儿童】【急速通过的自行车】【缓慢通过的行人】【准备横过的行人】避免交通事故。', 9, 1, '2021-12-19 01:14:51', '2021-12-19 01:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4226, '通过居民小区时需要注意什么？', '', '3', '4', NULL, '遵守标志', '低速行驶', '不鸣喇叭', '避让居民', 'ABCD', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。', 9, 1, '2021-12-19 01:14:52', '2021-12-19 01:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4227, '在这种情况下要避让左侧从公交车后横穿的行人。', '/uploadFile/yuanBeiTu227.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过公交站时，要提前减速慢行，与公交车保持较大的安全间距，预防车站上下车的乘客从车前或车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车避让的准备。图中可见左侧公交车后有行人正在横穿道路，应及时避让，注意安全。', 9, 1, '2021-12-19 01:14:52', '2021-12-19 01:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4228, '驾驶汽车驶近停有公交车的车站需要注意什么？', '', '3', '4', NULL, '做好随时停车的准备', '预防公交车突然起步', '预防行人从车前穿出', '与公交车保持安全间距', 'ABCD', '驾驶机动车驶近停有公交车的车站时，要减速慢行，与公交车保持较大的安全间距，预防公交车突然起步或车站上下车的乘客从车前、车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。', 9, 1, '2021-12-19 01:14:52', '2021-12-19 01:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4229, '遇到这样的路口，以下哪种做法是正确的？', '/uploadFile/yuanBeiTu229.gif', '1', '4', NULL, '快速闪烁前照灯，提醒前方车辆快速驶离路口', '紧急制动保证车辆能够在停车线前停止', '降低车速确认安全后通过', '以上行为都是正确的', 'C', '通过设有黄色闪光警告信号灯控制的路口时，应减速慢行，注意观察，确认安全后通过。', 9, 1, '2021-12-19 01:14:53', '2021-12-19 01:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4230, '在这种大雨中，跟车行驶时使用近光灯的目的是？', '/uploadFile/yuanBeiTu230.gif', '1', '4', NULL, '不干扰前车视线，有利自己看清道路', '提醒前方车辆让行', '提醒前方车辆减速', '以上说法都正确', 'A', '雨天影响行车安全的主要因素是视线受阻和路面湿滑。跟车行驶时，要加大与前车的安全距离；跟车行驶使用近光灯的目的是不干扰前车视线，同时有利于自己看清道路，降低事故发生的风险。', 9, 1, '2021-12-19 01:14:53', '2021-12-19 01:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4231, '夜间驾驶汽车通过十字交叉路口交替使用远近光灯的目的是什么？', '', '1', '4', NULL, '使其他交通参与者更容易发现自己', '更容易看清路面情况', '提醒其他车辆我在让行', '以上说法都不对', 'A', '夜间通过交叉路口时，应在距离路口150米外，变换远近光灯提示来往的车辆和行人，使其他交通参与者更容易发现自己，并低速通过。', 9, 1, '2021-12-19 01:14:53', '2021-12-19 01:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4232, '驾驶机动车遇到沙尘、冰雹、雾、雨、雪等低能见度条件时，应该怎样做？', '', '1', '4', NULL, '开启前照灯、示廓灯和后位灯', '高频率鸣喇叭使其他交通参与者知道自己的位置', '同向跟车较近时，应使用远光灯', '适当提高车速，尽快到达目的地，结束行车', 'A', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯。但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 9, 1, '2021-12-19 01:14:54', '2021-12-19 01:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4233, '水温表是用来指示哪个部件的温度？', '', '1', '4', NULL, '行驶系', '转向系', '发动机', '变速器', 'C', '水温表指的是冷却液的温度，全车只有发动机在发热，冷却液就是用来给发动机降温的。所以水温表指示的是发动机的温度。', 9, 1, '2021-12-19 01:14:54', '2021-12-19 01:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4234, '如果轮胎胎侧顺线出现裂口，以下做法正确的是什么？', '', '1', '4', NULL, '放气减压', '及时换胎', '给轮胎充气', '不用更换', 'B', '轮胎胎面磨损到小三角标志或轮胎花纹里的标记线或达到保质期或胎侧顺线出现裂口时，必须更换新胎。', 9, 1, '2021-12-19 01:14:54', '2021-12-19 01:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4235, '检查机动车机油时，以下做法正确是什么？', '', '1', '4', NULL, '停在平坦的地方，在启动前检查', '停在平坦的地方，在怠速状态下检查', '无需停在平坦的地方，在启动前检查', '无需停在平坦的地方，在怠速状态下检查', 'A', '检查汽车机油时确保车辆停在平坦的路面上，发动机启动前，拔出机油尺查看，机油高度要在上限和下限之间，低于下限时，按照车辆使用说明书规定的牌号添加。当发动机启动后，油底壳中的机油受到曲柄连杆机构的搅动，四处飞溅，不但测不出准确的油平面，还会影响机油仓的压力，有拉缸、烧瓦、机器报废等的危险。', 9, 1, '2021-12-19 01:14:54', '2021-12-19 01:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4236, '出车前检查刮水器时，应尽量在干燥状态下进行。', '', '2', '4', NULL, '', '', '', '', 'B', '刮水器是在雨天或下雪天行驶时使用，可清除风窗玻璃上的雨雪，保证驾驶人有良好的视线。因此，应该在有水的情况下检查，否则无法看出刮水器是否可以有效贴合彻底刮去水分。', 9, 1, '2021-12-19 01:14:55', '2021-12-19 01:14:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4237, '轮胎气压过高或过低都容易导致爆胎。', '', '2', '4', NULL, '', '', '', '', 'A', '胎压过高或过低都容易爆胎：胎压过高，使轮胎与地面的接触面积减少，单位面积所承受的压力、磨损剧增，容易造成刹车失控；如果胎压过低，使轮胎与地面接触面积增加，行驶时胎内温度不正常增加，同时，由于胎侧变形严重，内部的钢丝、帘布层老化加剧，从而为爆胎埋下隐患，并且使油耗呈增加，轮胎寿命降低。', 9, 1, '2021-12-19 01:14:56', '2021-12-19 01:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4238, '车辆在路边起步后，应随时注意车辆两侧道路情况，向左缓慢转向，逐渐驶入正常行驶的车道。', '', '2', '4', NULL, '', '', '', '', 'A', '机动车在路边起步后，要随时注意两侧道路情况，向左缓慢转向，在不影响其他车辆通行的前提下，逐渐驶入正常行驶道路。 【拓展延伸】 很多人认为本题没有提到打转向灯，因此是错误的。其实题目表达的意思是车辆在路边起步后，应包含随时注意车辆两侧道路情况，向左缓慢转向，逐渐驶入正常行驶的车道等行为，不是只有这些行为。所以题目虽然没有说打左灯，但并不代表车辆起步不要打左灯。题目描述没有问题，注意审题。', 9, 1, '2021-12-19 01:14:56', '2021-12-19 01:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4239, '机动车涉水后，制动器的制动效果不会改变。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车涉水驾驶后，水的浮力和流水的冲击，会使车辆驱动力的发挥受到限制，电器设备也极易受潮短路，影响制动效能。', 9, 1, '2021-12-19 01:14:56', '2021-12-19 01:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4240, '夜间行车时，全车灯光突然熄灭，应当立即迅速制动，靠边停车。', '', '2', '4', NULL, '', '', '', '', 'B', '夜间行车全车灯光突然熄灭正确做法： ①夜间行车时全车灯光突然熄灭，应当开启危险报警闪光灯，慢慢减速，靠边停车。 ②危险报警闪光灯不受其他设备影响，可单独工作的。 立即迅速制动做法错误，迅速制动会导致后车反应时间短，容易造成追尾事故。', 9, 1, '2021-12-19 01:14:57', '2021-12-19 01:14:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4241, '出车前检查的目的是什么？', '', '3', '4', NULL, '确认机动车车胎是否损毁', '确认周围是否有障碍物', '确认在车辆附近是否存在安全隐患', '确认出车方向的安全性', 'ABCD', '出车前检查的目的：1、确认汽车的车胎是否有故障；2、确认汽车的周围有没有障碍物，是否阻挡汽车的正常发车运行；3、保障行车的安全，确认汽车是否有安全隐患；4、确认出车方向的安全性。', 9, 1, '2021-12-19 01:14:57', '2021-12-19 01:14:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4242, '汽车各轮胎气压不一致时，容易造成的后果是什么？', '', '3', '4', NULL, '爆胎', '汽车行驶油耗增大', '操纵失控', '加剧轮胎磨损', 'ABCD', '轮胎具有承担汽车重量、缓冲汽车振动和保证与地面之间的附着力等作用。轮胎的气压及磨损情况关系这车辆行驶的安全性，各轮胎气压不一致时，汽车行驶油耗增大、操作失控并加剧轮胎磨损，严重时引起爆胎。', 9, 1, '2021-12-19 01:14:57', '2021-12-19 01:14:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4243, '驾驶机动车起步前，驾驶人对乘车人需要提出什么要求？', '', '3', '4', NULL, '系好安全带', '调整好后视镜', '不要把身体伸出车外', '不要向车外抛洒物品', 'ACD', '驾驶机动车起步前，驾驶人应根据身高调整好座椅、头枕、后视镜，系好安全带，并提醒车内乘员系好安全带，不要把身体伸出车外，不要向车外抛洒物品。 注意：对乘车人的要求指的是，上车后乘车人需要做什么，而调整后视镜是驾驶人需要做的。', 9, 1, '2021-12-19 01:14:57', '2021-12-19 01:14:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4244, '出车前，应该做的准备工作是什么？', '', '3', '4', NULL, '仔细巡视车辆四周的状况，观察车底和车身周围是否有障碍物', '上车后关好车门，调整好座位，系好安全带', '启动车辆，观察仪表，检查车辆工作是否正常', '调整好后视镜', 'ABCD', '出车前，应进行全面的安全检查。 上车前：要养成上车前绕车查看车辆外观及车周围、彻底安全情况的习惯。从左前门处开始，逆时针方向绕车一周，检查车辆外观、轮胎、号牌；观察有无液体渗漏现象；确认汽车底部及周围有无障碍物等影响安全起步的隐患后，方可上车。 上车后：关好车门，调整座位，系好安全带，调整后视镜；起动车辆，观察仪表，检查车辆工作是否正常。', 9, 1, '2021-12-19 01:14:57', '2021-12-19 01:14:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4245, '如图所示，起步时此灯亮起表示驻车制动(手刹)放下。', '/uploadFile/yuanBeiTu245.gif', '2', '4', NULL, '', '', '', '', 'B', '起步时，仪表盘上如图所示指示灯亮，表示驻车制动（手刹）处于制动状态，即手刹拉起未放下。', 9, 1, '2021-12-19 01:20:56', '2021-12-19 01:20:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4246, '机动车从高速公路加速车道汇入行车道车流时，以下做法正确的是什么？', '', '1', '4', NULL, '从正常行驶车辆后驶入行车道', '从正常行驶车辆前驶入行车道', '停车等待正常行驶车辆通过', '加速直接驶入行车道', 'A', '从加速车道驶入行车道时，要注意观察行车道内的情况，在车辆稀少时从正常行驶车辆后方驶入行车道；遇正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道，不得迅速从中间插入。', 9, 1, '2021-12-19 01:20:56', '2021-12-19 01:20:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4247, '驾驶机动车应当怎样汇入主路车流？', '', '1', '4', NULL, '加速直接汇入车流', '开启转向灯观察主路情况确保安全汇入车流', '开启转向灯直接汇入车流', '不用开启转向灯加速汇入车流', 'B', '驾驶机动车汇入主路车流时，应开启转向灯，随时注意主路两侧道路情况缓慢转向，在不影响其他车辆通行的前提下，逐渐汇入主路车流。', 9, 1, '2021-12-19 01:20:56', '2021-12-19 01:20:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4248, '关于驾驶机动车汇入主路车流，以下说法正确的是什么？', '', '1', '4', NULL, '不得妨碍主路车辆正常行驶', '只要不发生事故可随意行驶', '可以碾压实线及导流线', '在不发生事故的前提下干扰主路车流也是可以的', 'A', '驾驶机动车汇入主路车辆，应开启转向灯，随时注意主路两侧道路情况缓慢转向，在不影响其他车辆通行的前提下，逐渐汇入主路。', 9, 1, '2021-12-19 01:20:57', '2021-12-19 01:20:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4249, '如图所示、在同向3车道高速公路上行驶，车速低于每小时80公里的车辆应在哪条行车道上行驶？', '/uploadFile/yuanBeiTu249.gif', '1', '4', NULL, '最左侧行车道', '中间行车道', '最右侧行车道', '任意行车道', 'C', '如图所示，根据限速标志标明的限速区间，从左到右依次为：100-120km/h；80-100km/h；60-80km/h；因此，车速低于80km/h的车辆应在最右侧行车道行驶。 【热知识】高速公路限速标志标明的车速与本车道行驶车速的规定不一致时，按照限速标志标明的车速行驶。', 9, 1, '2021-12-19 01:20:57', '2021-12-19 01:20:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4250, '驶入高速公路减速车道后，应关闭转向灯，注意观察限速标志，进入匝道之前将车速降到多少以下？', '', '1', '4', NULL, '标志规定车速', '每小时80公里', '每小时60公里', '每小时40公里', 'A', '驾驶机动车驶离高速公路，进入减速车道后关闭右转向灯，注意观察车速表（降到规定速度以下），逐渐减速后进入匝道。注意：1、车速不得超过限速标志标明的速度；2、不得未经减速车道直接驶入匝道。', 9, 1, '2021-12-19 01:20:57', '2021-12-19 01:20:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4251, '如图所示，若车后50m范围内无其他车辆，可以不打转向灯变更车道。', '/uploadFile/yuanBeiTu251.gif', '2', '4', NULL, '', '', '', '', 'B', '不管车后有没有其他车辆，在任何时候变道都需要打转向灯的。', 9, 1, '2021-12-19 01:20:58', '2021-12-19 01:20:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4252, '驾驶机动车驶入高速公路加速车道后，应迅速将车速提高到每小时100公里以上。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车从匝道驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。 《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 9, 1, '2021-12-19 01:20:58', '2021-12-19 01:20:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4253, '驾驶机动车在高速公路加速车道提速到每小时60公里以上时，可直接驶入行车道。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车从匝道驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。', 9, 1, '2021-12-19 01:20:58', '2021-12-19 01:20:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4254, '如图所示，驾驶机动车遇这种情况应如何安全汇入车流？', '/uploadFile/yuanBeiTu254.gif', '3', '4', NULL, '加速直接汇入车流', '认真观察主路车流情况', '提前开启转向灯并降低车速', '不得妨碍主路正常行驶车辆', 'BCD', '驾驶机动车从辅路汇入主路时，应提前开启转向灯并降低车速，认真观察主路车流情况，在不影响主路车辆通行的前提下逐渐安全汇入。不要加速直接转向汇入车流。', 9, 1, '2021-12-19 01:20:58', '2021-12-19 01:20:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4255, '在山区道路行驶时，以下说法正确的是什么？', '', '1', '4', NULL, '上坡路段的安全距离应比平坦路段的大', '下坡路段的安全距离应比平坦路段的小', '急弯路段应当紧随前车', '以上说法都正确', 'A', '上坡路段跟车行驶时，与前车保持的安全距离应比平坦路段大，以防前车因动力不足或熄火时后溜而躲避不及时，发生追尾事故。', 9, 1, '2021-12-19 01:20:59', '2021-12-19 01:20:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4256, '前方遇有大型拉土(石)货车，应当尽量远离，避让。', '', '2', '4', NULL, '', '', '', '', 'A', '遇有大型拉土(石)货车，为了防止车上的土石掉落砸坏车辆，要避让这些大型车辆。', 9, 1, '2021-12-19 01:20:59', '2021-12-19 01:20:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4257, '多车跟车行驶，为避免追尾事故发生应至少观察前方两到三辆车，从而能对减速或停车具有预见性。', '', '2', '4', NULL, '', '', '', '', 'A', '多车跟车行驶，不能将注视点固定在前车上，要随时观察前方两到三辆车的动态，控制好与前车的安全距离，这是避免发生追尾、剐碰等事故的前提。', 9, 1, '2021-12-19 01:20:59', '2021-12-19 01:20:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4258, '雪天行车，车轮的附着力大大减小，跟车距离不是主要的，只需要保持低速行驶便可以防止事故发生。', '', '2', '4', NULL, '', '', '', '', 'B', '跟车行驶时，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，控制好与前车的安全距离，是避免发生追尾、剐碰等事故的前提，尤其冰雪路面易滑，制动距离比平时长，更应当保持足够的跟车距离。', 9, 1, '2021-12-19 01:20:59', '2021-12-19 01:20:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4259, '雪天行车，由于路面湿滑，车轮附着力减小，因此应当加大两车之间的安全距离。', '', '2', '4', NULL, '', '', '', '', 'A', '雪天行车，由于道路结冰，路面的摩擦力会减小，轮胎附着力减小，制动距离会延长。所以需要更大的安全距离制动。', 9, 1, '2021-12-19 01:21:00', '2021-12-19 01:21:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4260, '跟车行驶时，要留有足够的安全距离，是因为什么？', '', '3', '4', NULL, '遇到紧急情况时，能有足够的避让空间', '跟车越近，越不容易掌握前车前方的情况', '防止因前车尾灯损坏，不能及时发现前车制动', '跟车太近，容易发生追尾', 'ABCD', '跟车行驶，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，不能将注视点固定在前车上，要随时观察前方两到三辆车的动态，控制好与前车的安全距离，这是避免发生追尾、剐碰等事故的前提。', 9, 1, '2021-12-19 01:21:00', '2021-12-19 01:21:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4261, '关于影响制动停车距离的因素，以下说法正确的是什么？', '', '3', '4', NULL, '车辆行驶速度', '驾驶人的反应时间', '路面状况', '载货量的多少以及制动器的结构形式等', 'ABCD', '影响停车距离的主要因素有驾驶人反应时间、车速及制动系统的性能等。其中，车速是最主要的影响因素，车速控制的好坏关系着行车是否安全。另外，驾驶人疲劳、雨雪雾等恶劣天气及车辆载质量等因素，都会使停车距离加长。', 9, 1, '2021-12-19 01:21:00', '2021-12-19 01:21:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4262, '可以选择下坡路段超车。', '', '2', '4', NULL, '', '', '', '', 'B', '根据规定：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的路段不能超车。', 9, 1, '2021-12-19 01:21:01', '2021-12-19 01:21:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4263, '如图所示，在这种情况下可以借用快速车道超车。', '/uploadFile/yuanBeiTu263.gif', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全实施条例》第四十四条 在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。慢速车道内的机动车超越前车时，可以借用快速车道行驶。', 9, 1, '2021-12-19 01:21:01', '2021-12-19 01:21:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4264, '如图所示，当与对向车辆有会车可能时，不得超车。', '/uploadFile/yuanBeiTu264.gif', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 9, 1, '2021-12-19 01:21:01', '2021-12-19 01:21:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4265, '关于超车，以下说法正确的是什么？', '', '3', '4', NULL, '提前开启左转向灯', '夜间交替使用远近光灯', '鸣喇叭提示', '加速从右侧超越', 'ABC', '《道路交通安全法实施条例》第四十七条机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', 9, 1, '2021-12-19 01:21:02', '2021-12-19 01:21:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4266, '关于超车，以下说法正确的是什么？', '', '3', '4', NULL, '超车时从前车左侧超越', '超车时从前车右侧超越', '超车完毕，立即开启右转向灯驶回原车道', '超车完毕，与被超车拉开必要的安全距离后开启右转向灯驶回原车道', 'AD', '机动车超车时要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，开启左转向灯，从前车的左侧超越；超越完毕，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。 （选项C错在立即二字，应该开启右转向灯，但不应该立即驶回原车道，应该确认与被超车辆有一定的安全距离后再驶回）', 9, 1, '2021-12-19 01:21:02', '2021-12-19 01:21:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4267, '如图所示，行车中遇到这种情况应当如何安全会车？', '/uploadFile/yuanBeiTu267.gif', '1', '4', NULL, '鸣喇叭，加速通过', '减速靠右，让其先行', '靠道路左侧停靠让其先行', '抢在对方前先行通过', 'B', '行车中看到窄桥标志时，说明前方桥面变窄且宽度不超过6米，此时要减速慢行，谨慎驾驶。遇对向来车时，离桥较远的一方主动让行；遇对向来车已在桥面行驶时，未上桥的一方要注意选择安全地点靠右侧让行。如图所示，对向来车已在桥面行驶，因此应减速靠右，让其先行。', 9, 1, '2021-12-19 01:21:02', '2021-12-19 01:21:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4268, '夜间会车时，如遇对方持续开启远光灯，应当如何安全会车？', '', '1', '4', NULL, '鸣喇叭，加速通过', '及时开启远光灯', '使用近光灯，低速会车或停车让行', '使用远光灯，低速会车', 'C', '夜间会车应当在距对方来车150米以外改用近光灯，若对方车辆不关闭远光灯，可连续变换灯光提示对向车辆。当遇对面来车仍不关闭远光灯时，应及时减速靠右侧行驶或停车让行。', 9, 1, '2021-12-19 01:21:03', '2021-12-19 01:21:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4269, '如图所示，驾驶机动车在会车过程中遇到这种情况，应当持续鸣喇叭并提高车速迫使其驶回车道。', '/uploadFile/yuanBeiTu269.gif', '2', '4', NULL, '', '', '', '', 'B', '会车时遇对向车辆加速超车时，要正确判断安全间距，减速或停车让行，安全避让对向来车；不可与其争道抢行，以防发生碰撞。', 9, 1, '2021-12-19 01:21:03', '2021-12-19 01:21:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4270, '当感觉与对向驶来的车辆会有会车困难的时候，应及时减速靠边行驶，或停车让行。', '', '2', '4', NULL, '', '', '', '', 'A', '当感觉与对向驶来的车辆会车有困难时，及时靠边减速或停车让行，不可争道抢行，以防发生碰撞。', 9, 1, '2021-12-19 01:21:03', '2021-12-19 01:21:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4271, '在狭窄的坡路会车，如遇下坡车不减速、不让行，应持续鸣喇叭迫使其停车让行。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在狭窄的坡路会车，遇下坡车不减速不让行时，应主动减速靠右行驶，不得持续鸣喇叭迫使其停车让行。', 9, 1, '2021-12-19 01:21:03', '2021-12-19 01:21:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4272, '如图所示，驾驶机动车在这样的狭窄路段会车，驾驶人应当减速靠右并保持横向安全距离。', '/uploadFile/yuanBeiTu272.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在如图所示的狭窄路段会车时，应主动减速靠右并保持横向安全距离，防止发生剐碰事故。', 9, 1, '2021-12-19 01:21:04', '2021-12-19 01:21:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4273, '机动车行经视线受阻的急弯路段时，如遇对方车辆鸣喇叭示意，也应当及时鸣喇叭进行回应。', '', '2', '4', NULL, '', '', '', '', 'A', '机动车行经视线受阻的急弯路段时，可多使用喇叭引起对向车辆注意。如遇对方车辆鸣喇叭示意，也应当及时鸣喇叭进行回应，以保证行车安全。', 9, 1, '2021-12-19 01:21:04', '2021-12-19 01:21:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4274, '如图所示，在这种情况下要充分减速靠右行驶。', '/uploadFile/yuanBeiTu274.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，急弯路遇对面来车时，应主动减速靠右行驶，不得加速或靠道路中心行驶，防止发生剐碰事故。', 9, 1, '2021-12-19 01:21:04', '2021-12-19 01:21:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4275, '夜间临时停车时，只要有路灯就可以不开危险报警闪光灯。', '', '2', '4', NULL, '', '', '', '', 'B', '在道路上临时停车时，不得妨碍其他机动车和行人通行。夜间或遇风、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯和危险报警闪光灯。', 9, 1, '2021-12-19 01:21:04', '2021-12-19 01:21:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4276, '在立交桥上可以临时停车。', '', '2', '4', NULL, '', '', '', '', 'B', '立交桥车多速度快，路况复杂，随便停车容易引发交通事故。', 9, 1, '2021-12-19 01:21:05', '2021-12-19 01:21:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4277, '隧道中可以临时停车休息一会儿，避免疲劳驾驶。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 9, 1, '2021-12-19 01:21:05', '2021-12-19 01:21:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4278, '如图所示，红色汽车在此地点停车等候是违法行为。', '/uploadFile/yuanBeiTu278.gif', '2', '4', NULL, '', '', '', '', 'A', '红色车右侧有禁止停车标志，而且红车所在位置是交叉路口，是绝对不能停车的，所以在此停车是违法行为。', 9, 1, '2021-12-19 01:21:05', '2021-12-19 01:21:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4279, '如图所示，只要没有警察在场就可以在此地点停车。', '/uploadFile/yuanBeiTu279.gif', '2', '4', NULL, '', '', '', '', 'B', '', 9, 1, '2021-12-19 01:21:06', '2021-12-19 01:21:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4280, '关于停车，以下说法正确的是什么？', '', '3', '4', NULL, '应靠道路右侧', '开关车门不得妨碍其他车辆和行人通行', '交叉路口50米以内不得停车', '开左转向灯', 'ABC', '停车要选择不妨碍交通又无禁止停车标志标线的路段或地点，靠道路右侧依次停放，停车后先观察侧后方和左侧交通情况，确认安全后，再缓开车门，以免开车门时妨碍其他车辆及行人通行，交叉路口50米以内路段不得停车，不得在设有禁止停车标志标线的路段停车。', 9, 1, '2021-12-19 01:21:06', '2021-12-19 01:21:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4281, '应该选择什么地点停车？', '', '3', '4', NULL, '停车场', '道路施划的停车泊位内', '人行横道', '施工路段', 'AB', '停车应该选择停车场、准许长时间停放车辆的地点或道路施划的停车泊位，在规定的位置内依次停放。车辆长期停放时，尽量停入车库。不准在车行道、人行道和设有禁止长时停放标志的地点停放。', 9, 1, '2021-12-19 01:21:06', '2021-12-19 01:21:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4282, '以下什么地点不能停车？', '', '3', '4', NULL, '人行横道', '停车场', '山区容易塌方、泥石流路段', '道路施划的停车泊位内', 'AC', '停车要选择不妨碍交通又无禁止停车标志标线的路段或地点，不得在设有禁止停车标志标线的路段停车。车辆停放时间较长时，选择停车场或准许长时间停放车辆的地点，在规定的位置内依次停放。不准在车行道、人行道和设有禁止长时停车标志的地点停放。', 9, 1, '2021-12-19 01:21:06', '2021-12-19 01:21:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4283, '夜间路边临时停车，以下做法错误的是什么？', '', '3', '4', NULL, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', 'AB', '夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用，在路边临时停车时，应开启危险报警闪光灯、示廓灯和后位灯。请注意，题干要求选出做法错误的选项。', 9, 1, '2021-12-19 01:21:07', '2021-12-19 01:21:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4284, '关于停车，以下做法错误的是什么？', '', '3', '4', NULL, '在交叉路口停车', '在铁路道口停车', '在山区易落石路段停车', '在停车场停车', 'ABC', '机动车停放应当遵守下列规定：（一）在停车场或者交通标志、标线指示的道路停车泊位等规定地点停放。（二）在道路停车泊位内停放时，车身不得超出停车泊位。', 9, 1, '2021-12-19 01:21:07', '2021-12-19 01:21:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4285, '停车时，以下做法正确的是什么？', '', '3', '4', NULL, '按顺行方向停放', '车身不得超出停车泊位', '关闭电路', '锁好车门', 'ABCD', '正确的停车操作顺序是：按顺行方向停放、车身不得超出停车泊位、关闭电路、锁好车门。', 9, 1, '2021-12-19 01:21:07', '2021-12-19 01:21:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4286, '在路口掉头时，应提前开启左转向灯进入导向车道，不得妨碍行人和其他车辆正常通行。', '', '2', '4', NULL, '', '', '', '', 'A', '在路口掉头时，应提前打开左转向灯，在不影响其他车辆正常行驶的情况下进入导向车道，确认安全后完成掉头。', 9, 1, '2021-12-19 01:21:07', '2021-12-19 01:21:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4287, '在路口掉头时，为了保证通畅，应加速迅速完成掉头。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车行驶过程中，需要掉头时，观察前、后方交通情况，确认安全后减速或停车，开启左转向灯，正确选择掉头地点和时机进行掉头。在没有掉头成功的情况下可以倒车调整，但不要妨碍其他车辆和行人的正常通行。', 9, 1, '2021-12-19 01:21:08', '2021-12-19 01:21:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4288, '在路口掉头时，可以不避让直行车辆。', '', '2', '4', NULL, '', '', '', '', 'B', '在路口掉头时，应严格控制车速，认真观察道路上的交通动态，必要时应停车进行观察，确认车辆前后无车辆或行人通过时，方可打开转向灯进行掉头。', 9, 1, '2021-12-19 01:21:08', '2021-12-19 01:21:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4289, '掉头时，以下做法正确的是什么？', '', '3', '4', NULL, '不开转向灯', '提前开启左转向灯', '在掉头车道掉头', '在直行车道掉头', 'BC', '《道路交通安全法实施条例》第五十七条机动车应当按照下列规定使用转向灯：(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；(二)向右转夸、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 9, 1, '2021-12-19 01:21:08', '2021-12-19 01:21:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4290, '如图所示，驾驶机动车在路口前遇黄灯亮时，应停车等待。', '/uploadFile/yuanBeiTu290.gif', '2', '4', NULL, '', '', '', '', 'A', '黄灯亮时，已越过停止线的车辆可以继续通行；其它没有越过停止线的车辆需要停车等待。', 9, 1, '2021-12-19 01:21:09', '2021-12-19 01:21:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4291, '如图所示，造成这起事故的主要原因是机动车未按规定避让行人。', '/uploadFile/yuanBeiTu291.gif', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第四章道路通行规定 第二节机动车通行规定 ：第四十七条　机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。 如图所示，机动车遇行人正在通过人行横道时，应及时停车礼让，不得抢行。因此，造成这起事故的主要原因是机动车未按规定避让行人。', 9, 1, '2021-12-19 01:21:09', '2021-12-19 01:21:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4292, '机动车行经没有交通信号的道路，遇行人横过道路时，以下做法错误的是什么？', '', '3', '4', NULL, '减速或停车避让', '鸣喇叭催促', '寻找间隙穿插驶过', '绕前通过', 'BCD', '驾驶机动车接近人行横道线时，提前减速观察，注意观察人行横道左右两侧是否有行人通行，随时准备停车礼让行人。遇行人横过道路时，应及时减速或停车让行，不得鸣喇叭催促、抢行或绕行。 请注意，题干要求选出做法错误的选项，因此，鸣喇叭催促、穿插行驶和绕行通过做法是错误的。', 9, 1, '2021-12-19 01:21:09', '2021-12-19 01:21:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4293, '如图所示，驾驶机动车看到路边有这种标志时，表示前方接近学校区域，因此要提前减速注意观察。', '/uploadFile/yuanBeiTu293.gif', '2', '4', NULL, '', '', '', '', 'A', '图中标志为注意儿童标志：但题干描述的是这个标志的含义，用以警告车辆驾驶人减速慢行，注意儿童。此标志设在小学、幼儿园、少年宫、儿童游乐场等儿童频繁出入的场所或通道处。题干中描述“前方接近学校区域，因此要提前减速注意观察”是没有问题的，题目答案为对。', 9, 1, '2021-12-19 01:21:10', '2021-12-19 01:21:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4294, '驾驶机动车通过学校门口时应注意什么？', '', '3', '4', NULL, '注意观察标志标线', '注意减速慢行', '不要鸣喇叭', '快速通过', 'ABC', '驾驶机动车通过学校区域，提前减速至30公里/小时以下，观察左右方交通情况，注意道路两侧学生及家长的动态，与路侧行走的学生保持足够的安全距离，文明礼让，确保安全通过，遇有学生横过道路时应停车让行。学校区域禁止鸣笛。', 9, 1, '2021-12-19 01:21:10', '2021-12-19 01:21:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4295, '如图所示，驾驶机动车通过小区遇到这种情况，应减速行驶，随时准备停车。', '/uploadFile/yuanBeiTu295.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，道路两旁车辆较多，而且路上有球，很可能马上就有小孩子跑过来捡球。所以要提前减速，随时准备停车。', 9, 1, '2021-12-19 01:21:10', '2021-12-19 01:21:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4296, '如图所示，驾驶机动车在居民小区遇到这种情形要连续鸣喇叭，示意行人让路。', '/uploadFile/yuanBeiTu296.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。', 9, 1, '2021-12-19 01:21:10', '2021-12-19 01:21:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4297, '如图所示，驾驶机动车进入该居民小区，车速不能超过5km/h。', '/uploadFile/yuanBeiTu297.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。图中右侧禁令标志表示限速5km/h，即车速不得超过5km/h。', 9, 1, '2021-12-19 01:21:11', '2021-12-19 01:21:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4298, '如图所示，在居民区内为了预防突发情况出现，驾驶人应如何安全驾驶？', '/uploadFile/yuanBeiTu298.gif', '3', '4', NULL, '注意观察，随时准备停车', '进入小区前应降低车速', '不与行人抢行', '鸣喇叭示意行人让行', 'ABC', '通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或驾驶抢行。', 9, 1, '2021-12-19 01:21:11', '2021-12-19 01:21:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4299, '如图所示，驾驶机动车在居民区遇到这种情形，应如何安全驾驶？', '/uploadFile/yuanBeiTu299.gif', '3', '4', NULL, '紧跟其后行驶', '低速慢行', '连续鸣喇叭示意', '保持必要的安全距离', 'BD', '通过居民小区，要遵守限速标志的规定，低速慢行，不得连续鸣喇叭警示或加速抢行，随时注意观察两侧情况，注意避让行人，遇两侧有行人或行人占道行走时，要与行人保持安全距离低速行驶，待行人让路后通过。', 9, 1, '2021-12-19 01:21:11', '2021-12-19 01:21:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4300, '如图所示，在这种情况下要注意右侧的非机动车。', '/uploadFile/yuanBeiTu300.gif', '2', '4', NULL, '', '', '', '', 'A', '右前方有骑自行车的人，非机动车有很大的随意性，如果路边大巴车突然启动，骑自行车的人会转向，需要非常注意。', 9, 1, '2021-12-19 01:21:11', '2021-12-19 01:21:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4301, '如图所示，驾驶机动车在公交车站遇到这种情况要迅速向左变更车道绕行。', '/uploadFile/yuanBeiTu301.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过公交车站时，要减速慢行，与公交车保持较大的安全间距，预防车站上下车的乘客，同时应特别注意公交车前后是否有行人横穿道路，做好随时停车的准备，不可迅速绕行通过。', 9, 1, '2021-12-19 01:21:22', '2021-12-19 01:21:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4302, '如图所示，在这种情况下可以在公交车站临时停车。', '/uploadFile/yuanBeiTu302.gif', '2', '4', NULL, '', '', '', '', 'B', '通过公交车站，要提前减速行驶，注意观察车站内候车人的动态，不得占用公交专用车道，距离公交站30米内的路段不能停车。因此，题干表述错误。', 9, 1, '2021-12-19 01:21:23', '2021-12-19 01:21:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4303, '如图所示，驾驶机动车临近停在车站的公交车时，以下做法正确的是？', '/uploadFile/yuanBeiTu303.gif', '3', '4', NULL, '降低车速', '随时准备停车', '尽快超越', '加大横向安全距离', 'ABD', '驾驶机动车驶近停有公交车的车站时，要减速慢行，与公交车保持较大的安全间距，预防公交车突然起步或车站上下车的乘客从车前、车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。', 9, 1, '2021-12-19 01:21:23', '2021-12-19 01:21:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4304, '如图所示，驾驶机动车遇到这种情形，应如何安全通过？', '/uploadFile/yuanBeiTu304.gif', '3', '4', NULL, '减速慢行', '注意观察', '拉开横向安全距离', '预防突然横穿的行人', 'ABCD', '驾驶机动车通过公交车站，超越停在公交车站内的车辆时，要减速慢行，与公交车保持较大的安全间距，注意观察车站内候车人的动态，预防车站上下车的乘客从车前或车后横穿道路，同时注意对向公交车前后是否有行人横穿道路，做好随时停车的准备。', 9, 1, '2021-12-19 01:21:23', '2021-12-19 01:21:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4305, '如图所示，驾驶机动车行驶至此路段时，应当减速靠右侧行驶。', '/uploadFile/yuanBeiTu305.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，驾驶机动车行驶至过弯路段遇对面来车靠路中心行驶时，应充分减速靠右行驶，不得加速或也靠道路中心行驶，以免发生剐碰事故。', 9, 1, '2021-12-19 01:21:24', '2021-12-19 01:21:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4306, '如图所示，驾驶机动车行驶至此路段时，应当提前减速慢行，注意前方可能出现的行人及车辆。', '/uploadFile/yuanBeiTu306.gif', '2', '4', NULL, '', '', '', '', 'A', '前方是十字交叉路口，而且两旁有建筑物，视线受到一定的阻隔。所以要减速慢行，注意周围情况。', 9, 1, '2021-12-19 01:21:24', '2021-12-19 01:21:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4307, '如图所示，这种情况下，B车优先通行。', '/uploadFile/yuanBeiTu307.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在路口右转弯遇到红灯亮时，可减速靠右侧转弯通过，但不应影响被放行方向的其他车辆和行人通行。如图所示，B车右转弯红灯，A车直行绿灯属于正常被放行的车辆，因此A车应优先通行。', 9, 1, '2021-12-19 01:21:25', '2021-12-19 01:21:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4308, '如图所示，驾驶机动车驶出环岛时，应先驶入最右侧车道不用开启转向灯驶离即可。', '/uploadFile/yuanBeiTu308.gif', '2', '4', NULL, '', '', '', '', 'B', '驶出环岛，需提前开启右转向灯。驶近环岛时，注意观察左侧已在环岛内行驶车辆的动态，适时汇入车流，必要时减速或停车让行；驶出环岛前，打开右转向灯，注意观察右侧车辆、行人的动态。', 9, 1, '2021-12-19 01:21:25', '2021-12-19 01:21:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4309, '如图所示，驾驶机动车驶近这样的公交车站时，既要注意到路侧行人的活动情况随时准备减速避让，又要考虑前方道路可能存在拥堵，不应跟车过近，防止视线受阻。', '/uploadFile/yuanBeiTu309.gif', '2', '4', NULL, '', '', '', '', 'A', '题目的描述很大程度上做到了安全文明驾驶的要求，既考虑到了行人安全又考虑到了道路情况，因此是正确的。', 9, 1, '2021-12-19 01:21:25', '2021-12-19 01:21:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4310, '驾驶机动车驶入拥堵的环形路口，以下做法正确的是什么？', '', '1', '4', NULL, '注意避让已在路口内车辆', '优先驶入环形路口', '鸣喇叭示意其他车辆让行', '超越前方车辆进入路口', 'A', '环形路口通行原则：准备进入环形路口的机动车，让已在路口内的机动车先行。因此，驾驶机动车驶入拥堵的环形路口时，应当减速或停车，依次缓慢行驶或耐心排队等待，注意避让已在路口内的车辆。', 9, 1, '2021-12-19 01:21:25', '2021-12-19 01:21:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4311, '驾驶机动车驶离停车场进入主路时，驾驶人应当鸣喇叭示意主路车辆让行。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车驶离停车场进入主路时，驾驶人应当提前开启转向灯，观察前后左右，确认安全后驶入主路。', 9, 1, '2021-12-19 01:21:26', '2021-12-19 01:21:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4312, '如图所示，驾驶机动车遇到前方车辆正在停车时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu312.gif', '1', '4', NULL, '提前减速并停车等待', '借对向车道超越前车', '鸣喇叭催促前车让路', '继续行驶，靠近前车', 'A', '驾驶机动车遇到前方车辆正在停车时，应提前减速并停车等待，不得越实线超车、鸣喇叭催促和靠近前车，以免发生碰撞事故。', 9, 1, '2021-12-19 01:21:26', '2021-12-19 01:21:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4313, '如图所示，驾驶机动车跟车行驶遇到出租车正在接送乘客时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu313.gif', '1', '4', NULL, '停车等待', '从对向车道加速超越', '连续鸣喇叭催促', '从非机动车道通过', 'A', '1、道路两边都是实线，都不能跨线行驶。 2、连续鸣喇叭属于不文明驾驶行为。 因此在图中这样的情况下，驾驶机动车跟车行驶遇到出租车正在接送乘客时，应停车等待。', 9, 1, '2021-12-19 01:21:26', '2021-12-19 01:21:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4314, '驾驶小型汽车跟随装满货物的大货车行驶时，应当注意以下哪些方面？', '', '3', '4', NULL, '大货车制动距离相对较长', '大货车可能抛洒货物', '大货车盲区较大', '大货车遮挡小型机动车视线', 'ABCD', '跟随装满货物的大货车行驶时要注意信号灯的变化，还应注意货物掉落的危险；同时大货车会导致视线不佳，会挡住路口的信号灯或路边交通标志；大货车制动距离相对较长，应加大跟车距离，避免大货车紧急制动时与其追尾；大货车盲区较大。', 9, 1, '2021-12-19 01:21:27', '2021-12-19 01:21:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4315, '驾驶机动车在向左变更车道前，通过左后视镜看到图中情形时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu315.gif', '1', '4', NULL, '开启左转向灯后直接变更车道', '在确认左侧无其他车辆后，变更车道', '开启左转向灯稍向左行驶，后车让行后再变更车道', '开启左转向灯，让后方车辆通过后变更车道', 'D', '驾驶机动车在向左变更车道前，应提前打开左转向灯，然后观察后方来车的反应，后车速度降低，驶入；后车速度不降低，不能驶入。如图所示，通过左后视镜看到的后方来车变大，说明后车没有减速，不想让本车进入，因此，本车应减速让其先行通过后再变更车道。', 9, 1, '2021-12-19 01:21:27', '2021-12-19 01:21:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4316, '如图所示，机动车遇到这种情况，A车应当主动减速让行的原因是什么？', '/uploadFile/yuanBeiTu316.gif', '1', '4', NULL, '靠近山体一侧的车危险性更高', '靠近山体一侧的车更加容易减速', '临崖一侧的车危险性更高', '临崖一侧的车更容易通过', 'C', '山区危险路段会车时，应减速或停车让不靠山体的一方先行。主要原因为：不靠山体（临崖）的一侧缺少山体的保护，危险性更高。因为，靠山体的一方车让一让，最坏的情况是碰擦山体；而不靠山体的车让一让，则会有掉落悬崖，车毁人亡的危险。', 9, 1, '2021-12-19 01:21:27', '2021-12-19 01:21:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4317, '如图所示，驾驶机动车遇到这种主路左侧来车的情况，以下说法正确的是什么？', '/uploadFile/yuanBeiTu317.gif', '1', '4', NULL, '左侧来车应该给己车让行', '己车应该给左侧来车让行', '不需让行，谁车速快谁先过', '不需让行，己车有优先通行权', 'B', '《道路交通安全法实施条例》规定，通过无交通信号灯的交叉路口，转弯的机动车让直行的车辆先行。如图所示，左侧来车直行，己车转弯汇入主路，因此己车应该给左侧来车让行。', 9, 1, '2021-12-19 01:21:28', '2021-12-19 01:21:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4318, '驾驶机动车行驶到路口绿灯亮时，拥有优先通行权，可以不给行人或非机动车让行。', '', '2', '4', NULL, '', '', '', '', 'B', '路口绿灯亮时，只代表可以通行，但并不意味着路口处安全无风险，应注意避让行人和非机动车，随时做好停车的准备，即使有优先通行权，也不能忽视对面来车抢先左转弯或左右车道车辆抢行带来的危险。', 9, 1, '2021-12-19 01:21:28', '2021-12-19 01:21:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4319, '如图所示，驾驶机动车遇到这种情况，应及时降低车速，遇交通堵塞时，可以鸣喇叭。', '/uploadFile/yuanBeiTu319.gif', '2', '4', NULL, '', '', '', '', 'B', '图中有限速5km/h的标志，所以在此处应该把车速降低到5km/h之内。其次有禁止鸣笛标志，是不可以鸣喇叭的。', 9, 1, '2021-12-19 01:21:28', '2021-12-19 01:21:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4320, '驾驶机动车行驶过程中，驾驶人要随时注意与前车的安全距离，安全距离应随着车速的提高而增加。', '', '2', '4', NULL, '', '', '', '', 'A', '车速是影响安全距离的最主要因素，车速控制的好坏关系这行车是否安全，速度越快，紧急制动距离越大。因此，驾驶人要随时注意与前车的安全距离，安全距离应随着车速的提高而增加。', 9, 1, '2021-12-19 01:21:28', '2021-12-19 01:21:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4321, '如图所示，驾驶机动车发现前车向后溜车，以下做法正确的是什么？', '/uploadFile/yuanBeiTu321.gif', '1', '4', NULL, '迅速向右方倒车躲避', '停车鸣喇叭提示', '迅速向左方倒车躲避', '直接倒车躲避', 'B', '上坡路上，在道路狭窄、后方有跟车的情况下发现前车溜车，应及时停车并鸣喇叭提示。B项符合要求，因此答案选B。 【拓展延伸】 通过观察图片可知，前方汽车溜车，左侧有护栏，右侧有石墙，后方还有跟车，向右方倒车躲避、向左方倒车躲避、直接倒车躲避都是不现实的，排除A、C、D项。在这种情况下，立即停车鸣喇叭提示，既能防止与前车追尾，刹车灯亮起后，后车也会注意到前方有意外情况，鸣喇叭也能提示前车。', 9, 1, '2021-12-19 01:21:29', '2021-12-19 01:21:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4322, '如图所示，在这种无信号灯控制情况下，A车、B车、C车的通行权顺序是什么？', '/uploadFile/yuanBeiTu322.gif', '1', '4', NULL, 'B车、A车、C车', 'C车、A车、B车', 'A车、B车、C车', 'A车、C车、B车', 'C', '没有交通标志、标线控制的交叉路口，转弯的机动车让直行的车辆先行；相对方向行驶的右转弯的机动车让左转弯的车辆先行。如图所示，A车直行，B车左转弯，C车右转弯，因此，ABC车通行权顺序是：A车、B车、C车。', 9, 1, '2021-12-19 01:21:29', '2021-12-19 01:21:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4323, '如图所示，驾驶机动车遇到这种情况，可以借对向车道超越前车。', '/uploadFile/yuanBeiTu323.gif', '2', '4', NULL, '', '', '', '', 'B', '不能超。因为：1、行经弯道路段不得超车；2、路中心黄色实线，禁止越线。', 9, 1, '2021-12-19 01:21:30', '2021-12-19 01:21:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4324, '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？', '/uploadFile/yuanBeiTu324.gif', '3', '4', NULL, '左前方行人可能在前方机动车驶过后马上横穿道路', '左前方行人对是否横穿马路可能犹豫不决，无法准确判断', '前方机动车可能遇有其他横穿道路的行人减速或紧急停车', '前方机动车可能躲避横穿道路的行人，突然变更车道', 'ABCD', '前方有车辆且有行人欲横穿马路，驾驶机动车遇到这种情况时，驾驶人应注意：左前方行人可能在前方机动车驶过后马上横穿道路；左前方行人对是否横穿马路可能犹豫不决，无法准确判断；前方机动车可能遇有其他横穿道路的行人减速或紧急停车；前方机动车可能躲避横穿道路的行人，突然变更车道。', 9, 1, '2021-12-19 01:21:30', '2021-12-19 01:21:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4325, '如图所示，驾驶机动车遇到这种情况时，驾驶人应注意的是什么？', '/uploadFile/yuanBeiTu325.gif', '3', '4', NULL, '道路左侧儿童可能突然跑进路中', '前方行人可能未察觉有机动车驶近', '迎面来车可能造成会车困难', '右侧停放的机动车可能会突然起步', 'ABCD', '图中左侧有儿童，前方有来向车辆和行人，右侧有停放车辆，驾驶机动车遇到这种情况时，驾驶人应注意：道路左侧儿童可能突然跑进路中，前方行人可能未察觉有机动车驶近，迎面来车可能造成会车困难，右侧停放的机动车可能会突然起步。', 9, 1, '2021-12-19 01:21:30', '2021-12-19 01:21:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4326, '如图所示，驾驶机动车驶近公交车站时，驾驶人应注意的是什么？', '/uploadFile/yuanBeiTu326.gif', '3', '4', NULL, '下车的乘客可能从公交车前方横穿道路', '公交车可能即将启动并向左变更车道', '右侧摩托车可能驶入机动车道并穿插变更车道', '对向车道内的机动车可能违法跨越道路中心线超车', 'ABCD', '驾驶机动车驶近公交车站时，要减速慢行，注意观察车站捏候车人的动态；与公交车保持较大的安全间距，预防车站上下的乘客从车前或车后横穿道路；公交车可能启动向左变更车道；同时注意对向车道内的机动车可能违法跨越道路中心线超车；注意右侧摩托车可能驶入机动车道并穿插变更车道。', 9, 1, '2021-12-19 01:21:31', '2021-12-19 01:21:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4327, '驾驶机动车驶出小区上道路行驶，以下做法正确的是什么？', '', '1', '4', NULL, '无需观察直接汇入主路车流', '无需避让主路车辆', '在不妨碍主路车辆正常行驶的前提下汇入车流', '鸣喇叭示意主路车避让', 'C', '驾驶机动车驶出小区上道路行驶时，应开启转向灯，随时注意两侧道路情况缓慢转向，在不影响其他车辆通行的前提下，逐渐汇入主路车流。', 9, 1, '2021-12-19 01:21:31', '2021-12-19 01:21:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4328, '如图所示，驾驶机动车驶离停车场进主路时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu328.gif', '1', '4', NULL, '加速汇入主路车流', '可以不避让主路车辆', '无需观察，鸣喇叭示意后汇入车流', '减速慢行，在不妨碍主路车辆行驶的前提下汇入车流', 'D', '驾驶机动车驶离停车场进主路时，应注意观察，减速慢行，在不妨碍主路车辆行驶的前提下，逐渐汇入主路车流。', 9, 1, '2021-12-19 01:21:31', '2021-12-19 01:21:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4329, '如图所示，驾驶机动车跟车行驶遇到前方大货车行驶缓慢时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu329.gif', '1', '4', NULL, '连续鸣喇叭示意其让道', '加大安全车距，适时超车', '加速行驶，伺机超车', '紧跟前方大货车', 'B', '如图所示，在路中心是黄色虚线且对向无来车的路段跟车行驶时，遇前方大货车行驶缓慢，可加大跟车距离，适时超越大货车行驶。', 9, 1, '2021-12-19 01:21:31', '2021-12-19 01:21:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4330, '驾驶机动车在上坡道路跟车行驶，遇到前车停车时，为防止前车起步时溜车，应适当加大安全距离。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在上坡道路跟车行驶，要加大与前车的安全距离，仔细观察前车动态，以防前车突然停车或停车后溜。', 9, 1, '2021-12-19 01:21:32', '2021-12-19 01:21:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4331, '以下跟车情况中，应当注意的情形有哪些？', '', '3', '4', NULL, '跟随出租汽车行驶时，要预防其随时可能靠边停车上下乘客', '当前方汽车贴有实习标志时，应该增大跟车距离，预防前车紧急制动', '前方为装满货物的大货车时，应增大跟车距离并避免长时间跟随，以预防货物抛洒和车后盲区带来的危险', '雾天跟车行驶，注意前车紧急制动', 'ABCD', '为了保证行车安全，上道路行车中选对跟车对象十分重要，要时刻关注前车的速度和行车轨迹。 1、前方为装满货物的大货车时，应增大跟车距离并避免长时间跟随，以预防货物抛洒和车后盲区带来的危险，同时自己的车也不易被他人发现 2、跟随出租汽车行驶时，要预防其随时可能靠边停车上下乘客 3、雾天跟车行驶，视线受阻，注意前车紧急制动 4、实习期驾驶人开车上路对路况、车况和操作等方面不熟悉，容易引发事故，当前方汽车贴有实习标志时，应该增大跟车距离，预防前车紧急制动。 综上，选项全部正确。', 9, 1, '2021-12-19 01:21:32', '2021-12-19 01:21:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4332, '驾驶机动车发现后车开启左转向灯发出超车信号时，以下做法正确的是什么？', '', '1', '4', NULL, '在有让超车条件，保证安全的情况下，减速靠右让路', '加速行驶，使他不能超越', '开启危险报警闪光灯，暗示他不要超越', '向左行驶，阻止他超越', 'A', '驾驶机动车发现后车开启左转向灯发出超车信号时，若具备让车条件，应及时开启右转向灯，减速靠右让行，必要时辅以手势示意让超，不得故意不让或让路不让速。', 9, 1, '2021-12-19 01:21:33', '2021-12-19 01:21:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4333, '驾驶机动车遇到前方车辆停车，等待行人通过人行横道时，以下做法正确的是什么？', '', '1', '4', NULL, '从左侧超越前车', '鸣喇叭催促前车向前行驶', '从右侧超越前车', '与前车保持安全距离，排队等待', 'D', '驾驶机动车遇到前方车辆停车，等待行人通过人行横道时，应与前车保持安全距离，排队等待，不得鸣喇叭催促和超车。', 9, 1, '2021-12-19 01:21:33', '2021-12-19 01:21:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4334, '如图所示，驾驶机动车遇到这种情况，B车做法正确的是什么？', '/uploadFile/yuanBeiTu334.gif', '1', '4', NULL, '适当鸣喇叭加速通过', '长鸣喇叭催促', '挤靠自行车', '减速让行', 'D', '虽然自行车占用机动车道，是错误的，但是为了避免惊扰自行车造成事故，应该礼让优先，减速让行。', 9, 1, '2021-12-19 01:21:33', '2021-12-19 01:21:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4335, '驾驶机动车与前车距离过近时，容易发生追尾事故。', '', '2', '4', NULL, '', '', '', '', 'A', '控制好与前车的安全距离，是避免发生追尾、剐碰等事故的前提。跟车距离太近时，前方紧急制动时，后车没有反应时间和反应距离，就很容易造成追尾。', 9, 1, '2021-12-19 01:21:33', '2021-12-19 01:21:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4336, '如遇交通流量较大的路段，跟车距离不重要，只需要保持低速行驶即可。', '', '2', '4', NULL, '', '', '', '', 'B', '跟车行驶时，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离，在交通流量较大的路段，不但要控制车速，更应当保持安全距离行车。', 9, 1, '2021-12-19 01:21:34', '2021-12-19 01:21:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4337, '如图所示，驾驶机动车遇到这种情况时，我方车辆享有优先通行权。', '/uploadFile/yuanBeiTu337.gif', '2', '4', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法实施条例》第八十四条：机动车通过施工作业路段时，应当注意警示标志，减速行驶。此时为左侧车道在施工，因此我车可先行。', 9, 1, '2021-12-19 01:21:34', '2021-12-19 01:21:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4338, '如图所示，在这种情况下，A车应该让路口内的B车先行。', '/uploadFile/yuanBeiTu338.gif', '2', '4', NULL, '', '', '', '', 'A', '环形路口通行原则：准备进入环形路口的让已在路口内的机动车先行。如图所示，A车准备进环岛，B车已在环岛内，因此A车让B车先行，题干表述正确。', 9, 1, '2021-12-19 01:21:34', '2021-12-19 01:21:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4339, '如图所示，D车的停放方式是正确的。', '/uploadFile/yuanBeiTu339.gif', '2', '4', NULL, '', '', '', '', 'B', 'D车此时位于两个停车位之间，停放方式错误，应按照停车位标线停车。', 9, 1, '2021-12-19 01:21:34', '2021-12-19 01:21:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4340, '出租车为了方便乘客上下车，可以在交叉路口临时停车。', '', '2', '4', NULL, '', '', '', '', 'B', '按照规定，交叉路口不允许临时停车。', 9, 1, '2021-12-19 01:21:35', '2021-12-19 01:21:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4341, '如图所示，图中车辆如何通行符合安全文明行车要求？', '/uploadFile/yuanBeiTu341.gif', '1', '4', NULL, '按照B车、C车、A车的顺序通行', '按照A车、B车、C车的顺序通行', '按照C车、A车、B车的顺序通行', '按照C车、B车、A车的顺序通行', 'D', '在无交通信号灯控制的交叉路口通行原则：1、机动车应礼让行人和非机动车优先通行；2，转弯让直行。如图所示，自行车C优先通行，A车让B车先行，因此图中车辆应按照C车、B车、A车的顺序通行。', 9, 1, '2021-12-19 01:21:35', '2021-12-19 01:21:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4342, '如图所示，驾驶机动车遇到这种情况时，应注意左前方行人可能在前方机动车驶过后马上横穿道路。', '/uploadFile/yuanBeiTu342.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，前方马路中间的行人，很有可能会在前车驶过之后横穿道路，因此驾驶人要提高警惕，注意减速避让，必要时停车让行。', 9, 1, '2021-12-19 01:21:35', '2021-12-19 01:21:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4343, '如图所示，驾驶机动车在这种情况下，可以直行也可以右转。', '/uploadFile/yuanBeiTu343.gif', '2', '4', NULL, '', '', '', '', 'B', '机动车要按箭头指示行车，图中很明显是右转车道，所以就只能右转。', 9, 1, '2021-12-19 01:21:36', '2021-12-19 01:21:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4344, '如图所示，驾驶机动车驶近这样的人行横道时，驾驶人应注意的是什么？', '/uploadFile/yuanBeiTu344.gif', '3', '4', NULL, '前方行人可能滞留在人行横道内', '左前方骑自行车者可能突然右转弯', '右前方骑摩托车者可能突然向左变更车道横穿道路', '右前方白色机动车行驶动态', 'ABCD', '驾驶机动车驶近这样的人行横道时，驾驶人应注意：前方行人可能滞留在人行横道内；左前方骑自行车者可能突然右转弯；右前方骑摩托车者可能突然向左变更车道横穿道路；右前方白色机动车行驶动态。', 9, 1, '2021-12-19 01:21:36', '2021-12-19 01:21:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4345, '如图所示，驾驶机动车看到这个标志时，应及时减速注意观察。', '/uploadFile/yuanBeiTu345.gif', '2', '4', NULL, '', '', '', '', 'A', '前方是注意儿童标志。这种标志一般设在学校、少年宫等地段，提示前方儿童较多，要减速慢行。', 9, 1, '2021-12-19 01:21:36', '2021-12-19 01:21:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4346, '如图所示，驾驶机动车遇到这种情况时，应减速或停车，待前方车辆通过后再通行。', '/uploadFile/yuanBeiTu346.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车汇入主路遇对向有来车时，应减速或停车，待前方车辆通过后再通行。', 9, 1, '2021-12-19 01:21:36', '2021-12-19 01:21:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4347, '驾驶机动车汇入车流时应当开启转向灯，认真观察主路上车辆的行驶情况，在不妨碍主路车辆正常行驶的前提下汇入车流。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车汇入车流时应开启转向灯，保持直线行驶，认真观察主路上车辆的行驶情况，在不妨碍主路车辆正常行驶的前提下汇入车流。', 9, 1, '2021-12-19 01:21:37', '2021-12-19 01:21:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4348, '关于驾驶机动车跟车行驶，以下做法正确的是什么？', '', '1', '4', NULL, '与前车保持足以采取紧急制动措施的安全距离', '与前车保持较近距离，以防加塞', '将注意力全部集中在所跟随的车辆上', '将注意力全部集中在后方的车辆上', 'A', '跟车行驶时，必须与前车保持安全的距离，即保证前车制动时，本车随之制动而不与前车相撞的停车距离。', 9, 1, '2021-12-19 01:21:37', '2021-12-19 01:21:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4349, '如图所示，驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu349.gif', '1', '4', NULL, '紧跟前车通过交叉口', '减速做好停车准备', '立即变更车道', '立即停车', 'B', '驾驶机动车跟车行驶遇到前车遮挡路口交通信号灯时，可以通过减速，加大跟车距离来扩大视野范围，预防跟随大型车通过时，信号灯突然变化；同时还可避免大型车紧急制动时与其追尾。', 9, 1, '2021-12-19 01:21:37', '2021-12-19 01:21:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4350, '驾驶机动车在坡道路段跟车行驶时，应保持比平路跟车时更大的安全距离。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在坡道路段跟车行驶时，要适当加大与前车的安全距离，仔细观察前车动态，在视线不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 9, 1, '2021-12-19 01:21:38', '2021-12-19 01:21:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4351, '驾驶机动车不得超越正在超车的车辆。', '', '2', '4', NULL, '', '', '', '', 'A', '前方车辆正在超车，速度较快；如果后方车辆继续超车的话，不仅两车会比较容易相撞，我方车辆也容易因为前车遮挡而观察不到对向或旁边车道车况而出事故。 （道路交通安全法明确规定，驾驶机动车不得超越正在超车的车辆）', 9, 1, '2021-12-19 01:21:38', '2021-12-19 01:21:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4352, '如图所示，驾驶机动车在这个路段想要掉头时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu352.gif', '1', '4', NULL, '鸣喇叭提示行人后掉头', '对面黄色车辆通过后掉头', '行人通过后掉头', '继续直行，寻找可掉头路段', 'D', '人行横道禁止掉头，在如图所示路段应继续直行，寻找可掉头路段。如图所示路口，既无允许掉头标志又无允许掉头的标线，因此这个路段禁止掉头，驾驶人应继续直行，寻找可掉头路段。', 9, 1, '2021-12-19 01:21:38', '2021-12-19 01:21:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4353, '如图所示，驾驶机动车遇到前方道路中间有停驶车辆时，以下可能出现的危险情形有哪些？', '/uploadFile/yuanBeiTu353.gif', '3', '4', NULL, '前车左侧车门可能突然打开', '前车前方可能有行人横穿马路', '前车可能突然掉头', '前车可能突然倒车', 'ABCD', '【跟车行驶的险情预判】跟车时必须与前车保持安全的距离，注意观察，合理控制车速。防止前车左侧车门突然打开；前车前方有行人横穿马路；前车突然倒车、掉头等情况的发生。', 9, 1, '2021-12-19 01:21:39', '2021-12-19 01:21:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4354, '如图所示，驾驶机动车遇弯道会车时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu354.gif', '1', '4', NULL, '加速通过', '占用对向车道', '靠边停车', '减速靠右通过', 'D', '山区会车时，应尽量选择路面较宽的路段，并主动减速、选择安全路段减速或停车交会。当对面来车速度较高或靠路中行驶时，应减速靠右行驶，不得加速也靠道路中心行驶，防止发生剐碰事故。', 9, 1, '2021-12-19 01:21:39', '2021-12-19 01:21:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4355, '如图所示，驾驶机动车遇到这种情况时，应该考虑到路边儿童可能会因为打闹而突然冲入路内。', '/uploadFile/yuanBeiTu355.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇到路边儿童时，要注意观察儿童的动态，发现异常要及时停车避让。', 9, 1, '2021-12-19 01:21:39', '2021-12-19 01:21:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4356, '机动车可以在人行横道上临时停放，但不得长时间停放。', '', '2', '4', NULL, '', '', '', '', 'B', '在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车。', 9, 1, '2021-12-19 01:21:39', '2021-12-19 01:21:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4357, '社会车辆可以在出租车停车位临时停车。', '', '2', '4', NULL, '', '', '', '', 'B', '出租车专用待客停车位，只允许出租车停车待客，社会车辆不得在出租车停车位停车，长期或者临时都不行。', 9, 1, '2021-12-19 01:21:40', '2021-12-19 01:21:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4358, '如图所示，驾驶机动车遇到左侧支路白色汽车不减速让行时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu358.gif', '1', '4', NULL, '加速，并超越白色汽车通过路口', '减速，注意避让', '持续鸣喇叭，并保持原有车速', '用车灯闪烁，示意对方让行', 'B', '驾驶机动车遇到左侧支路白色汽车不减速让行时，应当减速注意避让，不得加速抢行或连续鸣喇叭警示，避免发生碰撞事故。', 9, 1, '2021-12-19 01:21:40', '2021-12-19 01:21:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4359, '如图所示，驾驶机动车驶近这样的路口时，应保持视线移动，时刻关注着周围可能出现的潜在危险。', '/uploadFile/yuanBeiTu359.gif', '2', '4', NULL, '', '', '', '', 'A', '前方路口，车辆和行人较多，驾驶机动车驶近这样的路口时，应保持视线移动，时刻关注着周围可能出现的潜在危险。', 9, 1, '2021-12-19 01:21:40', '2021-12-19 01:21:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4360, '如图所示，驾驶机动车通过这样的路口时，应注意行人、非机动车，提前减速随时准备停车避让。', '/uploadFile/yuanBeiTu360.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，驾驶机动车通过没有信号灯控制的复杂路口时，应仔细观察左右两侧道路交通情况，提前减速或停车瞭望，做到“一看，二慢，三通过”，注意避让正在通行的行人、非机动车和其他车辆，随时做好停车让行地点准备。', 9, 1, '2021-12-19 01:21:40', '2021-12-19 01:21:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4361, '如图所示，驾驶机动车行经该路段时，以下说法错误的是什么？', '/uploadFile/yuanBeiTu361.gif', '1', '4', NULL, '注意儿童', '禁止停车', '禁止鸣喇叭', '前方禁止通行', 'D', '请注意，题干要求选出说法错误的选项。如图所示，右侧交通标志牌从上到下依次是：注意儿童、禁止停车、禁止鸣喇叭。没有禁止通行标志，因此，选项“前方禁止通行”说法错误。', 9, 1, '2021-12-19 01:21:41', '2021-12-19 01:21:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4362, '驾驶机动车行经学校门前遇到放学时段，为了保证道路的车流通畅，应勤鸣喇叭督促学生让开主车道。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在学校区域，遇到上学或放学时段，随时准备避让横过道路的学生和儿童，遇学或儿童横过道路时，应及时停车礼让，不得鸣喇叭催促，不可从他们中间穿行或从两侧绕行通过，避免发生事故。', 9, 1, '2021-12-19 01:21:42', '2021-12-19 01:21:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4363, '驾驶机动车驶入居民小区时，为了警告出入口处车辆及行人应连续鸣喇叭。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭警示或加速强行。', 9, 1, '2021-12-19 01:21:42', '2021-12-19 01:21:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4364, '临时停车，要注意什么？', '', '3', '4', NULL, '紧靠道路右侧', '开关车门不得妨碍其他车辆和行人通行', '交叉路口50米以内不得停车', '开左转向灯', 'ABC', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定： (一)在设有禁停标志、标线的路段，在机动车道与非机动车道、人行道之间设有隔离设施的路段以及人行横道、施工地段，不得停车； (二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车； (三)公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车； (四)车辆停稳前不得开车门和上下人员，开关车门不得妨碍其他车辆和行人通行； (五)路边停车应当紧靠道路右侧，机动车驾驶人不得离车，上下人员或者装卸物品后，立即驶离； (六)城市公共汽车不得在站点以外的路段停车上下乘客。', 9, 1, '2021-12-19 01:21:43', '2021-12-19 01:21:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4365, '在路口掉头时，只要不妨碍行人通行可以在人行横道完成掉头。', '', '2', '4', NULL, '', '', '', '', 'B', '人行横道禁止掉头。虽然设有交通隔离护栏路段的人行横道不设禁止掉头交通标志，但规定严禁在人行横道掉头，这主要是为了保护人行横道上行人的安全。因为行人具有优先通行权，人行横道就视同“禁止性标志”。', 9, 1, '2021-12-19 01:21:43', '2021-12-19 01:21:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4366, '如图所示，驾驶机动车直行通过路口，遇对向车辆左转时，让已在路口内的左转车辆优先通过路口。', '/uploadFile/yuanBeiTu366.gif', '2', '4', NULL, '', '', '', '', 'A', '准备进入路口让已在路口内的先行。如图所示，驾驶机动车直行通过路口，遇对向车辆左转时，应让已在路口内的左转车辆优先通过。', 9, 1, '2021-12-19 01:21:43', '2021-12-19 01:21:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4367, '停车时，以下做法不正确是？', '', '3', '4', NULL, '在交叉路口停车', '在铁道路口停车', '在停车泊位内停车', '在停车场内停车', 'AB', '停车要选择不妨碍交通又无禁止停车标志标线的路段或地点，不得在设有停止停车标志标线的路段停车。交叉路口、隧道、桥梁、立交桥上、公交车站、消防栓、铁路与公路交叉口及人行道上不能临时停车和停放车辆。', 9, 1, '2021-12-19 01:21:43', '2021-12-19 01:21:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4368, '遇到这种情况时怎样行驶？', '/uploadFile/yuanBeiTu368.gif', '1', '4', NULL, '禁止车辆在两侧车道通行', '减速进入两侧车道行驶', '进入右侧车道行驶', '加速进入两侧车道行驶', 'A', '两侧车道都是红灯，红灯表示不可通行，所以是禁止车辆在两侧车道通行。', 10, 1, '2021-12-19 01:21:44', '2021-12-19 01:21:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4369, '这个路口允许车辆怎样行驶？', '/uploadFile/yuanBeiTu369.gif', '1', '4', NULL, '向左、向右转弯', '直行或向左转弯', '向左转弯', '直行或向右转弯', 'D', '首先观察图中信号灯，左转信号灯是红色的，表示不能左转；直行和右转弯的信号灯是绿色的，表示可以向前和向右通行；其次看车道，车道箭头是向前和向右，所以可以直行或者右转弯。', 10, 1, '2021-12-19 01:21:44', '2021-12-19 01:21:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4370, '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '/uploadFile/yuanBeiTu370.gif', '1', '4', NULL, '尽快加速通过', '靠边停车等待', '注意瞭望安全通过', '禁止通行', 'C', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。', 10, 1, '2021-12-19 01:21:45', '2021-12-19 01:21:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4371, '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '/uploadFile/yuanBeiTu371.gif', '1', '4', NULL, '边观察边缓慢通过', '不换挡加速通过', '在火车到来前通过', '不得越过停止线', 'D', '驾驶机动车通过有交通信号控制的铁路道口，与红灯亮时，要将车停在停止线以外等待，等红灯熄灭时通行，不准抢行通过铁路道口。', 10, 1, '2021-12-19 01:21:45', '2021-12-19 01:21:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4372, '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '/uploadFile/yuanBeiTu372.gif', '2', '4', NULL, '', '', '', '', 'A', '根据规定，机动车行驶至有机动车信号灯（圆形）的路口，红灯亮起时，禁止车辆通行，车辆不得越过停止线，右转弯的车辆在不妨碍被放行的车辆、行人通行的情况下，可以通行。', 10, 1, '2021-12-19 01:21:45', '2021-12-19 01:21:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4373, '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '/uploadFile/yuanBeiTu373.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 10, 1, '2021-12-19 01:21:46', '2021-12-19 01:21:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4374, '这辆红色轿车可以在该车道行驶。', '/uploadFile/yuanBeiTu374.gif', '2', '4', NULL, '', '', '', '', 'B', '车辆行驶在设置了车道信号灯的道路上，必须选择绿色箭头灯亮的车道行驶。绿色箭头灯亮时，准许本车道车辆按指示方向通行；红色叉形灯或者红色箭头灯亮时，禁止本车道车辆通行。 如图所示：红色轿车行驶的车道，红色叉形灯亮，表示该车道禁止车辆通行。', 10, 1, '2021-12-19 01:21:46', '2021-12-19 01:21:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4375, '驾驶机动车在这种情况下不能左转弯。', '/uploadFile/yuanBeiTu375.gif', '2', '4', NULL, '', '', '', '', 'A', '图中路口是方向指示信号灯，用于指示某一方向上机动车通行。箭头方向向左、向上、向右分别表示左转、直行、右转。绿色箭头表示允许车辆沿箭头所指方向通行；红色箭头表示禁止车辆沿箭头所指方向通行；黄色箭头表示对箭头所指方向车辆起黄灯作用。 如图所示：左转红灯，因此驾驶机动车在当前路况下，不可以左转弯。', 10, 1, '2021-12-19 01:21:47', '2021-12-19 01:21:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4376, '驾驶机动车在这种情况下不能直行和左转弯。', '/uploadFile/yuanBeiTu376.gif', '2', '4', NULL, '', '', '', '', 'A', '看信号灯，左转弯和直行的信号灯是红色的，因此不能直行和左转弯。', 10, 1, '2021-12-19 01:21:47', '2021-12-19 01:21:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4377, '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '/uploadFile/yuanBeiTu377.gif', '2', '4', NULL, '', '', '', '', 'A', '道路与铁路平面交叉道口有两个红灯交替闪烁时，表示火车要通过了，此时禁止车辆、行人通行，车辆不得越过停止线。', 10, 1, '2021-12-19 01:21:48', '2021-12-19 01:21:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4378, '右侧标志警示前方是什么路口？', '/uploadFile/yuanBeiTu378.gif', '1', '4', NULL, 'T形交叉路口', 'Y形交叉路口', '十字交叉路口', '环形交叉路口', 'D', '交叉路口：用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', 10, 1, '2021-12-19 01:21:48', '2021-12-19 01:21:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4379, '右侧标志是何含义？', '/uploadFile/yuanBeiTu379.gif', '1', '4', NULL, '不允许停放车辆', '允许临时停车', '允许停车上下客', '允许停车装卸货', 'A', '', 10, 1, '2021-12-19 01:22:00', '2021-12-19 01:22:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4380, '右侧标志是何含义？', '/uploadFile/yuanBeiTu380.gif', '1', '4', NULL, '允许长时停放车辆', '可以临时停车', '允许长时停车等客', '不允许停放车辆', 'B', '', 10, 1, '2021-12-19 01:22:01', '2021-12-19 01:22:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4381, '立体交叉处这个标志提示什么？', '/uploadFile/yuanBeiTu381.gif', '1', '4', NULL, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', 'B', '立体交叉直行和左转弯行驶：前方立体交叉口，一切车辆可以直行和按图示路线左转弯行驶。', 10, 1, '2021-12-19 01:22:01', '2021-12-19 01:22:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4382, '立体交叉处这个标志提示什么？', '/uploadFile/yuanBeiTu382.gif', '1', '4', NULL, '向右转弯', '直行和左转弯', '直行和右转弯', '在桥下掉头', 'C', '立交直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', 10, 1, '2021-12-19 01:22:01', '2021-12-19 01:22:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4383, '右侧标志表示什么？', '/uploadFile/yuanBeiTu383.gif', '1', '4', NULL, '前方道路靠右侧行驶', '前方道路不允许直行', '前方是直行单行路', '前方注意右侧路口', 'C', '', 10, 1, '2021-12-19 01:22:01', '2021-12-19 01:22:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4384, '右侧标志提示哪种车型不能通行？', '/uploadFile/yuanBeiTu384.gif', '1', '4', NULL, '大型货车', '大型客车', '各种机动车', '小型客货车', 'C', '右侧标志表示禁止各类机动车驶入。此标志设在禁止机动车通行路段的入口处。', 10, 1, '2021-12-19 01:22:02', '2021-12-19 01:22:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4385, '右侧标志警告前方是向右急转弯路。', '/uploadFile/yuanBeiTu385.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:02', '2021-12-19 01:22:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4386, '右侧标志警示前方道路有连续三个或三个以上的弯路。', '/uploadFile/yuanBeiTu386.gif', '2', '4', NULL, '', '', '', '', 'B', '【一急二反三连续】一个弯表示急转弯，两个弯表示反向弯路，三个弯表示连续弯路。', 10, 1, '2021-12-19 01:22:03', '2021-12-19 01:22:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4387, '右侧标志警告前方道路是向左连续弯路。', '/uploadFile/yuanBeiTu387.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:03', '2021-12-19 01:22:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4388, '右侧标志警告前方是向右反向弯路。', '/uploadFile/yuanBeiTu388.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:03', '2021-12-19 01:22:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4389, '右侧标志警告前方是上陡坡路段。', '/uploadFile/yuanBeiTu389.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:04', '2021-12-19 01:22:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4390, '右侧标志警告前方是连续下坡路段。', '/uploadFile/yuanBeiTu390.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:04', '2021-12-19 01:22:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4391, '右侧标志警告前方是下陡坡路段。', '/uploadFile/yuanBeiTu391.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:04', '2021-12-19 01:22:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4392, '右侧标志警告前方路面两侧变窄长度为5公里。', '/uploadFile/yuanBeiTu392.gif', '2', '4', NULL, '', '', '', '', 'A', '图中为两侧变窄标志：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。加上下面的辅助标志长度为5km，警告前方路面两侧变窄的路段长度为5公里。', 10, 1, '2021-12-19 01:22:04', '2021-12-19 01:22:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4393, '右侧标志警告前方道路右侧变宽。', '/uploadFile/yuanBeiTu393.gif', '2', '4', NULL, '', '', '', '', 'B', '请注意右侧标志是警告前方道路右侧变窄，不是变宽。 窄路：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。', 10, 1, '2021-12-19 01:22:05', '2021-12-19 01:22:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4394, '右侧标志警告前方道路左侧变宽。', '/uploadFile/yuanBeiTu394.gif', '2', '4', NULL, '', '', '', '', 'B', '请注意，右侧黄底黑边三角形标志的警告标志是警告前方道路左侧变窄。因此，题干表述错误。', 10, 1, '2021-12-19 01:22:05', '2021-12-19 01:22:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4395, '右侧标志警告前方进入两侧变窄路段。', '/uploadFile/yuanBeiTu395.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:05', '2021-12-19 01:22:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4396, '右侧标志警告前方路段要注意儿童。', '/uploadFile/yuanBeiTu396.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:05', '2021-12-19 01:22:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4397, '右侧标志提醒前方是野生动物保护区。', '/uploadFile/yuanBeiTu397.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:06', '2021-12-19 01:22:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4398, '右侧标志提醒前方经常有牲畜横穿、出入。', '/uploadFile/yuanBeiTu398.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:07', '2021-12-19 01:22:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4399, '右侧标志警告前方路段要注意儿童。', '/uploadFile/yuanBeiTu399.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:07', '2021-12-19 01:22:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4400, '右侧标志警告前方路段设有信号灯。', '/uploadFile/yuanBeiTu400.gif', '2', '4', NULL, '', '', '', '', 'A', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 10, 1, '2021-12-19 01:22:08', '2021-12-19 01:22:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4401, '右侧标志提醒前方是左侧傍山险路。', '/uploadFile/yuanBeiTu401.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:08', '2021-12-19 01:22:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4402, '右侧这个标志提醒注意左侧有落石危险。', '/uploadFile/yuanBeiTu402.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:08', '2021-12-19 01:22:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4403, '右侧这个标志提醒前方山口注意横风。', '/uploadFile/yuanBeiTu403.gif', '2', '4', NULL, '', '', '', '', 'A', '警告标志是注意横风，用以提醒车辆驾驶人小心驾驶。设在经常有很强的侧向风的路段以前适当位置。', 10, 1, '2021-12-19 01:22:08', '2021-12-19 01:22:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4404, '右侧这个标志提示前方是连续急转弯道路。', '/uploadFile/yuanBeiTu404.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:09', '2021-12-19 01:22:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4405, '右侧这个标志提醒注意前方是傍山险路。', '/uploadFile/yuanBeiTu405.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:09', '2021-12-19 01:22:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4406, '右侧这个标志提醒注意前方200米是堤坝道路。', '/uploadFile/yuanBeiTu406.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志由警告标志和辅助标志组合而成，用以提醒驾驶人注意前方200米处是堤坝道路，应小心驾驶。', 10, 1, '2021-12-19 01:22:09', '2021-12-19 01:22:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4407, '右侧标志提醒前方200米有村庄。', '/uploadFile/yuanBeiTu407.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志由警告标志和辅助标志组合而成，提醒前方200米有村庄，应小心驾驶。', 10, 1, '2021-12-19 01:22:10', '2021-12-19 01:22:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4408, '右侧这个标志提醒前方是单向行驶隧道。', '/uploadFile/yuanBeiTu408.gif', '2', '4', NULL, '', '', '', '', 'B', '注意看题，题目问的是右侧标志，右侧的标志是隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', 10, 1, '2021-12-19 01:22:10', '2021-12-19 01:22:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4409, '右侧标志提醒前方路口向右100米是渡口。', '/uploadFile/yuanBeiTu409.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:10', '2021-12-19 01:22:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4410, '右侧标志提醒前方路面高突。', '/uploadFile/yuanBeiTu410.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:11', '2021-12-19 01:22:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4411, '右侧标志提醒前方路面不平。', '/uploadFile/yuanBeiTu411.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志提醒前方路面不平。', 10, 1, '2021-12-19 01:22:11', '2021-12-19 01:22:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4412, '右侧标志提醒注意前方是驼峰桥。', '/uploadFile/yuanBeiTu412.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:12', '2021-12-19 01:22:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4413, '右侧这个标志提醒前方路面低洼。', '/uploadFile/yuanBeiTu413.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:12', '2021-12-19 01:22:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4414, '右侧标志提醒前方是过水路面。', '/uploadFile/yuanBeiTu414.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:23', '2021-12-19 01:22:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4415, '右侧这个标志警告前方是无人看守的有多股铁路与道路相交铁路道口。', '/uploadFile/yuanBeiTu415.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:23', '2021-12-19 01:22:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4416, '右侧这个标志警告前方是有人看守铁路道口。', '/uploadFile/yuanBeiTu416.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:24', '2021-12-19 01:22:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4417, '这个标志警告前方铁路道口有多股铁路与道路相交。', '/uploadFile/yuanBeiTu417.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，有人看守铁路道口标志上方是叉形符号标志，表示多股铁路与道路相交，设在铁路道口标志上方。', 10, 1, '2021-12-19 01:22:24', '2021-12-19 01:22:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4418, '右侧标志警告距前方有人看守铁路道口100米。', '/uploadFile/yuanBeiTu418.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:24', '2021-12-19 01:22:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4419, '右侧标志警告距前方有人看守铁路道口150米。', '/uploadFile/yuanBeiTu419.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:25', '2021-12-19 01:22:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4420, '右侧标志警告前方150米是无人看守铁路道口。', '/uploadFile/yuanBeiTu420.gif', '2', '4', NULL, '', '', '', '', 'A', '栅栏表示有人看守，火车头是无人看守，一条红线代表50米，右侧标志有3条红线代表150米。因此，右侧标志警告前方150米是无人看守铁路道口。', 10, 1, '2021-12-19 01:22:25', '2021-12-19 01:22:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4421, '右侧标志提醒前方是非机动车道。', '/uploadFile/yuanBeiTu421.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:25', '2021-12-19 01:22:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4422, '右侧标志告知前方注意残疾人。', '/uploadFile/yuanBeiTu422.gif', '2', '4', NULL, '', '', '', '', 'A', '黄色三角形的都是警告标志，提醒驾驶人要注意。学校是辅助标志，也是提醒驾驶人注意。此题前方注意残疾人是对的。', 10, 1, '2021-12-19 01:22:26', '2021-12-19 01:22:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4423, '右侧标志提醒前方路段保持车距。', '/uploadFile/yuanBeiTu423.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:26', '2021-12-19 01:22:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4424, '图中标志提醒障碍物在路中，车辆从两侧绕行。', '/uploadFile/yuanBeiTu424.gif', '2', '4', NULL, '', '', '', '', 'A', '左右绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', 10, 1, '2021-12-19 01:22:26', '2021-12-19 01:22:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4425, '图中标志提醒障碍物在路中，车辆从右侧绕行。', '/uploadFile/yuanBeiTu425.gif', '2', '4', NULL, '', '', '', '', 'B', '此标志为左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', 10, 1, '2021-12-19 01:22:26', '2021-12-19 01:22:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4426, '图中标志提醒障碍物在路中，车辆从左侧绕行。', '/uploadFile/yuanBeiTu426.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:27', '2021-12-19 01:22:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4427, '右侧标志提醒前方路段有塌方禁止通行。', '/uploadFile/yuanBeiTu427.gif', '2', '4', NULL, '', '', '', '', 'B', '警告标志注意危险，加上下面白色的辅助标志，提醒前方有塌方，通行时注意危险，因此，题干表述错误。', 10, 1, '2021-12-19 01:22:27', '2021-12-19 01:22:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4428, '右侧标志提醒前方道路正在施工。', '/uploadFile/yuanBeiTu428.gif', '2', '4', NULL, '', '', '', '', 'A', '图中是注意前方道路正在施工标志：用以告示前方道路施工，车辆应减速慢行或绕道行驶。', 10, 1, '2021-12-19 01:22:28', '2021-12-19 01:22:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4429, '右侧标志提醒前方有村庄或集镇，建议速度30公里/小时。', '/uploadFile/yuanBeiTu429.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，道路右侧设有30km/h的建议速度警告标志，提醒车辆驾驶人以标志建议的速度行驶。因此，题干表述正确。', 10, 1, '2021-12-19 01:22:28', '2021-12-19 01:22:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4430, '右侧这个标志警告进入隧道减速慢行。', '/uploadFile/yuanBeiTu430.gif', '2', '4', NULL, '', '', '', '', 'B', '右侧标志为隧道开车灯警告标志。警告前方是无照明或照明不足的隧道，应打开前照灯，注意行驶。', 10, 1, '2021-12-19 01:22:28', '2021-12-19 01:22:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4431, '左侧标志警告前方注意左侧路口有汇入车辆。', '/uploadFile/yuanBeiTu431.gif', '2', '4', NULL, '', '', '', '', 'A', '左侧是注意合流标志，警告前方注意左侧路口有汇入车辆。', 10, 1, '2021-12-19 01:22:28', '2021-12-19 01:22:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4432, '右侧标志警告前方注意右侧路口有汇入车辆。', '/uploadFile/yuanBeiTu432.gif', '2', '4', NULL, '', '', '', '', 'A', '注意合流 ，用以警告车辆驾驶人注意前方有车辆汇合进来。', 10, 1, '2021-12-19 01:22:29', '2021-12-19 01:22:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4433, '右侧标志提醒前方右侧500米有避险车道。', '/uploadFile/yuanBeiTu433.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志由警告标志和辅助标志组合而成，提醒前方右侧500米设置了避险车道，即供速度失控车辆驶离原车道，进行安全减速的专用车道。', 10, 1, '2021-12-19 01:22:29', '2021-12-19 01:22:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4434, '右侧标志表示前方路口要停车让行。', '/uploadFile/yuanBeiTu434.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:29', '2021-12-19 01:22:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4435, '右侧标志表示前方路段会车时停车让对方车先行。', '/uploadFile/yuanBeiTu435.gif', '2', '4', NULL, '', '', '', '', 'B', '图中右侧标志是减速让行标志。表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。 题目中“会车时停车让对方车先行”的描述是错误的，因此本题答案为错。', 10, 1, '2021-12-19 01:22:30', '2021-12-19 01:22:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4436, '左侧标志表示前方路段允许进入。', '/uploadFile/yuanBeiTu436.gif', '2', '4', NULL, '', '', '', '', 'B', '禁止通行：表示前方道路禁止一切车辆和行人通行。', 10, 1, '2021-12-19 01:22:31', '2021-12-19 01:22:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4437, '右侧标志提示一切车辆都不能驶入。', '/uploadFile/yuanBeiTu437.gif', '2', '4', NULL, '', '', '', '', 'A', '禁止驶入：表示禁止车辆驶入。此标志设在禁止驶入的路段入口处。', 10, 1, '2021-12-19 01:22:31', '2021-12-19 01:22:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4438, '右侧标志表示前方路口不准车辆左转。', '/uploadFile/yuanBeiTu438.gif', '2', '4', NULL, '', '', '', '', 'A', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置。', 10, 1, '2021-12-19 01:22:31', '2021-12-19 01:22:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4439, '右侧标志表示前方路口不准车辆右转。', '/uploadFile/yuanBeiTu439.gif', '2', '4', NULL, '', '', '', '', 'A', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 10, 1, '2021-12-19 01:22:32', '2021-12-19 01:22:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4440, '左侧标志表示前方路口不准掉头。', '/uploadFile/yuanBeiTu440.gif', '2', '4', NULL, '', '', '', '', 'A', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 10, 1, '2021-12-19 01:22:32', '2021-12-19 01:22:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4441, '右侧标志表示前方路段允许超车。', '/uploadFile/yuanBeiTu441.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:32', '2021-12-19 01:22:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4442, '这个标志表示前方路段不允许超车。', '/uploadFile/yuanBeiTu442.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:32', '2021-12-19 01:22:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4443, '右侧标志表示临时停车不受限制。', '/uploadFile/yuanBeiTu443.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:33', '2021-12-19 01:22:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4444, '右侧标志表示不允许长时鸣喇叭。', '/uploadFile/yuanBeiTu444.gif', '2', '4', NULL, '', '', '', '', 'B', '禁止鸣喇叭：表示前方道路禁止车辆鸣喇叭，此标志设在需要禁止鸣喇叭的地方，禁止鸣喇叭的时间和范围可用辅助标志说明。', 10, 1, '2021-12-19 01:22:33', '2021-12-19 01:22:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4445, '右侧标志表示前方道路限宽3米。', '/uploadFile/yuanBeiTu445.gif', '2', '4', NULL, '', '', '', '', 'A', '限制宽度：表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。 以图为例：装载宽度不得超过3米。', 10, 1, '2021-12-19 01:22:33', '2021-12-19 01:22:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4446, '隧道上方标志表示限制高度3.5米。', '/uploadFile/yuanBeiTu446.gif', '2', '4', NULL, '', '', '', '', 'A', '限制高度 :表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。 以图为例：装载高度不得超过3.5米。', 10, 1, '2021-12-19 01:22:34', '2021-12-19 01:22:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4447, '右侧标志表示前方路段解除时速40公里限制。', '/uploadFile/yuanBeiTu447.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:34', '2021-12-19 01:22:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4448, '右侧标志表示前方路段解除时速40公里限制。', '/uploadFile/yuanBeiTu448.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:34', '2021-12-19 01:22:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4449, '右侧标志表示前方100米是停车接受检查的地点。', '/uploadFile/yuanBeiTu449.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志是由禁令标志和辅助标志组合而成，表示前方100米是机动车必须停车接受检查的地点。', 10, 1, '2021-12-19 01:22:45', '2021-12-19 01:22:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4450, '右侧标志表示前方路口7:30-10:00允许车辆直行。', '/uploadFile/yuanBeiTu450.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志表示这条路的行驶方向为直行，图片标志下方的白色框内是辅助标志，表示在7:30-10:00之间允许通行。此标志设在直行的路口以前适当位置。', 10, 1, '2021-12-19 01:22:45', '2021-12-19 01:22:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4451, '右侧标志表示前方路口只能车辆向左转弯。', '/uploadFile/yuanBeiTu451.gif', '2', '4', NULL, '', '', '', '', 'A', '向左转弯，表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 10, 1, '2021-12-19 01:22:46', '2021-12-19 01:22:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4452, '右侧标志表示只能车辆向右转弯。', '/uploadFile/yuanBeiTu452.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，路前方设置向右转弯指示标志，指示前方路口一切车辆只准向右转弯。因此，题干表述正确。', 10, 1, '2021-12-19 01:22:46', '2021-12-19 01:22:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4453, '右侧标志表示只能靠左侧道路行驶。', '/uploadFile/yuanBeiTu453.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:46', '2021-12-19 01:22:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4454, '这个标志表示前方立体交叉处可以直行和右转弯。', '/uploadFile/yuanBeiTu454.gif', '2', '4', NULL, '', '', '', '', 'B', '立体交叉直行和左转弯行驶 ：这个表示一切车辆在立体交叉处可以直行和按图示路线左转弯行驶。审题时注意箭头的方向。', 10, 1, '2021-12-19 01:22:47', '2021-12-19 01:22:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4455, '前方标志表示向左是单向行驶道路。', '/uploadFile/yuanBeiTu455.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:47', '2021-12-19 01:22:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4456, '前方标志表示向右是单向行驶道路。', '/uploadFile/yuanBeiTu456.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，单行路指示标志，指示前方道路为单向行驶，已进入车辆应按标志指示方向（向左/向右/直行）行车。', 10, 1, '2021-12-19 01:22:47', '2021-12-19 01:22:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4457, '右侧标志表示注意避让直行方向来的机动车。', '/uploadFile/yuanBeiTu457.gif', '2', '4', NULL, '', '', '', '', 'B', '此标志是单行路，表示该道路为单向行驶，已进入车辆应依标志指示方向行车（图中为直行）。', 10, 1, '2021-12-19 01:22:48', '2021-12-19 01:22:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4458, '右前方标志表示该路段在规定时间内只供步行。', '/uploadFile/yuanBeiTu458.gif', '2', '4', NULL, '', '', '', '', 'A', '此图右侧标志为组合标志，上面为步行标志，下面为一个时间范围的辅助标志，该标志组合表示该路段在指定时间段只能步行。', 10, 1, '2021-12-19 01:22:48', '2021-12-19 01:22:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4459, '右侧标志表示此处不准鸣喇叭。', '/uploadFile/yuanBeiTu459.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:48', '2021-12-19 01:22:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4460, '右侧标志表示鸣喇叭提醒。', '/uploadFile/yuanBeiTu460.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:49', '2021-12-19 01:22:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4461, '右侧标志表示最高车速不准超过每小时50公里。', '/uploadFile/yuanBeiTu461.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:49', '2021-12-19 01:22:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4462, '右侧标志表示干路车辆优先通行。', '/uploadFile/yuanBeiTu462.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:22:49', '2021-12-19 01:22:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4463, '右侧标志表示会车时对向车辆先行。', '/uploadFile/yuanBeiTu463.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:50', '2021-12-19 01:22:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4464, '右侧标志表示车辆按箭头示意方向选择行驶车道。', '/uploadFile/yuanBeiTu464.gif', '2', '4', NULL, '', '', '', '', 'A', '前方是分向行驶车道标志，表示各车道车辆应按标志箭头指示方向行驶。', 10, 1, '2021-12-19 01:22:50', '2021-12-19 01:22:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4465, '前方标志表示除公交车以外的其他车辆不准进入该车道行驶。', '/uploadFile/yuanBeiTu465.gif', '2', '4', NULL, '', '', '', '', 'A', '公交线路专用车道 : 表示该车道专供本线路行驶的公交车辆行驶，禁止其他车辆行驶。', 10, 1, '2021-12-19 01:22:50', '2021-12-19 01:22:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4466, '红色圆圈内标志表示左侧道路只供小型车行驶。', '/uploadFile/yuanBeiTu466.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:22:51', '2021-12-19 01:22:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4467, '红色圆圈内标志表示除非机动车以外的其他车辆不准从该车道行驶。', '/uploadFile/yuanBeiTu467.gif', '2', '4', NULL, '', '', '', '', 'A', '红色圆圈内的标志是 [非机动车专用车道] 标志，表示该车道除非机动车以外的其他车辆不准进入该车道行驶。', 10, 1, '2021-12-19 01:22:51', '2021-12-19 01:22:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4468, '前方标志表示除大客车以外的其他车辆不准进入右侧车道行驶。', '/uploadFile/yuanBeiTu468.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:02', '2021-12-19 01:23:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4469, '左侧标志表示此处允许机动车掉头。', '/uploadFile/yuanBeiTu469.gif', '2', '4', NULL, '', '', '', '', 'A', '图中是允许掉头标志，允许机动车在此掉头。', 10, 1, '2021-12-19 01:23:03', '2021-12-19 01:23:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4470, '前方标志预告交叉路口通往方向的信息。', '/uploadFile/yuanBeiTu470.gif', '2', '4', NULL, '', '', '', '', 'A', '此为交叉路口预告标志，预告交叉路口通往方向的信息。', 10, 1, '2021-12-19 01:23:04', '2021-12-19 01:23:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4471, '前方标志告知前方道路各行其道的信息。', '/uploadFile/yuanBeiTu471.gif', '2', '4', NULL, '', '', '', '', 'B', '交叉路口预告，预告前方交叉路口形式、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息以及距前方交叉路口的距离。', 10, 1, '2021-12-19 01:23:15', '2021-12-19 01:23:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4472, '前方标志告知各个路口出口方向的信息。', '/uploadFile/yuanBeiTu472.gif', '2', '4', NULL, '', '', '', '', 'A', '前方是环形交叉路口指路标志，告知前方是环形交叉路口、交叉公路的编号或交叉道路的名称、通往方向信息、地理方向信息。因此题干表述正确。', 10, 1, '2021-12-19 01:23:16', '2021-12-19 01:23:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4473, '前方标志预告互通式立交桥通往方向的信息。', '/uploadFile/yuanBeiTu473.gif', '2', '4', NULL, '', '', '', '', 'A', '此标志为互通式立交桥，预告互通式立交桥通往方向的信息。互通式立交桥是城市里最常见的立交桥。互通式立交桥桥上面的道路与桥下面的道路有道路(即匝道)相联结，使上下道路上的车辆可以通行。互通式立交桥包括完全互通和部分互通两种形式，根据其平面上的形状，又有苜蓿叶形、菱形、环形、喇叭形立交桥等类型。一般用来告知道路通行方向信息。', 10, 1, '2021-12-19 01:23:16', '2021-12-19 01:23:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4474, '前方标志指示前方所要经过的重要地名和距离。', '/uploadFile/yuanBeiTu474.gif', '2', '4', NULL, '', '', '', '', 'A', '前方蓝色标志是地点距离指路标志，指示前方道路要经过的重要公路编号、道路名、地名和距离。', 10, 1, '2021-12-19 01:23:17', '2021-12-19 01:23:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4475, '前方标志告知前方200米处是露天停车场。', '/uploadFile/yuanBeiTu475.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧标志由指路标志和辅助标志组合而成，告知前方200米处设有露天停车场。', 10, 1, '2021-12-19 01:23:17', '2021-12-19 01:23:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4476, '前方标志告知向右100米为室内停车场。', '/uploadFile/yuanBeiTu476.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:17', '2021-12-19 01:23:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4477, '右侧标志指示前方设有避让来车的处所。', '/uploadFile/yuanBeiTu477.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:18', '2021-12-19 01:23:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4478, '右侧标志告知右前方100米是应急避难场所。', '/uploadFile/yuanBeiTu478.gif', '2', '4', NULL, '', '', '', '', 'A', '右侧是应急避难设施（场所）和向右100米的组合标志，表示右前方100米是应急避难场所。因此，题干表述正确。 【拓展知识】 应急避难设施（场所）：附近有应急避难场所、隧道等设施的疏散通道或其他应急避难设施。', 10, 1, '2021-12-19 01:23:18', '2021-12-19 01:23:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4479, '右侧标志指示前方路口绕行的路线。', '/uploadFile/yuanBeiTu479.gif', '2', '4', NULL, '', '', '', '', 'A', '请注意！题目问的是右前方标志，右前方是左转弯绕行标志，表示前方道路实施交通管制，车辆左转弯需按照标志所指示的路线绕行来实现，禁止直接左转弯。 绕行指路标志，表示前方道路实施交通管制，车辆需按照标志所指示的路线绕行。', 10, 1, '2021-12-19 01:23:18', '2021-12-19 01:23:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4480, '右前方标志指示前方路口左转弯绕行的路线。', '/uploadFile/yuanBeiTu480.gif', '2', '4', NULL, '', '', '', '', 'A', '请注意！题干问的是右前方标志，右前方是左转弯绕行标志，表示前方道路实施交通管制，车辆左转弯需按照标志所指示的路线绕行来实现，禁止直接左转弯。 绕行指路标志，表示前方道路实施交通管制，车辆需按照标志所指示的路线绕行。', 10, 1, '2021-12-19 01:23:19', '2021-12-19 01:23:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4481, '右侧标志指示前方是T型路口。', '/uploadFile/yuanBeiTu481.gif', '2', '4', NULL, '', '', '', '', 'B', '此路不通指路标志，指示前方道路无出口，不能通行。', 10, 1, '2021-12-19 01:23:19', '2021-12-19 01:23:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4482, '这个标志表示前方车道数量增加。', '/uploadFile/yuanBeiTu482.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:19', '2021-12-19 01:23:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4483, '右侧标志表示前方是分流路口。', '/uploadFile/yuanBeiTu483.gif', '2', '4', NULL, '', '', '', '', 'B', '车道数增加 指示前方车道数量增加，需谨慎驾驶。', 10, 1, '2021-12-19 01:23:20', '2021-12-19 01:23:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4484, '这个标志提示该路段已实行交通监控。', '/uploadFile/yuanBeiTu484.gif', '2', '4', NULL, '', '', '', '', 'A', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', 10, 1, '2021-12-19 01:23:20', '2021-12-19 01:23:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4485, '路两侧的标志提示前方道路线形变化。', '/uploadFile/yuanBeiTu485.gif', '2', '4', NULL, '', '', '', '', 'A', '道路两侧的标志为线形诱导标，作用是引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，注意谨慎驾驶。', 10, 1, '2021-12-19 01:23:20', '2021-12-19 01:23:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4486, '右侧标志警示前方道路两侧不能通行。', '/uploadFile/yuanBeiTu486.gif', '2', '4', NULL, '', '', '', '', 'B', '线形诱导标指路标志，引导行车方向，指示道路使用者前方线形变化，注意按标志指示改变行驶方向，谨慎驾驶。', 10, 1, '2021-12-19 01:23:20', '2021-12-19 01:23:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4487, '左侧标志警示前方道路右侧不能通行。', '/uploadFile/yuanBeiTu487.gif', '2', '4', NULL, '', '', '', '', 'B', '图中左侧标志是右侧通行标志，引导行车方向，提示道路使用者前方线形变化-右侧通行，注意按标志指示改变行驶方向，谨慎驾驶。因此，题干表述错误。 【拓展延伸】', 10, 1, '2021-12-19 01:23:21', '2021-12-19 01:23:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4488, '前方标志预告距离高速公路入口1公里。', '/uploadFile/yuanBeiTu488.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:21', '2021-12-19 01:23:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4489, '前方标志预告高速公路入口在路右侧。', '/uploadFile/yuanBeiTu489.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:21', '2021-12-19 01:23:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4490, '左侧标志指示高速公路两个行驶方向的目的地。', '/uploadFile/yuanBeiTu490.gif', '2', '4', NULL, '', '', '', '', 'A', '左侧绿底、白图形、白边框、绿色衬边的标志为高速公路和城市快速路指路标志，指示地点及方向，即前方高速公路或城市快速路的辆行驶方向的目的地。', 10, 1, '2021-12-19 01:23:22', '2021-12-19 01:23:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4491, '前方标志预告高速公路终点距离信息。', '/uploadFile/yuanBeiTu491.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:22', '2021-12-19 01:23:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4492, '前方标志指示高速公路的名称和编号。', '/uploadFile/yuanBeiTu492.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示编号，指示高速公路命名编号，作为指路标志的路线信息。字母G，为“国”字拼音的首字母。', 10, 1, '2021-12-19 01:23:22', '2021-12-19 01:23:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4493, '前方标志预告距离下一出口4公里。', '/uploadFile/yuanBeiTu493.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:22', '2021-12-19 01:23:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4494, '前方标志预告距离下一左侧出口1公里。', '/uploadFile/yuanBeiTu494.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:23', '2021-12-19 01:23:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4495, '右侧标志指示前方是高速公路的终点。', '/uploadFile/yuanBeiTu495.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:23', '2021-12-19 01:23:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4496, '前方标志预告前方距高速公路终点还有2公里。', '/uploadFile/yuanBeiTu496.gif', '2', '4', NULL, '', '', '', '', 'A', '图中右侧标志为终点预告标志：预告高速公路或城市快速路终点的距离。绿色标志牌上有红色斜线和距离说明的是高速公路终点预告，只有红色斜线没有距离说明的是高速公路终点标志。', 10, 1, '2021-12-19 01:23:23', '2021-12-19 01:23:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4497, '这个标志指示高速公路交通广播和无线电视频道。', '/uploadFile/yuanBeiTu497.gif', '2', '4', NULL, '', '', '', '', 'B', '标志是道路交通信息，指示收听高速公路或城市快速路交通信息广播的频率。此题错在“无线电视频道”。', 10, 1, '2021-12-19 01:23:24', '2021-12-19 01:23:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4498, '左侧标志提示前方收费口停车领卡。', '/uploadFile/yuanBeiTu498.gif', '2', '4', NULL, '', '', '', '', 'A', '左侧标志停车领卡，提示前方收费口停车领卡。', 10, 1, '2021-12-19 01:23:24', '2021-12-19 01:23:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4499, '右侧标志提示距离设有电子不停车收费车道的收费站1公里。', '/uploadFile/yuanBeiTu499.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:24', '2021-12-19 01:23:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4500, '右侧标志指示前方收费站设有电子不停车收费行驶车道。', '/uploadFile/yuanBeiTu500.gif', '2', '4', NULL, '', '', '', '', 'A', 'ETC车道是指电子不停车收费行驶车道。', 10, 1, '2021-12-19 01:23:25', '2021-12-19 01:23:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4501, '右侧标志指示距离设有电子不停车收费车道的收费站1公里。', '/uploadFile/yuanBeiTu501.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:25', '2021-12-19 01:23:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4502, '右侧标志指示高速公路紧急电话的位置。', '/uploadFile/yuanBeiTu502.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，右侧是高速公路救援电话标志，用于指示救援电话号码。紧急电话只有一个电话标志。', 10, 1, '2021-12-19 01:23:25', '2021-12-19 01:23:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4503, '右侧标志指示距离前方加油站入口200米。', '/uploadFile/yuanBeiTu503.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，绿色加油站标志，表示前方有通往加油站的入口，指示加油站的位置；下方是距离某地200米的辅助标志，对指路、旅游区和警告标志表示到达所指设施、危险点的距离。因此，右侧组合标志表示距离前方加油站入口200米。', 10, 1, '2021-12-19 01:23:25', '2021-12-19 01:23:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4504, '前方标志指示路右侧是高速公路临时停车处。', '/uploadFile/yuanBeiTu504.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:26', '2021-12-19 01:23:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4505, '右侧标志提示前方200米是车距确认路段。', '/uploadFile/yuanBeiTu505.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:26', '2021-12-19 01:23:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4506, '右侧标志预告距离高速公路东芦山服务区2公里。', '/uploadFile/yuanBeiTu506.gif', '2', '4', NULL, '', '', '', '', 'A', '服务区预告：预告前方服务区的位置和服务区能提供的停车、加油、维修、餐饮、等综合性服务。设在距服务区2km处。 有停车场、加油站、维修站等服务，右侧标志是服务区，预告距离高速公路东芦山服务区2公里。 顺便普及下服务区跟停车区的区别：服务区有吃住加油等服务，停车区属于紧急停车，没有相关的服务。', 10, 1, '2021-12-19 01:23:38', '2021-12-19 01:23:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4507, '路面中心黄虚线指示在保证安全的情况下可以越线超车。', '/uploadFile/yuanBeiTu507.gif', '2', '4', NULL, '', '', '', '', 'A', '因为图中中心线是黄色虚线，虚线可跨越，所以在保证安全的情况下可以越线超车。', 10, 1, '2021-12-19 01:23:38', '2021-12-19 01:23:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4508, '路中黄色虚线指示任何情况都不允许越线绕行。', '/uploadFile/yuanBeiTu508.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，路中黄色虚线，可跨越对向车行道分界线，车辆在确保安全的情况下可以越线超车或转弯。', 10, 1, '2021-12-19 01:23:38', '2021-12-19 01:23:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4509, '道路右侧白色虚线指示可以越线变更车道。', '/uploadFile/yuanBeiTu509.gif', '2', '4', NULL, '', '', '', '', 'A', '道路右侧白色虚线，用于分隔同向行驶的交通流。在保证安全的情况下，允许车辆短时越线行驶。', 10, 1, '2021-12-19 01:23:38', '2021-12-19 01:23:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4510, '路中两条双黄虚线并列组成的双黄虚线指示潮汐车道的位置。', '/uploadFile/yuanBeiTu510.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:39', '2021-12-19 01:23:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4511, '道路右侧白色实线标示机动车道与人行道的分界线。', '/uploadFile/yuanBeiTu511.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，右侧道路施划有非机动车路面标记，表示非机动车道。因此，道路右侧白色实线标示的是机动车道与非机动车道的分界线。题干表述错误。', 10, 1, '2021-12-19 01:23:39', '2021-12-19 01:23:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4512, '道路右侧车行道边缘白色虚线指示允许跨越。', '/uploadFile/yuanBeiTu512.gif', '2', '4', NULL, '', '', '', '', 'A', '白色虚线指示分隔同向行驶交通车流，在保证安全的情况下，允许车辆短时跨越行驶。', 10, 1, '2021-12-19 01:23:39', '2021-12-19 01:23:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4513, '路面白色虚实线指示变道或靠边停车时允许跨越。', '/uploadFile/yuanBeiTu513.gif', '2', '4', NULL, '', '', '', '', 'A', '车行道边缘白色虚实线的虚线一侧允许车辆越线行驶，实线一侧不允许车辆越线行驶，用以规范车辆行驶轨迹。在必要的地点，如公交车站临近路段、允许路边停车路段等，可设置车行道边缘白色虚实线。跨线行驶的车辆，应避让其他正常行驶的车辆、非机动车辆和行人。', 10, 1, '2021-12-19 01:23:40', '2021-12-19 01:23:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4514, '路面白色虚线实线指示实线一侧允许跨越。', '/uploadFile/yuanBeiTu514.gif', '2', '4', NULL, '', '', '', '', 'B', '虚实线，实线一侧禁止车辆越线或压线行驶，虚线一侧准许车辆暂时越线。', 10, 1, '2021-12-19 01:23:40', '2021-12-19 01:23:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4515, '如下图，左转弯车辆可直接进入左转弯待转区，等待放行信号。', '/uploadFile/yuanBeiTu515.gif', '2', '4', NULL, '', '', '', '', 'B', '左转弯专用车道前端伸入交叉路口内，以白色虚线划分的左弯待转区线，指示左转弯车辆在直行时段（即直行信号灯为绿灯时）进入待转区等待左转，但不得妨碍对向直行车辆的正常行驶。如图所示，直行信号灯为红灯，机动车应在停止线外停车等待，不可以直接进入左转弯待转区。', 10, 1, '2021-12-19 01:23:40', '2021-12-19 01:23:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4516, '如下图，左转弯车辆不可以直接进入左转弯待转区，等待放行信号。', '/uploadFile/yuanBeiTu516.gif', '2', '4', NULL, '', '', '', '', 'B', '在左转弯专用车道前端，伸入交叉路口内，以白色虚线划分的位置为左弯待转区，指示左转弯车辆在直行时段（即直行信号灯绿灯亮时）进入待转区等待左转，但不得妨碍对向直行车辆的正常行驶。图中直行信号灯绿灯亮，因此左转弯车辆可以进入左转弯待转区，等待放行信号，题干错在不可以进入。', 10, 1, '2021-12-19 01:23:40', '2021-12-19 01:23:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4517, '如下图，路口导向线用于辅助车辆转弯行驶。', '/uploadFile/yuanBeiTu517.gif', '2', '4', NULL, '', '', '', '', 'A', '路口导向线：可辅助车辆在路口行驶和转向。连接同向车行道分界线或机非分界线的路口导向线为白色圆曲（或直）虚线；连接对向车行道分界线的路口导向线为黄色圆曲（或直）虚线。', 10, 1, '2021-12-19 01:23:41', '2021-12-19 01:23:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4518, '如下图，路口导向线用于辅助车辆转弯行驶。', '/uploadFile/yuanBeiTu518.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:41', '2021-12-19 01:23:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4519, '如下图，进入交叉口前，车辆不允许跨越白色实线变更车道。', '/uploadFile/yuanBeiTu519.gif', '2', '4', NULL, '', '', '', '', 'A', '白色实线，禁止跨越同向车行道分界线。 谨记：道路上的实线，就像一堵墙，轧线就是撞墙！虽然车没“撞”坏，但极易造成交通事故。', 10, 1, '2021-12-19 01:23:41', '2021-12-19 01:23:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4520, '路面可变导向车道线指示可以随意选择通行方向。', '/uploadFile/yuanBeiTu520.gif', '2', '4', NULL, '', '', '', '', 'B', '可变导向车道线的意思就是进入该车道后的走向须根据对应车道信号灯的指示确定。因此，可变导向车道内的车辆应根据车道信号灯的指示通行，不可以随意选择通行方向。', 10, 1, '2021-12-19 01:23:41', '2021-12-19 01:23:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4521, '路口内人行横道线警示行人优先横过道路。', '/uploadFile/yuanBeiTu521.gif', '2', '4', NULL, '', '', '', '', 'A', '人行横道线是为行人横过道路而设置的安全通道上的标线，常被称为“生命线”。驾驶人驾车行经人行横道时，应减速观察，礼让行人。', 10, 1, '2021-12-19 01:23:42', '2021-12-19 01:23:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4522, '路面上菱形标识预告前方道路设置人行横道。', '/uploadFile/yuanBeiTu522.gif', '2', '4', NULL, '', '', '', '', 'A', '道路路面上的白色菱形图案，是中华人民共和国国家标准（GB5768/1999）《道路交通标志和标线》中新设置的一种交通标线，正式名称为人行横道预告标线。这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路。', 10, 1, '2021-12-19 01:23:42', '2021-12-19 01:23:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4523, '高速公路上的白色折线为行车中判断行车速度提供参考。', '/uploadFile/yuanBeiTu523.gif', '2', '4', NULL, '', '', '', '', 'B', '白色折线车距确认线：以白色折线作为车距确认标线，两道为一组，每组间隔50米，作为车辆驾驶人保持行车安全距离的参考。', 10, 1, '2021-12-19 01:23:42', '2021-12-19 01:23:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4524, '高速公路两侧白色半圆状的间隔距离是50米。', '/uploadFile/yuanBeiTu524.gif', '2', '4', NULL, '', '', '', '', 'A', '白色半圆状车距确认线：以白色半圆状作为车距确认线，设置在气象条件复杂、影响安全行车的路段两侧，每隔50米设置一个，作为车辆驾驶人保持行车安全距离的参考。 【拓展延伸】', 10, 1, '2021-12-19 01:23:43', '2021-12-19 01:23:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4525, '路面上的出口标线用于引导驶出该高速公路。', '/uploadFile/yuanBeiTu525.gif', '2', '4', NULL, '', '', '', '', 'A', '高速公路出入口标线为白色虚线，主要用于高速公路和其他采用立体交叉道路。该标线作用为，高速公路出入口横向标线或三角地带标线是为驶入或驶出匝道车辆提供安全交汇引导，减少与突出部缘石碰撞。', 10, 1, '2021-12-19 01:23:43', '2021-12-19 01:23:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4526, '允许沿着图中箭头方向驶入高速公路行车道。', '/uploadFile/yuanBeiTu526.gif', '2', '4', NULL, '', '', '', '', 'B', '路面V形导流线，表示车辆在交叉口需按规定的路线行驶，不得压线或越线行驶。因此，题干表述错误。', 10, 1, '2021-12-19 01:23:43', '2021-12-19 01:23:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4527, '路右侧黄色矩形标线框内表示允许临时停车。', '/uploadFile/yuanBeiTu527.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:43', '2021-12-19 01:23:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4528, '路右侧白色矩形虚线框内表示允许长时间停车。', '/uploadFile/yuanBeiTu528.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:44', '2021-12-19 01:23:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4529, '该车道路面导向箭头指示前方道路仅可直行。', '/uploadFile/yuanBeiTu529.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，机动车所在的车道的路面导向箭头指示直行，表示前方道路仅可直行。因此，题干表述正确（ybjk）。', 10, 1, '2021-12-19 01:23:44', '2021-12-19 01:23:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4530, '该车道路面导向箭头指示前方道路仅可左转。', '/uploadFile/yuanBeiTu530.gif', '2', '4', NULL, '', '', '', '', 'A', '该车道路面施划的是左转导向箭头，指示前方道路仅可左转弯行驶。因此，题干表述正确。', 10, 1, '2021-12-19 01:23:44', '2021-12-19 01:23:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4531, '该车道路面导向箭头指示在前方路口仅可直行。', '/uploadFile/yuanBeiTu531.gif', '2', '4', NULL, '', '', '', '', 'B', '没有交通信号的路口，箭头指哪往哪走，该车道路面导向箭头指示在前方路口仅可直行或者左转。', 10, 1, '2021-12-19 01:23:45', '2021-12-19 01:23:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4532, '该车道路面导向箭头指示在前方路口仅可右转弯。', '/uploadFile/yuanBeiTu532.gif', '2', '4', NULL, '', '', '', '', 'B', '图中的标志有两个箭头方向，表示可以直行或者右转弯。', 10, 1, '2021-12-19 01:23:45', '2021-12-19 01:23:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4533, '该车道路面导向箭头指示前方路口仅可左转弯。', '/uploadFile/yuanBeiTu533.gif', '2', '4', NULL, '', '', '', '', 'B', '路面导向箭头：用以指示车辆的行驶方向，如图所示车道内的导向箭头为掉头，指示前方路口允许掉头。因此，题干描述仅可左转弯说法错误。', 10, 1, '2021-12-19 01:23:45', '2021-12-19 01:23:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4534, '该车道路面导向箭头指示前方路口可左转弯或掉头。', '/uploadFile/yuanBeiTu534.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，该车道路面导向箭头指示当前车道为左转掉头合用车道，因此，在该车道行驶的机动车在前方路口可以左转弯或掉头行驶。', 10, 1, '2021-12-19 01:23:45', '2021-12-19 01:23:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4535, '该车道路面导向箭头指示前方路口仅能掉头。', '/uploadFile/yuanBeiTu535.gif', '2', '4', NULL, '', '', '', '', 'B', '导向箭头指示前方路口可以直行或者掉头，所以此题错误。', 10, 1, '2021-12-19 01:23:46', '2021-12-19 01:23:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4536, '该车道路面导向箭头指示前方道路仅可左右转弯。', '/uploadFile/yuanBeiTu536.gif', '2', '4', NULL, '', '', '', '', 'A', '导向箭头颜色为白色，用以指示车辆的行驶方向。如图所示，导向箭头为左右转弯，指示前方道路仅可以左转右转。', 10, 1, '2021-12-19 01:23:46', '2021-12-19 01:23:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4537, '该车道路面导向箭头提示前方道路需向左合流。', '/uploadFile/yuanBeiTu537.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:23:46', '2021-12-19 01:23:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4538, '该车道路面导向箭头提示前方道路右侧有路口。', '/uploadFile/yuanBeiTu538.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示车道内白色导向箭头，表示右弯或需向右合流。', 10, 1, '2021-12-19 01:23:57', '2021-12-19 01:23:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4539, '路面标记指示这段道路上最高限速为50公里/小时。', '/uploadFile/yuanBeiTu539.gif', '2', '4', NULL, '', '', '', '', 'A', '红色限制最高速度，此标志指示在这段道路上最高限速为50公里/小时。', 10, 1, '2021-12-19 01:23:57', '2021-12-19 01:23:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4540, '路面标记指示这段道路上最高限速为80公里/小时。', '/uploadFile/yuanBeiTu540.gif', '2', '4', NULL, '', '', '', '', 'B', '白色的是最低车速，黄色的是最高车速。题目图片中，地面数字是白色的80，表示最低限速80公里/小时，题目描述有误，因此本题答案为错。', 10, 1, '2021-12-19 01:23:58', '2021-12-19 01:23:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4541, '路面标记指示这段道路上最低限速为60公里/小时。', '/uploadFile/yuanBeiTu541.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:23:58', '2021-12-19 01:23:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4542, '右侧路面标记表示可以暂时借用超车。', '/uploadFile/yuanBeiTu542.gif', '2', '4', NULL, '', '', '', '', 'B', '首先超车要从左侧，左侧黄实线，不可跨越超车，另外不可跨越实线借非机动车车道超车。', 10, 1, '2021-12-19 01:23:58', '2021-12-19 01:23:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4543, '路中心黄色实虚线指示允许超车时越过。', '/uploadFile/yuanBeiTu543.gif', '2', '4', NULL, '', '', '', '', 'B', '中心黄色虚实线，实线一侧是禁止车辆越线超车或向左转弯，虚线一侧是准许车辆越线超车或向左转弯。', 10, 1, '2021-12-19 01:23:59', '2021-12-19 01:23:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4544, '路中心黄色虚实线指示允许暂时越过超车。', '/uploadFile/yuanBeiTu544.gif', '2', '4', NULL, '', '', '', '', 'A', '虚实线：虚线一侧可以跨越，实线一侧不可跨越。图中车辆在虚线一侧，，且对向车道无来车，因此允许暂时越线超车。', 10, 1, '2021-12-19 01:23:59', '2021-12-19 01:23:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4545, '路中心黄色双实线指示可以暂时跨越超车。', '/uploadFile/yuanBeiTu545.gif', '2', '4', NULL, '', '', '', '', 'B', '中心黄色双实线表示严格禁止车辆跨线超车或压线行驶,属禁止标线。用以划分上下行方向各有两条或两条以上机动车道而没有设置中央分隔带的道路。除交叉路口或允许车辆左转变（或回转）路段外，均应连续设置。', 10, 1, '2021-12-19 01:23:59', '2021-12-19 01:23:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4546, '黄色斜线填充线指示该区域禁止进入或压线行驶。', '/uploadFile/yuanBeiTu546.gif', '2', '4', NULL, '', '', '', '', 'A', '黄色斜线填充线禁止跨越,，在路面较宽时，为保证车行道宽度不大于3.75 m，双黄线间距可以适当调整。在双黄线间距大于50cm时，应用黄色斜线或其他设施填充两条黄实线间的部分，禁止车辆压线或进入该区域。', 10, 1, '2021-12-19 01:23:59', '2021-12-19 01:23:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4547, '路面同向车行道分界线指示允许跨越变换车道。', '/uploadFile/yuanBeiTu547.gif', '2', '4', NULL, '', '', '', '', 'B', '虚线可跨越，实线不可跨越。图中是实线，不可跨越。', 10, 1, '2021-12-19 01:24:00', '2021-12-19 01:24:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4548, '路面同向车行道分界线指示不允许跨越超车。', '/uploadFile/yuanBeiTu548.gif', '2', '4', NULL, '', '', '', '', 'A', '实线不能跨越，故不能超越。', 10, 1, '2021-12-19 01:24:00', '2021-12-19 01:24:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4549, '路缘石上的黄色虚线指示路边不允许停车上下人员或装卸货物。', '/uploadFile/yuanBeiTu549.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:00', '2021-12-19 01:24:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4550, '路缘石的黄色实线指示路边允许临时停放车辆。', '/uploadFile/yuanBeiTu550.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:01', '2021-12-19 01:24:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4551, '前方路口停车让行线表示减速让干道车先行。', '/uploadFile/yuanBeiTu551.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:01', '2021-12-19 01:24:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4552, '前方路口减速让行线表示要停车让干道车先行。', '/uploadFile/yuanBeiTu552.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:01', '2021-12-19 01:24:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4553, '路口两侧导流线表示直行或右转弯不得压线或越线行驶。', '/uploadFile/yuanBeiTu553.gif', '2', '4', NULL, '', '', '', '', 'A', '导流线的形式主要为一个或几个根据路口地形设置的白色V形线或斜纹线区域，表示车辆必须规定的路线行驶，不得压线或越线行驶。主要用于过宽、不规则或行驶条件比较复杂的交叉路口，立体交叉的匝道口或其他特殊地点。 法律依据：《道路交通安全法》第三十八条、第四十四条规定，导流线属于禁止标线的一种，车辆必须按规定的路线行驶，不得压线或越线行驶，也不得随意在导流线区域掉头或停放。', 10, 1, '2021-12-19 01:24:02', '2021-12-19 01:24:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4554, '路口内中心圈表示左小转弯要沿内侧行驶。', '/uploadFile/yuanBeiTu554.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在设有中心圈的路口左转弯时，要沿中心圈左侧（内侧）向左行驶，不得轧线或从右侧向左行驶。因此，题干表述正确。', 10, 1, '2021-12-19 01:24:02', '2021-12-19 01:24:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4555, '路面网状线表示允许进入该区域内等待。', '/uploadFile/yuanBeiTu555.gif', '2', '4', NULL, '', '', '', '', 'B', '黄色网状线，禁止标线，严格禁止一切车辆长时或临时停车防止交通阻塞。因此，题干表述错误。', 10, 1, '2021-12-19 01:24:02', '2021-12-19 01:24:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4556, '路面网状线表示不准进入该区域内停车等待。', '/uploadFile/yuanBeiTu556.gif', '2', '4', NULL, '', '', '', '', 'A', '路面黄色网状线表示严格禁止一切车辆长时或临时停车，防止交通阻塞。', 10, 1, '2021-12-19 01:24:02', '2021-12-19 01:24:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4557, '右侧车道路面标线表示可以临时借公交专用车道行驶。', '/uploadFile/yuanBeiTu557.gif', '2', '4', NULL, '', '', '', '', 'B', '公交专用车道 ，只供公交车行驶，其他车辆不得占用。右侧车道路面标线虽然是虚线，但表示的是公交车可以借用别的车道，但是其他车辆不能借用公交车专用车道，大家一定要记住。', 10, 1, '2021-12-19 01:24:03', '2021-12-19 01:24:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4558, '路面标记指示前方路口禁止车辆掉头。', '/uploadFile/yuanBeiTu558.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:03', '2021-12-19 01:24:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4559, '路面标记指示前方路口仅允许车辆向右转弯。', '/uploadFile/yuanBeiTu559.gif', '2', '4', NULL, '', '', '', '', 'B', '\"X\"表示不准，加上右转就是禁止右转，不是允许车辆向右转弯。', 10, 1, '2021-12-19 01:24:03', '2021-12-19 01:24:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4560, '路面白色反光虚线警告前方路段要减速慢行。', '/uploadFile/yuanBeiTu560.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:03', '2021-12-19 01:24:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4561, '路中黄色斜线填充标记警告前方有固定性障碍物。', '/uploadFile/yuanBeiTu561.gif', '2', '4', NULL, '', '', '', '', 'A', '路中黄色斜线填充标记警告前方有固定性障碍物，警告车辆驾驶人谨慎行车，左右绕行避开障碍物。引导交通流顺畅驶离障碍物区域。接近障碍物标线的颜色，应根据障碍物所在的位置，与对向车行道分界线或同向车行道分界线的颜色一致。', 10, 1, '2021-12-19 01:24:04', '2021-12-19 01:24:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4562, '路面菱形块虚线警告前方道路要减速慢行。', '/uploadFile/yuanBeiTu562.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:04', '2021-12-19 01:24:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4563, '看到这种手势信号时怎样行驶？', '/uploadFile/yuanBeiTu563.gif', '1', '4', NULL, '停车等待', '直行通过路口', '在路口向右转弯', '在路口向左转弯', 'A', '', 10, 1, '2021-12-19 01:24:04', '2021-12-19 01:24:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4564, '看到这种手势信号时怎样行驶？', '/uploadFile/yuanBeiTu564.gif', '1', '4', NULL, '直行通过路口', '停在停止线外等待', '在路口向左转弯', '在路口减速慢行', 'C', '', 10, 1, '2021-12-19 01:24:05', '2021-12-19 01:24:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4565, '看到这种手势信号时怎样行驶？', '/uploadFile/yuanBeiTu565.gif', '1', '4', NULL, '直行通过路口', '停车等待', '在路口向右转弯', '在路口向左转弯', 'B', '如图所示，交警手势是左转弯信号，表示准许交警左侧的车辆左转弯行驶；而图中车辆（交警正面的机动车）看到这种手势时，应当停车等待。', 10, 1, '2021-12-19 01:24:05', '2021-12-19 01:24:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4566, '看到这种手势信号时怎样行驶？', '/uploadFile/yuanBeiTu566.gif', '1', '4', NULL, '在路口向左转弯', '停车等待', '在路口直行', '进入左弯待转区', 'B', '交警面部对着他的右侧，表示准许交警右侧车辆右转弯行驶，而图中车辆（交警正前方的机动车）看到这种手势信号时，应停车等待。', 10, 1, '2021-12-19 01:24:05', '2021-12-19 01:24:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4567, '看到这种手势信号时怎样行驶？', '/uploadFile/yuanBeiTu567.gif', '1', '4', NULL, '直行通过路口', '靠路边停车', '进入左弯待转区', '在路口向右转弯', 'D', '', 10, 1, '2021-12-19 01:24:16', '2021-12-19 01:24:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4568, '交通警察发出的是禁止通行手势信号。', '/uploadFile/yuanBeiTu568.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:16', '2021-12-19 01:24:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4569, '交通警察发出的是右转弯手势信号。', '/uploadFile/yuanBeiTu569.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:17', '2021-12-19 01:24:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4570, '看到交通警察这种姿势时可以直行通过。', '/uploadFile/yuanBeiTu570.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，交警无手势信号，且车道信号灯为绿灯，表示准许通行。', 10, 1, '2021-12-19 01:24:17', '2021-12-19 01:24:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4571, '交通警察发出这种手势可以向左转弯。', '/uploadFile/yuanBeiTu571.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，交警手势是左转弯信号，表示准许交警左侧的车辆左转弯行驶；而图中车辆（交警正面的机动车）看到这种手势时，应当停车等待。', 10, 1, '2021-12-19 01:24:17', '2021-12-19 01:24:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4572, '交通警察发出这种手势信号可以向左转弯。', '/uploadFile/yuanBeiTu572.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:18', '2021-12-19 01:24:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4573, '交通警察发出这种手势信号可以直行通过。', '/uploadFile/yuanBeiTu573.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:18', '2021-12-19 01:24:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4574, '看到这种手势信号时可以向左转弯。', '/uploadFile/yuanBeiTu574.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:18', '2021-12-19 01:24:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4575, '交通警察发出的是左转弯待转手势信号。', '/uploadFile/yuanBeiTu575.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:18', '2021-12-19 01:24:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4576, '交通警察发出这种手势信号可以左转弯。', '/uploadFile/yuanBeiTu576.gif', '2', '4', NULL, '', '', '', '', 'B', '', 10, 1, '2021-12-19 01:24:19', '2021-12-19 01:24:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4577, '交通警察发出这种手势信号时可以直行通过。', '/uploadFile/yuanBeiTu577.gif', '2', '4', NULL, '', '', '', '', 'B', '交警面部对着他的右侧，表示准许交警右侧的车辆右转弯行驶，而图中车辆（交警正前方的机动车）看到这种手势信号时，应停车等待。', 10, 1, '2021-12-19 01:24:19', '2021-12-19 01:24:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4578, '交通警察发出这种手势信号时允许在路口向右转弯。', '/uploadFile/yuanBeiTu578.gif', '2', '4', NULL, '', '', '', '', 'A', '', 10, 1, '2021-12-19 01:24:19', '2021-12-19 01:24:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4579, '交通警察发出这种手势信号时要减速慢行。', '/uploadFile/yuanBeiTu579.gif', '2', '4', NULL, '', '', '', '', 'A', '左手是进入左转待转区，右手是减速慢行（上下摆动像叮嘱小孩）和变更车道（水平摆动像扇风）。图中交警是右手上下摆动，因此是减速慢行。', 10, 1, '2021-12-19 01:24:20', '2021-12-19 01:24:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4580, '图中红框内所示车辆可以怎样行驶？', '/uploadFile/yuanBeiTu580.gif', '1', '4', NULL, '不可左转弯', '可以右转，但要避让同向直行车辆', '可以左转，但要避让对向直行车辆', '可以直行', 'C', '由路面导向箭头可知，红框内的车行驶在左转弯车道，因此可以左转弯行驶，但要避让同向直行的车辆。', 10, 1, '2021-12-19 01:24:20', '2021-12-19 01:24:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4581, '图中红框内行驶车辆存在交通违法行为。', '/uploadFile/yuanBeiTu581.gif', '2', '4', NULL, '', '', '', '', 'A', '图中车辆压导流线行驶，是违法行为。 导流线的形式主要为一个或几个根据路口地形设置的白色V形线或斜纹线区域，表示车辆必须按规定的路线行驶，不得压线或越线行驶。主要用于过宽、不规则或行驶条件比较复杂的交叉路口，立体交叉的匝道口或其他特殊地点。', 10, 1, '2021-12-19 01:24:20', '2021-12-19 01:24:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4582, '按照下图红框内的标志，机动车应当在B区域内行驶。', '/uploadFile/yuanBeiTu582.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，A区域是机动车道，B区域是非机动车道。各行其道，机动车应在A区域机动车道内行驶。', 10, 1, '2021-12-19 01:24:21', '2021-12-19 01:24:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4583, '驾驶机动车在这种情况下怎样礼让行人？', '/uploadFile/yuanBeiTu583.gif', '1', '4', NULL, '等行人通过后再起步', '起步从行人前方绕过', '鸣喇叭告知行人让道', '起步后缓慢靠近行人', 'A', '驾驶人驾车行经人行横道时，即便是绿灯亮，遇到有行人正在通过时，要随时准备停车礼让行人，不得抢行或绕行。', 11, 1, '2021-12-19 01:24:21', '2021-12-19 01:24:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4584, '驾驶机动车遇到这样的情况要停车让行。', '/uploadFile/yuanBeiTu584.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在学习区域，遇到上学或放学时段，随时准备避让横过道路的学生和儿童。遇学生或儿童横过道路时，应及时停车礼让，不可从他们中间穿行或从两侧绕行通过，避免发生事故。', 11, 1, '2021-12-19 01:24:32', '2021-12-19 01:24:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4585, '驾驶机动车在这种情况下可以适当鸣喇叭加速通过。', '/uploadFile/yuanBeiTu585.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇到行人横穿人行横道时，应及时减速停车让行，不得加速从行人两侧绕行通过。', 11, 1, '2021-12-19 01:24:32', '2021-12-19 01:24:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4586, '如动画所示，驾驶人的行为是否正确？', '/uploadFile/yuanBeiTu586.gif', '2', '4', NULL, '', '', '', '', 'B', '行车中遇行人正在通过人行横道时，车辆驾驶人应当停车让行，等行人通过后再通过。动画中驾驶人绕过行人的做法是错误的。', 11, 1, '2021-12-19 01:24:32', '2021-12-19 01:24:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4587, '驾驶机动车遇到这种特殊情况怎样行驶？', '/uploadFile/yuanBeiTu587.gif', '1', '4', NULL, '靠左侧减速让行', '靠右侧减速让行', '加速靠左侧让行', '保持原行驶路线', 'A', '行车中遇执行任务的警车时，应及时避让。图中警车从右侧驶出，因此应靠左侧减速让行，确保执行任务的警车通行无阻。', 11, 1, '2021-12-19 01:24:33', '2021-12-19 01:24:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4588, '行车中遇抢救伤员的救护车从本车道逆向驶来时，要怎样做？', '', '1', '4', NULL, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', 'A', '行车中遇正在执行任务的特殊车辆逆向驶来的时，应当靠边减速或停车让行，确保特殊车辆通行无阻。', 11, 1, '2021-12-19 01:24:33', '2021-12-19 01:24:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4589, '驾驶机动车遇到这种情形要迅速靠右侧减速让行。', '/uploadFile/yuanBeiTu589.gif', '2', '4', NULL, '', '', '', '', 'A', '行车中遇执行任务的警车、消防车、救护车、工程抢险车时，应及时避让；遇抢救伤员的救护车逆向驶来时，应靠边减速或停车让行。', 11, 1, '2021-12-19 01:24:34', '2021-12-19 01:24:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4590, '驾驶机动车遇到这种情形怎么办？', '/uploadFile/yuanBeiTu590.gif', '1', '4', NULL, '迅速从车左侧超越', '保持较大跟车距离', '连续鸣喇叭告知', '迅速从车右侧超越', 'B', '如图所示，驾驶机动车遇到前方车辆行驶轨迹异常时，首先要与其保持足够的距离，跟在后方注意观察，在保证安全的情况下尽可能地超过前车，要注意防范前车驾驶人可能存在酒驾、毒驾、疲劳驾驶等情况。', 11, 1, '2021-12-19 01:24:34', '2021-12-19 01:24:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4591, '驾驶机动车遇到这种情况怎么办？', '/uploadFile/yuanBeiTu591.gif', '1', '4', NULL, '紧跟前车后方行驶', '迅速从车左侧超越', '保持较大跟车距离', '迅速从车右侧超越', 'C', '驾驶机动车跟车行驶遇到前方有大型车时会导致视线不佳，可以通过加大跟车距离来扩大视野范围，预防跟随大型车通过时，信号灯突然变化；同时还可避免大型车紧急制动时与其追尾。', 11, 1, '2021-12-19 01:24:34', '2021-12-19 01:24:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4592, '驾驶机动车在交叉路口遇到这种情况如何对待？', '/uploadFile/yuanBeiTu592.gif', '1', '4', NULL, '直接进入路口内等待', '在路口停止线外等待', '从右侧非机动车道通过', '借对向车道通过路口', 'B', '驾驶机动车在交叉路口遇有路口交通阻塞时，即便是绿灯亮，也要停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。 【拓展知识】', 11, 1, '2021-12-19 01:24:34', '2021-12-19 01:24:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4593, '驾驶机动车在拥堵的路口遇到这种情况怎样处置？', '/uploadFile/yuanBeiTu593.gif', '1', '4', NULL, '逼其回原车道', '紧跟前车不让行', '礼让通行', '鸣喇叭开前照灯', 'C', '驾驶机动车在拥堵路口遇到其他机动车违法变道时，要及时减速避让，礼让通行，千万不要开赌气车。', 11, 1, '2021-12-19 01:24:35', '2021-12-19 01:24:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4594, '如动画所示，驾驶人应当怎样做？', '/uploadFile/yuanBeiTu594.gif', '1', '4', NULL, '寻找机会超越前车', '从机动车空间穿插通过', '减速停车，依次排队等候', '鸣喇叭催促', 'C', '行车中遇前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 11, 1, '2021-12-19 01:24:35', '2021-12-19 01:24:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4595, '驾驶机动车在路口遇到这种情况要随时准备停车礼让。', '/uploadFile/yuanBeiTu595.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在复杂的交叉路口，遇到路口内车辆较多时，要减速观察路口内车辆的通行情况，随时准备停车礼让。', 11, 1, '2021-12-19 01:24:35', '2021-12-19 01:24:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4596, '驾驶机动车在这种情况下要跟前车进入路口等待。', '/uploadFile/yuanBeiTu596.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在复杂的交叉路口，遇到路口内车辆较多时，要减速观察路口内车辆的通行情况，随时准备停车礼让，不得跟车进入路口内等候，以免被夹在路口内进退两难。', 11, 1, '2021-12-19 01:24:36', '2021-12-19 01:24:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4597, '驾驶机动车遇到这种行人应该注意什么？', '/uploadFile/yuanBeiTu597.gif', '1', '4', NULL, '在路中心行驶', '持续鸣喇叭', '加速超越', '注意观察动态', 'D', '驾驶机动车遇到行人在路边占道行走时，要减速慢行，注意观察行人动态，不得加速超越或持续鸣喇叭。', 11, 1, '2021-12-19 01:24:36', '2021-12-19 01:24:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4598, '驾驶机动车突然遇到这种情况怎样做？', '/uploadFile/yuanBeiTu598.gif', '1', '4', NULL, '减速或停车让行', '从行人前方绕行', '持续鸣喇叭提醒', '从行人后方绕行', 'A', '机动车进出道路或者在没有交通信号的路段行驶，应当减速或停车避让横过道路的行人。', 11, 1, '2021-12-19 01:24:36', '2021-12-19 01:24:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4599, '驾驶机动车遇到这种情形应该注意什么？', '/uploadFile/yuanBeiTu599.gif', '1', '4', NULL, '预防机动车侧滑', '预防行人横穿', '尽快加速通过', '持续鸣喇叭', 'B', '驾驶机动车看到道路一侧有人向对面招手时，要及时减速，注意观察，做好随时停车的准备，预防对面行人突然横穿道路。', 11, 1, '2021-12-19 01:24:37', '2021-12-19 01:24:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4600, '驾驶机动车遇到这种情况要如何行驶？', '/uploadFile/yuanBeiTu600.gif', '1', '4', NULL, '低速缓慢通过', '加速通过', '连续鸣喇叭通过', '保持正常车速通过', 'A', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 11, 1, '2021-12-19 01:24:37', '2021-12-19 01:24:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4601, '驾驶机动车遇到这样的情景怎样行驶？', '/uploadFile/yuanBeiTu601.gif', '1', '4', NULL, '连续鸣喇叭', '快速通过', '减速慢行', '从一侧绕行', 'C', '在居民小区遇到在路边玩耍的儿童时，要注意观察儿童的动态，减速缓慢通过，必要时停车避让，以免孩子们突然跑动被撞伤。驾驶人遇到这种情景，第一反应是要减速慢行。', 11, 1, '2021-12-19 01:24:37', '2021-12-19 01:24:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4602, '驾驶机动车看到这样的儿童怎样行驶？', '/uploadFile/yuanBeiTu602.gif', '1', '4', NULL, '紧跟在后面行驶', '从左侧加速让过', '鸣喇叭示意让道', '减速或停车避让', 'D', '驾驶机动车遇到在路中玩滑板的儿童时，应减速行驶，必要时停车礼让。', 11, 1, '2021-12-19 01:24:38', '2021-12-19 01:24:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4603, '驾驶机动车遇到这样的行人怎样礼让？', '/uploadFile/yuanBeiTu603.gif', '1', '4', NULL, '加速从前方绕过', '加速从身后绕行', '减速或停车让行', '连续鸣喇叭提醒', 'C', '驾驶机动车遇到缓慢横过道路或在路边行走的老年人，要提前降低车速，距离较近时要及时停车让行，切不可采取加速绕行或连续鸣喇叭催促的方法通行。', 11, 1, '2021-12-19 01:24:38', '2021-12-19 01:24:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4604, '驾驶机动车遇到这样的行人怎样行驶？', '/uploadFile/yuanBeiTu604.gif', '1', '4', NULL, '从其前方绕过', '从其身后绕行', '鸣喇叭提醒', '主动停车礼让', 'D', '驾驶机动车遇到行动不便的行人横过道路时，要及时减速或停车礼让；遇盲人通行时，应及时减速避让，不要鸣喇叭示意其让路，也不可采取绕行方法通过。', 11, 1, '2021-12-19 01:24:38', '2021-12-19 01:24:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4605, '驾驶机动车在雨天遇到撑雨伞和穿雨衣的行人在路边行走怎样礼让？', '', '1', '4', NULL, '以正常速度行驶', '临近鸣喇叭示意', '加速从左侧绕行', '提前减速鸣喇叭', 'D', '下雨天遇撑雨伞和穿雨衣的行人在道路上行走时，要提前鸣喇叭进行提示，并适当降低车速，注意观察其动态，保持一定的距离通过，同时要随时准备应付突发状况。', 11, 1, '2021-12-19 01:24:39', '2021-12-19 01:24:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4606, '行车中对出现这种行为的人不能礼让。', '/uploadFile/yuanBeiTu606.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇到翻越中间护栏的行人时，应迅速减速并鸣喇叭提示，注意观察行人动态，做好随时停车的准备，预防行人迅速跑向右侧路边。', 11, 1, '2021-12-19 01:24:39', '2021-12-19 01:24:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4607, '行车中遇到这种行人需要保持较大的安全距离。', '/uploadFile/yuanBeiTu607.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇到路边有挑担子的行人时，应提前减速行驶，适当鸣喇叭提示，与其保持一定的横向距离，注意观察行人动态，预防行人担子换肩或担子突然横出。', 11, 1, '2021-12-19 01:24:39', '2021-12-19 01:24:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4608, '驾驶机动车遇到这种情况时，要快速向左绕过。', '/uploadFile/yuanBeiTu608.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇到在路中玩耍的儿童时，应减速行驶，必要时停车礼让，以防碰撞追逐皮球的儿童。', 11, 1, '2021-12-19 01:24:39', '2021-12-19 01:24:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4609, '突然出现这种情况，驾驶人要及时减速或停车避让。', '/uploadFile/yuanBeiTu609.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇到正在过马路的儿童，应及时减速慢行，随时观察儿童动态，发现异常要及时停车避让。', 11, 1, '2021-12-19 01:24:40', '2021-12-19 01:24:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4610, '驾驶机动车遇到这种情况的行人可连续鸣喇叭催其让道。', '/uploadFile/yuanBeiTu610.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇行动缓慢的老年人，要提前降低车速，距离较近时要及时停车让行，切不可采取加速绕行或连续鸣喇叭催促的方法通行。', 11, 1, '2021-12-19 01:24:40', '2021-12-19 01:24:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4611, '驾驶机动车遇到校车在道路右侧停车上下学生，同向只有一条机动车道时，后方机动车应当停车等待。', '', '2', '4', NULL, '', '', '', '', 'A', '1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 2、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。 题目描述无误，因此本题答案为对。', 11, 1, '2021-12-19 01:24:40', '2021-12-19 01:24:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4612, '驾驶机动车遇到校车在道路右侧停车上下学生，同向有两条机动车道时，左侧车道后方机动车应当停车等待。', '', '2', '4', NULL, '', '', '', '', 'A', '1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 2、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。 题目描述无误，因此本题答案为对。', 11, 1, '2021-12-19 01:24:41', '2021-12-19 01:24:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4613, '驾驶机动车遇到校车在道路右侧停车上下学生，同向只有三条机动车道时，最左侧车道后方机动车应当停车等待。', '', '2', '4', NULL, '', '', '', '', 'B', '1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 2、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。', 11, 1, '2021-12-19 01:24:41', '2021-12-19 01:24:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4614, '驾驶机动车遇到校车在道路右侧停车上下学生时，应注意什么？', '', '3', '4', NULL, '同向只有一条机动车道，后方机动车应当停车等待', '同向有两条机动车道，左侧车道后方机动车可以减速通过', '同向有三条机动车道，中间车道后方机动车应当停车等待', '同向有三条机动车道，左侧车道后方机动车可以减速通过', 'ACD', '驾驶机动车遇到校车在道路右侧停车上下学生，校车后方车辆和同向行驶的相邻机动车道上的机动车要停车等待，不得鸣喇叭或者使用灯光催促校车；与校车同方向行驶的其他车道上的车辆应当减速通过。', 11, 1, '2021-12-19 01:24:41', '2021-12-19 01:24:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4615, '驾驶机动车看到这种标志需要注意什么？', '/uploadFile/yuanBeiTu615.gif', '1', '4', NULL, '减速、观察、慢行', '鸣喇叭驱赶牲畜', '从牲畜的空隙中穿过', '低速行驶冲开牲畜群', 'A', '驾驶机动车在动物保护区或牧区行驶时，要降低车速，随时注意避让动物。遇到占道或横过道路的野生动物或牲畜，应及时减速或停车让行。', 11, 1, '2021-12-19 01:24:41', '2021-12-19 01:24:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4616, '驾驶机动车在这样的路段要注意观察，随时避让横过道路的动物。', '/uploadFile/yuanBeiTu616.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在动物保护区或牧区行驶时，要降低车速，随时注意避让动物，保持较远距离；遇到占道或横过道路的野生动物或牲畜，应及时减速或停车让行，切忌连续鸣喇叭或加速绕行，以防引起牲畜骚动。', 11, 1, '2021-12-19 01:24:42', '2021-12-19 01:24:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4617, '驾驶机动车在这种情况下正确的做法是什么？', '/uploadFile/yuanBeiTu617.gif', '1', '4', NULL, '立即超越', '连续鸣喇叭提醒', '保持安全距离超越', '鸣喇叭加速超越', 'C', '成群青少年骑自行车喜欢追逐打闹或并排占道行驶，当超越他们时，应当提前鸣喇叭提示，减速慢行，随时准备停车。超越同向行驶的自行车时，注意观察其动态，减速慢行，保持安全距离，待左侧机动车通过之后，选择合适时机，从左侧绕行。', 11, 1, '2021-12-19 01:24:42', '2021-12-19 01:24:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4618, '驾驶机动车遇到这种情况怎样应对？', '/uploadFile/yuanBeiTu618.gif', '1', '4', NULL, '连续鸣喇叭警告', '加速从前方绕过', '出现危险再减速', '主动减速让行', 'D', '遇有非机动车和行人通过人行横道时，应当主动减速让行。', 11, 1, '2021-12-19 01:24:43', '2021-12-19 01:24:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4619, '驾驶机动车在这种情况下要尽快加速通过。', '/uploadFile/yuanBeiTu619.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 11, 1, '2021-12-19 01:24:43', '2021-12-19 01:24:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4620, '驾驶机动车遇到骑自行车人占道影响通行时，可连续鸣喇叭加速从其左侧绕行。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇有非机动车占道行驶时，驾驶人应主动减速让行。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', 11, 1, '2021-12-19 01:24:43', '2021-12-19 01:24:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4621, '驾驶机动车遇到成群青少年绕过路边停放的机动车时，要主动减速让行。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇到成群的青少年绕过路边停放的机动车时，要注意观察其动态，主动减速让行，保持安全距离，随时准备停车。连续鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', 11, 1, '2021-12-19 01:24:43', '2021-12-19 01:24:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4622, '驾驶机动车变更车道时，属于交通陋习的是什么行为？', '', '1', '4', NULL, '提前开启转向灯', '仔细观察后变更车道', '随意并线', '不得妨碍其他车道正常行驶的车', 'C', '变更车道时，不观察车辆两侧和后方道路交通情况，不开启转向灯，随意频繁变更车道或强行突然变道，连续侵占正常通行车辆的行驶路线会严重扰乱道路通行秩序，影响其他车辆的正常通行，容易导致道路拥堵和剐蹭、碰撞事故的发生。因此，变更车道时，随意并线属于交通陋习的行为。', 11, 1, '2021-12-19 01:24:44', '2021-12-19 01:24:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4623, '机动车在道路上行驶时，属于交通陋习的是什么行为？', '', '1', '4', NULL, '按规定使用灯光', '带行驶证、驾驶证', '随意向车外抛洒物品', '遵守交通信号', 'C', '驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证和机动车驾驶证；遵守交通信号，按规定使用灯光；禁止向道路上抛撒物品。', 11, 1, '2021-12-19 01:24:44', '2021-12-19 01:24:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4624, '发现前方机动车停车排队缓慢行驶时，属于交通陋习的是什么行为？', '', '1', '4', NULL, '加塞抢行', '不强行超车', '停车或依次行驶', '不占用非机动车道行驶', 'A', '驾驶车辆遇到前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 11, 1, '2021-12-19 01:24:44', '2021-12-19 01:24:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4625, '驾驶人边驾车边吸烟的做法有什么影响？', '', '1', '4', NULL, '妨碍安全驾驶', '可提高注意力', '可缓解驾驶疲劳', '不影响驾驶操作', 'A', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，另外缓解疲劳没有根据，因此本题选A。', 11, 1, '2021-12-19 01:24:44', '2021-12-19 01:24:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4626, '驾驶人一边驾车，一边吸烟对安全行车无影响。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶人一边驾车，一边吸烟属于分心驾驶，对安全行车非常不利。题目描述错误。 分心驾驶，就是驾驶人一边驾驶一边做或想其他使注意力分散的事情，这违背了安全驾驶的“集中注意力”这个黄金原则，对安全行车非常不利。 分心驾驶的危害在于：1、驾驶人注意力分散、视线离开路面，无法提前观察或自然忽略道路交通中的信息；2、引起驾驶姿势或操作动作的变化，不利于安全平稳操纵车辆。', 11, 1, '2021-12-19 01:24:45', '2021-12-19 01:24:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4627, '行车中不开转向灯强行并线是违法行为。', '', '2', '4', NULL, '', '', '', '', 'A', '转弯、掉头、并线等应该开启转向灯，这是规定。不开转向灯强行并线，很容易和后方车辆发生刮擦或碰撞，并且责任全部在你，是一个违反交通法的行为。', 11, 1, '2021-12-19 01:24:45', '2021-12-19 01:24:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4628, '驾驶人在确认后方无来车的情况下，可以不开转向灯变更车道。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车在变更车道时，应当提前开启转向灯，夜间还需变换使用远、近光灯或者鸣喇叭；在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶，安全变更车道后关闭转向灯。', 11, 1, '2021-12-19 01:24:45', '2021-12-19 01:24:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4629, '机动车行驶中遇有自行车借道通行时，可急促鸣喇叭示意让道。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车行驶中遇有自行车借道通行时，应主动减速让行。急促鸣喇叭示意其让道或加速绕过，容易使骑车人惊慌失措而摔倒，引起剐碰事故。', 11, 1, '2021-12-19 01:24:46', '2021-12-19 01:24:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4630, '驾驶人边驾车，边打手持电话是违法行为。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条 驾驶机动车不得有下列行为：(三)拨打接听手持电话、观看电视等妨碍安全驾驶的行为； 【拓展知识】 驾驶人一边驾驶一边做或想其他使注意力分散的事情，违背了安全驾驶的“集中注意力”这个黄金原则，对安全行车非常不利。', 11, 1, '2021-12-19 01:24:46', '2021-12-19 01:24:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4631, '在正常行车中，尽量靠近中心线或压线行驶，不给对向机动车留有侵占行驶路线的机会。', '', '2', '4', NULL, '', '', '', '', 'B', '首先，题目中的压线行驶就是错误行为。其次，行车时要往中心线靠拢，但是遇到对向来车时，要主动减速向右避让。', 11, 1, '2021-12-19 01:24:46', '2021-12-19 01:24:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4632, '女驾驶人穿高跟鞋驾驶机动车，不利于安全行车。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶人穿高跟鞋驾驶机动车，不利于安全行车，题干表述正确。因为，驾驶人高高的鞋跟，会使脚无法正常地踩刹车脚踏板，紧急情况下会出现踩不到底的情况，有很大安全隐患。此外，由于跟比较高，不仅踩的时候比较累，而且脚后跟会悬空，不利于驾驶人很好地控制脚踏板角度，在急刹车、踩油门时很容易产生操作不到位的情况，存在很大的风险。', 11, 1, '2021-12-19 01:24:47', '2021-12-19 01:24:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4633, '驾驶机动车时，长时间左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆球头，是一种驾驶陋习。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车时，长时间将左臂搭在车门窗上，或者长时间右手抓住变速器操纵杆球头等的行为，是一种驾驶陋习，驾驶人应摒弃驾驶陋习，文明驾驶，安全出行。', 11, 1, '2021-12-19 01:24:47', '2021-12-19 01:24:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4634, '驾驶人频繁变更车道不属于驾驶陋习。', '', '2', '4', NULL, '', '', '', '', 'B', '频繁变更车道，连续侵占正常通行车辆的行驶路线会严重扰乱道路通行秩序，影响其他车辆的正常通行，容易导致道路拥堵和剐蹭、碰撞事故的发生。每变更一次车道，就会隐含着一次风险，因此禁止频繁变更车道。', 11, 1, '2021-12-19 01:24:47', '2021-12-19 01:24:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4635, '在路口遇到这种情况的行人，如何做到礼让？', '/uploadFile/yuanBeiTu635.gif', '1', '4', NULL, '在远处鸣喇叭催促', '从行人间低速穿过', '加速从行人前绕过', '停车等待行人通过', 'D', '驾驶机动车遇行人通过人行横道时，要及时停车让行，不得抢行或绕行。右转弯通行时，更要注意礼让通过人行横道的行人。', 11, 1, '2021-12-19 01:24:48', '2021-12-19 01:24:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4636, '夜间会车时，对面来车不关闭远光灯怎么办？', '', '3', '4', NULL, '及时减速让行，必要时靠边停车', '开启远光灯，迫使来车变换灯光', '视线向右平移，防止眩目', '交替变换远近光灯，提醒来车', 'ACD', '夜间会车，遇对向车辆不关闭远光灯时，①交替使用远近光灯提示对方，不要开启远光灯对射；②不要直视对向车辆的灯光，视线向右避开，以防眩光刺眼；③在不确定前方和右侧道路交通情况时，要及时减速避让，必要时停车让行，不可盲目前行或靠右让行，以防撞上路边的行人、非机动车等。', 11, 1, '2021-12-19 01:24:48', '2021-12-19 01:24:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4637, '超车过程中，被超车辆突然加速怎么办？', '', '1', '4', NULL, '加速迅速超越', '变换远近光灯超越', '减速放弃超车', '持续鸣喇叭超越', 'C', '超车过程中，被超车辆突然加速，应该减速放弃超车，否则容易引发事故。', 11, 1, '2021-12-19 01:24:48', '2021-12-19 01:24:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4638, '遇到这种前方拥堵路段通行缓慢时怎样行驶？', '/uploadFile/yuanBeiTu638.gif', '1', '4', NULL, '依次跟车行驶', '从右侧超越', '靠边停车等待', '从左侧超越', 'A', '《道路交通安全法实施条例》第五十三条 机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 11, 1, '2021-12-19 01:24:49', '2021-12-19 01:24:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4639, '雨天遇到这些撑雨伞和穿雨衣的行人在路边行走怎样通行？', '/uploadFile/yuanBeiTu639.gif', '3', '4', NULL, '注意观察行人动态', '适当降低车速', '保持安全距离', '提前轻按喇叭提醒', 'ABCD', '雨天行驶，一定要选择安全车速，同时要与其他车辆、行人等保持足够的安全距离。遇撑雨伞和穿雨衣的行人在道路上行走时，因雨伞或雨衣易造成行人视觉、听觉受阻，反应慢、不灵敏，因此要提前减速、鸣喇叭进行提示，密切注意观察其动态，保持一定的安全距离低速通过，同时要随时准备应付突发状况。', 11, 1, '2021-12-19 01:24:49', '2021-12-19 01:24:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4640, '驾驶汽车遇到牲畜横穿抢道的情况，要及时鸣喇叭进行驱赶。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇到牲畜横穿抢道时，应主动及时减速，必要时停车避让，不可鸣喇叭惊吓牲畜，导致事故。', 11, 1, '2021-12-19 01:24:49', '2021-12-19 01:24:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4641, '遇到这种同向行驶的非机动车时怎样行驶？', '/uploadFile/yuanBeiTu641.gif', '3', '4', NULL, '注意观察动态', '适当减速慢行', '保持安全间距', '鸣喇叭加速超越', 'ABC', '驾驶机动车遇同向行驶的非机动车时，注意观察其动态，减速慢行，保持安全距离，不得鸣喇叭加速超越。', 11, 1, '2021-12-19 01:24:49', '2021-12-19 01:24:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4642, '行车中不应该有以下哪些行为？', '', '3', '4', NULL, '经常观察后视镜', '变更车道不开启转向灯', '左臂长时间搭在车门窗上', '长时间抓变速杆', 'BCD', '驾驶机动车禁止行为：1、在车门、车厢没有关好时行车；2、驾驶时的前后窗范围内悬挂、放置妨碍驾驶人视线的物品；3、变更车道不开启转向灯、拨打接听手持电话、观看电视、左臂长时间搭在车门窗上、长时间抓变速杆等妨碍安全驾驶的行为；4、向道路抛撒物品等。', 11, 1, '2021-12-19 01:24:50', '2021-12-19 01:24:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4643, '驾驶汽车在交叉路口违法抢行容易引发交通事故。', '', '2', '4', NULL, '', '', '', '', 'A', '路口交通情况复杂，驾驶汽车在交叉路口违法抢行容易引发交通事故。', 11, 1, '2021-12-19 01:24:50', '2021-12-19 01:24:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4644, '变更车道或超车不开启转向灯属于违法行为。', '', '2', '4', NULL, '', '', '', '', 'A', '变更车道或超车开转向灯是规定，不开就是违法行为。现在的违规，违章统称违法行为。因此本题描述正确。', 11, 1, '2021-12-19 01:24:50', '2021-12-19 01:24:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4645, '关于超车，以下说法正确的是什么?', '', '3', '4', NULL, '超车前提前开启左转向灯，提醒前方被超车辆驾驶人', '切换远、近光灯提醒前方被超车辆驾驶人', '长时间鸣喇叭警示被超车辆驾驶人', '完成超车后并回行车道要开启右转向灯', 'ABD', '机动车超车时，要保持与被超越车辆的安全距离，观察左侧交通情况，选择合理时机，提前开启左转向灯、变换使用远近光灯或者鸣喇叭，从被超越车辆的左侧超越，超越后，在不影响被超越车辆正常行驶的情况下，开启右转向灯，逐渐驶回原车道。', 11, 1, '2021-12-19 01:24:51', '2021-12-19 01:24:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4646, '如何为特种车辆让路?', '', '1', '4', NULL, '向左减速让路，直到紧急车辆过去', '向右减速让路，直到紧急车辆过去', '立即停车，即使你在交叉口也是如此', '向前车鸣喇叭，自己在特种车辆前开路', 'B', '《道路交通安全法》第五十三条：警车、消防车、救护车、工程救险车执行紧急任务时，可以使用警报器、标志灯具；在确保安全的前提下，不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。警车、消防车、救护车、工程救险车非执行紧急任务时，不得使用警报器、标志灯具，不享有前款规定的道路优先通行权。让路即靠右边减速避让，确保特种车辆通行无阻。', 11, 1, '2021-12-19 01:24:51', '2021-12-19 01:24:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4647, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么?', '/uploadFile/yuanBeiTu647.gif', '1', '4', NULL, '放慢车速，缓缓绕过', '鸣喇叭示意该车让路', '立即停车等待，直至该车离开', '保持原车速绕行', 'C', '校车左侧有停车让行标志，表示前方校车有学生下车，遇到此情况，机动车驾驶员需要立即停车等待，直至校车离开。', 11, 1, '2021-12-19 01:24:51', '2021-12-19 01:24:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4648, '当您遇到以下车辆时，需要礼让的是什么?', '', '3', '4', NULL, '救护车', '消防车', '警车', '校车', 'ABCD', '【避让特种车辆及校车】驾驶机动车遇到执行紧急任务的警车、消防车、救护车、工程救险车时，要及时让行；遇到正在进行作业的道路养护车辆、工程作业车时要注意避让；校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。', 11, 1, '2021-12-19 01:24:52', '2021-12-19 01:24:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4649, '行车中遇牲畜通过道路影响通行时，可采取连续鸣喇叭的方式进行驱赶。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在动物保护区或牧区行驶时，要降低车速，随时注意避让动物；遇到占道或横过道路的野生动物或牲畜，应及时减速或停车让行，切忌连续鸣喇叭或加速绕行，以防引起牲畜骚动。', 11, 1, '2021-12-19 01:24:52', '2021-12-19 01:24:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4650, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu650.gif', '3', '4', NULL, '停车等待动物穿过', '鸣喇叭驱赶动物', '下车驱赶动物', '与动物保持较远距离', 'AD', '驾驶机动车在动物保护区或牧区行驶时，要降低车速，随时注意避让动物，保持较远距离；遇到占道或横过道路的野生动物或牲畜，应及时减速或停车让行，切忌连续鸣喇叭或加速绕行，以防引起牲畜骚动。', 11, 1, '2021-12-19 01:24:52', '2021-12-19 01:24:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4651, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu651.gif', '3', '4', NULL, '减速缓慢通过', '鸣喇叭警示牲畜，以免牲畜冲入行车道发生事故', '不能鸣喇叭，避免牲畜因惊吓窜入行车道', '加速通过此事故隐患路段', 'AC', '行车中遇牲畜时，应减速缓慢通过，与其保持较大的安全间距，切忌临近时鸣喇叭或加速绕行，以防引起牲畜骚动。', 11, 1, '2021-12-19 01:24:53', '2021-12-19 01:24:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4652, '驾驶机动车行经两侧有非机动车行驶且有积水的路面时，应怎样做？', '', '1', '4', NULL, '减速慢行', '正常行驶', '加速通过', '连续鸣喇叭', 'A', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 11, 1, '2021-12-19 01:24:53', '2021-12-19 01:24:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4653, '驾驶机动车在转弯之前应留意旁边行驶的自行车，是因为自行车比较小，不太容易被看到。', '', '2', '4', NULL, '', '', '', '', 'A', '相比机动车，自行车体积较小，容易被车身遮挡到，不容易被看到。机动车做任何操作前都要留意到旁边的自行车。', 11, 1, '2021-12-19 01:24:53', '2021-12-19 01:24:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4654, '当你看到前方车辆行驶线路左右摆动，以下说法正确的是什么？', '', '1', '4', NULL, '车内驾驶人可能为酒后驾车，应当保持距离', '车内驾驶人可能为酒后驾车，可以加速超过', '车内驾驶人可能为吸毒后驾车，可以加速超过', '此情况较为安全，可以紧密跟随', 'A', '跟车行驶时要警惕异常车辆。看到前方车辆行驶路线左右摆动时，首先要与其保持足够的距离，跟在后方注意观察，在保证安全的情况下尽可能地超过前车，要注意防范前车驾驶人可能存在酒驾、毒驾、疲劳驾驶等情况。', 11, 1, '2021-12-19 01:24:53', '2021-12-19 01:24:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4655, '驾驶机动车，遇前方停驶的油料运输车起火冒烟，以下做法正确的是什么？', '', '1', '4', NULL, '为减少交会时间，加速通过', '立即停车，上前查看是否有被困人员', '停车后围观', '立即停车，尽量远离，拨打报警电话', 'D', '油料运输车装载的是易燃易爆品，在已经起火冒烟时，随时有可能爆炸，应该立即停车，尽量远离，拨打报警电话！生命安全更重要，不要贸然施救不要靠近不要围观。', 11, 1, '2021-12-19 01:24:54', '2021-12-19 01:24:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4656, '如图所示，当您看到这个标志时，应该想到什么？', '/uploadFile/yuanBeiTu656.gif', '3', '4', NULL, '前方有人行横道', '应当相应减速行驶', '视野范围内无行人可以保持原速行驶', '视野范围内无行人可以适当加速通过', 'AB', '', 11, 1, '2021-12-19 01:24:54', '2021-12-19 01:24:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4657, '驾驶机动车在隧道内行驶，车辆出现故障时，应该立刻靠边停车，拦截过往车辆，帮助检修。', '', '2', '4', NULL, '', '', '', '', 'B', '隧道内禁止停车，既然车辆还在行驶，就应该减速慢行坚持驶离隧道并在安全的区域停车处理。', 11, 1, '2021-12-19 01:24:55', '2021-12-19 01:24:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4658, '如图所示，驾驶机动车在这样的山区道路跟车行驶时，由于视线受阻，应预防前车突发情况和对向来车，适当减速加大跟车距离保证安全。', '/uploadFile/yuanBeiTu658.gif', '2', '4', NULL, '', '', '', '', 'A', '在山区道路环境易突变，行车跟车需要增大跟车距离，适当减速，保证自己的行车安全。', 11, 1, '2021-12-19 01:24:55', '2021-12-19 01:24:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4659, '驾驶机动车在山区道路行驶时，应该尽量避免停车，如确实需要停车，尽量选择平直的路段。', '', '2', '4', NULL, '', '', '', '', 'A', '在山区道路不管是上坡路、下坡路或是坡顶都有可能让所停车辆发生溜车，应该尽量避免停车，如确实需要停车，应该尽量选择平直的路段。', 11, 1, '2021-12-19 01:24:55', '2021-12-19 01:24:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4660, '驾驶机动车下长坡时，车速会因为重力作用越来越快，以下控制车速方法正确的是什么？', '', '1', '4', NULL, '空挡滑行', '减挡，充分利用发动机制动', '踏下离合器滑行', '长时间使用驻车制动器制动', 'B', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降，控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速。', 11, 1, '2021-12-19 01:24:55', '2021-12-19 01:24:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4661, '驾驶机动车在下坡行驶过程中行车制动器失效，以下做法正确的是什么？', '', '3', '4', NULL, '驶入紧急避险车道', '使用发动机制动', '使用驻车制动器制动', '必要时，可用车体刮擦路边障碍物减速', 'ABCD', '驾驶机动车在下坡路行驶中制动突然失效时，要迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶，使用驻车制动器配合减速，但驻车制动器不能一次拉紧。不可采用迅速拉紧驻车制动器操作杆或越多级减挡的方法减速停车。 在不得已的情况下，可将车身靠向山坡、路旁的岩石、树木或护栏碰擦，迫使机动车减速停车。', 11, 1, '2021-12-19 01:24:56', '2021-12-19 01:24:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4662, '如图所示，驾驶机动车驶近这样的山区弯道时，驾驶人应注意的是什么？', '/uploadFile/yuanBeiTu662.gif', '3', '4', NULL, '对向可能有车辆驶来', '前方骑自行车者可能由于上坡等原因突然改变方向', '山区弯道可能转弯半径较小，车速过快容易引起车辆失控', '转弯后路面可能存在落石、凹陷等特殊路况', 'ABCD', '通过山区弯道时，要提前减速，注意观察，预防以下险情：1、弯道高速行驶，车辆容易发生侧翻；2、弯道盲区内，可能存在落石及停驶的故障车等障碍；3、弯道盲区内，可能有对向车辆占道行驶或对向来车占道超车；4、通过急弯处时不要超车，防止前方有车辆或行人出现而措手不及。', 11, 1, '2021-12-19 01:24:56', '2021-12-19 01:24:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4663, '如图所示，驾驶机动车遇到这种情况时，应注意哪些情况？', '/uploadFile/yuanBeiTu663.gif', '3', '4', NULL, '我方车辆灯光照向路外，前方即将进入弯道', '前方有灯光出现，可能即将发生会车', '左前方视线受阻，转弯后可能遇到突发情况', '为提高会车安全，改用远光灯', 'ABC', '如图所示，我方灯光照射由路中移到路侧，表明前方即将进入弯道，左前方视线受阻，转弯后可能遇到突发情况；前方有灯光，表明对向有来车，即将发生会车，应及时减速靠右侧行驶或停车让行；会车时不得使用远光灯。', 11, 1, '2021-12-19 01:24:56', '2021-12-19 01:24:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4664, '如图所示，驾驶机动车路遇右前方施工路段，应提前减速慢行。', '/uploadFile/yuanBeiTu664.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车行经施工路段时，驾驶人一定要注意交通信号的提示，提前控制车速，保持好适当的安全车距，掌握好方向，减速慢行，注意礼让。', 11, 1, '2021-12-19 01:24:57', '2021-12-19 01:24:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4665, '雾天驾驶机动车跟车行驶，以下做法错误的是什么？', '', '1', '4', NULL, '加大两车间的距离', '时刻注意前车刹车灯的变化', '降低行车速度', '鸣喇叭提醒前车提高车速，避免后车追尾', 'D', '请注意，题干要求选出做法错误的选项。雾天跟车距离较近，容易发生追尾事故，因此，驾驶人一定要控制车速，加大与前车的安全距离，可多使用喇叭引起车辆注意，但是鸣喇叭提醒前车加速的做法是错误的。', 11, 1, '2021-12-19 01:24:57', '2021-12-19 01:24:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4666, '如图所示，雾天驾驶机动车行驶，旁边车道无车时，可变更车道，快速超越前车。', '/uploadFile/yuanBeiTu666.gif', '2', '4', NULL, '', '', '', '', 'B', '雾天视线受阻，上道路行驶应减速并增大安全距离，快速变更车道超车容易引发事故。因此本题描述错误。', 11, 1, '2021-12-19 01:24:57', '2021-12-19 01:24:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4667, '车辆在泥泞、溜滑路面上猛转方向时，易导致行驶方向失控，容易发生危险。', '', '2', '4', NULL, '', '', '', '', 'A', '在泥泞、溜滑路面上行车时，路面摩擦力较小，容易发生打滑现象。猛转方向时，就更易导致行驶方向失控。因此本题描述正确。', 11, 1, '2021-12-19 01:24:57', '2021-12-19 01:24:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4668, '驾驶机动车在高速公路匝道上行驶，前方车辆速度过慢，可以超车。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第八十二条，机动车在高速公路上行驶，不得有下列行为： (一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； (二)在匝道、加速车道或者减速车道上超车； (三)骑、轧车行道分界线或者在路肩上行驶； (四)非紧急情况时在应急车道行驶或者停车； (五)试车或者学习驾驶机动车。', 11, 1, '2021-12-19 01:24:58', '2021-12-19 01:24:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4669, '驾驶机动车在高速公路上驶出匝道时，只要后方无来车，或者来车相距较远，可以不经过加速车道，直接驶入行车道。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车驶出匝道时，一定要注意：不得从匝道直接驶入行车道；从加速车道驶入行车道时不得影响其他车辆的正常行驶。从加速车道驶入行车道时，要注意观察行车道内的情况，在车辆稀少时从正常行驶车辆后方驶入行车道；遇正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道，不得迅速从中间插入。', 11, 1, '2021-12-19 01:24:58', '2021-12-19 01:24:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4670, '驾驶机动车在高速公路行驶，由加速车道汇入行车道时，操纵方向盘不应该过急过猛。', '', '2', '4', NULL, '', '', '', '', 'A', '从加速车道驶入行车道时，方向盘操作不得过急过猛，不得影响其他车辆的正常行驶。从加速车道驶入行车道时，要注意观察行车道内的情况，在车辆稀少时从正常行驶车辆后方驶入行车道；遇正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道，不得迅速从中间插入。', 11, 1, '2021-12-19 01:24:58', '2021-12-19 01:24:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4671, '驾驶机动车驶离高速公路时，在进入减速车道前，应提前开启右转向灯，警示后方车辆。', '', '2', '4', NULL, '', '', '', '', 'A', '驶离高速公路主车道的最佳时机是行至离出口500m处，打开右转向灯，适当调整车速，逐渐平顺地从减速车道始端驶入减速车道。（左进右出）', 11, 1, '2021-12-19 01:24:58', '2021-12-19 01:24:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4672, '驾驶机动车进入减速车道后，应平顺减速，避免猛烈制动，同时注意保持与前车车距。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车进入减速车道后，应适当减速，平稳地转动转向盘，避免猛烈制动，同时注意保持与前车的安全距离。', 11, 1, '2021-12-19 01:24:59', '2021-12-19 01:24:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4673, '如图所示，驾驶机动车在乡间道路上行驶，以下做法正确的是什么？', '/uploadFile/yuanBeiTu673.gif', '1', '4', NULL, '在成人和儿童之间快速通过', '连续鸣喇叭提示后通过', '从成人身后绕行', '减速鸣喇叭提示，做好随时停车准备', 'D', '驾驶机动车在乡间道路上行驶，遇前方有儿童玩耍时，一定要提前鸣喇叭提示，并减速慢行，随时做好停车准备，防止临近时鸣喇叭使儿童慌不择路，出现危险。', 11, 1, '2021-12-19 01:24:59', '2021-12-19 01:24:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4674, '如图所示，驾驶机动车遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu674.gif', '1', '4', NULL, '适当鸣喇叭，加速通过', '在行人或骑车人通过前提前加速通过', '减速，停车让行', '连续鸣喇叭使其让行', 'C', '如图所示为：机动车前方有注意行人标志，而且有行人在过马路，应该减速慢行或停车让行。保证行车安全。', 11, 1, '2021-12-19 01:24:59', '2021-12-19 01:24:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4675, '如图所示，夜间驾驶机动车行经没有行人通过的人行横道时可加速通过。', '/uploadFile/yuanBeiTu675.gif', '2', '4', NULL, '', '', '', '', 'B', '夜间通过没有行人通过的人行横道时，应在距路口约100米处减速慢行，并且交替使用远、近光灯示意，停车瞭望确认安全后，低速通过。', 11, 1, '2021-12-19 01:25:00', '2021-12-19 01:25:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4676, '如图所示，驾驶机动车遇到右侧车道车辆突然变更车道时，应当如何避让？', '/uploadFile/yuanBeiTu676.gif', '1', '4', NULL, '减速让行', '加速行驶', '向左打转向迅速超越', '连续鸣喇叭', 'A', '行车中遇到右侧车道车辆突然变道时，要减速让行，以免发生剐碰危险。', 11, 1, '2021-12-19 01:25:00', '2021-12-19 01:25:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4677, '如图所示，夜间驾驶机动车遇到其他机动车突然驶入本车道，可加速从右侧车道绕行。', '/uploadFile/yuanBeiTu677.gif', '2', '4', NULL, '', '', '', '', 'B', '夜间驾驶机动车遇到其他机动车突然驶入本车道，要减速或靠右停车避让，千万不要开赌气车加速从右侧车道绕行。', 11, 1, '2021-12-19 01:25:00', '2021-12-19 01:25:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4678, '如图所示，驾驶机动车行经交叉路口遇到这种情况时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu678.gif', '1', '4', NULL, '加速通过', '在骑车人通过前提前加速通过', '停车让行', '连续鸣喇叭使其让行', 'C', '机动车在没有交通信号的路段行驶，应当注意避让横过道路的非机动车，必要时停车让行。如图所示路况，驾驶人应停车礼让非机动车先行。', 11, 1, '2021-12-19 01:25:01', '2021-12-19 01:25:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4679, '如图所示，A车在这样的路口可以借用非机动车道右转弯。', '/uploadFile/yuanBeiTu679.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第三十六条　根据道路条件和通行需要，道路划分为机动车道、非机动车道和人行道的，机动车、非机动车、行人实行分道通行。没有划分机动车道、非机动车道和人行道的，机动车在道路中间通行，非机动车和行人在道路两侧通行。 如图所示，A车占用非机动车道，且前方有非机动车行驶，因此不可以借用非机动车道右转。', 11, 1, '2021-12-19 01:25:01', '2021-12-19 01:25:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4680, '如图所示，驾驶机动车右转遇到这种情况时，可以不给非机动车和行人让行。', '/uploadFile/yuanBeiTu680.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，右转弯车辆只代表可以通行，但并不意味着路口处安全无风险，应注意避让行人和非机动车，随时做好停车的准备，即使有优先通行权，也要注意避让。', 11, 1, '2021-12-19 01:25:01', '2021-12-19 01:25:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4681, '如图所示，驾驶机动车在雨天行驶遇到这种情形时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu681.gif', '3', '4', NULL, '随时准备停车', '减速行驶，防止泥水溅到行人身上', '减速行驶，注意行人动态', '鸣喇叭提醒行人后，加速通过', 'ABC', '下雨天遇撑雨伞和穿雨衣的行人在道路上行走时，要提前鸣喇叭进行提示，并适当降低车速，防止泥水溅到行人身上，同时注意观察其动态，保持一定的距离通过，随时准备应付突发状况。', 11, 1, '2021-12-19 01:25:01', '2021-12-19 01:25:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4682, '夜间驾驶机动车在窄路遇到对面驶来非机动车时，以下做法正确的是什么？', '', '1', '4', NULL, '连续变换远近光灯', '开启危险报警闪光灯', '使用远光灯，减速避让', '使用近光灯，减速避让', 'D', '夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时，应当使用近光灯。', 11, 1, '2021-12-19 01:25:02', '2021-12-19 01:25:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4683, '驾驶机动车行经驼峰桥会车时，以下做法正确的是什么？', '', '3', '4', NULL, '降低车速', '靠右通行', '鸣喇叭示意', '抢行通过', 'ABC', '驾驶机动车行经驼峰桥会车时，要减速慢行，鸣喇叭示意，同时靠右通行谨慎驾驶，不可加速强行通过，以免发生碰撞事故。', 11, 1, '2021-12-19 01:25:02', '2021-12-19 01:25:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4684, '驾驶机动车，遇到后方执行任务的特种车辆时，以下做法正确的是什么？', '', '1', '4', NULL, '主动减速让行', '加速行驶', '即使有让行条件也不让', '靠道路中心行驶', 'A', '驾驶机动车，遇到后方执行任务的特种车辆时，应主动减速让行。', 11, 1, '2021-12-19 01:25:02', '2021-12-19 01:25:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4685, '同方向有三条机动车道的路段，校车在右侧车道停靠上、下学生时，校车停靠车道后方和相邻机动车道上的机动车应停车等待。', '', '2', '4', NULL, '', '', '', '', 'A', '校车在道路上停车上下学生，应当靠道路右侧停靠，开启危险报警闪光灯，打开停车指示标志。 1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 2、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。', 11, 1, '2021-12-19 01:25:03', '2021-12-19 01:25:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4686, '驾驶机动车遇到非机动车违法在机动车道上行驶，并阻碍机动车前进时，以下做法错误的是什么？', '', '1', '4', NULL, '注意非机动车辆的动向，减速行驶', '谨慎驾驶低速通过', '持续鸣喇叭警告非机动车避让', '保持与非机动车安全车距', 'C', '请注意，题干要求选出做法错误的选项。行车中遇前方非机动车影响通行时，减速并注意观察其动态，预留足够的横向安全距离，从左侧绕行，防止骑车人摔倒或突然改变骑行方向。因此，持续鸣喇叭警告的做法错误。', 11, 1, '2021-12-19 01:25:03', '2021-12-19 01:25:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4687, '驾驶机动车通过积水路段时，应注意两侧的行人和非机动车，降低车速，防止路面积水飞溅。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车行经有积水路段时，应当减速慢行或避让，不得加速通过，以免溅起的泥水飞溅到路边行人身上。', 11, 1, '2021-12-19 01:25:03', '2021-12-19 01:25:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4688, '驾驶机动车应尽量骑轧可跨越车道分界线行驶，便于根据前方道路情况选择车道。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车长时间轧分道线或道路中心虚线行驶属于违法占道行驶行为。严重地扰乱了道路交通秩序，直接影响道路的畅通，在单项行车道长时间轧线或占道行驶，会阻碍同方向车辆正常行驶，使后方车辆无法超越，造成道路拥堵，甚至导致交通事故。', 11, 1, '2021-12-19 01:25:03', '2021-12-19 01:25:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4689, '如图所示，前车通过积水路段的方式是不文明的。', '/uploadFile/yuanBeiTu689.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车行经有积水路段时，应当减速慢行或避让，不得加速通过，以免溅起的泥水飞溅到路边行人身上。因此，前车的行驶方式是不文明的。', 11, 1, '2021-12-19 01:25:04', '2021-12-19 01:25:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4690, '夜间驾驶机动车遇到对向来车未关闭远光灯时，以下做法正确的是什么？', '', '1', '4', NULL, '变换使用远近光灯提示', '长时间鸣喇叭', '使用远光灯', '鸣喇叭并使用远光灯', 'A', '夜间会车遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。因为远光灯会使对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 11, 1, '2021-12-19 01:25:04', '2021-12-19 01:25:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4691, '停车后，驾驶人应当提醒乘车人开启车门前注意观察后方来车。', '', '2', '4', NULL, '', '', '', '', 'A', '停车后，驾驶人应当提醒乘车人开启车门前注意观察后方来车，以防发生意外。', 11, 1, '2021-12-19 01:25:04', '2021-12-19 01:25:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4692, '驾驶机动车在遇到有前方机动车停车排队等候或者缓慢行驶时，可进入网状线区域停车等候。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 11, 1, '2021-12-19 01:25:05', '2021-12-19 01:25:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4693, '驾驶机动车在山区道路下坡路段尽量避免超车，以下说法正确的是什么？', '', '3', '4', NULL, '下坡路段车辆由于重力作用，车速容易过快', '下坡路段由于重力作用，车辆比平路时操控困难', '下坡路段车辆阻力很大', '下坡路段前车车速较快，难以超越', 'AB', '驾驶机动车在山区道路下坡路段尽量避免超车，因为下坡路段车辆由于重力作用，车速容易过快，车辆比平路时更难操控，此时超车容易发生危险。 （重力加速度，下坡路段阻力较平地小，时间越久速度越快，题目问下坡尽量避免超车的理由 而前车车速较快不是不能超车的理由）', 11, 1, '2021-12-19 01:25:05', '2021-12-19 01:25:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4694, '如图所示，驾驶机动车遇到这种情况时，应注意哪些可能发生的危险？', '/uploadFile/yuanBeiTu694.gif', '3', '4', NULL, '行人通过速度较慢可能滞留在道路内', '电动自行车可能发生故障无法及时通过道路', '右侧驶来的机动车可能未能及时停车而进入路口', '考虑雨天路面湿滑，应注意按照限速规定控制车速', 'ABCD', '雨天行车，路人、电动车横穿人行横线时红灯变绿灯，驾驶机动车遇到这种情况时，应注意可能出现以下情况：行人通过速度较慢可能滞留在道路内、电动自行车可能发生故障无法及时通过道路、右侧驶来的机动车可能未能及时停车而进入路口，考虑雨天路面湿滑，应注意按照限速规定控制车速。 【拓展延伸】 有的学员认为D项“按照限速规定控制车速”指的还是按照正常天气时道路的限速行驶，一般在雨天会有临时限速，会通过广播、短信、道路电子屏幕等形式告知司机，临时限速会比原本限速要低，司机按规定开是没有问题的。', 11, 1, '2021-12-19 01:25:05', '2021-12-19 01:25:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4695, '如图所示，驾驶机动车路遇这种情况时，应注意施工地点情况，预防前方机动车由于异常情况紧急减速停车，应提前减速慢行，小心谨慎通过。', '/uploadFile/yuanBeiTu695.gif', '2', '4', NULL, '', '', '', '', 'A', '雨天驾驶机动车行经施工地段时，应提前减速慢行，注意观察施工地点情况，预防前方机动车由于异常情况紧急减速停车，小心谨慎通过。', 11, 1, '2021-12-19 01:25:06', '2021-12-19 01:25:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4696, '驾驶机动车在高速公路减速车道上行驶时，如遇前方有低速行驶的车辆，应伺机超车，以防止交通堵塞。', '', '2', '4', NULL, '', '', '', '', 'B', '根据《道路交通安全法实施条例》第八十二条第二项规定：机动车在高速公路上行驶，不得有下列行为： (一)倒车、逆行、穿越中央分隔带掉头或者在车道内停车； (二)在匝道、加速车道或者减速车道上超车； (三)骑、轧车行道分界线或者在路肩上行驶； (四)非紧急情况时在应急车道行驶或者停车； (五)试车或者学习驾驶机动车。', 11, 1, '2021-12-19 01:25:06', '2021-12-19 01:25:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4697, '驾驶机动车下长坡时，连续使用行车制动器，以下说法正确的是什么？', '', '1', '4', NULL, '会缩短发动机寿命', '增加车辆油耗', '会使制动器温度升高而使制动效能急剧下降', '容易造成车辆倾翻', 'C', '驾驶机动车下长坡或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。下长坡如果持续用刹车，会造成刹车片发热，从而导致刹车力减小，严重的话可能会丧失刹车能力导致刹车失灵。下坡时，控制车速最好的方法是使用低速挡行驶，成分利用发动机制动减速。', 11, 1, '2021-12-19 01:25:06', '2021-12-19 01:25:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4698, '驾驶机动车在山区上坡路段行驶，以下做法正确的是什么？', '', '3', '4', NULL, '应尽量匀速前进', '应尽量避免换挡', '时刻注意下行车辆', '应选择高速挡', 'ABC', '驾驶机动车在山区上坡路段行驶，要提前减速减挡，保持不换挡能一气爬到坡顶的动力，还要时刻注意观察对向交通情况，谨慎驾驶。', 11, 1, '2021-12-19 01:25:07', '2021-12-19 01:25:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4699, '驾驶机动车在山区道路上坡路段接近坡顶时，超车存在风险，以下说法正确的是什么？', '', '3', '4', NULL, '接近坡顶时视线受阻，无法观察坡顶之后道路走向', '接近坡顶时视线受阻，无法观察对向来车情况', '接近坡顶时车速较慢', '接近坡顶时视线受阻，无法观察坡顶之后是否有障碍物', 'ABD', '', 11, 1, '2021-12-19 01:25:07', '2021-12-19 01:25:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4700, '夜间驾驶机动车起步应首先开启近光灯。', '', '2', '4', NULL, '', '', '', '', 'A', '夜间驾驶机动车起步前，应先开启近光灯，仔细观察道路及周边情况，确认安全后再起步打转向灯。', 11, 1, '2021-12-19 01:25:07', '2021-12-19 01:25:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4701, '夜间驾驶机动车会车时，对方一直使用远光灯，以下做法正确的是什么？', '', '3', '4', NULL, '不停变换远近光灯以及鸣喇叭提醒对方', '视线适当右移，避免直视灯光', '降低车速，靠右行驶', '变换远光灯行驶', 'BC', '夜间会车，遇对向车辆一直不关闭远光灯时，不要直视对向车辆的灯光，视线向右避开，以防眩光刺眼；要及时减速靠右侧行驶或停车让行。', 11, 1, '2021-12-19 01:25:07', '2021-12-19 01:25:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4702, '如图所示，在这种情况下通过路口，驾驶人应减速或者停车观察，以应对两侧路口可能出现的危险。', '/uploadFile/yuanBeiTu702.gif', '2', '4', NULL, '', '', '', '', 'A', '雨天驾驶机动车行经交叉路口时，应减速或停车观察，以对两侧路口可能出现的危险做出正确的应对。', 11, 1, '2021-12-19 01:25:08', '2021-12-19 01:25:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4703, '如图所示，雾天驾驶机动车行驶时，玻璃上出现因雾气形成的小水珠时，及时用雨刮器刮净。', '/uploadFile/yuanBeiTu703.gif', '2', '4', NULL, '', '', '', '', 'A', '雾天行车，当风窗玻璃上凝有小水珠时，可使用刮水器清除，内侧的小水珠可用风窗玻璃除雾功能清除或在安全地点停车后用干毛巾擦干。', 11, 1, '2021-12-19 01:25:08', '2021-12-19 01:25:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4704, '驾驶机动车在高速公路加速车道上行驶，只要车速足够快，可以立刻插入车流。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在高速公路加速车道上行驶，应控制好车速，不得迅速从中间插入车辆。', 11, 1, '2021-12-19 01:25:08', '2021-12-19 01:25:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4705, '如图所示，夜间驾驶机动车遇到其他机动车突然驶入本车道时，应当如何避让？', '/uploadFile/yuanBeiTu705.gif', '1', '4', NULL, '及时减速让行', '向左猛打转向盘躲避', '向右猛打转向盘躲避', '加速从右侧绕行', 'A', '夜间驾驶机动车遇其他机动车突然驶入本车道时，应及时减速让行，千万不要开赌气车。', 11, 1, '2021-12-19 01:25:09', '2021-12-19 01:25:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4706, '如图所示，驾驶机动车遇到前车插入本车道时，可以向右转向，从前车右侧加速超越。', '/uploadFile/yuanBeiTu706.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在路面变窄路段遇前车抢行时，应在确认安全的情况下减速靠右避让，千万不要开赌气车。', 11, 1, '2021-12-19 01:25:09', '2021-12-19 01:25:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4707, '如图所示，驾驶机动车遇到非机动车占道行驶时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu707.gif', '1', '4', NULL, '减速并鸣喇叭提示', '交替变换远近光灯提示', '加速通过', '持续鸣喇叭催促', 'A', '驾驶机动车遇到非机动车占道行驶时，应当鸣喇叭提示，减速慢行，随时准备停车。', 11, 1, '2021-12-19 01:25:09', '2021-12-19 01:25:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4708, '如图所示，驾驶机动车遇到这种情形时，可以从左侧超越。', '/uploadFile/yuanBeiTu708.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车行经交叉路口、人行横道处不得超车。根据《道路交通安全法》第四十三条  有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的；   （二）与对面来车有会车可能的；   （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；   （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。   第四十四条  机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。', 11, 1, '2021-12-19 01:25:10', '2021-12-19 01:25:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4709, '如图所示，驾驶机动车在雨天行驶，驾驶人应当注意的是什么？', '/uploadFile/yuanBeiTu709.gif', '3', '4', NULL, '视线不清，不能及时发现行人', '行人可能滑倒', '行人可能突然进入行车道', '行人可能会横过道路', 'ABCD', '雨天行驶路面湿滑，视线受阻，一定要选择安全车速，同时要与其他车辆、行人等保持足够的安全距离。要密切注意行人和骑车人动态，预防车辆临近时，其突然转向或滑倒，同时避免积水溅到行人和骑车人身上。', 11, 1, '2021-12-19 01:25:10', '2021-12-19 01:25:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4710, '如图所示，驾驶机动车跟随前车右转弯时，应当注意的是什么？', '/uploadFile/yuanBeiTu710.gif', '3', '4', NULL, '前面的车可能停下', '右侧视野盲区内可能有自行车直行', '行人可能突然进入本车前的人行横道', '直行的黄色车辆可能影响本车右转弯', 'ABC', '如图所示，驾驶机动车跟随前车右转弯时，应注意：1、前面的车可能会避让路口行人或非机动车而停下；2、行人可能突然进入人行横道；3、右侧为非机动车车道，视野盲区内可能有非机动车直行。', 11, 1, '2021-12-19 01:25:10', '2021-12-19 01:25:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4711, '车辆通过桥梁时，一般要减速慢行。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过窄桥时，要注意减速慢行、谨慎驾驶；通过跨线桥时，要注意观察桥上车辆的通行情况，提前观察标志标线，按照标志标线指引的车道和限定速度行驶；通过跨江、河、海大桥时，可能会遇到横风，要控制好方向，遵守限速规定，以防横风造成车辆行驶方向偏离。', 11, 1, '2021-12-19 01:25:11', '2021-12-19 01:25:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4712, '车辆通过桥梁时，只要空间足够，尽可能超车提高通行效率。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车通过桥梁时，尽量靠右侧行驶，不得在桥上超车或超速行驶。', 11, 1, '2021-12-19 01:25:11', '2021-12-19 01:25:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4713, '驾驶机动车在双向行驶的隧道内行驶，如对向无来车，可借道超车。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在双向行驶的隧道内行驶，不得超车。 根据《道路交通安全法》第四十三条 有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 11, 1, '2021-12-19 01:25:11', '2021-12-19 01:25:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4714, '驾驶机动车驶出隧道时，应注意明暗视力的变化，控制车速。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车驶出隧道时，应注意明暗视力的变化，控制车速。', 11, 1, '2021-12-19 01:25:12', '2021-12-19 01:25:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4715, '驾驶机动车在山区路段超车时，以下做法正确的是什么？', '', '3', '4', NULL, '提前开启左转向灯', '提前鸣喇叭', '确认前车让超后超越', '直接加速超越', 'ABC', '山区道路行车要尽量避免超车，需要超车时，要选择路面宽阔的缓上坡路段，开启左转向灯，提前鸣喇叭，在确认前车让行后再超越。不得强行超车，严禁在路面狭窄、急转弯、连续转弯或对面有来车等不具备条件的路段超车。', 11, 1, '2021-12-19 01:25:12', '2021-12-19 01:25:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4716, '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，以下做法正确的是什么？', '', '3', '4', NULL, '拉起驻车制动器', '开启危险报警闪光灯', '在后方用塞车木或石块塞住车轮，以防车辆后溜', '按规定在车后方设置警示标志', 'ABCD', '驾驶机动车在山区道路上坡路段行驶，因发生故障需停车检修时，需要：拉起驻车制动器；开启危险报警闪光灯；在后方用塞车木或石块塞住车轮，以防车辆后溜；按规定在车后方设置警示标志。', 11, 1, '2021-12-19 01:25:12', '2021-12-19 01:25:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4717, '驾驶机动车在山区道路会车时，应该尽量提前让行，为临崖车辆留出足够的时间、空间会车。', '', '2', '4', NULL, '', '', '', '', 'A', '在山区道路遇对向来车时，应选择安全路段减速或停车交会，为临崖车辆留出足够的时间、空间会车。不得加速或紧靠道路中心会车，以防发生刮碰事故。', 11, 1, '2021-12-19 01:25:12', '2021-12-19 01:25:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4718, '驾驶机动车通过这种路段时，应该考虑到弯道后方可能有对面驶来的车辆占用我方车道。', '/uploadFile/yuanBeiTu718.gif', '2', '4', NULL, '', '', '', '', 'A', '通过弯道时，要提前减速，注意观察，预防以下险情：①弯道高速行驶，车辆容易发生侧翻；②弯道盲区内，可能存在落石及停驶的故障车等障碍；③弯道盲区内，可能有对向车辆占道行驶或对向来车占道超车；④通过急弯处时不要超车，防止前方有车辆或行人出现而措手不及。', 11, 1, '2021-12-19 01:25:13', '2021-12-19 01:25:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4719, '如图所示，该车在会车过程中未关闭远光灯的做法是错误的。', '/uploadFile/yuanBeiTu719.gif', '2', '4', NULL, '', '', '', '', 'A', '夜间会车遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。因为远光灯会使对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。因此，会车过程中不关闭远光灯的做法是错误的。', 11, 1, '2021-12-19 01:25:24', '2021-12-19 01:25:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4720, '夜间驾驶机动车开启灯光，不仅为了看清路况，更重要的是让其他交通参与者能够观察到我方车辆。', '', '2', '4', NULL, '', '', '', '', 'A', '夜间灯光有照明和信号两方面的作用，夜间驾驶机动车开启灯光，不仅为了看清路况，更重要的是让其他交通参与者能够观察到我方车辆。', 11, 1, '2021-12-19 01:25:24', '2021-12-19 01:25:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4721, '如图所示，驾驶机动车在这种情况下，由于前车相隔较远，可先观察情况后，临近再做调整。', '/uploadFile/yuanBeiTu721.gif', '2', '4', NULL, '', '', '', '', 'B', '雨天行驶，一定要选择安全车速，同时要与其他车辆、行人等保持足够的安全距离，密切观察其他交通参与者的动态，预防车辆临近时，其突然转向或侧滑等险情出现，同时应尽量避免超车，降低事故发生的风险。', 11, 1, '2021-12-19 01:25:24', '2021-12-19 01:25:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4722, '如图所示，在这种情况下，应加大跟车距离。', '/uploadFile/yuanBeiTu722.gif', '2', '4', NULL, '', '', '', '', 'A', '雨天跟车行驶，要增大与前车的安全距离，尽量避免超车，降低事故发生的风险。', 11, 1, '2021-12-19 01:25:25', '2021-12-19 01:25:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4723, '如图所示，雾天驾驶机动车跟车行驶，应加大与前车的距离。', '/uploadFile/yuanBeiTu723.gif', '2', '4', NULL, '', '', '', '', 'A', '雾天跟车距离过近，容易发生追尾事故，因此，驾驶人一定要控制车速，加大与前车的安全距离。', 11, 1, '2021-12-19 01:25:25', '2021-12-19 01:25:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4724, '驾驶机动车遇浓雾或沙尘暴时，必须打开雾灯或者危险报警闪光灯。', '', '2', '4', NULL, '', '', '', '', 'A', '雾天行车，应开启雾灯、示廓灯，及时让其他车辆驾驶人发现自己；在雾较大时，可开启近光灯照明，以便看清前方路况，开启危险报警闪光灯，不能使用远光灯。', 11, 1, '2021-12-19 01:25:25', '2021-12-19 01:25:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4725, '驾驶机动车在高速公路匝道上行驶，当有人给您打电话的时候，可以靠边停车，接打电话。', '', '2', '4', NULL, '', '', '', '', 'B', '《职业道德与安全行车知识》上注明：在匝道内不许超车、掉头、停车和倒车。因此，题干表述错误。', 11, 1, '2021-12-19 01:25:25', '2021-12-19 01:25:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4726, '驾驶机动车驶离高速公路时，若车辆制动性能良好，可直接驶入匝道。', '', '2', '4', NULL, '', '', '', '', 'B', '车辆进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，也不得未经减速车道直接驶入匝道。', 11, 1, '2021-12-19 01:25:26', '2021-12-19 01:25:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4727, '如图所示，浓雾天气中驾驶机动车两车交会，以下做法错误的是什么？', '/uploadFile/yuanBeiTu727.gif', '1', '4', NULL, '适当降低行驶车速', '靠右行驶', '集中注意力驾驶', '使用远光灯，提醒对方车辆', 'D', '浓雾天时方向难辨，行进中很难看清前方障碍，容易发生交通事故，因此要集中注意力驾驶，会车时要选择宽阔路段低速交会，保持足够的横向间距，适当鸣喇叭，对向来车车速较快时，要主动减速让行或靠边停车，雾天行车，不能使用远光灯。', 11, 1, '2021-12-19 01:25:26', '2021-12-19 01:25:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4728, '如图所示，夜间驾驶机动车通过没有交通信号灯的人行横道时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu728.gif', '1', '4', NULL, '减速，停车让行', '交替变换远近光灯加速通过', '连续鸣喇叭后通过', '确保安全加速通过', 'A', '夜间通过人行横道或者没有交通信号灯控制的路口，应在距路口约100米处减速慢行，并且交替使用远近光灯示意，停车瞭望确认安全后，低速通过，同时要注意黑暗中的行人和非机动车。', 11, 1, '2021-12-19 01:25:26', '2021-12-19 01:25:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4729, '如图所示，驾驶机动车遇到这种情况时，应当停车让行。', '/uploadFile/yuanBeiTu729.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇学生列队横过道路时，应及时停车礼让，不可从他们中间穿行或从两侧绕行通过，避免发生事故。', 11, 1, '2021-12-19 01:25:27', '2021-12-19 01:25:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4730, '如图所示，驾驶机动车遇到对向来车正在强行超车时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu730.gif', '1', '4', NULL, '减速避让', '向左打转向盘避让', '向右借用人行道避让', '迎着来车鸣喇叭将其逼回', 'A', '驾驶机动车遇到对向来车正在强行超车时，应减速或停车让行，安全避让对向来车；不可与其争道抢行，以防发生碰撞。', 11, 1, '2021-12-19 01:25:27', '2021-12-19 01:25:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4731, '如图所示，驾驶机动车A遇到异常行驶的车辆B，A车应当减速避让，确保安全。', '/uploadFile/yuanBeiTu731.gif', '2', '4', NULL, '', '', '', '', 'A', '在路口转弯时遇到特殊情况，优先要做的都是减速慢行，不要加速、抢行和鸣笛，确保安全下通行。', 11, 1, '2021-12-19 01:25:27', '2021-12-19 01:25:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4732, '遇到道路交通事故，过往车辆驾驶人应当予以协助。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条规定：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。', 11, 1, '2021-12-19 01:25:28', '2021-12-19 01:25:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4733, '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，以下做法正确的是什么？', '', '1', '4', NULL, '持续鸣喇叭警示', '交替使用远近光灯提示', '使用近光灯，减速靠右避让', '加速通过', 'C', '夜间驾驶机动车在农村道路行驶，遇到对向驶来畜力车时，应当使用近光灯，减速靠右避让。行车中遇畜力车时，应在较远处鸣喇叭，并提前减速。临近时急加速或鸣喇叭，赶车人会因牲畜受到惊吓而对车辆失去控制，导致事故。', 11, 1, '2021-12-19 01:25:28', '2021-12-19 01:25:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4734, '如图所示，A车正确的做法是什么？', '/uploadFile/yuanBeiTu734.gif', '1', '4', NULL, '鸣喇叭从左侧超越自行车', '减速待自行车通过后再从B车左侧超越', '停车等待B车驶离后，在原车道行驶', '借用对向车道加速通过', 'B', '图中停在路边的B车危险报警闪光灯开启，车后设置危险警示标志牌，可判断B车发生故障临时停在路边；为了保证行车安全，此时A车应当减速等待前方自行车通过后再从B车左侧绕行通过。', 11, 1, '2021-12-19 01:25:28', '2021-12-19 01:25:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4735, '如图所示，驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu735.gif', '1', '4', NULL, '保持较低车速通过', '停车让行，等行人通过后再通过', '连续鸣喇叭冲开人群', '确保安全的前提下绕行通过', 'B', '驾驶机动车通过交叉路口时右转遇到人行横道有行人通过时，应当停车让行，等行人通过后再通过。遇行人或非机动车通过人行横道时，及时停车让行，不得抢行或绕行。右转弯通行时，更要注意礼让通过人行横道的行人。', 11, 1, '2021-12-19 01:25:28', '2021-12-19 01:25:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4736, '如图所示，A车在交叉路口左转时遇到B车强行超越，以下做法错误的是什么？', '/uploadFile/yuanBeiTu736.gif', '3', '4', NULL, '持续鸣喇叭警告', '保持车速继续行驶', '与其抢行', '加速靠左侧绕行', 'ABCD', 'A车在交叉路口左转时遇到B车强行超越，应减速让行以免发生碰撞事故。采取持续鸣喇叭警告、保持原速行驶、争道抢行和加速绕行的方式容易引发交通事故，因此做法错误。', 11, 1, '2021-12-19 01:25:29', '2021-12-19 01:25:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4737, '如图所示，驾驶机动车遇到这种情形时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu737.gif', '1', '4', NULL, '加速行驶，在对面来车交会前超过行人', '减速靠右，等对向车辆通过后，再缓慢超越行人', '鸣喇叭提示行人后，保持原速行驶', '鸣喇叭提示左侧车辆后，保持原速行驶', 'B', '驾驶机动车遇到三个状况：对面来车，前方左侧车辆汇入主路、前方右侧有行人，此时机动车驾驶员应当减速靠右，等对向车辆通过后，再缓慢超越行人。', 11, 1, '2021-12-19 01:25:29', '2021-12-19 01:25:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4738, '如图所示，驾驶机动车在窄桥上会车，选择的交会位置不理想时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu738.gif', '1', '4', NULL, '加速行驶，在前方继续选择理想位置', '停车选择会车地点，必要时倒车，让对方通过', '靠左占道行驶，让对方停车让行', '变换远近光灯，示意对方停车让行', 'B', '驾驶机动车在窄桥上会车，选择的交会位置不理想时，应当选择合适会车地点停车，必要时倒车，让对方通过。', 11, 1, '2021-12-19 01:25:30', '2021-12-19 01:25:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4739, '如图所示，驾驶机动车遇到执行紧急任务的救护车时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu739.gif', '1', '4', NULL, '救护车违反交通信号通行，不予避让', '减速，避让救护车', '按照信号灯指示，正常通行', '加速通过', 'B', '遇到正在执行紧急任务的特殊车辆逆向驶来时，应当靠边减速或停车让行，为其让路，确保执行任务的特殊车辆通行无阻。', 11, 1, '2021-12-19 01:25:30', '2021-12-19 01:25:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4740, '如图所示，驾驶机动车遇到这种情形时，应减速在其后保持安全距离通过路口。', '/uploadFile/yuanBeiTu740.gif', '2', '4', NULL, '', '', '', '', 'A', '通过没有交通信号灯控制的路口时，应在距路口50-100米时减速，行至路口时仔细观察左、右侧道路交通情况，减速或停车瞭望，做到“一看二慢三通过”。图中所示情形，左右两侧道路无车无人，确认安全，应减速保持安全距离跟在自行车后面通过，不得加速从其两侧绕行超越。', 11, 1, '2021-12-19 01:25:30', '2021-12-19 01:25:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4741, '如图所示，驾驶机动车遇到这种情形时，以下做法错误的是什么？', '/uploadFile/yuanBeiTu741.gif', '3', '4', NULL, '加速通过', '连续鸣喇叭警示', '停车让行', '迅速超越前方非机动车', 'ABD', '驾驶机动车接近人行横道线遇行人或非机动车通过人行横道时，及时停车让行，不得鸣喇叭催促、抢行或绕行。', 11, 1, '2021-12-19 01:25:31', '2021-12-19 01:25:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4742, '驾驶机动车过程中遇到专注于使用手机的行人时，以下说法正确的是什么？', '', '3', '4', NULL, '注意观察', '从一侧加速绕过', '谨慎驾驶', '做好停车准备', 'ACD', '行车中，遇低头专注于使用手机的行人时，一定要注意观察，提前鸣喇叭提示并减速慢行谨慎驾驶，随时做好停车准备，防止临近时鸣喇叭使其慌不择路，出现危险。', 11, 1, '2021-12-19 01:25:31', '2021-12-19 01:25:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4743, '夜间驾驶机动车在道路上会车，为避免对方驾驶人眩目，应距离对向来车多远改用近光灯？', '', '1', '4', NULL, '150米以内', '150米以外', '100米以内', '50米以内', 'B', '根据《道路交通安全法实施条例》第四十八条规定，(五)夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 11, 1, '2021-12-19 01:25:31', '2021-12-19 01:25:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4744, '驾驶机动车会车时，当视线受阻不利于观察到对向来车时，双方都应做到减速靠右通过，并鸣喇叭示意。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车会车时，当视线受阻不利于观察到对向来车时，双方都应做到减速靠右通过，并鸣喇叭示意，以免发生危险。', 11, 1, '2021-12-19 01:25:31', '2021-12-19 01:25:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4745, '如图所示，驾驶机动车遇到校车停车上下学生时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu745.gif', '1', '4', NULL, '停车等待', '借对向车道绕行', '鸣喇叭催促', '变换远近光灯示意学生让行', 'A', '1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越； 2、校车在同方向有两条机动车道的道路上停靠时，后方和相邻机动车道上的机动车应当停车等待，不得超越； 3、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。', 11, 1, '2021-12-19 01:25:32', '2021-12-19 01:25:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4746, '驾驶机动车遇到同车道行驶的执行紧急任务的特种车辆时不得超车。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 11, 1, '2021-12-19 01:25:32', '2021-12-19 01:25:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4747, '如图所示，机动车A的行为是正确的。', '/uploadFile/yuanBeiTu747.gif', '2', '4', NULL, '', '', '', '', 'B', '白色实线不允许变道，A车在白色实线上变更车道，属于违法行驶。', 11, 1, '2021-12-19 01:25:32', '2021-12-19 01:25:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4748, '驾驶机动车准备进入拥堵的环形路口时，以下做法错误的是什么？', '', '3', '4', NULL, '继续驶入拥堵路口', '鸣喇叭让路口内的车让行', '快速驶入路口', '让路口内的车先行', 'ABC', '环形路口通行原则：准备进入环形路口的机动车，让已在路口内的机动车先行。因此，驾驶机动车驶入拥堵的环形路口时，应当减速或停车，依次缓慢行驶或耐心排队等待，注意避让已在路口内的车辆。因此，继续驶入、鸣喇叭、快速驶入均为错误做法。', 11, 1, '2021-12-19 01:25:32', '2021-12-19 01:25:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4749, '驾驶机动车行驶过程中，如遇到前方车辆行驶速度缓慢时，应持续鸣喇叭 。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车行驶过程中，如遇到前方车辆行驶速度缓慢时，应注意观察车况，合理超车。不能持续鸣喇叭催促。', 11, 1, '2021-12-19 01:25:33', '2021-12-19 01:25:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4750, '如图所示，前车乘车人的行为是不文明的。', '/uploadFile/yuanBeiTu750.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶过程中，向车外抛洒瓜果皮屑等废弃物，属于不文明行为。', 11, 1, '2021-12-19 01:25:33', '2021-12-19 01:25:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4751, '如图所示，造成事故的原因是货车遗洒货物，货车负全部责任。', '/uploadFile/yuanBeiTu751.gif', '2', '4', NULL, '', '', '', '', 'A', '造成该事故的原因是货车上货物固定不牢，货物遗洒导致后面的机动车事故，货车应负全部责任。 《道路交通安全法实施条例》第四十八条　机动车载物应当符合核定的载质量，严禁超载；载物的长、宽、高不得违反装载要求，不得遗洒、飘散载运物。', 11, 1, '2021-12-19 01:25:33', '2021-12-19 01:25:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4752, '驾驶机动车怎样经过公路跨线桥？', '/uploadFile/yuanBeiTu752.gif', '1', '4', NULL, '加速行驶，尽快通过', '车速控制在15公里/小时以内', '按照标志限定速度行驶', '尽量靠桥中心行驶', 'C', '如图所示，前方路段的行驶车速限制在40公里/小时，因此要按照此限速标志行驶，且靠右行驶。所以本题选C。', 12, 1, '2021-12-19 01:25:34', '2021-12-19 01:25:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4753, '驾驶机动车遇到这种情况怎样安全通过？', '/uploadFile/yuanBeiTu753.gif', '1', '4', NULL, '靠右侧正常通过', '鸣喇叭，加速通过隧道', '停车礼让对面车先通过', '开前照灯告知对面车让行', 'C', '', 12, 1, '2021-12-19 01:25:34', '2021-12-19 01:25:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4754, '驾驶机动车遇到这种情况要靠右侧停车等待。', '/uploadFile/yuanBeiTu754.gif', '2', '4', NULL, '', '', '', '', 'A', '图中有3个要点需要大家注意： 1、隧道 2、对向有来车驶出隧道 3、隧道口红灯亮 结合以上3点，为保障安全，驾驶机动车遇到图中这种情况时应该靠右侧停车等待。 【拓展延伸】 图中只有一条车道，因此红绿灯是针对道路所有车辆所设置，并不区分左右。此时面向车辆的红绿灯，是红灯亮起绿灯熄灭（如果绿灯亮，绿灯的颜色会更加鲜艳，可以与此时红灯的颜色亮度进行比较），因此车辆需要停车等待。', 12, 1, '2021-12-19 01:25:34', '2021-12-19 01:25:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4755, '驾驶机动车在这种隧道内要尽量靠左侧行驶。', '/uploadFile/yuanBeiTu755.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，当前隧道为双向行驶的隧道。根据【复杂道路行驶规定】：驾驶机动车驶入双向通行的隧道时，应开启近光灯，靠右侧行驶，注意对向来车，会车时，加大横向距离低速安全会车，会车不可开启远光灯。', 12, 1, '2021-12-19 01:25:35', '2021-12-19 01:25:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4756, '驾驶机动车在这个时候要减速慢行。', '/uploadFile/yuanBeiTu756.gif', '2', '4', NULL, '', '', '', '', 'A', '由图可知，机动车即将驶出隧道，在出隧道时需要有一个明适应的过程，因此要减速慢行，确保安全。因此本题正确。', 12, 1, '2021-12-19 01:25:35', '2021-12-19 01:25:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4757, '机动车通过隧道时，禁止以下哪些行为？', '', '3', '4', NULL, '超车', '停车', '掉头', '倒车', 'ABCD', '机动车通过隧道时，严禁在隧道内变更车道、超车、倒车、掉头和随意停车。', 12, 1, '2021-12-19 01:25:35', '2021-12-19 01:25:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4758, '下长坡时，控制车速的正确方法是什么？', '', '1', '4', NULL, '空挡滑行', '挂低速挡', '踏下离合器踏板滑行', '使用驻车制动器', 'B', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降，控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速。', 12, 1, '2021-12-19 01:25:35', '2021-12-19 01:25:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4759, '下长坡连续使用行车制动会造成什么不良后果？', '', '1', '4', NULL, '缩短发动机使用寿命', '驾驶人容易疲劳', '容易造成机动车倾翻', '制动器制动效果下降', 'D', '下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度会使制动效果急剧下降。', 12, 1, '2021-12-19 01:25:36', '2021-12-19 01:25:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4760, '驾驶机动车在山区道路遇到这种情况如何处理？', '/uploadFile/yuanBeiTu760.gif', '1', '4', NULL, '各行其道加速交会', '紧靠路中心行驶', '保持正常车速行驶', '减速行驶', 'D', '由图可知，前方是向右急转弯路段，且前方有来车，因此应当减速靠右行驶。因此本题选D。', 12, 1, '2021-12-19 01:25:36', '2021-12-19 01:25:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4761, '驾驶机动车在这种山区弯路怎样行驶？', '/uploadFile/yuanBeiTu761.gif', '1', '4', NULL, '占对向车道行驶', '靠右侧减速行驶', '在道路中心行驶', '紧靠路右侧行驶', 'B', '山区弯道行驶要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”。', 12, 1, '2021-12-19 01:25:36', '2021-12-19 01:25:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4762, '驾驶机动车在这种山区弯道怎样转弯最安全？', '/uploadFile/yuanBeiTu762.gif', '1', '4', NULL, '靠弯道外侧行驶', '减速、鸣喇叭、靠右行', '借用对向车道行驶', '靠道路中心行驶', 'B', '由图可知，前方是向右急转弯路段，因此为了避免前方有来车，应当减速鸣喇叭靠右行。', 12, 1, '2021-12-19 01:25:37', '2021-12-19 01:25:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4763, '驾驶机动车在山区道路遇到这种情况怎样行驶？', '/uploadFile/yuanBeiTu763.gif', '1', '4', NULL, '靠路左侧，加速绕行', '停车瞭望，缓慢通过', '注意观察，尽快通过', '勤鸣喇叭，低速通行', 'C', '驾驶机动车在山区道路遇落石路段行驶时，应当注意观察，尽快通过，以免落石掉落，造成危险。', 12, 1, '2021-12-19 01:25:37', '2021-12-19 01:25:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4764, '驾驶机动车遇到这种山路怎样通过？', '/uploadFile/yuanBeiTu764.gif', '1', '4', NULL, '前方左侧是傍山险路', '靠路左侧行驶', '选择路中心行驶', '靠右侧低速通过', 'D', '驾驶机动车通过傍山临崖的危险路段时，要靠右侧减速行驶。在较窄的路段靠山体一侧时，要尽量靠右侧低速行驶，遇对面有来车时，要选择安全的地点，尽量靠近山体一侧让行，做到先让、先慢、先停。', 12, 1, '2021-12-19 01:25:37', '2021-12-19 01:25:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4765, '驾驶机动车在山区道路怎样跟车行驶？', '', '1', '4', NULL, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', 'B', '山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 12, 1, '2021-12-19 01:25:37', '2021-12-19 01:25:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4766, '山区上坡路段跟车过程中遇前车停车时怎么办？', '', '1', '4', NULL, '从前车两侧超越', '紧跟前车后停车', '保持大距离停车', '连续鸣喇叭提示', 'C', '驾驶机动车在上坡道路跟车行驶，要加大与前车的安全距离，仔细观察前车动态，以防前车突然停车或停车后溜。', 12, 1, '2021-12-19 01:25:38', '2021-12-19 01:25:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4767, '驾驶机动车在山区道路因故障停车需要注意什么？', '', '1', '4', NULL, '选择下坡路段停放', '选择上坡路段停放', '选择平缓路段停放', '选择坡顶位置停放', 'C', '在山区道路，应尽量避免停车，确需停车时，要选择平缓路段靠右侧路边停车。', 12, 1, '2021-12-19 01:25:38', '2021-12-19 01:25:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4768, '驾驶机动车在下坡路段停车怎样使用行车制动？', '', '1', '4', NULL, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要轻踏制动踏板', 'A', '汽车下坡行驶时，在重力的作用下速度会逐渐提高，因而下坡路段停车制动距离会比平路时长，所以要比在平路时提前。', 12, 1, '2021-12-19 01:25:38', '2021-12-19 01:25:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4769, '上坡路段停车怎样使用行车制动？', '', '1', '4', NULL, '比在平路时提前', '比在平路时推迟', '和平路时一样', '要重踏制动踏板', 'B', '汽车上坡行驶时，由于坡度阻力车速自然会降下来，制动距离比平时短，因此停车时制动要比在平路时推迟。', 12, 1, '2021-12-19 01:25:38', '2021-12-19 01:25:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4770, '下长坡控制车速最安全的方法是什么？', '', '1', '4', NULL, '挂入空挡滑行', '踏下离合器滑行', '利用发动机制动', '持续踏制动踏板', 'C', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降，控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速。', 12, 1, '2021-12-19 01:25:39', '2021-12-19 01:25:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4771, '驾驶机动车上坡行驶如何保持充足动力？', '', '1', '4', NULL, '在车速下降前减挡', '在车速下降后减挡', '在车速过低时减挡', '尽量使用越级减挡', 'A', '上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。', 12, 1, '2021-12-19 01:25:39', '2021-12-19 01:25:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4772, '机动车驶近坡道顶端等影响安全视距的路段时，要如何保证安全？', '', '1', '4', NULL, '快速通过', '使用危险报警闪光灯', '减速慢行并鸣喇叭示意', '随意通行', 'C', '注意“影响安全视距的路段”，也就是说在此路段行驶时无法完全掌握路况，因此要减速慢行谨慎小心，同时鸣喇叭告知其他车辆。因此本题选C。', 12, 1, '2021-12-19 01:25:39', '2021-12-19 01:25:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4773, '机动车在通过山区道路弯道时，要做到“减速、鸣喇叭、靠右行”。', '/uploadFile/yuanBeiTu773.gif', '2', '4', NULL, '', '', '', '', 'A', '通过山区道路弯道时，要注意观察，提前发现急弯路警告标志并做到“减速、鸣喇叭、靠右行”，确保行车安全。因此，题干表述正确。', 12, 1, '2021-12-19 01:25:40', '2021-12-19 01:25:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4774, '驾驶机动车在对向没有来车的情况下可以超车。', '/uploadFile/yuanBeiTu774.gif', '2', '4', NULL, '', '', '', '', 'B', '山区道路行车要尽量避免超车，需要超车时，要选择路面宽阔的缓上坡路段，严禁在路面狭窄、急转弯、连续转弯或对面有来车等不具备条件的路段超车。', 12, 1, '2021-12-19 01:25:40', '2021-12-19 01:25:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4775, '驾驶机动车在这种情况下临时停车后，为避免机动车后溜可将转向盘向左转。', '/uploadFile/yuanBeiTu775.gif', '2', '4', NULL, '', '', '', '', 'A', '在车辆因故障或其他原因在有路缘石的上坡路段临时停车时，为避免车辆后溜，可将转向盘向左转；在下坡路段临时停车时，为避免车辆前溜，可将转向盘向右转，目的是充分利用路缘石阻断车辆溜动。', 12, 1, '2021-12-19 01:25:40', '2021-12-19 01:25:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4776, '驾驶机动车在这种情况下临时停车后，为避免机动车溜车可将转向盘向右转。', '/uploadFile/yuanBeiTu776.gif', '2', '4', NULL, '', '', '', '', 'A', '在车辆因故障或其他原因在有路缘石的上坡路段临时停车时，为避免车辆后溜，可将转向盘向左转；在下坡路段临时停车时，为避免车辆前溜，可将转向盘向右转，目的是充分利用路缘石阻断车辆溜动。', 12, 1, '2021-12-19 01:25:41', '2021-12-19 01:25:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4777, '因故障在山区上坡路段长时间停车时，要用这种办法塞住车轮。', '/uploadFile/yuanBeiTu777.gif', '2', '4', NULL, '', '', '', '', 'B', '当车辆因故障不得以在上坡路段长时间停车时，要在后轮后方用三角木或石块塞住车轮，防止车辆后溜；在下坡路段长时间停车时，要在前轮的前方用三角木或石块塞住车轮，防止车辆前溜。', 12, 1, '2021-12-19 01:25:41', '2021-12-19 01:25:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4778, '因故障在山区下坡路段长时间停车时，要用这种办法塞住车轮。', '/uploadFile/yuanBeiTu778.gif', '2', '4', NULL, '', '', '', '', 'A', '当车辆因故障不得以在上坡路段长时间停车时，要在后轮后方用三角木或石块塞住车轮，防止车辆后溜；在下坡路段长时间停车时，要在前轮的前方用三角木或石块塞住车轮，防止车辆前溜。', 12, 1, '2021-12-19 01:25:42', '2021-12-19 01:25:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4779, '驾驶机动车遇到这种道路要提前减挡，以保持充足动力。', '/uploadFile/yuanBeiTu779.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车上坡时，要提前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。因此，题干表述正确。', 12, 1, '2021-12-19 01:25:53', '2021-12-19 01:25:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4780, '驾驶机动车遇到这种道路要提前减速减挡，利用发动机制动控制速度。', '/uploadFile/yuanBeiTu780.gif', '2', '4', NULL, '', '', '', '', 'A', '下坡行驶，控制车速最好的方法是使用低速挡行驶，充分利用发动机制动来控制速度，严禁空挡滑行。', 12, 1, '2021-12-19 01:25:53', '2021-12-19 01:25:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4781, '驾驶机动车遇到这种道路，可充分利用空挡滑行。', '/uploadFile/yuanBeiTu781.gif', '2', '4', NULL, '', '', '', '', 'B', '图中右侧是连续下坡路警告标志，严禁空挡滑行 ，下坡路行驶，应挂中低速档位，成分利用发动机制动来控制车速，因此本题错误。', 12, 1, '2021-12-19 01:25:54', '2021-12-19 01:25:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4782, '驾驶机动车在这种情况下要加速冲过坡顶。', '/uploadFile/yuanBeiTu782.gif', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车驶近坡道顶端无法看清前方路口时，要减速慢行并鸣喇叭示意，不得加速冲过坡顶，防止对向有来车和其他意外情况。', 12, 1, '2021-12-19 01:25:54', '2021-12-19 01:25:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4783, '驾驶机动车通过这段山区道路要靠路中心行驶。', '/uploadFile/yuanBeiTu783.gif', '2', '4', NULL, '', '', '', '', 'B', '山区弯道较多，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”。通过连续急弯路时，要注意靠右侧减速慢行，不要占对向车道。', 12, 1, '2021-12-19 01:25:55', '2021-12-19 01:25:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4784, '驾驶机动车通过这种傍山险路要靠右侧行驶。', '/uploadFile/yuanBeiTu784.gif', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过这种傍山险路要注意观察，并做到减速、鸣号、靠右行。', 12, 1, '2021-12-19 01:25:55', '2021-12-19 01:25:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4785, '驾驶机动车通过这种傍山险路要靠左侧行驶。', '/uploadFile/yuanBeiTu785.gif', '2', '4', NULL, '', '', '', '', 'B', '山区弯道较多，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”。因此，如图所示急弯路，应当要减速靠右行驶。', 12, 1, '2021-12-19 01:25:55', '2021-12-19 01:25:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4786, '驾驶机动车在山区道路遇到这种情况要加速超越前车。', '/uploadFile/yuanBeiTu786.gif', '2', '4', NULL, '', '', '', '', 'B', '加速机动车在山区道路遇前车遮挡视线或扬起的沙尘造成视线不清时，不能盲目超越，要与其保持较大的安全距离。', 12, 1, '2021-12-19 01:25:55', '2021-12-19 01:25:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4787, '在山区道路行驶时，驾驶人要注意什么？', '', '3', '4', NULL, '保持与前车的安全距离', '避免转弯时占道行驶', '上陡坡提前换低速挡', '下长坡时，充分利用发动机制动', 'ABCD', '在山区道路驾驶，要注意观察，提前发现坡道警告标志。①山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态；②弯道行驶做到“减速、鸣号、靠右行”，不要占对向车道；③上陡坡时，要在坡底提前减挡，加速冲坡；④下长坡时，控制车速随后的方法是使用低速挡行驶，充分利用发动机制动减速，严禁空挡滑行。', 12, 1, '2021-12-19 01:25:56', '2021-12-19 01:25:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4788, '夜间驾驶机动车遇到这种情况时怎样处理？', '/uploadFile/yuanBeiTu788.gif', '1', '4', NULL, '保持正常车速行驶', '高速行驶避开灯光', '减速或停车让行', '开启远光灯对射', 'C', '夜间会车，遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。', 12, 1, '2021-12-19 01:25:56', '2021-12-19 01:25:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4789, '夜间驾驶机动车遇到这种情况怎样超车？', '/uploadFile/yuanBeiTu789.gif', '1', '4', NULL, '开远光灯', '交替使用远近光灯', '开近光灯', '开危险报警闪光灯', 'B', '夜间行车应尽量避免超车，确实需要超车时，要先变换远、近光灯提示前车，待前车让行后方可超越；遇前车不让行时，不得强行超车。', 12, 1, '2021-12-19 01:25:56', '2021-12-19 01:25:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4790, '夜间驾驶机动车超车遇前车不让路时怎样处置？', '', '1', '4', NULL, '连续鸣喇叭提示', '开远光灯尾随行驶', '保持距离等待让行', '连续变换前照灯远、近光', 'C', '夜间行车应尽量避免超车，确实需要超车时，要先变换远、近光灯提示前车，待前车让行后方可超越；遇前车不让行时，应保持安全距离等待让行，不得强行超车。', 12, 1, '2021-12-19 01:25:57', '2021-12-19 01:25:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4791, '夜间会车前，两车在相距150米之外交替变换前照灯远近光的作用是什么？', '', '1', '4', NULL, '会车前两车之间相互提示', '驾驶操作的习惯行为', '便于双方观察前方情况', '驾驶人之间的一种礼节', 'C', '夜间会车前，两车在距对向来车150米之外交替使用远近光灯便于双方观察前方情况；遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。', 12, 1, '2021-12-19 01:25:57', '2021-12-19 01:25:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4792, '机动车在夜间行驶如何保证安全？', '', '1', '4', NULL, '以最高设计车速行驶', '降低速度，谨慎驾驶', '保持现有速度行驶', '以超过规定的最高车速行驶', 'B', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。因此，夜间行车，一定要仔细观察、合理控制车速、正确使用灯光，谨慎驾驶。', 12, 1, '2021-12-19 01:25:57', '2021-12-19 01:25:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4793, '夜间行车，后方车辆提示超车，前方遇到这种情况时不能盲目让超。', '/uploadFile/yuanBeiTu793.gif', '2', '4', NULL, '', '', '', '', 'A', '夜间行车，看到后方来车的灯光越来越近，且来车发出超车信号时，要减速让行，有条件时靠右侧让路，不能盲目让超。如图所示，车辆前方有行人及非机动车，因此不能盲目让超，以防撞上路边行人、非机动车等。', 12, 1, '2021-12-19 01:25:58', '2021-12-19 01:25:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4794, '夜间会车遇到这种情况要警惕两车前照灯交汇处（视线盲区）的危险。', '/uploadFile/yuanBeiTu794.gif', '2', '4', NULL, '', '', '', '', 'A', '夜间近距离会车时，务必要警惕两车灯光交汇处（视线盲区）的危险，随时注意观察行人和非机动车的动态，以免交汇处有行人或非机动车横穿道路。', 12, 1, '2021-12-19 01:25:58', '2021-12-19 01:25:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4795, '机动车在夜间发生故障时，驾驶人要做什么以确保安全？', '', '3', '4', NULL, '选择安全区域停车', '开启危险报警闪光灯', '开启示廓灯和后位灯', '按规定设置警告标志', 'ABCD', '夜间车辆发生故障时，要尽量选择安全区域停车，开启危险报警闪光灯、示廓灯和后位灯，按规定设置警告标志，车上人员应该转移到安全地点，不要在车内停留。', 12, 1, '2021-12-19 01:25:58', '2021-12-19 01:25:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4796, '在普通道路驾车遇暴雨，刮水器无法改善驾驶人视线，此时要采取的措施是：', '', '1', '4', NULL, '减速行驶', '集中注意力谨慎驾驶', '立即减速靠边停驶', '以正常速度行驶', 'C', '在普通道路驾车遇暴雨天气，靠刮水器难以改善视线时，要选择安全地点停车，开启示廓灯和危险报警闪光灯，待雨小或雨停后再继续行驶。', 12, 1, '2021-12-19 01:25:58', '2021-12-19 01:25:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4797, '最容易发生侧滑的路面是：', '', '1', '4', NULL, '干燥水泥路面', '下雨开始时的路面', '潮湿水泥路面', '大雨中的路面', 'B', '不同路面的打滑程度不同：1、干燥水泥路面，不滑；2、潮湿水泥路面，比较滑；3、刚开始下雨时，路面最容易打滑。', 12, 1, '2021-12-19 01:25:59', '2021-12-19 01:25:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4798, '驾驶机动车在雨天临时停车注意什么？', '', '1', '4', NULL, '开启危险报警闪光灯', '开启前后雾灯', '开启近光灯', '在车后设置警告标志', 'A', '夜间或遇风、雨、雪、雾天在路边临时停车，要关闭前照灯，开启示廓灯和危险报警闪光灯。', 12, 1, '2021-12-19 01:25:59', '2021-12-19 01:25:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4799, '当机动车在湿滑路面上行驶时，路面附着力随着车速的增加如何变化？', '', '1', '4', NULL, '急剧增大', '逐渐增大', '急剧减小', '没有变化', 'C', '雨天行驶，路面湿滑，车辆的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时也易发生“水滑”现象，因此一定要合理控制车速。', 12, 1, '2021-12-19 01:25:59', '2021-12-19 01:25:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4800, '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '大雨天行车，路面湿滑，车轮的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时，易发生“水滑”现象。因此，避免“水滑”而造成的危险，应当合理控制车速，减速行驶。', 12, 1, '2021-12-19 01:25:59', '2021-12-19 01:25:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4801, '雨天超车要开启前照灯，连续鸣喇叭迅速超越。', '', '2', '4', NULL, '', '', '', '', 'B', '雨天应尽量避免超车，降低事故发生的风险；确需超车，应减速慢行、注意观察，不得连续鸣喇叭迅速超越。', 12, 1, '2021-12-19 01:26:00', '2021-12-19 01:26:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4802, '在雨天湿滑路面行车要尽量避免紧急制动。', '', '2', '4', NULL, '', '', '', '', 'A', '雨天汽车在积水路面上高速行驶时，轮胎与路面间行车“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。因此一定要合理控制车速，不要急转向或紧急制动，应松抬加速踏板，充分利用发动机制动减速。', 12, 1, '2021-12-19 01:26:00', '2021-12-19 01:26:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4803, '雨天安全行车的注意事项是什么？', '', '3', '4', NULL, '避免紧急制动、紧急转向', '保持足够的安全距离', '注意非机动车和行人动态', '选择安全车速行驶', 'ABCD', '雨天行驶，路面湿滑，车轮的附着力随车速的增加会急剧变小，车辆很容易发生侧滑，因此一定要合理控制车速，不要急转向或紧急制动；同时要与其他车辆、行人等保持足够的安全距离；跟车行驶时，要增大与前车的安全距离，尽量避免超车，降低事故发生的风险。', 12, 1, '2021-12-19 01:26:00', '2021-12-19 01:26:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4804, '在冰雪路面上减速或停车，要怎样降低车速？', '', '1', '4', NULL, '充分利用行车制动器', '充分利用发动机的牵制作用', '充分利用驻车制动器', '充分利用缓速器', 'B', '在冰雪路面上减速或停车，应充分利用发动机牵阻作用降低车速，不能使用紧急制动和急转向的方法。', 12, 1, '2021-12-19 01:26:01', '2021-12-19 01:26:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4805, '驾驶机动车在冰雪路面怎样跟车行驶？', '', '1', '4', NULL, '保持较大的安全距离', '开启危险报警闪光灯', '不断变换前照灯远近光', '适时鸣喇叭提示前车', 'A', '冰雪路面跟车行驶要与前车保持较大的安全距离，处理紧急情况不能使用紧急制动和急转向的方法躲避，应充分利用发动机牵阻作用减速或停车。', 12, 1, '2021-12-19 01:26:01', '2021-12-19 01:26:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4806, '驾驶机动车在结冰的道路上怎样会车？', '', '1', '4', NULL, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', 'C', '驾驶机动车在结冰的道路上会车时，要提前减速缓慢会车，因为对向来车有可能发生侧滑。', 12, 1, '2021-12-19 01:26:01', '2021-12-19 01:26:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4807, '驾驶机动车在山区冰雪道路上遇前车正在爬坡时如何处置？', '', '1', '4', NULL, '前车通过后再爬坡', '迅速超越前车爬坡', '低速超越前车爬坡', '紧随前车后爬坡', 'A', '在山区冰雪道路上行车，遇前车正在爬坡时，不得紧随或超越前车爬坡，要等前车通过坡顶后再上坡，避免前车突然停车或后溜造成事故。', 12, 1, '2021-12-19 01:26:02', '2021-12-19 01:26:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4808, '驾驶机动车在冰雪路面行车注意什么？', '', '1', '4', NULL, '制动距离延长', '抗滑能力变大', '路面附着力变大', '制动距离变短', 'A', '车辆在冰雪道路上行驶，车辆制度性能差，车辆很容易侧滑跑偏。因路面湿滑，汽车轮胎摩擦力减少、附着力大大降低，汽车驱动轮很容易打滑或空转，尤其是上坡、起步、停车时还会出现后溜车的现象；车辆在行驶中如果突然加速或减速，很容易造成侧滑及方向跑偏现象；遇情况紧急制动时，制动距离会大大延长。', 12, 1, '2021-12-19 01:26:02', '2021-12-19 01:26:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4809, '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人眩目。', '', '2', '4', NULL, '', '', '', '', 'A', '因为太阳光通过冰雪的折射到人的眼睛里，冰雪在太阳光下就好像一面镜子，折射到驾驶员眼睛里就容易造成炫目了。', 12, 1, '2021-12-19 01:26:02', '2021-12-19 01:26:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4810, '在冰雪道路上行车时，机动车的稳定性降低，加速过急时车轮易空转或溜滑。', '', '2', '4', NULL, '', '', '', '', 'A', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 12, 1, '2021-12-19 01:26:02', '2021-12-19 01:26:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4811, '雪天行车中，在有车辙的路段要循车辙行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '当路面被积雪覆盖或道路轮廓难以辨别时，可根据路边树木、电线杆等参照物判断行驶路线，控制车辆低速行驶；在有车辙的路段要循车辙低速行驶。', 12, 1, '2021-12-19 01:26:03', '2021-12-19 01:26:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4812, '冰雪路面处理情况不能使用紧急制动，但可采取急转向的方法躲避。', '', '2', '4', NULL, '', '', '', '', 'B', '冰雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向，雪融化后会结成薄冰，路面非常滑，一定要加大安全距离低速缓慢行驶，要避免紧急制动和急转方向，以防车辆侧滑驶出路面。', 12, 1, '2021-12-19 01:26:03', '2021-12-19 01:26:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4813, '在雪天临时停车要开启前照灯和雾灯。', '', '2', '4', NULL, '', '', '', '', 'B', '雪天在道路上临时停车，应当开启危险报警闪光灯，用以提醒其他车辆注意。因此本题错在开启雾灯，因为雾灯的穿透力较强，雪天开雾灯会反光，眩目等现象，影响行车安全。', 12, 1, '2021-12-19 01:26:03', '2021-12-19 01:26:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4814, '大雾天在高速公路遇事故不能继续行驶时，危险的做法是什么？', '', '1', '4', NULL, '尽快离开机动车', '尽量站到防护栏以外', '开启危险报警闪光灯和雾灯', '沿行车道到车后设置警告标志', 'D', '大雾天在高速公路遇事故不能继续行驶时，须开启危险报警闪光灯和尾灯，按规定在车后设置警告标志。驾乘人员尽快从右侧车门下车离开车辆并尽量站到防护栏以外安全的地方，不得在高速公路上行走。 综上，沿行车道到车后是大雾天在高速公路上的危险做法。', 12, 1, '2021-12-19 01:26:03', '2021-12-19 01:26:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4815, '驾驶机动车遇到大雾或特大雾等能见度过低天气时如何做？', '', '1', '4', NULL, '开启前照灯低速行驶', '开启雾灯低速行驶', '选择安全地点停车', '紧靠路边低速行驶', 'C', '驾驶机动车遇到大雾或特大雾等能见度过低天气时，应先将车开到路边安全地带或停车场，等能见度好转时再上路行驶。', 12, 1, '2021-12-19 01:26:04', '2021-12-19 01:26:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4816, '雾天对安全行车的主要影响是什么？', '', '1', '4', NULL, '易发生侧滑', '能见度低', '行驶阻力大', '视野变宽', 'B', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄；浓雾天时方向难辨，行进中很难看清前方再按（如慢行车、行人、故障车、凹坑等），容易发生交通事故。', 12, 1, '2021-12-19 01:26:04', '2021-12-19 01:26:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4817, '驾驶机动车在雾天怎样跟车行驶？', '', '1', '4', NULL, '保持大间距', '开启远光灯', '开启近光灯', '适时鸣喇叭', 'A', '雾天跟车距离过近，容易发生追尾事故，因此，驾驶人一定要低速大间距行驶，即控制车速，加大与前车的安全距离。', 12, 1, '2021-12-19 01:26:04', '2021-12-19 01:26:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4818, '驾驶机动车在雾天两车交会时怎样做最安全？', '', '1', '4', NULL, '开启远光灯', '低速大间距', '开启近光灯', '开启雾灯', 'B', '雾天行驶，由于能见度较低，视野会受到一定的限制，会车时，要选择宽阔路段低速交会，保持足够的横向间距，适当鸣喇叭，对向来车车速较快时，要主动减速让行或靠边停车。', 12, 1, '2021-12-19 01:26:04', '2021-12-19 01:26:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4819, '机动车在雾天行驶时，要开启什么灯？', '', '1', '4', NULL, '雾灯和危险报警闪光灯', '雾灯和转向灯', '雾灯和远光灯', '雾灯和近光灯', 'A', '《道路交通安全法》第五十八条 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 12, 1, '2021-12-19 01:26:05', '2021-12-19 01:26:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4820, '大雾天气能见度低，开启远光灯会提高能见度。', '', '2', '4', NULL, '', '', '', '', 'B', '雾天不能使用远光灯的原因：汽车远光灯的灯光是照向前上方的，射出的光线被雾气反射后，会在车前行车白茫茫的一片，这不但不能起到照明作用还会严重阻挡驾驶人视线、视野，当后面车辆使用远光灯时也会严重影响前车驾驶人观察后方情况，因此雾天行车，不能使用远光灯。', 12, 1, '2021-12-19 01:26:05', '2021-12-19 01:26:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4821, '雾天公路行车可多使用喇叭引起对向注意；听到对向机动车鸣喇叭，也要鸣喇叭回应。', '', '2', '4', NULL, '', '', '', '', 'A', '雾天能见度低，驾驶人视线模糊，在道路上行车，可多使用喇叭引起对向车辆注意，听到对向车辆鸣喇叭时，要鸣喇叭回应。', 12, 1, '2021-12-19 01:26:05', '2021-12-19 01:26:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4822, '驾驶机动车在大雾天临时停车后，只开启雾灯和近光灯。', '', '2', '4', NULL, '', '', '', '', 'B', '雾天临时停车时，一定要开启危险报警闪光灯。', 12, 1, '2021-12-19 01:26:06', '2021-12-19 01:26:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4823, '驾驶机动车在雾天行车要开启雾灯。', '', '2', '4', NULL, '', '', '', '', 'A', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄。因此，雾天行车应开启雾灯和危险报警闪光灯，及时让其他车辆驾驶人发现自己，但不能使用远光灯。雾天停车时，一定开启危险报警闪光灯。', 12, 1, '2021-12-19 01:26:06', '2021-12-19 01:26:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4824, '雾天机动车在道路上通行，驾驶人要怎样做？', '', '3', '4', NULL, '减速慢行', '保持安全车距', '正确使用灯光', '高速行驶', 'ABC', '雾天能见度低，视野不良，驾驶机动车上道路通行，要将车速控制在能及时停车的范围内，保持足够的安全间距，谨慎缓慢行驶，可多使用喇叭引起行人和车辆注意。', 12, 1, '2021-12-19 01:26:07', '2021-12-19 01:26:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4825, '大风天气行车，由于风速和风向不断地发生变化，当感到转向盘突然难以控制时，驾驶人要怎样做？', '', '1', '4', NULL, '逆风向转动转向盘', '顺风向转动转向盘', '采取紧急制动', '双手稳握转向盘', 'D', '大风天气，因风速和风向不断地发生变化，行驶中，驾驶人常常会有转向盘突然“被夺”的感觉，此时一定要双手握稳转向盘。当突遇狂风袭来，感觉车辆产生横向偏移时，应降低车速，微微转动转向盘调正车头，不可采取急转转向盘的方式恢复行驶方向。', 12, 1, '2021-12-19 01:26:07', '2021-12-19 01:26:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4826, '大风天气行车中，如果遇到狂风袭来，感觉机动车产生横向偏移时，要急转方向以恢复行驶方向。', '', '2', '4', NULL, '', '', '', '', 'B', '大风天气，因风速和风向不断地发生变化，行驶中，驾驶人常常会有转向盘突然“被夺”的感觉，此时一定要双手握稳转向盘。当突遇狂风袭来，感觉车辆产生横向偏移时，应降低车速，微微转动转向盘调正车头，不可采取急转转向盘的方式恢复行驶方向。', 12, 1, '2021-12-19 01:26:07', '2021-12-19 01:26:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4827, '如果遇到较强横风，感觉机动车产生横向偏移时，要握紧转向盘并紧急制动。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车突遇较强横风，感觉车辆产生横向偏移时，应降低车速，微微转动转向盘调正车头，不可采取紧急制动。因此，题干表述错误。', 12, 1, '2021-12-19 01:26:07', '2021-12-19 01:26:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4828, '在泥泞路段行车，要怎样控制速度，匀速一次性通过？', '', '1', '4', NULL, '使用驻车制动器', '踏下离合器踏板', '踏制动踏板', '用加速踏板', 'D', '泥泞路路面特别松软、粘稠，车轮易滑转、侧滑。因此行至泥泞路段时，要停车查看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 12, 1, '2021-12-19 01:26:08', '2021-12-19 01:26:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4829, '在泥泞路段行车容易出现什么现象？', '', '1', '4', NULL, '行驶阻力大', '车轮侧滑', '机动车颠簸', '方向失控', 'B', '泥泞路路面特别松软、粘稠，车轮易滑转、侧滑。因此行至泥泞路段时，要停车查看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 12, 1, '2021-12-19 01:26:08', '2021-12-19 01:26:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4830, '在泥泞路段遇车后轮向右侧滑时如何处置？', '', '1', '4', NULL, '继续加速', '向右转向', '向左转向', '紧急制动', 'B', '驾驶机动车在泥泞路发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正。', 12, 1, '2021-12-19 01:26:08', '2021-12-19 01:26:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4831, '在泥泞路段遇驱动车轮空转打滑时如何处置？', '', '1', '4', NULL, '在从动轮下铺垫砂石', '换高速挡加速猛冲', '在驱动轮下铺垫砂石', '猛打转向盘配合急加速', 'C', '当车辆不慎陷入泥泞中不能前进时，应先将车稍向后退出，然后改变车轮行进方向，挂入低速挡利用发动机的冲力驶出，车轮空转打滑时，立即停车，挖去泥浆或设法支起车轮，用铺垫柴草、碎石或在驱动轮上缠绕绳索等方式加大车轮的“抓地力”后，再尝试驶出。', 12, 1, '2021-12-19 01:26:08', '2021-12-19 01:26:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4832, '机动车行至泥泞或翻浆路段时，要停车观察，选择平整、坚实或有车辙的路段缓慢通过。', '', '2', '4', NULL, '', '', '', '', 'A', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞或翻浆路段时，要停车察看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 12, 1, '2021-12-19 01:26:09', '2021-12-19 01:26:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4833, '泥泞路对安全行车的影响是车轮极易空转和侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞路段时，要停车查看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 12, 1, '2021-12-19 01:26:09', '2021-12-19 01:26:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4834, '机动车在泥泞路段后轮发生侧滑时，要将转向盘向侧滑的相反方向缓转修正。', '', '2', '4', NULL, '', '', '', '', 'B', '【侧滑】驾驶机动车在泥泞路行车、冰雪路面转弯，速度过快时容易发生侧滑。1、前轮侧滑时，要向侧滑相反方向转动转向盘；2、后轮侧滑时，要向侧滑方向转动转向盘；3、在泥泞路发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正（即向侧滑方向转动转向盘）。', 12, 1, '2021-12-19 01:26:09', '2021-12-19 01:26:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4835, '在泥泞路段行车要牢牢握住转向盘加速通过。', '', '2', '4', NULL, '', '', '', '', 'B', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞路段时，要停车察看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速挡位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防止整车滑移。', 12, 1, '2021-12-19 01:26:09', '2021-12-19 01:26:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4836, '在泥泞道路上行车时，采取的正确做法是什么？', '', '3', '4', NULL, '尽量避免使用行车制动器', '选用中低速挡慢速行驶', '稳握转向盘', '加速通过', 'ABC', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞路段时，要停车察看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速挡位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防止整车滑移。', 12, 1, '2021-12-19 01:26:10', '2021-12-19 01:26:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4837, '驾驶人在行车中经过积水路面时，要怎样做以保证安全？', '/uploadFile/yuanBeiTu837.gif', '1', '4', NULL, '特别注意减速慢行', '迅速加速通过', '保持正常车速通过', '低档加速通过', 'A', '驾驶机动车行经有积水、泥泞、碎石或者易产生扬尘的道路，应当减速慢行或者避让，不得加速通过，以免溅起的泥水、沙石、尘土飞溅到路边行人身上。', 12, 1, '2021-12-19 01:26:10', '2021-12-19 01:26:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4838, '驾驶机动车通过漫水桥，停车观察水情确认安全后，怎样通过？', '', '1', '4', NULL, '挂高速挡快速通过', '时刻观察水流的变化', '做好随时停车准备', '挂低速挡匀速通过', 'D', '机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，挂低速挡匀速通过。', 12, 1, '2021-12-19 01:26:10', '2021-12-19 01:26:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4839, '机动车涉水后，驾驶人要间断轻踩制动踏板，以恢复制动效能。', '', '2', '4', NULL, '', '', '', '', 'A', '涉水驾驶注意事项：①涉水行车时，不要看水流，防止视觉上判断错误导致车辆行驶方向偏移，应看远处固定目标；②机动车涉水后，擦干被水浸湿的部位，保持低速行驶，并间歇踩踏制动踏板，以恢复制动效果；③通过漫水桥时，无法看清桥面或水流速度大时，不可冒险通过。', 12, 1, '2021-12-19 01:26:11', '2021-12-19 01:26:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4840, '漫水道路行车时，要挂高速挡，快速通过。', '', '2', '4', NULL, '', '', '', '', 'B', '不应该挂高速挡，而是要“应选用低速挡，以保证汽车有足够的驱动力；入水时应平稳，以防溅起的水花落入电器设备，造成电器短路；发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘”这样才对。', 12, 1, '2021-12-19 01:26:11', '2021-12-19 01:26:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4841, '驾驶机动车通过漫水路时驾驶人要挂低速挡匀速通过。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车通过漫水路时，驾驶人应挂低速挡保持车辆足够动力，低速缓慢驶入水中，稳住方向、匀速一气通过，中途不要换挡、停车和急转向。', 12, 1, '2021-12-19 01:26:11', '2021-12-19 01:26:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4842, '涉水驾驶要保持车速均匀有足够动力，避免停留。', '', '2', '4', NULL, '', '', '', '', 'A', '水中行驶是有一定阻力的，动力不足很有可能会造成涉水时熄火。保持车速均匀有足够动力，避免停留是正确的。', 12, 1, '2021-12-19 01:26:12', '2021-12-19 01:26:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4843, '驾驶机动车遇有漫水路时，要采取的正确做法是什么？', '', '3', '4', NULL, '停车察明水情', '确认安全后，低速通过', '机动车涉水后，间断轻踏制动踏板', '机动车涉水后，持续轻踏制动踏板', 'ABC', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过，中途不要换挡、停车和急转向。 涉水中，特别注意减速慢行，不要注视水流的变化，避免中途停留；涉水后，间断轻踏制动踏板，以恢复制动效能。', 12, 1, '2021-12-19 01:26:12', '2021-12-19 01:26:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4844, '高速公路上行车，如果因疏忽驶过出口且下一出口距离较远时怎样做？', '', '1', '4', NULL, '沿路肩倒车驶回', '继续向前行驶', '立即停车', '在原地掉头', 'B', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，不得紧急制动；即便是刚刚错过出口且距下一出口很远，也不得掉头逆行或倒回出口。', 12, 1, '2021-12-19 01:26:12', '2021-12-19 01:26:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4845, '机动车在高速公路行驶，以下哪种说法是正确的？', '', '1', '4', NULL, '可在应急车道停车上下人员', '可在紧急停车带停车装卸货物', '可在减速或加速车道上超车、停车', '非紧急情况时不得在应急车道行驶或者停车', 'D', '在高速公路上，除了遇到障碍、发生故障等必须停车的情况外，不准随意停车、停车上下人员或者装卸货物，也不准掉头、转弯、逆行、倒车。', 12, 1, '2021-12-19 01:26:13', '2021-12-19 01:26:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4846, '驾驶机动车驶入高速公路匝道后，以下哪种说法是正确的？', '', '1', '4', NULL, '允许超车', '不准掉头', '允许停车', '可以倒车', 'B', '驾驶机动车驶入高速公路匝道后，不许超车、掉头、停车和倒车。', 12, 1, '2021-12-19 01:26:13', '2021-12-19 01:26:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4847, '机动车驶出高速公路隧道口时，如遇横风会明显出现什么情况？', '', '1', '4', NULL, '减速感', '加速感', '压力感', '方向偏移', 'D', '机动车驶出高速公路隧道时，要注意控制车速，为避免隧道口外的横风引起车辆行驶方向偏移，应双手握稳转向盘。', 12, 1, '2021-12-19 01:26:13', '2021-12-19 01:26:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4848, '需要在高速公路停车时，要选择在什么地方停车？', '', '1', '4', NULL, '匝道', '加速车道', '减速车道', '服务区', 'D', '高速公路行车应选择到服务区停车，不得在匝道、减速车道、加速车道、应急车道内停车。', 12, 1, '2021-12-19 01:26:14', '2021-12-19 01:26:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4849, '发生紧急故障必须停车检查时，要在什么地方停车？', '', '1', '4', NULL, '最外侧行车道上', '内侧行车道上', '应急车道', '匝道口', 'C', '当车辆发生故障必须停车时，应控制好车速，观察前后及右侧交通情况，开启右转向灯，尽快驶离行车道，将车停在紧急停车带或应急车道内，切不可紧急停车，更不要在行车道内停车。', 12, 1, '2021-12-19 01:26:14', '2021-12-19 01:26:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4850, '机动车因故障或者事故在高速公路行车道上紧急停车时，驾乘人员怎么办？', '', '1', '4', NULL, '站在机动车前方', '留在车上等待救援', '站在机动车后方', '迅速转移至右侧路肩上或应急车道内', 'D', '机动车因故障必须在高速公路上停车时，要采取的正确做法是：1、立即开启危险报警闪光灯，夜间还要开启示廓灯和后位灯；2、在车后150米以外设置警告标志；3、车上人员要迅速转移至车右前方护栏以外的安全地点；4、面向来车方向并迅速拨打紧急电话求援或报警。', 12, 1, '2021-12-19 01:26:14', '2021-12-19 01:26:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4851, '驾驶机动车遇到这种情况怎样进入行车道？', '/uploadFile/yuanBeiTu851.gif', '1', '4', NULL, '控制速度随尾车后进入', '加速从第二辆车前进入', '加速从第一辆车前进入', '可从任意两车之间插入', 'A', '根据图来看，三辆车之间的距离是安全距离，首先排除穿插在车之间进入行车道，因为插入的话容易发生擦碰等事故。 如此一来就只有选项A是正确的。因此本题选A。', 12, 1, '2021-12-19 01:26:14', '2021-12-19 01:26:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4852, '驾驶机动车进入高速公路加速车道后，尽快将车速提高到多少？', '', '1', '4', NULL, '30公里/小时以上', '40公里/小时以上', '50公里/小时以上', '60公里/小时以上', 'D', '驾驶机动车进入高速公路加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机，不得在加速车道内紧急制动或停车。 《道路交通安全法实施条例》第七十八条：高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 12, 1, '2021-12-19 01:26:15', '2021-12-19 01:26:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4853, '驾驶机动车进入高速公路隧道前需要注意什么？', '/uploadFile/yuanBeiTu853.gif', '1', '4', NULL, '开启远光灯行驶', '开启示宽灯、尾灯行驶', '开启近光灯行驶', '到达隧道口时鸣喇叭', 'C', '驾车行至高速公路隧道前，要注意观察交通标志和情报板的提示，提前50米左右开启近光灯，同时按照限速标志要求调整车速，密切注意隧道内的交通情况，防止隧道内有事故或有因故障停驶的车辆。', 12, 1, '2021-12-19 01:26:15', '2021-12-19 01:26:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4854, '在同向3车道高速公路上行车，车速高于每小时90公里、低于每小时110公里的机动车不应在哪条车道上行驶？', '', '1', '4', NULL, '最左侧', '中间', '最右侧', '任意', 'A', '高速公路限速规定：1、小型载客汽车，最低60公里/小时，最高120公里/小时（通用）；2、同方向有2条车道的，左侧车道最低车速为100公里/小时；3、同方向有3条以上车道的，最左侧车道的最低车速为110公里/小时，中间车道的最低车速为90公里/小时。 因此，车速高于90公里/小时，低于110公里/小时的机动车，不应在同方向3车道的最左侧车道行驶。', 12, 1, '2021-12-19 01:26:15', '2021-12-19 01:26:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4855, '机动车从匝道驶入高速公路，应当开启什么灯？', '', '1', '4', NULL, '左转向灯', '右转向灯', '危险报警闪光灯', '前照灯', 'A', '驾驶车辆从匝道驶入高速公路行车道，要开启左转向灯。', 12, 1, '2021-12-19 01:26:15', '2021-12-19 01:26:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4856, '机动车驶离高速公路时，应当开启什么灯？', '', '1', '4', NULL, '左转向灯', '右转向灯', '危险报警闪光灯', '前照灯', 'B', '根据《道路交通安全法实施条例》第七十九条规定，机动车从匝道驶入高速公路，应当开启左转向灯，在不妨碍已在高速公路内的机动车正常行驶的情况下驶入车道。机动车驶离高速公路时，应当开启右转向灯，驶入减速车道，降低车速后驶离。', 12, 1, '2021-12-19 01:26:16', '2021-12-19 01:26:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4857, '机动车上高速公路，以下哪种说法是正确的？', '', '1', '4', NULL, '可在匝道、加速车道、减速车道上超车', '不准倒车、逆行、穿越中央分隔带掉头', '非紧急情况时可在应急车道行驶', '可以试车或学习驾驶', 'B', '《中华人民共和国道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，不得有下列行为： 　　 （一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车； 　　 （二）在匝道、加速车道或者减速车道上超车； 　　 （三）骑、轧车行道分界线或者在路肩上行驶； 　　 （四）非紧急情况时在应急车道行驶或者停车； 　　 （五）试车或者学习驾驶机动车。 、', 12, 1, '2021-12-19 01:26:16', '2021-12-19 01:26:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4858, '机动车上高速公路，以下哪种说法是错误的？', '', '1', '4', NULL, '不可骑、轧车道分界线行驶', '不可在路肩上行驶', '可以在匝道、加速车道或者减速车道上超车', '不可学习驾驶', 'C', '《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，不得有下列行为： （一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车； 　　 （二）在匝道、加速车道或者减速车道上超车； 　　 （三）骑、轧车行道分界线或者在路肩上行驶； 　　 （四）非紧急情况时在应急车道行驶或者停车； 　　 （五）试车或者学习驾驶机动车。 请注意，题干要求选出说法错误的选项。', 12, 1, '2021-12-19 01:26:16', '2021-12-19 01:26:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4859, '机动车在高速公路上发生故障或者交通事故，无法正常行驶的，可由同行机动车拖曳、牵引。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第六十八条，机动车在高速公路上发生故障或者交通事故，无法正常行驶的，应当由救援车、清障车拖曳、牵引。', 12, 1, '2021-12-19 01:26:16', '2021-12-19 01:26:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4860, '行驶在高速公路上遇大雾视线受阻时，要立即紧急制动停车。', '', '2', '4', NULL, '', '', '', '', 'B', '行驶在高速公路遇大雾视线受阻时，应开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过20公里/小时，并从最近的出口尽快驶离高速公路。高速公路停车应选择到服务区停车，不得在行车道内停车。因此，题干表述错误。', 12, 1, '2021-12-19 01:26:17', '2021-12-19 01:26:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4861, '机动车在高速公路上行车，如果因疏忽驶过出口，可沿路肩倒车退回出口处。', '', '2', '4', NULL, '', '', '', '', 'B', '高速公路行车注意事项：如果因疏忽驶过出口，只能继续向前行驶至立交桥掉头或在下一出口驶出高速公路后掉头，不得紧急制动；即便是刚刚错过出口且距下一出口很远，也不得掉头逆行或倒回出口。', 12, 1, '2021-12-19 01:26:17', '2021-12-19 01:26:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4862, '机动车驶离高速公路进入匝道后，要使车速降到限定时速以下。', '', '2', '4', NULL, '', '', '', '', 'A', '车辆驶离高速公路进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，也不得未经减速车道直接驶入匝道。 【拓展延伸】 很多学员会误以为题目说的是进入匝道后再减速，题目是错误的。其实题目说的是，进入匝道后，要使车速降到限定时速以下，并未说进入匝道后再减速。机动车可以在向右靠近匝道的过程中，便开始逐渐减速（前提要符合所在车道的规定时速范围）。要注意审题。', 12, 1, '2021-12-19 01:26:17', '2021-12-19 01:26:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4863, '驾驶机动车遇到这种情况可迅速从前车左侧超越。', '/uploadFile/yuanBeiTu863.gif', '2', '4', NULL, '', '', '', '', 'B', '前方蓝色小车正在汇入行车道内，并且前方红色小车在本车的前方，不满足超车的条件。因此要安全驾驶，与前车保持安全距离。', 12, 1, '2021-12-19 01:26:18', '2021-12-19 01:26:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4864, '驾驶机动车看到这个标志时，将车速迅速提高到40公里/小时以上。', '/uploadFile/yuanBeiTu864.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，右侧为限制速度禁令标志，表示机动车行驶速度不得超过40公里/小时。', 12, 1, '2021-12-19 01:26:18', '2021-12-19 01:26:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4865, '驾驶机动车在高速公路匝道上不准停车。', '', '2', '4', NULL, '', '', '', '', 'A', '首先要知道，高速公路匝道是高速公路出口或入口靠右侧的一条道路，也就是说是高速公路的一部分。 根据有关规定，在高速公路上不准掉头、倒车或停车，因此在高速公路匝道上也不能掉头、倒车或是停车。 因此本题正确。', 12, 1, '2021-12-19 01:26:18', '2021-12-19 01:26:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4866, '如动画所示，机动车在高速公路上行车，A车驾驶行为是否正确？', '/uploadFile/yuanBeiTu866.gif', '2', '4', NULL, '', '', '', '', 'B', '由动画可知，A车频繁变更车道，是种陋习，有很大的安全隐患。', 12, 1, '2021-12-19 01:26:19', '2021-12-19 01:26:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4867, '如动画所示，高速公路因发生事故造成堵塞时，A车驾驶行为是否正确？', '/uploadFile/yuanBeiTu867.gif', '2', '4', NULL, '', '', '', '', 'B', '由动画可知，前方道路拥堵，此时应当减速慢行，必要时停车，依次排队等候通过。A车却违法占用紧急车道，行为不正确。', 12, 1, '2021-12-19 01:26:19', '2021-12-19 01:26:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4868, '驾驶机动车从加速车道进入行车道不能影响其他机动车正常行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '一定要注意：不得从匝道直接驶入行车道；从加速车道驶入行车道时不得影响其他车辆的正常行驶。从加速车道驶入行车道时，要注意观察行车道内的情况，在车辆稀少时从正常行驶车辆后方驶入行车道；遇正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道，不得迅速从中间插入。', 12, 1, '2021-12-19 01:26:19', '2021-12-19 01:26:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4869, '驾驶机动车从加速车道汇入行车道有困难时可停车让行。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车从匝道驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。', 12, 1, '2021-12-19 01:26:19', '2021-12-19 01:26:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4870, '高速公路安全距离确认路段用于确认车速在每小时100公里时的安全距离。', '/uploadFile/yuanBeiTu870.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，路面施划有白色折线车距确认线，两道为一组，每组间隔50米，作为车辆驾驶人保持行车安全距离的参考。', 12, 1, '2021-12-19 01:26:20', '2021-12-19 01:26:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4871, '驾驶机动车进入隧道口前按照隧道口标志上规定的速度调整车速。', '/uploadFile/yuanBeiTu871.gif', '2', '4', NULL, '', '', '', '', 'A', '在隧道入口前很明确地竖立了两个道路标志，一个是规定了车速的限速标志，一个是提醒前方是隧道的标志。因此综合起来看，本题就是对的。', 12, 1, '2021-12-19 01:26:20', '2021-12-19 01:26:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4872, '驾驶机动车到达隧道出口时要握稳转向盘，预防出口处的强横向风。', '/uploadFile/yuanBeiTu872.gif', '2', '4', NULL, '', '', '', '', 'A', '驶出隧道时，要注意控制车速，为避免隧道口外的横风引起车辆行驶方向偏移，应双手握稳转向盘。驶出隧道后，在眼睛的明适应过程中不要盲目加速，以免引发危险。', 12, 1, '2021-12-19 01:26:21', '2021-12-19 01:26:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4873, '驾驶机动车进入高速公路加速车道后再开启左转向灯。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车从匝道驶入加速车道后，要先开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得在加速车道内紧急制动或停车。', 12, 1, '2021-12-19 01:26:21', '2021-12-19 01:26:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4874, '在高速公路上驾驶机动车不要频繁地变更车道。', '', '2', '4', NULL, '', '', '', '', 'A', '频繁变更车道是种陋习，而且容易造成事故，不利于安全行车。', 12, 1, '2021-12-19 01:26:21', '2021-12-19 01:26:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4875, '驾驶机动车驶离高速公路要经过减速车道减速后进入匝道。', '', '2', '4', NULL, '', '', '', '', 'A', '车辆驶离高速公路进入减速车道后关闭右转向灯，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，也不得未经减速车道直接驶入匝道。', 12, 1, '2021-12-19 01:26:21', '2021-12-19 01:26:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4876, '机动车在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', '', '2', '4', NULL, '', '', '', '', 'B', '错误的关键在于“直接”两个字，正确的做法是应该先打开左转向灯，在不防碍已在高速公路内的机动车正常行驶的情况下驶入车道。', 12, 1, '2021-12-19 01:26:22', '2021-12-19 01:26:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4877, '在高速公路上，遇尾随较近行驶的机动车时，可以选择时机迅速从中间插入。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶车辆在高速路遇正常行驶车辆尾随相距较近时，应控制好车速，在所有车辆通过后再驶入行车道，不得迅速从中间插入。', 12, 1, '2021-12-19 01:26:22', '2021-12-19 01:26:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4878, '驾驶机动车在高速公路上长时间行驶时，为了防止瞌睡，可以通过超车或迅速变道提神。', '', '2', '4', NULL, '', '', '', '', 'B', '首先驾驶机动车不允许疲劳驾驶，其次在高速公路上进行超车或变道需要观察，安全驾驶。', 12, 1, '2021-12-19 01:26:22', '2021-12-19 01:26:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4879, '遇高速公路限速标志标明的车速与车道行驶车速的规定不一致的，应按照车道行驶规定的车速行驶。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第七十八条：道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', 12, 1, '2021-12-19 01:26:23', '2021-12-19 01:26:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4880, '为确保机动车在高速公路行驶的安全，不得有下列哪些行为？', '', '3', '4', NULL, '倒车逆行，穿越中央分隔带掉头，或在车道内停车', '骑轧车行道分界线或者在路肩上行驶', '在匝道、加速车道或者在减速车道上超车', '试车或者学习驾驶机动车', 'ABCD', '根据《道路交通安全法实施条例》第八十二条 机动车在高速公路上行驶，不得有下列行为： （一）倒车、逆行、穿越中央分隔带掉头或者在车道内停车； （二）在匝道、加速车道或者减速车道上超车； （三）骑、轧车行道分界线或者在路肩上行驶； （四）非紧急情况时在应急车道行驶或者停车； （五）试车或者学习驾驶机动车。', 12, 1, '2021-12-19 01:26:23', '2021-12-19 01:26:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4881, '机动车在高速公路上行驶，遇有雾、雨、雪且能见度在100米至200米之间时，应该怎么做？', '', '3', '4', NULL, '开启雾灯、近光灯、示廓灯、前后位灯', '车速不超过60km/h', '与同车道前车保持100米以上的距离', '从最近的出口尽快驶离高速公路', 'ABC', '根据《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定： （一）能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离； （二）能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； （三）能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。 题干所说的是“能见度在100米至200米之间”，按照道路交通安全法规定，正确操作应该是ABC。 【拓展延伸】 当能见度小于50米，才需要从最近的出口驶离高速公路。根据题目描述的情况，机动车不需要从最近的出口尽快驶离高速公路。', 12, 1, '2021-12-19 01:26:23', '2021-12-19 01:26:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4882, '机动车因故障必须在高速公路上停车时，要采取的正确做法是什么?', '', '3', '4', NULL, '在车后150米处设置故障警告标志', '在车后100米处设置故障警告标志', '夜间要开启示廓灯和后位灯', '要开启危险报警闪光灯', 'ACD', '机动车因故障必须在高速公路上停车时，要采取的正确做法是：1、立即开启危险报警闪光灯，夜间还要开启示廓灯和后位灯；2、在车后150米以外设置警告标志；3、车上人员要迅速转移至车右前方护栏以外的安全地点；4、面向来车方向并迅速拨打紧急电话求援或报警。', 12, 1, '2021-12-19 01:26:23', '2021-12-19 01:26:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4883, '当车辆驶出隧道时，驾驶人易出现图中所示的“明适应”现象，以下做法正确的是什么？', '/uploadFile/yuanBeiTu883.gif', '1', '4', NULL, '加速驶出隧道', '减少与前车距离，利用前车挡住强光', '与前车保持安全距离，降低行驶车速，驶出隧道', '变更至车辆较少的车道，迅速驶出隧道', 'C', '隧道一般比较狭窄、黑暗，路面潮湿，进出隧道，驾驶人的眼睛也会有一个明暗适应过程。当驾驶人从黑暗的隧道驶出，进入非常明亮的环境时，眼睛出现“明适应”现象，因此，通过隧道时一定要合理使用灯光和控制车速，与前车保持安全距离行驶，切忌盲目加速，迅速驶出隧道，以免发生危险。', 12, 1, '2021-12-19 01:26:24', '2021-12-19 01:26:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4884, '当高速公路上车辆发生故障时，人员应当疏散到下图哪个位置。', '/uploadFile/yuanBeiTu884.gif', '1', '4', NULL, '位置A', '位置B', '位置C', '位置D', 'D', '当高速公路上车辆发生故障时，车上人员应当迅速疏散至右侧护栏以外 的安全地带，面向来车方向并迅速拨打紧急电话求援或报警。因此，如图所示人员应当疏散到【位置D】。', 12, 1, '2021-12-19 01:26:24', '2021-12-19 01:26:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4885, '遇到这种跨江、河、海大桥时，可能会遇到横风，要控制好方向。', '/uploadFile/yuanBeiTu885.gif', '2', '4', NULL, '', '', '', '', 'A', '通过跨江、河、海大桥时，易遇到横风，应当握紧方向盘从而稳住方向。', 12, 1, '2021-12-19 01:26:24', '2021-12-19 01:26:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4886, '通过这种路面条件较好的窄桥怎样控制车速？', '/uploadFile/yuanBeiTu886.gif', '1', '4', NULL, '不超过60公里/小时', '不超过50公里/小时', '不超过40公里/小时', '不超过30公里/小时', 'D', '如图所示，通过这种路面条件较好的窄桥时，应控制车速不超过30公里/小时。 根据《道路交通安全法实施条例》第四十六条规定，机动车行驶中通过铁路道口、急弯路、窄路、窄桥时，最高行驶速度不得超过每小时30公里。', 12, 1, '2021-12-19 01:26:25', '2021-12-19 01:26:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4887, '驾驶汽车在进出隧道时应注意什么？', '', '1', '4', NULL, '开启远光灯', '适当提高车速', '关闭近光灯', '提前降低车速', 'D', '驾驶机动车进出隧道时，要提前降低车速，密切注意隧道内的交通情况，进入隧道后，驾驶人要看向前方远处，不要看两侧隧道墙壁，以防眩晕；驶出隧道后，要注意控制车速，在眼睛的明适应过程中不要盲目加速，以免引发危险。', 12, 1, '2021-12-19 01:26:25', '2021-12-19 01:26:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4888, '驾驶汽车进入双向通行的隧道时应注意什么？', '', '3', '4', NULL, '开启近光灯', '靠右侧行驶', '开启远光灯', '注意对向来车', 'ABD', '驾驶机动车进入双向通行的隧道时，应开启近光灯，靠右行驶，注意对向来车，会车时，加大横向距离低速安全会车，会车不可开启远光灯。', 12, 1, '2021-12-19 01:26:25', '2021-12-19 01:26:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4889, '在隧道内通行时哪些行为是不正确的？', '', '3', '4', NULL, '会车使用远光灯', '在隧道内超车', '会车时保持安全间距', '开启近光灯行驶', 'AB', '驾驶机动车进出隧道时，要提前降低车速，密切注意隧道内的交通情况，进入隧道后，驾驶人要看向前方远处，不要看两侧隧道墙壁，以防眩晕；驶出隧道后，要注意控制车速，在眼睛的明适应过程中不要盲目加速，以免引发危险。请注意！题干要求选出不正确的行为，在隧道内使用远光灯和超车都是十分危险的行为。', 12, 1, '2021-12-19 01:26:25', '2021-12-19 01:26:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4890, '在山区道路跟车行驶的距离要比平路时大。', '/uploadFile/yuanBeiTu890.gif', '2', '4', NULL, '', '', '', '', 'A', '山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 12, 1, '2021-12-19 01:26:26', '2021-12-19 01:26:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4891, '在这种山区道路怎样跟车行驶？', '/uploadFile/yuanBeiTu891.gif', '1', '4', NULL, '紧跟前车行驶', '加大安全距离', '减小跟车距离', '尽快超越前车', 'B', '山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 12, 1, '2021-12-19 01:26:26', '2021-12-19 01:26:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4892, '在这种山区危险路段怎样安全会车？', '/uploadFile/yuanBeiTu892.gif', '3', '4', NULL, '选择安全的地点', '做到先让、先慢、先停', '靠山体一侧的让行', '不靠山体一侧的让行', 'ABC', '山区会车时，应尽量选择路面较宽的路段，并主动减速，选择安全路段减速或停车交会，靠山体的一方应该让行。当对面来车速度较高或靠路中行驶时，应减速靠右行驶，不得加速或也靠道路中心行驶，防止发生剐碰事故。', 12, 1, '2021-12-19 01:26:26', '2021-12-19 01:26:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4893, '驾驶汽车在山区道路下陡坡时，怎样利用发动机制动控制车速？', '', '1', '4', NULL, '挂入空挡', '挂入低速挡', '踏下离合器踏板', '挂入高速挡', 'B', '驾驶机动车在山区道路下陡坡时，要挂低速挡位，充分利用发动机制动来控制车速，严禁空挡滑行。', 12, 1, '2021-12-19 01:26:27', '2021-12-19 01:26:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4894, '驾驶汽车在山区道路转弯下陡坡路段遇对面来车怎样行驶？', '', '3', '4', NULL, '转弯前减速', '进入弯道后加速', '靠路右侧行驶', '挂空挡滑行', 'AC', '驾驶机动车在山区道路转弯下陡坡遇对面来车时，应当主动减速靠右行驶，不得加速或靠道路中心行驶，以免发生剐碰事故，下坡行驶，严禁空挡滑行。', 12, 1, '2021-12-19 01:26:27', '2021-12-19 01:26:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4895, '驾驶汽车在山区上陡坡路段怎样行驶？', '', '3', '4', NULL, '挂高速档加速冲坡', '提前观察坡道长度', '尽量避免途中减挡', '上坡前减挡保持动力', 'BCD', '驾驶机动车上陡坡路段行驶时，要提前观察路况、坡道长度和坡度，在坡底减速减挡，以保证车辆具有充足的动力中途不换挡，避免爬坡中途减挡或车辆熄火。上陡坡时，要在坡底提前减挡，加速冲坡。驶近坡道顶端无法看清前方路口时，要减速慢行并鸣喇叭示意，不得加速冲过坡顶，防止对向有来车和其他意外情况', 12, 1, '2021-12-19 01:26:27', '2021-12-19 01:26:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4896, '驾驶汽车在山区上这种陡坡道转弯时怎样行驶？', '/uploadFile/yuanBeiTu896.gif', '3', '4', NULL, '转弯前减速', '靠右侧行驶', '鸣喇叭提示', '转弯时加档', 'ABC', '驾驶机动车通过如图所示的陡坡弯道时，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”，不要占对向车道。', 12, 1, '2021-12-19 01:26:28', '2021-12-19 01:26:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4897, '驾驶汽车可以在这种急弯处超车。', '/uploadFile/yuanBeiTu897.gif', '2', '4', NULL, '', '', '', '', 'B', '傍山险路，而且是急弯路，不具备超车条件。', 12, 1, '2021-12-19 01:26:28', '2021-12-19 01:26:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4898, '在这种路面较窄的急弯处行车时要注意什么？', '/uploadFile/yuanBeiTu898.gif', '3', '4', NULL, '集中注意力', '降低车速', '注意鸣喇叭', '做好停车准备', 'ABCD', '在图中路面较窄的急弯处行车，为确保安全，司机应该集中注意力降低车速行驶。同时由于视线被阻挡，司机可以使用鸣喇叭的方式来提示对方来车注意避让。此外，由于路面十分狭窄，很难同时供两辆车通行，为了安全要随时做好停车让行的准备。请注意！做好停车准备，不是把车停在急弯处。', 12, 1, '2021-12-19 01:26:28', '2021-12-19 01:26:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4899, '夜间在这种情况下跟车要注意观察前车信号灯的变化，随时做好减速或停车的准备。', '/uploadFile/yuanBeiTu899.gif', '2', '4', NULL, '', '', '', '', 'A', '夜间跟车行驶时，要使用近光灯，保持安全距离，注意观察前车信号灯的变化，随时做好减速或停车的准备。跟车时不得使用远光灯，同时要提防路侧黑暗处的行人，防止其突然横穿。', 12, 1, '2021-12-19 01:26:28', '2021-12-19 01:26:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4900, '夜间遇到这种后车发出超车信号时怎样行驶？', '/uploadFile/yuanBeiTu900.gif', '1', '4', NULL, '靠路中心减速行驶', '加速甩掉后车', '开启左转向灯警示', '减速靠右侧让行', 'D', '夜间行车，看到后方来车的灯光越来越近，且来车发出超车信号时，要减速让行，有条件时靠右侧让路。', 12, 1, '2021-12-19 01:26:29', '2021-12-19 01:26:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4901, '夜间在这种道路条件怎样跟车行驶？', '/uploadFile/yuanBeiTu901.gif', '3', '4', NULL, '注意前车信号灯变化', '使用近光灯', '保持安全距离', '做好减速或停车准备', 'ABCD', '夜间跟车行驶时，要使用近光灯，保持安全距离，注意观察前车信号灯的变化，随时做好减速或停车的准备。跟车时不得使用远光灯，同时要提防路侧黑暗处的行人，防止其突然横穿。', 12, 1, '2021-12-19 01:26:29', '2021-12-19 01:26:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4902, '夜间驾驶汽车在人行横道前遇行人横过时怎样行驶？', '/uploadFile/yuanBeiTu902.gif', '1', '4', NULL, '交替变换远近光灯绕过行人', '开启近光灯绕过行人', '使用远光灯绕过行人', '停车让行人优先通过', 'D', '驾驶机动车遇行人或非机动车通过人行横道时，及时停车让行，不得抢行或绕行。', 12, 1, '2021-12-19 01:26:29', '2021-12-19 01:26:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4903, '夜间驾驶汽车驶近上坡路坡顶怎样行驶？', '', '3', '4', NULL, '加速冲过坡顶', '开启远光灯', '合理控制车速', '交替变换远近光灯', 'CD', '夜间驾驶汽车驶近上坡路坡顶时，无法看清前方路口状况，要减速慢行，并交替变换远近光灯示意，不得开启远光灯，不得加速冲过坡顶，防止对向有来车和其他意外情况。', 12, 1, '2021-12-19 01:26:30', '2021-12-19 01:26:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4904, '夜间驾驶汽车在急弯道停车时要开启危险报警闪光灯。', '', '2', '4', NULL, '', '', '', '', 'B', '注意陷阱！夜间驾驶机动车在急弯道处不得停车，这道题重点不是灯光。 《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定： (二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；', 12, 1, '2021-12-19 01:26:30', '2021-12-19 01:26:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4905, '夜间驾驶汽车在急弯道超车时要不断变换远、近光灯。', '', '2', '4', NULL, '', '', '', '', 'B', '注意陷阱，不得在急弯道处超车。《道路交通安全法》第四十三条：有下列情形之一的，不得超车：（一）前车正在左转弯、掉头、超车的；（二）与对面来车有会车可能的；（三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的；（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 12, 1, '2021-12-19 01:26:30', '2021-12-19 01:26:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4906, '夜间行车中汽车发生故障需要停车时怎么办？', '', '3', '4', NULL, '尽量选择安全区域停车', '开启危险报警闪光灯', '开启示廓灯和后位灯', '按规定设置警告标志', 'ABCD', '夜间行车中汽车发生故障需要停车时，尽量选择安全区域停车，开启危险报警闪光灯、示廓灯和后位灯，并在车后方设置警告标志。所以以上选项均正确。', 12, 1, '2021-12-19 01:26:30', '2021-12-19 01:26:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4907, '在雨天哪类路面最容易发生侧滑？', '', '1', '4', NULL, '刚下雨的路面', '大雨过后路面', '暴雨中的路面', '大雨中的路面', 'A', '刚开始下雨时，路面最容易打滑。因为刚下雨的路面，粉尘混合雨水造成泥巴路，容易滑。下雨时间久了，雨水把这些粉尘冲走了，就不滑了。', 12, 1, '2021-12-19 01:26:31', '2021-12-19 01:26:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4908, '雨天影响安全行车的主要因素有哪些？', '', '3', '4', NULL, '视线受阻', '路面湿滑', '附着力变小', '行驶阻力增大', 'ABC', '雨天行驶，视线受阻、路面湿滑，车轮的附着力随车速的增加急剧变小，车轮很容易发生侧滑，当车辆高速行驶时也易发生“水滑”现象，因此一定要合理控制车速。', 12, 1, '2021-12-19 01:26:31', '2021-12-19 01:26:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4909, '雨天遇到这种行人占道行走时怎样通行？', '/uploadFile/yuanBeiTu909.gif', '3', '4', NULL, '提前减速行驶', '提前鸣喇叭提醒', '不得急加速绕行', '保持安全间距', 'ABCD', '雨天行驶，一定要选择安全车速，同时要与其他车辆、行人等保持足够的安全距离。遇撑雨伞和穿雨衣的行人在道路上行走时，要提前减速、鸣喇叭进行提示，密切注意观察其动态，保持一定的安全距离低速通过，同时要随时准备应付突发状况。', 12, 1, '2021-12-19 01:26:31', '2021-12-19 01:26:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4910, '在积雪覆盖的冰雪路行车时，可根据路边树木、电杆等参照物判断行驶路线。', '/uploadFile/yuanBeiTu910.gif', '2', '4', NULL, '', '', '', '', 'A', '在有积雪的道路行车，当路面被积雪覆盖或道路轮廓难以辨别时，可根据路边树木、电线杆等参照物判断行驶路线，控制车辆低速行驶。', 12, 1, '2021-12-19 01:26:31', '2021-12-19 01:26:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4911, '由于冰雪路面不能使用紧急制动，遇到突然情况可采取急转向的方法躲避。', '', '2', '4', NULL, '', '', '', '', 'B', '冰雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向，雪融化后会结成薄冰，路面非常滑，一定要加大安全距离低速缓慢行驶，要避免紧急制动和急转方向，以防车辆侧滑驶出路面。', 12, 1, '2021-12-19 01:26:32', '2021-12-19 01:26:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4912, '在这种冰雪路面怎样跟车行驶？', '/uploadFile/yuanBeiTu912.gif', '1', '4', NULL, '保持较大的跟车距离', '开启危险报警闪光灯', '不断变换远近光灯', '持续鸣喇叭提示前车', 'A', '雪天跟车行驶要与前车保持较大的安全距离，处理紧急情况不能使用紧急制动和急转向的方法躲避，应充分利用发动机牵阻作用减速或停车。', 12, 1, '2021-12-19 01:26:32', '2021-12-19 01:26:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4913, '在这种结冰的道路上怎样会车？', '/uploadFile/yuanBeiTu913.gif', '1', '4', NULL, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', 'C', '驾驶机动车在结冰的道路上会车时，要提前减速缓慢会车，因为对向来车有可能发生侧滑。', 12, 1, '2021-12-19 01:26:32', '2021-12-19 01:26:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4914, '在山区冰雪道路上遇到这种前车正在上坡的情况如何处置？', '/uploadFile/yuanBeiTu914.gif', '1', '4', NULL, '前车通过后再上坡', '迅速超越前车上坡', '低速超越前车上坡', '紧随前车后上坡', 'A', '在山区冰雪道路上行车，遇前车正在爬坡时，不得紧随或超越前车爬坡，要等前车通过坡顶后再上坡，避免前车突然停车或后溜造成事故。', 12, 1, '2021-12-19 01:26:33', '2021-12-19 01:26:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4915, '冰雪路面对行车有哪些不利影响？', '', '3', '4', NULL, '车辆操控难度增大', '制动距离延长', '易产生车轮滑转', '极易发生侧滑', 'ABCD', '雪天驾驶，积雪路面行驶阻力增大，制动距离延长，路面被积雪覆盖，驾驶人很难辨别方向，雪融化后会结成薄冰，路面非常滑，车辆操控难度增大，极易发生侧滑，因此雪天及冰雪路面行车，一定要加大安全距离低速行驶。', 12, 1, '2021-12-19 01:26:33', '2021-12-19 01:26:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4916, '在这种有车辙的冰雪路段怎样行驶？', '/uploadFile/yuanBeiTu916.gif', '3', '4', NULL, '避免紧急制动', '循车辙行驶', '避免急转方向', '降低车速行驶', 'ABCD', '当路面被积雪覆盖或道路轮廓难以辨别时，可根据路边树木、电线杆等参照物判断行驶路线，控制车辆低速行驶；在有车辙的路段要循车辙低速行驶，避免紧急制动和急转方向，以防车辆侧滑驶出路面。', 12, 1, '2021-12-19 01:26:44', '2021-12-19 01:26:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4917, '驾驶汽车在冰雪道路上怎样安全行车？', '', '3', '4', NULL, '必要时安装防滑链', '必须降低车速', '开启雾灯行驶', '利用发动机制动', 'ABD', '在冰雪道路上行车，为预防积雪反射引起炫目，驾驶人可以佩戴墨镜，但不可以开启远光灯行驶，有条件的要安装防滑链，挂低速挡缓慢行驶，处理紧急情况不能使用紧急制动和急转向的方法，应充分利用发动机牵阻作用减速或停车。', 12, 1, '2021-12-19 01:26:45', '2021-12-19 01:26:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4918, '如图所示，驾驶机动车在这样的路段怎样跟车行驶。', '/uploadFile/yuanBeiTu918.gif', '1', '4', NULL, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', 'B', '山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 12, 1, '2021-12-19 01:26:45', '2021-12-19 01:26:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4919, '如图所示，驾驶机动车遇前方白色车辆，以下说法正确的是什么。', '/uploadFile/yuanBeiTu919.gif', '1', '4', NULL, '快速超越前车', '只要对向无来车，可进行超车', '保持安全距离，跟车行驶', '鸣喇叭示意让行', 'C', '如图所示路况，应保持安全距离跟车行驶，严禁在路面狭窄、急转弯、连续转弯或对面有来车等不具备条件的路段超车。', 12, 1, '2021-12-19 01:26:46', '2021-12-19 01:26:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4920, '如图所示，驾驶机动车在这样的路段遇前方两车交会应及时减速。', '/uploadFile/yuanBeiTu920.gif', '2', '4', NULL, '', '', '', '', 'A', '山区会车时，应尽量选择路面较宽的路段，并主动减速、选择安全路段减速或停车交会。当对面来车速度较高或靠路中行驶时，应减速靠右行驶，不得加速或也靠道路中心行驶，防止发生剐碰事故。', 12, 1, '2021-12-19 01:26:46', '2021-12-19 01:26:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4921, '如图所示，驾驶机动车在这种落石多发的山区道路行驶，以下说法正确的是什么？', '/uploadFile/yuanBeiTu921.gif', '1', '4', NULL, '尽量靠道路左侧通行', '停车瞭望，缓慢通过', '尽量避免临时停车', '勤鸣喇叭，低速通行', 'C', '驾驶机动车通过经常有落石或经常发生塌方、泥石流的山区路段时，要减速慢行，注意观察，尽快通过，不要停留。', 12, 1, '2021-12-19 01:26:56', '2021-12-19 01:26:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4922, '如图所示，驾驶机动车遇到这种路段时，以下说法错误的是什么？', '/uploadFile/yuanBeiTu922.gif', '1', '4', NULL, '应提前降低车速', '应提前降低挡位', '尽量利用发动机制动控制车速', '尽量利用驻车制动器控制车速', 'D', '驾驶机动车遇如图所示下坡路行驶，要挂中低速档位，充分利用发动机制动来控制车速，严禁空挡滑行。请注意，题干要求选出说法错误的选项。', 12, 1, '2021-12-19 01:26:57', '2021-12-19 01:26:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4923, '驾驶机动车遇暴雨，无法看清路面情况，以下做法正确的是什么？', '', '1', '4', NULL, '保持原速行驶', '减速行驶', '打开危险报警闪光灯，将机动车停到路外', '减速行驶，不断鸣喇叭，提醒周边驾驶人', 'C', '驾驶机动车遇暴雨，靠雨刮器难以改善视线看清路面的情况下，要选择安全地点停车，开启危险报警闪光灯，待雨小或雨停后再继续行驶。', 12, 1, '2021-12-19 01:26:57', '2021-12-19 01:26:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4924, '如图所示，在这种情况下，应该减速慢行。', '/uploadFile/yuanBeiTu924.gif', '2', '4', NULL, '', '', '', '', 'A', '如图所示，大雨天行车，视线受阻路面湿滑。雨滴使前风窗玻璃和后视镜变得模糊，前车溅起的水雾使得视线受到严重影响，潮湿的路面不但反光，还容易使车辆打滑，因此雨天一定要合理控制车速，减速慢行。', 12, 1, '2021-12-19 01:26:57', '2021-12-19 01:26:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4925, '雨天驾驶机动车减速慢行的主要原因是什么？', '', '3', '4', NULL, '影响驾驶人视野', '过快的速度会使得机动车油耗增加', '制动距离会增大', '紧急制动易发生侧滑', 'ACD', '雨天行车驾驶人视线受阻，路面湿滑，车轮的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时，制动距离会增大，易发生“水滑”现象。因此一定要合理控制车速，不要急转向或紧急制动，应松抬加速踏板，充分利用发动机制动减速。', 12, 1, '2021-12-19 01:26:58', '2021-12-19 01:26:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4926, '车辆发生“水滑”时，以下做法正确的是什么？', '', '3', '4', NULL, '不可急踩制动踏板', '逐渐松抬加速踏板，让车速逐渐减缓', '不得迅速转向', '立刻猛踏制动踏板，降低车速', 'ABC', '所谓“水滑”现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间形成“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。因此当车辆发生“水滑”时，应松抬加速踏板，充分利用发动机制动减速，不可急转向或紧急制动。', 12, 1, '2021-12-19 01:26:58', '2021-12-19 01:26:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4927, '雨天驾驶机动车，不宜超车的主要原因是什么？', '', '3', '4', NULL, '不能准确判断周围车辆距离', '周围车辆驾驶人不容易看清超车信号', '道路湿滑，车辆易出现侧滑现象', '不能够及时发现危险情况', 'ABCD', '雨中行车，要随时注意前车的行驶速度和方向，绝不可因前车速度慢而加速超车。尤其是在高速公路上，由于各车道的车速相对较高，司机的视角变窄，加上路面湿滑，强行越线超车时，稍动方向就很容易造成车轮打滑，极易造成与其他车辆发生刮蹭，引发车辆侧翻等意外事故。所以以上选项均正确。', 12, 1, '2021-12-19 01:26:58', '2021-12-19 01:26:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4928, '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么？', '', '3', '4', NULL, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', 'AC', '雨天路面湿滑，车轮的附着力随车速的增加急剧变小，紧急制动很容易发生侧滑，后车也会反应不及造成追尾事故。', 12, 1, '2021-12-19 01:26:59', '2021-12-19 01:26:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4929, '驾驶机动车在山区道路应紧跟前车之后行驶。', '', '2', '4', NULL, '', '', '', '', 'B', '山区道路突发状况多，需保持一定的距离来应急，保证行车安全。', 12, 1, '2021-12-19 01:26:59', '2021-12-19 01:26:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4930, '如图所示，该货运车辆驶出高速公路的方法是正确的。', '/uploadFile/yuanBeiTu930.gif', '2', '4', NULL, '', '', '', '', 'B', '驶出高速公路需要提前在白色虚线处进入匝道，图中的货运车辆明显压到了白色实线，所以是错误的。', 12, 1, '2021-12-19 01:26:59', '2021-12-19 01:26:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4931, '驾驶机动车在高速公路上行驶，能见度小于50米时，只要车速不超过20公里/小时，可以不驶离高速公路。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第八十一条规定，机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件，当能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 12, 1, '2021-12-19 01:27:00', '2021-12-19 01:27:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4932, '驾驶机动车在距离隧道前多少米内不得停车？', '', '1', '4', NULL, '30米', '50米', '80米', '100米', 'B', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。【口5】', 12, 1, '2021-12-19 01:27:00', '2021-12-19 01:27:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4933, '如图所示，驾驶机动车在雨天行经交叉口时必须鸣喇叭，并加速通过，以免造成交通混乱。', '/uploadFile/yuanBeiTu933.gif', '2', '4', NULL, '', '', '', '', 'B', '如图所示，雨天遇行人时，要提前减速、鸣喇叭，与其保持一定的安全距离低速通过，不得抢行或从其身边急加速绕过。', 12, 1, '2021-12-19 01:27:00', '2021-12-19 01:27:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4934, '驾驶机动车在城市快速路上行驶，以下做法错误的是什么？', '', '3', '4', NULL, '在最左侧车道内停车', '在路肩上行驶', '学习驾驶机动车', '倒车', 'ABCD', '驾驶机动车在城市快速路上行驶，不得有下列行为：1，倒车、逆行、在车道内停车；2，骑、轧车行道分界线或者在路肩上行驶；3，试车或者学习驾驶机动车等。', 12, 1, '2021-12-19 01:27:00', '2021-12-19 01:27:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4935, '驾驶机动车在高速公路上行驶不得倒车、逆行、穿越中央分隔带掉头或者在车道内停车。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在高速公路上行驶不得倒车、逆行、穿越中央分隔带掉头或者在车道内停车。', 12, 1, '2021-12-19 01:27:01', '2021-12-19 01:27:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4936, '以下这个标志的含义是什么？', '/uploadFile/yuanBeiTu936.gif', '1', '4', NULL, '电子不停车收费专用车道', '应急车道', '绿色通道', '快速公交车道', 'A', '这个标志的含义是前方有电子不停车收费专用车道。', 12, 1, '2021-12-19 01:27:01', '2021-12-19 01:27:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4937, '驾驶机动车驶入高速公路收费口时，没有安装ETC卡的车辆，不能驶入ETC车道。', '', '2', '4', NULL, '', '', '', '', 'A', 'ETC通道收费方式为电子不停车收费，没有安装ETC卡的车辆，驶入ETC车道无法通过，只能驶入人工收费通道。', 12, 1, '2021-12-19 01:27:01', '2021-12-19 01:27:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4938, '驾驶机动车在高速公路减速车道行驶时要依次通行。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在高速公路减速车道行驶时要依次通行。高速公路出口的匝道一般限速每小时60公里以下，弯道限速每小时40公里以下。而在高速公路上行驶时的车速远高于这两个数字，因此应当先减速再进入匝道，不可倒车、掉头、超车。', 12, 1, '2021-12-19 01:27:01', '2021-12-19 01:27:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4939, '驾驶机动车在匝道内行驶，以下做法正确的是什么？', '', '1', '4', NULL, '可以超车', '可以倒车', '依次通行', '可以掉头', 'C', '“匝道”是立交桥或高架路的进出道引道。驾驶机动车在匝道内不许超车、掉头、倒车和停车。', 12, 1, '2021-12-19 01:27:02', '2021-12-19 01:27:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4940, '驾驶机动车在高速公路上行驶，车速超过100公里/小时，只要与同车道前车保持80米的距离即可。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车在高速公路上行驶，车速超过每小时100公里时，应当与同车道前车保持100米以上的距离。', 12, 1, '2021-12-19 01:27:02', '2021-12-19 01:27:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4941, '驾驶机动车行经此路段多少米内不得停车？', '/uploadFile/yuanBeiTu941.gif', '1', '4', NULL, '30米', '50米', '80米', '100米', 'B', '《道路交通安全法实施条例》第六十三条 机动车在道路上临时停车，应当遵守下列规定：(二)交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车；', 12, 1, '2021-12-19 01:27:02', '2021-12-19 01:27:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4942, '驾驶机动车在隧道中超车时应该注意观察、谨慎驾驶。', '', '2', '4', NULL, '', '', '', '', 'B', '根据《道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。', 12, 1, '2021-12-19 01:27:03', '2021-12-19 01:27:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4943, '驾驶机动车夜间通过拱桥时应当交替使用远近光灯示意。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 12, 1, '2021-12-19 01:27:03', '2021-12-19 01:27:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4944, '驾驶机动车在山区冰雪道路上行驶，遇到前车正在爬坡时，后车应选择适当地点停车，等前车通过后再爬坡。', '', '2', '4', NULL, '', '', '', '', 'A', '在山区冰雪道路上行车，遇前车正在爬坡时，不得紧随或超越前车爬坡，要等前车通过坡顶后再上坡，避免前车突然停车或后溜造成事故。因此，题干表述正确。', 12, 1, '2021-12-19 01:27:03', '2021-12-19 01:27:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4945, '驾驶机动车驶入高速公路收费口应减速慢行，有序行驶，选择绿灯亮起的收费口进入。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车驶近高速公路收费站时，要严格遵守限速规定，选择通道上方绿灯亮且车辆较少的收费口，依次排队通过，切勿争道抢行。进入收费口尽量将车身靠近收费亭，驾驶室门窗对齐收费窗口时停车，在入口处领到通行卡后，要妥善收存好，以备出口时交回通行卡和缴纳通行费。', 12, 1, '2021-12-19 01:27:04', '2021-12-19 01:27:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4946, '驾驶机动车在高速公路减速车道行驶时，以下做法正确的是什么？', '', '1', '4', NULL, '可以超车', '可以倒车', '依次通行', '可以掉头', 'C', '《高速公路交通管理办法》第十七条 驾驶机动车在高速公路上行驶，不得有下列行为： 1、倒车、逆向、穿越中央分隔带掉头或者在车道内停车； 2、在匝道、加速车道或者减速车道上超车、停车； 3、骑、压车道分界线和在超车道上连续行驶； 4、非紧急情况时在应急车道行驶或者停车； 5、试车或者学习驾驶机动车； 6、在高速公路上行驶的载货汽车车厢不得载人。', 12, 1, '2021-12-19 01:27:04', '2021-12-19 01:27:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4947, '以下关于驶离高速公路的做法正确的是什么？', '', '3', '4', NULL, '提前开启右转向灯', '驶入减速车道', '按减速车道规定的时速行驶', '加速直接驶离高速公路', 'ABC', '驾驶机动车驶离高速公路时，提前开启右转向灯，在不妨碍已在高速公路内的车辆正常行驶的情况下驶入减速车道，降低车速后驶入匝道。', 12, 1, '2021-12-19 01:27:04', '2021-12-19 01:27:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4948, '关于在高速公路匝道路段行驶，以下说法正确的是什么？', '', '1', '4', NULL, '从匝道驶入高速公路，应当开启右转向灯', '驶离高速公路进入匝道时，应当开启右转向灯', '可在匝道上超车', '驶入错误的匝道后，可倒车行驶回高速公路', 'B', '驾驶机动车从匝道驶入高速公路，要开启左转向灯；驶离高速公路时，提前开启右转向灯，在不妨碍已在高速公路内的车辆正常行驶的情况下驶入减速车道，降低车速后驶入匝道。注意：在匝道内不许超车、掉头、停车和倒车。', 12, 1, '2021-12-19 01:27:05', '2021-12-19 01:27:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4949, '驾驶机动车在高速公路上行驶，遇有能见度小于100米的气象条件时，如何安全行驶？', '', '3', '4', NULL, '与前车保持50米以上的距离', '车速不得超过40公里/小时', '开启危险报警闪光灯', '在应急车道行驶', 'ABC', '《道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离。', 12, 1, '2021-12-19 01:27:05', '2021-12-19 01:27:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4950, '如图所示，驾驶机动车在暴雨天气条件下行驶，当刮水器无法刮净雨水影响行车安全时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu950.gif', '1', '4', NULL, '减速行驶', '集中注意力谨慎驾驶', '注意观察，减速靠边停车', '以正常速度行驶', 'C', '驾驶机动车在暴雨天气条件下行驶，当靠刮水器难以改善视线时，要注意观察减速慢行，选择安全地点停车，开启示廓灯和危险报警闪光灯，待雨小或雨停后再继续行驶。', 12, 1, '2021-12-19 01:27:05', '2021-12-19 01:27:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4951, '以下这个标志的含义是什么？', '/uploadFile/yuanBeiTu951.gif', '1', '4', NULL, '设有电子不停车收费（ETC）车道的收费站', '停车领卡标志', '服务区标志', '紧急停车带', 'A', '图中标志表示前方设有电子不停车收费（ETC）车道的收费站。', 12, 1, '2021-12-19 01:27:06', '2021-12-19 01:27:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4952, '如图所示，驾驶机动车驶入减速车道后最高时速不能超过多少？', '/uploadFile/yuanBeiTu952.gif', '1', '4', NULL, '60公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', 'C', '驾驶机动车驶离高速公路，进入减速车道后关闭右转向灯，注意观察车速表（降到规定速度以下），逐渐减速后进入匝道。注意：1、车速不得超过限速标志标明的速度；2、不得未经减速车道直接驶入匝道。右侧限速40标志表示最高时速不能超过40公里/小时。', 12, 1, '2021-12-19 01:27:06', '2021-12-19 01:27:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4953, '驾驶机动车驶离高速公路进入匝道时，应当加速驶离。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车驶离高速公路时，提前开启右转向灯，适当减速，平顺地进入减速车道，注意观察车速表，逐渐减速后进入匝道，车速不得超过限速标志标明的速度，也不得未经减速车道减速直接驶入匝道。', 12, 1, '2021-12-19 01:27:06', '2021-12-19 01:27:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4954, '驾驶机动车通过短而陡的上坡坡道时，采用加速冲坡的方法，在接近坡顶时应提前松开加速踏板，利用惯性冲过坡顶。', '', '2', '4', NULL, '', '', '', '', 'A', '通过短而陡的上坡坡道时，要在坡底提前减挡，加速冲坡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火；在接近坡顶时应提前松开加速踏板，利用惯性冲过坡顶。', 12, 1, '2021-12-19 01:27:06', '2021-12-19 01:27:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4955, '驾驶机动车遇浓雾或沙尘暴时，行驶速度不要过慢，避免后方来车追尾。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车遇浓雾或沙尘暴时，要将车速控制在能及时停车的范围内，保持足够的安全间距，谨慎缓慢行驶，可多使用喇叭引起行人和车辆注意。', 12, 1, '2021-12-19 01:27:07', '2021-12-19 01:27:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4956, '驾驶机动车在高速公路意外碰撞护栏时，应迅速向相反方向转向修正。', '', '2', '4', NULL, '', '', '', '', 'B', '在高速公路驾驶机动车意外碰撞护栏时，要稳住方向，适当向碰撞一侧转向，不可乱打或急打转向盘，否则可能导致车辆连续碰撞两侧护栏，甚至发生倾翻事故。在高速公路行驶时发生碰撞护栏的事故后，如果车辆受损不严重，应将车移到应急车道安全停车。', 12, 1, '2021-12-19 01:27:07', '2021-12-19 01:27:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4957, '在冰雪路面制动时，发现车辆偏离方向，以下做法正确的是？', '', '1', '4', NULL, '连续轻踩轻放刹车', '用力踩刹车', '停止踩刹车', '以上做法都不对', 'C', '冰雪路面行车，发现车辆偏离方向行驶时，不能使用紧急制动和急转向的方法躲避，应充分利用发动机牵阻作用减速或停车。', 12, 1, '2021-12-19 01:27:07', '2021-12-19 01:27:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4958, '为什么大雪天气，在有雪泥的路上超车危险？', '', '3', '4', NULL, '雪泥可以增加轮胎的附着力', '飞起的雪泥使视线不好', '雪泥下的路面更容易打滑', '遇紧急情况制动距离长', 'BCD', '大雪天气行车，积雪路面行驶阻力增大，路面被积雪覆盖，驾驶人很难辨别方向；且雪泥下的路面非常滑，一定要加大安全距离低速行驶；处理紧急情况不能使用紧急制动和急转向的方法躲避，应充分利用发动机牵阻作用减速或停车，因此，冰雪道路行车，尽量不要超车。', 12, 1, '2021-12-19 01:27:07', '2021-12-19 01:27:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4959, '大雾天行驶，以下做法正确的是？', '', '1', '4', NULL, '可以紧急制动刹车', '可以紧急制动刹车，但是需要停到紧急停车带上', '不可以紧急制动刹车，因为会造成后面的车辆追尾', '以上说法都不对', 'C', '大雾天行车时，应谨慎缓慢并加大安全距离行驶，不可猛踩或快松油门，更不能紧急制动和急打方向盘，以防造成追尾事故。', 12, 1, '2021-12-19 01:27:08', '2021-12-19 01:27:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4960, '在这样的雾天跟车行驶，以下说法不正确的是？', '/uploadFile/yuanBeiTu960.gif', '1', '4', NULL, '加大跟车间距', '注意前车动态', '降低行车速度', '缩小跟车距离', 'D', '请注意，题干要求选出说法不正确的选项。雾天跟车行驶，应减速并增大安全距离，谨慎缓慢行驶。因此，缩小跟车距离说法不正确。', 12, 1, '2021-12-19 01:27:08', '2021-12-19 01:27:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4961, '雾天行车开启雾灯是因为雾灯放射的灯光具有更好的穿透力，更容易让道路中其他车辆驾驶员注意到自己的车辆。', '', '2', '4', NULL, '', '', '', '', 'A', '雾灯就是雨雾天气里使用的灯光信号，其主要特点是在雾中的穿透性较强，在能见度低下的天气里容易让车辆或行人及早注意到，从而有效预防事故的发生。但是，雾灯并不能当作日常照明灯使用，在能见度好的夜间使用雾灯，其恶劣影响不亚于使用远光灯。', 12, 1, '2021-12-19 01:27:08', '2021-12-19 01:27:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4962, '浓雾中行车听到对方车辆鸣喇叭时，只要视野中看不到，可不必理会。', '', '2', '4', NULL, '', '', '', '', 'B', '雾天在道路上行车，可多使用喇叭引起对向车辆注意，听到对向车辆鸣喇叭时，要鸣喇叭回应。', 12, 1, '2021-12-19 01:27:09', '2021-12-19 01:27:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4963, '假如您的机动车雾天在道路中抛锚不能移动，你应该采取以下措施：', '', '3', '4', NULL, '立即打开危险警报灯', '在车后设置危险警告标志，警告来往车辆', '要求车内所有人员立即下车远离事故车辆', '立即拨打交通事故报警电话122请求援助', 'ABCD', '《道路交通安全法》第五十二条： 机动车在道路上发生故障，需要停车排除故障时，驾驶人应当立即开启危险报警闪光灯，将机动车移至不妨碍交通的地方停放；难以移动的，应当持续开启危险报警闪光灯，并在来车方向设置警告标志等措施扩大示警距离，必要时迅速报警。 第六十八条： 机动车在高速公路上发生故障时，应当依照本法第五十二条的有关规定办理；警告标志应当设置在故障车来车方向一百五十米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警，拨打122事故报警电话请求救援。', 12, 1, '2021-12-19 01:27:09', '2021-12-19 01:27:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4964, '大风沙尘天气行车，以下做法正确的是？', '/uploadFile/yuanBeiTu964.gif', '3', '4', NULL, '降低行驶速度', '注意观察路面情况', '关紧车窗', '握稳转向盘', 'ABCD', '1、出行前注意检查车辆密封性，关紧车窗；2、注意观察路面状况，选择合适的行车道路；3、注意慢速行车、紧握方向盘，防止车辆偏移；4、行车时注意保持车距。', 12, 1, '2021-12-19 01:27:10', '2021-12-19 01:27:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4965, '大风天行车需要注意什么？', '', '3', '4', NULL, '注意车辆的横向移动', '尽量减少超车', '尽量避免制动', '关紧车窗', 'ACD', '大风天气行车需谨慎，减速慢行，安全第一，严禁超车。 安全原则一：尽量以中低速度行驶，避免制动，遇到不确定的目标，及时按喇叭提示； 安全原则二：减少车辆的横向移动，避免因为突来大风致使车辆失去控制或翻车； 安全原则三：尽量摇紧车窗，防止沙尘飞进影响驾驶人的呼吸和观察，避免枯树危墙，远离满载货物的卡车，以防被散落物品砸伤。', 12, 1, '2021-12-19 01:27:10', '2021-12-19 01:27:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4966, '车辆在泥泞路上发生侧滑时，以下做法正确的是？', '', '1', '4', NULL, '向侧滑的一侧转动转向盘适量修正', '向侧滑的另一侧转动转向盘适量修正', '迅速制动减速', '迅速制动停车', 'A', '【侧滑】驾驶机动车在泥泞路行车、冰雪路面转弯，速度过快时容易发生侧滑。1、前轮侧滑时，要向侧滑相反方向转动转向盘；2、后轮侧滑时，要向侧滑方向转动转向盘；3、在泥泞路发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正（即向侧滑方向转动转向盘）。', 12, 1, '2021-12-19 01:27:10', '2021-12-19 01:27:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4967, '在泥泞路段行车，要平稳地转动方向盘，避免由快速转动方向盘而引起侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '行车过程中根据车辆的转向与滑动情况，平稳地转动方向盘、灵活调整。快速的转动方向只会加剧车辆的滑动程度。', 12, 1, '2021-12-19 01:27:11', '2021-12-19 01:27:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4968, '车辆在泥泞路段起步或者陷住时，切忌选择急加速。', '', '2', '4', NULL, '', '', '', '', 'A', '泥泞路面行驶尽量不换挡，当车陷入泥泞路面后，大多数司机是想通过踩油门来提高车速通过，其实这样更不容易通过泥泞路面。正确的做法是中低速行驶，这样发动机可以输出更大的扭矩，有利于通过泥泞路面。 注意“切忌”指的是“禁忌；千万不要。”题目说“切忌选择急加速”，意思就是“千万不要选择急加速”，描述是没有问题的，因此本题答案为对。', 12, 1, '2021-12-19 01:27:11', '2021-12-19 01:27:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4969, '车辆在泥泞路段发生侧滑时，要向车尾侧滑方向缓打方向盘修正。', '', '2', '4', NULL, '', '', '', '', 'A', '《复杂道路条件下的驾驶-泥泞路驾驶》泥泞路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。当车辆在泥泞路发生侧滑时，在松抬加速踏板的同时，将转向盘向后轮侧滑的方向适当缓转修正，慢慢的“给油”（踩加速踏板）。注意：切忌猛打转向盘或紧急制动。', 12, 1, '2021-12-19 01:27:11', '2021-12-19 01:27:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4970, '通过泥泞道路时，以下做法正确的是？', '', '3', '4', NULL, '停车观察前方道路', '避免使用行车制动', '尽量避免中途换挡', '提前换入低速挡', 'ABCD', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞路段时，①要停车察看路况，尽量选择平整、坚实或有车辙的路段通过；②选用中低档位行车，握紧转向盘；③稳住加速踏板匀速一次性通过；④尽量避免使用行车制动器，以防止整车滑移。综上，均为安全通过泥泞道路时的正确做法。', 12, 1, '2021-12-19 01:27:12', '2021-12-19 01:27:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4971, '遇紧急情况避险时，要沉着冷静，坚持什么样的处理原则？', '', '1', '4', NULL, '先避人后避物', '先避物后避车', '先避车后避人', '先避物后避人', 'A', '驾驶机动车遇紧急情况避险时，要沉着冷静，坚持“先避人后避物”的处理原则。', 13, 1, '2021-12-19 01:27:12', '2021-12-19 01:27:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4972, '在高速公路上遇到紧急情况避险时需注意什么？', '', '1', '4', NULL, '采取制动措施减速', '向左侧转向避让', '迅速转动转向盘躲避', '向右侧转向避让', 'A', '在高速公路上发生紧急情况，应首先采取制动减速，使车辆在碰撞前处于停止或低速行进状态，以减小碰撞损坏程度，不要急转方向避让，以免车辆侧滑或倾翻。', 13, 1, '2021-12-19 01:27:12', '2021-12-19 01:27:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4973, '车速较高，前方发生紧急情况时，要先转方向避让，再采取制动减速，以减小碰撞损坏程度。', '', '2', '4', NULL, '', '', '', '', 'B', '在车速较高时遇紧急情况，要坚持“先制动减速、后转向避让”的原则，不要轻易急转向，否则极易造成车辆侧滑或倾翻。', 13, 1, '2021-12-19 01:27:12', '2021-12-19 01:27:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4974, '机动车在高速行驶时，前面扬起的飞石或是遗撒物将挡风玻璃击裂，造成视线模糊不清的状况下，驾驶人要逐渐降低车速、开启危险报警闪光灯并将机动车移至不妨碍交通的地点。', '', '2', '4', NULL, '', '', '', '', 'A', '在视线模糊不清的状况下若还是高速行驶，很容易因为无法及时发现周围情况而发生事故，因此在这样的情况下就应该逐渐降低车速、开启危险报警闪光灯并将机动车移至不妨碍交通的地点。 因此本题正确。', 13, 1, '2021-12-19 01:27:13', '2021-12-19 01:27:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4975, '在高速公路上遇到紧急情况时不要轻易急转向避让。', '', '2', '4', NULL, '', '', '', '', 'A', '在高速公路上发生紧急情况，应首先采取制动减速，使车辆在碰撞前处于停止或低速行进状态，以减小碰撞损坏程度，不要急转方向避让，以免车辆侧滑或倾翻。', 13, 1, '2021-12-19 01:27:13', '2021-12-19 01:27:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4976, '轮胎气压过低时，高速行驶可能导致什么结果？', '', '1', '4', NULL, '气压不稳', '气压增高', '行驶阻力减小', '爆胎', 'D', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。', 13, 1, '2021-12-19 01:27:13', '2021-12-19 01:27:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4977, '行车中发现左侧轮胎漏气时怎样处置？', '', '1', '4', NULL, '慢慢制动减速', '迅速制动减速', '迅速向右转向', '采取紧急制动', 'A', '行车中发现轮胎漏气或气压过低时，要平缓制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。', 13, 1, '2021-12-19 01:27:13', '2021-12-19 01:27:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4978, '行车中发现右侧轮胎漏气时怎样处置？', '', '1', '4', NULL, '迅速制动减速', '慢慢制动减速', '迅速向左转向', '采取紧急制动', 'B', '当车辆漏气时，引起轮胎不正常工作，因此此时就应该减速并找安全的地方停车。迅速向左转向或是紧急制动都有可能造成爆胎等状况的发生，因此不可行。 选项A和B的差别在于是快还是要慢，若是动作过快也有可能造成爆胎，因此本题要选B。', 13, 1, '2021-12-19 01:27:13', '2021-12-19 01:27:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4979, '驾驶人发现轮胎漏气，将机动车驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '', '2', '4', NULL, '', '', '', '', 'A', '行车中发现轮胎漏气或气压过低时，要平缓制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。', 13, 1, '2021-12-19 01:27:14', '2021-12-19 01:27:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4980, '后轮胎爆裂时，驾驶人要如何处置？', '', '1', '4', NULL, '迅速转动转向盘调整', '控制行驶方向并慢慢减速', '迅速向相反方向转动转向盘', '迅速采取制动措施', 'B', '后轮爆胎时，要先控制行驶方向并平缓减速；前轮爆胎时，在控制住行驶方向后，采取抢挂低速挡的方法减速停车。', 13, 1, '2021-12-19 01:27:14', '2021-12-19 01:27:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4981, '前轮爆胎时，驾驶人控制住行驶方向后，要采取什么措施减速停车？', '', '1', '4', NULL, '抢挂高速挡', '抢挂低速挡', '抢挂空挡', '紧急制动', 'B', '后轮爆胎时，要先控制行驶方向并平缓减速；前轮爆胎时，危险较大，一定要极力控制转向盘，在控制住行驶方向后，采取抢挂低速挡的方法减速停车。前轮爆裂已出现转向困难时，驾驶人不要过度矫正，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速。', 13, 1, '2021-12-19 01:27:14', '2021-12-19 01:27:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4982, '行车中轮胎突然爆裂时的不正确做法是什么？', '', '1', '4', NULL, '保持镇静，缓抬加速踏板', '紧握转向盘，控制机动车直线行驶', '采取紧急制动，在最短的时间内停车', '待车速降低后，再轻踏制动踏板', 'C', '行车中遇到突然爆胎，正确的做法是双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。因此，采取紧急制动是不正确做法，注意审题。', 13, 1, '2021-12-19 01:27:14', '2021-12-19 01:27:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4983, '行车中轮胎突然爆裂时的应急措施是什么？', '', '1', '4', NULL, '迅速制动减速', '紧握转向盘，尽快平稳停车', '迅速转动转向盘调整方向', '低速行驶，寻找换轮胎地点', 'B', '行驶中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬加速踏板，禁令按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或倾翻。', 13, 1, '2021-12-19 01:27:15', '2021-12-19 01:27:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4984, '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么情况发生？', '', '1', '4', NULL, '气压不稳', '气压更低', '行驶阻力增大', '爆胎', 'D', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。', 13, 1, '2021-12-19 01:27:15', '2021-12-19 01:27:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4985, '避免爆胎的错误做法是什么？', '', '1', '4', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽里的异物', '更换掉有裂纹或有很深损伤的轮胎', 'A', '请注意，题干要求选出做法错误的选项。降低轮胎气压不仅不能避免爆胎，还会增加爆胎的风险。避免爆胎的正确做法是定期检查轮胎，及时清理轮胎沟槽里的异物，更换有裂纹或有很深损伤的轮胎。 【拓展知识】 当轮胎气压降低，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。而如果车辆在低速行驶，也会伤胎，而且潜伏期长，隐蔽性高，危害性更大，会埋下日后爆胎的隐患。', 13, 1, '2021-12-19 01:27:15', '2021-12-19 01:27:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4986, '行车中当机动车突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用抢挂低速档的方法，利用发动机制动使机动车减速。', '', '2', '4', NULL, '', '', '', '', 'A', '行驶当中遇到突然爆胎，驾驶人采取抢挂低速挡的方法，利用发动机制动使车辆缓慢减速，切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:15', '2021-12-19 01:27:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4987, '行车中当突然爆胎时，驾驶人要双手紧握转向盘，尽力控制机动车直线行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:16', '2021-12-19 01:27:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4988, '避免机动车爆胎的正确做法是降低轮胎气压。', '', '2', '4', NULL, '', '', '', '', 'B', '降低轮胎气压，易导致爆胎。车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。 【拓展知识】 避免爆胎的正确做法是定期检查轮胎，及时清理轮胎沟槽里的异物，更换掉有裂纹或有很深损伤的轮胎。', 13, 1, '2021-12-19 01:27:16', '2021-12-19 01:27:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4989, '驾驶人行车中意识到爆胎时，要轻踏制动踏板，缓慢减速停车。', '', '2', '4', NULL, '', '', '', '', 'A', '行驶当中遇到突然爆胎，驾驶人要双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后间断轻踩制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:16', '2021-12-19 01:27:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4990, '行车中遇突然爆胎时，驾驶人要急踏制动踏板减速停车。', '', '2', '4', NULL, '', '', '', '', 'B', '行驶当中遇到突然爆胎，驾驶人要双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后间断轻踩制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:16', '2021-12-19 01:27:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4991, '机动车发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免机动车横甩发生更大的险情。', '', '2', '4', NULL, '', '', '', '', 'A', '行驶当中遇到突然爆胎，驾驶人要双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后间断轻踩制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:17', '2021-12-19 01:27:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4992, '行车中当驾驶人意识到机动车爆胎时，应在控制住方向的情况下采取紧急制动，迫使机动车迅速停住。', '', '2', '4', NULL, '', '', '', '', 'B', '行驶当中遇到突然爆胎，驾驶人尽量采用“抢挡”的方法，利用发动机制动使车辆缓慢减速，切忌慌乱中急踏制动踏板，以避免车辆横甩发生更大的险情，双手要紧握转向盘控制住方向、松抬制动踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:17', '2021-12-19 01:27:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4993, '机动车避免爆胎的正确做法是什么？', '', '3', '4', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽内的异物', '更换掉有裂纹或损伤的轮胎', 'BCD', '轮胎气压过低，车辆高速行驶时会出现波浪变形、温度升高极易导致爆胎。避免爆胎的正确做法是：1、每次行车前检查胎压是否正常，及时清理轮胎表面及沟槽里的异物；2、胎面磨损到小三角标志或轮胎花纹里的标记线或达到保质期时，必须更换新胎；3、季节更替时不可随意变更轮胎的气压；4、要选用同一品牌同一型号的轮胎；5、行驶中，应尽量避免碾压、碰撞、剐蹭坚硬的物体及液体。', 13, 1, '2021-12-19 01:27:17', '2021-12-19 01:27:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4994, '机动车在高速行驶中，突然爆胎要采取的安全措施是什么？', '', '3', '4', NULL, '紧急制动，靠边停车', '牢牢地握住转向盘，保持直行', '立即松开加速踏板', '轻踩制动踏板', 'BCD', '机动车在高速行驶中遇到突然爆胎时，驾驶人要双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断轻踩制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:17', '2021-12-19 01:27:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4995, '当机动车转向失控行驶方向偏离，事故已经无可避免时，要采取什么措施？', '', '1', '4', NULL, '紧急制动', '迅速转向进行调整', '迅速向无障碍一侧转向躲避', '迅速向有障碍一侧转向躲避', 'A', '装有助力转向的机动车发现转向困难时，要停车查明原因。行驶过程中遇到转向突然失控、行驶方向偏离，事故已经无可避免时，要果断地采取紧急制动，也可果断地连续踩踏、放松制动踏板使机动车尽快减速停车，尽量缩短停车距离，减轻撞车强度。 请注意！车辆在高速行驶的情况下紧急制动，容易造成翻车。', 13, 1, '2021-12-19 01:27:18', '2021-12-19 01:27:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4996, '驾驶装有动力转向的机动车发现转向困难怎样处置？', '', '1', '4', NULL, '停车查明原因', '控制转向缓慢行驶', '降低车速行驶', '保持机动车直线行驶', 'A', '驾驶装有助力转向的机动车发现转向困难时，要停车查明原因。(P174)', 13, 1, '2021-12-19 01:27:18', '2021-12-19 01:27:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4997, '行车中遇到转向失控，行驶方向偏离时怎样处置？', '', '1', '4', NULL, '迅速转向调整', '尽快减速停车', '向无障碍一侧躲避', '向有障碍一侧躲避', 'B', '装有助力转向的机动车发现转向困难时，要停车查明原因。行驶过程中遇到转向突然失控、行驶方向偏离，要果断地连续踩踏、放松制动踏板使机动车尽快减速停车，尽量缩短停车距离，减轻撞车强度。', 13, 1, '2021-12-19 01:27:18', '2021-12-19 01:27:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4998, '转向失控后，若机动车偏离直线行驶方向，应怎样使机动车尽快减速停车？', '', '1', '4', NULL, '轻踏制动踏板', '拉紧驻车制动器操纵杆', '迅速抢挡减速', '果断地连续踩踏、放松制动踏板', 'D', '行驶过程中遇到转向突然失控、行驶方向偏离，要果断地连续踩踏、放松制动踏板使机动车尽快减速停车，尽量缩短停车距离，减轻撞车强度。', 13, 1, '2021-12-19 01:27:18', '2021-12-19 01:27:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (4999, '装有转向助力装置的机动车，驾驶人突然发现转向困难，操作费力，要紧握转向盘保持低速行驶。', '', '2', '4', NULL, '', '', '', '', 'B', '装有动力转向的车辆，突然发现转向困难，操作费力时，应迅速松开油门，降低车速，并将车停到安全的地方。驾驶途中遇到方向盘转向系统失灵时，将损失降到最低应当减速停车，本题错在“保持低速行驶”。', 13, 1, '2021-12-19 01:27:19', '2021-12-19 01:27:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11000, '车辆在高速公路行驶时，可以仅凭感觉确认车速。', '', '2', '1', NULL, '', '', '', '', 'B', '车辆在高速公路高速行驶时，驾驶人对车速的判断会有偏差，即“速度错觉”，因此不要单纯凭感觉判断车速，一定要通过观察车速表来判断车速。', 3, 1, '2021-12-19 00:13:03', '2021-12-19 00:13:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11001, '车辆在山区道路跟车行驶时，应怎样做？', '', '1', '1', NULL, '紧随前车之后', '适当加大安全距离', '适当减小安全距离', '尽可能寻找超车机会', 'B', '山区道路跟车，要适当加大与前车的安全距离，仔细观察前车动态。在视野不佳或道路条件差的路段，要进一步加大跟车距离，以防前车突然停车或停车后溜。', 3, 1, '2021-12-19 00:13:03', '2021-12-19 00:13:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11002, '在山区道路超车时，应怎样超越？', '', '1', '1', NULL, '选择较缓的下坡路段超车', '选择任意路段超车', '选择宽阔的缓上坡路段超车', '选择较长的下坡路段超车', 'C', '山区道路行车要尽量避免超车，需要超车时，要选择路面宽阔的缓上坡路段，开启左转向灯，提前鸣喇叭，在确认前车让行后再超越。不得强行超车，严禁在路面狭窄、急转弯、连续转弯或对面有来车等不具备条件的路段超车。', 3, 1, '2021-12-19 00:13:04', '2021-12-19 00:13:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11003, '在山区道路遇对向来车时，应怎样会车？', '', '1', '1', NULL, '不减速', '紧靠道路中心', '加速', '减速或停车让行', 'D', '山区会车时，应尽量选择路面较宽的路段，并主动减速、选择安全路段减速或停车交会，不得加速或靠路中心行驶，防止发生剐碰事故。', 3, 1, '2021-12-19 00:13:04', '2021-12-19 00:13:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11004, '下长坡时，控制车速除了刹车制动以外还有什么有效的辅助方法？', '', '1', '1', NULL, '挂入空挡滑行', '利用发动机制动', '踏下离合器滑行', '关闭发动机熄火滑行', 'B', '发动机制动就是挂低档，让车利用低档位的惯性制动。下长坡时，可以通过这种方式有效控制车速。', 3, 1, '2021-12-19 00:13:04', '2021-12-19 00:13:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11005, '下长坡连续使用行车制动会导致什么？', '', '1', '1', NULL, '会缩短发动机寿命', '增加驾驶人的劳动强度', '会使制动器温度升高而使制动效果急剧下降', '容易造成车辆倾翻', 'C', '下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度会使制动效果急剧下降。', 3, 1, '2021-12-19 00:13:04', '2021-12-19 00:13:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11006, '车辆在较窄的山路上行驶时，如果靠山体的一方不让行，应怎样做？', '', '1', '1', NULL, '向左占道，谨慎驶过', '提前减速或停车避让', '保持正常车速行驶', '鸣喇叭催其让行', 'B', '车辆在较窄的山路上行驶时，要尽量靠右侧低速行驶，遇靠山体一侧车辆不让行时，要提前减速并选择安全的地方停车避让。', 3, 1, '2021-12-19 00:13:05', '2021-12-19 00:13:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11007, '车辆上坡行驶，要提前观察路况、坡道长度，及时减挡使车辆保持充足的动力。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。', 3, 1, '2021-12-19 00:13:05', '2021-12-19 00:13:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11008, '车辆下坡行驶，要适当控制车速，充分利用发动机进行制动。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车下坡行驶，要挂中低速档位，充分利用发动机制动来控制车速，严禁空挡滑行。', 3, 1, '2021-12-19 00:13:05', '2021-12-19 00:13:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11009, '车辆下长坡时要减挡行驶，以充分利用发动机的制动作用。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速。', 3, 1, '2021-12-19 00:13:05', '2021-12-19 00:13:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11010, '车辆在通过山区道路弯道时，要做到“减速、鸣喇叭、靠右行”。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车通过弯道时，要注意观察，提前发现急弯路警告标志，并做到“减速、鸣号、靠右行”，不要占对向车道。', 3, 1, '2021-12-19 00:13:06', '2021-12-19 00:13:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11011, '车辆在山区道路行车下陡坡时，不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '根据《道路交通安全法》第四十三条　同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： 1、前车正在左转弯、掉头、超车的； 2、与对面来车有会车可能的； 3、前车为执行紧急任务的警车、消防车、救护车、工程救险车的； 4、行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 3, 1, '2021-12-19 00:13:06', '2021-12-19 00:13:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11012, '车辆在下坡行驶时，可充分利用空挡滑行。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车下坡行驶，要挂中低速挡位，充分利用发动机制动来控制车速，严禁空挡滑行。 【拓展知识-空挡滑行不省油而且危险大】 空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。', 3, 1, '2021-12-19 00:13:06', '2021-12-19 00:13:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11013, '车辆进入山区道路后，要特别注意“连续转弯”标志，并主动避让车辆及行人，适时减速和提前鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'A', '山区弯道较多，要注意观察，提前发现急弯路警告标志，特别注意“连续转弯”标志，并做到“减速、鸣号、靠右行”，主动避让车辆及行人。', 3, 1, '2021-12-19 00:13:06', '2021-12-19 00:13:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11014, '车辆在山区上坡路驾驶，减挡要及时、准确、迅速，避免拖挡行驶导致发动机动力不足。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。', 3, 1, '2021-12-19 00:13:07', '2021-12-19 00:13:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11015, '通过山区危险路段，尤其是通过经常发生塌方、泥石流的山区地段，应谨慎驾驶，避免停车。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车通过经常有落石或经常发生塌方、泥石流的山区路段时，要减速慢行，注意观察，尽快通过，不要停留。因此，题干表述正确。', 3, 1, '2021-12-19 00:13:07', '2021-12-19 00:13:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11016, '通过经常发生塌方、泥石流的山区地段，避免停车。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车通过经常有落石或经常发生塌方、泥石流的山区路段时，要减速慢行，注意观察，尽快通过，不要停留。因此，题干表述正确。', 3, 1, '2021-12-19 00:13:07', '2021-12-19 00:13:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11017, '车辆驶入双向行驶隧道前，应开启什么灯？', '', '1', '1', NULL, '危险报警闪光灯', '远光灯', '雾灯', '示廓灯或近光灯', 'D', '驶入双向通行的隧道前，应开启示廓灯或近光灯，靠右行驶，注意对向来车；会车时，加大横向距离低速安全会车，会车不可开启远光灯。', 3, 1, '2021-12-19 00:13:07', '2021-12-19 00:13:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11018, '立交桥上一般都是单向行驶，车辆不必减速行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '立交桥的形式多种多样，驾驶机动车通过立交桥时必须按照限速标志标线规定的速度行驶。', 3, 1, '2021-12-19 00:13:08', '2021-12-19 00:13:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11019, '夜间车辆通过照明条件良好的路段时，应使用什么灯？', '', '1', '1', NULL, '雾灯', '近光灯', '远光灯', '危险报警闪光灯', 'B', '夜间驾驶，灯光有照明和信号两方面的作用，应根据行驶中的实际情况正确使用。夜间通过照明条件良好的道路时，使用近光灯。', 3, 1, '2021-12-19 00:13:08', '2021-12-19 00:13:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11020, '夜间行车中，前方出现弯道时，灯光照射会发生怎样的变化？', '', '1', '1', NULL, '距离不变', '由高变低', '离开路面', '由路中移到路侧', 'D', '夜间行车，可以通过灯光位置的变化来识别和判断路面的情况。 灯光照射由路中移到路侧：前方道路可能是一般弯道或连续弯道，当是连续弯道时灯光会随之从道路的一侧移到另一侧。', 3, 1, '2021-12-19 00:13:08', '2021-12-19 00:13:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11021, '夜间行车，驾驶人的视野受限，很难观察到灯光照射区域以外的交通情况，因此要减速行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。因此，夜间行车一定要仔细观察、合理控制车速、正确使用灯光。', 3, 1, '2021-12-19 00:13:08', '2021-12-19 00:13:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11022, '夜间驾驶人对事物的观察能力明显比白天差，视距变短。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。', 3, 1, '2021-12-19 00:13:09', '2021-12-19 00:13:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11023, '夜间起步前，应当先开启近光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间起步，先开启近光灯观察路况，确认周围情况后，再开启左转向灯，起步。', 3, 1, '2021-12-19 00:13:09', '2021-12-19 00:13:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11024, '夜间会车时，若对方车辆不关闭远光灯，可变换灯光提示对向车辆，同时减速靠右侧行驶或停车。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间会车遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。 因为开启远光灯会使对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 3, 1, '2021-12-19 00:13:09', '2021-12-19 00:13:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11025, '夜间通过没有路灯或路灯照明不良的道路时，应将近光灯转换为远光灯，但同向行驶的后车不得使用远光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间通过没有路灯或路段照明不良的道路时，应将近光灯转换为远光灯，低速行驶；但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。因此，题干表述正确。', 3, 1, '2021-12-19 00:13:10', '2021-12-19 00:13:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11026, '夜间行车，遇对面来车未关闭远光灯时，应减速行驶，以防两车灯光的交汇处有行人通过时发生事故。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间会车遇对向车辆不关闭远光灯，可交替使用远、近光灯提示对向车辆，当对向来车仍不关闭远光灯时，要及时减速靠右侧行驶或停车让行。因为远光灯会使对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 3, 1, '2021-12-19 00:13:10', '2021-12-19 00:13:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11027, '夜间通过无交通信号灯控制的交叉路口时，不得变换远、近光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 3, 1, '2021-12-19 00:13:10', '2021-12-19 00:13:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11028, '夜间行车，要尽量避免超车，确需超车时，可变换远近光灯向前车示意。', '', '2', '1', NULL, '', '', '', '', 'A', '夜间驾驶时，受车辆、环境条件和人自身的生理特点影响，驾驶人的视力变差、视距变短、视野变窄。同时，驾驶人的观察力和判断力有所降低，这使得夜间成为交通事故高发的一个时段。因此，夜间行车应尽量避免超车，确实需要超车时，可变换远、近光灯提示前车，待前车让行后方可超越；遇前车不让行时，不得强行超车。', 3, 1, '2021-12-19 00:13:11', '2021-12-19 00:13:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11029, '夜间尾随前车行驶时，后车可以使用远光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十八条：“机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。”', 3, 1, '2021-12-19 00:13:11', '2021-12-19 00:13:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11030, '在暴雨天气驾车，刮水器无法刮净雨水时，应怎样做？', '', '1', '1', NULL, '减速行驶', '集中注意力谨慎驾驶', '立即减速靠边停车', '以正常速度行驶', 'C', '当大雨、暴雨天气，靠刮水器难以改善视线时，要立即减速行驶，选择安全地点停车，开启示廓灯和危险报警闪光灯，待雨小或雨停后再继续行驶。 请注意：立即减速靠边停车≠紧急制动。', 3, 1, '2021-12-19 00:13:11', '2021-12-19 00:13:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11031, '在山区冰雪道路上行车，遇有前车正在爬坡时，后车应怎样做？', '', '1', '1', NULL, '选择适当地点停车，等前车通过后再爬坡', '迅速超越前车', '低速爬坡', '紧随其后爬坡', 'A', '在山区冰雪道路上行车，遇前车正在爬坡时，不得紧随或超越前车爬坡，要等前车通过坡顶后再上坡，避免前车突然停车或后溜造成事故。', 3, 1, '2021-12-19 00:13:11', '2021-12-19 00:13:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11032, '雾天行车时，应及时开启什么灯？', '', '1', '1', NULL, '倒车灯', '近光灯', '远光灯', '雾灯', 'D', '雾天行车时，应开启雾灯，及时让其他车辆驾驶人发现自己。', 3, 1, '2021-12-19 00:13:12', '2021-12-19 00:13:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11033, '遇有浓雾或特大雾天能见度过低，行车困难时，应怎样做？', '', '1', '1', NULL, '开启前照灯，继续行驶', '开启示廓灯、雾灯，靠右行驶', '开启危险报警闪光灯和雾灯，选择安全地点停车', '开启危险报警闪光灯，继续行驶', 'C', '雾天行车，应开启雾灯；雾较大时还应同时开启危险报警闪光灯；当遇到浓雾或特大雾天能见度过低行车困难时，应选择安全地点停车等待雾散。', 3, 1, '2021-12-19 00:13:12', '2021-12-19 00:13:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11034, '车辆涉水后，应保持低速行驶，怎样操作制动踏板，以恢复制动效果？', '', '1', '1', NULL, '持续重踏', '间断重踏', '持续轻踏', '间断轻踏', 'D', '机动车涉水后，擦干被水浸湿的部位，保持低速行驶，并间断轻踏制动踏板，以恢复制动效果。', 3, 1, '2021-12-19 00:13:12', '2021-12-19 00:13:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11035, '风、雨、雪、雾等复杂气象条件，遇前车速度较低时，应开启前照灯，连续鸣喇叭迅速超越。', '', '2', '1', NULL, '', '', '', '', 'B', '风、雨、雪、雾等复杂气象条件，会对驾驶人的判断和操作产生不利的影响，应合理控制车速，保持足够的安全距离行驶，当遇前车速度较低时，严禁开启前照灯，连续鸣喇叭迅速超越。', 3, 1, '2021-12-19 00:13:13', '2021-12-19 00:13:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11036, '雨天路面湿滑，车辆制动距离增大，行车中尽量使用紧急制动减速。', '', '2', '1', NULL, '', '', '', '', 'B', '雨天驾驶机动车在积水路面上高速行驶时，轮胎与路面间行成“水膜”，车辆的转向和制动将有失效的风险，车辆制动距离增大，行车中应松抬加速踏板，充分利用发动机制动减速，切勿急转向或紧急制动。因此，题干表述错误。', 3, 1, '2021-12-19 00:13:13', '2021-12-19 00:13:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11037, '浓雾天气能见度低，开启远光灯会提高能见度。', '', '2', '1', NULL, '', '', '', '', 'B', '在雾较大时，可开启近光灯照明，以便看清前方路口，同时开启危险报警闪光灯，不能使用远光灯。 【拓展知识】 雾天不能使用远光灯的原因：汽车远光灯的灯光是照向前上方的，射出的光线被雾气反射后，会在车前行车白茫茫的一片，这不但不能起到照明作用还会严重阻挡驾驶人视线、视野，当后面车辆使用远光灯时也会严重影响前车驾驶人观察后方情况，因此雾天行车，不能使用远光灯。', 3, 1, '2021-12-19 00:13:13', '2021-12-19 00:13:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11038, '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，行车时应选择道路中间坚实的路面，避免靠近路边行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，道路泥泞、黏稠，行驶阻力大且车轮易滑转、侧滑。因此，行车时应尽量选择道路中间平整、坚实或有车辙的路段通过，避免靠近路边行驶，以免出现危险。', 3, 1, '2021-12-19 00:13:13', '2021-12-19 00:13:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11039, '雾天行车多使用喇叭可引起对方注意；听到对方车辆鸣喇叭，也应鸣喇叭回应。', '', '2', '1', NULL, '', '', '', '', 'A', '雾天能见度低，驾驶人视线模糊，在道路上行车，可多使用喇叭引起对向车辆注意，听到对向车辆鸣喇叭时，要鸣喇叭回应。', 3, 1, '2021-12-19 00:13:13', '2021-12-19 00:13:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11040, '在冰雪路面上行车，必须降低车速、加大安全距离。', '', '2', '1', NULL, '', '', '', '', 'A', '冰雪路面行驶时，路面滑摩擦系数小，制动距离比平时要大。因此要考虑到紧急状况。如果出现紧急情况，需要有足够的制动距离。', 3, 1, '2021-12-19 00:13:14', '2021-12-19 00:13:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11041, '雪天行车中，在有车辙的路段应循车辙行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '当路面被积雪覆盖或道路轮廓难以辨别时，可根据路边树木、电线杆等参照物判断行驶路线，控制车辆低速行驶；在有车辙的路段要循车辙低速行驶。', 3, 1, '2021-12-19 00:13:14', '2021-12-19 00:13:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11042, '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，可利用发动机制动进行减速。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，充分利用发动机牵阻作用减速或停车，不能紧急制动和急转向。', 3, 1, '2021-12-19 00:13:14', '2021-12-19 00:13:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11043, '车辆行至泥泞或翻浆路段时，应停车观察，选择平整、坚实的路段缓慢通过。', '', '2', '1', NULL, '', '', '', '', 'A', '泥泞路路面特别松软、粘稠，汽车行驶阻力大且车轮易滑转、侧滑。因此行至泥泞或翻浆路段时，要停车察看路况，尽量选择平整、坚实或有车辙的路段通过。一般选用中低速档位行车，握稳转向盘，稳住加速踏板匀速一次性通过，尽量避免使用行车制动器，以防整车滑移。', 3, 1, '2021-12-19 00:13:15', '2021-12-19 00:13:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11044, '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '大雨天行车，路面湿滑，车轮的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时，易发生“水滑”现象。因此，避免“水滑”而造成的危险，应当合理控制车速，减速行驶。', 3, 1, '2021-12-19 00:13:15', '2021-12-19 00:13:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11045, '前轮胎爆裂已出现转向时，驾驶人不要过度矫正，应在控制住方向的情况下，应怎样做，使车辆缓慢减速？', '', '1', '1', NULL, '采取紧急制动', '使用驻车制动', '轻踏制动踏板', '迅速踏下制动踏板', 'C', '当意识到前轮爆胎已出现转向时，驾驶人也不要过度矫正，要在控制住车辆行驶方向的情况下，轻踏制动踏板（×禁止紧急制动），使车辆缓慢减速，待车速降至适当时候，平稳将车停住，尽量将车逐渐停靠在路边为妥。 【拓展知识】 后轮爆胎，车尾会摇摆不定，但方向一般不会失控。只要保持镇定，双手紧握转向盘，便可控制车辆保持直行行驶。', 3, 1, '2021-12-19 00:13:15', '2021-12-19 00:13:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11046, '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么？', '', '1', '1', NULL, '气压不稳', '气压更低', '行驶阻力增大', '爆胎', 'D', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。', 3, 1, '2021-12-19 00:13:15', '2021-12-19 00:13:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11047, '避免爆胎的错误做法是什么？', '', '1', '1', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽里的异物', '更换掉有裂纹或有很深损伤的轮胎', 'A', '轮胎气压过低，车辆高速行驶时会出现波浪变形、温度升高极易导致爆胎。 避免爆胎的正确做法是：1、每次行车前检查胎压是否正常，及时清理轮胎表面及沟槽里的异物；2、胎面磨损到小三角标志或轮胎花纹里的标记线或达到保质期时，必须更换新胎；3、季节更替时不可随意变更轮胎的气压；4、要选用同一品牌同一型号的轮胎；5、行驶中，应尽量避免碾压、碰撞、剐蹭坚硬的物体及液体。', 3, 1, '2021-12-19 00:13:15', '2021-12-19 00:13:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11048, '驾驶人发现轮胎漏气，将车辆驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。 1、发现轮胎漏气或气压过低时，要平稳制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。 2、行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬制动踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 3, 1, '2021-12-19 00:13:16', '2021-12-19 00:13:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11049, '车辆后轮胎爆裂，车尾会摇摆不定，驾驶人应双手紧握转向盘，控制车辆保持直线行驶，减速停车。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆后轮爆胎时，双手要先紧握转向盘控制行驶方向，保持直线行驶，并平缓减速，直至平稳停车。', 3, 1, '2021-12-19 00:13:16', '2021-12-19 00:13:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11050, '行车中当驾驶人意识到爆胎时，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速，逐渐平稳地停靠于路边。', '', '2', '1', NULL, '', '', '', '', 'A', '行驶当中驾驶人意识到爆胎时，应双手紧握转向盘控制住方向，平缓的制动减速，尽快驶离主车道直至平稳地靠边停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 3, 1, '2021-12-19 00:13:16', '2021-12-19 00:13:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11051, '行车中当车辆突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用“抢挡”的方法，利用发动机制动使车辆减速。', '', '2', '1', NULL, '', '', '', '', 'A', '行驶当中遇到突然爆胎，驾驶人采取抢挂低速挡的方法，利用发动机制动使车辆缓慢减速，切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 3, 1, '2021-12-19 00:13:17', '2021-12-19 00:13:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11052, '车辆前轮胎爆裂，危险较大，方向会立刻向爆胎车轮一侧跑偏，直接影响驾驶人对转向盘的控制。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆高速行驶时，若轮胎气压过低，会出现波浪变形、温度升高极易导致爆胎。 1、发现轮胎漏气或气压过低时，要平稳制动减速，尽快驶离主车道，不要紧急制动，以免造成翻车或后车制动不及时导致追尾事故。 2、行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬制动踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。 3、后轮爆胎时，要先控制行驶方向并平缓减速；前轮爆胎时，危险较大，一定要极力控制转向盘，在控制住行驶方向后，采取抢挂低速挡的方法减速停车。前轮爆裂已出现转向困难时，驾驶人不要过度矫正，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速。 4、避免爆胎的正确做法是定期检查轮胎，及时清理轮胎沟槽里的异物，更换有裂纹或有很深损伤的轮胎。', 3, 1, '2021-12-19 00:13:17', '2021-12-19 00:13:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11053, '车辆发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免车辆横甩发生更大的险情。', '', '2', '1', NULL, '', '', '', '', 'A', '行驶中遇到突然爆胎时，驾驶人要双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断轻踩制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 3, 1, '2021-12-19 00:13:18', '2021-12-19 00:13:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11054, '行车中当车辆前轮爆胎已发生转向时，驾驶人应双手紧握转向盘，尽力控制车辆直线行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶人意识到前轮胎爆裂时，应双手紧握转向盘，松抬加速踏板，极力控制车辆直线行驶。前轮爆胎时，危险较大，驾驶人一定要极力控制转向盘，迅速抢挂低速挡。前轮爆裂已出现转向时，驾驶人不要过度矫正，应在控制住直线行驶的情况下，轻踏制动踏板，使车辆缓慢减速。', 3, 1, '2021-12-19 00:13:18', '2021-12-19 00:13:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11055, '行车中当驾驶人意识到车辆爆胎时，应在控制住方向的情况下采取紧急制动，迫使车辆迅速停住。', '', '2', '1', NULL, '', '', '', '', 'B', '行驶当中遇到突然爆胎，双手要紧握转向盘控制住方向、松抬加速踏板，尽量按既定路线行驶，然后用力间断踩踏制动踏板减速，直至平稳停车。爆胎瞬间切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 3, 1, '2021-12-19 00:13:18', '2021-12-19 00:13:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11056, '夜间会车规定150米以内使用近光灯的原因是什么？', '', '1', '1', NULL, '提示后方车辆', '两车之间相互提示', '使用远光灯会造成驾驶人出现眩目，易引发危险', '驾驶人的操作习惯行为', 'C', '夜间会车使用远光灯，会使得对面车辆驾驶人因强烈灯光照射造成炫目而无法看清前方道路情况，导致操作失控，发生车辆之间碰撞或车辆碰撞行人、非机动车的事故。', 3, 1, '2021-12-19 00:13:18', '2021-12-19 00:13:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11057, '雾天行车听到对方鸣喇叭，也应该鸣喇叭回应，以提示对方车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '雾天能见度低，驾驶人视线模糊，在道路上行车，可多使用喇叭引起对向车辆注意，听到对向车辆鸣喇叭时，要鸣喇叭回应。', 3, 1, '2021-12-19 00:13:19', '2021-12-19 00:13:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11058, '夜间行车，可选择下列哪个地段超车？', '', '1', '1', NULL, '窄路窄桥', '交叉路口', '路宽车少', '弯道陡坡', 'C', '夜间行车超车前，应选择视线良好、平直宽阔、左右均无障碍且前方路段200米范围内没有来车的道路，在保证安全的前提下方能超车。', 3, 1, '2021-12-19 00:13:19', '2021-12-19 00:13:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11059, '变更车道时只需开启转向灯，并迅速转向驶入相应的车道，以不妨碍同车道机动车正常行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '迅速转向操作错误。为保证安全，驾驶人在变更车道时应提前开启转向灯提醒后车，然后观察有无变更车道的条件，在确保安全的情况下，保持安全距离变更车道，迅速变更车道做法错误。', 3, 1, '2021-12-19 00:13:19', '2021-12-19 00:13:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11060, '如图所示，当您车速为95km/h时，您可以在哪条车道内行驶？', '/uploadFile/yuanBeiTu1060.gif', '1', '1', NULL, '车道A', '车道B', '车道C', '车道D', 'B', '如图所示，A车道限速为100~120km/h，B车道限速为80~100km/h，C车道限速为60~80km/h，因此，车速为95km/h时，可以在B车道内行驶。', 3, 1, '2021-12-19 00:13:20', '2021-12-19 00:13:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11061, '牵引发生故障的机动车时，最高车速不得超过多少？', '', '1', '1', NULL, '50公里/小时', '40公里/小时', '30公里/小时', '20公里/小时', 'C', '《道路交通安全法实施条例》第四十六条　机动车行驶中遇有下列情形之一的，最高行驶速度不得超过每小时30公里，其中拖拉机、电瓶车、轮式专用机械车不得超过每小时15公里： 　　（一）进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时； 　　（二）掉头、转弯、下陡坡时； 　　（三）遇雾、雨、雪、沙尘、冰雹，能见度在50米以内时； 　　（四）在冰雪、泥泞的道路上行驶时； 　　（五）牵引发生故障的机动车时。', 3, 1, '2021-12-19 00:13:20', '2021-12-19 00:13:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11062, '如图所示，在高速公路同方向三条机动车道最左侧道行驶，应保持什么车速？', '/uploadFile/yuanBeiTu1062.gif', '1', '1', NULL, '110公里/小时～120公里/小时', '100公里/小时～120公里/小时', '90公里/小时～110公里/小时', '60公里/小时～120公里/小时', 'A', '《道路交通安全法实施条例》第七十八条：高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 1、同方向有2条车道的，左侧车道的最低车速为每小时100公里； 2、同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里； 3、道路限速标志标明的车速与上述车道行驶车速的规定不一致的，技照道路限速标志标明的车速行驶。', 3, 1, '2021-12-19 00:13:20', '2021-12-19 00:13:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11063, '驾驶机动车变更车道前应仔细观察，目的是判断有无变更车道的条件。', '', '2', '1', NULL, '', '', '', '', 'A', '为保证安全，驾驶人在变更车道前打开转向灯示意约3秒以上，先通过内外后视镜观察后方车辆和将驶入车道的交通状况；车辆的侧方存在盲区，因此在确认自己的前方安全后需再次侧头观察驶入车道一侧有无他车，以判断有无变更车道的条件，正确的选择行驶车道和变更时机，安全变道行驶。', 3, 1, '2021-12-19 00:13:20', '2021-12-19 00:13:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11064, '超车时，如果无法保证与被超车辆的安全间距，应主动放弃超车。', '', '2', '1', NULL, '', '', '', '', 'A', '超车时要保持与被超越车辆的安全距离，观察好超车条件，有充分把握保证万无一失再开始超越，无法保证与被超车辆的安全间距说明不具备超车条件，应主动放弃超车。', 3, 1, '2021-12-19 00:13:21', '2021-12-19 00:13:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11065, '驾驶机动车正在被其他车辆超越时，被超车辆减速靠右侧行驶的目的是什么？', '', '1', '1', NULL, '给该车让出足够的超车空间', '以便随时停车', '避让行人与非机动车', '以上选项都不正确', 'A', '《道路交通安全法实施条例》第四十七条：机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下，应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越，在与被超车辆拉开必要的安全距离后，开启右转向灯，驶回原车道。', 3, 1, '2021-12-19 00:13:21', '2021-12-19 00:13:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11066, '如图所示，在高速公路同方向三条机动车道右侧车道行驶，车速不能低于多少？', '/uploadFile/yuanBeiTu1066.gif', '1', '1', NULL, '100公里/小时', '60公里/小时', '110公里/小时', '80公里/小时', 'B', '根据《道路交通安全法实施条例》第七十八条规定，高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。', 3, 1, '2021-12-19 00:13:21', '2021-12-19 00:13:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11067, '人行横道上禁止掉头的原因是什么？', '', '1', '1', NULL, '人行横道禁止车辆通行', '避免妨碍行人正常通行，确保行人安全', '人行横道禁止停车', '路段有监控设备', 'B', '人行横道线是为行人横过道路而设置的安全通道上的标线，常被称为“生命线”，驾驶人驾车行经人行横道时，应减速观察，礼让行人，不得在人行横道上进行掉头、停车、倒车等操作，以免妨碍行人正常通行，确保行人安全。', 3, 1, '2021-12-19 00:13:21', '2021-12-19 00:13:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11068, '在划有道路中心线的道路上会车时，应当保持安全车速、不越线行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '在划有道路中心线的道路上会车，为了保障安全，要保持安全车速。同时因为越过中心线对向车容易相撞，所以不能越过中心线会车。', 3, 1, '2021-12-19 00:13:22', '2021-12-19 00:13:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11069, '如图所示，在超车过程中，遇对向有来车时要放弃超车是因为什么？', '/uploadFile/yuanBeiTu1069.gif', '1', '1', NULL, '前车车速快', '如继续超车，易与对面机动车发生刮擦、相撞', '对向来车车速快', '我方车辆提速太慢', 'B', '如图所示，对向车道有来车，前方车辆左侧没有足够的安全距离，应放弃超车，避免与对面车辆发生刮擦、相撞。', 3, 1, '2021-12-19 00:13:22', '2021-12-19 00:13:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11070, '如图所示，在这种道路上行驶，应在道路中间通行的主要原因是在道路中间通行速度快。', '/uploadFile/yuanBeiTu1070.gif', '2', '1', NULL, '', '', '', '', 'B', '没有划分车道的，在遵循右侧通行前提下，混合通行。行人在最右侧通行，非机动车在靠右侧，机动车在中间偏右的位置通行。因为对向来车也是如此，所以对整条路而言，就是机动车在中间，非机动车在两边。给两侧的非机动车和行人留有充足的通行空间，相对来讲更加安全。', 3, 1, '2021-12-19 00:13:22', '2021-12-19 00:13:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11071, '如图所示，当越过停在人行横道前的A车时，B车应减速，准备停车让行。', '/uploadFile/yuanBeiTu1071.gif', '2', '1', NULL, '', '', '', '', 'A', '交通法规定：机动车在通过人行横道时，应减速慢行，遇到行人通过人行横道时，应停车礼让。如图所示，B车越过A车后，前方是人行横道，有行人在通行，B车应当减速行驶，随时准备停车礼让行人。', 3, 1, '2021-12-19 00:13:23', '2021-12-19 00:13:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11072, '如图所示，A车在这种情况下应适当减速。', '/uploadFile/yuanBeiTu1072.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，B车要减速转弯，A车也要适当减速，以免两车发生碰撞。', 3, 1, '2021-12-19 00:13:23', '2021-12-19 00:13:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11073, '当您即将通过交叉路口的时候，才意识到要左转而不是向前，以下说法正确的是什么？', '', '1', '1', NULL, '继续向前行驶', '停在交叉路口，等待安全时左转', '在确保安全的情况下，倒车然后左转', '以上说法都不正确', 'A', '左转弯或右转弯前需要变更车道时，需按导向箭头的指示在虚线区变更车道，进入实线区后不能变更车道。题干中情形是即将通过交叉路口，说明此时机动车已在实线区，因此应当继续向前行驶。按规定，交叉路口既不可以停车，也不可以倒车。', 3, 1, '2021-12-19 00:13:23', '2021-12-19 00:13:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11074, '如图所示，驾驶机动车遇到没有行人通过的人行横道时不用减速慢行。', '/uploadFile/yuanBeiTu1074.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》规定：驾驶机动车遇到人行横道必须减速让行，必要时停车让行。行人的流动很随意，需要谨慎减速慢行。', 3, 1, '2021-12-19 00:13:24', '2021-12-19 00:13:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11075, '遇到前方车辆停车排队或者缓慢行驶时，强行穿插，以下说法正确的是什么？', '', '1', '1', NULL, '禁止，因为这样不利于省油', '禁止，因为这样扰乱车流，加重拥堵', '允许，因为可以快速的通过拥堵区', '允许，因为可以省油', 'B', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 3, 1, '2021-12-19 00:13:24', '2021-12-19 00:13:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11076, '如图所示，在高速公路同方向两条机动车左侧车道行驶，应保持什么车速？', '/uploadFile/yuanBeiTu1076.gif', '1', '1', NULL, '110公里/小时～130公里/小时', '100公里/小时～120公里/小时', '90公里/小时～110公里/小时', '60公里/小时～120公里/小时', 'B', '《道路交通安全法实施条例》第七十八条：高速公路最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。 1、同方向有2条车道的，左侧车道的最低车速为每小时100公里；', 3, 1, '2021-12-19 00:13:24', '2021-12-19 00:13:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11077, '如图所示，铁路道口禁止掉头的原因是什么？', '/uploadFile/yuanBeiTu1077.gif', '1', '1', NULL, '有铁路道口标志', '容易引发事故', '铁路道口车流量大', '有铁路道口信号灯', 'B', '在铁路道口、人行横道、桥梁、急弯、陡坡、隧道等容易发生危险的路段禁止掉头。', 3, 1, '2021-12-19 00:13:24', '2021-12-19 00:13:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11078, '如图所示，在这种情况下通过交叉路口时，不得超车的原因是什么？', '/uploadFile/yuanBeiTu1078.gif', '1', '1', NULL, '机动车速度慢，不足以超越前车', '路口有交通监控设备', '路口设有信号灯', '路口内交通情况复杂，易发生交通事故', 'D', '交叉路口内交通情况复杂，易发生交通事故，不具备超车条件。 拓展知识： 交叉路口险情预测：1，前方可能会有非机动车、行人违法横穿；2，前方车辆可能会突然变更车道，准备转弯或掉头；3，对向准备左转弯的车辆可能会占用部分直行车道或强行左转；4，对向直行车辆后方可能突然出现横穿的非机动车、行人；5，右侧车道有车辆等待右转时，起后侧车辆可能突然向左变更车道。 驾驶人应明白，即使绿灯亮时亦不能改变交叉路口处固有的危险性质，因此，交叉路口禁止超车。', 3, 1, '2021-12-19 00:13:25', '2021-12-19 00:13:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11079, '如图所示，在这种情况下只要后方没有来车，可以倒车。', '/uploadFile/yuanBeiTu1079.gif', '2', '1', NULL, '', '', '', '', 'B', '图上蓝色指标为直行单行道。道路交通安全法实施条例第五十条规定：机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 3, 1, '2021-12-19 00:13:25', '2021-12-19 00:13:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11080, '如图所示，在这种雾天情况下，通过交叉路口时必须鸣喇叭，加速通过，以免造成交通拥堵。', '/uploadFile/yuanBeiTu1080.gif', '2', '1', NULL, '', '', '', '', 'B', '雾天通过交叉路口时，要将车速控制在能及时停车的范围内，保持足够的安全间距，谨慎缓慢行驶，可多使用喇叭引起行人和车辆注意。加速通过，做法错误。', 3, 1, '2021-12-19 00:13:25', '2021-12-19 00:13:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11081, '如图所示，在高速公路最左侧车道行驶，想驶离高速公路，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1081.gif', '1', '1', NULL, '每次变更一条车道，直到最右侧车道', '为了快速变更车道，可以加速超越右侧车辆后变更车道', '找准机会一次变更到最右侧车道', '立即减速后向右变更车道', 'A', '为保证安全，变更车道的过程中要注意：1、不得连续变更两条以上车道；2、左右两侧车辆向同一车道变更时，左侧车辆让右侧车辆先行变更；3、在车道分界线为虚实线的路段，实线一侧的车辆严禁变更车道；4、变更车道不宜过缓，长距离轧线行驶会影响其他车辆行驶，一般情况应用50-60米的距离变更车道；5、每变更一次车道，就会隐含一次风险，因此禁止频繁变更车道。', 3, 1, '2021-12-19 00:13:26', '2021-12-19 00:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11082, '如图所示，机动车遇行人正在通过人行横道时，要停车让行，是因为行人享有优先通行权。', '/uploadFile/yuanBeiTu1082.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十七条：机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 3, 1, '2021-12-19 00:13:26', '2021-12-19 00:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11083, '如图所示，在这种情况下准备进入环形路口时，为了保证车后车流的通畅，应加速超越红车进入路口。', '/uploadFile/yuanBeiTu1083.gif', '2', '1', NULL, '', '', '', '', 'B', '环岛是交通事故多发地点，通过时应在距环岛50-100米处减速慢行，适时控制车速，以逆时针方向进入环岛。驶近环岛时，注意观察左侧已在环岛内行驶车辆的动态，适时汇入车流，必要时减速或停车让行。加速超越做法错误。', 3, 1, '2021-12-19 00:13:26', '2021-12-19 00:13:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11084, '在以下哪些路段不能倒车？', '', '1', '1', NULL, '交叉路口', '隧道', '急弯', '以上皆是', 'D', '《道路交通安全法实施条例》第五十条，机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 3, 1, '2021-12-19 00:13:27', '2021-12-19 00:13:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11085, '如图所示，在这种情形下，对方车辆具有先行权。', '/uploadFile/yuanBeiTu1085.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示路况会车时，在有障碍的路段，无障碍的一方先行。我方有障碍，对方无障碍，因此对方车辆具有先行权。', 3, 1, '2021-12-19 00:13:27', '2021-12-19 00:13:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11086, '如图所示，在高速公路同方向三条机动车道中间车道行驶，车速不能低于多少？', '/uploadFile/yuanBeiTu1086.gif', '1', '1', NULL, '100公里/小时', '90公里/小时', '110公里/小时', '60公里/小时', 'B', '《道路交通安全法实施条例》第七十八条：同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。三车道最低限速从左到右分别是：110、90、60。', 3, 1, '2021-12-19 00:13:27', '2021-12-19 00:13:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11087, '遇前方路段车道减少，车辆行驶缓慢，为保证道路通畅，应借对向车道迅速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '第五十三条机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道，网状线区域内停车等候。', 3, 1, '2021-12-19 00:13:28', '2021-12-19 00:13:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11088, '如图所示，当您超越右侧车辆时，应该尽快超越，减少并行时间。', '/uploadFile/yuanBeiTu1088.gif', '2', '1', NULL, '', '', '', '', 'A', '减少并行时间一方面是为了防止与右侧车刮擦，另一方面则是为了防止与对向来车相撞。减少并行，快速超越，这样才能确保超车安全。 【拓展延伸】 不少学员认为“尽快超越”有问题，担心右侧车辆会在超车时突然向左，会发生事故。但在超车时，被超越车辆更大几率情况应靠右侧让行，而正在进行超车的车辆占用了对向车道，随时有可能驶来车辆，如果不尽快完成超车，很容易发生事故。综上所述，题目描述没有问题。', 3, 1, '2021-12-19 00:13:28', '2021-12-19 00:13:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11089, '如图所示，当车辆驶近这样的路口时，以下说法错误的是什么？', '/uploadFile/yuanBeiTu1089.gif', '1', '1', NULL, '为避免车辆从路口突然冲出引发危险，应适当降低车速', '本车有优先通行权，可加速通过', '因为视野受阻，应鸣喇叭提醒侧方道路来车', '右前方路口视野受阻，如有突然冲出车辆，容易引发事故', 'B', '请注意，题干要求选出说法错误的选项。驾驶机动车驶近没有交通信号灯控制的路口时，应仔细观察左、右两侧道路交通情况，全面预防险情，减速或停车瞭望，做到“一看，二慢，三通过”，直行车辆优先通行。因此，有加速通过的选项错误。', 3, 1, '2021-12-19 00:13:29', '2021-12-19 00:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11090, '如图所示，在这种情况下不能超车的原因是什么？', '/uploadFile/yuanBeiTu1090.gif', '1', '1', NULL, '我方车速不足以超越前车', '前车速度过快', '路中心为黄线', '前车正在超车', 'D', '《道路交通安全法》第四十三条　同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 如图所示，前方蓝色车辆正在超车，因此这种情况下，我方车辆不得超车。', 3, 1, '2021-12-19 00:13:29', '2021-12-19 00:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11091, '大雾天行车，多鸣喇叭是为了什么？', '', '1', '1', NULL, '催促前车让行', '准备超越前车', '催促前车提速，避免发生追尾', '引起对方注意，避免发生危险', 'D', '雾天能见度低，驾驶人的视线模糊、视距变短、视野变窄，在道路上行车，多鸣喇叭引起行人和车辆注意，避免发生交通事故。', 3, 1, '2021-12-19 00:13:29', '2021-12-19 00:13:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11092, '如图所示，在这种情况下，驾驶机动车要停车让行。', '/uploadFile/yuanBeiTu1092.gif', '2', '1', NULL, '', '', '', '', 'A', '根据相关规定：机动车行经人行横道时，应当减速行驶；遇行人正在通过人行横道，应当停车让行。机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 3, 1, '2021-12-19 00:13:30', '2021-12-19 00:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11093, '如图所示，直行车辆遇到前方路口堵塞，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1093.gif', '1', '1', NULL, '等前方道路疏通后，且信号灯为绿灯时方可继续行驶', '等有其他机动车进入路口时跟随行驶', '只要信号灯为绿灯，就可通过', '可以直接驶入路口内等待通行', 'A', '机动车遇有前方交叉路口交通阻塞时，即便是绿灯亮，也要停在路口外等候，不得进入路口或停在路口内等候，以免加剧阻塞或被夹在路口内进退两难。等前方道路疏通后，且绿灯亮时方可继续行驶。', 3, 1, '2021-12-19 00:13:30', '2021-12-19 00:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11094, '如图所示，机动车在这种道路上行驶，在道路中间通行的原因是什么？', '/uploadFile/yuanBeiTu1094.gif', '1', '1', NULL, '在道路中间通行视线好', '防止车辆冲出路外', '给两侧的非机动车和行人留有充足的通行空间', '在道路中间通行速度快', 'C', '没有划分车道的，在遵循右侧通行前提下，混合通行。行人在最右侧通行，非机动车在靠右侧，机动车在中间偏右的位置通行。因为对向来车也是如此，所以对整条路而言，就是机动车在中间，非机动车在两边。给两侧的非机动车和行人留有充足的通行空间，相对来讲更加安全。', 3, 1, '2021-12-19 00:13:30', '2021-12-19 00:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11095, '通过漫水路时要谨慎慢行，不得空挡滑行。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车行经漫水桥路段时，应当停车察明水情，确认安全后，低速通过。空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', 3, 1, '2021-12-19 00:13:30', '2021-12-19 00:13:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11096, '超车时应从前车的左侧超越，是因为左侧超车便于观察，有利于安全。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶人的座位在左侧，所以不管是对于超车的驾驶人还是被超车的驾驶人而言都比右侧利于观察，也更加有利于安全。', 3, 1, '2021-12-19 00:13:31', '2021-12-19 00:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11097, '如图所示，在这种情况下通过前方路口，应该怎么行驶？', '/uploadFile/yuanBeiTu1097.gif', '1', '1', NULL, '加速通过', '减速或停车避让行人', '赶在行人前通过', '靠左侧行驶', 'B', '驾驶机动车在道路通行中要坚持行人优先的原则，当遇行人横过道路时，应当减速或停车避让，待行人通过后再通行，不得加速抢行。', 3, 1, '2021-12-19 00:13:31', '2021-12-19 00:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11098, '交叉路口不得倒车的原因是什么？', '', '1', '1', NULL, '交通情况复杂，容易造成交通堵塞甚至引发事故', '交通监控设备多', '交通警察多', '车道数量少', 'A', '交叉路口车流最多，情况很复杂，如果倒车极易造车拥堵或易发事故。', 3, 1, '2021-12-19 00:13:31', '2021-12-19 00:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11099, '行至漫水路时，应当怎样做？', '', '1', '1', NULL, '高速通过，减少涉水时间', '空挡滑行', '低速通过涉水路段', '高挡位低速通过', 'C', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 3, 1, '2021-12-19 00:13:31', '2021-12-19 00:13:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11100, '在后方无来车的情况下，在隧道中倒车应靠边行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十条 机动车倒车时，应当察明车后情况，确认安全后倒车。不得在铁路道口、交叉路口、单行路、桥梁、急弯、陡坡或者隧道中倒车。', 3, 1, '2021-12-19 00:13:32', '2021-12-19 00:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11101, '如图所示，驾驶机动车遇到这种情况能够加速通过，是因为人行横道没有行人通过。', '/uploadFile/yuanBeiTu1101.gif', '2', '1', NULL, '', '', '', '', 'B', '按规定，驾驶机动车遇到人行横道时必须减速让行，必要时需要停车让行。因为行人有很大的随意性，需要谨慎减速慢行。', 3, 1, '2021-12-19 00:13:32', '2021-12-19 00:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11102, '超车需从前车左侧超越，以下说法正确是什么？', '', '1', '1', NULL, '左侧为慢速车道', '我国实行左侧通行原则', '右侧为快速车道', '便于观察，有利于安全', 'D', '我国实行右侧通行原则，左侧为快速车道，右侧为慢速车道。选项描述中只有【便于观察，有利于安全】的说法是正确的。', 3, 1, '2021-12-19 00:13:32', '2021-12-19 00:13:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11103, '驾驶机动车向右变更车道前应仔细观察右侧车道车流情况的原因是什么？', '', '1', '1', NULL, '判断有无变更车道的条件', '准备抢行', '迅速变更车道', '准备迅速停车', 'A', '仔细观察是为了判断是否可以变道，也就是有无变更车道的条件。如果没有变更车道的条件，应该放弃变道等候合适时机；如果有变更车道的条件，驾驶员打开转向灯后，确认安全后完成变更车道。', 3, 1, '2021-12-19 00:13:33', '2021-12-19 00:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11104, '在划有道路中心线的道路上会车时，应做到保持安全车速、不越线行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '在有中心线的道路上会车时，注意观察道路两侧交通情况，做到保持安全车速、安全距离，不越线行驶。遇对方车辆超过中心线或开启转向灯示意占道行驶时，立即靠路右侧减速或停车让行。', 3, 1, '2021-12-19 00:13:33', '2021-12-19 00:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11105, '如图所示，在前方路口可以掉头。', '/uploadFile/yuanBeiTu1105.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十九条 机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 3, 1, '2021-12-19 00:13:33', '2021-12-19 00:13:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11106, '如图所示，在这种情况下遇右侧车辆变更车道，应减速保持间距，注意避让。', '/uploadFile/yuanBeiTu1106.gif', '2', '1', NULL, '', '', '', '', 'A', '碰到右侧车辆打了左转灯，前车轮已经向本车道转过来了，需要减速保持车距，如果不避让硬冲上去，极有可能发生事故。', 3, 1, '2021-12-19 00:13:34', '2021-12-19 00:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11107, '如图所示，以下哪种情况可以超车。', '/uploadFile/yuanBeiTu1107.gif', '1', '1', NULL, 'C图', 'D图', 'B图', 'A图', 'D', '虚线一侧才能超车，对向有来车不得超车，故只有A图可以超车，请注意！图片的ABCD对应的并不是选项的ABCD！选项D表示A图。', 3, 1, '2021-12-19 00:13:34', '2021-12-19 00:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11108, '遇前方路段车道减少，车辆行驶缓慢，为保证安全有序应该怎样做？', '', '1', '1', NULL, '穿插到前方排队车辆中通过', '依次交替通行', '加速从前车左右超越', '借对向车道迅速通过', 'B', '《道路交通安全法》第四十五条，机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。 在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当依次交替通行。', 3, 1, '2021-12-19 00:13:34', '2021-12-19 00:13:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11109, '为什么规定辅路车让主路车先行？', '', '1', '1', NULL, '辅路车便于观察', '主路车流量大、速度快', '主路车流量小、速度快', '辅路车速度快', 'B', '正因为车流量大才成为主路，主路为干线，辅路为支线，支线车让干线车先行。一般主路车道较多，车速较快，所以辅路车要让主路车先行。', 3, 1, '2021-12-19 00:13:35', '2021-12-19 00:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11110, '如图所示，机动车在这样的城市道路上行驶，最高的行驶速度不得超过50公里/小时。', '/uploadFile/yuanBeiTu1110.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，驾驶机动车在没有道路中心线的城市道路，行驶速度不得超过30公里/小时。根据《道路交通安全法实施条例》中规定：第四十五条 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： （一）没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； （二）同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 3, 1, '2021-12-19 00:13:35', '2021-12-19 00:13:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11111, '在涉水路段跟车行驶时，应当怎样做？', '', '1', '1', NULL, '紧跟其后', '超越前车，抢先通过', '适当增加车距', '并行通过', 'C', '涉水路段路滑， 为了避免“水滑”时出现追尾撞车，应该保持安全距离。', 3, 1, '2021-12-19 00:13:36', '2021-12-19 00:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11112, '如图所示，在这种情况下，会车时必须减速靠右通过。', '/uploadFile/yuanBeiTu1112.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，车道较窄，会车时，为了防止不必要的摩擦，所以要减速靠右行驶。', 3, 1, '2021-12-19 00:13:36', '2021-12-19 00:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11113, '在狭窄的山路会车，规定不靠山体的一方优先行驶的原因是什么？', '', '1', '1', NULL, '靠山体的一方相对安全', '靠山体的一方视野宽阔', '不靠山体的一方车速较快', '三项都正确', 'A', '狭窄的山路会车，因为不靠山体一方缺少山体的保护，如果减速靠右停车让行，有掉下山崖的危险；靠山体一方有山体的保护，停车让行相对安全。', 3, 1, '2021-12-19 00:13:36', '2021-12-19 00:13:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11114, '牵引故障车时，牵引与被牵引的机动车，在行驶中都要开启危险报警闪光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十一条 牵引故障机动车应当遵守下列规定： 　　 (一)被牵引的机动车除驾驶人外不得载人，不得拖带挂车； 　　 (二)被牵引的机动车宽度不得大于牵引机动车的宽度； 　　 (三)使用软连接牵引装置时，牵引车与被牵引车之间的距离应当大于4米小于10米； 　　 (四)对制动失效的被牵引车，应当使用硬连接牵引装置牵引； 　　 (五)牵引车和被牵引车均应当开启危险报警闪光灯。汽车吊车和轮式专用机械车不得牵引车辆。摩托车不得牵引车辆或者被其他车辆牵引。转向或者照明、信号装置失效的故障机动车，应当使用专用清障车拖曳。>>>因此本题正确。', 3, 1, '2021-12-19 00:13:37', '2021-12-19 00:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11115, '雾天行车为了提高能见度，应该开启远光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '在雾较大时，可开启近光灯照明，以便看清前方路口，同时开启危险报警闪光灯，不能使用远光灯。 雾天不能使用远光灯的原因：汽车远光灯的灯光是照向前上方的，射出的光线被雾气反射后，会在车前行车白茫茫的一片，这不但不能起到照明作用还会严重阻挡驾驶人视线、视野，当后面车辆使用远光灯时也会严重影响前车驾驶人观察后方情况，因此雾天行车，不能使用远光灯。', 3, 1, '2021-12-19 00:13:37', '2021-12-19 00:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11116, '驾驶机动车在没有道路中心线的道路上行驶，应该在道路的左侧通行。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》规定：在没有中心线的道路上，机动车应该靠中间行驶，行人和非机动车在道路两侧行驶。', 3, 1, '2021-12-19 00:13:37', '2021-12-19 00:13:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11117, '如图所示，车辆在拥挤路段排队行驶时，遇到其他车辆强行穿插行驶，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1117.gif', '1', '1', NULL, '迅速左转躲避', '减速或停车让行', '持续鸣喇叭警告', '迅速提高车速不让其穿插', 'B', '驾驶车辆遇前方车辆行进缓慢或道路、路口因故堵塞时，应减速或停车，依次缓慢行驶或耐心排队等待；连续鸣喇叭催促、抢行、加塞或穿插绕行、选择空当逐车超越等做法都会加剧道路拥堵或路口堵塞，甚至会发生剐碰事故。', 3, 1, '2021-12-19 00:13:38', '2021-12-19 00:13:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11118, '如图所示，在这种情况下，A车可以向左变更车道。', '/uploadFile/yuanBeiTu1118.gif', '2', '1', NULL, '', '', '', '', 'B', '变更车道时不得妨碍驶入车道内车辆的正常行驶，在如图所示情况下，A车不得向左变更车道，以免与C车发生碰撞事故。', 3, 1, '2021-12-19 00:13:38', '2021-12-19 00:13:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11119, '如图所示，驾驶机动车接打电话容易导致发生交通事故。', '/uploadFile/yuanBeiTu1119.gif', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车需要驾驶员集中注意力观察周围的情况，打电话容易分散驾驶员的注意力，会导致驾驶员不注意周围情况从而引发交通事故。', 3, 1, '2021-12-19 00:13:39', '2021-12-19 00:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11120, '驾驶机动车下长坡时，利用惯性滑行可以减少燃油消耗，值得提倡。', '', '2', '1', NULL, '', '', '', '', 'B', '下长坡利用惯性空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。 下长坡时或连续下坡时，车速会越来越快，连续使用行车制动，制动器温度升高到一定程度时会使制动效果急剧下降。控制车速最好的方法是使用低速挡行驶，充分利用发动机制动减速，严禁利用惯性空挡滑行，不但不省油，反而会有很大的安全隐患。', 3, 1, '2021-12-19 00:13:39', '2021-12-19 00:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11121, '驾驶机动车超车时，前方车辆不减速让路，应停止超车并适当减速，与前方车辆保持安全距离。', '', '2', '1', NULL, '', '', '', '', 'A', '不具备超车条件时，应该主动放弃超车，并与前车保持安全距离，避免发生碰撞。', 3, 1, '2021-12-19 00:13:39', '2021-12-19 00:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11122, '如图所示，A车货物掉落，导致B车与掉落货物发生碰撞，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1122.gif', '1', '1', NULL, 'B车自负责任', 'A车负全部责任', '各负一半责任', '偶然事件，不可避免', 'B', '如图所示，A车违反货物装载要求，装载的货物长度超出车厢长度，导致货物掉落与B车发生碰撞，因此A车负全部责任。', 3, 1, '2021-12-19 00:13:39', '2021-12-19 00:13:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11123, '闪光警告信号灯为持续闪烁的黄灯，其作用是提示车辆、行人需要快速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '闪光警告信号灯为持续闪烁的黄灯，提示车辆、行人通行时注意瞭望，确认安全后通过。因此，快速通过做法错误。', 3, 1, '2021-12-19 00:13:40', '2021-12-19 00:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11124, '因避让特种车辆而发生违法行为，被电子警察拍到时，可向交管部门复议。', '', '2', '1', NULL, '', '', '', '', 'A', '根据规定，路上碰见特种车辆需要避让。因此避让特种车辆是遵守交通法规的行为，如果因此被电子警察拍到，是可以申请复议的。', 3, 1, '2021-12-19 00:13:40', '2021-12-19 00:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11125, '如图所示，驾驶机动车遇到这种情况时，A车应当注意避让。', '/uploadFile/yuanBeiTu1125.gif', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法》第五十四条条明确规定：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。', 3, 1, '2021-12-19 00:13:40', '2021-12-19 00:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11126, '如图所示，驾驶过程中遇到这种情况时，A车可以长鸣喇叭提醒道路养护车辆暂停喷水。', '/uploadFile/yuanBeiTu1126.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第五十四条条明确规定：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。不能鸣喇叭让其停止喷水。', 3, 1, '2021-12-19 00:13:40', '2021-12-19 00:13:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11127, '在高速公路上驾驶机动车，车辆发生故障后的处置方法，以下说法错误的是什么？', '', '1', '1', NULL, '打开危险报警闪光灯，夜间还应开启示廓灯、后位灯', '在车后150米以外设置安全警告标志', '车内乘员应下车辅助将故障车辆推移到紧急停车带上', '所有人员需离开故障车辆，在紧急停车带或护栏以外安全位置报警并等候救援', 'C', '请注意，题干要求选出说法错误的选项。根据《道路交通安全法》规定：机动车在高速公路上发生故障时，驾驶人应当立即开启危险报警闪光灯，将车辆移至不妨碍交通的地点，并在车后150米设置警告标志。夜间还应当同时开启示廓灯和后位灯。驾驶人和乘车人应当迅速转移到右侧路肩上或者应急车道内。 如果发生故障无法正常行驶的，应当由救援车、清障车拖曳、牵引，车内乘员将其推到紧急停车带上，做法错误。', 3, 1, '2021-12-19 00:13:41', '2021-12-19 00:13:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11128, '驾驶机动车发生故障或事故不能正常行驶时，应立即打开危险报警闪光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》规定：机动车在高速公路上发生故障时，驾驶人应当立即开启危险报警闪光灯，将车辆移至不妨碍交通的地点，并在车后150米设置警告标志。夜间还应当同时开启示廓灯、后位灯和危险报警闪光灯。驾驶人和乘车人应当迅速转移到右侧路肩上或者应急车道内。', 3, 1, '2021-12-19 00:13:42', '2021-12-19 00:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11129, '驾驶机动车在高速公路上行驶，能见度小于200米时，车速不得超过每小时60公里。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法实施条例》第八十一条：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，应当遵守下列规定：能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离。', 3, 1, '2021-12-19 00:13:42', '2021-12-19 00:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11130, '上道路行驶的机动车驾驶人未携带机动车驾驶证、行驶证的，除扣留机动车外，并受到什么处罚？', '', '1', '1', NULL, '警告', '罚款', '拘留', '吊销驾驶证', 'B', '未携带驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供驾驶证，并可以依照道路交通法第九十条的规定予以200元的罚款。', 3, 1, '2021-12-19 00:13:42', '2021-12-19 00:13:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11131, '以下哪种行为处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证？', '', '1', '1', NULL, '醉酒驾驶机动车的', '故意遮挡机动车号牌的', '使用其他车辆保险标志的', '因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的', 'D', '根据《道路交通安全法》第九十一条规定，饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。', 3, 1, '2021-12-19 00:13:43', '2021-12-19 00:13:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11132, '以下哪种违法行为的机动车驾驶人将被一次记6分？', '', '1', '1', NULL, '驾驶与准驾车型不符的机动车', '车速超过规定时速50%以上', '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', 'C', '《道路交通安全法》规定 机动车驾驶人有下列违法行为之一，一次记6分：（二）驾驶机动车违反道路交通信号灯通行的；', 3, 1, '2021-12-19 00:13:44', '2021-12-19 00:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11133, '驾驶机动车造成重大交通事故后逃逸，构成犯罪的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第一百零一条规定，造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', 3, 1, '2021-12-19 00:13:44', '2021-12-19 00:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11134, '隐瞒有关情况或者提供虚假材料申请机动车驾驶证，申请人在多少年内不得再次申领机动车驾驶证？', '', '1', '1', NULL, '1年', '2年', '3年', '4年', 'A', '《机动车驾驶证申领和使用规定》第六章第七十八条规定：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。【假1】', 3, 1, '2021-12-19 00:13:44', '2021-12-19 00:13:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11135, '以下哪种行为机动车驾驶人将被一次记12分？', '', '1', '1', NULL, '驾驶机动车不按规定避让校车的', '驾驶证被暂扣期间驾驶机动车的', '驾驶机动车违反道路交通信号灯通行的', '驾驶与准驾车型不符的机动车的', 'D', '《道路交通安全违法行为记分分值》一、机动车驾驶人有下列违法行为之一，一次记12分 ： （一）驾驶与准驾车型不符的机动车的； 二、机动车驾驶人有下列违法行为之一，一次记6分： （一）机动车驾驶证被暂扣期间驾驶机动车的； （二）驾驶机动车违反道路交通信号灯通行的； （十四）驾驶机动车不按照规定避让校车的。', 3, 1, '2021-12-19 00:13:45', '2021-12-19 00:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11136, '驾驶机动车时接打电话容易引发事故，以下原因错误的是什么?', '', '1', '1', NULL, '单手握方向盘，对机动车控制力下降', '驾驶人注意力不集中，不能及时判断危险', '电话的信号会对汽车电子设备的运行造成干扰', '驾驶人对路况观察不到位，容易导致操作失误', 'C', '请注意！题干要求选出原因错误的选项。 驾驶机动车时接打电话容易引发事故，主要是因为接电话时单手握方向盘，对机动车控制力下降。而且驾驶人注意力不集中，不能及时判断危险。同时驾驶人对路况观察不到位，容易导致操作失误。电话信号并不会对汽车电子设备的运行造成干扰，因此是错误的说法。', 3, 1, '2021-12-19 00:13:45', '2021-12-19 00:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11137, '如图所示，在这起交通事故中，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1137.gif', '1', '1', NULL, 'A车负全部责任', 'B车负全部责任', '各负一半的责任', 'B车负主要责任', 'A', '在有障碍的路段遇对向来车时，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行。 如图所示，AB两车会车时，无障碍一方的B车享有优先通行权，A车应让B车先行，因此，A车违反了道路通行规定，负事故的全部责任。', 3, 1, '2021-12-19 00:13:45', '2021-12-19 00:13:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11138, '驾驶机动车前，以下说法错误的是什么？', '', '1', '1', NULL, '调整驾驶座椅，保证踩踏踏板舒适', '调整安全带的松紧与高低', '调整适合驾驶的方向盘位置', '调整安全头枕高度，使头枕正对驾驶人的颈椎', 'D', '起步前的调整：1、调整座椅的前后位置、靠背角度，保证踩踏踏板舒适：左脚能够轻松自如地控制离合器踏板，右脚能够自如地控制加速踏板和行车制动器踏板，两手能自如地抓住转向盘上端； 2、调整头枕高度：头枕顶部与头平齐，头枕中央对准脑后部； 3、调整安全带的松紧与高低：系安全带时，先根据身高扳动安全带高度调整钮调整好高度，再用右手缓慢将安全带向下平顺拉出。', 3, 1, '2021-12-19 00:13:46', '2021-12-19 00:13:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11139, '如图所示，驾驶机动车行经该路段时，应减速慢行，避免因眩目导致的交通事故。', '/uploadFile/yuanBeiTu1139.gif', '2', '1', NULL, '', '', '', '', 'A', '行经狭窄、黑暗的隧道时，驾驶人的严谨会有一个明暗适应过程，因此，应合理使用灯光和减速慢行，避免因隧道外的炫目导致交通事故。', 3, 1, '2021-12-19 00:13:46', '2021-12-19 00:13:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11140, '驾驶机动车遇到前方低速行驶的洒水车作业时，以下做法错误的是什么？', '', '1', '1', NULL, '注意避让', '若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道', '若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更', '通过洒水车时应急加速通过', 'D', '请注意，题干要求选出做法错误的选项。驾驶机动车遇到前方低速行驶的洒水车作业时，需要注意避让；若洒水车有指示箭头，在确保安全的情况下按箭头指示方向变更车道；若洒水车无指示箭头，在确保安全的情况下选择合适的车道变更；不能加速通过。', 3, 1, '2021-12-19 00:13:46', '2021-12-19 00:13:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11141, '驾驶机动车在高速公路上遇到雨雪天气时，需要降低车速、保持安全距离的原因，以下说法错误的是什么？', '', '1', '1', NULL, '能见度下降，驾驶人难以及时发现前方车辆', '此类天气条件下的道路上，车辆的制动距离变长', '为车辆安全行驶提供足够的安全距离', '降低恶劣天气对车辆造成的损害', 'D', '驾驶机动车在高速公路上遇到雨雪天气时，能见度下降，驾驶人难以及时发现前方车辆，在此类天气条件下的道路上，车辆的制动距离变长，因此需要降低车速，为车辆安全行驶提供足够的安全距离。故，选项中“降低恶劣天气对车辆造成的损害”这一说法是错误的。', 3, 1, '2021-12-19 00:13:46', '2021-12-19 00:13:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11142, '驾驶机动车在高速公路发生故障，需要停车排除故障时，以下做法先后顺序正确的是？①放置警告标志，转移乘车人员至安全处，迅速报警②开启危险报警闪光灯③将车辆移至不妨碍交通的位置④等待救援', '', '1', '1', NULL, '④③①②', '①②③④', '③②①④', '②③①④', 'D', '车辆在高速公路上发生故障，需要停车排除故障时，应①迅速打开危险报警闪光灯；②将车辆移至不妨碍交通的位置；③在来车方向150米外设置警告标志，同时驾乘人员都应尽快从车辆安全的一侧离开转移至护栏外避险，并立即报警；④等待救援。不要因紧急避险造成二次事故或更大的损失。【车靠边、人撤离、即报警】', 3, 1, '2021-12-19 00:13:47', '2021-12-19 00:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11143, '行人参与道路交通的特点除了行走随意性大、方向多变以外，还喜欢聚集、围观。', '', '2', '1', NULL, '', '', '', '', 'A', '行人参与道路交通的特点是行走随意性大、方向多变，聚集、围观等。但是如果问主要特点则是行走随意性大、方向多变。', 3, 1, '2021-12-19 00:13:47', '2021-12-19 00:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11144, '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度在50米以下时，以下做法正确的是什么？', '', '1', '1', NULL, '加速驶离高速公路', '在应急车道上停车等待', '可以继续行驶，但车速不得超过每小时40公里', '以不超过每小时20公里的车速从最近的出口尽快驶离高速公路', 'D', '《道路交通安全法》规定：机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件时，能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 3, 1, '2021-12-19 00:13:47', '2021-12-19 00:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11145, '申请人在考试过程中有贿赂、舞弊行为的，申请人在多少年内不得再次申领机动车驾驶证？', '', '1', '1', NULL, '1年', '2年', '3年', '4年', 'A', '《机动车驾驶证申领和使用规定》第七十八条规定：申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。', 3, 1, '2021-12-19 00:13:47', '2021-12-19 00:13:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11146, '机动车参加安全技术检验的主要目的是检查车辆各项性能系数，及时消除车辆安全隐患，减少事故发生。', '', '2', '1', NULL, '', '', '', '', 'A', '机动车参加安全技术检验的主要目的，检查车辆各项性能系数，及时消除车辆的安全隐患，从而有效减少交通事故的发生。', 3, 1, '2021-12-19 00:13:48', '2021-12-19 00:13:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11147, '驾驶机动车遇紧急事务，可以边开车边接打电话。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全违法行为记分分值》四、机动车驾驶人有下列违法行为之一，一次记2分： （二）驾驶机动车有拨打、接听手持电话等妨碍安全驾驶的行为的；', 3, 1, '2021-12-19 00:13:48', '2021-12-19 00:13:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11148, '如图所示，驾驶机动车时，前风窗玻璃处悬挂放置干扰视线的物品是错误的。', '/uploadFile/yuanBeiTu1148.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，在机动车驾驶室的前后窗范围内悬挂、放置妨碍驾驶人视线的物品，属于驾驶机动车的禁止行为之一，因此题干表述正确。', 3, 1, '2021-12-19 00:13:48', '2021-12-19 00:13:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11149, '驾驶机动车下长坡时，仅靠行车制动器制动，容易引起行车制动器失灵。', '', '2', '1', NULL, '', '', '', '', 'A', '汽车制动分为三种：1、行车制动，就是脚下的制动踏板，控制四轮；2、驻车制动，就是手刹车，停车时防止前滑后溜；3、发动机制动，也就是抢档，用于刹车失灵，在行车制动失灵时用，一级一级减档，从高到底，利用发动机变速箱的原理使车速降下来。下长坡时，驾驶员长时间使用行车制动器，易造成制动器热衰退，汽车的制动效能变差。', 3, 1, '2021-12-19 00:13:48', '2021-12-19 00:13:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11150, '如图所示，在这起交通事故中，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1150.gif', '1', '1', NULL, 'A车负全部责任', 'B车负全部责任', '都无责任，后果自行承担', '各负一半责任', 'B', '驾驶机动车需要掉头时，观察前、后方交通情况，确认安全后减速或停车开启左转向灯，正确选择掉头地点和时机进行掉头。掉头时不要妨碍其他车辆和行人的正常通行。 图中B车掉头妨碍A车正常行驶，因此，B车负全部责任。', 3, 1, '2021-12-19 00:13:49', '2021-12-19 00:13:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11151, '如图所示，在环岛交叉路口发生的交通事故中，应由A车负全部责任。', '/uploadFile/yuanBeiTu1151.gif', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十一条 机动车通过有交通信号灯控制的交叉路口，应当按照下列规定通行： (一)在划有导向车道的路口，按所需行进方向驶入导向车道； (二)准备进入环形路口的让已在路口内的机动车先行； 如图所示，A车准备进入环岛，B车在环岛内行驶，A车未让B车先行引发的交通事故，因此A车负事故的全部责任。', 3, 1, '2021-12-19 00:13:49', '2021-12-19 00:13:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11152, '行驶过程中发现车门未关好，应及时关闭车门，否则车辆在转弯等激烈运动过程中会造成人员或货物被甩到车外。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶机动车禁止行为：1、在车门、车厢没有关好时行车；因此，行驶过程中发现车门未关好，应及时关闭车门，否则车辆在转弯等激烈运动过程中会造成人员或货物被甩到车外，发生事故。', 3, 1, '2021-12-19 00:13:49', '2021-12-19 00:13:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11153, '驾驶机动车在山路行驶时，为了减少油耗，下坡时可以空挡滑行，并使用行车制动器控制速度。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶机动车下坡行驶，要挂中低速档位，成分利用发动机制动来控制车速，严禁空挡滑行。 【拓展知识-空挡滑行不省油而且危险大】 空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', 3, 1, '2021-12-19 00:13:50', '2021-12-19 00:13:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11154, '避让特种车辆使其顺利通过后，车辆应有序回到原车道继续行驶，不要尾随特种车辆，以免发生交通事故。', '', '2', '1', NULL, '', '', '', '', 'A', '特种车辆本身车速较快，而且由于其特殊性，驾驶路线与常规车辆也不同。如果跟随特种车会十分危险，因此避让特种车后，要有序回到原车道继续行驶。', 3, 1, '2021-12-19 00:13:50', '2021-12-19 00:13:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11155, '驾驶机动车行驶过程中，遇道路养护车辆从本车道逆向驶来时，以下做法正确的是什么？', '', '1', '1', NULL, '靠边减速或停车让行', '在原车道继续行驶', '占用非机动车道行驶', '鸣喇叭示意其让道', 'A', '遇道路养护车辆逆向驶来时，应靠边减速或停车让行。根据《道路交通安全法》第五十四条条明确规定：道路养护车辆、工程作业车进行作业时，在不影响过往车辆通行的前提下，其行驶路线和方向不受交通标志、标线限制，过往车辆和人员应当注意避让。', 3, 1, '2021-12-19 00:13:50', '2021-12-19 00:13:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11156, '驾驶机动车由加速车道进入高速公路行驶，以下做法错误的是什么？', '', '1', '1', NULL, '在加速车道上加速，同时要开启左转向灯', '密切注意左侧行车道的车流状态，同时用后视镜观察后方的情况', '充分利用加速车道的长度加速，确认安全后，平顺地进入行车道', '经加速车道充分加速后，可直接驶入最左侧车道', 'D', '驾驶机动车驶入加速车道后，要开启左转向灯，尽快将车速提高到60公里/小时以上，仔细观察行车道上车辆行驶情况，选择驶入行车道的时机。不得直接驶入最左侧车道，不得在加速车道内紧急制动或停车。请注意，题干要求选出做法错误的选项。', 3, 1, '2021-12-19 00:13:51', '2021-12-19 00:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11157, '驾驶机动车在高速公路上车辆发生故障时，为获得其他车辆的帮助，可将警告标志放置在其他车道。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车在高速路或城市快速路上发生故障时，警告标志应当设置在故障车来车方向150米以外，车上人员应当迅速转移到右侧路肩上或者应急车道内，并且迅速报警。因此，题干将警告标志放在其他车道的做法是错误的。', 3, 1, '2021-12-19 00:13:51', '2021-12-19 00:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11158, '驾驶机动车在高速公路上行驶，遇有雾、雨、雪、沙尘、冰雹等低能见度气象条件下，能见度在100米以下时，车速不得超过每小时多少公里，与同车道前车至少保持多少米的距离？', '', '1', '1', NULL, '40，50', '40，40', '50，40', '50，30', 'A', '能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离；能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离；能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 3, 1, '2021-12-19 00:13:51', '2021-12-19 00:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11159, '驾驶机动车在高速公路上行驶，能见度小于200米时，与同车车道前车应保持100米以上的距离。', '', '2', '1', NULL, '', '', '', '', 'A', '“261”数字记忆法：能见度小于200米时，开启雾灯、近光灯、示廓灯和前后位灯，车速不得超过每小时60公里，与同车道前车保持100米以上的距离； “145”数字记忆法：能见度小于100米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时40公里，与同车道前车保持50米以上的距离； “520”数字记忆法：能见度小于50米时，开启雾灯、近光灯、示廓灯、前后位灯和危险报警闪光灯，车速不得超过每小时20公里，并从最近的出口尽快驶离高速公路。', 3, 1, '2021-12-19 00:13:51', '2021-12-19 00:13:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11160, '机动车驾驶人有以下哪种违法行为的，暂扣六个月机动车驾驶证？', '', '1', '1', NULL, '醉酒后驾驶机动车的', '伪造、变造机动车驾驶证的', '饮酒后驾驶机动车的', '使用伪造、变造机动车驾驶证的', 'C', '《道路交通安全法》第九十一条，饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。', 3, 1, '2021-12-19 00:13:52', '2021-12-19 00:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11161, '饮酒后或者醉酒驾驶机动车发生重大交通事故构成犯罪的，依法追究刑事责任，吊销机动车驾驶证，将多少年内不得申请机动车驾驶证？', '', '1', '1', NULL, '五年', '十年', '二十年', '终生', 'D', '《道路交通安全法》第九十一条，饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 3, 1, '2021-12-19 00:13:52', '2021-12-19 00:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11162, '驾驶拼装的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，并吊销机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废。对驾驶前款所列机动车上道路行驶的驾驶人，处二百元以上二千元以下罚款，并吊销机动车驾驶证。', 3, 1, '2021-12-19 00:13:52', '2021-12-19 00:13:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11163, '机动车驾驶人造成重大交通事故后逃逸，构成犯罪的，十年内不能申请机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'B', '《交通法》第一百零一条：违反道路交通安全法律、法规的规定，发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证。造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。', 3, 1, '2021-12-19 00:13:53', '2021-12-19 00:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11164, '驾驶机动车造成交通事故后逃逸，尚不构成犯罪的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处15日以下拘留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十九条　有下列行为之一的，由公安机关交通管理部门处二百元以上二千元以下罚款： （一）未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的； （二）将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； （三）造成交通事故后逃逸，尚不构成犯罪的； （四）机动车行驶超过规定时速百分之五十的；（超过规定时速50%） （五）强迫机动车驾驶人违反道路交通安全法律、法规和机动车安全驾驶要求驾驶机动车，造成交通事故，尚不构成犯罪的； （六）违反交通管制的规定强行通行，不听劝阻的； （七）故意损毁、移动、涂改交通设施，造成危害后果，尚不构成犯罪的； （八）非法拦截、扣留机动车辆，不听劝阻，造成交通严重阻塞或者较大财产损失的。 行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证；有第一项、第三项、第五项至第八项情形之一的，可以并处十五日以下拘留。', 3, 1, '2021-12-19 00:13:53', '2021-12-19 00:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11165, '机动车驾驶人一次有两个以上违法行为记分的，应当分别计算累加分值。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第五十六条 对机动车驾驶人的道路交通安全违法行为，处罚与记分同时执行。机动车驾驶人一次有两个以上违法行为记分的，应当分别计算，累加分值。', 3, 1, '2021-12-19 00:13:53', '2021-12-19 00:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11166, '隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在1年内不得再次申领机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第七十八条：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。申请人在考试过程中有贿赂、舞弊行为的，取消考试资格，已经通过考试的其他科目成绩无效；申请人在一年内不得再次申领机动车驾驶证。', 3, 1, '2021-12-19 00:13:53', '2021-12-19 00:13:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11167, '驾驶机动车下长坡时，空挡滑行会导致再次挂挡困难。', '', '2', '1', NULL, '', '', '', '', 'A', '空档滑行时，汽车会失去发动机牵制作用，下坡的过程中，由于重力的作用，汽车速度会越来越快，在没有发动机牵制下的汽车，制动距离要增大很多，也就是说，刹车的作用会大大降低，车辆会处于失控状态，再次挂档也会很难。一旦前方有状况出，驾驶员没法使车辆及时减速或者停下来，造成很大的安全隐患。因此，下坡行驶严禁空挡滑行。', 3, 1, '2021-12-19 00:13:54', '2021-12-19 00:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11168, '驾驶机动车超车时，被超越车辆未减速让路，应迅速提速超越前方车辆完成超车。', '', '2', '1', NULL, '', '', '', '', 'B', '被超车辆未减速让行，说明不具备超车条件，此时应该放弃超车，否则容易撞在一起，非常危险。', 3, 1, '2021-12-19 00:13:54', '2021-12-19 00:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11169, '警车、消防车、救护车、工程救险车执行紧急任务时，耽误或影响其通行可能会导致严重后果，所以其他车辆和行人应当主动让行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十三条：警车、消防车、救护车、工程救险车执行紧急任务时，可以使用警报器、标志灯具；在确保安全的前提下，不受行驶路线、行驶方向、行驶速度和信号灯的限制，其他车辆和行人应当让行。', 3, 1, '2021-12-19 00:13:54', '2021-12-19 00:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11170, '驾驶机动车在高速公路上车辆发生故障时，若车辆可以移动至应急车道内，只需开启危险报警闪光灯，警告标志可根据交通流情况选择是否放置。', '', '2', '1', NULL, '', '', '', '', 'B', '车辆移至应急车道停车后，应①立即开启危险报警闪光灯（夜间还要开启示廓灯和后位灯），②在车后150米以外设置警告标志，③车上人员要迅速转移至车右前方护栏以外的安全地带，④面向来车方向并迅速拨打紧急电话求援或报警。', 3, 1, '2021-12-19 00:13:54', '2021-12-19 00:13:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11171, '将机动车交由未取得机动车驾驶证的人驾驶的，由公安交通管理部门处二百元以上二千元以下罚款，可以并处以下哪种处罚？', '', '1', '1', NULL, '15日以下拘留', '吊销驾驶证', '扣留车辆', '5年不得重新取得新驾驶证', 'B', '将车借给未取得驾驶证的人驾驶的，出借人要受到如下处罚：罚款200元以上2000元以下，吊销驾驶证。根据《道路交通安全法》第九十九条规定：有下列行为之一的，由公安机关交通管理部门处200元以上2000元以下罚款： 2、将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的； 行为人有前款第二项、第四项情形之一的，可以并处吊销机动车驾驶证。', 3, 1, '2021-12-19 00:13:55', '2021-12-19 00:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11172, '申请人以不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，申请人在3年内不得再次申领机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路运输条例》说明，申请人以不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可，申请人在3年内不得再次申领机动车驾驶证。', 3, 1, '2021-12-19 00:13:55', '2021-12-19 00:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11173, '这个仪表是何含义？', '/uploadFile/yuanBeiTu1173.gif', '1', '1', NULL, '发动机转速表', '行驶速度表', '区间里程表', '百公里油耗表', 'A', '发动机转速表：指示发动机的转速，单位为1000转/分（×1000r/min）；转速表指针所指的数字显示当前发动机转速。发动机运转时，转速表指针不能超过红色警示区。', 4, 1, '2021-12-19 00:13:55', '2021-12-19 00:13:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11174, '这个仪表是何含义？', '/uploadFile/yuanBeiTu1174.gif', '1', '1', NULL, '百公里油耗表', '速度和里程表', '发动机转速表', '最高时速值表', 'B', '速度和里程表：速度表指示汽车行驶速度，单位为公里/小时（km/h）,速度表指针所指的数字显示当前车辆的行驶速度。里程表累计行驶总里程数以公里（km）为单位；日里程表用于记录一天或某段区间的里程数；按回零按钮至“0”位后重新开始计数。', 4, 1, '2021-12-19 00:13:56', '2021-12-19 00:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11175, '这个仪表是何含义？', '/uploadFile/yuanBeiTu1175.gif', '1', '1', NULL, '电流表', '压力表', '水温表', '燃油表', 'C', '水温表：指示发动机冷却液的温度，单位为摄氏度（℃）。“C”表示温度低，“H”表示温度高，水温表指针所指的位置显示当前冷却液的温度。', 4, 1, '2021-12-19 00:13:56', '2021-12-19 00:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11176, '这个仪表是何含义？', '/uploadFile/yuanBeiTu1176.gif', '1', '1', NULL, '电流表', '压力表', '水温表', '燃油表', 'D', '燃油表：指示油箱内的燃油量。“E”表示空，“F”表示满。当指针指在红色警告线以内时，提示油箱内燃油不足，需及时加油。', 4, 1, '2021-12-19 00:13:56', '2021-12-19 00:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11177, '仪表显示当前车速是20公里/小时。', '/uploadFile/yuanBeiTu1177.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示是发动机转速表，指示发动机的转速，转速表指针所指的数字显示当前发动机转速，发动机运转时，转速表指针不能超过红色警示区。', 4, 1, '2021-12-19 00:13:56', '2021-12-19 00:13:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11178, '仪表显示当前发动机转速是6000转/分钟。', '/uploadFile/yuanBeiTu1178.gif', '2', '1', NULL, '', '', '', '', 'B', '', 4, 1, '2021-12-19 00:13:57', '2021-12-19 00:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11179, '仪表显示当前冷却液的温度是90℃。', '/uploadFile/yuanBeiTu1179.gif', '2', '1', NULL, '', '', '', '', 'A', '水温表：指示发动机冷却液的温度，单位为摄氏度（℃）。水温表指针所指的位置显示当前冷却液的温度。 如图所示，指针指的位置是90，表示当前冷却液的温度是90℃。', 4, 1, '2021-12-19 00:13:57', '2021-12-19 00:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11180, '仪表显示油箱内存油量已在警告线以内。', '/uploadFile/yuanBeiTu1180.gif', '2', '1', NULL, '', '', '', '', 'A', '燃油表：指示油箱内的燃油量。当指针指在红色警告线以内时，提示油箱内燃油不足，需及时加油。', 4, 1, '2021-12-19 00:13:57', '2021-12-19 00:13:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11181, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1181.gif', '1', '1', NULL, '前雾灯打开', '后雾灯打开', '前照灯近光打开', '前照灯远光打开', 'A', '如图所示，前雾灯开启。', 4, 1, '2021-12-19 00:13:58', '2021-12-19 00:13:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11182, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1182.gif', '1', '1', NULL, '前雾灯打开', '后雾灯打开', '前照灯近光打开', '前照灯远光打开', 'B', '如图所示，后雾灯开启。', 4, 1, '2021-12-19 00:13:58', '2021-12-19 00:13:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11183, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1183.gif', '1', '1', NULL, '危险报警闪光灯开启', '前照灯开启', '前后位置灯开启', '前后雾灯开启', 'C', '如图所示，前后位置灯（又称示廓灯）开启。', 4, 1, '2021-12-19 00:13:58', '2021-12-19 00:13:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11184, '打开前雾灯开关，（如图所示）亮起。', '/uploadFile/yuanBeiTu1184.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，表示后雾灯开启。', 4, 1, '2021-12-19 00:13:59', '2021-12-19 00:13:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11185, '打开后雾灯开关，（如图所示）亮起。', '/uploadFile/yuanBeiTu1185.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，表示前雾灯开启。', 4, 1, '2021-12-19 00:14:10', '2021-12-19 00:14:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11186, '打开位置灯开关，（如图所示）亮起。', '/uploadFile/yuanBeiTu1186.gif', '2', '1', NULL, '', '', '', '', 'A', '图中标志为前后位置灯（又称示廓灯）开启。 该指示灯是用来显示车辆示宽灯（也叫示廓灯、位置灯）的工作状态，平时为熄灭状态，当示宽灯打开时，该指示灯随即点亮。当示宽灯关闭或者关闭示宽灯打开大灯时，该指示灯自动熄灭。', 4, 1, '2021-12-19 00:14:11', '2021-12-19 00:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11187, '发动机起动后仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1187.gif', '1', '1', NULL, '发动机机油压力过高', '发动机主油道堵塞', '发动机机油压力过低', '发动机曲轴箱漏气', 'C', '如图所示灯亮，表示发动机机油压力过低或机油量不足。 应对措施：停车检查机油量，若过少则进行补充添加，若不少则尽快联系救援。', 4, 1, '2021-12-19 00:14:11', '2021-12-19 00:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11188, '机动车仪表板上（如图所示）亮表示发动机可能机油量不足。', '/uploadFile/yuanBeiTu1188.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示灯亮，表示发动机机油压力过低或机油量不足。', 4, 1, '2021-12-19 00:14:11', '2021-12-19 00:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11189, '机动车仪表板上（如图所示）亮表示发动机可能机油压力过高。', '/uploadFile/yuanBeiTu1189.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示灯亮，表示发动机机油压力过低或机油量不足。', 4, 1, '2021-12-19 00:14:11', '2021-12-19 00:14:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11190, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1190.gif', '1', '1', NULL, '制动踏板没回位', '驻车制动解除', '行车制动器失效', '制动系统出现异常', 'D', '括号圆圈里面是“！” 表示制动系统出现异常或故障。 应对措施：1，检查手刹是否完全松开；2、检查刹车油液是否足够；3、行驶时是否有拖拽感。若有故障，则应尽快行驶至修理厂进行检修。', 4, 1, '2021-12-19 00:14:12', '2021-12-19 00:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11191, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1191.gif', '1', '1', NULL, '防抱死制动系统故障', '驻车制动器处于解除状态', '安全气囊处于故障状态', '行车制动系统故障', 'A', '机动车仪表板上ABS指示灯亮，表示防抱死制动系统出现故障。 【拓展知识】防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果。', 4, 1, '2021-12-19 00:14:12', '2021-12-19 00:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11192, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1192.gif', '1', '1', NULL, '行车制动系统出现故障', '驻车制动器处于制动状态', '防抱死制动系统出现故障', '驻车制动器处于解除状态', 'B', '如图所示，表示驻车制动器处于制动状态。', 4, 1, '2021-12-19 00:14:12', '2021-12-19 00:14:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11193, '机动车仪表板上（如图所示）亮，表示驻车制动器操纵杆可能没松到底。', '/uploadFile/yuanBeiTu1193.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车仪表板上如图所示标志灯亮，表示制动系统出现异常或故障。因此，题干表述错误。（！)——制动系统；（P）——驻车制动。', 4, 1, '2021-12-19 00:14:13', '2021-12-19 00:14:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11194, '机动车仪表板上（如图所示）亮，表示行车制动系统可能出现故障。', '/uploadFile/yuanBeiTu1194.gif', '2', '1', NULL, '', '', '', '', 'A', '中间是个“!”的，表示制动系统出现异常或故障； 中间是个“P”的，表示驻车制动器处于制动状态。', 4, 1, '2021-12-19 00:14:13', '2021-12-19 00:14:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11195, '机动车仪表板上（如图所示）亮时，不影响正常行驶。', '/uploadFile/yuanBeiTu1195.gif', '2', '1', NULL, '', '', '', '', 'B', '图中指示灯亮表示：制动系统出现异常或故障。行车途中该灯亮起，表示制动系统出了问题或故障，会影响正常行驶。', 4, 1, '2021-12-19 00:14:13', '2021-12-19 00:14:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11196, '机动车仪表板上（如图所示）亮时，防抱死制动系统处于打开状态。', '/uploadFile/yuanBeiTu1196.gif', '2', '1', NULL, '', '', '', '', 'B', '仪表板上如图所示指示灯亮，表示防抱死制动系统出现故障。', 4, 1, '2021-12-19 00:14:13', '2021-12-19 00:14:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11197, '机动车仪表板上（如图所示）亮时，表示驻车制动器处于制动状态。', '/uploadFile/yuanBeiTu1197.gif', '2', '1', NULL, '', '', '', '', 'A', '仪表板上如图所示灯亮，表示驻车制动器处于制动状态，即手刹没松开，但是车辆没有发生故障。', 4, 1, '2021-12-19 00:14:14', '2021-12-19 00:14:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11198, '发动机起动后仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1198.gif', '1', '1', NULL, '油箱内燃油已到最低液面', '发动机供油系统出现异常', '发动机点火系统出现故障', '燃油泵出现异常或者故障', 'A', '仪表板上如图所示灯亮，表示油箱内燃油已到最低液面。', 4, 1, '2021-12-19 00:14:14', '2021-12-19 00:14:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11199, '机动车仪表板上（如图所示）亮时，提醒发动机需要补充机油。', '/uploadFile/yuanBeiTu1199.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示灯亮，表示燃油不足，油箱内燃油已到最低液面，应当补充燃油，不是补充发动机机油。', 4, 1, '2021-12-19 00:14:14', '2021-12-19 00:14:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11200, '行车中仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1200.gif', '1', '1', NULL, '发动机温度过低', '发动机温度过高', '发动机冷却系故障', '发动机润滑系故障', 'B', '发动机温度过高时，仪表盘上如图所示标志会亮起，这个类似于温度计的图标，叫做水温指示灯。当这个图标亮起，说明可能是冷却液不足了，发动机温度自然就高了。', 4, 1, '2021-12-19 00:14:15', '2021-12-19 00:14:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11201, '机动车仪表板上（如图所示）亮时，提醒发动机冷却液可能不足。', '/uploadFile/yuanBeiTu1201.gif', '2', '1', NULL, '', '', '', '', 'A', '当发动机的冷却液液面过低（不足）、过高或泄露；风扇皮带过松或断裂；传感器故障时，仪表板上如图所示的水温报警灯亮起。 因此，题干说法正确，当如图所示的水温报警灯亮时，提醒可能是发动机的冷却液不足引起的（即冷却液不足是引起发动机水温报警灯亮的原因之一）。', 4, 1, '2021-12-19 00:14:15', '2021-12-19 00:14:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11202, '机动车仪表板上（如图所示）亮时提醒发动机需要加注机油。', '/uploadFile/yuanBeiTu1202.gif', '2', '1', NULL, '', '', '', '', 'B', '当发动机的温度过高时，车辆仪表盘上的水温报警灯（如图所示）就会亮起，提醒可能是发动机的冷却液不足引起的，不是提醒要加注机油。', 4, 1, '2021-12-19 00:14:15', '2021-12-19 00:14:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11203, '机动车仪表板上（如图所示）亮时表示什么？', '/uploadFile/yuanBeiTu1203.gif', '1', '1', NULL, '已开启前照灯近光', '已开启前雾灯', '已开启前照灯远光', '已开启后雾灯', 'C', '仪表板上如图所示灯亮，表示远光灯开启。', 4, 1, '2021-12-19 00:14:16', '2021-12-19 00:14:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11204, '机动车仪表板上（如图所示）亮时表示什么？', '/uploadFile/yuanBeiTu1204.gif', '1', '1', NULL, '已开启前照灯远光', '已开启前雾灯', '已开启后雾灯', '已开启前照灯近光', 'D', '仪表板上如图所示灯亮，表示近光灯开启。', 4, 1, '2021-12-19 00:14:16', '2021-12-19 00:14:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11205, '开启前照灯远光时仪表板上（如图所示）亮起。', '/uploadFile/yuanBeiTu1205.gif', '2', '1', NULL, '', '', '', '', 'B', '前照灯俗称大灯，包含远光灯和近光灯。如图所示，光线斜向地面照射，表示近光灯开启。', 4, 1, '2021-12-19 00:14:16', '2021-12-19 00:14:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11206, '开启前照灯近光时仪表板上（如图所示）亮起。', '/uploadFile/yuanBeiTu1206.gif', '2', '1', NULL, '', '', '', '', 'B', '开启前照远光灯时，仪表板上如图所示灯亮起。', 4, 1, '2021-12-19 00:14:17', '2021-12-19 00:14:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11207, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1207.gif', '1', '1', NULL, '没有系好安全带', '安全带出现故障', '已经系好安全带', '安全带系得过松', 'A', '仪表板上如图所示指示灯亮，表示没系安全带或安全带插头未插好。', 4, 1, '2021-12-19 00:14:17', '2021-12-19 00:14:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11208, '机动车仪表板上（如图所示）亮时，提醒驾驶人座椅没调整好。', '/uploadFile/yuanBeiTu1208.gif', '2', '1', NULL, '', '', '', '', 'B', '该指示灯亮起时，说明有驾乘人员没系安全带或安全带插头未插好，有些车型会有相应的提示音。当驾乘人员正确系好安全带后，该指示灯自动熄灭。', 4, 1, '2021-12-19 00:14:17', '2021-12-19 00:14:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11209, '机动车仪表板上（如图所示）亮时，提醒驾驶人安全带插头未插入锁扣。', '/uploadFile/yuanBeiTu1209.gif', '2', '1', NULL, '', '', '', '', 'A', '仪表板上如图所示指示灯亮，表示没系安全带或安全带插头未插好。', 4, 1, '2021-12-19 00:14:17', '2021-12-19 00:14:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11210, '危险报警闪光灯可用于下列什么场合？', '', '1', '1', NULL, '在道路上跟车行驶时', '遇到道路拥堵时', '机动车发生故障停车时', '引领后车行驶时', 'C', '危险报警闪光灯（俗称双闪）：与转向信号灯共同用一套灯具，当汽车发生故障停车或遇到紧急情况需要处理时使用，发出交替的闪光信号，用于警告后方车辆和行人。', 4, 1, '2021-12-19 00:14:18', '2021-12-19 00:14:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11211, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1211.gif', '1', '1', NULL, '右转向指示灯闪烁', '危险报警闪光灯闪烁', '左转向指示灯闪烁', '车前后位置灯闪烁', 'B', '', 4, 1, '2021-12-19 00:14:18', '2021-12-19 00:14:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11212, '机动车发生故障时，（如图所示）闪烁。', '/uploadFile/yuanBeiTu1212.gif', '2', '1', NULL, '', '', '', '', 'B', '危险报警闪光灯开关（俗称双闪）装在仪表板上，都是按钮式的，按下开关，前后的两侧转向灯会同时闪烁；再按一次开关，危险报警闪光灯关闭。当机动车发生故障停车或遇到紧急情况需要处理时，按下开关键开启，发出交替的闪光信号，用于警告后方车辆和行人。', 4, 1, '2021-12-19 00:14:18', '2021-12-19 00:14:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11213, '开启危险报警闪光灯时，（如图所示）闪烁。', '/uploadFile/yuanBeiTu1213.gif', '2', '1', NULL, '', '', '', '', 'A', '危险报警闪光灯开关（俗称双闪）装在仪表板上，都是按钮式的，按下开关，前后的两侧转向灯会同时闪烁；再按一次开关，危险报警闪光灯关闭。当机动车发生故障停车或遇到紧急情况需要处理时，按下开关键开启，发出交替的闪光信号，用于警告后方车辆和行人。', 4, 1, '2021-12-19 00:14:19', '2021-12-19 00:14:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11214, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1214.gif', '1', '1', NULL, '左转向指示灯闪烁', '右转向指示灯闪烁', '车前后位置灯亮起', '车前后示宽灯亮起', 'B', '如图所示，机动车仪表板上的指示灯亮，表示右转向指示灯开启，右转向指示灯闪烁。', 4, 1, '2021-12-19 00:14:19', '2021-12-19 00:14:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11215, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1215.gif', '1', '1', NULL, '车前后位置灯亮起', '右转向指示灯闪烁', '左转向指示灯闪烁', '车前后示宽灯亮起', 'C', '如图所示，机动车仪表板上指示灯亮，表示左转向指示灯开启，左转向指示灯闪烁。', 4, 1, '2021-12-19 00:14:19', '2021-12-19 00:14:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11216, '打开左转向灯开关，（如图所示）亮起。', '/uploadFile/yuanBeiTu1216.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，亮灯的箭头向右，表示右转向灯开启。因此，题干表述错误。', 4, 1, '2021-12-19 00:14:20', '2021-12-19 00:14:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11217, '打开右转向灯开关，（如图所示）亮起。', '/uploadFile/yuanBeiTu1217.gif', '2', '1', NULL, '', '', '', '', 'B', '如图所示，亮灯的箭头向左，表示左转向灯开启。因此，题干表述错误。', 4, 1, '2021-12-19 00:14:20', '2021-12-19 00:14:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11218, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1218.gif', '1', '1', NULL, '充电电流过大', '蓄电池损坏', '电流表故障', '充电电路故障', 'D', '如图所示指示灯亮，表示充电电路故障或发电机不向蓄电池充电。', 4, 1, '2021-12-19 00:14:20', '2021-12-19 00:14:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11219, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1219.gif', '1', '1', NULL, '两侧车门开启', '行李舱开启', '发动机舱开启', '燃油箱盖开启', 'A', '该指示灯用来显示车辆各车门状况，任意车门未关上，或者未关好，该指示灯都有点亮相应的车门指示灯，提示车主车门未关好，当车门关闭或者关好时，相应车门指示灯熄灭。', 4, 1, '2021-12-19 00:14:20', '2021-12-19 00:14:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11220, '机动车仪表板上（如图所示）这个符号表示什么？', '/uploadFile/yuanBeiTu1220.gif', '1', '1', NULL, '一侧车门开启', '行李舱开启', '发动机舱开启', '燃油箱盖开启', 'B', '车头发动机舱，车尾行李舱（俗称后备箱），机动车仪表板上出现如图所示符号，提示行李舱盖开启。', 4, 1, '2021-12-19 00:14:21', '2021-12-19 00:14:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11221, '机动车仪表板上（如图所示）这个符号表示什么？', '/uploadFile/yuanBeiTu1221.gif', '1', '1', NULL, '一侧车门开启', '行李舱开启', '发动机舱开启', '燃油箱盖开启', 'C', '车头发动机舱，车尾行李舱，机动车仪表板上出现如图所示的符号，提示发动机舱开启。', 4, 1, '2021-12-19 00:14:21', '2021-12-19 00:14:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11222, '机动车仪表板上（如图所示）一直亮表示什么？', '/uploadFile/yuanBeiTu1222.gif', '1', '1', NULL, '防抱死制动系统故障', '安全气囊处于故障状态', '安全气囊处于工作状态', '安全带没有系好', 'B', '圆球表示安全气囊，黄色表示处于故障状态。机动车仪表板上出现如图所示指示灯亮，表示安全气囊处于故障状态。', 4, 1, '2021-12-19 00:14:22', '2021-12-19 00:14:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11223, '机动车仪表板上（如图所示）这个符号表示什么？', '/uploadFile/yuanBeiTu1223.gif', '1', '1', NULL, '近光灯开关', '远光灯开关', '车灯总开关', '后雾灯开关', 'C', '图中符号表示车灯总开关。', 4, 1, '2021-12-19 00:14:22', '2021-12-19 00:14:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11224, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1224.gif', '1', '1', NULL, '空气内循环', '空气外循环', '迎面吹风', '风窗玻璃除霜', 'B', '如图所示，箭头由车外进入车内，表示空气外循环。', 4, 1, '2021-12-19 00:14:22', '2021-12-19 00:14:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11225, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1225.gif', '1', '1', NULL, '迎面出风', '空气外循环', '空气内循环', '风窗玻璃除霜', 'C', '箭头从车外到车内是空气外循环，箭头只在车内是空气内循环。', 4, 1, '2021-12-19 00:14:22', '2021-12-19 00:14:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11226, '机动车仪表板上（如图所示）这个符号表示什么？', '/uploadFile/yuanBeiTu1226.gif', '1', '1', NULL, '冷风暖气风扇', '空调制冷', '空气循环', '雪地起步模式', 'A', '图中符号表示启用冷风暖气风扇。', 4, 1, '2021-12-19 00:14:23', '2021-12-19 00:14:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11227, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1227.gif', '1', '1', NULL, '空气内循环', '地板及迎面出风', '空气外循环', '侧面及地板出风', 'B', '如图所示：箭头，一个指向驾驶人，一个指向地面，因此该标志表示地板及迎面出风。', 4, 1, '2021-12-19 00:14:23', '2021-12-19 00:14:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11228, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1228.gif', '1', '1', NULL, '空气内循环', '侧面出风', '空气外循环', '迎面出风', 'D', '如图所示：箭头指向驾驶人，因此该标志表示迎面出风。', 4, 1, '2021-12-19 00:14:23', '2021-12-19 00:14:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11229, '（如图所示）这个符号的开关控制什么装置？', '/uploadFile/yuanBeiTu1229.gif', '1', '1', NULL, '前风窗玻璃刮水器', '后风窗玻璃除霜', '后风窗玻璃刮水器', '前风窗玻璃除霜', 'A', '图中符号的开关控制前风窗玻璃刮水器装置。扇形图标是前风窗，四四方方的图标是后风窗。 【拓展延伸】 前风窗玻璃刮水器 前风窗玻璃刮水器及洗涤器 后风窗玻璃刮水器及洗涤器', 4, 1, '2021-12-19 00:14:24', '2021-12-19 00:14:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11230, '（如图所示）这个符号的开关控制什么装置？', '/uploadFile/yuanBeiTu1230.gif', '1', '1', NULL, '后风窗玻璃除霜或除雾', '前风窗玻璃刮水器及洗涤器', '后风窗玻璃刮水器及洗涤器', '前风窗玻璃除霜或除雾', 'B', '图中符号的开关控制前风窗玻璃刮水器及洗涤器装置。扇形图标是前风窗，四四方方的图标是后风窗。', 4, 1, '2021-12-19 00:14:24', '2021-12-19 00:14:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11231, '（如图所示）这个符号的开关控制什么装置？', '/uploadFile/yuanBeiTu1231.gif', '1', '1', NULL, '后风窗玻璃除霜或除雾', '前风窗玻璃刮水器及洗涤器', '后风窗玻璃刮水器及洗涤器', '前风窗玻璃除霜或除雾', 'C', '图中符合的开关控制后风窗玻璃刮水器及洗涤器装置。扇形图标是前风窗，四四方方的图标是后风窗。', 4, 1, '2021-12-19 00:14:24', '2021-12-19 00:14:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11232, '（如图所示）这个符号的开关控制什么装置？', '/uploadFile/yuanBeiTu1232.gif', '1', '1', NULL, '儿童安全锁', '两侧车窗玻璃', '电动车门', '车门锁住开锁', 'D', '由本图而知，是车门锁住开锁。所以选D', 4, 1, '2021-12-19 00:14:24', '2021-12-19 00:14:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11233, '（如图所示）这个符号的开关控制什么装置？', '/uploadFile/yuanBeiTu1233.gif', '1', '1', NULL, '儿童安全锁', '两侧车窗玻璃', '电动车门', '车门锁住开锁', 'A', '如图所示，儿童安全锁开关，是为保证乘车儿童安全的一种主动安全装置，防止车辆行驶过程中儿童从车内误开门而产生的危险。在儿童安全锁锁止情况下，即使通过中控锁电动解锁，儿童安全装置仍处在锁止状态，车门只能从外部打开，车内门拉手暂时失去开门作用。', 4, 1, '2021-12-19 00:14:25', '2021-12-19 00:14:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11234, '机动车仪表板上（如图所示）亮表示什么？', '/uploadFile/yuanBeiTu1234.gif', '1', '1', NULL, '制动液不足', '洗涤液不足', '冷却系故障', '冷却液不足', 'D', '如图所示，仪表板上此图标亮起，表示冷却液不足，用于提醒机动车驾驶人车辆冷却液不足，需及时补充。', 4, 1, '2021-12-19 00:14:25', '2021-12-19 00:14:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11235, '机动车仪表板上（如图所示）亮，提示发电机向蓄电池充电。', '/uploadFile/yuanBeiTu1235.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车仪表板上如图所示指示灯亮，表示充电电路故障或发电机不向蓄电池充电。因此，题干表述错误。', 4, 1, '2021-12-19 00:14:25', '2021-12-19 00:14:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11236, '机动车仪表板上（如图所示)亮，提示两侧车门未关闭。', '/uploadFile/yuanBeiTu1236.gif', '2', '1', NULL, '', '', '', '', 'A', '机动车仪表板上如图所示指示灯亮，表示车辆两侧车门均未关闭。', 4, 1, '2021-12-19 00:14:26', '2021-12-19 00:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11237, '机动车仪表板上（如图所示）亮，提示左侧车门未关闭。', '/uploadFile/yuanBeiTu1237.gif', '2', '1', NULL, '', '', '', '', 'B', '从图而知，图标中右侧车门显示开启状态，因此该标志是提示右侧车门未关闭，并非提示左侧车门未关闭。题目描述错误，因此本题答案为错误。', 4, 1, '2021-12-19 00:14:26', '2021-12-19 00:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11238, '机动车仪表板上（如图所示）亮，提示右侧车门未关闭。', '/uploadFile/yuanBeiTu1238.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车仪表板上左侧车门打开的指示灯亮，提示左侧车门未关闭。', 4, 1, '2021-12-19 00:14:26', '2021-12-19 00:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11239, '机动车仪表板上（如图所示）亮，提示行李舱开启。', '/uploadFile/yuanBeiTu1239.gif', '2', '1', NULL, '', '', '', '', 'B', '车头发动机舱，车尾行李舱。图中标志提示发动机舱开启。', 4, 1, '2021-12-19 00:14:26', '2021-12-19 00:14:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11240, '机动车仪表板上（如图所示）亮，提示发动机舱开启。', '/uploadFile/yuanBeiTu1240.gif', '2', '1', NULL, '', '', '', '', 'B', '车头发动机舱，车尾行李舱。图中标志提示行李舱开启。 【拓展延伸】', 4, 1, '2021-12-19 00:14:27', '2021-12-19 00:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11241, '机动车仪表板上（如图所示）一直亮，表示安全气囊处于工作状态。', '/uploadFile/yuanBeiTu1241.gif', '2', '1', NULL, '', '', '', '', 'B', '机动车仪表板上如图所示指示灯亮，表示安全气囊处于故障状态，不是工作状态。', 4, 1, '2021-12-19 00:14:27', '2021-12-19 00:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11242, '机动车仪表板上（如图所示）亮表示启用地板及前风窗玻璃吹风。', '/uploadFile/yuanBeiTu1242.gif', '2', '1', NULL, '', '', '', '', 'A', '扇型代表前风窗玻璃，曲线上升表示启用前风窗玻璃吹风，向下的大箭头表示启用地板吹风。因此，图中标志亮表示启用地板及前风窗玻璃吹风。', 4, 1, '2021-12-19 00:14:27', '2021-12-19 00:14:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11243, '机动车仪表板上（如图所示）一直亮，表示发动机控制系统故障。', '/uploadFile/yuanBeiTu1243.gif', '2', '1', NULL, '', '', '', '', 'A', '此标志亮起，表示发动机控制系统故障，因此本题正确。', 4, 1, '2021-12-19 00:14:28', '2021-12-19 00:14:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11244, '这种握转向盘的动作是正确的。', '/uploadFile/yuanBeiTu1244.gif', '2', '1', NULL, '', '', '', '', 'B', '转向盘的握法：双手握在转向盘两侧盘缘，左手握住9点位置，右手握住3点位置，食指到小指四个手指由内向外自然地握住，拇指自然按住转向盘缘，注意不要握得太紧。转动转向盘以左手为主，右手辅助。 综上，如图所示握转向盘的动作是错误的。', 4, 1, '2021-12-19 00:14:28', '2021-12-19 00:14:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11245, '这是什么踏板？', '/uploadFile/yuanBeiTu1245.gif', '1', '1', NULL, '加速踏板', '离合器踏板', '制动踏板', '驻车制动器', 'B', '如图所示装置，从左向右依次是：离合器踏板、制动踏板、加速踏板。', 4, 1, '2021-12-19 00:14:29', '2021-12-19 00:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11246, '这是什么踏板？', '/uploadFile/yuanBeiTu1246.gif', '1', '1', NULL, '加速踏板', '离合器踏板', '制动踏板', '驻车制动器', 'C', '如图所示装置，从左向右依次是：离合器踏板、制动踏板、加速踏板。', 4, 1, '2021-12-19 00:14:29', '2021-12-19 00:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11247, '这是什么踏板？', '/uploadFile/yuanBeiTu1247.gif', '1', '1', NULL, '加速踏板', '离合器踏板', '制动踏板', '驻车制动器', 'A', '如图所示装置，从左向右依次是：离合器踏板、制动踏板、加速踏板。', 4, 1, '2021-12-19 00:14:29', '2021-12-19 00:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11248, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1248.gif', '1', '1', NULL, '节气门操纵杆', '驻车制动器操纵杆', '变速器操纵杆', '离合器操纵杆', 'C', '如图所示，变速器操纵杆。变速器操纵杆内不同齿轮的啮合，可改变汽车的动力、速度和进退放下，使汽车加速、减速或倒车。设有N挡、R挡和5个（或6个）前进挡。', 4, 1, '2021-12-19 00:14:29', '2021-12-19 00:14:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11249, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1249.gif', '1', '1', NULL, '驻车制动器操纵杆', '节气门操纵杆', '变速器操纵杆', '离合器操纵杆', 'A', '如图所示，驻车制动器操纵杆（手刹）拉紧驻车制动器操纵杆起制动作用，放下操纵杆，制动解除。', 4, 1, '2021-12-19 00:14:30', '2021-12-19 00:14:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11250, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1250.gif', '1', '1', NULL, '灯光开关', '空调开关', '点火开关', '雨刷开关', 'C', '图中为点火开关操作装置。点火开关4个档位的功能是：LOCK：切断电源，锁定方向盘；ACC：接通附件电源（比如收音机等附件）ON：接通除起动机外的全车全部电源；START：接通起动机电源，起动发动机。', 4, 1, '2021-12-19 00:14:30', '2021-12-19 00:14:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11251, '将点火开关转到ACC位置起动机工作。', '/uploadFile/yuanBeiTu1251.gif', '2', '1', NULL, '', '', '', '', 'B', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，START起动。 点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； 点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； 点火开关转到ON位置，发动机工作； 点火开关转到START位置，起动机起动。', 4, 1, '2021-12-19 00:14:30', '2021-12-19 00:14:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11252, '点火开关在ON位置，车用电器不能使用。', '/uploadFile/yuanBeiTu1252.gif', '2', '1', NULL, '', '', '', '', 'B', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，START起动。 点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； 点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； 点火开关转到ON位置，发动机工作； 点火开关转到START位置，起动机起动。', 4, 1, '2021-12-19 00:14:31', '2021-12-19 00:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11253, '点火开关在LOCK位置拔出钥匙转向盘会锁住。', '/uploadFile/yuanBeiTu1253.gif', '2', '1', NULL, '', '', '', '', 'A', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，START起动。 点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； 点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； 点火开关转到ON位置，发动机工作； 点火开关转到START位置，起动机起动。', 4, 1, '2021-12-19 00:14:31', '2021-12-19 00:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11254, '点火开关在START位置起动机起动。', '/uploadFile/yuanBeiTu1254.gif', '2', '1', NULL, '', '', '', '', 'A', '汽车的点火开关，一共有4个挡位，分别为LOCK锁止，ACC断电，ON通电，START起动。 点火开关转到LOCK位置，发动机关闭，拔出钥匙，转向盘会锁止； 点火开关转到ACC位置，发动机关闭，其他车用电器可正常使用； 点火开关转到ON位置，发动机工作； 点火开关转到START位置，起动机起动。', 4, 1, '2021-12-19 00:14:31', '2021-12-19 00:14:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11255, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1255.gif', '1', '1', NULL, '灯光、信号组合开关', '倒车灯开关', '刮水器开关', '危险报警闪光灯开关', 'A', '灯光-信号组合开关，是控制照明灯光和信号灯光的装置，常见的有旋转——提拉式，大多数安装在转向盘左下方转向柱上，用左手操纵。', 4, 1, '2021-12-19 00:14:32', '2021-12-19 00:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11256, '提拉这个开关控制机动车哪个部位？', '/uploadFile/yuanBeiTu1256.gif', '1', '1', NULL, '倒车灯', '左右转向灯', '示廓灯', '报警闪光灯', 'B', '左右转向灯开关是控制转向信号灯的操纵装置，与前照灯共用一个操纵杆。向上抬杆，开启右转向灯；向下按杆，开启左转向灯。', 4, 1, '2021-12-19 00:14:32', '2021-12-19 00:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11257, '旋转开关这一档控制机动车哪个部位？', '/uploadFile/yuanBeiTu1257.gif', '1', '1', NULL, '左右转向灯', '近光灯', '前后雾灯', '远光灯', 'C', '雾灯开关与前照灯和转向信号灯开关组合为一体，或者装在仪表板上，分为前雾灯开关和后雾灯开关，用于控制前后雾灯。常见的有旋转、按钮式。向前旋转（或按下）前雾灯开关，前雾指示灯、前雾灯亮；向后旋转（或按下）后雾灯开关，后雾指示灯、后雾灯亮。', 4, 1, '2021-12-19 00:14:32', '2021-12-19 00:14:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11258, '将转向灯开关向上提，左转向灯亮。', '/uploadFile/yuanBeiTu1258.gif', '2', '1', NULL, '', '', '', '', 'B', '向上提：右转向灯亮；向下拉：左转向灯亮。', 4, 1, '2021-12-19 00:14:33', '2021-12-19 00:14:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11259, '将转向灯开关向下拉，右转向灯亮。', '/uploadFile/yuanBeiTu1259.gif', '2', '1', NULL, '', '', '', '', 'B', '转向灯开关向上提：右转向灯亮（顺时针方向，向右打方向盘的方向） ；转向灯向下拉：左转向灯亮（逆时针方向，向左打方向盘的方向）。因此，题干表述错误。', 4, 1, '2021-12-19 00:14:33', '2021-12-19 00:14:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11260, '灯光开关旋转到这个位置时，全车灯光点亮。', '/uploadFile/yuanBeiTu1260.gif', '2', '1', NULL, '', '', '', '', 'B', '', 4, 1, '2021-12-19 00:14:33', '2021-12-19 00:14:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11261, '灯光开关在该位置时，前雾灯点亮。', '/uploadFile/yuanBeiTu1261.gif', '2', '1', NULL, '', '', '', '', 'A', '', 4, 1, '2021-12-19 00:14:34', '2021-12-19 00:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11262, '灯光开关在该位置时，后雾灯点亮。', '/uploadFile/yuanBeiTu1262.gif', '2', '1', NULL, '', '', '', '', 'A', '', 4, 1, '2021-12-19 00:14:34', '2021-12-19 00:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11263, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1263.gif', '1', '1', NULL, '除雾器开关', '转向灯开关', '前照灯开关', '刮水器开关', 'D', '刮水器开关控制刮水器与洗涤器，在雨天或下雪天行驶时使用，可清除风窗玻璃上的雨雪，保证驾驶人有良好的视线。拨动开关，风窗玻璃刮水器开始工作。', 4, 1, '2021-12-19 00:14:34', '2021-12-19 00:14:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11264, '这是什么操纵装置？', '/uploadFile/yuanBeiTu1264.gif', '1', '1', NULL, '除雾器开关', '转向灯开关', '前照灯开关', '刮水器开关', 'A', '除雾器开关控制汽车除雾器的工作，用于减少前、后风窗和车外后视镜表面上的湿气、雾气和霜，以改善视线。有的车型还设有风量调整钮、温度调整钮、送风开关、冷气开关。', 4, 1, '2021-12-19 00:14:35', '2021-12-19 00:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11265, '这个开关控制机动车哪个部位？', '/uploadFile/yuanBeiTu1265.gif', '1', '1', NULL, '风窗玻璃除雾器', '风窗玻璃刮水器', '危险报警闪光灯', '照明、信号装置', 'B', '风窗玻璃刮水器开关一般安装在转向盘右下方转向柱上，用右手操纵。上下拨动这个开关，前风窗玻璃刮水器开始工作。向上拨动拨杆，刮水器单次刮水一回；向下拨动拨杆有3个挡位，分别是间歇刮水、慢速刮水、快速刮水；将杠向内拉动，清洗液喷出。', 4, 1, '2021-12-19 00:14:35', '2021-12-19 00:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11266, '按下这个开关，后风窗玻璃除霜器开始工作。', '/uploadFile/yuanBeiTu1266.gif', '2', '1', NULL, '', '', '', '', 'B', '前扇后方，前风窗玻璃除雾器开关点亮，前风窗玻璃除雾器开始开始工作。弯曲的箭头表示雾气，扇形图标是前风窗；四四方方是后风窗。', 4, 1, '2021-12-19 00:14:35', '2021-12-19 00:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11267, '上下扳动这个开关前风窗玻璃刮水器开始工作。', '/uploadFile/yuanBeiTu1267.gif', '2', '1', NULL, '', '', '', '', 'A', '风窗玻璃刮水器开关一般安装在转向盘右下方转向柱上，用右手操纵。向上拨操纵杆，将操纵杆向下拉或向上推，可选择不同的挂刷挡位；向内拉动操纵杆，将喷出清洗液。因此，题干表述正确。', 4, 1, '2021-12-19 00:14:35', '2021-12-19 00:14:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11268, '安全头枕在发生追尾事故时，能有效保护驾驶人的什么部位？', '', '1', '1', NULL, '腰部', '胸部', '颈部', '头部', 'C', '座椅上头枕的主要作用是汽车被追尾时，有效保护驾乘人的颈椎。调整头枕高度时，保持头枕中心与后脑中心平齐，才能发挥保护作用。', 4, 1, '2021-12-19 00:14:36', '2021-12-19 00:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11269, '安全头枕用于在发生追尾事故时保护驾驶人的头部不受伤害。', '', '2', '1', NULL, '', '', '', '', 'B', '座椅上头枕的主要作用是汽车被追尾时，有效保护驾驶人和乘车人的颈椎。调整头枕高度时，保持头枕中心与后脑中心平齐，才能发挥保护作用。【安全头枕】三要素：1、正对后脑；2、支撑头部；3、保护颈椎。', 4, 1, '2021-12-19 00:14:36', '2021-12-19 00:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11270, '机动车发生碰撞时座椅安全带主要作用是什么？', '', '1', '1', NULL, '减轻驾乘人员伤害', '保护驾乘人员腰部', '保护驾乘人员颈部', '保护驾乘人员胸部', 'A', '座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。', 4, 1, '2021-12-19 00:14:36', '2021-12-19 00:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11271, '机动车发生正面碰撞时，安全气囊加上安全带的双重保护才能充分发挥作用。', '', '2', '1', NULL, '', '', '', '', 'A', '汽车发生碰撞时，安全气囊迅速膨胀，在驾驶人、乘车人的证正面形成一个气垫，从而降低人体受伤害的程度。安全气囊与安全带要配套使用，如果不系安全带，气囊引爆时会对驾乘人员头部和颈部造成严重伤害。', 4, 1, '2021-12-19 00:14:36', '2021-12-19 00:14:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11272, '机动车在发生碰撞时，安全带可以减轻驾乘人员伤害。', '', '2', '1', NULL, '', '', '', '', 'A', '座椅安全带的作用是在汽车发生碰撞或紧急制动时，固定驾乘人员位置，减轻对驾乘人员的伤害。驾驶人、乘车人在汽车行驶前，系好安全带是最有效的自我保护方法，在遇到意外危险情况时可避免受到致命的伤害。', 4, 1, '2021-12-19 00:14:37', '2021-12-19 00:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11273, '机动车在紧急制动时ABS系统会起到什么作用？', '/uploadFile/yuanBeiTu1273.gif', '1', '1', NULL, '切断动力输出', '自动控制方向', '减轻制动惯性', '防止车轮抱死', 'D', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果.', 4, 1, '2021-12-19 00:14:37', '2021-12-19 00:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11274, '防抱死制动系统（ABS）在什么情况下可以最大限度发挥制动器效能？', '', '1', '1', NULL, '间歇制动', '持续制动', '缓踏制动踏板', '紧急制动', 'D', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果。', 4, 1, '2021-12-19 00:14:37', '2021-12-19 00:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11275, '机动车紧急制动时，ABS系统在提供最大制动力的同时能使车前轮保持转向能力。', '', '2', '1', NULL, '', '', '', '', 'A', '防抱死制动系统（ABS）的作用是保证车辆在任何路面上进行紧急制动时，防止车轮抱死，保持前轮转向能力，消除制动过程中的跑偏、侧滑等非稳定状态，并获得良好的制动效果。', 4, 1, '2021-12-19 00:14:37', '2021-12-19 00:14:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11276, '装有ABS系统的机动车在冰雪路面上会最大限度缩短制动距离。', '', '2', '1', NULL, '', '', '', '', 'B', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 4, 1, '2021-12-19 00:14:38', '2021-12-19 00:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11277, '驾驶有ABS系统的机动车在紧急制动的同时转向可能会发生侧滑。', '', '2', '1', NULL, '', '', '', '', 'A', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 4, 1, '2021-12-19 00:14:38', '2021-12-19 00:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11278, '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', '', '2', '1', NULL, '', '', '', '', 'A', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。', 4, 1, '2021-12-19 00:14:38', '2021-12-19 00:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11279, '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短，因此不必保持安全车距。', '', '2', '1', NULL, '', '', '', '', 'B', '安装ABS的车辆紧急制动时，可用力踏制动踏板，但在紧急制动的同时转向，车辆还可能侧滑。另外，不要依赖ABS缩短制动距离，尤其是在冰雪路面上紧急制动时，ABS无法有效缩短制动距离。因此，无论什么情况下，都应当保持安全车距。', 4, 1, '2021-12-19 00:14:38', '2021-12-19 00:14:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11280, '安全气囊是一种什么装置？', '', '1', '1', NULL, '驾驶人头颈保护系统', '防抱死制动系统', '电子制动力分配系统', '辅助驾乘人员保护系统', 'D', '安全气囊是一种辅助驾乘人员保护系统，当汽车发生碰撞时，安全气囊迅速膨胀，在驾驶人、乘车人的正面形成一个气垫（安装有侧面安全气囊的，在驾乘人侧面也会形成气垫），从而降低人体受伤害的程度。', 4, 1, '2021-12-19 00:14:39', '2021-12-19 00:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11281, '正面安全气囊与什么配合才能充分发挥保护作用？', '/uploadFile/yuanBeiTu1281.gif', '1', '1', NULL, '座椅安全带', '防抱死制动系统', '座椅安全头枕', '安全玻璃', 'A', '安全气囊与安全带要配套使用，如果不系安全带，气囊引爆时会对驾乘人员头部和颈部造成严重伤害，这种伤害对儿童可能是致命的。', 4, 1, '2021-12-19 00:14:39', '2021-12-19 00:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11282, '下列哪个指示灯亮表示车辆在使用近光灯。', '/uploadFile/yuanBeiTu1282.gif', '1', '1', NULL, 'D图', 'C图', 'B图', 'A图', 'B', 'A图：前后位置灯（又称示廓灯）开启；B图：前后雾灯；C图：近光灯开启；D图：制动系统出现异常或故障。因此，C图指示灯亮表示车辆正在使用近光灯。【请注意】：图片的ABCD并不是对应选项的ABCD。', 4, 1, '2021-12-19 00:14:39', '2021-12-19 00:14:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11283, '下列哪个指示灯亮表示车辆在使用远光灯。', '/uploadFile/yuanBeiTu1283.gif', '1', '1', NULL, 'C图', 'D图', 'B图', 'A图', 'B', '如图所示，A图：驻车制动器处于制动状态；B图：前后雾灯；C图：车灯总开关；D图：开启远光灯。因此，D图指示灯亮表示车辆在使用远光灯。【请注意】：图片的ABCD并不是对应选项的ABCD。', 4, 1, '2021-12-19 00:14:40', '2021-12-19 00:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11284, '湿滑路面制动过程中，发现车辆偏离方向，以下做法正确的是？', '', '1', '1', NULL, '连续轻踩轻放制动踏板', '用力踩制动踏板', '不要踩制动踏板', '任意踩制动踏板', 'C', '湿滑路面，车轮的附着力随车速的增加急剧变小，当车辆发生侧滑或水滑方向偏离时，不要急转向或紧急制动，应松抬加速踏板，充分利用发动机制动减速。', 4, 1, '2021-12-19 00:14:40', '2021-12-19 00:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11285, '假如你驾车行驶在颠簸路段时，以下做法正确的是什么？', '', '1', '1', NULL, '稳住加速踏板', '挂低档位缓抬加速踏板', '挂高档位缓抬加速踏板', '挂低挡位踏满加速踏板', 'B', '行车中遇颠簸路段时，应挂低速挡缓抬加速踏板，充分利用发动机制动减速，降低凹凸不平路面带来的冲击，缓解车身震动，从而低速平稳地通过颠簸路段。', 4, 1, '2021-12-19 00:14:40', '2021-12-19 00:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11286, '行驶至这种上坡路段时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu1286.gif', '1', '1', NULL, '换低挡位，踏加速踏板', '换低挡位，松开加速踏板', '换高挡位，踏加速踏板', '换高档位，松开加速踏板', 'A', '驾驶机动车在山区上坡时，提前观察路况、坡道长度和坡度，在车速下降前减挡，以保证车辆具有充足的动力爬坡，避免爬坡中途减挡或车辆熄火。上陡坡时，要在坡底提前减挡，加速冲坡。驶近坡道顶端无法看清前方路口时，要减速慢行并鸣喇叭示意，不得加速冲过坡顶，防止对向有来车和其他意外情况。', 4, 1, '2021-12-19 00:14:40', '2021-12-19 00:14:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11287, '长下坡禁止挂空挡，下列原因错误的是？', '', '1', '1', NULL, '长下坡挂低速挡可以借助发动机控制车速', '避免因刹车失灵发生危险', '长下坡空挡滑行导致车速过高时，难以抢挂低速档控制车速', '下坡挂空挡，油耗容易增多', 'D', '因为长下坡空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效、转向失灵等极端情况，很容易引发严重的交通事故。空挡滑行不但不省油，会伤车，而且还不安全。 请注意，题目要求选出原因错误的选项，因此挂空挡，油耗增多说法错误。', 4, 1, '2021-12-19 00:14:41', '2021-12-19 00:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11288, '驾驶机动车下陡坡时，以下说法正确的是？', '', '1', '1', NULL, '可以熄火', '可以空挡但不准熄火', '可以空挡', '不准空挡或熄火', 'D', '驾驶机动车下陡坡行驶，要挂中低速档位，充分利用发动机制动来控制车速，严禁空挡滑行。 【拓展知识-空挡滑行不省油而且危险大】 空挡滑行时，一旦遇到突发情况紧急制动，没有发动机的制动作用不仅制动效果不好，而且容易使车辆失去平衡发生侧滑，甚至出现制动失效。转向失灵等极端情况，很容易引发严重的交通事故。', 4, 1, '2021-12-19 00:14:41', '2021-12-19 00:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11289, '下面哪种做法能帮助您避免被其他车辆从后方追撞？', '', '1', '1', NULL, '在任何时候都打开转向灯', '在转弯前提前打开相应的转向灯', '一直打开双闪', '转弯前鸣笛示意', 'B', '驾驶人要充分借助车辆照明及信号装置向其他交通参与者发出信号，表明自己的行驶意图和行驶状态，让其他车辆和行人及时地注意到自己。因此，当车辆转弯时，要提前开启相应的转向灯，可避免被其他车辆从后方追撞。', 4, 1, '2021-12-19 00:14:41', '2021-12-19 00:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11290, '夜间行驶，与对向车道车辆交会时，以下做法正确的是？', '/uploadFile/yuanBeiTu1290.gif', '1', '1', NULL, '保持使用远光灯', '远光灯与近光灯之间不断来回切换', '切换为近光灯', '关闭灯光', 'C', '夜间会车前，应当在距对向来车150米之外改用近光灯。', 4, 1, '2021-12-19 00:14:41', '2021-12-19 00:14:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11291, '以下安全带系法正确的是？', '/uploadFile/yuanBeiTu1291.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '系安全带时，先根据身高扳动安全带高度调整钮调整好高度，再用右手缓慢将安全带向下平顺拉出，使安全带从肩与颈根（锁骨下）之间，通过胸部的适当位置，将搭扣插头插入插座里，当听到“喀”的一声为止。然后，猛拉安全带，检查自动锁止是否安全有效。', 4, 1, '2021-12-19 00:14:42', '2021-12-19 00:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11292, '以下哪个仪表表示发动机转速表？', '/uploadFile/yuanBeiTu1292.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '转速表刻度不会超过两位数', 4, 1, '2021-12-19 00:14:42', '2021-12-19 00:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11293, '以下哪个仪表表示速度和里程表？', '/uploadFile/yuanBeiTu1293.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '速度和里程表的刻度标记一般超过两位数', 4, 1, '2021-12-19 00:14:42', '2021-12-19 00:14:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11294, '以下哪个仪表表示水温表？', '/uploadFile/yuanBeiTu1294.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'C', '如图所示：水温表（图3），指示发动机冷却液的温度，单位为摄氏度（℃）。“C”表示温度低，“H”表示温度高，水温表指针所指的位置显示当前冷却液的温度。', 4, 1, '2021-12-19 00:14:43', '2021-12-19 00:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11295, '以下哪个仪表表示燃油表？', '/uploadFile/yuanBeiTu1295.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '燃油表上有个加油站里的加油机的标志', 4, 1, '2021-12-19 00:14:43', '2021-12-19 00:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11296, '图中哪个报警灯亮，提示充电电路异常或故障？', '/uploadFile/yuanBeiTu1296.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '如图所示，各报警灯亮的含义如下：图1提示油箱内燃油已到最低液面；图2提示制动系统异常或故障；图3提示防抱死制动系统出现故障；图4提示充电电路故障或发电机不向蓄电池充电。有正负极＋－标志的，就表示有电。', 4, 1, '2021-12-19 00:14:43', '2021-12-19 00:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11297, '哪个报警灯亮，提示发动机控制系统异常或故障？', '/uploadFile/yuanBeiTu1297.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'C', '', 4, 1, '2021-12-19 00:14:43', '2021-12-19 00:14:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11298, '为提示车辆和行人注意，雾天必须开启哪个灯？', '/uploadFile/yuanBeiTu1298.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '', 4, 1, '2021-12-19 00:14:44', '2021-12-19 00:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11299, '图中左侧白色轿车，在这种情况下为了保证安全，应适当降低车速。', '/uploadFile/yuanBeiTu1299.gif', '2', '1', NULL, '', '', '', '', 'A', '如图所示，右侧限速标志指示该路段限速60公里/小时，而速度仪表指针指在数字60，随时都有超速风险。为了保证安全，应适当降低车速。题目描述无误，因此本题答案为对。', 4, 1, '2021-12-19 00:14:44', '2021-12-19 00:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11300, '以下哪个指示灯亮时，表示发动机机油压力过低？', '/uploadFile/yuanBeiTu1300.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '', 4, 1, '2021-12-19 00:14:44', '2021-12-19 00:14:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11301, '机油压力报警灯持续亮，可边行驶，边观察，等待报警灯自行熄灭。', '', '2', '1', NULL, '', '', '', '', 'B', '机油报警灯亮起，如果继续行驶，容易造成发动机损坏，影响行车安全', 4, 1, '2021-12-19 00:14:45', '2021-12-19 00:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11302, '以下哪个指示灯亮时，表示防抱死制动系统出现故障？', '/uploadFile/yuanBeiTu1302.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'C', '', 4, 1, '2021-12-19 00:14:45', '2021-12-19 00:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11303, '行车中下列哪个灯亮，提示驾驶人车辆制动系统出现异常?', '/uploadFile/yuanBeiTu1303.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'C', '', 4, 1, '2021-12-19 00:14:45', '2021-12-19 00:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11304, '行车中，制动报警灯亮，应试踩一下制动，只要有效可正常行车。', '', '2', '1', NULL, '', '', '', '', 'B', '行车中制动报警灯亮，说明制动系统出现异常或故障，不可继续行驶，应尽快选择安全地点靠边停车，对故障原因进行排查，必要时开到修理厂检修。', 4, 1, '2021-12-19 00:14:45', '2021-12-19 00:14:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11305, '以下哪个指示灯亮时，表示油箱内燃油已到最低液面？', '/uploadFile/yuanBeiTu1305.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '', 4, 1, '2021-12-19 00:14:46', '2021-12-19 00:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11306, '行车中，燃油报警灯亮，应及时到附近加油站加油，以免造成车辆乘员滞留公路，发生交通事故。', '', '2', '1', NULL, '', '', '', '', 'A', '行车中，燃油报警灯亮，提示油箱内燃油不足，需及时加油，保证车辆正常运行。', 4, 1, '2021-12-19 00:14:46', '2021-12-19 00:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11307, '以下哪个指示灯亮时，表示发动机温度过高？', '/uploadFile/yuanBeiTu1307.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'B', '图1：机油指示灯；图2：发动机温度过高；图3：防抱死制动系统出现故障；图4：油箱内燃油已到最低液面', 4, 1, '2021-12-19 00:14:46', '2021-12-19 00:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11308, '行车中水温报警灯亮，下列可能是其原因的是？', '', '1', '1', NULL, '缺少润滑油', '指示灯损坏', '缺少冷却液', '冷却液过多', 'C', '水温灯报警是水温过高的表现，可能是由于冷却液不足导致的，应及时去添加。', 4, 1, '2021-12-19 00:14:46', '2021-12-19 00:14:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11309, '以下哪个指示灯亮时，提醒驾驶人安全带插头未插入锁扣？', '/uploadFile/yuanBeiTu1309.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'A', '图1表示安全带插头未插入锁扣；图2是水温指示灯；图3是ABS指示灯；图4是燃油指示灯。', 4, 1, '2021-12-19 00:14:47', '2021-12-19 00:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11310, '车辆因故障等原因需被牵引时，以下说法正确的是什么？', '/uploadFile/yuanBeiTu1310.gif', '1', '1', NULL, '前后车均应打开报警灯', '所有车辆都应让行', '两车尽量快速行驶', '不受交通信号限制', 'A', '《道路交通安全法实施条例》第六十一条 牵引故障机动车应当遵守下列规定： (一)被牵引的机动车除驾驶人外不得载人，不得拖带挂车； (二)被牵引的机动车宽度不得大于牵引机动车的宽度； (三)使用软连接牵引装置时，牵引车与被牵引车之间的距离应当大于4米小于10米； (四)对制动失效的被牵引车，应当使用硬连接牵引装置牵引； (五)牵引车和被牵引车均应当开启危险报警闪光灯。', 4, 1, '2021-12-19 00:14:47', '2021-12-19 00:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11311, '车辆发生意外，要及时打开哪个灯?', '/uploadFile/yuanBeiTu1311.gif', '1', '1', NULL, '图1', '图2', '图3', '图4', 'D', '', 4, 1, '2021-12-19 00:14:47', '2021-12-19 00:14:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11312, '载货汽车牵引挂车时，下列做法正确的是________？', '', '1', '1', NULL, '可牵引2辆挂车', '可牵引3辆挂车', '挂车的载质量不得超过载货汽车本身的载质量', '允许牵引总质量超过本身载质量的挂车', 'C', '《交通安全法实施条例》中指出，载货汽车、半挂牵引车、拖拉机只允许牵引1辆挂车。载货汽车所牵引挂车的载质量不得超过载货汽车本身的载质量。', 5, 1, '2021-12-19 00:14:48', '2021-12-19 00:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11313, '重型、中型载货汽车，半挂车载物，高度从地面起不得超过多少米？', '', '1', '1', NULL, '4', '4.2', '5', '5.5', 'A', '《交通安全法实施条例》第五十四条,机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢，并应当遵守下列规定：(一)重型、中型载货汽车，半挂车载物，高度从地面起不得超过4米，载运集装箱的车辆不得超过4.2米。', 5, 1, '2021-12-19 00:14:48', '2021-12-19 00:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11314, '关于货运机动车载物，下列说法正确的是？', '', '1', '1', NULL, '可超载20%', '宽度可超过车身1米', '允许超载', '严禁超载', 'D', '只有D选项最和谐，任何车都严禁超载。', 5, 1, '2021-12-19 00:14:48', '2021-12-19 00:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11315, '机动车运载超限不可解体物品影响交通安全的，应当按照哪个部门指定的时间、路线、速度行驶，并悬挂明显标志？', '', '1', '1', NULL, '道路运输管理机构', '公安机关交通管理部门', '城市管理部门', '安全监督部门', 'B', '《中华人民共和国道路交通安全法》第四十八条第二款：机动车运载超限的不可解体的物品，影响交通安全的，应当按照公安机关交通管理部门指定的时间、路线、速度行驶，悬挂明显标志。在公路上运载超限的不可解体的物品，并应当依照公路法的规定执行。 所以这题选B', 5, 1, '2021-12-19 00:14:48', '2021-12-19 00:14:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11316, '机动车载运危险化学品，应当经哪个部门批准后，按指定的时间、路线、速度行驶，悬挂警示标志并采取必要的安全措施？', '', '1', '1', NULL, '公安机关', '道路运输管理机构', '城市管理部门', '环保部门', 'A', '根据《中华人民共和国道路交通安全法》，机动车载运爆炸物品、易燃易爆化学物品以及剧毒、放射性等危险物品，应当经公安机关批准后，按指定的时间、路线、速度行驶，悬挂警示标志并采取必要的安全措施。 注意：公安机关包括公安交通管理部门！交警也是公安机关的一部分！', 5, 1, '2021-12-19 00:14:49', '2021-12-19 00:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11317, '饮酒后驾驶营运机动车的，吊销机动车驾驶证，且多长时间内不得重新取得机动车驾驶证？', '', '1', '1', NULL, '1年', '2年', '5年', '10年', 'C', '《道路交通安全法》规定：饮酒后驾驶营运机动车的，处十五日拘留，并处五千元罚款，吊销机动车驾驶证，五年内不得重新取得机动车驾驶证。', 5, 1, '2021-12-19 00:14:49', '2021-12-19 00:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11318, '醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，并做怎样处理？', '', '1', '1', NULL, '暂扣机动车', '暂扣行驶证', '吊销机动车驾驶证', '终身禁驾', 'C', '违法行为：　　 处罚措施： 醉驾：　　　　 追究刑责，吊销驾照，5年禁驾 醉驾（营运车）： 追究刑责，吊销驾照，10年禁驾，重新考驾照后禁驾营运车 初次酒驾：　　 扣驾照6个月，罚款1000-2000元 再次酒驾：　　 吊销驾照，10天以下拘留，罚款1000-2000元 酒驾（营运车）： 拘留15天，罚款5000元，吊销驾照，5年禁驾', 5, 1, '2021-12-19 00:14:49', '2021-12-19 00:14:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11319, '货运机动车超过核定载质量，但没有超过核定载质量30%的，处多少罚款？', '', '1', '1', NULL, '100元以上200元以下', '200元以上500元以下', '500元以上1000元以下', '1000元以上', 'B', '没有超过核定载质量30%的，罚款200元以上500元以下，超过核定载质量30%的，罚款500元以上2000元以下。', 5, 1, '2021-12-19 00:14:50', '2021-12-19 00:14:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11320, '货运机动车超过核定载质量，但没有超过核定载质量的________，处200元以上500元以下罚款。', '', '1', '1', NULL, '15%', '20%', '25%', '30%', 'D', '超载不到30%处200--500的罚款，超过30%的处500--2000的罚款。', 5, 1, '2021-12-19 00:14:50', '2021-12-19 00:14:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11321, '货运机动车超过核定载质量30%或者违反规定载客的，处多少罚款？', '', '1', '1', NULL, '200元以上500元以下', '500元', '500元以上2000元以下', '2000元以上', 'C', '没超过30%处200以上500以下。超过30%处500以上2000以下。', 5, 1, '2021-12-19 00:14:50', '2021-12-19 00:14:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11322, '货运机动车超过核定载质量多少或者违反规定载客的，处500元以上2000元以下罚款？', '', '1', '1', NULL, '15%', '20%', '25%', '30%', 'D', '首先要看清题，记住超过30%或违法载人罚500-2000，超载未超过30%的罚200-500！', 5, 1, '2021-12-19 00:14:50', '2021-12-19 00:14:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11323, '载货汽车从注册登记之日起，多长时间以内每年检验1次？', '', '1', '1', NULL, '5年', '6年', '8年', '10年', 'D', '根据《道路交通安全法实施条例》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验： (二)载货汽车和大型、中型非营运载客汽车10年以内每年检验1次；超过10年的，每6个月检验1次。', 5, 1, '2021-12-19 00:14:51', '2021-12-19 00:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11324, '载货汽车从注册登记之日起，10年以内每年检验几次？', '', '1', '1', NULL, '1次', '2次', '3次', '4次', 'A', '十年内一年检验一次，十年后一年两次。', 5, 1, '2021-12-19 00:14:51', '2021-12-19 00:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11325, '载货汽车从注册登记之日起，超过10年的，每隔多长时间检验1次？', '', '1', '1', NULL, '6个月', '1年', '2年', '3年', 'A', '未超过10年的每一年检查一次，超过10年的每半年检查一次，车老了当然就检查的勤了！', 5, 1, '2021-12-19 00:14:51', '2021-12-19 00:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11326, '载货汽车从注册登记之日起，超过10年的，每年检验多少次？', '', '1', '1', NULL, '1', '2', '3', '4', 'B', '10年内是一次，超过10年是两次。', 5, 1, '2021-12-19 00:14:51', '2021-12-19 00:14:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11327, '载运集装箱的车辆高度从地面起不得超过多少米？', '', '1', '1', NULL, '5.5', '5', '4.5', '4.2', 'D', '《道路交通安全法实施条例》第五十四条规定，机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢，并应当遵守下列规定： (一)重型、中型载货汽车，半挂车载物，高度从地面起不得超过4米，载运集装箱的车辆不得超过4.2米。', 5, 1, '2021-12-19 00:14:52', '2021-12-19 00:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11328, '在城市道路上，货运机动车在留有安全位置的情况下，车厢内可以附载临时作业人员1人至几人？', '', '1', '1', NULL, '5', '6', '7', '8', 'A', '《中华人民共和国道路交通安全法实施条例》第五十五条 机动车载人应当遵守下列规定： （二）载货汽车车厢不得载客。在城市道路上，货运机动车在留有安全位置的情况下，车厢内可以附载临时作业人员1人至5人；载物高度超过车厢栏板时，货物上不得载人。', 5, 1, '2021-12-19 00:14:52', '2021-12-19 00:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11329, '在城市道路上，载货汽车载物高度________时，货物上不得载人。', '', '1', '1', NULL, '超过2米', '超过3米', '超过4米', '超过车厢栏板', 'D', '根据《道路交通安全法》第五十五条 机动车载人应当遵守下列规定： （二）载货汽车车厢不得载客。在城市道路上，货运机动车在留有安全位置的情况下，车厢内可以附载临时作业人员1人至5人；载物高度超过车厢栏板时，货物上不得载人！', 5, 1, '2021-12-19 00:14:52', '2021-12-19 00:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11330, '下列关于载货汽车、半挂牵引车、拖拉机牵引挂车的说法正确的是？', '', '1', '1', NULL, '不得牵引', '只允许牵引1辆', '允许牵引2辆', '允许牵引3辆', 'B', '《道路交通安全法实施条例》第五十六条：载货汽车、半挂牵引车、拖拉机只允许牵引一辆挂车。根据规定，本题答案选B。', 5, 1, '2021-12-19 00:14:52', '2021-12-19 00:14:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11331, '在高速公路上行驶的载货汽车最高车速不得超过每小时多少公里？', '', '1', '1', NULL, '60', '80', '100', '120', 'C', '高速公路上行驶的载货汽车最高车速不得超过每小时100公里。 《道路交通安全法实施条例》第七十八条 高速公路应当标明车道的行驶速度，最高车速不得超过每小时120公里，最低车速不得低于每小时60公里。高速公路上行驶的小型载客汽车最高车速不得超过每小时120公里，其他机动车不得超过每小时100公里，摩托车不得超过每小时80公里。同方向有2条车道的，左侧车道的最低车速为每小时100公里；同方向有3条以上车道的，最左侧车道的最低车速为每小时110公里，中间车道的最低车速为每小时90公里。道路限速标志标明的车速与上述车道行驶车速的规定不一致的，按照道路限速标志标明的车速行驶。', 5, 1, '2021-12-19 00:14:53', '2021-12-19 00:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11332, '机动车载运超限物品行经铁路道口时，应当按照当地哪个部门规定的铁路道口、时间通过？', '', '1', '1', NULL, '交通部门', '公安部门', '运输部门', '铁路部门', 'D', '铁路道口属于铁路部管理，周边的交通，协调应该是地方交警大队', 5, 1, '2021-12-19 00:14:53', '2021-12-19 00:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11333, '申请增加哪些准驾车型的，不得有在造成人员死亡的交通事故中承担同等以上责任的记录？', '', '1', '1', NULL, '大型客车、牵引车、中型客车', '小型汽车、小型自动挡汽车', '普通三轮摩托车、普通二轮摩托车、轻便摩托车', '低速载货汽车、三轮汽车', 'A', '这个题目，只要认真想一下，哪些驾照持有人容易出事，还有申请大型客车驾照的人一般都是驾驶技术过关的，所以这个题目可以从驾照方面考虑，所以选择A!', 5, 1, '2021-12-19 00:14:53', '2021-12-19 00:14:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11334, '图中标志的含义是什么？', '/uploadFile/yuanBeiTu1334.gif', '1', '1', NULL, '载货汽车驶入', '禁止载客汽车驶入', '禁止载货汽车驶入', '禁止机动车驶入', 'C', '红色代表禁止，图上的车为载货汽车，所以是禁止载货汽车驶入。', 5, 1, '2021-12-19 00:14:54', '2021-12-19 00:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11335, '图中标志的含义是什么？', '/uploadFile/yuanBeiTu1335.gif', '1', '1', NULL, '汽车拖、挂车驶入', '禁止机动车驶入', '禁止载货汽车驶入', '禁止汽车拖、挂车驶入', 'D', '如图所示，图中白底红圈红杠黑图形表示禁令标志，指示前方路段禁止拖、挂车驶入。', 5, 1, '2021-12-19 00:14:54', '2021-12-19 00:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11336, '图中标志的含义是什么？', '/uploadFile/yuanBeiTu1336.gif', '1', '1', NULL, '限制质量', '限制轴重', '限制速度', '限制长度', 'A', '物理里面，质量就是指重量 。所以该题表示禁止总质量超过标志所示数值的车辆通行。此标志设在需要限制车辆质量的桥梁两端。以图为例：装载总质量不得超过10t。', 5, 1, '2021-12-19 00:14:54', '2021-12-19 00:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11337, '图中标志的含义是什么？', '/uploadFile/yuanBeiTu1337.gif', '1', '1', NULL, '禁止机动车驶入', '禁止运输危险品车辆驶入', '禁止载货汽车驶入', '禁止小型车辆驶入', 'B', '看到车上爆炸的图形，就是禁止运输危险品车辆驶入。', 5, 1, '2021-12-19 00:14:54', '2021-12-19 00:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11338, '连接半挂车时，使牵引车的牵引座与挂车的牵引销连接后，将锁止杆置于哪个位置？', '', '1', '1', NULL, '锁止', '松开', '紧固', '断开', 'A', '本题锁止杆就是锁止用的。', 5, 1, '2021-12-19 00:14:54', '2021-12-19 00:14:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11339, '分离半挂车时，先降下挂车支撑架，然后断开________，开启牵引座锁止机构，将牵引车驶离挂车？', '', '1', '1', NULL, '保险绳和拉簧', '制动管路接头和灯用电缆插头', '锁止臂', '牵引销', 'B', '前面都断下了支撑架，后面又开启了锁止机构，所以只有断开制动和电缆。因为挂车时只有锁止和连接电缆。', 5, 1, '2021-12-19 00:14:55', '2021-12-19 00:14:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11340, '连接全挂车时，将牵引车的牵引钩与挂车挂钩连接好，并将牵引钩锁止好，连接什么？', '', '1', '1', NULL, '防护网', '导线', '缓冲弹簧', '制动管路接头、灯用电缆插头等', 'D', '如果不连制动管路接头，后挂车无法制动；如果不连灯用电缆插头，后挂车无灯光。所以选D！', 5, 1, '2021-12-19 00:14:55', '2021-12-19 00:14:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11341, '寒冷状态下起动柴油车时，应怎样做？', '', '1', '1', NULL, '直接起动', '将点火开关钥匙置于Ⅱ档即可起动', '先将点火开关钥匙置于ON上预热，预热灯熄灭后再起动', '将点火开关钥匙置于Ⅲ档即可起动', 'C', '在冬天寒冷的季节，发动机经过一夜的静置，各摩擦面上的润滑油基本消失，失去油膜保护；低温使润滑油的黏度大大增加，附着力和流动性变差。这个时候，启动发动机的运动阻力大大增加，启动时超过60％的功率都用于克服运动阻力。因此，冷机时爱车需要预热。但是热车的时间并非越长越好。长时间热车，发动机做无用功、浪费燃料还不利于环保。一般情况下，中低档车以能够正常怠速运转，水温70℃－80℃为宜。预热灯熄灭后再起动。', 5, 1, '2021-12-19 00:14:55', '2021-12-19 00:14:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11342, '车辆下长坡过程中，当制动鼓温度过高时，应怎样做？', '', '1', '1', NULL, '要尽快进入水中冷却', '不要立即进入水中冷却', '要立即浇水冷却', '不用理会', 'B', '车辆制动鼓过热，也常被人称作：制动鼓发热、刹车锅发烫、制动鼓温度过高、车轱辘发烫锅子温度高、轮毂发烫等。车辆制动鼓过热时，千万不要向制动鼓上泼水降温，制动鼓骤然冷却，收缩不匀，极易造成开裂。待车辆刹车制动鼓温度降下来后，将车辆慢慢开到服务站检查。', 5, 1, '2021-12-19 00:14:55', '2021-12-19 00:14:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11343, '大型车辆起步前除要观察后视镜以外，还应怎样做，以看清前挡风玻璃前下方长1.5米、宽3米范围内的情况？', '', '1', '1', NULL, '站起来观察前下方', '下车观察前下方', '观察前下视镜', '伸出头观察前下方', 'C', '所谓前下视镜就是后视镜下面有一个小镜子对着车头前下方的镜子。通过观察，可以了解前下方的情况，避免因视野盲区而起步出状况。', 5, 1, '2021-12-19 00:14:56', '2021-12-19 00:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11344, '汽车列车换档减档时机，要比单车怎样？', '', '1', '1', NULL, '滞后', '提前', '一样', '有时滞后，有时提前', 'B', '减档要早，因为列车惯性大，所以要及时及早减档，希望帮到你。', 5, 1, '2021-12-19 00:14:56', '2021-12-19 00:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11345, '汽车列车的车身越长，转弯半径会怎样？', '', '1', '1', NULL, '可能变大，也可能变小', '越小', '不变', '越大', 'D', '汽车转弯半径的大小由汽车前后轴的轴距决定。轴距大转弯半径就大。', 5, 1, '2021-12-19 00:14:56', '2021-12-19 00:14:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11346, '汽车的车身越高，转弯时的稳定性会怎样？', '', '1', '1', NULL, '可能变差，也可能变好', '越差', '不变', '越好', 'B', '离心力问题，车身越高，重心越高，转弯时由于惯性稳定性会越差一些，因此要减速慢行。', 5, 1, '2021-12-19 00:37:29', '2021-12-19 00:37:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11347, '汽车列车急转弯时，无论向左或向右，都应降低车速，低速沿车道的哪个位置通过？', '', '1', '1', NULL, '内侧', '中间', '外侧', '任意一侧', 'C', '因为汽车列车都比较长，转弯占道也比较多，所以要沿道路外侧，并随时做好让车准备。', 5, 1, '2021-12-19 00:37:29', '2021-12-19 00:37:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11348, '汽车列车转弯时，牵引车的尾部或挂车部分往往要借用对方车道。因此，驾驶汽车列车在转弯过程中要注意什么？', '', '1', '1', NULL, '做好让车准备', '提前占道', '靠内侧行驶', '连续鸣喇叭', 'A', '注意题目中的“往往要借用对方车道”，因此在转弯时一定要注意避开对向车道的车辆，并要随时做好让车准备，便于在对向有来车时不造成道路拥堵。', 5, 1, '2021-12-19 00:37:29', '2021-12-19 00:37:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11349, '全挂车倒车，要避免牵引车与挂车形成怎样的角度？', '', '1', '1', NULL, '较大的角度', '较小的角度', '直线', '直角', 'B', '应该是避免形成较小角度，因为小角度的话不但不容易预见到可能发生的状况，同时由于角度小，因此稳定性不佳。', 5, 1, '2021-12-19 00:37:30', '2021-12-19 00:37:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11350, '准驾车型为大型货车的，可以驾驶以下哪种车型？', '', '1', '1', NULL, '中型客车.', '两轮摩托车', '小型汽车', '无轨电车', 'C', '准驾车型为大型货车的就是B2驾驶证，持有B2驾驶证按规定的准驾型车型代号为C1型小汽车、C2小型自动挡汽车、C3低速载货汽车。', 5, 1, '2021-12-19 00:37:30', '2021-12-19 00:37:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11351, '图中标志的含义是限制轴重。', '/uploadFile/yuanBeiTu1351.gif', '2', '1', NULL, '', '', '', '', 'B', '在全套禁令标志中,这个标志表示“限制质量”，如果是在C1的题中，应该选择“限制总质量10吨”如果是在大车题中应选择“限制质量”。', 5, 1, '2021-12-19 00:37:30', '2021-12-19 00:37:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11352, '图中标志设在禁止运输危险品车辆驶入路段的入口处。', '/uploadFile/yuanBeiTu1352.gif', '2', '1', NULL, '', '', '', '', 'A', '【禁令标志】禁止运输危险物品车辆驶入：表示前方路段禁止运输危险物品车辆驶入。设置在禁止运输危险品车辆驶入路段的入口处。', 5, 1, '2021-12-19 00:37:30', '2021-12-19 00:37:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11353, '机动车载运危险化学品，经公安部门批准后，行驶时间、路线可不受限制。', '', '2', '1', NULL, '', '', '', '', 'B', '经公安部门批准后，行驶时间、路线都要受到限制的，因为你装着危险品。', 5, 1, '2021-12-19 00:37:31', '2021-12-19 00:37:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11354, '禁止货运机动车载客。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十条规定，禁止货运机动车载客；货运机动车需要附载作业人员的，应当设置保护作业人员的安全措施。', 5, 1, '2021-12-19 00:37:31', '2021-12-19 00:37:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11355, '货运机动车需要附载作业人员的，应当设置保护作业人员的安全措施。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十条规定，禁止货运机动车载客；货运机动车需要附载作业人员的，应当设置保护作业人员的安全措施。', 5, 1, '2021-12-19 00:37:31', '2021-12-19 00:37:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11356, '全挂拖斗车不得进入高速公路。', '', '2', '1', NULL, '', '', '', '', 'A', '《 中华人民共和国道路交通安全法》第六十七条 行人、非机动车、拖拉机、轮式专用机械车、铰接式客车、全挂拖斗车以及其他设计最高时速低于七十公里的机动车，不得进入高速公路。', 5, 1, '2021-12-19 00:37:31', '2021-12-19 00:37:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11357, '营运机动车在规定检验期限内经安全技术检验合格的，不再重复进行安全技术检验。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第十六条：营运机动车在规定检验期限内经安全技术检验合格的，不再重复进行安全技术检验。', 5, 1, '2021-12-19 00:37:32', '2021-12-19 00:37:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11358, '机动车驾驶人在实习期内不得驾驶载有危险化学品的机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十二条: 机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 5, 1, '2021-12-19 00:37:32', '2021-12-19 00:37:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11359, '驾驶人实习期内可以单独驾驶大型客车、电车、起重车、牵引车。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法实施条例》第二十二条 机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 5, 1, '2021-12-19 00:37:32', '2021-12-19 00:37:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11360, '机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度超出车厢不得超过1米。', '', '2', '1', NULL, '', '', '', '', 'B', '《交通安全法实施条例》第五十四条：机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢。 所以就是超出1cm都不可以！', 5, 1, '2021-12-19 00:37:32', '2021-12-19 00:37:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11361, '挂车车厢内在留有安全位置的情况下，可以附载临时作业人员1人至5人。', '', '2', '1', NULL, '', '', '', '', 'B', '挂车车厢内在留有安全位置的情况下，可以附载临时作业人员1人至5人。错! 货车车厢内在留有安全位置的情况下，可以附载临时作业人员1人至5人。对! 一字之差!“挂”与“货”', 5, 1, '2021-12-19 00:37:33', '2021-12-19 00:37:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11362, '在高速公路上行驶的载货汽车车厢内不得载人。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法实施条例》第八十三条：在高速公路上行驶的载货汽车车厢不得载人。 这个问题区别在于公路和高速公路。在普通公路上有安全空位,可以临时随车作业,但上了高速公路就不是临时随车作业的性质了。所以，请区分公路和高速公路。', 5, 1, '2021-12-19 00:37:33', '2021-12-19 00:37:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11363, '货运机动车装载超过核定质量的，公安机关交通管理部门应当扣留机动车，直至消除违法状态。', '', '2', '1', NULL, '', '', '', '', 'A', '货运机动车超载处罚规定 （一）货运机动车超过核定载质量的，处二百元以上五百元以下罚款。 （二）超过核定载质量百分之三十或者违反规定载客的，处五百元以上二千元以下罚款，并由公安机关交通管理部门扣留机动车至违法状态消除。 （三）运输单位的车辆经处罚不改的，对直接负责的主管人员处二千元以上五千元以下罚款。 所以此题是正确的。', 5, 1, '2021-12-19 00:37:33', '2021-12-19 00:37:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11364, '已持有大型货车驾驶证5年以上，并在申请前最近连续3个记分周期内没有满分记录，可以申请增加大型客车准驾车型。', '', '2', '1', NULL, '', '', '', '', 'B', '1、根据公安部交通管理局发布的新措施，自2020年11月20日起，缩短增驾时间间隔，对无相应记分周期满分记录，申请大型客车驾驶证的，由取得大型货车驾驶证至少5年缩短至3年。2、《机动车驾驶证申领和使用规定》应在申请前最近连续五个记分周期内没有记满12分记录。', 5, 1, '2021-12-19 00:37:33', '2021-12-19 00:37:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11365, '已持有大型货车驾驶证2年以上，并在申请前最近连续3个记分周期内没有满分记录，可以申请增加牵引车准驾车型。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》根据公安部交通管理局发布的新措施，自2020年11月20日起，缩短增驾时间间隔，对无相应记分周期满分记录，申请增加牵引车准驾车型的，由取得大型货车驾驶证至少3年缩短至2年，或者取得驾驶大型客车准驾车型资格一年以上，并在申请前最近连续三个记分周期内没有记满12分记录。', 5, 1, '2021-12-19 00:37:33', '2021-12-19 00:37:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11366, '报考大型货车准驾车型科目三考试的，在取得驾驶技能准考证明满30日后预约。', '', '2', '1', NULL, '', '', '', '', 'A', '根据公安部推出的“我为群众办实事”公安交管12项便利措施实施内容，自2021年6月1日起，优化驾驶证考试内容和程序—调整考试预约时间，对申请小型自动挡汽车驾驶证的，科目三约考间隔时间由科目一考试合格后30日调整为20日；增驾大中型客货车驾驶证的，间隔时间由40日调整为30日，减少待考时间。', 5, 1, '2021-12-19 00:37:34', '2021-12-19 00:37:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11367, '报考牵引车准驾车型科目三考试的，在取得驾驶技能准考证明满40日后预约。', '', '2', '1', NULL, '', '', '', '', 'A', '根据《机动车驾驶证申领和使用规定》第三十四条:初次申请机动车驾驶证或者申请增加准驾车型的，申请人预约考试科目三，应当符合下列规定： （三）报考大型客车、牵引车、城市公交车、中型客车、大型货车准驾车型的，在取得驾驶技能准考证明满四十日后预约考试。 备注：2021年6月新规中未对报考牵引车准驾车型科目三约考间隔时间进行调整。 “调整考试预约时间，对申请小型自动挡汽车驾驶证的，科目三约考间隔时间由科目一考试合格后30日调整为20日，增驾大中型客货车驾驶证的，间隔时间由40日调整为30日，减少待考时间；”', 5, 1, '2021-12-19 00:37:34', '2021-12-19 00:37:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11368, '年龄在60岁以上持有准驾车型为大型货车的驾驶人，应当到驾驶证核发地车辆管理所换领准驾车型为小型汽车或小型自动挡汽车的驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《驾驶证申领和使用规定》第五十九条 年龄在60周岁以上的，不得驾驶大型客车、牵引车、城市公交车、中型客车、大型货车、无轨电车和有轨电车；持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的，应当到机动车驾驶证核发地或者核发地以外的车辆管理所换领准驾车型为小型汽车或者小型自动挡汽车的机动车驾驶证。', 5, 1, '2021-12-19 00:37:34', '2021-12-19 00:37:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11369, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，在一个记分周期内有记满12分记录的，车辆管理所应当注销其最高准驾车型驾驶资格，并通知机动车驾驶人在三十日内办理降级换证业务。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第六十八条规定： 持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人有下列情形之一的， 车辆管理所应当注销其最高准驾车型驾驶资格，并通知机动车驾驶人在三十日内办理降级换证业务： （二）在一个记分周期内有记满12分记录的。', 5, 1, '2021-12-19 00:37:34', '2021-12-19 00:37:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11370, '牵引车拖带挂车时，挂车必须装有有效的制动装置。', '', '2', '1', NULL, '', '', '', '', 'A', '如果牵引车急刹车，而被牵引车没有有效的制动刹车装置，那么被牵引车会靠惯性继续追尾牵引车，所以挂车必须装有有效的制动装置。', 5, 1, '2021-12-19 00:37:35', '2021-12-19 00:37:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11371, '半挂车倒车时转向盘转动方向与单车倒车方向相反。', '', '2', '1', NULL, '', '', '', '', 'A', '因为半挂车“车箱”只是后侧有车轮（全挂车箱前后都有车轮），前侧无车轮（挂车驶离时需放下前方支撑架），因此当半挂车倒车时如果向左转向，牵引车后轮也向左行驶，此时半挂车车箱前侧也向左偏移，车箱前方向左的话箱尾向右，倒车时车体肯定向右，所以此时倒车的方向肯定是往右。大家可以想象一下“Z”字！！！', 5, 1, '2021-12-19 00:37:35', '2021-12-19 00:37:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11372, '全挂车倒车时转向盘转动方向与单车倒车方向相反。', '', '2', '1', NULL, '', '', '', '', 'B', '全挂跟半挂不同的是，因为全挂的车箱是和车头一起的。而半挂的车头和车箱是分离的，所以全挂倒车是跟单车一样的转向盘转动方向与单车倒车方向相同。', 5, 1, '2021-12-19 00:37:35', '2021-12-19 00:37:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11373, '道路较窄时，车辆转弯前应该注意摆位“借量”，右转需向左摆位。', '', '2', '1', NULL, '', '', '', '', 'A', '右转需向左摆位，目的是为了把车尾移到左侧，能顺利右拐。', 5, 1, '2021-12-19 00:37:35', '2021-12-19 00:37:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11374, '汽车列车转直角弯时，需先判断弯道情况，减速或停车后重新起步，缓慢通过。', '', '2', '1', NULL, '', '', '', '', 'A', '遵循“一停二看三通过”的原则，安全第一。', 5, 1, '2021-12-19 00:37:36', '2021-12-19 00:37:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11375, '汽车列车的车身越长，转弯半径越小。', '', '2', '1', NULL, '', '', '', '', 'B', '汽车列车的车身越长，转弯半径越大。和圆规画圆性质一样，圆规两只脚距离越长所画出半圆半径越大 。', 5, 1, '2021-12-19 00:37:36', '2021-12-19 00:37:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11376, '汽车装载的货物越高，转弯时稳定性越差。', '', '2', '1', NULL, '', '', '', '', 'A', '装货越高，重心就越高，如果有横风，就更容易晃动，稳定性差，容易侧翻。', 5, 1, '2021-12-19 00:37:36', '2021-12-19 00:37:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11377, '汽车列车在转弯过程中，要注意提前做好让车准备。', '', '2', '1', NULL, '', '', '', '', 'A', '由于汽车列车较长，因此在转弯时不可避免地会占用到其他车道，因此在转弯时一定要注意避开对向车道的车辆，并要随时做好让车准备，便于在对向有来车时不造成道路拥堵。', 5, 1, '2021-12-19 00:37:36', '2021-12-19 00:37:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11378, '全挂列车倒车，要避免牵引车与挂车形成较大的角度。', '', '2', '1', NULL, '', '', '', '', 'B', '当牵引车倒车应该与挂车形成较大的角度。为了安全起见，应该避免小角度倒车。', 5, 1, '2021-12-19 00:37:37', '2021-12-19 00:37:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11379, '车辆下长坡过程中，遇制动鼓温度过高时，不要立即进入冷水中冷却，以免造成制动鼓损坏。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆下长坡过程中，一般使用低档位控制车速，当制动鼓温度太高后，可能会有刹车失灵的的危险，先尝试一下是否车辆还能刹住，如果能刹住，应降低车速，大坡过后，路边停车，自然冷却后再上路，千万不可立即进入冷水中冷却，以免造成制动鼓的损坏。', 5, 1, '2021-12-19 00:37:37', '2021-12-19 00:37:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11380, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在联系电话、联系地址以及从业单位等信息发生变化的，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第八十条，机动车驾驶人联系电话、联系地址等信息发生变化，以及持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人从业单位等信息发生变化的，应当在信息变更后三十日内，向驾驶证核发地车辆管理所备案。', 5, 1, '2021-12-19 00:37:37', '2021-12-19 00:37:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11381, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，在记分周期内有记分的，应当在记分周期结束后三十日内到公安机关交通管理部门接受审验。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十条：持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，应当在每个记分周期结束后三十日内到公安机关交通管理部门接受审验。但在一个记分周期内没有记分记录的，免予本记分周期审验。', 5, 1, '2021-12-19 00:37:37', '2021-12-19 00:37:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11382, '持有大型货车驾驶证的驾驶人在一个记分周期内有记满12分记录的，车辆管理所应当注销其最高准驾车型驾驶资格。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十八条：持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人有下列情形之一的，车辆管理所应当注销其最高准驾车型驾驶资格，并通知机动车驾驶人在三十日内办理降级换证业务：（一）发生交通事故造成人员死亡，承担同等以上责任，未构成犯罪的；（二）在一个记分周期内有记满12分记录的；（三）连续三个记分周期不参加审验的。', 5, 1, '2021-12-19 00:37:38', '2021-12-19 00:37:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11383, '下列关于大型、中型载客汽车能否牵引挂车的说法，正确的是什么？', '', '1', '1', NULL, '不得牵引挂车', '允许牵引1辆挂车', '可以牵引不超过本身载质量的挂车', '允许牵引总质量700千克以下的挂车', 'A', '客车是禁止拖挂车辆的，因为客车的用途是载客，要保证旅客的安全为本。', 6, 1, '2021-12-19 00:37:38', '2021-12-19 00:37:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11384, '饮酒后驾驶营运机动车的，处十五日拘留，并处多少罚款？', '', '1', '1', NULL, '2000元', '5000元', '10000元', '20000元', 'B', '《道路交通安全法》第九十一条：饮酒后驾驶营运机动车的，处15日拘留，并处5000元罚款，吊销机动车驾驶证，5年内不得重新取得机动车驾驶证。', 6, 1, '2021-12-19 00:37:38', '2021-12-19 00:37:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11385, '醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，多长时间内不得重新取得机动车驾驶证，并依法追究刑事责任；重新取得机动车驾驶证后，不得驾驶营运机动车？', '', '1', '1', NULL, '1年', '2年', '5年', '10年', 'D', '因饮酒后驾驶营运机动车或者“醉酒驾驶机动车”依法被吊销驾驶证的，5年内不得重新申领；因“醉酒驾驶营运机动车”依法被吊销驾驶证的，10年内不得重新申领。', 6, 1, '2021-12-19 00:37:38', '2021-12-19 00:37:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11386, '因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处多少罚款，吊销机动车驾驶证。', '', '1', '1', NULL, '2000元', '500元', '200元', '1000元以上2000元以下', 'D', '《道路交通安全法》第九十一条： 饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。', 6, 1, '2021-12-19 00:37:38', '2021-12-19 00:37:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11387, '公路客运车辆载客超过额定乘员，但没有超过额定成员20%的，处多少罚款？', '', '1', '1', NULL, '100以上200元以下', '200元以上500元以下', '500元以上1000元以下', '1000元以上', 'B', '《道路交通安全法》第九十二条：公路客运车辆载客超过额定乘员的，处200元以上500元以下的罚款；超过额定乘员20%或者违反规定载货的，处500元以上2000元以下的罚款。', 6, 1, '2021-12-19 00:37:39', '2021-12-19 00:37:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11388, '公路客运车辆载客超过额定成员20%或者违反规定载货的，处多少罚款？', '', '1', '1', NULL, '200元以上500元以下', '500元', '500元以上2000元以下', '2000元以上', 'C', '《道路交通安全法》第九十二条： 公路客运车辆载客超过额定乘员的，处二百元以上五百元以下罚款;超过额定乘员百分之二十或者违反规定载货的，处五百元以上二千元以下罚款。', 6, 1, '2021-12-19 00:37:39', '2021-12-19 00:37:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11389, '连续驾驶大型载客汽车超过4小时未停车休息，按照规定将被公安机关交通管理部门给予一次记多少分？', '', '1', '1', NULL, '3分', '6分', '12分', '2分', 'C', '机动车驾驶人有下列违法行为之一，一次记12分： （一）驾驶与准驾车型不符的机动车的； （二）饮酒后驾驶机动车的； （三）驾驶营运客车（不包括公共汽车）、校车载人超过核定人数20％以上的； （四）造成交通事故后逃逸，尚不构成犯罪的； （五）上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的； （六）使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的； （七）驾驶机动车在高速公路上倒车、逆行、穿越中央分隔带掉头的； （八）驾驶营运客车在高速公路车道内停车的； （九）驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的； （十）连续驾驶中型以上载客汽车、危险物品运输车辆超过4小时未停车休息或者停车休息时间少于20分钟的； （十一）未取得校车驾驶资格驾驶校车的。', 6, 1, '2021-12-19 00:37:39', '2021-12-19 00:37:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11390, '客运驾驶人24小时内累计驾驶时间原则上不超过多少小时？', '', '1', '1', NULL, '6', '8', '10', '12', 'B', '客运驾驶人在24小时内累计驾驶时间不得超过8小时，连续驾驶时间不得超过4小时，每次停车休息时间不少于20分钟。', 6, 1, '2021-12-19 00:37:39', '2021-12-19 00:37:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11391, '大型、中型非营运载客汽车从注册登记之日起，多长时间以内每年检验1次？', '', '1', '1', NULL, '5年', '6年', '8年', '10年', 'D', '大型、中型非营运载客汽车的10年以内每年检验1次，超过10年的每年两次！牢记。', 6, 1, '2021-12-19 00:37:40', '2021-12-19 00:37:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11392, '大型、中型非营运载客汽车从注册登记之日起，超过10年的，每多长时间检验1次？', '', '1', '1', NULL, '6个月', '1年', '2年', '3年', 'A', '《道路交通安全法》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验：(二)载货汽车和大型、中型非营运载客汽车10年以内每年检验1次；超过10年的，每6个月检验1次。', 6, 1, '2021-12-19 00:37:40', '2021-12-19 00:37:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11393, '营运载客汽车从注册登记之日起，多长时间以内每年检验1次？', '', '1', '1', NULL, '2年', '3年', '4年', '5年', 'D', '《道路交通安全法》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验：(一)营运载客汽车5年以内每年检验1次；超过5年的，每6个月检验1次。', 6, 1, '2021-12-19 00:37:40', '2021-12-19 00:37:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11394, '营运载客汽车从注册登记之日起，超过5年的，每多长时间检验1次？', '', '1', '1', NULL, '6个月', '1年', '2年', '3年', 'A', '《道路交通安全法》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验：(一)营运载客汽车5年以内每年检验1次；超过5年的，每6个月检验1次。', 6, 1, '2021-12-19 00:37:40', '2021-12-19 00:37:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11395, '载客汽车行李架载货时，从地面起高度不得超过多少米？', '', '1', '1', NULL, '4', '4.2', '5', '5.5', 'A', '《交通安全法实施条例》第五十四条 机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢，并应当遵守下列规定： 载客汽车除车身外部的行李架和内置的行李箱外，不得载货。载客汽车行李架载货，从车顶起高度不得超过0.5米，从地面起高度不得超过4米。', 6, 1, '2021-12-19 00:37:41', '2021-12-19 00:37:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11396, '公路客运车辆载客超过核定乘员，公安机关交通管理部门依法扣留机动车后，驾驶人应当将超载的乘车人转运，费用由谁承担？', '', '1', '1', NULL, '乘客', '超载机动车的驾驶人或者所有人', '公安机关交通管理部门', '转运机动车的驾驶人或者所有人', 'B', '《交通安全法实施条例》第一百零六条：公路客运载客汽车超过核定乘员、载货汽车超过核定载质量的，公安机关交通管理部门依法扣留机动车后，驾驶人应当将超载的乘车人转运、将超载的货物卸载，费用由超载机动车的驾驶人或者所有人承担。', 6, 1, '2021-12-19 00:37:41', '2021-12-19 00:37:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11397, '已取得驾驶中型客车或者大型货车准驾车型资格多少年以上，可以申请增加大型客车准驾车型？', '', '1', '1', NULL, '2年', '3年', '4年', '5年', 'B', '根据公安部交通管理局发布的新措施，自2020年11月20日起，缩短增驾时间间隔，对无相应记分周期满分记录，申请大型客车驾驶证的，由取得大型货车驾驶证至少5年缩短至3年，申请牵引车和中型客车驾驶证的，由取得大型货车驾驶证至少3年缩短至2年。', 6, 1, '2021-12-19 00:37:41', '2021-12-19 00:37:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11398, '持有中型客车或者大型货车驾驶证，申请增加大型客车准驾车型的，应当在申请前最近连续几个记分周期内没有满分记录？', '', '1', '1', NULL, '2个', '3个', '4个', '5个', 'B', '【优化大型客货车驾驶证申领条件】：根据公安部交通管理局发布的新措施，自2020年11月20日起，缩短增驾时间间隔，申请大型客车驾驶证的，由取得大型货车驾驶证至少5年缩短至3年，并在申请前最近连续3个记分周期内没有记满12分记录 。', 6, 1, '2021-12-19 00:37:42', '2021-12-19 00:37:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11399, '年龄在70周岁以上，在1个记分周期结束后，多长时间内未提交身体检查结果的，车辆管理所注销其驾驶证。', '', '1', '1', NULL, '3年', '4年', '1年', '2年', 'C', '修改后的《机动车驾驶证申领和使用规定》(公安部令第139号)中第三十条规定：年龄在70周岁以上的机动车驾驶人，应当每年进行一次身体检查，在记分周期结束后三十日内，提交县级或者部队团级以上医疗机构出具的有关身体条件的证明。在1个记分周期结束后，一年内未提交身体检查结果的，车辆管理所注销其驾驶证。', 6, 1, '2021-12-19 00:37:42', '2021-12-19 00:37:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11400, '申请增加哪些准驾车型的，不得有在造成人员死亡的交通事故中承担同等以上责任的记录？', '', '1', '1', NULL, '小型汽车、小型自动挡汽车', '大型客车、牵引车、中型客车', '普通三轮摩托车、普通二轮摩托车、轻便摩托车', '低速载货汽车、三轮汽车', 'B', '这个题目，只要认真想一下，哪些驾照持有人容易出事。还有申请大型客车驾照的人一般都是驾驶技术过关的，所以这个题目可以从驾照方面考虑，所以选择B!', 6, 1, '2021-12-19 00:37:42', '2021-12-19 00:37:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11401, '客车通过险桥、漫水桥、渡口、危险地段前，应当怎么做？', '', '1', '1', NULL, '让乘客坐稳', '组织乘客下车', '让乘客抓住车内固定物', '让乘客系好安全带', 'B', '乘客的安全要放在第一位，在不能确定是否可以安全通过危险地段时，最好的做法是组织乘客下车。', 6, 1, '2021-12-19 00:37:42', '2021-12-19 00:37:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11402, '取得驾驶牵引车准驾车型资格多少年以上的，并在申请前最近1个记分周期内没有满分记录的，可以申请增加大型客车？', '', '1', '1', NULL, '2年', '1年', '3年', '年限没有规定', 'B', '2020年11月20日实施的12项公安交管新措施，驾驶证的申领和增驾新规定：申请增加大型客车准驾车型的，已取得驾驶牵引车准驾车型资格1年以上，并在申请前最近连续1个记分周期内没有记满12分记录。', 6, 1, '2021-12-19 00:37:43', '2021-12-19 00:37:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11403, '机动车驾驶人在实习期内不得驾驶哪类车型？', '', '1', '1', NULL, '公共汽车', '大型客车', '大型货车', '中型客车', 'A', '机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 6, 1, '2021-12-19 00:37:43', '2021-12-19 00:37:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11404, '客车在加油站加油时，驾驶人应当提醒乘客怎么做？', '', '1', '1', NULL, '系好安全带', '在车内等待', '不能拨打手机、吸烟', '不要在车内随意走动', 'C', '安全第一，加油站最怕引起火灾。而C选项是容易造成火灾的因素，所以此题选择C。', 6, 1, '2021-12-19 00:37:43', '2021-12-19 00:37:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11405, '客车在行驶中遇险时，要做到哪些？', '', '1', '1', NULL, '优先保护乘客人身安全', '先抢救财产', '根据情况进行抢救', '先抢救贵重物品', 'A', '乘客人身安全是第一重要的，以人为本。', 6, 1, '2021-12-19 00:37:43', '2021-12-19 00:37:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11406, '缓速器可以在不使用或少使用行车制动装置的条件下，使车辆如何？而且不会使车辆紧急制动。', '', '1', '1', NULL, '制动抱死', '加速行驶', '速度降低或保持稳定', '空挡滑行', 'C', '缓速器的作用是使车辆速度降低或保持稳定。', 6, 1, '2021-12-19 00:37:44', '2021-12-19 00:37:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11407, '为了保证长途和山区行车的安全，大型客车上常装有对车辆起缓速作用的部件是什么？', '', '1', '1', NULL, '缓速器', '制动器', '差速器', '减速器', 'A', '根据题意，可知道选择“缓速器”，缓速器的作用是使车辆速度降低或保持稳定。', 6, 1, '2021-12-19 00:37:44', '2021-12-19 00:37:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11408, '报考中型客车准驾车型科目三考试的，在取得驾驶技能准考证明满30日后预约。', '', '2', '1', NULL, '', '', '', '', 'A', '根据公安部推出的“我为群众办实事”公安交管12项便利措施实施内容，自2021年6月1日起，优化驾驶证考试内容和程序—调整考试预约时间，对申请小型自动挡汽车驾驶证的，科目三约考间隔时间由科目一考试合格后30日调整为20日；增驾大中型客货车驾驶证的，间隔时间由40日调整为30日，减少待考时间。', 6, 1, '2021-12-19 00:37:44', '2021-12-19 00:37:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11409, '报考大型客车准驾车型科目三考试的，在取得驾驶技能准考证明满40日后预约。', '', '2', '1', NULL, '', '', '', '', 'B', '根据公安部推出的“我为群众办实事”公安交管12项便利措施实施内容，自2021年6月1日起，优化驾驶证考试内容和程序—调整考试预约时间，对申请小型自动挡汽车驾驶证的，科目三约考间隔时间由科目一考试合格后30日调整为20日；增驾大中型客货车驾驶证的，间隔时间由40日调整为30日，减少待考时间。', 6, 1, '2021-12-19 00:37:44', '2021-12-19 00:37:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11410, '客车遇碰刮或制动失效时，应迅速告知乘客向车厢哪个位置或没有被刮碰的一侧挤靠，并抓住车内固定物，注意防范车身变形挤伤身体？', '', '1', '1', NULL, '左侧', '中部', '右侧', '前方', 'B', '本着将伤亡减小到最低程度的原则，只有车厢的中间或是没有刮擦到的一边的安全系数较高。', 6, 1, '2021-12-19 00:37:45', '2021-12-19 00:37:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11411, '客车失火无法开启车门时，为了减少伤害，应尽快组织乘客如何逃生？', '', '1', '1', NULL, '砸碎前挡风玻璃', '砸碎侧挡风玻璃', '撬开车门', '先行灭火，再撬开车门', 'B', '车辆前面有燃油系统，所以从前风挡逃跑是不安全的，另外一般来说，两侧车窗较多，可以节省逃生时间。', 6, 1, '2021-12-19 00:37:45', '2021-12-19 00:37:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11412, '年龄在60周岁以上，超过机动车驾驶证有效期一年以上未换证的，车辆管理所将注销其驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《机动车驾驶证申领和使用规定》第六十七条：机动车驾驶人具有下列情形之一的，车辆管理所应当注销其机动车驾驶证：（七）超过机动车驾驶证有效期一年以上未换证的。', 6, 1, '2021-12-19 00:37:45', '2021-12-19 00:37:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11413, '大型载客汽车允许牵引总质量700千克以下的挂车。', '', '2', '1', NULL, '', '', '', '', 'B', '客车是禁止拖挂车辆的，因为客车的用途是载客，要保证旅客的安全为本。 挂车分全挂及半挂，基本上是货车，全挂为一辆主车通过刚性导件连接拖挂一节货箱，半挂多为集装箱平板车，由一个车头拖挂货箱。挂车基本上可以与主车分离，比如集装箱可以放下后重新挂另外一货箱，但不管哪种都只准挂一节车箱。 小型载客汽车只允许牵引旅居挂车或者总质量700千克以下的挂车。挂车不得载人，那种都不能称之为真正意义上的挂车。', 6, 1, '2021-12-19 00:37:45', '2021-12-19 00:37:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11414, '年龄在60岁以上持有准驾车型为大型客车的驾驶人，应当到驾驶证核发地车辆管理所换领准驾车型为小型汽车或小型自动挡汽车的驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《驾驶证申领和使用规定》第五十九条 年龄在60周岁以上的，不得驾驶大型客车、牵引车、城市公交车、中型客车、大型货车、无轨电车和有轨电车；持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的，应当到机动车驾驶证核发地或者核发地以外的车辆管理所换领准驾车型为小型汽车或者小型自动挡汽车的机动车驾驶证。', 6, 1, '2021-12-19 00:37:45', '2021-12-19 00:37:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11415, '机动车载人不得超过核定的人数。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十九条:机动车载人不得超过核定的人数，客运机动车不得违反规定载货。', 6, 1, '2021-12-19 00:37:46', '2021-12-19 00:37:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11416, '客运机动车可以载货。', '', '2', '1', NULL, '', '', '', '', 'B', '客车机动车载货是违法的。', 6, 1, '2021-12-19 00:37:46', '2021-12-19 00:37:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11417, '机动车行驶时，乘坐人员可不使用安全带。', '', '2', '1', NULL, '', '', '', '', 'B', '机动车行驶时，驾驶人或者乘坐人员未按照规定使用安全带的，处警告或者二百元罚款。所以本题选择【错误】。', 6, 1, '2021-12-19 00:37:46', '2021-12-19 00:37:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11418, '已持有小型汽车驾驶证，申请增加中型客车准驾车型的，应当在申请前最近一个记分周期内没有满分记录。', '', '2', '1', NULL, '', '', '', '', 'B', '应该是两个记分周期内没有满分记录 第十四条 已持有机动车驾驶证，申请增加准驾车型的，应当在申请前最近一个记分周期内没有满分记录。申请增加中型客车、牵引车、大型客车准驾车型的，还应当符合下列规定： （一）申请增加中型客车准驾车型的，已取得驾驶小型汽车、小型自动挡汽车、低速载货汽车或者三轮汽车准驾车型资格三年以上，并在申请前最近连续两个记分周期内没有满分记录；或者取得驾驶城市公交车、大型货车准驾车型资格一年以上，并在申请前最近一个记分周期内没有满分记录。', 6, 1, '2021-12-19 00:37:46', '2021-12-19 00:37:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11419, '在造成人员死亡的交通事故中承担主要责任的，不得申请增加大型客车准驾车型，但可以申请增加中型客车准驾车型。', '', '2', '1', NULL, '', '', '', '', 'B', '公安机关预防特大道路交通事故工作意见： 对申请大型、中型客车准驾车型的人员要严格审核把关，对有在造成人员死亡的交通事故中承担全部责任或者主要责任记录的，不予受理。对近3年有交通死亡责任事故记录的驾驶人，建议交通部门不予准许参加客运从业资格考试。', 6, 1, '2021-12-19 00:37:47', '2021-12-19 00:37:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11420, '铰接式客车不得进入高速公路。', '', '2', '1', NULL, '', '', '', '', 'A', '《 中华人民共和国道路交通安全法》 第六十七条行人、非机动车、拖拉机、轮式专用机械车、铰接式客车、全挂拖斗车以及其他设计最高时速低于七十公里的机动车，不得进入高速公路。高速公路限速标志标明的最高时速不得超过一百二十公里。', 6, 1, '2021-12-19 00:37:47', '2021-12-19 00:37:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11421, '营运机动车改为非营运机动车的，机动车所有人不需要向公安机关交通管理部门申请登记。', '', '2', '1', NULL, '', '', '', '', 'B', '　第十条 已注册登记的机动车有下列情形之一的，机动车所有人应当向登记地车辆管理所申请变更登记： (一)改变车身颜色的； (二)更换发动机的； (三)更换车身或者车架的； (四)因质量问题更换整车的； (五)营运机动车改为非营运机动车或者非营运机动车改为营运机动车等使用性质改变的; (六)机动车所有人的住所迁出或者迁入车辆管理所管辖区域的。', 6, 1, '2021-12-19 00:37:47', '2021-12-19 00:37:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11422, '营运机动车在规定检验期限内经安全技术检验合格的，不再重复进行安全技术检验。', '', '2', '1', NULL, '', '', '', '', 'A', '所有机动车，十年内为安全期，十年内每年都要安检一次。过了十年后每半年要安检一次。所以在规定检验期限内经安全技术检验合格的，当然就不需要再重复进行安全技术检验。', 6, 1, '2021-12-19 00:37:47', '2021-12-19 00:37:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11423, '机动车驾驶人在实习期内可以单独驾驶大型营运客车。', '', '2', '1', NULL, '', '', '', '', 'B', '123号令规定第六十五条： 机动车驾驶人在实习期内不得驾驶公共汽车、营运客车或者执行任务的警车、消防车、救护车、工程救险车以及载有爆炸物品、易燃易爆化学物品、剧毒或者放射性等危险物品的机动车；驾驶的机动车不得牵引挂车。', 6, 1, '2021-12-19 00:37:48', '2021-12-19 00:37:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11424, '载客汽车除车身外部的行李架和内置的行李箱外，不得载货。', '', '2', '1', NULL, '', '', '', '', 'A', '《交通安全法实施条例》第五十四条 机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢，并应当遵守下列规定： 载客汽车除车身外部的行李架和内置的行李箱外，不得载货。载客汽车行李架载货，从车顶起高度不得超过0.5米，从地面起高度不得超过4米。', 6, 1, '2021-12-19 00:37:48', '2021-12-19 00:37:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11425, '城市公共汽车不得在站点以外的路段停车上下乘客。', '', '2', '1', NULL, '', '', '', '', 'A', '一是严重影响交通秩序。此类违法往往成为交通堵塞的原因，因此，严重影响正常的交通秩序。 二是诱发交通事故，因不按规定停车造成的追尾事故是交通事故的主要类型之一。 三是影响城市的形象。', 6, 1, '2021-12-19 00:37:48', '2021-12-19 00:37:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11426, '图中标志的含义是禁止小型客车通行。', '/uploadFile/yuanBeiTu1426.gif', '2', '1', NULL, '', '', '', '', 'B', '四个窗的是大型客车，三个窗的是小型客车。', 6, 1, '2021-12-19 00:37:48', '2021-12-19 00:37:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11427, '为保障乘车人不受伤害，客车驾驶人或乘务员应当向乘客讲解安全门的使用方法。', '', '2', '1', NULL, '', '', '', '', 'A', '这个是上车必学的常识了！', 6, 1, '2021-12-19 00:37:48', '2021-12-19 00:37:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11428, '客车在起步前，应检查行李包是否装捆牢固，长、宽、高及重量是否符合规定，可将行李放置在门道或者过道上。', '', '2', '1', NULL, '', '', '', '', 'B', '应检查行李包是否装捆牢固，长、宽、高及重量是否符合规定----这个说的很对； 可将行李放置在门道或者过道上----这个说的就不对了。 通道上严禁放置任何物品，那是逃生通道。 所以这题错在了后半句。', 6, 1, '2021-12-19 00:37:49', '2021-12-19 00:37:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11429, '客车起步、行驶和停车时，应当平稳，避免乘客受伤。', '', '2', '1', NULL, '', '', '', '', 'A', '安全文明驾驶！', 6, 1, '2021-12-19 00:37:49', '2021-12-19 00:37:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11430, '客车在起步前，驾驶人应向乘客强调不要将手、头部以及其他部位伸出窗外。', '', '2', '1', NULL, '', '', '', '', 'A', '客车在行驶过程中速度很快，如果乘客做出这些危险举动，很有可能受到伤害！', 6, 1, '2021-12-19 00:37:49', '2021-12-19 00:37:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11431, '营运客车起步前，乘务人员应提醒乘客系好安全带。', '', '2', '1', NULL, '', '', '', '', 'A', '安全第一，开车前不管是乘客还是驾驶人都一定要系好安全带！', 6, 1, '2021-12-19 00:37:49', '2021-12-19 00:37:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11432, '客车行经转弯、上下坡、凹凸路时，应让乘客抓住车内固定物，尤其提醒后排乘客注意安全。', '', '2', '1', NULL, '', '', '', '', 'A', '因为在行经这些路面时，很容易发生颠簸，让人失去平衡。大家都有这样的感觉，坐在后排起伏的幅度较大，很多时候通过颠簸路面时，会有强烈的波动。 在这种情况下，若没有做好安全措施，很有可能因为惯性等因素发生危险。', 6, 1, '2021-12-19 00:37:50', '2021-12-19 00:37:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11433, '严禁在车辆未停稳或行驶途中开启车门，以防乘客被甩出酿成事故。', '', '2', '1', NULL, '', '', '', '', 'A', '基本的安全常识，一定要知道！', 6, 1, '2021-12-19 00:37:50', '2021-12-19 00:37:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11434, '客车驾驶人在行车中，应始终保持良好的心理状态，不得带着不良情绪驾驶车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '心情好了，做任何事情都倍儿有精神！客车驾驶人承担着一车人的安危，所以更该保持心情舒畅！', 6, 1, '2021-12-19 00:37:50', '2021-12-19 00:37:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11435, '客运驾驶人日间连续驾驶不得超过4小时，夜间连续驾驶不得超过2小时，每次停车休息时间不少于20分钟。', '', '2', '1', NULL, '', '', '', '', 'A', '在24小时内累计驾驶时间不得超过8小时。日间连续驾驶时间不得超过4小时，夜间连续驾驶时间不得超过2小时，每次停车休息时间应不少于20分钟。 依据《道路旅客运输企业安全管理规范》第三十八条 客运企业在制定运输计划时应当严格遵守客运驾驶员驾驶时间和休息时间等规定： （一）日间连续驾驶时间不得超过4小时，夜间连续驾驶时间不得超过2小时，每次停车休息时间应不少于20分钟； （二）在24小时内累计驾驶时间不得超过8小时； （三）任意连续7日内累计驾驶时间不得超过44小时，期间有效落地休息； （四）禁止在夜间驾驶客运车辆通行达不到安全通行条件的三级及以下山区公路；', 6, 1, '2021-12-19 00:37:50', '2021-12-19 00:37:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11436, '客车在遇险的一刹那，应果断地采取一切有效措施保护乘客不受伤害或少受伤害，要避免车辆发生倾覆。', '', '2', '1', NULL, '', '', '', '', 'A', '以人为本，要将伤害和损失尽可能降到最低，因此这题是正确的。', 6, 1, '2021-12-19 00:37:51', '2021-12-19 00:37:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11437, '驾驶客车遇非常情况或者发生事故时，应力所能及的将损失降到最低限度，决不能因紧急避险造成二次事故或更大的损失。', '', '2', '1', NULL, '', '', '', '', 'A', '要以人为本，秉承着将伤害和损失降到最低的原则。', 6, 1, '2021-12-19 00:37:51', '2021-12-19 00:37:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11438, '驾驶客车在山区、桥梁、高速公路遇紧急情况避险时，应先转动转向盘避让，再使用制动减速。', '', '2', '1', NULL, '', '', '', '', 'B', '应先减速再调整方向。贸然转动方向盘避让，车速过快的话很容易造成翻车。', 6, 1, '2021-12-19 00:37:51', '2021-12-19 00:37:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11439, '客车遇险后，应设法开启车门、安全门或紧急出口，迅速疏散车上乘客。', '', '2', '1', NULL, '', '', '', '', 'A', '车辆遇险，当然是需要最快的时间撤离！', 6, 1, '2021-12-19 00:37:51', '2021-12-19 00:37:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11440, '客车遇险后，没有安全门的，可用车上配备的铁锤或者其他物品将车窗玻璃敲破，从窗口疏散乘客。', '', '2', '1', NULL, '', '', '', '', 'A', '以人为本，要想尽一切办法为乘客创造逃生机会。', 6, 1, '2021-12-19 00:37:52', '2021-12-19 00:37:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11441, '遇紧急情况时，客车的顶窗也可用作疏散乘客的紧急出口。', '', '2', '1', NULL, '', '', '', '', 'A', '以人为本，没有条件也要创造条件，以期将伤害和损失降到最低。', 6, 1, '2021-12-19 00:37:52', '2021-12-19 00:37:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11442, '驾驶人逃离火灾如果无法开启驾驶室门或车门时，应敲碎前挡风玻璃脱离。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶人紧急破窗逃生时，应首选敲碎侧面车窗逃生。因为前风窗玻璃较厚，且多为夹层玻璃，很难敲碎，即使敲碎也不易大面积脱落快速形成逃生出口，而侧面车窗玻璃相对前风窗玻璃厚度小更容易敲碎。此外，破窗时应选择玻璃的边缘或四角进行敲击。', 6, 1, '2021-12-19 00:37:52', '2021-12-19 00:37:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11443, '安装乘客安全带的车辆，应要求乘客在起步前按规定使用安全带。', '', '2', '1', NULL, '', '', '', '', 'A', '安全第一，所以一定要要求乘客在起步前按规定使用安全带。', 6, 1, '2021-12-19 00:37:52', '2021-12-19 00:37:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11444, '持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人，在记分周期内有记分的，应当在记分周期结束后三十日内到公安机关交通管理部门接受审验。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十八条：持有大型客车、牵引车、城市公交车、中型客车、大型货车驾驶证的驾驶人有下列情形之一的，车辆管理所应当注销其最高准驾车型驾驶资格，并通知机动车驾驶人在三十日内办理降级换证业务，在一个记分周期内有记满12分记录的。', 6, 1, '2021-12-19 00:37:53', '2021-12-19 00:37:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11445, '汽车的车身越高，转弯时的稳定性会怎样？', '', '1', '1', NULL, '可能变差，也可能变好', '越差', '不变', '越好', 'B', '离心力问题，车身越高，重心越高，转弯时由于惯性稳定性会越差一些，因此要减速慢行。', 5, 1, '2021-12-19 00:38:27', '2021-12-19 00:38:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11446, '驾驶机动车在道路上违反道路交通安全法的行为，属于什么行为？', '', '1', '1', NULL, '违章行为', '违法行为', '过失行为', '违规行为', 'B', '“违反道路交通安全法”，违反法律法规即为违法行为。官方已无违章/违规的说法。', 7, 1, '2021-12-19 00:51:24', '2021-12-19 00:51:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11447, '机动车驾驶人违法驾驶造成重大交通事故构成犯罪的，依法追究什么责任？', '', '1', '1', NULL, '刑事责任', '民事责任', '经济责任', '经济责任', 'A', '经济责任', 7, 1, '2021-12-19 00:51:25', '2021-12-19 00:51:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11448, '机动车驾驶人造成事故后逃逸构成犯罪的，吊销驾驶证且多长时间不得重新取得驾驶证？', '', '1', '1', NULL, '5年内', '10年内', '终生', '20年内', 'C', '《道路交通安全法》第一百零一条：造成交通事故后逃逸的，由公安机关交通管理部门吊销机动车驾驶证，且终生不得重新取得机动车驾驶证。本题解释由外运驾校提供', 7, 1, '2021-12-19 00:51:26', '2021-12-19 00:51:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11449, '驾驶机动车应当随身携带哪种证件？', '', '1', '1', NULL, '工作证', '驾驶证', '身份证', '职业资格证', 'B', '职业资格证', 7, 1, '2021-12-19 00:51:27', '2021-12-19 00:51:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11450, '机动车驾驶人初次申领驾驶证后的实习期是多长时间？', '', '1', '1', NULL, '6个月', '12个月', '16个月', '18个月', 'B', '《公安部令第123号》第六十四条规定： 机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 7, 1, '2021-12-19 00:51:29', '2021-12-19 00:51:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11451, '以欺骗、贿赂等不正当手段取得驾驶证被依法撤销驾驶许可的，多长时间不得重新申请驾驶许可？', '', '1', '1', NULL, '3年内', '终身', '1年内', '5年内', 'A', '《公安部令第123号》第七十八条规定：申请人以欺骗、贿赂等不正当手段取得机动车驾驶证的，公安机关交通管理部门收缴机动车驾驶证，撤销机动车驾驶许可；申请人在三年内不得再次申领机动车驾驶证。', 7, 1, '2021-12-19 00:51:30', '2021-12-19 00:51:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11452, '驾驶报废机动车上路行驶的驾驶人，除按规定罚款外，还要受到哪种处理？', '', '1', '1', NULL, '撤销驾驶许可', '收缴驾驶证', '强制恢复车况', '吊销驾驶证', 'D', '吊销驾驶证', 7, 1, '2021-12-19 00:51:31', '2021-12-19 00:51:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11453, '下列哪种证件是驾驶机动车上路行驶应当随车携带？', '', '1', '1', NULL, '机动车登记证', '机动车保险单', '机动车行驶证', '出厂合格证明', 'C', '《道路交通安全法》第十一条：　驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。', 7, 1, '2021-12-19 00:51:32', '2021-12-19 00:51:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11454, '下列哪种标志是驾驶机动车上路行驶应当在车上放置的标志？', '', '1', '1', NULL, '产品合格标志', '保持车距标志', '提醒危险标志', '检验合格标志', 'D', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。', 7, 1, '2021-12-19 00:51:33', '2021-12-19 00:51:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11455, '驾驶人在下列哪种情况下不能驾驶机动车？', '', '1', '1', NULL, '饮酒后', '喝茶后', '喝咖啡后', '喝牛奶后', 'A', '喝酒后不能开车，酒后驾车违法。', 7, 1, '2021-12-19 00:51:34', '2021-12-19 00:51:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11456, '公安机关交通管理部门对驾驶人的交通违法行为除依法给予行政处罚外,实行下列哪种制度？', '', '1', '1', NULL, '违法登记制度', '奖励里程制度', '累积记分制度', '强制报废制度', 'C', '《道路交通安全法》第二十四条：公安机关交通管理部门对机动车驾驶人违反道路交通安全法律、法规的行为，除依法给予行政处罚外，实行累积记分制度。', 7, 1, '2021-12-19 00:51:35', '2021-12-19 00:51:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11457, '道路交通安全违法行为累积记分的周期是多长时间？', '', '1', '1', NULL, '3个月', '6个月', '12个月', '24个月', 'C', '《公安部令第123号》第五十五条：道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 7, 1, '2021-12-19 00:51:36', '2021-12-19 00:51:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11458, '公安机关交通管理部门对累积记分达到规定分值的驾驶人怎样处理？', '', '1', '1', NULL, '依法追究刑事责任', '处15日以下拘留', '终生禁驾', '进行法律法规教育，重新考试', 'D', '《道路交通安全法》第二十四条：公安机关交通管理部门对累积记分达到规定分值的机动车驾驶人，扣留机动车驾驶证，对其进行道路交通安全法律、法规教育，重新考。', 7, 1, '2021-12-19 00:51:37', '2021-12-19 00:51:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11459, '驾驶人出现下列哪种情况，不得驾驶机动车？', '', '1', '1', NULL, '驾驶证丢失、损毁', '驾驶证接近有效期', '记分达到10分', '记分达到6分', 'A', '驾驶证遗失的、损毁应当向机动车驾驶证核发地车辆管理所申请补发、换证，在此期间不得驾驶机动车。', 7, 1, '2021-12-19 00:51:38', '2021-12-19 00:51:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11460, '机动车在道路上变更车道需要注意什么？', '', '1', '1', NULL, '尽快加速进入左侧车道', '不能影响其他车辆正常行驶', '进入左侧车道时适当减速', '开启转向灯迅速向左转向', 'B', '《道路交通安全法实施条例》第四十四条：在道路同方向划有2条以上机动车道的，变更车道的机动车不得影响相关车道内行驶的机动车的正常行驶。', 7, 1, '2021-12-19 00:51:39', '2021-12-19 00:51:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11461, '驾驶机动车在道路上向左变更车道时如何使用灯光？', '', '1', '1', NULL, '不用开启转向灯', '提前开启右转向灯', '提前开启左转向灯', '提前开启近光灯', 'C', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:51:40', '2021-12-19 00:51:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11462, '驾驶机动车在道路上靠路边停车过程中如何使用灯光？', '', '1', '1', NULL, '变换使用远近光灯', '不用指示灯提示', '开启危险报警闪光灯', '提前开启右转向灯', 'D', '《道路交通安全法实施条例》第五十七条 机动车应当按照下列规定使用转向灯： (一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯； (二)向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:51:41', '2021-12-19 00:51:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11463, '驾驶机动车遇雾、雨、雪等能见度在50米以内时，最高速度不能超过多少？', '', '1', '1', NULL, '70公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', 'D', '《道路交通安全法实施条例》第四十六条：机动车行驶中遇到雾、雨、雪、沙尘、冰雹，能见度在50米以内时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:42', '2021-12-19 00:51:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11464, '驾驶机动车在进出非机动车道时，最高速度不能超过多少？', '', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '60公里/小时', 'A', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:43', '2021-12-19 00:51:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11465, '驾驶机动车通过铁路道口时，最高速度不能超过多少？', '', '1', '1', NULL, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', 'C', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:44', '2021-12-19 00:51:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11466, '驾驶机动车通过急弯路时，最高速度不能超过多少？', '', '1', '1', NULL, '20公里/小时', '30公里/小时', '40公里/小时', '50公里/小时', 'B', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:46', '2021-12-19 00:51:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11467, '驾驶机动车通过窄路、窄桥时，最高速度不能超过多少？', '', '1', '1', NULL, '60公里/小时', '50公里/小时', '40公里/小时', '30公里/小时', 'D', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:47', '2021-12-19 00:51:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11468, '驾驶机动车下陡坡、转弯、掉头时，最高速度不能超过多少？', '', '1', '1', NULL, '30公里/小时', '40公里/小时', '50公里/小时', '60公里/小时', 'A', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到掉头、转弯、下陡坡时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:48', '2021-12-19 00:51:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11469, '驾驶机动车在冰雪道路行驶时，最高速度不能超过多少？', '', '1', '1', NULL, '20公里/小时', '30公里/小时', '40公里/小时', '50公里/小时', 'B', '《道路交通安全法实施条例》第四十六条： 机动车行驶中在冰雪、泥泞的道路上行驶时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:49', '2021-12-19 00:51:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11470, '驾驶机动车在泥泞道路行驶时，最高速度不能超过多少？', '', '1', '1', NULL, '15公里/小时', '20公里/小时', '30公里/小时', '40公里/小时', 'C', '《道路交通安全法实施条例》第四十六条： 机动车行驶中在冰雪、泥泞的道路上行驶时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:51:50', '2021-12-19 00:51:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11471, '驾驶机动车在下列哪种情形下不能超越前车？', '', '1', '1', NULL, '前车减速让行', '前车正在左转弯', '前车靠边停车', '前车正在右转弯', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:51', '2021-12-19 00:51:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11472, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '', '1', '1', NULL, '正在停车', '减速让行', '正常行驶', '正在超车', 'D', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:52', '2021-12-19 00:51:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11473, '同车道行驶的车辆遇前车有下列哪种情形时不得超车？', '', '1', '1', NULL, '正在停车', '减速让行', '正在掉头', '正常行驶', 'C', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:53', '2021-12-19 00:51:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11474, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '执行任务的警车', '超载大型货车', '大型客车', '城市公交车', 'A', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:54', '2021-12-19 00:51:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11475, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '超载大型货车', '大型客车', '执行任务的救护车', '小型货车', 'C', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:55', '2021-12-19 00:51:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11476, '同车道行驶的车辆前方遇到下列哪种车辆不得超车？', '', '1', '1', NULL, '超载大型货车', '执行任务的消防车', '大型客车', '中型客车', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:56', '2021-12-19 00:51:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11477, '驾驶机动车行经下列哪种路段不得超车？', '', '1', '1', NULL, '主要街道', '高架路', '人行横道', '环城高速', 'C', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:57', '2021-12-19 00:51:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11478, '驾驶机动车行经下列哪种路段时不得超车？', '', '1', '1', NULL, '高架路', '交叉路口', '环城高速', '中心街道', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:58', '2021-12-19 00:51:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11479, '驾驶机动车在下列哪种路段不得超车？', '', '1', '1', NULL, '山区道路', '城市高架路', '城市快速路', '窄桥、弯道', 'D', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:51:59', '2021-12-19 00:51:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11480, '驾驶机动车在夜间超车时怎样使用灯光？', '', '1', '1', NULL, '变换远、近光灯', '开启雾灯', '开启远光灯', '关闭前大灯', 'A', '夜间超车应提前开启左转向灯，并交替使用远、近光灯示意，提醒前车。', 7, 1, '2021-12-19 00:52:00', '2021-12-19 00:52:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11481, '驾驶机动车在没有道路中心线的狭窄山路怎样会车？', '', '1', '1', NULL, '速度慢的先行', '重车让空车先行', '靠山体的一方先行', '不靠山体的一方先行', 'D', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，在狭窄的山路，不靠山体的一方先行。', 7, 1, '2021-12-19 00:52:01', '2021-12-19 00:52:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11482, '夜间在道路上会车时，距离对向来车多远将远光灯改用近光灯？', '', '1', '1', NULL, '200米以外', '150米以外', '100米以内', '50米以内', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 7, 1, '2021-12-19 00:52:02', '2021-12-19 00:52:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11483, '驾驶机动车在没有中心线的道路上遇相对方向来车时怎样行驶？', '', '1', '1', NULL, '减速靠右行驶', '借非机动车道行驶', '紧靠路边行驶', '靠路中心行驶', 'A', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，减速靠右行驶，并与其他车辆、行人保持必要的安全距离。', 7, 1, '2021-12-19 00:52:04', '2021-12-19 00:52:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11484, '夜间驾驶机动车在窄路、窄桥会车怎样使用灯光？', '', '1', '1', NULL, '关闭所有灯光', '开启近光灯', '关闭前照灯', '开启远光灯', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 7, 1, '2021-12-19 00:52:05', '2021-12-19 00:52:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11485, '在路口右转弯遇同车道前车等候放行信号时如何行驶？', '', '1', '1', NULL, '从前车左侧转弯', '从右侧占道转弯', '鸣喇叭让前车让路', '依次停车等候', 'D', '《道路交通安全法实施条例》第五十一条： 机动车通过有交通信号灯控制的交叉路口，向右转弯遇有同车道前车正在等候放行信号时，依次停车等候。', 7, 1, '2021-12-19 00:52:06', '2021-12-19 00:52:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11486, '驾驶机动车通过没有交通信号的交叉路口怎样行驶？', '', '1', '1', NULL, '减速慢行', '加速通过', '大型车先行', '左侧车辆先行', 'A', '在没有交通信号的交叉路口，应当减速慢行，同时注意观察周围情况，确认可以通过再通过。', 7, 1, '2021-12-19 00:52:07', '2021-12-19 00:52:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11487, '驾驶机动车通过没有交通信号和管理人员的铁路道口怎样通过？', '', '1', '1', NULL, '适当减速通过', '空挡滑行通过', '停车确认安全后通过', '加速尽快通过', 'C', '《道路交通安全法》第四十六条：机动车通过铁路道口时，应当按照交通信号或者管理人员的指挥通行；没有交通信号或者管理人员的，应当减速或者停车，在确认安全后通过。', 7, 1, '2021-12-19 00:52:08', '2021-12-19 00:52:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11488, '驾驶机动车在车道减少的路口，遇到前方车辆依次停车或缓慢行驶时怎么办？', '', '1', '1', NULL, '从前车右侧路肩进入路口', '从有空隙一侧进入路口', '每车道一辆依次交替驶入路口', '向左变道穿插进入路口', 'C', '《道路交通安全法实施条例》第五十三条：机动车在车道减少的路口、路段，遇有前方机动车停车排队等候或者缓慢行驶的，应当每车道一辆依次交替驶入车道减少后的路口、路段。', 7, 1, '2021-12-19 00:52:09', '2021-12-19 00:52:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11489, '驾驶机动车遇到前方车辆停车排队等候或缓慢行驶时怎么办？', '', '1', '1', NULL, '可借道超车', '占用对面车道', '穿插等候的车辆', '依次行驶', 'D', '《道路交通安全法》第四十五条：机动车遇有前方车辆停车排队等候或者缓慢行驶时，不得借道超车或者占用对面车道，不得穿插等候的车辆。', 7, 1, '2021-12-19 00:52:10', '2021-12-19 00:52:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11490, '驾驶机动车遇有前方交叉路口交通阻塞时怎么办？', '', '1', '1', NULL, '依次停在路口外等候', '可借对向车道通过', '从前车两侧穿插通过', '进入路口内等候', 'A', '《道路交通安全法实施条例》第五十三条：机动车遇有前方交叉路口交通阻塞时，应当依次停在路口以外等候，不得进入路口。', 7, 1, '2021-12-19 00:52:11', '2021-12-19 00:52:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11491, '机动车在道路上发生故障，需要停车排除时，驾驶人应该怎么办？', '', '1', '1', NULL, '就地停车排除故障', '开启近光灯或雾灯', '将车停到不妨碍交通的地方', '将车停在道路中间', 'C', '需要停车排除就说明不是车胎抱死，车还是可以开的，所以要开到不防碍交通的地方。', 7, 1, '2021-12-19 00:52:12', '2021-12-19 00:52:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11492, '驾驶机动车需要在路边停车时怎样选择停车地点？', '', '1', '1', NULL, '在人行道上停放', '在路边随意停放', '在停车泊位内停放', '靠左侧路边逆向停放', 'C', '停车应该在停车泊位内。', 7, 1, '2021-12-19 00:52:13', '2021-12-19 00:52:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11493, '在道路上发生未造成人员伤亡且无争议的轻微交通事故如何处置？', '', '1', '1', NULL, '保护好现场再协商', '不要移动车辆', '疏导其他车辆绕行', '撤离现场自行协商', 'D', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 7, 1, '2021-12-19 00:52:14', '2021-12-19 00:52:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11494, '驾驶人连续驾驶不得超过多长时间？', '', '1', '1', NULL, '4小时', '6小时', '8小时', '10小时', 'A', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 7, 1, '2021-12-19 00:52:15', '2021-12-19 00:52:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11495, '驾驶人连续驾驶4小时以上，停车休息的时间不得少于多少？', '', '1', '1', NULL, '5分钟', '10分钟', '15分钟', '20分钟', 'D', '《道路交通安全法实施条例》第六十二条：连续驾驶机动车超过4小时应停车休息，停车休息时间不少于20分钟。', 7, 1, '2021-12-19 00:52:16', '2021-12-19 00:52:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11496, '驾驶机动车下陡坡时不得有哪些危险行为？', '', '1', '1', NULL, '提前减挡', '空挡滑行', '低挡行驶', '制动减速', 'B', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得在下陡坡时熄火或者空挡滑行。', 7, 1, '2021-12-19 00:52:17', '2021-12-19 00:52:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11497, '驾驶人违反交通运输管理法规发生重大事故致人重伤、死亡，可能会受到什么刑罚？', '', '1', '1', NULL, '处3年以下徒刑或者拘役', '处3年以上7年以下徒刑', '处5年以上徒刑', '处7年以上徒刑', 'A', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:52:18', '2021-12-19 00:52:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11498, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失，可能会受到什么刑罚？', '', '1', '1', NULL, '处5年以上徒刑', '处3年以下徒刑或者拘役', '处3年以上徒刑', '处3年以上7年以下徒刑', 'B', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:52:20', '2021-12-19 00:52:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11499, '驾驶人违反交通运输管理法规发生重大事故致人死亡且逃逸的，处多少年有期徒刑？', '', '1', '1', NULL, '7年以上', '3年以下', '3年以上7年以下', '10年以上', 'C', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:52:21', '2021-12-19 00:52:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11500, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处多少年有期徒刑？', '', '1', '1', NULL, '2年以下', '3年以下', '7年以下', '7年以上', 'D', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:52:22', '2021-12-19 00:52:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11501, '驾驶机动车在道路上追逐竞驶，情节恶劣，会受到什么处罚？', '', '1', '1', NULL, '处拘役，并处罚金', '处管制，并处罚金', '处1年以上徒刑', '处6个月徒刑', 'A', '2011年《中华人民共和国刑法修正案（八）》第二十二条：在刑法第一百三十三条后增加一条，作为第一百三十三条之一：“在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。”', 7, 1, '2021-12-19 00:52:23', '2021-12-19 00:52:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11502, '醉酒驾驶机动车在道路上行驶会受到什么处罚？', '', '1', '1', NULL, '处2年以下徒刑', '处拘役，并处罚金', '处2年以上徒刑', '处管制，并处罚金', 'B', '《道路交通安全法》 第九十一条：饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。饮酒后驾驶营运机动车的，处十五日拘留，并处五千元罚款，吊销机动车驾驶证，五年内不得重新取得机动车驾驶证。醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；十年内不得重新取得机动车驾驶证，重新取得机动车驾驶证后，不得驾驶营运机动车。饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 7, 1, '2021-12-19 00:52:24', '2021-12-19 00:52:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11503, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未悬挂机动车号牌', '未携带身份证', '未携带保险合同', '未放置城市环保标志', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。 故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:52:25', '2021-12-19 00:52:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11504, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未携带身份证', '未放置检验合格标志', '未放置城市环保标志', '未携带机动车登记证书', 'B', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。 故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:52:26', '2021-12-19 00:52:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11505, '上道路行驶的机动车有哪种情形交通警察可依法扣留车辆？', '', '1', '1', NULL, '未携带机动车登记证书', '未携带保险合同', '未放置保险标志', '未放置城市环保标志', 'C', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。 故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:52:27', '2021-12-19 00:52:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11506, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '', '1', '1', NULL, '机动车驾驶证', '居民身份证', '从业资格证', '机动车通行证证', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:52:28', '2021-12-19 00:52:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11507, '驾驶人未携带哪种证件驾驶机动车上路，交通警察可依法扣留车辆？', '', '1', '1', NULL, '机动车通行证', '居民身份证', '从业资格证', '机动车行驶证', 'D', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:52:29', '2021-12-19 00:52:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11508, '驾驶人有哪种情形，交通警察可依法扣留机动车驾驶证？', '', '1', '1', NULL, '饮酒后驾驶机动车', '超过规定速度10%', '疲劳后驾驶机动车', '行车中未系安全带', 'A', '饮酒、醉酒后驾车的，可扣留驾驶证。', 7, 1, '2021-12-19 00:52:30', '2021-12-19 00:52:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11509, '驾驶人将机动车交由什么样的人驾驶的，交通警察可依法扣留机动车驾驶证？', '', '1', '1', NULL, '实习期驾驶人', '取得驾驶证的人', '驾驶证被吊销的人', '驾驶证记分达到6分的人', 'C', '《道路交通安全法》第九十九条：将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，并处吊销机动车驾驶证。', 7, 1, '2021-12-19 00:52:31', '2021-12-19 00:52:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11510, '3年内有下列哪种行为的人不得申请机动车驾驶证？', '', '1', '1', NULL, '吸烟成瘾', '注射毒品', '注射胰岛素', '酒醉经历', 'B', '《公安部令第123号》第十二条：三年内有吸食、注射毒品行为或者解除强制隔离戒毒措施未满三年，或者长期服用依赖性精神药品成瘾尚未戒除的。', 7, 1, '2021-12-19 00:52:32', '2021-12-19 00:52:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11511, '驾驶人在驾驶证有效期满前多长时间申请换证', '', '1', '1', NULL, '30日内', '60日内', '90日内', '6个月内', 'C', '《公安部令第123号》第四十八条：机动车驾驶人应当于机动车驾驶证有效期满前九十日内，向机动车驾驶证核发地车辆管理所申请换证。', 7, 1, '2021-12-19 00:52:33', '2021-12-19 00:52:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11512, '驾驶人户籍迁出原车辆管理所需要向什么地方的车辆管理所提出申请？', '', '1', '1', NULL, '迁出地', '居住地', '所在地', '迁入地', 'D', '《公安部令第123号》第四十九条：机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。', 7, 1, '2021-12-19 00:52:34', '2021-12-19 00:52:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11513, '驾驶证记载的驾驶人信息发生变化的要在多长时间内申请换证？', '', '1', '1', NULL, '60日', '50日', '40日', '30日', 'D', '《公安部令第123号》第五十一条：具有下列情形之一的，机动车驾驶人应当在三十日内到机动车驾驶证核发地车辆管理所申请换证：（一）在车辆管理所管辖区域内，机动车驾驶证记载的机动车驾驶人信息发生变化的；（二）机动车驾驶证损毁无法辨认的。', 7, 1, '2021-12-19 00:52:35', '2021-12-19 00:52:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11514, '道路交通违法行为累积记分周期是多长时间？', '', '1', '1', NULL, '14个月', '12个月', '10个月', '6个月', 'B', '《公安部令第123号》第五十五条：道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 7, 1, '2021-12-19 00:52:37', '2021-12-19 00:52:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11515, '机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的多长时间为实习期？', '', '1', '1', NULL, '6个月', '12个月', '3个月', '2年', 'B', '《公安部令第123号》第六十四条：机动车驾驶人初次申请机动车驾驶证和增加准驾车型后的12个月为实习期。', 7, 1, '2021-12-19 00:52:38', '2021-12-19 00:52:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11516, '提供虚假材料申领驾驶证的申请人会承担下列哪种法律责任？', '', '1', '1', NULL, '处20元以上200元以下罚款', '取消申领驾驶证资格', '1年内不得再次申领驾驶证', '2年内不能再次申领驾驶证', 'C', '《公安部令第123号》第七十八条：隐瞒有关情况或者提供虚假材料申领机动车驾驶证的，申请人在一年内不得再次申领机动车驾驶证。', 7, 1, '2021-12-19 00:52:39', '2021-12-19 00:52:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11517, '驾驶人有下列哪种违法行为一次记12分？', '', '1', '1', NULL, '违反交通信号灯', '使用伪造机动车号牌', '违反禁令标志指示', '拨打、接听手机的', 'B', '《公安部令第123号》附件2，机动车驾驶人使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 7, 1, '2021-12-19 00:52:40', '2021-12-19 00:52:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11518, '驾驶与准驾车型不符的机动车一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '《公安部令第123号》附件2，驾驶与准驾车型不符的机动车的，一次记12分 。', 7, 1, '2021-12-19 00:52:41', '2021-12-19 00:52:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11519, '饮酒后驾驶机动车一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '酒驾12分，5年内不得再考取驾照。', 7, 1, '2021-12-19 00:52:42', '2021-12-19 00:52:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11520, '造成交通事故后逃逸，尚不构成犯罪的一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '《公安部令第123号》附件2，造成交通事故后逃逸，尚不构成犯罪的，一次记12分 。', 7, 1, '2021-12-19 00:52:43', '2021-12-19 00:52:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11521, '上道路行驶的机动车未悬挂机动车号牌的一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '《公安部令第123号》附件2，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。', 7, 1, '2021-12-19 00:52:44', '2021-12-19 00:52:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11522, '上道路行驶的机动车故意遮挡、污损、不按规定安装机动车号牌的一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '《公安部令第123号》附件2，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。', 7, 1, '2021-12-19 00:52:45', '2021-12-19 00:52:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11523, '使用伪造、变造的机动车号牌一次记几分？', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'D', '《公安部令第123号》附件2，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 7, 1, '2021-12-19 00:52:46', '2021-12-19 00:52:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11524, '使用伪造、变造的行驶证一次记几分？', '', '1', '1', NULL, '12分', '6分', '3分', '2分', 'A', '《公安部令第123号》附件2，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 7, 1, '2021-12-19 00:52:47', '2021-12-19 00:52:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11525, '驾驶人有下列哪种违法行为一次记6分？', '', '1', '1', NULL, '饮酒后驾驶机动车', '使用其他车辆行驶证', '车速超过规定时速50%以上', '违法占用应急车道行驶', 'D', '《公安部令第123号》附件2，驾驶机动车在高速公路或者城市快速路上违法占用应急车道行驶的，一次记6分 。', 7, 1, '2021-12-19 00:52:48', '2021-12-19 00:52:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11526, '驾驶人驾驶机动车违反道路交通信号灯通行一次记多少分?', '', '1', '1', NULL, '2分', '3分', '6分', '12分', 'C', '《公安部令第123号》附件2，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 7, 1, '2021-12-19 00:52:49', '2021-12-19 00:52:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11527, '有下列哪种违法行为的机动车驾驶人将被一次记6分？', '', '1', '1', NULL, '驾驶与准驾车型不符的机动车', '饮酒后驾驶机动车', '驾驶机动车违反道路交通信号灯', '未取得校车驾驶资格驾驶校车', 'C', '《公安部令第123号》附件2，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 7, 1, '2021-12-19 00:52:50', '2021-12-19 00:52:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11528, '有下列哪种违法行为的机动车驾驶人将被一次记12分？', '', '1', '1', NULL, '驾驶故意污损号牌的机动车上道路行驶', '机动车驾驶证被暂扣期间驾驶机动车的', '以隐瞒、欺骗手段补领机动车驾驶证的', '驾驶机动车不按照规定避让校车的', 'A', '《公安部令第123号》附件2，上道路行驶的机动车未悬挂机动车号牌的，或者故意遮挡、污损、不按规定安装机动车号牌的，一次记12分 。其余均为6分。', 7, 1, '2021-12-19 00:52:51', '2021-12-19 00:52:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11529, '摩托车从注册登记之日起4年以内多长时间检验1次？', '', '1', '1', NULL, '1年', '2年', '3年', '4年', 'B', '《道路交通安全法实施条例》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验：（四）摩托车4年以内每2年检验1次；超过4年的，每年检验1次。', 7, 1, '2021-12-19 00:52:52', '2021-12-19 00:52:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11530, '摩托车从注册登记之日起超过几年的每年检验1次？', '', '1', '1', NULL, '1年', '2年', '3年', '4年', 'D', '《道路交通安全法实施条例》第十六条：机动车应当从注册登记之日起，按照下列期限进行安全技术检验：（四）摩托车4年以内每2年检验1次；超过4年的，每年检验1次。', 7, 1, '2021-12-19 00:52:54', '2021-12-19 00:52:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11531, '下列关于三轮摩托车载物宽度的说法正确的是哪一项？', '', '1', '1', NULL, '不得超过车身0.15米', '不得超过车身', '不得超过车身0.20米', '不得超过车身0.25米', 'B', '《道路交通安全法实施条例》第五十四条：机动车载物不得超过机动车行驶证上核定的载质量，装载长度、宽度不得超出车厢，并应当遵守下列规定：(三)摩托车载物，高度从地面起不得超过1.5米，长度不得超出车身0.2米。两轮摩托车载物宽度左右各不得超出车把0.15米；三轮摩托车载物宽度不得超过车身。', 7, 1, '2021-12-19 00:52:55', '2021-12-19 00:52:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11532, '下列关于轻便摩托车的说法正确的是哪一项？', '', '1', '1', NULL, '可乘载未满12周岁以内的未成年人', '可以乘载18周岁以上的成年人', '不得载人', '可乘载学龄前儿童', 'C', '《道路交通安全法实施条例》第五十四条：机动车载人应当遵守下列规定：(三)摩托车后座不得乘坐未满12周岁的未成年人，轻便摩托车不得载人。', 7, 1, '2021-12-19 00:52:56', '2021-12-19 00:52:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11533, '驾驶机动车违反道路交通安全法律法规发生交通事故属于交通违章行为。', '', '2', '1', NULL, '', '', '', '', 'B', '“违反道路交通安全法”明显是违法，题目已暗示“违法”行为。况且现在没有违章的说法。', 7, 1, '2021-12-19 00:52:57', '2021-12-19 00:52:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11534, '驾驶机动车在道路上违反道路通行规定应当接受相应的处罚。', '', '2', '1', NULL, '', '', '', '', 'A', '违反规定就应该受到相应处罚。', 7, 1, '2021-12-19 00:52:58', '2021-12-19 00:52:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11535, '对未取得驾驶证驾驶机动车的，追究其法律责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条规定：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、检验合格标志、保险标志、驾驶证或者使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，并处二百元以上二千元以下罚款；构成犯罪的，依法追究刑事责任。该法第九十九条一款还规定，未取得机动车驾驶证、机动车驾驶证被吊销或者机动车驾驶证被暂扣期间驾驶机动车的，处二百元以上二千元以下罚款，可以并处十五日以下拘留。', 7, 1, '2021-12-19 00:52:59', '2021-12-19 00:52:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11536, '对违法驾驶发生重大交通事故且构成犯罪的，不追究其刑事责任。', '', '2', '1', NULL, '', '', '', '', 'B', '《最高人民法院关于审理交通肇事刑事案件具体应用法律若干问题的解释》第一条：从事交通运输人员或者非交通运输人员，违反交通运输管理法规发生重大交通事故，在分清事故责任的基础上，对于构成犯罪的，依照刑法第一百三十三条的规定定罪处罚。', 7, 1, '2021-12-19 00:53:00', '2021-12-19 00:53:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11537, '造成交通事故后逃逸且构成犯罪的驾驶人,将吊销驾驶证且终生不得重新取得驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第十二条：有下列情形之一的，不得申请机动车驾驶证：（三）造成交通事故后逃逸构成犯罪的。', 7, 1, '2021-12-19 00:53:01', '2021-12-19 00:53:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11538, '驾驶机动车在道路上违反交通安全法规的行为属于违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '“违反道路交通安全法”明显是违法，题目已暗示“违法”行为。况且现在没有违章的说法。', 7, 1, '2021-12-19 00:53:02', '2021-12-19 00:53:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11539, '驾驶人要按照驾驶证载明的准驾车型驾驶车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》道路交通安全违法行为记分分值：一、机动车驾驶人有下列违法行为之一，一次记12分：（一）驾驶与准驾车型不符的机动车的。', 7, 1, '2021-12-19 00:53:03', '2021-12-19 00:53:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11540, '上路行驶的机动车未随车携带身份证的，交通警察可依法扣留机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '请注意提干为“身份证”不是“驾驶证”，不要被题目误导。所以此题错误。', 7, 1, '2021-12-19 00:53:04', '2021-12-19 00:53:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11541, '上路行驶的机动车未放置检验合格标志的，交通警察可依法扣留机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车。', 7, 1, '2021-12-19 00:53:05', '2021-12-19 00:53:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11542, '伪造、变造机动车驾驶证构成犯罪的将被依法追究刑事责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处15日以下拘留，并处2000元以上5000元以下罚款；构成犯罪的，依法追究刑事责任。', 7, 1, '2021-12-19 00:53:06', '2021-12-19 00:53:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11543, '驾驶机动车上路前应当检查车辆安全技术性能。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十一条：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', 7, 1, '2021-12-19 00:53:07', '2021-12-19 00:53:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11544, '不得驾驶具有安全隐患的机动车上道路行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十一条：驾驶人驾驶机动车上道路行驶前，应当对机动车的安全技术性能进行认真检查；不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车。', 7, 1, '2021-12-19 00:53:08', '2021-12-19 00:53:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11545, '拼装的机动车只要认为安全就可以上路行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第一百条：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 7, 1, '2021-12-19 00:53:09', '2021-12-19 00:53:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11546, '已经达到报废标准的机动车经大修后可以上路行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第一百条：驾驶拼装的机动车或者已达到报废标准的机动车上道路行驶的，公安机关交通管理部门应当予以收缴，强制报废，处200元以上2000元以下罚款，并吊销机动车驾驶证。', 7, 1, '2021-12-19 00:53:11', '2021-12-19 00:53:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11547, '驾驶机动车上路行驶应当按规定悬挂号牌。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第十一条：驾驶机动车上道路行驶，应当悬挂机动车号牌，放置检验合格标志、保险标志，并随车携带机动车行驶证。机动车号牌应当按照规定悬挂并保持清晰、完整，不得故意遮挡、污损。任何单位和个人不得收缴、扣留机动车号牌。', 7, 1, '2021-12-19 00:53:12', '2021-12-19 00:53:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11548, '服用国家管制的精神药品可以短途驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第二十二条：饮酒、服用国家管制的精神药品或者麻醉药品，或者患有妨碍安全驾驶机动车的疾病，或者过度疲劳影响安全驾驶的，不得驾驶机动车。', 7, 1, '2021-12-19 00:53:13', '2021-12-19 00:53:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11549, '饮酒后只要不影响驾驶操作可以短距离驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十一条：饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。', 7, 1, '2021-12-19 00:53:14', '2021-12-19 00:53:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11550, '驾驶人在驾驶证丢失后3个月内还可以驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '驾驶证丢失相当于无证，无证不得驾驶机动车。', 7, 1, '2021-12-19 00:53:15', '2021-12-19 00:53:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11551, '驾驶人持超过有效期的驾驶证可以在1年内驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 7, 1, '2021-12-19 00:53:16', '2021-12-19 00:53:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11552, '驾驶人的驾驶证损毁后不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十八条：机动车驾驶人在机动车驾驶证丢失、损毁、超过有效期或者被依法扣留、暂扣期间以及记分达到12分的，不得驾驶机动车。', 7, 1, '2021-12-19 00:53:17', '2021-12-19 00:53:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11553, '记分满12分的驾驶人拒不参加学习和考试的将被公告驾驶证停止使用。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十五条：机动车驾驶人记分达到12分，拒不参加公安机关交通管理部门通知的学习，也不接受考试的，由公安机关交通管理部门公告其机动车驾驶证停止使用。', 7, 1, '2021-12-19 00:53:18', '2021-12-19 00:53:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11554, '驾驶人的机动车驾驶证被依法扣留、暂扣的情况下不得驾驶机动车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第二十五条：机动车驾驶人记分达到12分，拒不参加公安机关交通管理部门通知的学习，也不接受考试的，由公安机关交通管理部门公告其机动车驾驶证停止使用。', 7, 1, '2021-12-19 00:53:19', '2021-12-19 00:53:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11555, '交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', '', '2', '1', NULL, '', '', '', '', 'A', '全国统一的道路交通信号，包括交通信号灯、交通标志、交通标线和交通警察的指挥。', 7, 1, '2021-12-19 00:53:20', '2021-12-19 00:53:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11556, '交通标志和交通标线不属于交通信号。', '', '2', '1', NULL, '', '', '', '', 'B', '交通信号包括交通信号灯、交通标志、交通标线和交通警察的指挥。', 7, 1, '2021-12-19 00:53:21', '2021-12-19 00:53:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11557, '交通信号灯由红灯、绿灯和黄灯组成。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第二十六条：交通信号灯由红灯、绿灯、黄灯组成。红灯表示禁止通行，绿灯表示准许通行，黄灯表示警示。', 7, 1, '2021-12-19 00:53:22', '2021-12-19 00:53:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11558, '道路交通标线分为指示标线、警告标线、禁止标线。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第三十条：道路交通标线分为：指示标线、警告标线、禁止标线。', 7, 1, '2021-12-19 00:53:23', '2021-12-19 00:53:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11559, '在路口遇有交通信号灯和交通警察指挥不一致时，按照交通信号灯通行。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第三十八条：车辆、行人应当按照交通信号通行；遇有交通警察现场指挥时，应当按照交通警察的指挥通行；在没有交通信号的道路上，应当在确保安全、畅通的原则下通行。', 7, 1, '2021-12-19 00:53:24', '2021-12-19 00:53:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11560, '驾驶机动车不能进入红色叉形灯或者红色箭头灯亮的车道。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十条：车道信号灯表示：（一）绿色箭头灯亮时，准许本车道车辆按指示方向通行；（二）红色叉形灯或者箭头灯亮时，禁止本车道车辆通行。', 7, 1, '2021-12-19 00:53:25', '2021-12-19 00:53:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11561, '驾驶机动车在道路上超车时可以不使用转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例实施条例》第五十七条：机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:53:26', '2021-12-19 00:53:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11562, '驾驶机动车在道路上超车完毕驶回原车道时开启右转向灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例实施条例》第五十七条：机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:53:27', '2021-12-19 00:53:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11563, '驾驶机动车在道路上掉头时提前开启左转向灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例实施条例》第五十七条：机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:53:29', '2021-12-19 00:53:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11564, '驾驶机动车在道路上向右变更车道可以不使用转向灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例实施条例》第五十七条：机动车应当按照下列规定使用转向灯：（一）向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；（二）向右转弯、向右变更车道、超车完毕驶回原车道、靠路边停车时，应当提前开启右转向灯。', 7, 1, '2021-12-19 00:53:30', '2021-12-19 00:53:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11565, '驾驶机动车在沙尘天气条件下行车不用开启前照灯、示廓灯和后位灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 7, 1, '2021-12-19 00:53:31', '2021-12-19 00:53:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11566, '驾驶机动车在雾天行车开启雾灯和危险报警闪光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例实施条例》第五十八条： 机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 7, 1, '2021-12-19 00:53:32', '2021-12-19 00:53:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11567, '夜间驾驶机动车通过人行横道时需要交替使用远近光灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 7, 1, '2021-12-19 00:53:33', '2021-12-19 00:53:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11568, '驾驶机动车上坡时,在将要到达坡道顶端时要加速并鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'B', '在上坡应提前加速，交替使用远近光灯，快到坡顶时，要合理控制速度，不能加速，将远光灯关了，只用近光灯，鸣喇叭。', 7, 1, '2021-12-19 00:53:34', '2021-12-19 00:53:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11569, '驾驶机动车上道路行驶,不允许超过限速标志标明的最高时速。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十二条：机动车上道路行驶，不得超过限速标志标明的最高时速。在没有限速标志的路段，应当保持安全车速。', 7, 1, '2021-12-19 00:53:35', '2021-12-19 00:53:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11570, '驾驶机动车在没有中心线的城市道路上，最高速度不能超过每小时50公里。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十五条： 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 7, 1, '2021-12-19 00:53:36', '2021-12-19 00:53:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11571, '驾驶机动车在没有中心线的公路上，最高速度不能超过每小时70公里。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十五条： 机动车在道路上行驶不得超过限速标志、标线标明的速度。在没有限速标志、标线的道路上，机动车不得超过下列最高行驶速度： (一)没有道路中心线的道路，城市道路为每小时30公里，公路为每小时40公里； (二)同方向只有1条机动车道的道路，城市道路为每小时50公里，公路为每小时70公里。', 7, 1, '2021-12-19 00:53:37', '2021-12-19 00:53:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11572, '驾驶机动车掉头、转弯、下陡坡时的最高速度不能超过每小时40公里。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到掉头、转弯、下陡坡时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:53:38', '2021-12-19 00:53:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11573, '驾驶机动车通过窄路、窄桥时的最高速度不能超过每小时30公里。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十六条： 机动车行驶中遇到进出非机动车道，通过铁路道口、急弯路、窄路、窄桥时最高行驶速度不得超过每小时30公里。', 7, 1, '2021-12-19 00:53:39', '2021-12-19 00:53:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11574, '驾驶机动车超车应该提前开启左转向灯、变换使用远近光灯或鸣喇叭。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第四十七条： 应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。', 7, 1, '2021-12-19 00:53:40', '2021-12-19 00:53:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11575, '驾驶机动车超车后立即开启右转向灯驶回原车道。', '', '2', '1', NULL, '', '', '', '', 'B', '不能立即，应在与被超车辆拉开必要的安全距离后，开启右转向灯驶回原车道', 7, 1, '2021-12-19 00:53:41', '2021-12-19 00:53:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11576, '驾驶机动车行经城市没有列车通过的铁路道口时允许超车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:53:42', '2021-12-19 00:53:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11577, '驾驶机动车在隧道、陡坡等特殊路段不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十三条： 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车： （一）前车正在左转弯、掉头、超车的； （二）与对面来车有会车可能的； （三）前车为执行紧急任务的警车、消防车、救护车、工程救险车的； （四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。', 7, 1, '2021-12-19 00:53:43', '2021-12-19 00:53:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11578, '夜间驾驶机动车在窄路、窄桥会车时正确的做法是使用远光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十八条： 在没有中心隔离设施或者没有中心线的道路上，夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 7, 1, '2021-12-19 00:53:45', '2021-12-19 00:53:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11579, '驾驶机动车通过交叉路口要遵守交通信号。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十四条：机动车通过交叉路口，应当按照交通信号灯、交通标志、交通标线或者交通警察的指挥通过；通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通行的车辆先行。', 7, 1, '2021-12-19 00:53:46', '2021-12-19 00:53:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11580, '驾驶机动车在没有交通信号的路口要尽快通过。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十四条：通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通过的车辆先行。', 7, 1, '2021-12-19 00:53:47', '2021-12-19 00:53:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11581, '驾驶机动车在没有交通信号的路口遇到前方车辆缓慢行驶时要依次交替通行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第四十五条：在车道减少的路段、路口，或者在没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口遇到停车排队等候或者缓慢行驶时，机动车应当依次交替通行。', 7, 1, '2021-12-19 00:53:48', '2021-12-19 00:53:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11582, '驾驶机动车通过漫水路时要加速行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 7, 1, '2021-12-19 00:53:49', '2021-12-19 00:53:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11583, '驾驶机动车遇到漫水桥时要察明水情确认安全后再低速通过。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 7, 1, '2021-12-19 00:53:50', '2021-12-19 00:53:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11584, '机动车在道路上发生故障难以移动时要在车后50米以内设置警告标志。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 7, 1, '2021-12-19 00:53:51', '2021-12-19 00:53:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11585, '机动车在道路上发生轻微交通事故且妨碍交通时，不需移动。', '', '2', '1', NULL, '', '', '', '', 'B', '如果可以移动，应移动到安全位置，并且不要妨碍交通。', 7, 1, '2021-12-19 00:53:52', '2021-12-19 00:53:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11586, '机动车在夜间道路上发生故障难以移动时要开启危险报警闪光灯、示廓灯、后位灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十条：机动车在道路上发生故障或者发生交通事故，妨碍交通又难以移动的，应当按照规定开启危险报警闪光灯并在车后50米至100米处设置警告标志，夜间还应当同时开启示廓灯和后位灯。', 7, 1, '2021-12-19 00:53:53', '2021-12-19 00:53:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11587, '行车中遇到执行紧急任务的消防车、救护车、工程救险车时要及时让行。', '', '2', '1', NULL, '', '', '', '', 'A', '题干指明“执行紧急任务”，当然是应当让行啦！', 7, 1, '2021-12-19 00:53:54', '2021-12-19 00:53:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11588, '行车中遇到正在进行作业的道路养护车辆、工程作业车时要注意避让。', '', '2', '1', NULL, '', '', '', '', 'A', '他在为人民服务，我们还是要让一让的。', 7, 1, '2021-12-19 00:53:55', '2021-12-19 00:53:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11589, '在道路上临时停车不得妨碍其他车辆和行人通行。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十六条：在道路上临时停车的，不得妨碍其他车辆和行人通行。', 7, 1, '2021-12-19 00:53:56', '2021-12-19 00:53:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11590, '驾驶机动车找不到停车位时可以借人行道停放。', '', '2', '1', NULL, '', '', '', '', 'B', '停车不得占用人行道。', 7, 1, '2021-12-19 00:53:57', '2021-12-19 00:53:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11591, '距离交叉路口50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 7, 1, '2021-12-19 00:53:58', '2021-12-19 00:53:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11592, '社会车辆距离消防栓或者消防队(站)门前30米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条：公共汽车站、急救站、加油站、消防栓或者消防队(站)门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车。', 7, 1, '2021-12-19 00:54:00', '2021-12-19 00:54:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11593, '距离桥梁、陡坡、隧道50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 7, 1, '2021-12-19 00:54:01', '2021-12-19 00:54:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11594, '距离宽度不足4米的窄路50米以内的路段不能停车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十三条：交叉路口、铁路道口、急弯路、宽度不足4米的窄路、桥梁、陡坡、隧道以及距离上述地点50米以内的路段，不得停车。', 7, 1, '2021-12-19 00:54:02', '2021-12-19 00:54:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11595, '驾驶机动车在道路上发生交通事故要立即将车移到路边。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 7, 1, '2021-12-19 00:54:03', '2021-12-19 00:54:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11596, '驾驶人在发生交通事故后因抢救伤员变动现场时要标明位置。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 7, 1, '2021-12-19 00:54:04', '2021-12-19 00:54:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11597, '在道路上发生交通事故造成人身伤亡时，要立即抢救受伤人员并迅速报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第七十条：在道路上发生交通事故，车辆驾驶人应当立即停车，保护现场；造成人身伤亡的，车辆驾驶人应当立即抢救受伤人员，并迅速报告执勤的交通警察或者公安机关交通管理部门。因抢救受伤人员变动现场的，应当标明位置。乘车人、过往车辆驾驶人、过往行人应当予以协助。 在道路上发生交通事故，未造成人身伤亡，当事人对事实及成因无争议的，可以即行撤离现场，恢复交通，自行协商处理损害赔偿事宜；不即行撤离现场的，应当迅速报告执勤的交通警察或者公安机关交通管理部门。 在道路上发生交通事故，仅造成轻微财产损失，并且基本事实清楚的，当事人应当先撤离现场再进行协商处理。', 7, 1, '2021-12-19 00:54:05', '2021-12-19 00:54:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11598, '驾驶人违反交通运输管理法规发生重大事故致人重伤的可能判处3年以下徒刑或拘役。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:54:06', '2021-12-19 00:54:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11599, '驾驶人违反交通运输管理法规发生重大事故致人死亡的处3年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:54:07', '2021-12-19 00:54:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11600, '驾驶人违反交通运输管理法规发生重大事故使公私财产遭受重大损失的可能处3年以下徒刑或拘役。', '', '2', '1', NULL, '', '', '', '', 'A', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:54:08', '2021-12-19 00:54:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11601, '驾驶人违反交通运输管理法规发生重大事故后，逃逸或者有其他特别恶劣情节的，处7年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:54:09', '2021-12-19 00:54:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11602, '驾驶人违反交通运输管理法规发生重大事故后，因逃逸致人死亡的，处3年以上7年以下有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国刑法》第一百三十三条：违反交通运输管理法规，因而发生重大事故，致人重伤、死亡或者使公私财产遭受重大损失的，处三年以下有期徒刑或者拘役；交通运输肇事后逃逸或者有其他特别恶劣情节的，处三年以上七年以下有期徒刑；因逃逸致人死亡的，处七年以上有期徒刑。看清题目是因逃逸致人死亡的，不是先死亡再逃逸的，所以应该是7年以上。在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。有前款行为，同时构成其他犯罪的，依照处罚较重的规定定罪处罚。', 7, 1, '2021-12-19 00:54:10', '2021-12-19 00:54:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11603, '驾驶人在道路上驾驶机动车追逐竞驶，情节恶劣的处3年以下有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《中华人民共和国刑法修正案（八）》第二十二、在刑法第一百三十三条后增加一条，作为第一百三十三条之一：在道路上驾驶机动车追逐竞驶，情节恶劣的，或者在道路上醉酒驾驶机动车的，处拘役，并处罚金。', 7, 1, '2021-12-19 00:54:11', '2021-12-19 00:54:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11604, '驾驶人在道路上醉酒驾驶机动车的处3年以上有期徒刑。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十一条：饮酒后驾驶机动车的，处暂扣六个月机动车驾驶证，并处一千元以上二千元以下罚款。因饮酒后驾驶机动车被处罚，再次饮酒后驾驶机动车的，处十日以下拘留，并处一千元以上二千元以下罚款，吊销机动车驾驶证。 醉酒驾驶机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；五年内不得重新取得机动车驾驶证。饮酒后驾驶营运机动车的，处十五日拘留，并处五千元罚款，吊销机动车驾驶证，五年内不得重新取得机动车驾驶证。 醉酒驾驶营运机动车的，由公安机关交通管理部门约束至酒醒，吊销机动车驾驶证，依法追究刑事责任；十年内不得重新取得机动车驾驶证，重新取得机动车驾驶证后，不得驾驶营运机动车。饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。 饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 7, 1, '2021-12-19 00:54:12', '2021-12-19 00:54:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11605, '对未放置检验合格标志上道路行驶的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:54:13', '2021-12-19 00:54:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11606, '交通警察对未放置保险标志上道路行驶的车辆可依法扣留行驶证。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十五条：上道路行驶的机动车未悬挂机动车号牌，未放置检验合格标志、保险标志，或者未随车携带行驶证、驾驶证的，公安机关交通管理部门应当扣留机动车，通知当事人提供相应的牌证、标志或者补办相应手续，并可以依照本法第九十条的规定予以处罚。当事人提供相应的牌证、标志或者补办相应手续的，应当及时退还机动车。故意遮挡、污损或者不按规定安装机动车号牌的，依照本法第九十条的规定予以处罚。', 7, 1, '2021-12-19 00:54:14', '2021-12-19 00:54:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11607, '对有伪造或变造号牌、行驶证嫌疑的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 7, 1, '2021-12-19 00:54:15', '2021-12-19 00:54:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11608, '对有使用伪造或变造检验合格标志嫌疑的车辆，交通警察只进行罚款处罚。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处十日以下拘留，并处一千元以上三千元以下罚款；构成犯罪的，依法追究刑事责任。', 7, 1, '2021-12-19 00:54:16', '2021-12-19 00:54:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11609, '对使用其他车辆号牌、行驶证的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', 7, 1, '2021-12-19 00:54:18', '2021-12-19 00:54:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11610, '对未按照国家规定投保交强险的车辆，交通警察可依法予以扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十八条：机动车所有人、管理人未按照国家规定投保机动车第三者责任强制保险的，由公安机关交通管理部门扣留车辆至依照规定投保后，并处依照规定投保最低责任限额应缴纳的保险费的二倍罚款。', 7, 1, '2021-12-19 00:54:19', '2021-12-19 00:54:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11611, '对发生道路交通事故需要收集证据的事故车，交通警察可以依法扣留。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全违法行为处理程序规定》第二十五条规定：有下列情形之一的，依法扣留车辆：对发生道路交通事故，因收集证据需要的，可以依法扣留事故车辆。', 7, 1, '2021-12-19 00:54:20', '2021-12-19 00:54:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11612, '驾驶人有使用其他车辆号牌、行驶证嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '使用其他车辆的机动车登记证书、号牌、行驶证、检验合格标志、保险标志的，由公安机关交通管理部门予以收缴，扣留该机动车，处二千元以上五千元以下罚款。', 7, 1, '2021-12-19 00:54:21', '2021-12-19 00:54:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11613, '驾驶人有使用其他车辆检验合格标志嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '交通警察可依法扣留车辆，等他们查清楚车的来由，当场教导让其车在指定时间内接受车辆检验并放行。', 7, 1, '2021-12-19 00:54:22', '2021-12-19 00:54:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11614, '驾驶人有使用其他车辆保险标志嫌疑的，交通警察可依法扣留车辆。', '', '2', '1', NULL, '', '', '', '', 'A', '交通警察可依法扣留 车辆，等他们查清楚车的来由，当场教导让其车在指定时间内接受车辆检验并放行。', 7, 1, '2021-12-19 00:54:23', '2021-12-19 00:54:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11615, '驾驶人将机动车交给驾驶证被吊销的人驾驶的，交通警察依法扣留驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十九条：将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处吊销机动车驾驶证。', 7, 1, '2021-12-19 00:54:24', '2021-12-19 00:54:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11616, '驾驶人将机动车交给驾驶证被暂扣的人驾驶的，交通警察给予口头警告。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十九条：将机动车交由未取得机动车驾驶证或者机动车驾驶证被吊销、暂扣的人驾驶的，由公安机关交通管理部门处二百元以上二千元以下罚款，可以并处吊销机动车驾驶证。', 7, 1, '2021-12-19 00:54:25', '2021-12-19 00:54:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11617, '驾驶人驾驶有达到报废标准嫌疑机动车上路的，交通警察依法予以拘留。', '', '2', '1', NULL, '', '', '', '', 'B', '交警只能扣留车辆，还没达到拘留的地步。', 7, 1, '2021-12-19 00:54:26', '2021-12-19 00:54:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11618, '驾驶人在一个记分周期内累积记分达到12分的，交通警察依法扣留驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '正确的，所以要及时去处理扣分。', 7, 1, '2021-12-19 00:54:27', '2021-12-19 00:54:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11619, '发生交通事故造成人员受伤时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第八条：发生交通事故造成人员受伤时，要保护现场并立即报警。', 7, 1, '2021-12-19 00:54:28', '2021-12-19 00:54:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11620, '道路交通事故中，驾驶人有饮酒、醉酒嫌疑时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第八条：发生交通事故驾驶人有饮酒、醉酒嫌疑时，要保护现场并立即报警。', 7, 1, '2021-12-19 00:54:29', '2021-12-19 00:54:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11621, '道路交通事故中，机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第八条：发生交通事故机动车无号牌、检验合格标志、保险标志时，要保护现场并立即报警。', 7, 1, '2021-12-19 00:54:30', '2021-12-19 00:54:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11622, '驾驶机动车发生财产损失交通事故，当事人对事实及成因无争议的可先撤离现场。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 7, 1, '2021-12-19 00:54:31', '2021-12-19 00:54:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11623, '驾驶机动车发生财产损失交通事故后，当事人对事实及成因无争议移动车辆时需要对现场拍照或者标划停车位置。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：机动车与机动车、机动车与非机动车发生财产损失事故，当事人对事实及成因无争议的，可以自行协商处理损害赔偿事宜。车辆可以移动的，当事人应当在确保安全的原则下对现场拍照或者标划事故车辆现场位置后，立即撤离现场，将车辆移至不妨碍交通的地点，再进行协商。非机动车与非机动车或者行人发生财产损失事故，基本事实及成因清楚的，当事人应当先撤离现场，再协商处理损害赔偿事宜。', 7, 1, '2021-12-19 00:54:32', '2021-12-19 00:54:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11624, '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离的，交通警察不可以责令当事人撤离现场。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通事故处理程序规定》第十三条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 7, 1, '2021-12-19 00:54:33', '2021-12-19 00:54:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11625, '机动车发生财产损失交通事故，对应当自行撤离现场而未撤离造成交通堵塞的，可以对驾驶人处以200元罚款。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通事故处理程序规定》第十三条：对应当自行撤离现场而未撤离的，交通警察应当责令当事人撤离现场；造成交通堵塞的，对驾驶人处以200元罚款；驾驶人有其他道路交通安全违法行为的，依法一并处罚。', 7, 1, '2021-12-19 00:54:35', '2021-12-19 00:54:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11626, '酒后驾驶发生重大交通事故被依法追究刑事责任的人不能申请机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '饮酒后或者醉酒驾驶机动车发生重大交通事故，构成犯罪的，依法追究刑事责任，并由公安机关交通管理部门吊销机动车驾驶证，终生不得重新取得机动车驾驶证。', 7, 1, '2021-12-19 00:54:36', '2021-12-19 00:54:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11627, '造成交通事故后逃逸构成犯罪的人不能申请机动车驾驶证。', '', '2', '1', NULL, '', '', '', '', 'A', '吊销机动车证的为二年，撤消机动车证的为三年，以醉酒吊销五年，因逃跑而吊销是终身，简称“吊二撤三醉五逃终身”。', 7, 1, '2021-12-19 00:54:37', '2021-12-19 00:54:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11628, '驾驶人在驾驶证核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第四十九条：机动车驾驶人户籍迁出原车辆管理所管辖区的，应当向迁入地车辆管理所申请换证。机动车驾驶人在核发地车辆管理所管辖区以外居住的，可以向居住地车辆管理所申请换证。申请时应当填写申请表，并提交第四十八条：规定的证明、凭证。', 7, 1, '2021-12-19 00:54:38', '2021-12-19 00:54:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11629, '道路交通安全违法行为累积记分一个周期满分为12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第五十五条：道路交通安全违法行为累积记分周期（即记分周期）为12个月，满分为12分，从机动车驾驶证初次领取之日起计算。', 7, 1, '2021-12-19 00:54:39', '2021-12-19 00:54:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11630, '驾驶人记分没有达到满分，有罚款尚未缴纳的，记分转入下一记分周期。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条条例》第二十四条：机动车驾驶人在一个记分周期内记分未达到12分，所处罚款已经缴纳的，记分予以清除；记分虽未达到12分，但尚有罚款未缴纳的，记分转入下一记分周期。', 7, 1, '2021-12-19 00:54:40', '2021-12-19 00:54:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11631, '驾驶人在实习期内驾驶机动车时，应当在车身后部粘贴或者悬挂统一式样的实习标志。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十四条：在实习期内驾驶机动车的，应当在车身后部粘贴或者悬挂统一式样的实习标志。', 7, 1, '2021-12-19 00:54:41', '2021-12-19 00:54:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11632, '机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》第六十九条：机动车驾驶人在实习期内有记满12分记录的，注销其实习的准驾车型驾驶资格。', 7, 1, '2021-12-19 00:54:42', '2021-12-19 00:54:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11633, '伪造、变造或者使用伪造、变造驾驶证的驾驶人构成犯罪的，将依法追究刑事责任。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处十五日以下拘留，并处二千元以上五千元以下罚款；构成犯罪的，依法追究刑事责任。', 7, 1, '2021-12-19 00:54:43', '2021-12-19 00:54:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11634, '造成交通事故后逃逸，尚不构成犯罪的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》附件2，造成交通事故后逃逸，尚不构成犯罪的，一次记12分。凡是逃逸的均为12分。', 7, 1, '2021-12-19 00:54:44', '2021-12-19 00:54:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11635, '饮酒后驾驶机动车的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '酒后驾车扣12分，醉驾吊销驾照5年内不得考驾照。', 7, 1, '2021-12-19 00:54:45', '2021-12-19 00:54:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11636, '使用伪造、变造的驾驶证一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》附件2，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 7, 1, '2021-12-19 00:54:46', '2021-12-19 00:54:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11637, '使用其他机动车号牌、行驶证的一次记3分。', '', '2', '1', NULL, '', '', '', '', 'B', '《公安部令第123号》附件2，使用伪造、变造的机动车号牌、行驶证、驾驶证、校车标牌或者使用其他机动车号牌、行驶证的，一次记12分 。', 7, 1, '2021-12-19 00:54:47', '2021-12-19 00:54:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11638, '违反交通信号灯通行的一次记6分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》附件2，驾驶机动车违反道路交通信号灯通行的，一次记6分 。', 7, 1, '2021-12-19 00:54:48', '2021-12-19 00:54:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11639, '车速超过规定时速50%以上的一次记12分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》附件2，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 7, 1, '2021-12-19 00:54:49', '2021-12-19 00:54:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11640, '车速超过规定时速达到50%的一次记3分。', '', '2', '1', NULL, '', '', '', '', 'B', '《公安部令第123号》附件2，驾驶中型以上载客载货汽车、校车、危险物品运输车辆在高速公路、城市快速路上行驶超过规定时速20％以上或者在高速公路、城市快速路以外的道路上行驶超过规定时速50％以上，以及驾驶其他机动车行驶超过规定时速50%以上的，一次记12分。', 7, 1, '2021-12-19 00:54:50', '2021-12-19 00:54:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11641, '驾驶机动车不按照规定避让校车的，一次记6分。', '', '2', '1', NULL, '', '', '', '', 'A', '《公安部令第123号》附件2，驾驶机动车不按照规定避让校车的，一次记6分 。', 7, 1, '2021-12-19 00:54:52', '2021-12-19 00:54:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11642, '这个标志是何含义？', '/uploadFile/yuanBeiTu11642.gif', '1', '1', NULL, 'T型交叉路口', 'Y型交叉路口', '十字交叉路口', '环行交叉路口', 'C', '十字交叉路口：除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', 7, 1, '2021-12-19 00:54:53', '2021-12-19 00:54:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11643, '这个标志是何含义？', '/uploadFile/yuanBeiTu11643.gif', '1', '1', NULL, '.T型交叉路口', 'Y型交叉路口', '十字交叉路口', '环行交叉路口', 'A', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', 7, 1, '2021-12-19 00:54:54', '2021-12-19 00:54:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11644, '这个标志是何含义？', '/uploadFile/yuanBeiTu11644.gif', '1', '1', NULL, '向左急转弯', '向右急转弯', '向右绕行', '连续弯路', 'B', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 7, 1, '2021-12-19 00:54:55', '2021-12-19 00:54:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11645, '这个标志是何含义？', '/uploadFile/yuanBeiTu11645.gif', '1', '1', NULL, '向左急转弯', '向右急转弯', '向左绕行', '连续弯路', 'A', '向左急转弯：向左急弯路标志设在左急转弯的道路前方适当位置。', 7, 1, '2021-12-19 00:54:56', '2021-12-19 00:54:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11646, '这个标志是何含义？', '/uploadFile/yuanBeiTu11646.gif', '1', '1', NULL, 'N型弯路', '急转弯路', '反向弯路', '连续弯路', 'C', '此标志表示反向弯路。警告标志，提醒车辆与行人注意。', 7, 1, '2021-12-19 00:54:57', '2021-12-19 00:54:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11647, '这个标志是何含义？', '/uploadFile/yuanBeiTu11647.gif', '1', '1', NULL, 'N型弯路', '急转弯路', '反向弯路', '连续弯路', 'D', '连续转弯，警告标志。', 7, 1, '2021-12-19 00:54:58', '2021-12-19 00:54:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11648, '这个标志是何含义？', '/uploadFile/yuanBeiTu11648.gif', '1', '1', NULL, '堤坝路', '上陡坡', '连续上坡', '下陡坡', 'B', '上陡坡：用以提醒车辆驾驶人小心驾驶。', 7, 1, '2021-12-19 00:54:59', '2021-12-19 00:54:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11649, '这个标志是何含义？', '/uploadFile/yuanBeiTu11649.gif', '1', '1', NULL, '堤坝路', '上陡坡', '下陡坡', '连续上坡', 'C', '下陡坡：用以提醒车辆驾驶人小心驾驶。', 7, 1, '2021-12-19 00:55:00', '2021-12-19 00:55:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11650, '这个标志是何含义？', '/uploadFile/yuanBeiTu11650.gif', '1', '1', NULL, '连续上坡', '上陡坡', '下陡坡', '连续下坡', 'D', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3 km后，应重复设置。', 7, 1, '2021-12-19 00:55:02', '2021-12-19 00:55:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11651, '这个标志是何含义？', '/uploadFile/yuanBeiTu11651.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '宽度变窄', 'A', '此标志表示两侧变窄，内部有两条线向内变窄。', 7, 1, '2021-12-19 00:55:03', '2021-12-19 00:55:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11652, '这个标志是何含义？', '/uploadFile/yuanBeiTu11652.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '宽度变窄', 'B', '右侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6 m以下的路段起点前方。', 7, 1, '2021-12-19 00:55:04', '2021-12-19 00:55:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11653, '这个标志是何含义？', '/uploadFile/yuanBeiTu11653.gif', '1', '1', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '宽度变窄', 'C', '警告标志，左侧变窄。', 7, 1, '2021-12-19 00:55:05', '2021-12-19 00:55:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11654, '这个标志是何含义？', '/uploadFile/yuanBeiTu11654.gif', '1', '1', NULL, '窄路', '右侧变窄', '左侧变窄', '窄桥', 'D', '窄路和窄桥的区别是：窄路图标没有上面放宽的那块，窄路是行道变窄，窄桥是一段变窄，然后路又变宽。', 7, 1, '2021-12-19 00:55:06', '2021-12-19 00:55:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11655, '这个标志是何含义？', '/uploadFile/yuanBeiTu11655.gif', '1', '1', NULL, '人行横道', '注意行人', '注意儿童', '学校区域', 'B', '看清楚标志牌的颜色，黄色的为注意行人标志，蓝色底的为人行横道。', 7, 1, '2021-12-19 00:55:07', '2021-12-19 00:55:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11656, '这个标志是何含义？', '/uploadFile/yuanBeiTu11656.gif', '1', '1', NULL, '注意行人', '人行横道', '注意儿童', '学校区域', 'C', '注意儿童：用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', 7, 1, '2021-12-19 00:55:08', '2021-12-19 00:55:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11657, '这个标志是何含义？', '/uploadFile/yuanBeiTu11657.gif', '1', '1', NULL, '交叉路口', '注意信号灯', '注意行人', '人行横道灯', 'B', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 7, 1, '2021-12-19 00:55:09', '2021-12-19 00:55:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11658, '这个标志是何含义？', '/uploadFile/yuanBeiTu11658.gif', '1', '1', NULL, '注意行人', '有人行横道', '村庄或集镇', '有小学校', 'C', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', 7, 1, '2021-12-19 00:55:10', '2021-12-19 00:55:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11659, '这个标志是何含义？', '/uploadFile/yuanBeiTu11659.gif', '1', '1', NULL, '路面低洼', '驼峰桥', '路面不平', '路面高突', 'C', '一个坡是高突，一个坑是低洼，一个凸中间有空间是驼峰桥，两个凸肯定是不平。', 7, 1, '2021-12-19 00:55:11', '2021-12-19 00:55:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11660, '这个标志是何含义？', '/uploadFile/yuanBeiTu11660.gif', '1', '1', NULL, '路面低洼', '驼峰桥', '路面不平', '路面高突', 'D', '一个坡是高突，一个坑是低洼，一个凸中间有空间是驼峰桥，两个凸肯定是不平。', 7, 1, '2021-12-19 00:55:13', '2021-12-19 00:55:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11661, '这个标志是何含义？', '/uploadFile/yuanBeiTu11661.gif', '1', '1', NULL, '路面高突', '有驼峰桥', '路面不平', '路面低洼', 'D', '一个坡是高突，一个坑是低洼，一个凸中间有空间是驼峰桥，两个凸肯定是不平。', 7, 1, '2021-12-19 00:55:14', '2021-12-19 00:55:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11662, '这个标志是何含义？', '/uploadFile/yuanBeiTu11662.gif', '1', '1', NULL, '过水路面', '渡口', '泥泞道路', '低洼路面', 'A', '过水路面指的是：通过平时无水或流水很少的宽浅河流，而修筑的在洪水期间容许水流浸过的路面。', 7, 1, '2021-12-19 00:55:15', '2021-12-19 00:55:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11663, '这个标志是何含义？', '/uploadFile/yuanBeiTu11663.gif', '1', '1', NULL, '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', 'B', '有火车形状的，为无人看守的铁路口，此图为有人看守的铁路口。', 7, 1, '2021-12-19 00:55:16', '2021-12-19 00:55:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11664, '这个标志是何含义？', '/uploadFile/yuanBeiTu11664.gif', '1', '1', NULL, '多股铁路与道路相交', '有人看守铁路道口', '无人看守铁路道口', '注意长时鸣喇叭', 'C', '有火车形状的，为无人看守的铁路口，还有一个栅栏形式的图片为有人看守的铁路口。', 7, 1, '2021-12-19 00:55:17', '2021-12-19 00:55:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11665, '这个标志是何含义？', '/uploadFile/yuanBeiTu11665.gif', '1', '1', NULL, '避让非机动车', '非机动车道', '禁止非机动车通行', '注意非机动车', 'D', '黄底标志为警告标志，自行车是非机动车所以答案是注意非机动车。', 7, 1, '2021-12-19 00:55:18', '2021-12-19 00:55:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11666, '这个标志是何含义？', '/uploadFile/yuanBeiTu11666.gif', '1', '1', NULL, '注意残疾人', '残疾人出入口', '残疾人休息处', '残疾人专用通道', 'A', '黄底标志为警告标志，所以是注意残疾人。', 7, 1, '2021-12-19 00:55:19', '2021-12-19 00:55:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11667, '这个标志是何含义？', '/uploadFile/yuanBeiTu11667.gif', '1', '1', NULL, '施工路段', '事故易发路段', '减速慢行路段', '拥堵路段', 'B', '此标志表示慢行，用以提醒车辆驾驶人减速慢行。此标志设在前方需要减速慢行的路段以前适当位置。', 7, 1, '2021-12-19 00:55:20', '2021-12-19 00:55:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11668, '这个标志是何含义？', '/uploadFile/yuanBeiTu11668.gif', '1', '1', NULL, '施工路段', '车多路段', '慢行', '拥堵路段', 'C', '此标志表示慢行，用以题型车辆驾驶人减速慢行。此标志设在前方需要减速慢行的路段以前适当位置。', 7, 1, '2021-12-19 00:55:21', '2021-12-19 00:55:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11669, '这个标志是何含义？', '/uploadFile/yuanBeiTu11669.gif', '1', '1', NULL, '事故多发路段', '减速慢行', '注意危险', '拥堵路段', 'C', '注意危险：用以促使车辆驾驶员谨慎慢行。', 7, 1, '2021-12-19 00:55:22', '2021-12-19 00:55:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11670, '这个标志是何含义？', '/uploadFile/yuanBeiTu11670.gif', '1', '1', NULL, '不准车辆驶入', '不准长时间停车', '停车让行', '不准临时停车', 'C', '停车让行：表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', 7, 1, '2021-12-19 00:55:24', '2021-12-19 00:55:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11671, '这个标志是何含义？', '/uploadFile/yuanBeiTu11671.gif', '1', '1', NULL, '不准让行', '会车让行', '停车让行', '减速让行', 'D', '三角型的是减速让行，八角行的是停车让行，此标志表示减速让行，车辆应该减速让行，告示车辆驾驶人应慢行或停车，观察干道行车情况，在确保干道车辆优先，确保安全的前提下，方可进入路口。', 7, 1, '2021-12-19 00:55:25', '2021-12-19 00:55:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11672, '这个标志是何含义？', '/uploadFile/yuanBeiTu11672.gif', '1', '1', NULL, '禁止驶入', '禁止通行', '减速行驶', '限时进入', 'B', '此标志表示禁止通行，禁止一切车辆和行人通行。', 7, 1, '2021-12-19 00:55:26', '2021-12-19 00:55:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11673, '这个标志是何含义？', '/uploadFile/yuanBeiTu11673.gif', '1', '1', NULL, '禁止向左转弯', '禁止驶入左车道', '禁止车辆掉头', '禁止向左变道', 'A', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置。', 7, 1, '2021-12-19 00:55:27', '2021-12-19 00:55:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11674, '这个标志是何含义？', '/uploadFile/yuanBeiTu11674.gif', '1', '1', NULL, '禁止驶入路口', '禁止向右转弯', '禁止车辆掉头', '禁止变更车道', 'B', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 7, 1, '2021-12-19 00:55:28', '2021-12-19 00:55:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11675, '这个标志是何含义？', '/uploadFile/yuanBeiTu11675.gif', '1', '1', NULL, '禁止掉头', '禁止向右转弯', '禁止直行', '禁止向左转弯', 'C', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', 7, 1, '2021-12-19 00:55:29', '2021-12-19 00:55:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11676, '这个标志是何含义？', '/uploadFile/yuanBeiTu11676.gif', '1', '1', NULL, '禁止在路口掉头', '禁止向左向右变道', '禁止车辆直行', '禁止向左向右转弯', 'D', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', 7, 1, '2021-12-19 00:55:30', '2021-12-19 00:55:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11677, '这个标志是何含义？', '/uploadFile/yuanBeiTu11677.gif', '1', '1', NULL, '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'A', '禁止直行和向左转弯，表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', 7, 1, '2021-12-19 00:55:31', '2021-12-19 00:55:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11678, '这个标志是何含义？', '/uploadFile/yuanBeiTu11678.gif', '1', '1', NULL, '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'D', '禁止直行和向右转弯：表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。', 7, 1, '2021-12-19 00:55:32', '2021-12-19 00:55:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11679, '这个标志是何含义？', '/uploadFile/yuanBeiTu11679.gif', '1', '1', NULL, '禁止直行', '禁止掉头', '禁止变道', '禁止左转', 'B', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 7, 1, '2021-12-19 00:55:34', '2021-12-19 00:55:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11680, '这个标志是何含义？', '/uploadFile/yuanBeiTu11680.gif', '1', '1', NULL, '禁止借道', '禁止变道', '禁止超车', '禁止掉头', 'C', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 7, 1, '2021-12-19 00:55:35', '2021-12-19 00:55:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11681, '这个标志是何含义？', '/uploadFile/yuanBeiTu11681.gif', '1', '1', NULL, '解除禁止借道', '解除禁止变道', '准许变道行驶', '解除禁止超车', 'D', '解除禁止超车：表示禁止超车路段结束。此标志设在禁止超车的终点。', 7, 1, '2021-12-19 00:55:36', '2021-12-19 00:55:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11682, '这个标志是何含义？', '/uploadFile/yuanBeiTu11682.gif', '1', '1', NULL, '允许长时停车', '允许临时停车', '禁止长时停车', '禁止停放车辆', 'D', '禁止停放车辆：表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 7, 1, '2021-12-19 00:55:38', '2021-12-19 00:55:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11683, '这个标志是何含义？', '/uploadFile/yuanBeiTu11683.gif', '1', '1', NULL, '允许长时停车', '禁止临时停车', '禁止长时停车', '禁止停放车辆', 'C', '禁止长时停车：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', 7, 1, '2021-12-19 00:55:39', '2021-12-19 00:55:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11684, '这个标志是何含义？', '/uploadFile/yuanBeiTu11684.gif', '1', '1', NULL, '禁止长时鸣喇叭', '断续鸣喇叭', '禁止鸣喇叭', '减速鸣喇叭', 'C', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 7, 1, '2021-12-19 00:55:40', '2021-12-19 00:55:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11685, '这个标志是何含义？', '/uploadFile/yuanBeiTu11685.gif', '1', '1', NULL, '限制40吨轴重', '限制最高时速40公里', '前方40米减速', '最低时速40公里', 'B', '限制最高时速40公里：表示该标志至前方解除限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 7, 1, '2021-12-19 00:55:41', '2021-12-19 00:55:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11686, '这个标志是何含义？', '/uploadFile/yuanBeiTu11686.gif', '1', '1', NULL, '40米减速行驶路段', '最低时速40公里', '解除时速40公里限制', '最高时速40公里', 'C', '解除时速40公里限制：表示限制速度路段结束。此标志设在限制车辆速度路段的终点。', 7, 1, '2021-12-19 00:55:42', '2021-12-19 00:55:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11687, '这个标志是何含义？', '/uploadFile/yuanBeiTu11687.gif', '1', '1', NULL, '边防检查', '禁止通行', '海关检查', '停车检查', 'D', '停车检查：表示机动车必须停车接受检查。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。标志中可加注说明检查事项。', 7, 1, '2021-12-19 00:55:43', '2021-12-19 00:55:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11688, '这个标志是何含义？', '/uploadFile/yuanBeiTu11688.gif', '1', '1', NULL, '直行车道', '只准直行', '单行路', '禁止直行', 'B', '此标志表示一切车辆只准直行。', 7, 1, '2021-12-19 00:55:44', '2021-12-19 00:55:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11689, '这个标志是何含义？', '/uploadFile/yuanBeiTu11689.gif', '1', '1', NULL, '直行车道', '单行路', '向左转弯', '禁止直行', 'C', '向左转弯：表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 7, 1, '2021-12-19 00:55:45', '2021-12-19 00:55:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11690, '这个标志是何含义？', '/uploadFile/yuanBeiTu11690.gif', '1', '1', NULL, '直行车道', '只准直行', '单行路', '向右转弯', 'D', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 7, 1, '2021-12-19 00:55:46', '2021-12-19 00:55:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11691, '这个标志是何含义？', '/uploadFile/yuanBeiTu11691.gif', '1', '1', NULL, '直行和向右转弯', '直行和向左转弯', '禁止直行和向右转弯', '只准向左和向右转弯', 'A', '直行和向右转弯：表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯的路口以前适当位置。', 7, 1, '2021-12-19 00:55:48', '2021-12-19 00:55:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11692, '这个标志是何含义？', '/uploadFile/yuanBeiTu11692.gif', '1', '1', NULL, '直行和向右转弯', '直行和向左转弯', '禁止直行和向左转弯', '只准向右和向左转弯', 'B', '直行和向左转弯：表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 7, 1, '2021-12-19 00:55:49', '2021-12-19 00:55:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11693, '这个标志是何含义？', '/uploadFile/yuanBeiTu11693.gif', '1', '1', NULL, '禁止向右转弯', '禁止向左转弯', '向左和向右转弯', '禁止向左右转弯', 'C', '向左和向右转弯：表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', 7, 1, '2021-12-19 00:55:50', '2021-12-19 00:55:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11694, '这个标志是何含义？', '/uploadFile/yuanBeiTu11694.gif', '1', '1', NULL, '靠道路右侧停车', '只准向右转弯', '右侧是下坡路段', '靠右侧道路行驶', 'D', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', 7, 1, '2021-12-19 00:55:51', '2021-12-19 00:55:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11695, '这个标志是何含义？', '/uploadFile/yuanBeiTu11695.gif', '1', '1', NULL, '靠左侧道路行驶', '只准向左转弯', '左侧是下坡路段', '靠道路左侧停车', 'A', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 7, 1, '2021-12-19 00:55:52', '2021-12-19 00:55:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11696, '这个标志是何含义？', '/uploadFile/yuanBeiTu11696.gif', '1', '1', NULL, '右侧通行', '左侧通行', '向右行驶', '环岛行驶', 'D', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', 7, 1, '2021-12-19 00:55:53', '2021-12-19 00:55:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11697, '这个标志是何含义？', '/uploadFile/yuanBeiTu11697.gif', '1', '1', NULL, '应当鸣喇叭', '禁止鸣喇叭', '禁止鸣高音喇叭', '禁止鸣低音喇叭', 'A', '此标志表示机动车行至该标志处应鸣喇叭，以提醒对面车辆驾驶人注意并减速慢行。一般在公路的急转弯处，陡坡等视野不良路段的起点。', 7, 1, '2021-12-19 00:55:54', '2021-12-19 00:55:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11698, '这个标志是何含义？', '/uploadFile/yuanBeiTu11698.gif', '1', '1', NULL, '人行横道', '学生通道', '儿童通道', '注意行人', 'A', '此标志表示该处为人行横道。注意行人标志是三角形黄色框包围的。人行横道标志是蓝色的正方形，在里面有一个白色的三角形，同样有黑线和一个人向左在走，这样多种形状、蓝底、白图案的属于指示车辆、行人进行的标志。', 7, 1, '2021-12-19 00:55:55', '2021-12-19 00:55:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11699, '这个标志是何含义？', '/uploadFile/yuanBeiTu11699.gif', '1', '1', NULL, '禁止小型车行驶', '机动车行驶', '只准小型车行驶', '只准小型车行驶', 'B', '此标志表示该道路只供机动车行驶。看到小汽车你应该想到的是机动车行驶。', 7, 1, '2021-12-19 00:55:56', '2021-12-19 00:55:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11700, '这个标志是何含义？', '/uploadFile/yuanBeiTu11700.gif', '1', '1', NULL, '小型车车道', '小型车专用车道', '机动车车道', '多乘员车辆专用车道', 'C', '此标志表示该车道只供机动车行驶。比多乘员车辆专用车道少俩人。', 7, 1, '2021-12-19 00:55:57', '2021-12-19 00:55:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11701, '这个标志是何含义？', '/uploadFile/yuanBeiTu11701.gif', '1', '1', NULL, '非机动车停车位', '电动自行车行驶', '非机动车停放区', '非机动车行驶', 'D', '此标志表示该道路只供非机动车行驶。行驶的标志是用圆圈表示，而停车位是用矩形。', 7, 1, '2021-12-19 00:55:59', '2021-12-19 00:55:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11702, '这个标志是何含义？', '/uploadFile/yuanBeiTu11702.gif', '1', '1', NULL, '非机动车车道', '禁止自行车通行车道', '自行车专用车道', '停放自行车路段', 'A', '此图为非机动车车道，别误以为自行车专用车道，没有自行车专用车道这一说。', 7, 1, '2021-12-19 00:56:00', '2021-12-19 00:56:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11703, '这个标志是何含义？', '/uploadFile/yuanBeiTu11703.gif', '1', '1', NULL, '掉头', '倒车', '左转', '绕行', 'A', '此标志表示允许掉头。', 7, 1, '2021-12-19 00:56:01', '2021-12-19 00:56:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11704, '出车前对轮胎进行哪些方面的检查？', '', '1', '1', NULL, '什么也不用检查', '轮胎有没有清洗', '备胎在什么位置', '轮胎的紧固和气压', 'D', '对轮胎的检查是为了确保在行车过程中能安全行驶，由此本题应选“轮胎的紧固和气压”。', 7, 1, '2021-12-19 00:56:02', '2021-12-19 00:56:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11705, '会车中道路一侧有障碍，双方机动车应如何做？', '', '1', '1', NULL, '无障碍一方让对向先行', '速度慢的让速度快的先行', '有障碍的一方让对向先行', '速度快的让速度慢的先行', 'C', '有障碍的让无障碍的一方先行；但有障碍的一方已经驶入有障碍路段而无障碍一方未驶入的，有障碍的一方先行。', 7, 1, '2021-12-19 00:56:03', '2021-12-19 00:56:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11706, '机动车在狭窄的坡路会车时，正确的会车方法是什么？', '', '1', '1', NULL, '下坡车让上坡车', '坡顶交会时距离坡顶远的一方让行', '上坡车让下坡车', '下坡车已行至中途而上坡车未上坡时，让上坡车', 'A', '《道路交通安全法实施条例》第四十八条： 在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。图中下坡的车已经到了中途，可以先行。', 7, 1, '2021-12-19 00:56:04', '2021-12-19 00:56:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11707, '超车时前方机动车不减速、不让道，驾驶人怎么办？', '', '1', '1', NULL, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', 'C', '安全第一，文明行车。', 7, 1, '2021-12-19 00:56:05', '2021-12-19 00:56:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11708, '超车时，发现前方机动车正在超车，驾驶人怎么办？', '', '1', '1', NULL, '紧跟其后，伺机超越', '加速强行超越', '连续鸣喇叭催前车让路', '停止超车，让前方机动车先超车', 'D', '若强行超车，很有可能发生危险。只有在前车完成超车后，才能看清前方道路的状况，此时超车也才较为安全。', 7, 1, '2021-12-19 00:56:06', '2021-12-19 00:56:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11709, '机动车在雨天临时停车时，应开启什么灯？', '', '1', '1', NULL, '前后防雾灯', '危险报警闪光灯', '前大灯', '倒车灯', 'B', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用一说。', 7, 1, '2021-12-19 00:56:07', '2021-12-19 00:56:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11710, '机动车在雾天临时停车时，应开启什么灯？', '', '1', '1', NULL, '危险报警闪光灯、示廓灯和后位灯', '左转向灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'A', '雾天开车视野不良，因此若要停车，就应该开启危险报警闪光灯，提醒其他车辆注意。', 7, 1, '2021-12-19 00:56:08', '2021-12-19 00:56:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11711, '机动车在夜间临时停车时，应开启什么灯？', '', '1', '1', NULL, '前后防雾灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'C', '夜间行驶时视野不良，因此若要停车，应当开启危险报警闪光灯，提醒其他车辆注意。', 7, 1, '2021-12-19 00:56:09', '2021-12-19 00:56:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11712, '机动车在雪天临时停车时，应开启什么灯？', '', '1', '1', NULL, '前后防雾灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', 'D', '雪天开车视野不良，因此若要停车，就应该开启危险报警闪光灯、示廓灯和后位灯，提醒其他车辆注意。', 7, 1, '2021-12-19 00:56:10', '2021-12-19 00:56:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11713, '机动车停车的错误做法是什么？', '', '1', '1', NULL, '应当在规定地点停放', '禁止在人行道上停放', '在道路上临时停车时，不得妨碍其他机动车和行人通行', '可以停放在非机动车道上', 'D', '注意本题考的是错误的做法，机动车不能停放在非机动车道上。', 7, 1, '2021-12-19 00:56:11', '2021-12-19 00:56:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11714, '夜间驾驶机动车在窄路或者窄桥遇自行车对向驶来时，要怎样使用灯光？', '', '1', '1', NULL, '连续变换远、近光灯', '使用示廓灯', '使用远光灯', '使用近光灯', 'D', '根据《道路交通安全法实施条例》第四十八条：在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 7, 1, '2021-12-19 00:56:12', '2021-12-19 00:56:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11715, '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', '', '1', '1', NULL, '前后雾灯', '近光灯', '远光灯', '危险报警闪光灯', 'B', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。', 7, 1, '2021-12-19 00:56:13', '2021-12-19 00:56:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11716, '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？', '', '1', '1', NULL, '使用远光灯', '使用近光灯', '使用危险报警闪光灯', '交替使用远近光灯示意', 'D', '《道路交通安全法实施条例》第五十九条：机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 7, 1, '2021-12-19 00:56:15', '2021-12-19 00:56:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11717, '夜间驾驶机动车在照明条件良好的路段跟车行驶怎样使用灯光？', '', '1', '1', NULL, '关闭前照灯', '使用远光灯', '关闭所有车灯', '使用近光灯', 'D', '据《道路交通安全法实施条例》第四十八条：在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 7, 1, '2021-12-19 00:56:16', '2021-12-19 00:56:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11718, '机动车驶入双向行驶隧道前，要如何使用灯光？', '', '1', '1', NULL, '开启危险报警闪光灯', '开启远光灯', '开启雾灯', '开启近光灯', 'D', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', 7, 1, '2021-12-19 00:56:17', '2021-12-19 00:56:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11719, '行车中遇抢救伤员的救护车从本车道逆向驶来时，要怎样做？', '', '1', '1', NULL, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', 'A', '遇到正在执行任务的特种车辆，应当予以让行。而本题的救护车正是因为抢救伤员而逆行，因此其他车辆要给予方便，为其让行。因此正确的做法是靠边减速或停车让行。', 7, 1, '2021-12-19 00:56:18', '2021-12-19 00:56:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11720, '驾驶机动车在雨天遇到撑雨伞和穿雨衣的行人在路边行走怎样礼让？', '', '1', '1', NULL, '以正常速度行驶', '临近鸣喇叭示意', '加速从左侧绕行', '提前减速鸣喇叭', 'D', '首先下雨天本来视野就不好，其次行人这样的着装很难在第一时间意识到周围的交通状况变化。因此驾驶人在遇到此类行人时，应当先鸣喇叭示意，再以较低的车速通过。', 7, 1, '2021-12-19 00:56:19', '2021-12-19 00:56:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11721, '驾驶机动车变更车道时，属于交通陋习的是什么行为？', '', '1', '1', NULL, '提前开启转向灯', '仔细观察后变更车道', '随意并线', '不得妨碍其他车道正常行驶的车', 'C', '随意向车外抛洒物品，会影响其他车辆等的行驶，也不环保卫生。', 7, 1, '2021-12-19 00:56:20', '2021-12-19 00:56:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11722, '驾驶人边驾车边吸烟的做法有什么影响？', '', '1', '1', NULL, '妨碍安全驾驶', '可提高注意力', '可缓解驾驶疲劳', '不影响驾驶操作', 'A', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，另外缓解疲劳没有根据。', 7, 1, '2021-12-19 00:56:21', '2021-12-19 00:56:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11723, '下长坡时，控制车速的正确方法是什么？', '', '1', '1', NULL, '空挡滑行', '挂低速挡', '踏下离合器踏板滑行', '使用驻车制动器', 'B', '下坡时不能空挡滑行，也不能塌下离合器踏板滑行，而驻车制动是在停车后用的。', 7, 1, '2021-12-19 00:56:22', '2021-12-19 00:56:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11724, '下长坡连续使用行车制动会造成什么不良后果？', '', '1', '1', NULL, '缩短发动机使用寿命', '驾驶人容易疲劳', '容易造成机动车倾翻', '制动器制动效果下降', 'D', '持续用刹车，会造成刹车片发热，从而制动力减小，严重的会丧失制动，所以长下坡要用低档，用发动机制动。', 7, 1, '2021-12-19 00:56:23', '2021-12-19 00:56:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11725, '驾驶机动车在山区道路怎样跟车行驶？', '', '1', '1', NULL, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', 'B', '山区道路复杂多变，因此跟车时要非常注意，需加大安全距离。', 7, 1, '2021-12-19 00:56:24', '2021-12-19 00:56:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11726, '山区上坡路段跟车过程中遇前车停车时怎么办？', '', '1', '1', NULL, '从前车两侧超越', '紧跟前车后停车', '保持大距离停车', '连续鸣喇叭提示', 'C', '上坡路段停车的车辆容易出现后溜的情况，因此后车在跟车时要加大跟车距离，确保安全。', 7, 1, '2021-12-19 00:56:25', '2021-12-19 00:56:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11727, '下长坡控制车速最安全的方法是什么？', '', '1', '1', NULL, '挂入空挡滑行', '踏下离合器滑行', '利用发动机制动', '持续踏制动踏板', 'C', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 7, 1, '2021-12-19 00:56:26', '2021-12-19 00:56:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11728, '驾驶机动车上坡行驶如何保持充足动力？', '', '1', '1', NULL, '在车速下降前减挡', '在车速下降后减挡', '在车速过低时减挡', '尽量使用越级减挡', 'A', '车速慢了再换挡不容易前进甚至会熄火，造成车掉下坡来。', 7, 1, '2021-12-19 00:56:27', '2021-12-19 00:56:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11729, '机动车驶近坡道顶端等影响安全视距的路段时，要如何保证安全？', '', '1', '1', NULL, '快速通过', '使用危险报警闪光灯', '减速慢行并鸣喇叭示意', '随意通行', 'C', '注意“影响安全视距的路段”，也就是说在此路段行驶时无法完全掌握路况，因此要减速慢行谨慎小心，同时鸣喇叭告知其他车辆。', 7, 1, '2021-12-19 00:56:28', '2021-12-19 00:56:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11730, '夜间驾驶机动车超车遇前车不让路时怎样处置？', '', '1', '1', NULL, '连续鸣喇叭提示', '开远光灯尾随行驶', '保持距离等待让行', '连续变换前照灯远、近光', 'C', '遇到前车不让路时，只能等到前车让路了才能超车，否则容易发生危险。', 7, 1, '2021-12-19 00:56:29', '2021-12-19 00:56:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11731, '夜间会车前，两车在相距150米之外交替变换前照灯远近光的作用是什么？', '', '1', '1', NULL, '会车前两车之间相互提示', '驾驶操作的习惯行为', '便于双方观察前方情况', '驾驶人之间的一种礼节', 'C', '夜间会车，由于视线受限的关系，必须借助灯光等了解周围情况，因此这么做是为了了解情况。', 7, 1, '2021-12-19 00:56:30', '2021-12-19 00:56:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11732, '机动车在夜间行驶如何保证安全？', '', '1', '1', NULL, '以最高设计车速行驶', '降低速度，谨慎驾驶', '保持现有速度行驶', '以超过规定的最高车速行驶', 'B', '由于夜间行驶视野不良，因此要降低车速行驶，同时要注意观察周围情况。', 7, 1, '2021-12-19 00:56:32', '2021-12-19 00:56:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11733, '驾驶机动车在雨天临时停车注意什么？', '', '1', '1', NULL, '开启危险报警闪光灯', '开启前后雾灯', '开启近光灯', '在车后设置警告标志', 'A', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用。', 7, 1, '2021-12-19 00:56:33', '2021-12-19 00:56:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11734, '当机动车在湿滑路面上行驶时，路面附着力随着车速的增加如何变化？', '', '1', '1', NULL, '急剧增大', '逐渐增大', '急剧减小', '没有变化', 'C', '湿滑路面就是路面又湿又滑，即很易滑，因此车子与地面的抓地力就会变小。', 7, 1, '2021-12-19 00:56:34', '2021-12-19 00:56:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11735, '在冰雪路面上减速或停车，要怎样降低车速？', '', '1', '1', NULL, '充分利用行车制动器', '充分利用发动机的牵制作用', '充分利用驻车制动器', '充分利用缓速器', 'B', '发动机制动有三个显著特点：一是由于差速器的作用，可将制动力矩平均地分配在左右车轮上，减少侧滑、甩尾的可能性；二是有效地减少脚制动的使用频率，避免因长时间使用制动器，导致制动器摩擦片的温度升高，使制动力下降，甚至失去作用；三是车速始终被限定在一定范围内，有利于及时降速或停车，确保行车安全。', 7, 1, '2021-12-19 00:56:35', '2021-12-19 00:56:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11736, '驾驶机动车在冰雪路面怎样跟车行驶？', '', '1', '1', NULL, '保持较大的安全距离', '开启危险报警闪光灯', '不断变换前照灯远近光', '适时鸣喇叭提示前车', 'A', '冰雪路面易滑，因此在跟车时要加大跟车距离。', 7, 1, '2021-12-19 00:56:36', '2021-12-19 00:56:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11737, '驾驶机动车在结冰的道路上怎样会车？', '', '1', '1', NULL, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', 'C', '由于是结冰的道路，说明路面很易滑，因此在这样的路面上会车时，要注意减速慢行。', 7, 1, '2021-12-19 00:56:37', '2021-12-19 00:56:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11738, '驾驶机动车在山区冰雪道路上遇前车正在爬坡时如何处置？', '', '1', '1', NULL, '前车通过后再爬坡', '迅速超越前车爬坡', '低速超越前车爬坡', '紧随前车后爬坡', 'A', '本题的关键在于“冰雪道路”，也就是说这样的路面状况很差，极易滑。因此若跟在前车后面，很有可能因前车后溜滑坡而发生事故。因此只能等前车通过后再爬坡。', 7, 1, '2021-12-19 00:56:38', '2021-12-19 00:56:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11739, '驾驶机动车在冰雪路面行车注意什么？', '', '1', '1', NULL, '制动距离延长', '抗滑能力变大', '路面附着力变大', '制动距离变短', 'A', '由于冰雪路面易滑，因此要比平时更早的踩刹车，也就是说制动距离延长。', 7, 1, '2021-12-19 00:56:39', '2021-12-19 00:56:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11740, '驾驶机动车遇到大雾或特大雾等能见度过低天气时如何做？', '', '1', '1', NULL, '开启前照灯低速行驶', '开启雾灯低速行驶', '选择安全地点停车', '紧靠路边低速行驶', 'C', '注意题中的“大雾或特大雾等能见度过低天气”，也就是说可能出现无法正常行驶的情况，因此为了安全起见，应停车，而停车则应选择安全的地点。', 7, 1, '2021-12-19 00:56:40', '2021-12-19 00:56:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11741, '雾天对安全行车的主要影响是什么？', '', '1', '1', NULL, '易发生侧滑', '能见度低', '行驶阻力大', '视野变宽', 'B', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，行驶阻力大这一点更无从得知。', 7, 1, '2021-12-19 00:56:41', '2021-12-19 00:56:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11742, '驾驶机动车在雾天怎样跟车行驶？', '', '1', '1', NULL, '保持大间距', '开启远光灯', '开启近光灯', '适时鸣喇叭', 'A', '本题考的是跟车注意事项，与跟车有关的最先想到就是跟车距离。', 7, 1, '2021-12-19 00:56:42', '2021-12-19 00:56:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11743, '驾驶机动车在雾天两车交会时怎样做最安全？', '', '1', '1', NULL, '开启远光灯', '低速大间距', '开启近光灯', '开启雾灯', 'B', '雾天行车能见度低，视野不良，因此与其他车辆会车时要低速会车，目的是为了加大会车距离。', 7, 1, '2021-12-19 00:56:43', '2021-12-19 00:56:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11744, '在泥泞路段行车容易出现什么现象？', '', '1', '1', NULL, '行驶阻力大', '车轮侧滑', '机动车颠簸', '方向失控', 'B', '泥泞路面行驶阻力小，凹凸不平的路面机动车才会颠簸，横风时方向才会失控。', 7, 1, '2021-12-19 00:56:44', '2021-12-19 00:56:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11745, '驾驶机动车通过漫水桥，停车观察水情确认安全后，怎样通过？', '', '1', '1', NULL, '挂高速挡快速通过', '时刻观察水流的变化', '做好随时停车准备', '挂低速挡匀速通过', 'D', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 7, 1, '2021-12-19 00:56:45', '2021-12-19 00:56:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11746, '遇紧急情况避险时，要沉着冷静，坚持什么样的处理原则？', '', '1', '1', NULL, '先避人后避物', '：先避物后避车', '先避车后避人', '先避物后避人', 'A', '以人为本，所以首先要避人。', 7, 1, '2021-12-19 00:56:46', '2021-12-19 00:56:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11747, '轮胎气压过低时，高速行驶可能导致什么结果？', '', '1', '1', NULL, '气压不稳', '气压增高', '行驶阻力减小', '爆胎', 'D', '轮胎气压过低，在车子行驶过程中，会让轮胎不正常工作，若是高速行驶的话，产生摩擦之后就很容易发生爆胎了。', 7, 1, '2021-12-19 00:56:47', '2021-12-19 00:56:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11748, '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么情况发生？', '', '1', '1', NULL, '气压不稳', '气压更低', '行驶阻力增大', '爆胎', 'D', '轮胎气不足，就会造成轮胎与地面的摩擦范围增大，所以会使温度升高，从而爆胎。', 7, 1, '2021-12-19 00:56:49', '2021-12-19 00:56:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11749, '避免爆胎的错误做法是什么？', '', '1', '1', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽里的异物', '更换有裂纹或有很深损伤的轮胎', 'A', '轮胎缺气行驶是爆胎的祸根。车辆的缺气（轮胎胎压低于标准胎压）行驶时，随着胎压的下降，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。如果车辆在低速行驶，也会伤胎，而且潜伏期长，隐蔽性大，更有危害性，为以后高速行车时埋下爆胎隐患。', 7, 1, '2021-12-19 00:56:50', '2021-12-19 00:56:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11750, '在事故现场抢救伤员的基本要求是什么？', '', '1', '1', NULL, '先治伤，后救命', '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', 'B', '必须先救人，否则很有可能因为送医不及时而造成伤者死亡。而受伤的人员没有生命危险，可以暂缓一下。因此要“先救命，后治伤”。', 7, 1, '2021-12-19 00:56:51', '2021-12-19 00:56:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11751, '下列关于驾驶摩托车的说法正确的是哪一项？', '', '1', '1', NULL, '双手可以临时离开转向把', '可以随意操作转向把', '严禁双手同时离开转向把', '严禁原地转动转向把', 'C', '《道路交通安全法实施条例》第六十二条规定：驾驶机动车不得有下列行为： （六）驾驶摩托车手离车把或者在车把上悬挂物品。', 7, 1, '2021-12-19 00:56:52', '2021-12-19 00:56:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11752, '下列关于使用摩托车制动的错误做法是哪一项？', '', '1', '1', NULL, '同时使用前后制动', '先使用前轮制动', '先使用后轮制动', '不能过早使用前制动', 'B', '看清楚题目，要选择“错误做法”，当然是选择“先使用前轮制动”了。车是往前行驶的，这时使用前轮制动，在惯性的作用下，容易造成翻车，大家要注意安全呀！', 7, 1, '2021-12-19 00:56:53', '2021-12-19 00:56:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11753, '下列关于夏季驾驶摩托车的错误做法是哪一项？', '', '1', '1', NULL, '长时间高速行驶', '用油门控制车速', '尽量不用或少用制动', '尽量不要载人或载货', 'A', '夏天温度本来就很高，若一直高速行驶，很有可能让引擎过热，容易发生危险。', 7, 1, '2021-12-19 00:56:54', '2021-12-19 00:56:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11754, '摩托车后轮胎爆裂时，驾驶人应当怎样做？', '', '1', '1', NULL, '迅速转动转向把', '双手紧握转向把', '迅速向相反方向转动转向把', '迅速采取制动措施', 'B', '双手紧握转向把，松油门，减挡，直到停下来，切记不要紧急制动。', 7, 1, '2021-12-19 00:56:55', '2021-12-19 00:56:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11755, '驾驶人发现转向把不灵活时，下列做法哪一项是错误的？', '', '1', '1', NULL, '应尽快减速停车', '在安全地点停车', '继续驾驶', '停车查明原因', 'C', '发现转向不灵活时若是继续驾驶，而不是停车找原因的话，很有可能会发生状况。', 7, 1, '2021-12-19 00:56:56', '2021-12-19 00:56:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11756, '下列哪一项防护装备是摩托车驾驶人按规定应当佩戴的？', '', '1', '1', NULL, '眼镜', '安全头盔', '手套', '安全护膝', 'B', '《道路交通安全法》第五十一条规定：“机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。”', 7, 1, '2021-12-19 00:56:57', '2021-12-19 00:56:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11757, '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', '', '2', '1', NULL, '', '', '', '', 'A', '确保出行前一定都OK。', 7, 1, '2021-12-19 00:56:58', '2021-12-19 00:56:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11758, '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '', '2', '1', NULL, '', '', '', '', 'B', '驾车要谨慎，所以出现迅速基本都是错误！', 7, 1, '2021-12-19 00:56:59', '2021-12-19 00:56:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11759, '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', '', '2', '1', NULL, '', '', '', '', 'A', '一看二慢三汇入，这样做既出于安全考虑，同时也不影响其他车辆的正常行驶。因此本题正确。', 7, 1, '2021-12-19 00:57:00', '2021-12-19 00:57:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11760, '驾驶机动车汇入车流时不能影响其他机动车通行。', '', '2', '1', NULL, '', '', '', '', 'A', '汇入车流的前提是不能影响其他车辆的正常通行，因此本题正确。', 7, 1, '2021-12-19 00:57:01', '2021-12-19 00:57:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11761, '驾驶机动车从辅路汇入主路车流时要迅速。', '', '2', '1', NULL, '', '', '', '', 'B', '驾车要谨慎，不要那么“迅速”！注意是汇入车流，所以正确的做法是要减速慢行，在不影响其他车辆正常行驶的情况下汇入主路。', 7, 1, '2021-12-19 00:57:02', '2021-12-19 00:57:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11762, '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '', '2', '1', NULL, '', '', '', '', 'B', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 7, 1, '2021-12-19 00:57:03', '2021-12-19 00:57:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11763, '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十三条：同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：（四）行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。', 7, 1, '2021-12-19 00:57:04', '2021-12-19 00:57:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11764, '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', '', '2', '1', NULL, '', '', '', '', 'B', '专用车道指的是“机动车道、非机动车道、人行道”这些都属于专用车道，什么车走什么道，应该就不能借道超车，否则有可能开到人行道上去借道超车！', 7, 1, '2021-12-19 00:57:06', '2021-12-19 00:57:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11765, '机动车行经交叉路口，不得超车。', '', '2', '1', NULL, '', '', '', '', 'A', '路口通行时，要减速慢行注意观察。超车当然不可以。', 7, 1, '2021-12-19 00:57:07', '2021-12-19 00:57:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11766, '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '', '2', '1', NULL, '', '', '', '', 'B', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。因此本题错误。', 7, 1, '2021-12-19 00:57:08', '2021-12-19 00:57:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11767, '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', '', '2', '1', NULL, '', '', '', '', 'A', '在行驶过程中要留意与前后车的距离，因此若是贸然使用制动，很容易使后车来不及反应发生追尾，所以本题正确。', 7, 1, '2021-12-19 00:57:09', '2021-12-19 00:57:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11768, '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '', '2', '1', NULL, '', '', '', '', 'B', '跟车距离很重要，不同的道路对跟车距离要求是不一样的。假设你跟前车的的速度相等，但是距离是1米，是不可能刹得住车的。', 7, 1, '2021-12-19 00:57:10', '2021-12-19 00:57:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11769, '机动车遇有急弯路时要在进入弯路后减速。', '', '2', '1', NULL, '', '', '', '', 'B', '遇到急弯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。因此本题错误。', 7, 1, '2021-12-19 00:57:11', '2021-12-19 00:57:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11770, '驾驶机动车遇到骑自行车人占道影响通行时，可连续鸣喇叭加速从其左侧绕行。', '', '2', '1', NULL, '', '', '', '', 'B', '遇到这种情况应当减速慢行，必要时避让。因此本题错误。', 7, 1, '2021-12-19 00:57:12', '2021-12-19 00:57:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11771, '驾驶机动车遇到成群青少年绕过路边停放的机动车时，要主动减速让行。', '', '2', '1', NULL, '', '', '', '', 'A', '遇到这种情况，要随时注意出现发生突发状况，因此要减速让行，以免发生意外。所以本题正确。', 7, 1, '2021-12-19 00:57:13', '2021-12-19 00:57:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11772, '驾驶人一边驾车，一边吸烟对安全行车无影响。', '', '2', '1', NULL, '', '', '', '', 'B', '一心两用妨碍安全驾驶，容易发生危险。', 7, 1, '2021-12-19 00:57:14', '2021-12-19 00:57:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11773, '行车中不开转向灯强行并线是违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '不开转向灯强行并线，很容易和后方车辆发生刮擦或碰撞，并且责任全部在你，是一个违反交通法的行为。', 7, 1, '2021-12-19 00:57:15', '2021-12-19 00:57:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11774, '驾驶人在确认后方无来车的情况下，可以不开转向灯变更车道。', '', '2', '1', NULL, '', '', '', '', 'B', '变更车道时要提前开启转向灯，养成良好的开车习惯，防患于未然，因此本题错误。', 7, 1, '2021-12-19 00:57:16', '2021-12-19 00:57:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11775, '机动车行驶中遇有自行车借道通行时，可急促鸣喇叭示意让道。', '', '2', '1', NULL, '', '', '', '', 'B', '发生这种情况，应当予以让行，避免发生碰撞，看到有急促鸣喇叭的行为，基本都是错误的。', 7, 1, '2021-12-19 00:57:17', '2021-12-19 00:57:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11776, '驾驶人边驾车，边打手持电话是违法行为。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得有下列行为：（三）拨打接听手持电话、观看电视等妨碍安全驾驶的行为。', 7, 1, '2021-12-19 00:57:18', '2021-12-19 00:57:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11777, '在正常行车中，尽量靠近中心线或压线行驶，不给对向机动车留有侵占行驶路线的机会。', '', '2', '1', NULL, '', '', '', '', 'B', '压线行驶已经错误，另外行车应礼貌让行。', 7, 1, '2021-12-19 00:57:19', '2021-12-19 00:57:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11778, '女驾驶人穿高跟鞋驾驶机动车，不利于安全行车。', '', '2', '1', NULL, '', '', '', '', 'A', '高跟鞋踩刹车有安全隐患，因此这题是对的。', 7, 1, '2021-12-19 00:57:20', '2021-12-19 00:57:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11779, '驾驶人频繁变更车道不属于驾驶陋习。', '', '2', '1', NULL, '', '', '', '', 'B', '变更车道不能太过频繁，否则会影响正常交通，属于陋习，因此本题错误。', 7, 1, '2021-12-19 00:57:21', '2021-12-19 00:57:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11780, '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '避免水滑最根本的办法就是降低车速。减速或者停车都是为了安全。', 7, 1, '2021-12-19 00:57:23', '2021-12-19 00:57:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11781, '雨天超车要开启前照灯，连续鸣喇叭迅速超越。', '', '2', '1', NULL, '', '', '', '', 'B', '这种天气里面能见度低，车辆应该慢行，就算要超车，也不能连续鸣喇叭，而且要确保安全的情况下超车。所以此题的关键在“迅速”两字。 因此本题错误。', 7, 1, '2021-12-19 00:57:24', '2021-12-19 00:57:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11782, '在雨天湿滑路面行车要尽量避免紧急制动。', '', '2', '1', NULL, '', '', '', '', 'A', '由于湿滑路面很易滑，若是采取紧急制动，容易发生侧滑或横滑，因此本题正确。', 7, 1, '2021-12-19 00:57:25', '2021-12-19 00:57:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11783, '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人眩目。', '', '2', '1', NULL, '', '', '', '', 'A', '因为太阳光通过冰的折射到人的眼睛里，冰在太阳光下就好像一面镜子。', 7, 1, '2021-12-19 00:57:26', '2021-12-19 00:57:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11784, '在冰雪道路上行车时，机动车的稳定性降低，加速过急时车轮易空转或溜滑。', '', '2', '1', NULL, '', '', '', '', 'A', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 7, 1, '2021-12-19 00:57:27', '2021-12-19 00:57:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11785, '雪天行车中，在有车辙的路段要循车辙行驶。', '', '2', '1', NULL, '', '', '', '', 'A', '雪天行驶时，道路通常会被雪所掩盖，因此无法了解路面的实际情况。因此为了避免不可见的危险，就要尽可能沿着车辙行驶。因为这样安全系数更高一些。', 7, 1, '2021-12-19 00:57:28', '2021-12-19 00:57:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11786, '冰雪路面处理情况不能使用紧急制动，但可采取急转向的方法躲避。', '', '2', '1', NULL, '', '', '', '', 'B', '冰雪路面行驶过程中不能采取紧急制动或是急转方向的方法，这是因为冰雪路面易滑，这样做容易造成车子侧滑或侧翻。因此本题错误。', 7, 1, '2021-12-19 00:57:29', '2021-12-19 00:57:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11787, '在雪天临时停车要开启前照灯和雾灯。', '', '2', '1', NULL, '', '', '', '', 'B', '雪天在道路上临时停车，应当开启危险报警闪光灯，用以提醒其他车辆注意。因此本题错误。', 7, 1, '2021-12-19 00:57:30', '2021-12-19 00:57:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11788, '大雾天气能见度低，开启远光灯会提高能见度。', '', '2', '1', NULL, '', '', '', '', 'B', '雾天，当然是雾灯和危险警报灯。另外，雾天的能见度不是灯能改变的！', 7, 1, '2021-12-19 00:57:31', '2021-12-19 00:57:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11789, '雾天公路行车可多使用喇叭引起对向注意；听到对向机动车鸣喇叭，也要鸣喇叭回应。', '', '2', '1', NULL, '', '', '', '', 'A', '雾天视野不好，看不清楚的情况下，只能通过声音来判断其他车辆的位置。因此雾天比较多用喇叭来“交流”，当听到其他车辆鸣喇叭时，也该告知对方自己所在的位置，避免因看不清发生事故。 所以这题是对的。', 7, 1, '2021-12-19 00:57:32', '2021-12-19 00:57:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11790, '驾驶机动车在大雾天临时停车后，只开启雾灯和近光灯。', '', '2', '1', NULL, '', '', '', '', 'B', '雾天停车要开危险报警灯。', 7, 1, '2021-12-19 00:57:33', '2021-12-19 00:57:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11791, '驾驶机动车在雾天行车要开启雾灯。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 7, 1, '2021-12-19 00:57:34', '2021-12-19 00:57:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11792, '大风天气行车中，如果遇到狂风袭来，感觉机动车产生横向偏移时，要急转方向以恢复行驶方向。', '', '2', '1', NULL, '', '', '', '', 'B', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法就是收油，等车速自己下来，这样就可以让车轮恢复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时打轮的话不管是前轮离地还是后轮离地，都容易发生甩尾，再加上当时的横风，可能还会加重甩尾的趋势。即本题错误。', 7, 1, '2021-12-19 00:57:35', '2021-12-19 00:57:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11793, '机动车行至泥泞或翻浆路段时，要停车观察，选择平整、坚实或有车辙的路段缓慢通过。', '', '2', '1', NULL, '', '', '', '', 'A', '停车观察是在通过泥泞或翻浆路段之前的操作，也就是说看清楚路况之后再行驶。遇到这类情况，大家都知道要走好走的路，那么什么样的路是好走的？就是平整、坚实的或是有车辙的。这三种是并列的，并不是一定要同时满足这三个条件，可以任选其一。 因此这题是对的。', 7, 1, '2021-12-19 00:57:36', '2021-12-19 00:57:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11794, '泥泞路对安全行车的影响是车轮极易空转和侧滑。', '', '2', '1', NULL, '', '', '', '', 'A', '因为泥泞路面很易滑，因此车轮容易空转或是侧滑，因此本题正确。', 7, 1, '2021-12-19 00:57:37', '2021-12-19 00:57:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11795, '机动车涉水后，驾驶人要间断轻踩制动踏板，以恢复制动效能。', '', '2', '1', NULL, '', '', '', '', 'A', '通过积水路面时，会进水，若持续轻踏制动踏板，磨擦后会产生污物，粘附在轮毂内后会影响制动效果。 因此本题正确。', 7, 1, '2021-12-19 00:57:38', '2021-12-19 00:57:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11796, '漫水道路行车时，要挂高速挡，快速通过。', '', '2', '1', NULL, '', '', '', '', 'B', '不应该挂高速挡，而是要“应选用低速挡，以保证汽车有足够的驱动力；入水时应平稳，以防溅起的水花落 入电器设备，造成电器短路;发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘”这样才对。', 7, 1, '2021-12-19 00:57:40', '2021-12-19 00:57:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11797, '驾驶机动车通过漫水路时驾驶人要挂低速挡匀速通过。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 7, 1, '2021-12-19 00:57:41', '2021-12-19 00:57:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11798, '涉水驾驶要保持车速均匀有足够动力，避免停留。', '', '2', '1', NULL, '', '', '', '', 'A', '若是动力不够，很有可能在水中停下来，停下来之后很容易熄火。因此本题正确。', 7, 1, '2021-12-19 00:57:42', '2021-12-19 00:57:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11799, '车速较高，前方发生紧急情况时，要先转方向避让，再采取制动减速，以减小碰撞损坏程度。', '', '2', '1', NULL, '', '', '', '', 'B', '车速较高的情况下，若直接转向，很有可能发生侧翻，此题就错在“要先转方向避让”。', 7, 1, '2021-12-19 00:57:43', '2021-12-19 00:57:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11800, '驾驶人发现轮胎漏气，将机动车驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '', '2', '1', NULL, '', '', '', '', 'A', '当车辆漏气时，引起轮胎不正常工作，为了避免意外发生，应该驶离主干道，在安全地方检查也不应该紧急制动，避免后面车辆反应不及时引起事故。', 7, 1, '2021-12-19 00:57:44', '2021-12-19 00:57:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11801, '行车中当机动车突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用抢挂低速档的方法，利用发动机制动使机动车减速。', '', '2', '1', NULL, '', '', '', '', 'A', '如果爆胎时，方向盘会偏向爆胎处，而且是打死了的任你怎么搬都搬不动。这个时候如果踩刹车，车子立马会发生侧翻，所以说这个时候应该抬起刹车，轰油门，抢次档，车就会往反方向跑，然后在亲点刹车，同时抢3档，然后2档，1档这样做你就会平安了~只是你如果超速行驶，上面说的效果就没有了~所以说控制好速度才是最重要的！', 7, 1, '2021-12-19 00:57:45', '2021-12-19 00:57:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11802, '避免机动车爆胎的正确做法是降低轮胎气压。', '', '2', '1', NULL, '', '', '', '', 'B', '降低轮胎气压的过程中，若是轮胎气压过低也是会造成爆胎的，因此这个做法不可行。所以本题错误。', 7, 1, '2021-12-19 00:57:46', '2021-12-19 00:57:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11803, '驾驶人行车中意识到爆胎时，要轻踏制动踏板，缓慢减速停车。', '', '2', '1', NULL, '', '', '', '', 'A', '意识到爆胎，要紧握方向盘，掌握好方向，轻踩制动踏板，缓速减速停车。汽车爆胎重心会偏向爆胎的一侧，这时千万不能紧急制动，会造成侧翻。', 7, 1, '2021-12-19 00:57:47', '2021-12-19 00:57:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11804, '行车中遇突然爆胎时，驾驶人要急踏制动踏板减速停车。', '', '2', '1', NULL, '', '', '', '', 'B', '这样做很危险，容易造成车子侧翻，不能急踏制动踏板减速停车，而是要轻踏制动踏板慢慢减速停车，因此本题错误。', 7, 1, '2021-12-19 00:57:48', '2021-12-19 00:57:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11805, '行车中当驾驶人意识到机动车爆胎时，应在控制住方向的情况下采取紧急制动，迫使机动车迅速停住。', '', '2', '1', NULL, '', '', '', '', 'B', '题目只要出现“迅速”，“快”等字眼就是错的，就只有一道题就是事故无可避免的时候要迅速刹车，其他都是错的。', 7, 1, '2021-12-19 00:57:49', '2021-12-19 00:57:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11806, '摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第五十一条规定：“机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。”', 7, 1, '2021-12-19 00:57:50', '2021-12-19 00:57:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11807, '在道路同方向划有2条以上机动车道的，摩托车应当在最左侧车道行驶。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十四条规定：在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。在快速车道行驶的机动车应当按照快速车道规定的速度行驶，未达到快速车道规定的行驶速度的，应当在慢速车道行驶。摩托车应当在最右侧车道行驶。', 7, 1, '2021-12-19 00:57:51', '2021-12-19 00:57:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11808, '轻便摩托车只允许乘载学龄前儿童。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十五条第三例：机动车载人应当遵守下列规定摩托车后座不得乘坐未满12周岁的未成年人，轻便摩托车不得载人。所以本题选择“错误”。', 7, 1, '2021-12-19 00:57:52', '2021-12-19 00:57:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11809, '驾驶摩托车时可单手离车把，但不得双手同时离把。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第六十二条规定：驾驶机动车不得有下列行为： （六）驾驶摩托车手离车把或者在车把上悬挂物品。所以，单手离车把也是不对的。', 7, 1, '2021-12-19 00:57:53', '2021-12-19 00:57:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11810, '驾驶摩托车时不得在车把上悬挂物品。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条规定：驾驶机动车不得有下列行为： （六）驾驶摩托车手离车把或者在车把上悬挂物品。', 7, 1, '2021-12-19 00:57:54', '2021-12-19 00:57:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11811, '乘坐两轮摩托车的人应当侧向骑坐。', '', '2', '1', NULL, '', '', '', '', 'B', '侧向骑坐不易保持平衡，出现紧急情况极易被甩出受伤。', 7, 1, '2021-12-19 00:57:55', '2021-12-19 00:57:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11812, '摩托车不得牵引车辆或者被其他车辆牵引。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路安全法实施条例》第五十六条规定：大型、中型载客汽车，低速载货汽车，三轮汽车以及其他机动车不得牵引挂车。', 7, 1, '2021-12-19 00:57:57', '2021-12-19 00:57:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11813, '使用伪造机动车号牌、行驶证的，交通警察可以扣留摩托车。', '', '2', '1', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条规定：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处15日以下拘留，并处2000元以上5000元以下罚款；构成犯罪的，依法追究刑事责任。', 7, 1, '2021-12-19 00:57:58', '2021-12-19 00:57:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11814, '驾驶摩托车，应穿着颜色鲜明的长袖及长裤服装，易被其他交通参与者发现。', '', '2', '1', NULL, '', '', '', '', 'A', '越易被发现就证明你越安全。', 7, 1, '2021-12-19 00:57:59', '2021-12-19 00:57:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11815, '穿高跟鞋驾驶摩托车，不利于安全行车。', '', '2', '1', NULL, '', '', '', '', 'A', '穿高跟鞋踏制动踏板危险。', 7, 1, '2021-12-19 00:58:00', '2021-12-19 00:58:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11816, '驾驶摩托车可以穿拖鞋。', '', '2', '1', NULL, '', '', '', '', 'B', '不安全！因为拖鞋的边可能会挂到档杆！还有踩刹车的时候拖鞋的边边是受不住力的！', 7, 1, '2021-12-19 00:58:01', '2021-12-19 00:58:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11817, '狂风袭来，可能会使行驶中的摩托车产生横向偏移。', '', '2', '1', NULL, '', '', '', '', 'A', '有人认为这题是错的，因为题中并没有指出风向。但大家注意题中的可能两个字，也就是这只是一种假设的可能性，因此这题是对的。', 7, 1, '2021-12-19 00:58:02', '2021-12-19 00:58:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11818, '在冰雪道路上行车时，摩托车的稳定性降低，加速过急时车轮极易空转或滑溜。', '', '2', '1', NULL, '', '', '', '', 'A', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 7, 1, '2021-12-19 00:58:03', '2021-12-19 00:58:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11819, '在泥泞路上制动时,摩托车车轮易发生侧滑或甩尾，导致交通事故。', '', '2', '1', NULL, '', '', '', '', 'A', '泥泞道路制动时极易发生侧滑。', 7, 1, '2021-12-19 00:58:04', '2021-12-19 00:58:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11820, '摩托车高速行驶时，仅使用前制动，驾驶人易因惯性从车上甩出摔伤。', '', '2', '1', NULL, '', '', '', '', 'A', '前制动就是前刹车，前面的轮子停了，后面的轮子还有速度，就会随着惯性后轮飞出去，人也会翻出去的！！所以有经验的人一般都是先刹后刹车的！！', 7, 1, '2021-12-19 00:58:05', '2021-12-19 00:58:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11821, '驾驶摩托车使用制动时，先使用前轮制动，后使用后轮制动。', '', '2', '1', NULL, '', '', '', '', 'B', '先使用前轮制动，容易发生翻车，十分危险。', 7, 1, '2021-12-19 00:58:06', '2021-12-19 00:58:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11822, '驾驶摩托车紧急制动时，应特别注意使车身垂直于路面，以免摩托车侧滑倾倒。', '', '2', '1', NULL, '', '', '', '', 'A', '摩托车不比四轮汽车，平衡性本来就稍微差一些。若是紧急制动时，车身有侧偏，很有可能就侧倾了。', 7, 1, '2021-12-19 00:58:07', '2021-12-19 00:58:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11823, '摩托车在较高速度转弯过程中，应当尽量不用或少用制动，否则易产生侧滑。', '', '2', '1', NULL, '', '', '', '', 'A', '用制动容易抱死，车速过快时转弯就会发生侧滑。', 7, 1, '2021-12-19 00:58:08', '2021-12-19 00:58:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11824, '摩托车通过弯道或曲线路段时，应提前减速，换入空挡。', '', '2', '1', NULL, '', '', '', '', 'B', '正确过弯应该是：先离合器拉起，几乎同时油门开始减小（不然你的车会轰的怒吼起来，空负荷大，油门造成的发动机高转速），然后换成低档稍点刹车，等车速符合低档位标准的时候，松开离合器，低速进弯，车辆行过弯心的时候可以适当的加速，增加车的操控性。', 7, 1, '2021-12-19 00:58:09', '2021-12-19 00:58:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11825, '侧身乘坐摩托车极不安全，一旦发生侧滑，由于重心不稳，会被甩出造成伤亡。', '', '2', '1', NULL, '', '', '', '', 'A', '侧身坐摩托车，重心偏向一边，不能保证两边的平衡，一旦出现侧滑，很容易被甩出，造成伤亡。', 7, 1, '2021-12-19 00:58:10', '2021-12-19 00:58:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11826, '摩托车在颠簸路面上行驶时，驾驶人应采用低挡低速，尽量选择坑大的地方行驶，以减小颠簸。', '', '2', '1', NULL, '', '', '', '', 'B', '要减小颠簸，肯定是尽可能找平缓的路面或是坑浅的，大坑不一定就是浅坑，所以这题选择“错误”。', 7, 1, '2021-12-19 00:58:11', '2021-12-19 00:58:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11827, '摩托车前轮爆胎时，会产生严重的左右摆动，驾驶人应立即减小油门，紧握转向把，逐级减挡，减速停车。', '', '2', '1', NULL, '', '', '', '', 'A', '此时若迅速踩制动，很有可能使得轮胎抱死，这样一来就很容易发生危险了。因此应该采取逐级减档的方式减速，直至停车。', 7, 1, '2021-12-19 00:58:12', '2021-12-19 00:58:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11828, '摩托车遇危险、复杂路况时，驾驶人应以中低速匀速行驶，注意保持车身平衡，谨慎通过。', '', '2', '1', NULL, '', '', '', '', 'A', '谨慎、低速，都做到了，当然选择“正确”。', 7, 1, '2021-12-19 00:58:14', '2021-12-19 00:58:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11829, '摩托车下长坡时要减挡行驶，以充分利用发动机的制动作用。', '', '2', '1', NULL, '', '', '', '', 'A', '下坡不能空挡滑行，减速，利用发动机制动才是正确的做法。', 7, 1, '2021-12-19 00:58:15', '2021-12-19 00:58:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11830, '摩托车在下坡行驶时，可充分利用空挡滑行。', '', '2', '1', NULL, '', '', '', '', 'B', '危险动作，下坡时，不准熄火或用空挡。如果下坡空挡滑行，只靠刹车来控制车速，遇到连续下坡的时候可能会造成刹车负担过重，刹车片过热而失灵。', 7, 1, '2021-12-19 00:58:16', '2021-12-19 00:58:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11831, '摩托车在山区道路下急坡时，切忌超车。', '', '2', '1', NULL, '', '', '', '', 'A', '坡道不能超车。', 7, 1, '2021-12-19 00:58:17', '2021-12-19 00:58:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11832, '摩托车通过立交桥时，如发现选择路线错误，应立即在原地掉头或倒车更改路线。', '', '2', '1', NULL, '', '', '', '', 'B', '立交上一般都只有一车道，而且其它车辆几乎都是以转弯的形式前进，视线不好，如果选择原地掉头或倒车，危险程度很高极易产生重大交通事故。如果走错路线就只能错到下一出口可以掉头的时候再返回原路线或选择其它的路线到达目的地。', 7, 1, '2021-12-19 00:58:18', '2021-12-19 00:58:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11833, '摩托车涉水后，制动器的制动效果不会改变。', '', '2', '1', NULL, '', '', '', '', 'B', '摩托车经过积水道路后，后制动效果逐渐变差，此时极易发生事故，直接影响到行车安全。', 7, 1, '2021-12-19 00:58:19', '2021-12-19 00:58:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11834, '摩托车在冰雪路制动时，驾驶人应采用点制动，并在制动时双脚做好落地的准备。', '', '2', '1', NULL, '', '', '', '', 'A', '冰雪路面较滑，容易发生车轮抱死或者侧滑。所谓点制动就是慢慢连续踩刹车，踩一下松开，再踩。这样做能使车辆缓慢稳定地停下来。', 7, 1, '2021-12-19 00:58:20', '2021-12-19 00:58:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11835, '摩托车行至泥泞路段时，应停车观察，选择平整、坚实的路段通过。', '', '2', '1', NULL, '', '', '', '', 'A', '常识题，注意选择比较坚硬的地方通行，不要选择泥泞地方。', 7, 1, '2021-12-19 00:58:21', '2021-12-19 00:58:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11836, '驾驶人发现轮胎漏气，将摩托车驶离主车道时，不要采用紧急制动，以免造成翻车或后车制动不及时导致追尾事故。', '', '2', '1', NULL, '', '', '', '', 'A', '连续轻踏制动，缓慢将车停下，更换轮胎。', 7, 1, '2021-12-19 00:58:22', '2021-12-19 00:58:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11837, '摩托车爆胎时，驾驶人应迅速踏下制动踏板减速，极力控制转向把，迅速停车。', '', '2', '1', NULL, '', '', '', '', 'B', '制动踏板=脚刹，爆胎后，驾驶人在尚未控制车速的时候，是不能急踩刹车的，要不然会横甩或者翻车以致后面来车追尾。正确的做法应该是紧握把手，控制方向，极力保持直线行驶，利用发动机制动，减档或抢挡减速，然后轻踏制动踏板缓慢减速，平稳靠边停车。', 7, 1, '2021-12-19 00:58:23', '2021-12-19 00:58:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11838, '摩托车爆胎后，驾驶人在尚未控制住车速前，不要冒险使用制动器停车，以避免摩托车横甩发生更大的险情。', '', '2', '1', NULL, '', '', '', '', 'A', '制动踏板=脚刹，爆胎后，驾驶人在尚未控制车速的时候，是不能急踩刹车的，要不然会横甩或者翻车以致后面来车追尾。正确的做法应该是紧握把手，控制方向，极力保持直线行驶，利用发动机制动，减档或抢挡减速，然后轻踏制动踏板缓慢减速，平稳靠边停车。', 7, 1, '2021-12-19 00:58:24', '2021-12-19 00:58:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11839, '摩托车爆胎时，驾驶人应在控制住方向的情况下采取紧急制动，迫使摩托车迅速停住。', '', '2', '1', NULL, '', '', '', '', 'B', '爆胎是要利用发动机缓慢制动停车的，紧急制动搞不好会造成翻车事故的。', 7, 1, '2021-12-19 00:58:25', '2021-12-19 00:58:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11840, '转弯时速度过快，摩托车容易冲出弯道或侧滑。', '', '2', '1', NULL, '', '', '', '', 'A', '转弯速度越快，角速度越大，离心力也越大，这样一来车子的抓地力就变小了，此时就容易发生侧滑。因此本题选择“正确”。', 7, 1, '2021-12-19 00:58:26', '2021-12-19 00:58:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11841, '行至两座山谷之间，如果遇到较强的横风，感觉摩托车产生横向偏移时，要急转转向把调正行进方向。', '', '2', '1', NULL, '', '', '', '', 'B', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法是收油，等车速自己下来，这样就可以让车轮回复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时打轮的话不管是前轮离地还是后轮离地，都容易发生甩尾，再加上当时的横风，可能还会加重甩尾的趋势。', 7, 1, '2021-12-19 00:58:27', '2021-12-19 00:58:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11842, '摩托车高速行驶时急转向，极易造成侧滑相撞或在离心力作用下倾翻的事故。', '', '2', '1', NULL, '', '', '', '', 'A', '通俗的讲，就是高速公路由于速度快，千万不要急打方向，否则在惯性的作用下很容易翻车。', 7, 1, '2021-12-19 00:58:28', '2021-12-19 00:58:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11843, '驾驶摩托车时应当穿着有脚后跟、鞋底不容易滑的鞋或靴，以保证用脚换档的灵活，准确而可靠。', '', '2', '1', NULL, '', '', '', '', 'A', '这是因为这样的鞋子或靴子才能确保不出现踩空或是用力过猛的情况。', 7, 1, '2021-12-19 00:58:29', '2021-12-19 00:58:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11844, '驾驶摩托车前必须戴好安全头盔，调整后视镜的角度至能够看清左右两侧后方情况。', '', '2', '1', NULL, '', '', '', '', 'A', '都是出于安全考虑。', 7, 1, '2021-12-19 00:58:31', '2021-12-19 00:58:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (11845, '乘坐摩托车的人，不必戴安全头盔。', '', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法》第五十一条规定：“机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。”', 7, 1, '2021-12-19 00:58:32', '2021-12-19 00:58:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41000, '高速行驶的机动车，在转向失控的情况下紧急制动，不会造成翻车。', '', '2', '4', NULL, '', '', '', '', 'B', '高速行驶的车辆，在转向装置失控的情况下紧急制动，很容易造成翻车。高速行驶中机动车转向装置突然失控后，若前方道路条件能够保持直线行驶，要迅速开启危险报警闪光灯，抢挂低速挡或合理使用行车制动和驻车制动减速停车，避免紧急制动。', 13, 1, '2021-12-19 01:27:19', '2021-12-19 01:27:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41001, '机动车转向突然失控后，若前方道路条件能够保持直线行驶，不要紧急制动。', '', '2', '4', NULL, '', '', '', '', 'A', '高速行驶的车辆，在转向装置失控的情况下紧急制动，很容易造成翻车。机动车转向装置突然失控后，若前方道路条件能够保持直线行驶，要迅速开启危险报警闪光灯，抢挂低速挡或合理使用行车制动和驻车制动减速停车，避免紧急制动。题干表述正确。', 13, 1, '2021-12-19 01:27:19', '2021-12-19 01:27:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41002, '高速行驶的机动车，在转向失控的情况下紧急制动，很容易造成翻车。', '', '2', '4', NULL, '', '', '', '', 'A', '高速行驶的车辆，在转向装置失控的情况下紧急制动，很容易造成翻车。高速行驶中机动车转向装置突然失控后，若前方道路条件能够保持直线行驶，要迅速开启危险报警闪光灯，抢挂低速挡或合理使用行车制动和驻车制动减速停车，避免紧急制动。', 13, 1, '2021-12-19 01:27:19', '2021-12-19 01:27:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41003, '当机动车已偏离直线行驶方向，事故已经无可避免时，应果断地连续踏制动踏板，尽量缩短停车距离，减轻撞车力度。', '', '2', '4', NULL, '', '', '', '', 'A', '装有助力转向的机动车发现转向困难时，要停车查明原因。行驶过程中遇到转向突然失控、行驶方向偏离，事故已经无可避免时，要果断地连续踩踏制动踏板使机动车尽快减速停车，尽量缩短停车距离，减轻撞车强度。 请注意！车辆在高速行驶的情况下紧急制动，容易造成翻车。', 13, 1, '2021-12-19 01:27:20', '2021-12-19 01:27:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41004, '机动车高速行驶中出现转向失控时，驾驶人要如何处置？', '', '3', '4', NULL, '紧急制动', '抢挂低速挡', '合理使用行车制动和驻车制动，避免紧急制动', '开启危险报警闪光灯', 'BCD', '高速行驶的车辆，在转向装置失控的情况下紧急制动，很容易造成翻车。高速行驶中机动车转向装置突然失控后，若前方道路条件能够保持直线行驶，要迅速开启危险报警闪光灯，抢挂低速挡或合理使用行车制动和驻车制动减速停车，避免紧急制动。', 13, 1, '2021-12-19 01:27:20', '2021-12-19 01:27:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41005, '高速行车中行车制动突然失灵时，驾驶人要如何制动？', '', '1', '4', NULL, '连续踩踏制动踏板', '抢挂低速档减速后，使用驻车制动', '迅速踏下离合器踏板', '迅速拉紧驻车制动器操纵杆', 'B', '高速行驶中机动车制动突然失灵后，驾驶人应采取的制动措施是：抢挂低速挡减速，使用驻车制动器配合减速，但驻车制动器不能一次拉紧。', 13, 1, '2021-12-19 01:27:20', '2021-12-19 01:27:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41006, '下坡路行车中制动突然失效怎样处置？', '', '1', '4', NULL, '可利用避险车道减速停车', '越二级挡位减挡', '挂倒挡迫使停车', '拉紧驻车制动器减速', 'A', '下坡路行驶中制动突然失效时，要迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶。不可采用迅速拉紧驻车制动器操作杆或越多级减挡的方法减速停车。', 13, 1, '2021-12-19 01:27:20', '2021-12-19 01:27:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41007, '下坡路制动突然失效后，不可采用的办法是什么？', '', '1', '4', NULL, '将机动车向上坡道方向行驶', '用车身靠向路旁的岩石或树林碰擦', '利用道路边专设的避险车道停车', '拉紧驻车制动器操纵杆或越二级挡位减挡', 'D', '请注意！题目要求选出不可采用的方法，下坡路行驶中制动突然失效时，不可采用迅速拉紧驻车制动器操作杆或越多级减挡的方法减速停车。 应当迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶。 下坡行驶中制动突然失效时，在不得已的情况下，可将车身靠向山坡、路旁的岩石、树木或护栏碰擦，迫使机动车减速停车。', 13, 1, '2021-12-19 01:27:21', '2021-12-19 01:27:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41008, '下坡路制动失效后，在不得已的情况下，可用车身侧面擦撞山坡，迫使机动车减速停车。', '', '2', '4', NULL, '', '', '', '', 'A', '下坡行驶中制动突然失效时，在不得已的情况下，可将车身靠向山坡、路旁的岩石、树木或护栏碰擦，迫使机动车减速停车。因此，题干表述正确。', 13, 1, '2021-12-19 01:27:21', '2021-12-19 01:27:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41009, '下坡路制动失效后，要迅速逐级或越一级减挡，利用发动机制动作用控制车速。', '', '2', '4', NULL, '', '', '', '', 'A', '【紧急避险-制动失效】下坡路行驶中制动突然失效时，要迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶。不可采用迅速拉紧驻车制动器操作杆或越多级减挡的方法减速停车。', 13, 1, '2021-12-19 01:27:21', '2021-12-19 01:27:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41010, '出现制动失效后，要首先控制方向，再设法控制车速。', '', '2', '4', NULL, '', '', '', '', 'A', '机动车在行驶中突遇制动失灵后，首先要握稳转向盘控制方向，再抢挂低速挡减速，使用驻车制动器配合减速，但驻车制动器不能一次拉紧。题干描述正确。', 13, 1, '2021-12-19 01:27:21', '2021-12-19 01:27:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41011, '下坡路制动失效后，驾驶人应立即寻找并冲入紧急避险车道；停车后，拉紧驻车制动器，以防溜动发生二次险情。', '', '2', '4', NULL, '', '', '', '', 'A', '下坡路行驶中制动突然失效时，要迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶。停车后，拉紧驻车制动器，以防溜动发生二次险情。 不少学员会觉得这一题错误，认为“冲入”不对，本题的考点在于这整个的操作过程，即制动失灵后要找到可以停车的地方，那么最快的方法就是找到紧急避险车道，此处用“冲入”应该是想强调迅速，因为车速很快。 因此在做本题时，提醒大家不要受“冲入”等词汇的影响。', 13, 1, '2021-12-19 01:27:22', '2021-12-19 01:27:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41012, '下坡路制动失效后，若无可利用的地形和时机，应迅速逐级或越一级减挡，利用发动机制动作用控制车速。', '', '2', '4', NULL, '', '', '', '', 'A', '发动机制动是利用发动机的牵阻作用控制车速，档位越低牵阻越明显，制动性越强。在下长坡道路行驶，抢挂低速档利用发动机的牵阻作用，可以减少制动器的负担和减少制动次数，防止制动过热引起制动力热衰减；在冰雪、泥泞的路面上行驶，应用发动机牵阻制动可以防止侧滑。因此本题正确。', 13, 1, '2021-12-19 01:27:22', '2021-12-19 01:27:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41013, '有效预防机动车发生制动失效的措施是什么？', '', '3', '4', NULL, '定期维护制动系统', '行车前检查制动踏板的自由行程', '正确使用制动，防止热衰退', '采用液压制动的机动车，行车前检查制动液是否有滴漏', 'ABCD', '【紧急避险知识-制动失效】预防机动车制动失效的有效措施是定期维护制动系统，行车前注意检查制动踏板的自由行程和制动液压，行车中正确使用制动，防止出现热衰退现象。', 13, 1, '2021-12-19 01:27:22', '2021-12-19 01:27:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41014, '机动车在行驶中突遇制动失灵时，驾驶人要采取什么措施？', '', '3', '4', NULL, '握稳方向', '抢挂低速挡减速', '使用驻车制动器减速', '开启危险报警闪光灯', 'ABCD', '机动车在行驶中突遇制动失灵时，要迅速开启危险报警闪光灯，握稳转向盘控制方向，抢挂低速挡减速，使用驻车制动器配合减速，但驻车制动器不能一次拉紧。', 13, 1, '2021-12-19 01:27:22', '2021-12-19 01:27:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41015, '下坡路行驶，制动突然失效后，可采用的减速方法是什么？', '', '3', '4', NULL, '利用道路边专设避险车道减速停车', '用车身靠向路旁的岩石或树木碰擦', '首先拉紧驻车制动', '抢挂低速挡', 'ABD', '下坡路行驶中制动突然失效时，要迅速开启危险报警闪光灯，握稳转向盘控制方向，抢挂低速挡减速，使用驻车制动器配合减速，但驻车制动器不能一次拉紧。迅速逐级或越一级减挡，利用发动机牵阻作用控制车速，迅速利用避险车道减速停车或向上坡道方向行驶。 下坡行驶中制动突然失效时，在不得已的情况下，可将车身靠向山坡、路旁的岩石、树木或护栏碰擦，迫使机动车减速停车。 不可采用迅速拉紧驻车制动器操作杆或越多级减挡的方法减速停车。', 13, 1, '2021-12-19 01:27:22', '2021-12-19 01:27:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41016, '行车中发动机突然熄火怎样处置？', '', '1', '4', NULL, '紧急制动停车', '缓慢减速停车', '挂空挡滑行', '关闭点火开关', 'B', '行车中发动机突然熄火后，驾驶人应开启危险报警闪光灯，依靠惯性，缓慢减速将车辆驶向右侧靠边停车，按规定在车后放置警告标志，然后查明原因，决定当场修理或是重新起动发动机。', 13, 1, '2021-12-19 01:27:23', '2021-12-19 01:27:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41017, '行车途中发动机突然熄火，不能继续启动时要采取紧急制动措施，迫使机动车迅速停住。', '', '2', '4', NULL, '', '', '', '', 'B', '行车途中发动机突然熄火，不能继续启动时，驾驶人应开启危险报警闪光灯，依靠惯性，将车辆驶向右侧靠边停车，不得采取紧急制动措施。在车辆惯性行驶中不要再次起动发动机，因为在重新起动的过程总容易出现操作失误，导致转向盘锁死，这时若前方有障碍物需避让时，则无法躲避。', 13, 1, '2021-12-19 01:27:23', '2021-12-19 01:27:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41018, '行车中发动机突然熄火后不能启动时，及时靠边停车检查熄火原因。', '', '2', '4', NULL, '', '', '', '', 'A', '行车中发动机突然熄火不能启动后，驾驶人应依靠惯性，及时将车辆靠边停车察明熄火原因。', 13, 1, '2021-12-19 01:27:23', '2021-12-19 01:27:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41019, '行车中发动机突然熄火后，要采取什么措施？', '', '3', '4', NULL, '立即停车检修', '立即开启危险报警闪光灯', '将机动车移到不妨碍交通的地点停车', '放置故障车警告标志', 'BCD', '行车中发动机突然熄火后，驾驶人应开启危险报警闪光灯，依靠惯性，将车辆驶向不妨碍交通的地点停车，按规定在车后放置警告标志，然后查明原因。 发动机突然熄火后，在车辆惯性行驶中不要再次起动发动机，因为在重新起动的过程中容易出现操作失误，导致转向盘锁死，这时若前方有障碍物需避让时，则无法躲避。', 13, 1, '2021-12-19 01:27:23', '2021-12-19 01:27:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41020, '机动车发生撞击的位置不在驾驶人一侧或撞击力量较小时，驾驶人不正确的做法是什么？', '', '1', '4', NULL, '紧握转向盘', '两腿向前蹬', '从一侧跳车', '身体向后紧靠座椅', 'C', '行车中，车辆碰撞容易造成严重的事故后果，因此应尽力避免碰撞。若发生撞击的位置不在驾驶人一侧或撞击力量较小时，驾驶人应紧握转向盘，两腿向前蹬，身体紧靠座椅。题干要求选出不正确的做法，因此，从一侧跳车做法错误。', 13, 1, '2021-12-19 01:27:24', '2021-12-19 01:27:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41021, '在车速较高可能与前方机动车发生碰撞时，驾驶人要采取什么措施？', '', '1', '4', NULL, '先制动减速，后转向避让', '急转方向向左避让', '急打方向，向右避让', '先转向避让，后制动减速', 'A', '在车速较高时遇紧急情况，要坚持“先制动减速、后转向避让”的原则，不要轻易急转向，否则极易造成车辆侧滑或倾翻。', 13, 1, '2021-12-19 01:27:24', '2021-12-19 01:27:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41022, '行车中与其他机动车发生正面碰撞已不可避免时怎样处置？', '', '1', '4', NULL, '变正面碰撞为侧面碰撞', '向右急转转向盘躲避', '迅速采取紧急制动', '向左急转转向盘躲避', 'C', '行车中当与前方机动车或障碍物发生正面碰撞已不可避免时，应迅速采取紧急制动，减轻碰撞力度。 【拓展知识】 车辆的侧面碰撞安全防护性能明显弱于正面碰撞安全防护性能，所以车辆撞击无法避免时，驾驶人应当尽力避免侧面碰撞。', 13, 1, '2021-12-19 01:27:24', '2021-12-19 01:27:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41023, '在高速公路驾驶机动车意外碰撞护栏时采取什么保护措施？', '', '1', '4', NULL, '握紧转向盘，适量修正', '迅速向相反方向转向', '迅速采取紧急制动', '迅速向碰撞一侧转向', 'A', '在高速公路驾驶机动车意外碰撞护栏时，要稳住方向，适当向碰撞一侧转向修正，不可乱打或急打转向盘，否则可能导致车辆连续碰撞两侧护栏，甚至发生倾翻事故。在高速公路行驶时发生碰撞护栏的事故后，如果车辆受损不严重，应将车移到应急车道安全停车。', 13, 1, '2021-12-19 01:27:25', '2021-12-19 01:27:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41024, '机动车在高速公路意外撞击护栏时，有效的保护措施是向相反方向大幅度转向。', '', '2', '4', NULL, '', '', '', '', 'B', '在高速公路驾驶机动车意外碰撞护栏时，要稳住方向，适当向碰撞一侧转向，不可乱打或急打转向盘，否则可能导致车辆连续碰撞两侧护栏，甚至发生倾翻事故。在高速公路行驶时发生碰撞护栏的事故后，如果车辆受损不严重，应将车移到应急车道安全停车。', 13, 1, '2021-12-19 01:27:25', '2021-12-19 01:27:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41025, '机动车在高速公路意外撞击护栏时，要稳住方向，适当修正，切忌猛转转向盘。', '', '2', '4', NULL, '', '', '', '', 'A', '在高速公路驾驶机动车意外碰撞护栏时，要稳住方向，适当向碰撞一侧转向，不可乱打或急打转向盘，否则可能导致车辆连续碰撞两侧护栏，甚至发生倾翻事故。在高速公路行驶时发生碰撞护栏的事故后，如果车辆受损不严重，应将车移到应急车道安全停车。', 13, 1, '2021-12-19 01:27:25', '2021-12-19 01:27:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41026, '与对向来车发生正面碰撞且碰撞位置在驾驶人正前方时，驾驶人正确的应急驾驶姿势是什么？', '', '3', '4', NULL, '迅速躲离转向盘', '往副驾驶座位躲避', '迅速将两腿抬起', '两腿蹬直', 'ABC', '行车中当与前方机动车或障碍物发生正面碰撞已不可避免时，应迅速采取紧急制动，减轻碰撞力度。 1、若与对向来车不可避免地发生正面碰撞且碰撞位置在正前方时，驾驶人要迅速离开转向盘，往副驾驶位置躲避，迅速将两腿抬起。 2、若发生撞击的位置不在驾驶人一侧或撞击力量较小时，驾驶人应紧握转向盘，两腿向前瞪，身体紧靠座椅。', 13, 1, '2021-12-19 01:27:25', '2021-12-19 01:27:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41027, '机动车燃油着火时，不能用于灭火的是什么？', '', '1', '4', NULL, '路边沙土', '棉衣', '工作服', '水', 'D', '车辆燃油着火时，应当使用灭火器或路边砂土、棉衣、工作服等覆盖法灭火，不能使用水灭火。因为燃油比水轻，当水与燃油接触后，水不但不能覆盖住燃油，反而将承载油四处流淌扩展火势。', 13, 1, '2021-12-19 01:27:25', '2021-12-19 01:27:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41028, '发动机着火后首先怎样处置？', '', '1', '4', NULL, '迅速关闭发动机', '用水进行灭火', '开启发动机罩灭火', '站在下风处灭火', 'A', '发动机着火时应迅速停熄发动机，用灭火器或覆盖法灭火。不得开启发动机罩后灭火，因为这样做可能会因大量空气进入而加大火势。', 13, 1, '2021-12-19 01:27:26', '2021-12-19 01:27:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41029, '怎样正确使用灭火器灭火？', '', '1', '4', NULL, '人要站在下风处', '灭火器瞄准火源', '尽量接近火源', '灭火器瞄准火苗', 'B', '使用灭火器时，人要站在上风处，尽量远离火源，灭火器要瞄准火源而不是火苗，借风势将灭火器泡沫吹向火源。', 13, 1, '2021-12-19 01:27:26', '2021-12-19 01:27:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41030, '机动车发生火灾时，要设法将机动车停在远离城镇、建筑物、树木、机动车及易燃物的空旷地带。', '', '2', '4', NULL, '', '', '', '', 'A', '车辆发生火灾时，应设法将车辆停在远离人群、建筑物、树木、车辆及易燃物的空旷地带，及时把事故情况和地点通报给救援机构。高速公路行车发生火灾时，不得将车辆驶进服务区或停车场灭火。', 13, 1, '2021-12-19 01:27:26', '2021-12-19 01:27:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41031, '高速公路行车发生火灾时，要将机动车驶进服务区或停车场灭火。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在高速公路发生火灾时，要尽快靠边停车，在来车方向设置警告标志，及时报警，使用车内备用的灭火器灭火。若发动机着火时，要迅速关闭发动机，千万不要开启发动机舱盖进行灭火，更不要将车驶进服务区或停车场灭火。', 13, 1, '2021-12-19 01:27:26', '2021-12-19 01:27:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41032, '发动机着火时，要迅速关闭发动机，开启发动机罩进行灭火。', '', '2', '4', NULL, '', '', '', '', 'B', '发动机着火时应迅速停熄发动机，用灭火器或覆盖法灭火。不得开启发动机罩后灭火，因为这样做可能会因大量空气进入而加大火势。', 13, 1, '2021-12-19 01:27:27', '2021-12-19 01:27:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41033, '机动车电器、汽油着火后可用水来熄灭。', '', '2', '4', NULL, '', '', '', '', 'B', '水的密度比油大，不能用来灭火。而电器类，若是用水熄灭的话，很容易导电。因此本题错误。', 13, 1, '2021-12-19 01:27:27', '2021-12-19 01:27:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41034, '救火时不要脱去所穿的化纤服装，以免伤害暴露的皮肤。', '', '2', '4', NULL, '', '', '', '', 'B', '救火时，要脱去所穿的化纤服装，注意保护暴露在外面的皮肤。不要撕扯已经粘在皮肤上的衣服，以免将表皮一起撕下，造成细菌侵入。', 13, 1, '2021-12-19 01:27:27', '2021-12-19 01:27:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41035, '救火时不要张嘴呼吸或高声呐喊，以免烟火灼伤上呼吸道。', '', '2', '4', NULL, '', '', '', '', 'A', '救火时，不要张嘴呼吸或高声呼喊，以免烟火灼伤上呼吸道或者吸入有害气体、被烟呛到。', 13, 1, '2021-12-19 01:27:27', '2021-12-19 01:27:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41036, '机动车行驶时突然发生自燃，驾驶人采取的以下紧急避险措施中，正确做法是什么？', '', '3', '4', NULL, '用清水喷洒扑灭', '及时报警', '使用车内备用的灭火器灭火', '在来车方向设置警告标志', 'BCD', '机动车行驶时突然发生自燃，驾驶人应按规定开启危险报警闪光灯，在来车方向设置警告标志，车上人员迅速撤离到安全地点报警求助。 使用车内灭火器或覆盖法灭火，不能使用清水喷洒灭火，因为燃油比水轻，当水与燃油接触后，水不但不能覆盖住燃油，反而将承载燃油四处流淌扩展火势。', 13, 1, '2021-12-19 01:27:28', '2021-12-19 01:27:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41037, '机动车不慎落水，车门无法开启时，可选择的自救方法是什么？', '', '1', '4', NULL, '敲碎侧窗玻璃', '关闭车窗', '打电话求救', '用工具撬开车门', 'A', '车辆落水后，车内压力低于车外压力，车门因水压太大无法开启时，要等到水快浸满车厢时，再设法开启车门或摇下车窗玻璃逃生。如果车门无法开启且车窗无法摇下时，驾驶人可以利用车内的安全锤或螺丝刀等坚硬物品敲击车窗四个边角破窗逃生，提示：紧急破窗逃生首选侧面车窗。', 13, 1, '2021-12-19 01:27:28', '2021-12-19 01:27:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41038, '机动车落水后，要迅速关闭车窗阻挡车内进水，短暂闭绝空气，可打电话告知救援人员失事地点，等待救援。', '', '2', '4', NULL, '', '', '', '', 'B', '车辆落水后，切记不能迅速关闭车窗阻挡车内进水，一定要为自己留出逃生窗口；也不要用打电话告知救援人员的方法等待救援，一定要保持冷静，充分抓住逃生时机，以设法逃生作为第一准则。', 13, 1, '2021-12-19 01:27:28', '2021-12-19 01:27:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41039, '机动车落水后，只有在水快浸满车厢时，才有可能开启车门或摇下车窗玻璃逃生。', '', '2', '4', NULL, '', '', '', '', 'A', '车辆落水后，车内压力低于车外压力，车门因水压太大无法开启时，要等到水快浸满车厢时，再设法开启车门或摇下车窗玻璃逃生。如果车门无法开启且车窗无法摇下时，驾驶人可以利用车内的安全锤或螺丝刀等坚硬物品敲击车窗四个边角破窗逃生。如果车内无可利用的坚硬物品，驾驶人可以拆下座椅头枕，将头枕的钢棍插入车窗下方的玻璃或车门的缝隙直接，采用撬玻璃的方式破窗逃生。', 13, 1, '2021-12-19 01:27:28', '2021-12-19 01:27:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41040, '雨天机动车在高速公路行驶发生“水滑”现象时怎样处置？', '', '1', '4', NULL, '急踏制动踏板减速', '缓抬加速踏板减速', '迅速转向进行调整', '提速增大车轮排水量', 'B', '所谓“水滑”现象就是雨天汽车在积水路面上高速行驶时，轮胎与路面间行车“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。因此一定要合理控制车速，不要急转向或紧急制动，应缓抬加速踏板减速。', 13, 1, '2021-12-19 01:27:29', '2021-12-19 01:27:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41041, '大雨天在高速公路行车时，怎样避免发生“水滑”现象？', '', '1', '4', NULL, '安装防滑装置', '提高车速行驶', '降低车速行驶', '断续使用制动', 'C', '“水滑”现象是指雨天高速行车，轮胎和路面之间会形成“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。故“水滑”现象是因大雨天高速行驶导致的，因此避免发生“水滑”，一定要降低车速。', 13, 1, '2021-12-19 01:27:29', '2021-12-19 01:27:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41042, '雨天在高速公路行车，为避免发生“水滑”现象而造成方向失控，要降低车速。', '', '2', '4', NULL, '', '', '', '', 'A', '雨天行驶，路面湿滑，车轮的附着力随车速的增加急剧变小，车辆很容易发生侧滑，当车辆高速行驶时，易发生水滑现象。因此，雨天高速行车，应降低车速行驶以避免发生“水滑”现象。', 13, 1, '2021-12-19 01:27:29', '2021-12-19 01:27:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41043, '雨天避免“水滑”现象的有效方法就是保持高速行驶。', '', '2', '4', NULL, '', '', '', '', 'B', '“水滑”现象是指雨天高速行车，轮胎和路面之间会形成“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。故“水滑”现象是因大雨天高速行驶导致的，因此避免发生“水滑”现象的有效防范应当要降低车速行驶。', 13, 1, '2021-12-19 01:27:29', '2021-12-19 01:27:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41044, '驾驶未安装制动防抱死装置（ABS）的机动车在冰雪路面怎样使用制动？', '', '1', '4', NULL, '轻踏或间歇踩踏制动踏板', '与其他路面一样踏制动踏板', '重踏制动踏板', '猛踏制动踏板', 'A', '没有配备ABS的车辆，在冰雪路面制动时，应采取轻踏制动踏板或间歇踩踏制动踏板的方法减速。', 13, 1, '2021-12-19 01:27:30', '2021-12-19 01:27:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41045, '机动车在什么样的路面上制动时车轮最容易抱死?', '', '1', '4', NULL, '混凝土路', '土路', '冰雪路面', '沙土路', 'C', '由于冰雪路面摩擦系数较另外三种路面来说小许多，所以摩擦力比较小，当刹车片和刹车盘之间的摩擦力大于路面和车轮间的摩擦力时，车轮就会抱死而打滑！因此本题选C。', 13, 1, '2021-12-19 01:27:30', '2021-12-19 01:27:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41046, '制动时前车轮抱死会出现丧失转向能力的情况。', '', '2', '4', NULL, '', '', '', '', 'A', '(1)前轮抱死，车辆丧失转向能力，不能实现转弯转向，躲避障碍物或行人；后轮抱死，车辆丧失稳定性，产生侧滑。 (2)在非对称附着系数的路面，汽车将丧失直线行驶的稳定性，出现侧滑、甩尾及急转等危险现象。 (3)车轮抱死导致轮胎局部与地面拖滑,大大降低了轮胎的使用寿命', 13, 1, '2021-12-19 01:27:30', '2021-12-19 01:27:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41047, '制动时后车轮抱死可能会出现侧滑甩尾的情况。', '', '2', '4', NULL, '', '', '', '', 'A', '所谓“侧滑”就是车辆急加速、突然制动或启动时扭矩过大而产生的侧向甩动的现象，俗称“甩尾”。造成车辆侧滑的因素很多。雪天，雨天、冰面上行车时突然抬起油门或突然加速；采取紧急制动，制动时后轮比前轮先抱死；都极易发生车辆侧滑而造成事故。 所以本题正确。', 13, 1, '2021-12-19 01:27:30', '2021-12-19 01:27:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41048, '机动车在行驶中，遇雨雪天气向右侧滑时，要向左打方向，使其稳定。', '', '2', '4', NULL, '', '', '', '', 'B', '【雨雪天侧滑，后同前不同】即前轮侧滑，向相反方向修正；后轮侧滑，向侧滑方向修正。机动车在雨雪天气发生侧滑时紧急避险处理分两种情况：1、前轮侧滑时，要向侧滑相反方向转动转向盘；2、后轮侧滑时，要向侧滑方向转动转向盘。 因为题干没有说明是前轮还是后轮，向左打方向盘不适用后轮侧滑情况，所以表述错误。', 13, 1, '2021-12-19 01:27:30', '2021-12-19 01:27:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41049, '机动车转弯时速度过快，容易发生侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '转弯速度越快，角速度越大，离心力也越大。这样一来车子的抓地力就变小了，此时就容易发生侧滑。因此本题正确。 【拓展延伸】 行驶的汽车因制动、转动惯性和其他原因，引发某一轴的车轮或两轴的车轮出现横向移动（即向侧面发生甩动）的现象，称为侧滑。汽车侧滑并不仅限于冰雪天道路湿滑时出现，在制动时四轮受到的阻力不平衡、制动不当、转向操作不当都会造成汽车侧滑，大家注意。', 13, 1, '2021-12-19 01:27:31', '2021-12-19 01:27:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41050, '驾驶机动车在冰雪路面发生侧滑时，要猛打方向调整。', '', '2', '4', NULL, '', '', '', '', 'B', '在冰雪道路上行车，有条件的要安装防滑链，挂低速挡缓慢行驶，避免紧急制动和急转向，以防车辆侧滑驶出路面。当车辆发生侧滑时，应立即缓慢、适当地向后轮侧滑的一侧转动转向盘，可连续数次回转转向盘，以便调正车身。', 13, 1, '2021-12-19 01:27:31', '2021-12-19 01:27:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41051, '在泥泞路行车中发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正。', '', '2', '4', NULL, '', '', '', '', 'A', '不同路面发生侧滑时的修正方法：【泥泞路侧滑】向后轮侧滑的方向转动转向盘适量修正；【冰雪路侧滑】1、前轮侧滑时，要向侧滑相反方向转动转向盘；2、后轮侧滑时，要向侧滑方向转动转向盘。', 13, 1, '2021-12-19 01:27:31', '2021-12-19 01:27:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41052, '机动车发生侧滑时要如何调整方向？', '', '3', '4', NULL, '前轮侧滑，向侧滑方向转动转向盘', '前轮侧滑，向侧滑相反方向转动转向盘', '后轮侧滑，向侧滑方向转动转向盘', '后轮侧滑，向侧滑相反方向转动转向盘', 'BC', '驾驶机动车在泥泞路行车、冰雪路面转弯，速度过快时容易发生侧滑。前轮侧滑时，要向侧滑相反方向转动转向盘；后轮侧滑时，要向侧滑方向转动转向盘。在泥泞路发生侧滑时，要向后轮侧滑的方向转动转向盘适量修正。', 13, 1, '2021-12-19 01:27:31', '2021-12-19 01:27:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41053, '大雾天在高速公路遇事故不能继续行驶时怎样处置？', '', '1', '4', NULL, '车上人员要迅速从左侧车门离开', '在来车方向100米处设置警告标志', '开启危险报警闪光灯和远光灯', '车上人员站到护栏以外安全的地方', 'D', '大雾天在高速公路遇事故不能继续行驶时，须开启危险报警闪光灯和尾灯，按规定在车后150米左右位置设置警告标志。驾乘人员尽快从右侧车门下车离开车辆并尽量站到防护栏以外安全的地方，不得在高速公路上行走。 综上，选项中只有“车上人员站到护栏以外安全的地方”符合要求。', 13, 1, '2021-12-19 01:27:32', '2021-12-19 01:27:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41054, '驾驶客车遇非常情况或者发生事故时，要力所能及的将损失降到最低限度，决不能因紧急避险造成二次事故或更大的损失。', '', '2', '4', NULL, '', '', '', '', 'A', '要以人为本，要以将伤害和损失降到最低为原则。', 13, 1, '2021-12-19 01:27:32', '2021-12-19 01:27:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41055, '雾天在高速公路上发生事故后，车上人员不要随便下车行走。', '', '2', '4', NULL, '', '', '', '', 'B', '大雾天在高速公路遇事故不能继续行驶时，须开启危险报警闪光灯和尾灯，按规定在车后设置警告标志。驾乘人员尽快从右侧车门下车离开车辆并尽量站到防护栏以外安全的地方，不得在高速公路上行走。【车靠边、人撤离、即报警】 因此，雾天在高速公路上发生事故后，车上人员： 1、要下车；2、要站在防护栏以外的安全地方，不得在高速公路上行走。', 13, 1, '2021-12-19 01:27:32', '2021-12-19 01:27:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41056, '发生交通事故后，防止二次事故的有效措施是什么？', '', '3', '4', NULL, '疏散人员', '开启危险报警闪光灯', '标记伤员的原始位置', '正确放置危险警告标志', 'ABD', '驾驶机动车在道路上发生交通事故，驾驶人应立即停车，保护现场，迅速报警。同时，做好现场安全防护，开启危险报警闪光灯，按规定在车后设置警告标志，车上有其他乘员时，应尽快疏散乘员到安全地带避险，同时也要注意自身的安全，迅速离开车辆和行车道，防止二次事故的发生。', 13, 1, '2021-12-19 01:27:32', '2021-12-19 01:27:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41057, '机动车在高速公路上发生故障需检查时怎样停车？', '', '1', '4', NULL, '在最外侧行车道上停车', '在内侧行车道上停车', '在应急车道停车', '在匝道口三角地带停车', 'C', '机动车在高速路发生故障必须停车检查时，应控制好车速，观察前后及右侧交通情况，开启右转向灯，尽快驶离行车道，将车停在紧急停车带或应急车道内。切不可紧急停车，更不要在行车道内停车。', 13, 1, '2021-12-19 01:27:33', '2021-12-19 01:27:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41058, '在高速公路行车选择什么地方停车？', '', '1', '4', NULL, '服务区', '加速车道', '减速车道', '匝道', 'A', '高速公路行车应选择到服务区停车，不得在应急车道内停车。', 13, 1, '2021-12-19 01:27:33', '2021-12-19 01:27:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41059, '高速公路行车紧急情况避险的处理原则是什么？', '', '1', '4', NULL, '先避车后避物', '先避人后避物', '先避车后避人', '先避物后避人', 'B', '高速公路行车遇紧急情况避险时，要沉着冷静，坚持“先避人后避物”的处理原则。', 13, 1, '2021-12-19 01:27:33', '2021-12-19 01:27:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41060, '高速路上，机动车因故障暂时不能离开应急车道或路肩时，驾乘人员要下车在路边等候，但不得离开高速公路。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车在高速公路上发生故障，因故障暂时不能离开应急车道或路肩时，驾乘人员应迅速转移至车辆右前方护栏外的安全地带，并且迅速报警。题干中，不得离开高速公路做法错误。', 13, 1, '2021-12-19 01:27:33', '2021-12-19 01:27:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41061, '机动车在高速公路上，因故障不能离开行车道时，可在行车道上迅速抢修。', '', '2', '4', NULL, '', '', '', '', 'B', '车辆在高速公路行车道发生故障不能移动时，不得在行车道上检修车辆。应①开启危险报警闪光灯（夜间还要开启示廓灯和后位灯），②在车后150米以外设置警告标志，③车上人员要迅速转移至车右前方护栏以外的安全地带，④面向来车方向并迅速拨打紧急电话求援或报警。', 13, 1, '2021-12-19 01:27:34', '2021-12-19 01:27:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41062, '在高速公路上除遇障碍、发生故障等必须停车外，不准停车上下人员或者装卸货物。', '', '2', '4', NULL, '', '', '', '', 'A', '在高速公路上遇车辆故障或发生事故等紧急情况时，要尽可能地将车辆停到应急车道内，立即开启危险报警闪光灯；如果车辆难以移动，则应迅速打开危险报警闪光灯。然后，在来车方向150米外设置警告标志，同时驾乘人员应尽快从车辆安全的一侧离开转移至护栏外避险，不要因紧急避险造成二次事故或更大的损失。 注意：不能在行车道上检修车辆；不得在高速公路上停车上下人员或装卸货物。', 13, 1, '2021-12-19 01:27:34', '2021-12-19 01:27:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41063, '机动车因故障不能离开高速公路时，驾乘人员要在车上等候救援。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车因故障必须在高速公路上停车时，要采取的正确做法是：1、立即开启危险报警闪光灯，夜间还要开启示廓灯和后位灯；2、在车后150米以外设置警告标志；3、车上人员要迅速转移至车右前方护栏以外的安全地点；4、面向来车方向并迅速拨打紧急电话求援或报警。', 13, 1, '2021-12-19 01:27:34', '2021-12-19 01:27:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41064, '在高速公路上遇到紧急情况时，可采取急转向的方式躲避。', '', '2', '4', NULL, '', '', '', '', 'B', '在高速公路上发生紧急情况，不要采取急转方向避让，应首先采取制动减速，使车辆在碰撞前处于停止或低速行进状态，以减小碰撞损坏程度。', 13, 1, '2021-12-19 01:27:34', '2021-12-19 01:27:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41065, '驾驶员在高速公路上行驶时，车辆左前轮突然爆胎，须第一时间紧握转向盘，然后轻踏制动踏板进行减速，并将车停靠在紧急停车带上。这样做的原因是什么？', '', '3', '4', NULL, '爆胎后，车辆行驶方向易发生变化，须紧握转向盘', '爆胎后，车辆自身开始减速，所以只需轻踏制动踏板', '爆胎后，紧急制动容易引起侧翻', '轻踏制动踏板进行减速是为了保护轮胎', 'AC', '前轮爆胎时，危险较大，一定要极力控制转向盘，在控制住行驶方向后，采取抢挂低速挡的方法减速停车。前轮爆裂已出现转向时，驾驶人不要过度矫正，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速。爆胎后，车辆行驶方向极易发生变化，切忌猛打转向盘和紧急制动，否则容易造成车辆侧滑或侧翻。', 13, 1, '2021-12-19 01:27:34', '2021-12-19 01:27:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41066, '驾驶机动车在高速公路行驶，如果发生转向失灵，不能紧急制动。', '', '2', '4', NULL, '', '', '', '', 'A', '高速行驶的车辆，在转向装置失控的情况下紧急制动，很容易造成翻车。高速行驶中机动车转向装置突然失控后，若前方道路条件能够保持直线行驶，要迅速开启危险报警闪光灯，抢挂低速挡或合理使用行车制动和驻车制动减速停车，避免紧急制动。', 13, 1, '2021-12-19 01:27:35', '2021-12-19 01:27:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41067, '驾驶机动车在高速公路行驶，雨天发生“水滑”现象时，以下做法正确的是什么？', '', '3', '4', NULL, '双手握稳转向盘', '迅速转向调整', '逐渐降低车速', '急踏制动踏板减速', 'AC', '“水滑”现象是指雨天高速行车，轮胎和路面之间会形成“水膜”，车辆的转向和制动将有失效的风险，如同惊险的水上滑板一样。当车辆发生侧滑或水滑时，不要急转向或紧急制动，应双手握稳转向盘，松抬加速踏板，充分利用发动机制动逐渐降低车速。', 13, 1, '2021-12-19 01:27:35', '2021-12-19 01:27:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41068, '驾驶机动车时，为了预防行车中突然起火造成的危险，应随车携带以下哪项物品？', '', '1', '4', NULL, '安全帽', '灭火器', '安全锤', '冷冻液', 'B', '安全帽、安全锤和冷却液都不能有效灭火，只有灭火器才能起到灭火的功能。冷冻液是防冻液，是一种化学合成制剂，用于防止发动机的冷却系统在低温时冻结，其次是散热，不能灭火。', 13, 1, '2021-12-19 01:27:35', '2021-12-19 01:27:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41069, '驾驶机动车在高速公路行驶，遇意外情况需紧急停车时，可在行车道上直接停车。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在高速路行驶，遇意外情况必须停车时，应控制好车速，观察前后及右侧交通情况，开启右转向灯，尽快驶离行车道，将车停在紧急停车带或应急车道内。切不可紧急停车，更不要在行车道内停车。', 13, 1, '2021-12-19 01:27:36', '2021-12-19 01:27:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41070, '以下哪些能够引起轮胎爆裂？', '', '3', '4', NULL, '轮胎磨损严重', '轮胎气压过高', '尖锐物体刺伤轮胎', '车辆超载超员', 'ABCD', '轮胎爆裂的原因：1，高温导致爆胎；2，超速导致爆胎；3，超载导致爆胎；4，胎压异常（过高或过低）导致爆胎；5，轮胎磨损严重导致爆胎；6，轮胎漏气，在被铁钉或其他尖锐物刺扎而暂时没有把轮胎扎破，轮胎会出现漏气现象，进而引起爆胎。 【拓展知识】 除了上面所说的造成汽车轮胎爆胎的种种原因外，气温高、路面不平等也是造成汽车爆胎的不可忽视的因素。气温偏高的夏季是爆胎事故的多发期，高速公路的路面塌陷路段和路面损毁严重的坑洼路段也往往是爆胎事故的多发路段。', 13, 1, '2021-12-19 01:27:36', '2021-12-19 01:27:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41071, '驾驶机动车行驶过程中发动机着火，以下做法错误的是什么？', '', '1', '4', NULL, '迅速关闭发动机', '用覆盖法灭火', '开启发动机罩灭火', '用灭火器灭火', 'C', '请注意：题干要求选出做法错误的选项。发动机着火时应迅速停熄发动机，用灭火器或覆盖法灭火。不得开启发动机罩后灭火，因为这样做可能会因大量空气进入而加大火势。', 13, 1, '2021-12-19 01:27:36', '2021-12-19 01:27:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41072, '驾驶机动车在高速公路行驶过程中，发现前方有动物突然横穿时，可以采取急转向的方式避让。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在高速公路行驶过程中，遇到占道或横过道路的野生动物或牲畜，应注意避让动物，保持较远距离，切忌采取急转向的方式避让或连续鸣喇叭、加速绕行等，以防引起牲畜骚动。', 13, 1, '2021-12-19 01:27:36', '2021-12-19 01:27:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41073, '驾驶机动车在高速公路上发生故障时，车上人员应当迅速转移到故障车前方躲避。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车因故障必须在高速公路上停车时，要采取的正确做法是：1、立即开启危险报警闪光灯，夜间还要开启示廓灯和后位灯；2、在车后150米以外设置警告标志；3、车上人员要迅速转移至车右前方护栏以外的安全地点；4、面向来车方向并迅速拨打紧急电话求援或报警。【车靠边、人撤离、即报警】', 13, 1, '2021-12-19 01:27:36', '2021-12-19 01:27:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41074, '驾驶机动车在高速公路行驶遇到横风时，应紧握方向盘，减速行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在高速公路行驶遇到横风时，应紧握方向盘，向来风的一侧适当修正，并且力度要轻，不要急打转向盘，同时应减速行驶，防止横高速行驶造成车辆失控。', 13, 1, '2021-12-19 01:27:37', '2021-12-19 01:27:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41075, '驾驶机动车突然发生倾翻时，以下做法正确的是什么？', '', '3', '4', NULL, '迅速跳车逃生', '双手紧握转向盘', '双脚勾住踏板', '背部紧靠椅背', 'BCD', '当车辆发生倾翻时，驾乘人员应尽可能地抓住车内固定的物体，例如转向盘、座椅后背等，尽量不要在车内发生翻滚和碰撞，同时注意避免因车体变形而使身体遭受挤压，切不可在发生倾翻时跳车。 在车辆稳定后，驾驶人应第一时间将发动机熄火，防止车辆发生自燃和爆炸；然后稳住身体，迅速解开安全带，从车门或破碎的车窗逃出车辆。', 13, 1, '2021-12-19 01:27:37', '2021-12-19 01:27:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41076, '驾驶机动车在夜间发生故障时，以下做法正确的是什么？', '', '3', '4', NULL, '选择安全区域停车', '开启危险报警闪光灯、示廓灯和后位灯', '按规定设置警告标志', '给朋友打电话，坐在车内等待救援', 'ABC', '驾驶机动车在道路上发生故障，驾驶人应选择安全地带停车，做好现场安全防护，开启危险报警闪光灯，按规定在车后设置警告标志，夜间还应当同时开启示廓灯和后位灯，车上有其他成员时，应尽快疏散乘员到安全地带避险，同时也要注意自身的安全，迅速离开车辆和行车道。', 13, 1, '2021-12-19 01:27:37', '2021-12-19 01:27:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41077, '行车过程中遇到以下情况，正确的做法是什么？', '/uploadFile/yuanBeiTu1077.gif', '1', '4', NULL, '鸣笛并继续直行', '减速并随时准备停车', '转向道路左侧并继续行驶', '匀速驶过该区域', 'B', '驾驶机动车通过居民小区，要遵守限速标志的规定，低速行驶，随时注意观察两侧情况，遇到突然情况，要停车让行，不得连续鸣喇叭或加速抢行。如图所示，看到儿童踏板车，要注意观察两侧情况。', 13, 1, '2021-12-19 01:27:37', '2021-12-19 01:27:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41078, '当驾驶人驶出隧道时，出现下图所示的“明适应”现象时，以下做法正确的是什么？', '/uploadFile/yuanBeiTu1078.gif', '1', '4', NULL, '加速驶出隧道', '减少与前车的距离，利用前车挡住强光', '与前车保持安全距离，切忌盲目加速', '变更至车辆较少的车道，迅速驶出隧道', 'C', '隧道一般比较狭窄、黑暗，路面潮湿，进出隧道，驾驶人的眼睛也会有一个明暗适应过程。当驾驶人从黑暗的隧道驶出，进入非常明亮的环境时，眼睛出现“明适应”现象，因此，通过隧道时一定要合理使用灯光和控制车速，与前车保持安全距离行驶，切忌盲目加速，迅速驶出隧道，以免发生危险。', 13, 1, '2021-12-19 01:27:38', '2021-12-19 01:27:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41079, '驾驶机动车途经这个路段的时候，以下关于潜在风险的说法正确的是？', '/uploadFile/yuanBeiTu1079.gif', '3', '4', NULL, '坡顶可能停放一辆车', '对面驶来的车辆可能占用你的车道', '前方道路可能有障碍物', '前方道路可能有弯道', 'ABCD', '通过山区弯道时，要提前减速，注意观察，预防以下险情：1、弯道高速行驶，车辆容易发生侧翻；2、弯道盲区内，可能存在落石及停驶的故障车等障碍；3、弯道盲区内，可能有对向车辆占道行驶或对向来车占道超车；4、通过急弯处时不要超车，防止前方有车辆或行人出现而措手不及。', 13, 1, '2021-12-19 01:27:38', '2021-12-19 01:27:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41080, '在事故现场抢救伤员的基本要求是什么？', '', '1', '4', NULL, '先治伤，后救命', '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', 'B', '在事故现场抢救伤员的基本要求是先救命后治伤。遇重特大事故有众多伤员需送往医院时，处于昏迷状态的伤员，首先送往医院。', 14, 1, '2021-12-19 01:27:38', '2021-12-19 01:27:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41081, '遇伤者被压于车轮或货物下时，要立即拉拽伤者的肢体将其拖出。', '', '2', '4', NULL, '', '', '', '', 'B', '车轮压住了伤者，必须将车轮或货物移开后才能将伤者移出。如果硬拖的话，容易使伤者伤得更严重，因此错误。', 14, 1, '2021-12-19 01:27:38', '2021-12-19 01:27:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41082, '受伤者在车内无法自行下车时，可设法将其从车内移出，尽量避免二次受伤。', '', '2', '4', NULL, '', '', '', '', 'A', '避免二次伤害原则指出：受伤者在车内无法自行下车时，可设法将其从车内移出，尽量避免二次受伤。对于没有救护知识或经验的人员，不得盲目施救，对伤员生拉硬拽很有可能对伤员造成二次伤害。在没有把握时，应保护好现场，等候医护人员的到来。', 14, 1, '2021-12-19 01:27:39', '2021-12-19 01:27:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41083, '抢救昏迷失去知觉的伤员需注意什么？', '', '1', '4', NULL, '马上实施心肺复苏', '使劲掐伤员的人中', '连续拍打伤员面部', '抢救前先检查呼吸', 'D', '急救员在对伤员实施具体的救护措施之前，应首先对伤员的全身进行检查，顺序依次是头、颈和气管、胸、腹、背、髋、下肢和上肢。对于昏迷失去知觉的伤员，要先检查伤员呼吸，再进行救护。', 14, 1, '2021-12-19 01:27:39', '2021-12-19 01:27:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41084, '搬运昏迷失去知觉的伤员要采取仰卧位。', '', '2', '4', NULL, '', '', '', '', 'B', '当伤者昏迷时，对于不存在疑似脊柱、髋部或骨盆损伤的伤者，可以将其摆放至稳定侧卧位，即恢复体位，并拨打急救电话，尽早送医院治疗。因情况紧急，需要对昏迷伤员进行搬运时，要采取侧卧的方式进行搬运。', 14, 1, '2021-12-19 01:27:39', '2021-12-19 01:27:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41085, '抢救昏迷失去知觉的伤员要在抢救前先检查呼吸。', '', '2', '4', NULL, '', '', '', '', 'A', '抢救昏迷失去知觉的伤员，应先测定生命体征，确定有无呼吸、心跳，确定大动脉有无搏动，如果伤员失去生命体征，应立即进行心肺复苏。', 14, 1, '2021-12-19 01:27:39', '2021-12-19 01:27:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41086, '抢救失血伤员时，要先采取什么措施？', '', '1', '4', NULL, '观察', '包扎', '止血', '询问', 'C', '抢救失血伤员，首先要止血。', 14, 1, '2021-12-19 01:27:40', '2021-12-19 01:27:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41087, '在没有绷带急救伤员的情况下，以下救护行为中错误的是什么？', '', '1', '4', NULL, '用手帕包扎', '用毛巾包扎', '用棉质衣服包扎', '用细绳缠绕包扎', 'D', '先看清题意，问的是错误的做法。细绳不平整，不能压紧血管，会影响止血效果。包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内的血液再在流通，达到止血的目的。包扎时应尽量使用绷带、三角巾或其他干净的棉质衣物、毛巾、手帕、床单、长筒尼龙袜子等代替，避免伤口感染。', 14, 1, '2021-12-19 01:27:40', '2021-12-19 01:27:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41088, '采用指压止血法为动脉出血伤员止血时，拇指压住伤口的什么位置？', '', '1', '4', NULL, '近心端动脉', '血管下方动脉', '远心端动脉', '血管中部', 'A', '伤员较大动脉出血时，采用指压止血法，用拇指压住伤口的近心端动脉，阻断动脉运动，达到快速止血的目的。', 14, 1, '2021-12-19 01:27:40', '2021-12-19 01:27:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41089, '包扎止血不能用的物品是什么？', '', '1', '4', NULL, '绷带', '三角巾', '止血带', '麻绳', 'D', '包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内的血液再在流通，达到止血的目的。 麻绳，一般说来总是比较粗的，是由几股细绳绞合而成的。其表面不是平整光滑的，也不够柔软，不适合用以止血包扎。有时，可见护士用纱布撕成条，再合并起来用以包扎止血，这是因为纱布比较柔软，合并后较粗一些，包扎效果更好。', 14, 1, '2021-12-19 01:27:40', '2021-12-19 01:27:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41090, '在没有绷带急救伤员的情况下，可用毛巾、手帕、床单、长筒尼龙袜子等代替绷带包扎。', '', '2', '4', NULL, '', '', '', '', 'A', '包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内的血液再在流通，达到止血的目的。包扎时应尽量使用绷带、三角巾或其他干净的棉质衣物、毛巾、手帕、床单、长筒尼龙袜子等代替，避免伤口感染。', 14, 1, '2021-12-19 01:27:41', '2021-12-19 01:27:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41091, '在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', '', '2', '4', NULL, '', '', '', '', 'A', '压迫止血法是最直接、最简单、最有效地制止外部出血的方法。在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', 14, 1, '2021-12-19 01:27:41', '2021-12-19 01:27:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41092, '救助失血过多出现休克的伤员要采取保暖措施。', '', '2', '4', NULL, '', '', '', '', 'A', '因失血过多出现休克时，由于血容量减少，刺激交感神经兴奋，引起皮肤苍白，四肢厥冷、出冷汗、体温下降等表现，所以除输液、输血等急救措施外，还要采取保暖措施，避免休克加重。。', 14, 1, '2021-12-19 01:27:41', '2021-12-19 01:27:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41093, '救助全身燃烧伤员采取哪种应急措施？', '', '1', '4', NULL, '用沙土覆盖火焰灭火', '向身上喷冷水灭火', '用灭火器进行灭火', '帮助脱掉燃烧的衣服', 'B', '救助全身燃烧伤员，迅速扑灭衣服上的火，向全身燃烧伤员身上喷冷水，用消过毒的绷带包扎烧伤伤口。使用沙土覆盖会造成伤口感染，甚至危及生命；灭火器喷出的物质可能会造成身体上的伤害；脱去受伤人员燃烧的衣服一方面会对救助者自身安全造成威胁，二来可能会对伤者的皮肤造成二次伤害。', 14, 1, '2021-12-19 01:27:41', '2021-12-19 01:27:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41094, '烧伤伤员口渴时，可喝少量的淡盐水。', '', '2', '4', NULL, '', '', '', '', 'A', '救助全身燃烧的伤员，迅速扑灭衣服上的火焰、向全身燃烧伤员身上喷冷水、用消过毒的绷带包扎烧伤伤口。用沙土覆盖会造成伤口感染，甚至危及生命。烧伤伤员口渴时，可喝少量的淡盐水。当伤口已经起泡的情况下，可以用塑料袋或保鲜膜轻轻覆盖在水泡上进行保护，尽快将伤者送往医院。', 14, 1, '2021-12-19 01:27:42', '2021-12-19 01:27:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41095, '烧伤伤员口渴时，只能喝白开水。', '', '2', '4', NULL, '', '', '', '', 'B', '救助全身燃烧的伤员，迅速扑灭衣服上的火焰、向全身燃烧伤员身上喷冷水、用消过毒的绷带包扎烧伤伤口。用沙土覆盖会造成伤口感染，甚至危及生命。烧伤伤员口渴时，可喝少量的淡盐水。当伤口已经起泡的情况下，可以用塑料袋或保鲜膜轻轻覆盖在水泡上进行保护，尽快将伤者送往医院。', 14, 1, '2021-12-19 01:27:42', '2021-12-19 01:27:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41096, '救助全身燃烧伤员可以采取向身上喷冷水灭火的措施。', '', '2', '4', NULL, '', '', '', '', 'A', '救助全身燃烧的伤员，迅速扑灭衣服上的火焰、向全身燃烧伤员身上喷冷水。用沙土覆盖会造成伤口感染，甚至危及生命。烧伤伤员口渴时，可喝少量的淡盐水。', 14, 1, '2021-12-19 01:27:42', '2021-12-19 01:27:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41097, '救助有害气体中毒伤员，首先采取的措施是什么？', '', '1', '4', NULL, '采取保暖措施', '将伤员转移到有新鲜空气的地方', '进行人工呼吸', '进行胸外心脏按压', 'B', '救助有害气体中毒伤员时，要在第一时间迅速将伤员移到有新鲜空气的地方，以防止继续中毒。', 14, 1, '2021-12-19 01:27:45', '2021-12-19 01:27:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41098, '为防止有害气体中毒伤员继续中毒，首先将伤员转移到空气新鲜的地方。', '', '2', '4', NULL, '', '', '', '', 'A', '救助有害气体中毒伤员时，要在第一时间迅速将伤员移到有新鲜空气的地方，以防止继续中毒。题干做法正确。', 14, 1, '2021-12-19 01:27:45', '2021-12-19 01:27:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41099, '抢救骨折伤员时注意什么？', '', '1', '4', NULL, '迅速抬上担架送往医院', '适当调整损伤时的姿势', '用绷带对骨折部位进行包扎', '不要移动身体骨折部位', 'D', '抢救骨折伤员时，为防止骨折伤员休克，不要移动伤员身体的骨折部位。', 14, 1, '2021-12-19 01:27:45', '2021-12-19 01:27:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41100, '怎样抢救脊柱骨折的伤员？', '', '1', '4', NULL, '采取保暖措施', '用软板担架运送', '用三角巾固定', '扶持伤者移动', 'C', '【紧急情况处理-脊柱损伤伤员救助】对于脊柱损伤的伤员，如果搬运不当则会造成二次伤害，因此如果救助人员无法判断伤员的伤情，一般不要对伤员进行搬运。如果情况紧急，如周围发生火灾或即将发生爆炸等，不得不对伤员进行搬运时，对脊柱骨折的伤员要用三角巾固定。移动脊柱骨折的伤员，切勿扶持伤者走动，应由3名人员把手托放在伤员身下，一起将伤员抬上硬担架运送。', 14, 1, '2021-12-19 01:27:45', '2021-12-19 01:27:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41101, '伤员骨折处出血时，要先固定，然后止血和包扎伤口。', '', '2', '4', NULL, '', '', '', '', 'B', '伤员骨折处出血时，应先止血并消毒包扎伤口，然后再固定。', 14, 1, '2021-12-19 01:27:45', '2021-12-19 01:27:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41102, '移动脊柱骨折的伤员，切勿扶持伤者走动，可用软担架运送。', '', '2', '4', NULL, '', '', '', '', 'B', '本题错在软担架运送。移动脊柱骨折的伤员，切勿扶持伤者走动，应由3名人员把手托放在伤员身下，一起将伤员抬上硬担架运送。', 14, 1, '2021-12-19 01:27:46', '2021-12-19 01:27:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41103, '伤员大腿、小腿和脊椎骨折时，一般不要随便移动伤者。', '', '2', '4', NULL, '', '', '', '', 'A', '对于脊柱损伤的伤员，如果搬运不当则会造成二次伤害，因此如果救助人员无法判断伤员的伤情，一般不要对伤员进行搬运。', 14, 1, '2021-12-19 01:27:46', '2021-12-19 01:27:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41104, '对无骨端外露的骨折伤员肢体固定时，要超过伤口上下关节。', '', '2', '4', NULL, '', '', '', '', 'A', '对无骨端外露骨折伤员的肢体，用夹板或木棍、树枝等固定时要超过伤口上、下关节。', 14, 1, '2021-12-19 01:27:46', '2021-12-19 01:27:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41105, '伤员骨折处出血时，首先要对伤口进行止血和包扎，再固定肢体。', '', '2', '4', NULL, '', '', '', '', 'A', '伤员骨折处出血时，首先要对伤口进行止血并包扎，然后再固定。', 14, 1, '2021-12-19 01:27:46', '2021-12-19 01:27:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41106, '火药、炸药和起爆药属于哪类危险化学品？', '', '1', '4', NULL, '氧化性物质', '易燃固体', '爆炸品', '自燃物品', 'C', '氧化性物质是指在有氧气的环境下会被氧化，发生化学反应，但火药、炸药和起爆药这三类物质在空气中不会自己变化，因此不是氧化性物质；三类物质均不会无缘无故自燃，都需要明火，需要明火点燃的都不是易燃物品。因此这三类都属于爆炸品。', 14, 1, '2021-12-19 01:27:47', '2021-12-19 01:27:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41107, '火柴、硫黄和赤磷属于哪类危险化学品？', '', '1', '4', NULL, '爆炸品', '氧化性物质', '自燃物品', '易燃固体', 'D', '火柴、硫黄和赤磷属于易燃固体类危险化学品。易燃固体是燃烧点低，遇火、受热、撞击、摩擦或与氧化剂接触后，易引起急剧燃烧或爆炸的固态物资。', 14, 1, '2021-12-19 01:27:47', '2021-12-19 01:27:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41108, '下列属于危险易燃固体的是什么？', '', '1', '4', NULL, '火柴', '火药', '电石', '炸药', 'A', '火柴、硫黄和赤磷属于易燃固体类危险化学品。易燃固体是燃烧点低，遇火、受热、撞击、摩擦或与氧化剂接触后，易引起急剧燃烧或爆炸的固态物资。', 14, 1, '2021-12-19 01:27:47', '2021-12-19 01:27:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41109, '易燃液体一旦发生火灾，要及时用水扑救。', '', '2', '4', NULL, '', '', '', '', 'B', '易燃液体着火时应当使用灭火器或路边砂土、棉衣等覆盖法灭火，不能使用水灭火。', 14, 1, '2021-12-19 01:27:47', '2021-12-19 01:27:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41110, '腐蚀品着火时，不能用水柱直接喷射扑救。', '', '2', '4', NULL, '', '', '', '', 'A', '无机腐蚀品或有机腐蚀品直接燃烧时，除具有与水反应特性的物质外，一般可用大量的水扑救。但宜用雾状水，不能用高压水柱直接喷射物品，以免飞溅的水珠带上腐蚀品灼伤灭火人员。 液体腐蚀品应用干砂、干土覆盖吸收，扫干净后，再用水洗刷。大量溢出时可用稀酸或稀碱中和。中和时，要防止发生剧烈反应。用水洗刷撒漏现场时，只能缓慢地浇洗或用雾状水喷淋，以防水珠飞溅伤人。', 14, 1, '2021-12-19 01:27:48', '2021-12-19 01:27:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41111, '危险化学品具有爆炸、易燃、毒害、腐蚀、放射性等特性。', '', '2', '4', NULL, '', '', '', '', 'A', '危险化学品是指具有毒害、腐蚀、爆炸、燃烧、助燃等性质，对人体、设施、环境具有危害的剧毒化学品和其他化学品。因此本题正确。', 14, 1, '2021-12-19 01:27:48', '2021-12-19 01:27:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41112, '扑救易散发腐蚀性蒸气或有毒气体的火灾时，扑救人员应穿戴防毒面具和相应的防护用品，站在上风处施救。', '', '2', '4', NULL, '', '', '', '', 'A', '由于是“易散发腐蚀性蒸气或有毒气体的火灾”，因此既要做到不中毒，也要做到不被烫伤等，因此在扑救时，要穿戴防毒面具和相应的防护用品，这是为了防止中毒，而站在上风处施救，是因为不会因火势被伤到。 因此本题是对的。', 14, 1, '2021-12-19 01:27:48', '2021-12-19 01:27:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41113, '液化石油气罐车在运输途中发生大量泄漏时，下列措施错误的是什么？', '', '1', '4', NULL, '切断一切电源', '戴好防护面具和手套', '关闭阀门制止渗漏', '组织人员向下风方向疏散', 'D', '发生危险物品泄漏、着火等危险情况时，驾驶人应切断一切电源，戴好防护面罩和手套，关闭阀门制止泄漏，并尽快组织车上人员下车撤离到上风安全区域，同时做好隔离和警戒工作，劝导周围群众不围观，并远离危险区域。', 14, 1, '2021-12-19 01:27:48', '2021-12-19 01:27:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41114, '道路危险货物运输驾驶人、装卸人员和押运员必须了解所运载的危险化学品的性质、危害特性、包装容器的使用特性和发生意外时的应急措施。', '', '2', '4', NULL, '', '', '', '', 'A', '根据《道路运输从业人员管理规定》第十二条 道路危险货物运输装卸管理人员和押运人员应当符合下列条件： （三）接受相关法规、安全知识、专业技术、职业卫生防护和应急救援知识的培训，了解危险货物性质、危害特征、包装容器的使用特性和发生意外时的应急措施； 因此本题是对的。', 14, 1, '2021-12-19 01:27:49', '2021-12-19 01:27:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41115, '驾驶机动车发生交通事故后，应注意是否有燃油泄漏、管路破裂的情况，避免意外情况出现。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车发生交通事故后，应注意是否有燃油泄漏、管路破裂的情况，避免意外情况出现。', 14, 1, '2021-12-19 01:27:49', '2021-12-19 01:27:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41116, '驾驶机动车遇车辆出现燃烧现象，应迅速离开车内，以免对呼吸道造成伤害或发生窒息。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇车辆出现燃烧现象，应迅速离开车内，以免对呼吸道造成伤害或发生窒息。', 14, 1, '2021-12-19 01:27:49', '2021-12-19 01:27:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41117, '抢救有害气体中毒伤员时，应第一时间将伤员移送到有新鲜空气的地方，脱离危险环境，防止吸入更多有害气体。', '', '2', '4', NULL, '', '', '', '', 'A', '救助有害气体中毒伤员时，要在第一时间迅速将伤员移到有新鲜空气的地方，以防止继续中毒。因此，题干表述正确。', 14, 1, '2021-12-19 01:27:49', '2021-12-19 01:27:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41118, '交通事故中急救中毒伤员，以下做法错误的是什么？', '', '1', '4', NULL, '尽快将中毒人员移出毒区', '脱去接触有毒空气的衣服', '用清水清洗暴露部位', '原地等待救援', 'D', '请注意，题干要求选出做法错误的选项。交通事故中急救中毒伤员，要第一时间迅速将伤员移到有新鲜空气的地方，以防止继续中毒。原地等待救援只会令中毒伤员更严重，因此做法错误。', 14, 1, '2021-12-19 01:27:50', '2021-12-19 01:27:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41119, '在交通事故现场，一旦遇到有毒有害物质泄漏，一定要第一时间疏散人员，并立即报警。', '', '2', '4', NULL, '', '', '', '', 'A', '《职业道德与安全行车知识》说明：在交通事故现场，一旦遇到有毒有害物质泄漏，一定要第一时间疏散人员，并立即报警。', 14, 1, '2021-12-19 01:27:50', '2021-12-19 01:27:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41120, '因交通事故造成有害气体泄漏后，进入现场抢救伤员时，抢救人员须佩戴空气呼吸器或用湿毛巾捂住口鼻。', '', '2', '4', NULL, '', '', '', '', 'A', '《职业道德与安全行车知识》说明：因交通事故造成有害气体泄漏后，进入现场抢救伤员时，抢救人员须佩戴空气呼吸器或用湿毛巾捂住口鼻。', 14, 1, '2021-12-19 01:27:51', '2021-12-19 01:27:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41121, '紧急情况下避险始终要把人的生命安全放到第一位。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶过程中遇到紧急情况处理时要把生命安全放在第一位。', 14, 1, '2021-12-19 01:27:51', '2021-12-19 01:27:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41122, '在事故现场抢救伤员的基本要求是什么？', '', '1', '4', NULL, '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', '先治伤，后救命', 'A', '优先救治有生命危险的，再救受伤的。受伤的人员没有生命危险的还可以暂缓一下，有生命危险的救治不及时就可能造成伤者死亡。', 15, 1, '2021-12-19 01:42:46', '2021-12-19 01:42:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41123, '遇伤者被压于车轮或货物下时，要立即拉拽伤者的肢体将其拖出。', '', '2', '4', NULL, '', '', '', '', 'B', '不能硬拽，车轮压住了伤者，必须将车轮或者货物移开后才能将伤者移出，如果硬拖，会使伤者受伤更加严重。', 15, 1, '2021-12-19 01:42:47', '2021-12-19 01:42:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41124, '受伤者在车内无法自行下车时，可设法将其从车内移出，尽量避免二次受伤。', '', '2', '4', NULL, '', '', '', '', 'A', '伤者无法自行下车，就说明伤者行动不便，这时应设法将其移出，以免汽车发生突发状况，造成第二次伤害。', 15, 1, '2021-12-19 01:42:48', '2021-12-19 01:42:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41125, '抢救昏迷失去知觉的伤员需注意什么？', '', '1', '4', NULL, '马上实施心肺复苏', '连续拍打伤员面部', '抢救前先检查呼吸', '使劲掐伤员的人中', 'C', '抢救昏迷的伤员时，应先确认伤员的生命体征，也就是先检查呼吸。然后再根据情况进行施救。', 15, 1, '2021-12-19 01:42:49', '2021-12-19 01:42:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41126, '搬运昏迷失去知觉的伤员首先要采取仰卧位。', '', '2', '4', NULL, '', '', '', '', 'B', '正确的做法是首先开放气道，再采取去枕仰卧位，头偏向一侧，保持呼吸道通畅，以防窒息。记住首先是放开气道，再采取仰卧位。', 15, 1, '2021-12-19 01:42:50', '2021-12-19 01:42:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41127, '抢救昏迷失去知觉的伤员要在抢救前先检查呼吸。', '', '2', '4', NULL, '', '', '', '', 'A', '检查呼吸的目的是为了了解伤员的生命体征，这是必须要先确认的。', 15, 1, '2021-12-19 01:42:51', '2021-12-19 01:42:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41128, '抢救失血伤员时，要先采取什么措施？', '', '1', '4', NULL, '观察', '询问', '包扎', '止血', 'D', '既然是失血伤员，首先要做的当然是止血。', 15, 1, '2021-12-19 01:42:52', '2021-12-19 01:42:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41129, '在没有绷带急救伤员的情况下，以下救护行为中错误的是什么？', '', '1', '4', NULL, '用细绳缠绕包扎', '用手帕包扎', '用棉质衣服包扎', '用毛巾包扎', 'A', '首先看清题意，问的是错误的做法。明显细绳不能用来包扎伤口，因为细绳表面不是平整光滑的，也不够柔软，只会让伤口更加严重。', 15, 1, '2021-12-19 01:42:53', '2021-12-19 01:42:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41130, '采用指压止血法为动脉出血伤员止血时，拇指压住伤口的什么位置？', '', '1', '4', NULL, '近心端动脉', '血管下方动脉', '血管中部', '远心端动脉', 'A', '近心端就是靠近心脏方向的一端。动脉血管中血液的流动依靠心脏搏动来完成，如果动脉血管出血，压迫近心端血管就可以降低心脏对血液的传递，达到减少出血的目的。因此选择“近心端动脉”。', 15, 1, '2021-12-19 01:42:55', '2021-12-19 01:42:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41131, '包扎止血不能用的物品是什么？', '', '1', '4', NULL, '绷带', '止血带', '三角巾', '麻绳', 'D', '首先看清题意，问的是不能用的物品。明显粗糙的麻绳是不能用来止血包扎的，其表面不是平整光滑的，也不够柔软，用了只会加重伤势。包扎止血，要求包扎物表面平整而且松软，这样才能有效地扎紧受伤部位体内的血管，阻止血管内血液的再流通，达到止血的目的。', 15, 1, '2021-12-19 01:42:56', '2021-12-19 01:42:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41132, '在没有绷带急救伤员的情况下，可用毛巾、手帕、床单、长筒尼龙袜子等代替绷带包扎。', '', '2', '4', NULL, '', '', '', '', 'A', '上述的物品质地都较为柔软，在没有绷带的情况下可作为替代物使用，根据受伤位置的不同和创面的大小，你可以利用毛巾、手帕做成简易的三角巾，用于手臂骨折的固定等，长筒尼龙袜子可以作为固定的捆绑绳。', 15, 1, '2021-12-19 01:42:57', '2021-12-19 01:42:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41133, '在紧急情况下为伤员止血时，须先用压迫法止血后再根据出血情况改用其他止血法。', '', '2', '4', NULL, '', '', '', '', 'A', '一般小动脉和静脉出血可用加压包扎止血法，较大的动脉出血，应用止血带止血。但在紧急情况下，须先用压迫法止血，然后再根据出血情况改用其它止血法。指压止血法可以在较大的动脉出血后，用拇指压住出血的血管上方(近心端)，使血管被压闭住，中断血液。不需要大家理解其中原理，只需牢记即可。', 15, 1, '2021-12-19 01:42:58', '2021-12-19 01:42:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41134, '救助失血过多出现休克的伤员要采取保暖措施。', '', '2', '4', NULL, '', '', '', '', 'A', '失血过多+休克的伤员。注意是休克，休克的意思是血压下降，口渴，体温下降，意识模糊，皮肤发白，尿量减少的一种状态。所以要采取保暖措施.不需要大家理解其中原理，只需要记住即可。', 15, 1, '2021-12-19 01:42:59', '2021-12-19 01:42:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41135, '救助全身燃烧伤员采取哪种应急措施？', '', '1', '4', NULL, '用灭火器进行灭火', '帮助脱掉燃烧的衣服', '用沙土覆盖火焰灭火', '向身上喷冷水灭火', 'D', '救助全身燃烧伤员采取的最安全的方式是向身上喷冷水灭火。其他的方式或多或少会对伤员有害或者加重伤情。首先大家要明确，燃烧过后人体皮肤已经受损。而沙土上附着的病原体会感染受伤者的皮肤，事后的清理和后续治疗也会相当的麻烦；灭火器喷出的东西也可能会对受伤皮肤造成伤害；脱去受伤人员燃烧的衣服可能对其皮肤造成二次伤害。', 15, 1, '2021-12-19 01:43:00', '2021-12-19 01:43:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41136, '烧伤伤员口渴时，可喝少量的淡盐水。', '', '2', '4', NULL, '', '', '', '', 'A', '较严重的烧伤病人常有口渴的感觉。这是由于皮肤大面积烧伤后，体液从伤面大量外渗，致使体内血容量下降，水分减少所致。伤员口渴感越重，表示伤情越重。按医学要求，烧伤后口渴时不能给伤员饮白开水。因在烧伤后，体液丢失的同时，体液中的钠盐也跟着一起丧失，如果单纯给病人喝白开水，可导致血液内氯化钠浓度进一步下降，细胞外液渗透压降低，引起细胞内水肿，出现脑水肿或肺水肿，形成所谓的水中毒，可危及伤员生命。因此，这时千万别给病人喝白开水，但可喝含盐饮料。', 15, 1, '2021-12-19 01:43:01', '2021-12-19 01:43:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41137, '烧伤伤员口渴时，只能喝白开水。', '', '2', '4', NULL, '', '', '', '', 'B', '烧伤伤员可以喝淡盐水补充体液。如果单纯给病人喝白开水，可导致血液内氯化钠浓度进一步下降，细胞外液渗透压降低，引起细胞内水肿，出现脑水肿或肺水肿，形成所谓的水中毒，可危及伤员生命。不论其他，光看“只能”这么绝对的词，就知道描述错误。', 15, 1, '2021-12-19 01:43:02', '2021-12-19 01:43:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41138, '救助全身燃烧伤员可以采取向身上喷冷水灭火的措施。', '', '2', '4', NULL, '', '', '', '', 'A', '大家记住，救助全身燃烧伤员最安全的措施就是向伤员身上喷冷水灭火，这样既能给伤员的受伤创面降温，还可以清洁伤口。', 15, 1, '2021-12-19 01:43:03', '2021-12-19 01:43:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41139, '救助有害气体中毒伤员，首先采取的措施是什么？', '', '1', '4', NULL, '采取保暖措施', '进行人工呼吸', '进行胸外心脏按压', '将伤员转移到有新鲜空气的地方', 'D', '题目说明是“有害气体中毒伤员”，而空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应将这类伤员转移到有新鲜空气的地方，让伤员呼吸新鲜空气，减少有害气体的伤害，再采取其他措施。', 15, 1, '2021-12-19 01:43:04', '2021-12-19 01:43:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41140, '为防止有害气体中毒伤员继续中毒，首先将伤员转移到空气新鲜的地方。', '', '2', '4', NULL, '', '', '', '', 'A', '题目说明是“有害气体中毒伤员”，而空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应将这类伤员转移到有新鲜空气的地方，让伤员呼吸新鲜空气，减少有害气体的伤害，再采取其他措施。', 15, 1, '2021-12-19 01:43:05', '2021-12-19 01:43:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41141, '抢救骨折伤员时注意什么？', '', '1', '4', NULL, '用绷带对骨折部位进行包扎', '迅速抬上担架送往医院', '不要移动身体骨折部位', '适当调整损伤时的姿势', 'C', '骨折是肉眼看不到的伤害，随意碰触很有可能造成第二次伤害，所以为了不让骨折情况变严重或恶化，尽量不要去触碰，不要移动身体骨折部位。', 15, 1, '2021-12-19 01:43:06', '2021-12-19 01:43:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41142, '怎样抢救脊柱骨折的伤员？', '', '1', '4', NULL, '采取保暖措施', '扶持伤者移动', '用三角巾固定', '用软板担架运送', 'C', '应该是原地固定等待专业救护人员，脊柱骨折被移动很有可能将导致高位截瘫，用三角巾固定是其中最安全的措施。', 15, 1, '2021-12-19 01:43:07', '2021-12-19 01:43:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41143, '伤员骨折处出血时，要先固定，然后止血和包扎伤口。', '', '2', '4', NULL, '', '', '', '', 'B', '伤员骨折处出血，应该是先止血消毒包扎再固定。', 15, 1, '2021-12-19 01:43:08', '2021-12-19 01:43:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41144, '移动脊柱骨折的伤员，切勿扶持伤者走动，可用软担架运送。', '', '2', '4', NULL, '', '', '', '', 'B', '注意是“软担架”，可能会给大家造成误解，脊柱骨折意味着柔软的脊髓面临机械损伤（哪怕是非常微小的震动），所以必须保持脊柱固定（软担架根本不能规避机械损伤），而软担架运送脊柱骨折的伤员会造成第二次伤害。', 15, 1, '2021-12-19 01:43:09', '2021-12-19 01:43:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41145, '伤员大腿、小腿和脊椎骨折时，一般不要随便移动伤者。', '', '2', '4', NULL, '', '', '', '', 'A', '骨折是肉眼看不到的，随意移动可能会造成第二次伤害。脊椎骨折被移动很有可能将导致高位截瘫。所以一般不要移动伤者，待医护人员到后再行处理。', 15, 1, '2021-12-19 01:43:10', '2021-12-19 01:43:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41146, '对无骨端外露的骨折伤员肢体固定时，要超过伤口上下关节。', '', '2', '4', NULL, '', '', '', '', 'A', '超过伤口上下关节是因为不能让这两个关节活动。这样做能防止骨折端的移位，防止造成血管和神经的损伤，后者的损伤是比较头疼的，比单纯的骨折要麻烦多了，尤其是神经的损伤，很可能会造成感觉或运动方面障碍。此外，防止骨折断端戳破皮肤从而造成骨折端的污染影响愈合。不需要大家了解其中原理，牢记即可。', 15, 1, '2021-12-19 01:43:12', '2021-12-19 01:43:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41147, '伤员骨折处出血时，先固定好肢体再进行止血和包扎。', '', '2', '4', NULL, '', '', '', '', 'B', '伤员骨折处出血时，应先止血和消毒，然后再固定。', 15, 1, '2021-12-19 01:43:13', '2021-12-19 01:43:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41148, '火药、炸药和起爆药属于哪类危险化学品？', '', '1', '4', NULL, '氧化性物质', '易燃固体', '自燃物品', '爆炸品', 'D', '火药、炸药和起爆药品属于危险品中的易爆品。', 15, 1, '2021-12-19 01:43:14', '2021-12-19 01:43:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41149, '火柴、硫黄和赤磷属于哪类危险化学品？', '', '1', '4', NULL, '易燃固体', '氧化性物质', '自燃物品', '爆炸品', 'A', '易燃固体是指在常温下以固态形式存在，燃点较低，遇火受热、撞击、摩擦或接触氧化剂能引起燃烧的物质，这三类物质刚好符合这些特性，因此选“易燃固体”。', 15, 1, '2021-12-19 01:43:15', '2021-12-19 01:43:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41150, '下列属于危险易燃固体的是什么？', '', '1', '4', NULL, '电石', '炸药', '火柴', '火药', 'C', '易燃固体是指在常温下以固态形式存在，燃点较低，遇火受热、撞击、摩擦或接触氧化剂能引起燃烧的物质。只有火柴是易燃固体，其他选项属于易燃易爆固体。爆炸物泛指能够引起爆炸现象的物质。例如炸药、雷管、黑火药等。 粉尘、可燃气体、燃油、锯末等在特定条件下引起爆炸的物质，广义上也属于爆炸物。', 15, 1, '2021-12-19 01:43:16', '2021-12-19 01:43:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41151, '易燃液体一旦发生火灾，要及时用水扑救。', '', '2', '4', NULL, '', '', '', '', 'B', '不是所有的火都能用水扑救的，如果易燃液体密度比水小，就不应该用水，因为易燃液体会浮在水面上继续燃烧，所以本题错误。', 15, 1, '2021-12-19 01:43:17', '2021-12-19 01:43:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41152, '腐蚀品着火时，不能用水柱直接喷射扑救。', '', '2', '4', NULL, '', '', '', '', 'A', '无机腐蚀品或有机腐蚀品直接燃烧时，除具有与水反应特性的物质外，一般可用大量的水补救。但宜用雾状水，不能用高压水柱直接喷射物品，以免飞溅的水珠带上腐蚀品灼伤灭火人员。液体腐蚀品应用干砂、干土覆盖吸收，扫干净后，再用水洗刷。大量溢出时可用稀酸或稀碱中和。中和时，要防止发生剧烈反应。用水洗刷撒漏现场时，只能缓慢地浇洗或用雾状水喷淋，以防水珠飞溅伤人。因此本题论述正确。', 15, 1, '2021-12-19 01:43:18', '2021-12-19 01:43:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41153, '危险化学品具有爆炸、易燃、毒害、腐蚀、放射性等特性。', '', '2', '4', NULL, '', '', '', '', 'A', '危险化学品是指具有毒害、腐蚀、爆炸、燃烧、助燃等性质，对人体、设施，环境具有危害的剧毒化学品和和其他化学品。因此本题正确。', 15, 1, '2021-12-19 01:43:19', '2021-12-19 01:43:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41154, '扑救易散发腐蚀性蒸气或有毒气体的火灾时，扑救人员应穿戴防毒面具和相应的防护用品，站在上风处施救。', '', '2', '4', NULL, '', '', '', '', 'A', '注意题目是“易散发腐蚀性蒸气或有毒气体的火灾”，因此在扑救时，要戴防毒面具阻隔有害气体，站在上风处避免风将有害气体吹过来。', 15, 1, '2021-12-19 01:43:20', '2021-12-19 01:43:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41155, '液化石油气罐车在运输途中发生大量泄漏时，下列措施错误的是什么？', '', '1', '4', NULL, '切断一切电源', '关闭阀门制止渗漏', '组织人员向下风方向疏散', '戴好防护面具和手套', 'C', '看清题意，问的是错误的措施。人员不应向下风方向疏散，因为风会将气体带到下风处，正确的做法是往上风方向疏散。', 15, 1, '2021-12-19 01:43:21', '2021-12-19 01:43:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41156, '道路危险货物运输驾驶人、装卸人员和押运员必须了解所运载的危险化学品的性质、危害特性、包装容器的使用特性和发生意外时的应急措施。', '', '2', '4', NULL, '', '', '', '', 'A', '依据《道路运输从业人员管理规定》第十二条 道路危险货物运输装卸管理人员和押运人员应当符合下列条件：（三）接受相关法规、安全知识、专业技术、职业卫生防护和应急救援知识的培训，了解危险货物性质、危害特征、包装容器的使用特性和发生意外时的应急措施。', 15, 1, '2021-12-19 01:43:22', '2021-12-19 01:43:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41157, '驾驶机动车发生交通事故后，应注意是否有燃油泄漏、管路破裂的情况，避免意外情况出现。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车发生交通事故后，如题中所述，应该注意是否有燃油泄漏、管路破裂的情况，避免意外情况出现。', 15, 1, '2021-12-19 01:43:23', '2021-12-19 01:43:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41158, '驾驶机动车遇车辆出现燃烧现象，应迅速离开车内，以免对呼吸道造成伤害或发生窒息。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车遇车辆出现燃烧现象，应迅速离开车内，以免对呼吸道造成伤害或发生窒息。因此题目是正确的。', 15, 1, '2021-12-19 01:43:24', '2021-12-19 01:43:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41159, '抢救有害气体中毒伤员时，应第一时间将伤员移送到有新鲜空气的地方，脱离危险环境，防止吸入更多有害气体。', '', '2', '4', NULL, '', '', '', '', 'A', '中毒伤员所处的空间肯定空气不流通，空气新鲜的地方可以让人体的呼吸管道起畅通的作用。因此应当先转移位置到空气新鲜的地方。', 15, 1, '2021-12-19 01:43:25', '2021-12-19 01:43:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41160, '交通事故中急救中毒伤员，以下做法错误的是什么？', '', '1', '4', NULL, '用清水清洗暴露部位', '原地等待救援', '尽快将中毒人员移出毒区', '脱去接触有毒空气的衣服', 'B', '交通事故中急救中毒伤员，需要：尽快将中毒人员移出毒区；脱去接触有毒空气的衣服；用清水清洗暴露部位。原地等待救援只会令中毒伤员伤情严重。', 15, 1, '2021-12-19 01:43:26', '2021-12-19 01:43:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41161, '在交通事故现场，一旦遇到有毒有害物质泄漏，一定要第一时间疏散人员，并立即报警。', '', '2', '4', NULL, '', '', '', '', 'A', '《职业道德与安全行车知识》说明：在交通事故现场，一旦遇到有毒有害物质泄漏，一定要第一时间疏散人员，并立即报警。', 15, 1, '2021-12-19 01:43:27', '2021-12-19 01:43:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41162, '因交通事故造成有害气体泄漏后，进入现场抢救伤员时，抢救人员须佩戴空气呼吸器或用湿毛巾捂住口鼻。', '', '2', '4', NULL, '', '', '', '', 'A', '《职业道德与安全行车知识》说明：因交通事故造成有害气体泄漏后，进入现场抢救伤员时，抢救人员须佩戴空气呼吸器或用湿毛巾捂住口鼻。因此本题是正确的。', 15, 1, '2021-12-19 01:43:29', '2021-12-19 01:43:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41163, '驾驶机动车在高速公路行驶过程中，发现前方有动物突然横穿时，不可以采取急转向的方式避让。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车在高速公路行驶过程中，发现前方有动物突然横穿时，驾驶员的第一反应应该迅速踏制动踏板，如果有足够的距离，应迅速停车。不可急转向以防发生事故。', 15, 1, '2021-12-19 01:43:30', '2021-12-19 01:43:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41164, '紧急情况下避险始终要把人的生命安全放到第一位。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶过程中遇到紧急情况处理时要把生命安全放在第一位。因此本题是正确的。', 15, 1, '2021-12-19 01:43:31', '2021-12-19 01:43:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41165, '这个标志是何含义？', '/uploadFile/yuanBeiTu41165.gif', '1', '4', NULL, '十字交叉路口', 'T型交叉路口', '环行交叉路口', 'Y型交叉路口', 'A', '图标中为十字，属于十字交叉标志。', 15, 1, '2021-12-19 01:43:32', '2021-12-19 01:43:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41166, '这个标志是何含义？', '/uploadFile/yuanBeiTu41166.gif', '1', '4', NULL, 'T型交叉路口', 'Y型交叉路口', '十字交叉路口', '环行交叉路口', 'A', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', 15, 1, '2021-12-19 01:43:33', '2021-12-19 01:43:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41167, '这个标志是何含义？', '/uploadFile/yuanBeiTu41167.gif', '1', '4', NULL, '向左急转弯', '向右急转弯', '向右绕行', '连续弯路', 'B', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 15, 1, '2021-12-19 01:43:34', '2021-12-19 01:43:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41168, '这个标志是何含义？', '/uploadFile/yuanBeiTu41168.gif', '1', '4', NULL, '向左急转弯', '向右急转弯', '向左绕行', '连续弯路', 'A', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', 15, 1, '2021-12-19 01:43:35', '2021-12-19 01:43:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41169, '这是什么交通标志？', '/uploadFile/yuanBeiTu41169.gif', '1', '4', NULL, '易滑路段', '急转弯路', '反向弯路', '连续弯路', 'C', '这是反向弯路标志，不是连续弯路标志。', 15, 1, '2021-12-19 01:43:36', '2021-12-19 01:43:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41170, '这是什么交通标志？', '/uploadFile/yuanBeiTu41170.gif', '1', '4', NULL, '易滑路段', '急转弯路', '反向弯路', '连续弯路', 'D', '这是连续弯路标志。', 15, 1, '2021-12-19 01:43:37', '2021-12-19 01:43:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41171, '这个标志是何含义？', '/uploadFile/yuanBeiTu41171.gif', '1', '4', NULL, '堤坝路', '上陡坡', '连续上坡', '下陡坡', 'B', '上陡坡交通警告标志设在纵坡度在7%和市区纵坡度在大于4%的陡坡道路前适当位置。在上陡坡时会有视觉上的盲点，应缓速驾车，以防车祸发生。', 15, 1, '2021-12-19 01:43:39', '2021-12-19 01:43:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41172, '这个标志是何含义？', '/uploadFile/yuanBeiTu41172.gif', '1', '4', NULL, '堤坝路', '上陡坡', '下陡坡', '连续上坡', 'C', '下陡坡：此标志设在纵坡度在7%和市区纵坡度大于4%的陡坡道路前适当位置。用以提醒车辆驾驶人小心驾驶。', 15, 1, '2021-12-19 01:43:40', '2021-12-19 01:43:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41173, '这个标志是何含义？', '/uploadFile/yuanBeiTu41173.gif', '1', '4', NULL, '提醒车辆驾驶人前方有两个及以上的连续上坡路段', '提醒车辆驾驶人前方有向上的陡坡路段', '提醒车辆驾驶人前方有向下的陡坡路段', '提醒车辆驾驶人前方有两个及以上的连续下坡路段', 'D', '警告标志，前方有两个及以上的连续下坡路段。当连续下坡总长大于3 km后，应重复设置。', 15, 1, '2021-12-19 01:43:41', '2021-12-19 01:43:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41174, '这是什么交通标志？', '/uploadFile/yuanBeiTu41174.gif', '1', '4', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'A', '两侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减达到6 m以下的路段起点前方。', 15, 1, '2021-12-19 01:43:42', '2021-12-19 01:43:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41175, '这是什么交通标志？', '/uploadFile/yuanBeiTu41175.gif', '1', '4', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'B', '右侧变窄：用以警告车辆驾驶人注意前方右侧车行道或路面狭窄情况，遇有来车应予减速避让。', 15, 1, '2021-12-19 01:43:43', '2021-12-19 01:43:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41176, '这是什么交通标志？', '/uploadFile/yuanBeiTu41176.gif', '1', '4', NULL, '两侧变窄', '右侧变窄', '左侧变窄', '桥面变窄', 'C', '左侧变窄：用以警告车辆驾驶人注意前方左侧车行道或路面狭窄情况，遇有来车应予减速避让。', 15, 1, '2021-12-19 01:43:44', '2021-12-19 01:43:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41177, '这个标志是何含义？', '/uploadFile/yuanBeiTu41177.gif', '1', '4', NULL, '窄路', '右侧变窄', '左侧变窄', '窄桥', 'D', '窄桥：用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6 m的桥梁以前适当位置。', 15, 1, '2021-12-19 01:43:45', '2021-12-19 01:43:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41178, '这个标志是何含义？', '/uploadFile/yuanBeiTu41178.gif', '1', '4', NULL, '人行横道', '注意行人', '注意儿童', '学校区域', 'B', '黄色底色表示注意，这是注意行人，不是人行横道，人行横道标志是蓝底。', 15, 1, '2021-12-19 01:43:46', '2021-12-19 01:43:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41179, '这个标志是何含义？', '/uploadFile/yuanBeiTu41179.gif', '1', '4', NULL, '注意行人', '人行横道', '注意儿童', '学校区域', 'C', '黄色底色表示注意，这是注意儿童标志。', 15, 1, '2021-12-19 01:43:47', '2021-12-19 01:43:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41180, '这个标志是何含义？', '/uploadFile/yuanBeiTu41180.gif', '1', '4', NULL, '交叉路口', '注意信号灯', '注意行人', '人行横道灯', 'B', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', 15, 1, '2021-12-19 01:43:48', '2021-12-19 01:43:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41181, '这个标志是何含义？', '/uploadFile/yuanBeiTu41181.gif', '1', '4', NULL, '注意行人', '有人行横道', '村庄或集镇', '有小学校', 'C', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', 15, 1, '2021-12-19 01:43:50', '2021-12-19 01:43:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41182, '这个标志是何含义？', '/uploadFile/yuanBeiTu41182.gif', '1', '4', NULL, '路面低洼', '驼峰桥', '路面不平', '路面高突', 'C', '这是路面不平的标志', 15, 1, '2021-12-19 01:43:51', '2021-12-19 01:43:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41183, '这个标志是何含义？', '/uploadFile/yuanBeiTu41183.gif', '1', '4', NULL, '路面低洼', '驼峰桥', '路面不平', '路面高突', 'D', '标志很明显了，中间突出一个高处。', 15, 1, '2021-12-19 01:43:52', '2021-12-19 01:43:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41184, '这个标志是何含义？', '/uploadFile/yuanBeiTu41184.gif', '1', '4', NULL, '路面高突', '有驼峰桥', '路面不平', '路面低洼', 'D', '这是路面低洼标志', 15, 1, '2021-12-19 01:43:53', '2021-12-19 01:43:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41185, '这个标志是何含义？', '/uploadFile/yuanBeiTu41185.gif', '1', '4', NULL, '过水路面', '渡口', '泥泞道路', '低洼路面', 'A', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面。', 15, 1, '2021-12-19 01:43:54', '2021-12-19 01:43:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41186, '这个标志是何含义？', '/uploadFile/yuanBeiTu41186.gif', '1', '4', NULL, '无人看守铁路道口', '有人看守铁路道口', '多股铁路与道路相交', '立交式的铁路道口', 'B', '有人看守铁路有闸道标志，表示有人会根据火车是否通过关上闸道，无人看守铁路是有一个火车头标志。', 15, 1, '2021-12-19 01:43:55', '2021-12-19 01:43:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41187, '这个标志是何含义？', '/uploadFile/yuanBeiTu41187.gif', '1', '4', NULL, '多股铁路与道路相交', '有人看守铁路道口', '无人看守铁路道口', '注意长时鸣喇叭', 'C', '有人看守铁路有闸道标志，表示有人会根据火车是否通过关上闸道，无人看守铁路是有一个火车头标志，表示注意观察是否有火车通过。', 15, 1, '2021-12-19 01:43:56', '2021-12-19 01:43:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41188, '这个标志是何含义？', '/uploadFile/yuanBeiTu41188.gif', '1', '4', NULL, '避让非机动车', '非机动车道', '禁止非机动车通行', '注意非机动车', 'D', '黄色底色表示要注意，这是注意非机动车标志。', 15, 1, '2021-12-19 01:43:57', '2021-12-19 01:43:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41189, '这个标志是何含义？', '/uploadFile/yuanBeiTu41189.gif', '1', '4', NULL, '注意残疾人', '残疾人出入口', '残疾人休息处', '残疾人专用通道', 'A', '黄色标志表示要注意，图中为残疾人图案，所以是提示我们注意残疾人。', 15, 1, '2021-12-19 01:43:58', '2021-12-19 01:43:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41190, '这个标志是何含义？', '/uploadFile/yuanBeiTu41190.gif', '1', '4', NULL, '施工路段', '事故易发路段', '减速慢行路段', '拥堵路段', 'B', '事故易发路段：此标志设在交通事故易发路段以前适当位置。', 15, 1, '2021-12-19 01:44:00', '2021-12-19 01:44:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41191, '这个标志是何含义？', '/uploadFile/yuanBeiTu41191.gif', '1', '4', NULL, '施工路段', '车多路段', '慢行', '拥堵路段', 'C', '慢行：此标志设在前方需要减速慢行的路段以前适当位置。', 15, 1, '2021-12-19 01:44:01', '2021-12-19 01:44:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41192, '这个标志是何含义？', '/uploadFile/yuanBeiTu41192.gif', '1', '4', NULL, '事故多发路段', '减速慢行', '注意危险', '拥堵路段', 'C', '黄色代表注意，感叹号是前方有危险， 所以是注意危险。', 15, 1, '2021-12-19 01:44:02', '2021-12-19 01:44:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41193, '这个标志是何含义？', '/uploadFile/yuanBeiTu41193.gif', '1', '4', NULL, '不准车辆驶入', '不准长时间停车', '停车让行', '不准临时停车', 'C', '三角形的是减速让行，八角形的是停车让行。', 15, 1, '2021-12-19 01:44:03', '2021-12-19 01:44:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41194, '这个标志是何含义？', '/uploadFile/yuanBeiTu41194.gif', '1', '4', NULL, '不准让行', '会车让行', '停车让行', '减速让行', 'D', '三角形的是减速让行，八角形的是停车让行。', 15, 1, '2021-12-19 01:44:04', '2021-12-19 01:44:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41195, '这个标志是何含义？', '/uploadFile/yuanBeiTu41195.gif', '1', '4', NULL, '禁止驶入', '禁止通行', '减速行驶', '限时进入', 'B', '此标志表示禁止通行，禁止一切车辆和行人通行。', 15, 1, '2021-12-19 01:44:05', '2021-12-19 01:44:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41196, '这个标志是何含义？', '/uploadFile/yuanBeiTu41196.gif', '1', '4', NULL, '禁止向左转弯', '禁止驶入左车道', '禁止车辆掉头', '禁止向左变道', 'A', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置。', 15, 1, '2021-12-19 01:44:06', '2021-12-19 01:44:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41197, '这个标志是何含义？', '/uploadFile/yuanBeiTu41197.gif', '1', '4', NULL, '禁止驶入路口', '禁止向右转弯', '禁止车辆掉头', '禁止变更车道', 'B', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', 15, 1, '2021-12-19 01:44:07', '2021-12-19 01:44:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41198, '这个标志是何含义？', '/uploadFile/yuanBeiTu41198.gif', '1', '4', NULL, '禁止掉头', '禁止向右转弯', '禁止直行', '禁止向左转弯', 'C', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', 15, 1, '2021-12-19 01:44:08', '2021-12-19 01:44:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41199, '这个标志是何含义？', '/uploadFile/yuanBeiTu41199.gif', '1', '4', NULL, '禁止在路口掉头', '禁止向左向右变道', '禁止车辆直行', '禁止向左向右转弯', 'D', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', 15, 1, '2021-12-19 01:44:10', '2021-12-19 01:44:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41200, '这个标志是何含义？', '/uploadFile/yuanBeiTu41200.gif', '1', '4', NULL, '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'A', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', 15, 1, '2021-12-19 01:44:11', '2021-12-19 01:44:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41201, '这个标志是何含义？', '/uploadFile/yuanBeiTu41201.gif', '1', '4', NULL, '禁止直行和向左转弯', '禁止直行和向左变道', '允许直行和向左变道', '禁止直行和向右转弯', 'D', '禁止直行和向右转弯：表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。', 15, 1, '2021-12-19 01:44:12', '2021-12-19 01:44:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41202, '这个标志是何含义？', '/uploadFile/yuanBeiTu41202.gif', '1', '4', NULL, '禁止直行', '禁止掉头', '禁止变道', '禁止左转', 'B', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', 15, 1, '2021-12-19 01:44:13', '2021-12-19 01:44:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41203, '这个标志是何含义？', '/uploadFile/yuanBeiTu41203.gif', '1', '4', NULL, '禁止借道', '禁止变道', '禁止超车', '禁止掉头', 'C', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', 15, 1, '2021-12-19 01:44:14', '2021-12-19 01:44:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41204, '这个标志是何含义？', '/uploadFile/yuanBeiTu41204.gif', '1', '4', NULL, '解除禁止借道', '解除禁止变道', '准许变道行驶', '解除禁止超车', 'D', '灰色是解除超车限制', 15, 1, '2021-12-19 01:44:15', '2021-12-19 01:44:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41205, '这个标志是何含义？', '/uploadFile/yuanBeiTu41205.gif', '1', '4', NULL, '允许长时停车', '允许临时停车', '禁止长时停车', '禁止停放车辆', 'D', '这是禁止停放车标志', 15, 1, '2021-12-19 01:44:16', '2021-12-19 01:44:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41206, '这个标志是何含义？', '/uploadFile/yuanBeiTu41206.gif', '1', '4', NULL, '允许长时停车', '禁止临时停车', '禁止长时停车', '禁止停放车辆', 'C', '这是禁止长时停车标志', 15, 1, '2021-12-19 01:44:17', '2021-12-19 01:44:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41207, '这个标志是何含义？', '/uploadFile/yuanBeiTu41207.gif', '1', '4', NULL, '禁止长时鸣喇叭', '断续鸣喇叭', '禁止鸣喇叭', '减速鸣喇叭', 'C', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', 15, 1, '2021-12-19 01:44:18', '2021-12-19 01:44:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41208, '这个标志是何含义？', '/uploadFile/yuanBeiTu41208.gif', '1', '4', NULL, '限制40吨轴重', '限制最高时速40公里', '前方40米减速', '最低时速40公里', 'B', '图中标志为限制最高时速40公里：表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', 15, 1, '2021-12-19 01:44:19', '2021-12-19 01:44:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41209, '这个标志是何含义？', '/uploadFile/yuanBeiTu41209.gif', '1', '4', NULL, '40米减速行驶路段', '最低时速40公里', '解除时速40公里限制', '最高时速40公里', 'C', '红色禁令标志，黄色警告标志，蓝色指令标志与一般路段的指路标志，绿色高速路的指路标志，棕色旅游标志。黑色表示解除限制。', 15, 1, '2021-12-19 01:44:21', '2021-12-19 01:44:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41210, '这个标志是何含义？', '/uploadFile/yuanBeiTu41210.gif', '1', '4', NULL, '边防检查', '禁止通行', '海关检查', '停车检查', 'D', '停车检查：表示机动车必须停车接受检查。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。标志中可加注说明检查事项。', 15, 1, '2021-12-19 01:44:22', '2021-12-19 01:44:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41211, '这个标志是何含义？', '/uploadFile/yuanBeiTu41211.gif', '1', '4', NULL, '直行车道', '只准直行', '单行路', '禁止直行', 'B', '此标志表示一切车辆只准直行。', 15, 1, '2021-12-19 01:44:23', '2021-12-19 01:44:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41212, '这个标志是何含义？', '/uploadFile/yuanBeiTu41212.gif', '1', '4', NULL, '直行车道', '单行路', '向左转弯', '禁止直行', 'C', '向左转弯：表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', 15, 1, '2021-12-19 01:44:24', '2021-12-19 01:44:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41213, '这个标志是何含义？', '/uploadFile/yuanBeiTu41213.gif', '1', '4', NULL, '直行车道', '只准直行', '单行路', '向右转弯', 'D', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', 15, 1, '2021-12-19 01:44:25', '2021-12-19 01:44:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41214, '这个标志是何含义？', '/uploadFile/yuanBeiTu41214.gif', '1', '4', NULL, '直行和向右转弯', '直行和向左转弯', '禁止直行和向右转弯', '只准向左和向右转弯', 'A', '直行和向右转弯：表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯的路口以前适当位置。', 15, 1, '2021-12-19 01:44:26', '2021-12-19 01:44:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41215, '这个标志是何含义？', '/uploadFile/yuanBeiTu41215.gif', '1', '4', NULL, '直行和向右转弯', '直行和向左转弯', '禁止直行和向左转弯', '只准向右和向左转弯', 'B', '直行和向左转弯：表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', 15, 1, '2021-12-19 01:44:27', '2021-12-19 01:44:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41216, '这个标志是何含义？', '/uploadFile/yuanBeiTu41216.gif', '1', '4', NULL, '禁止向右转弯', '禁止向左转弯', '向左和向右转弯', '禁止向左右转弯', 'C', '向左和向右转弯：表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', 15, 1, '2021-12-19 01:44:28', '2021-12-19 01:44:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41217, '这个标志是何含义？', '/uploadFile/yuanBeiTu41217.gif', '1', '4', NULL, '靠道路右侧停车', '只准向右转弯', '右侧是下坡路段', '靠右侧道路行驶', 'D', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', 15, 1, '2021-12-19 01:44:29', '2021-12-19 01:44:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41218, '这个标志是何含义？', '/uploadFile/yuanBeiTu41218.gif', '1', '4', NULL, '靠左侧道路行驶', '只准向左转弯', '左侧是下坡路段', '靠道路左侧停车', 'A', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', 15, 1, '2021-12-19 01:44:31', '2021-12-19 01:44:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41219, '这个标志是何含义？', '/uploadFile/yuanBeiTu41219.gif', '1', '4', NULL, '右侧通行', '左侧通行', '向右行驶', '环岛行驶', 'D', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', 15, 1, '2021-12-19 01:44:32', '2021-12-19 01:44:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41220, '这个标志是何含义？', '/uploadFile/yuanBeiTu41220.gif', '1', '4', NULL, '应当鸣喇叭', '禁止鸣喇叭', '禁止鸣高音喇叭', '禁止鸣低音喇叭', 'A', '这是应当鸣笛标志', 15, 1, '2021-12-19 01:44:33', '2021-12-19 01:44:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41221, '这个标志是何含义？', '/uploadFile/yuanBeiTu41221.gif', '1', '4', NULL, '人行横道', '学生通道', '儿童通道', '注意行人', 'A', '蓝色底是人行横道标志', 15, 1, '2021-12-19 01:44:34', '2021-12-19 01:44:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41222, '这个标志是何含义？', '/uploadFile/yuanBeiTu41222.gif', '1', '4', NULL, '禁止小型车行驶', '机动车行驶', '只准小型车行驶', '不准小型车通行', 'B', '此标志表示该道路只供机动车行驶。看到小汽车你应该想到的是机动车行驶。', 15, 1, '2021-12-19 01:44:35', '2021-12-19 01:44:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41223, '这个标志是何含义？', '/uploadFile/yuanBeiTu41223.gif', '1', '4', NULL, '小型车车道', '小型车专用车道', '机动车车道', '多乘员车辆专用车道', 'C', '大客车专用道是一个大客车；公交车道是一个公交车；多乘员车辆专用车道，里面画了两个小人；小轿车的就是所有机动车的车道', 15, 1, '2021-12-19 01:44:36', '2021-12-19 01:44:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41224, '这个标志是何含义？', '/uploadFile/yuanBeiTu41224.gif', '1', '4', NULL, '非机动车停车位', '电动自行车行驶', '非机动车停放区', '非机动车行驶', 'D', '非机动车行驶。圆形里面是单车。记住就好了，', 15, 1, '2021-12-19 01:44:37', '2021-12-19 01:44:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41225, '这个标志是何含义？', '/uploadFile/yuanBeiTu41225.gif', '1', '4', NULL, '非机动车车道', '禁止自行车通行车道', '自行车专用车道', '停放自行车路段', 'A', '记住就好了，矩形蓝色标志加上两条虚线，就表示是某某车道。', 15, 1, '2021-12-19 01:44:39', '2021-12-19 01:44:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41226, '这个标志是何含义？', '/uploadFile/yuanBeiTu41226.gif', '1', '4', NULL, '掉头', '倒车', '左转', '绕行', 'A', '此标志表示允许掉头。', 15, 1, '2021-12-19 01:44:40', '2021-12-19 01:44:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41227, '出车前对轮胎进行哪些方面的检查？', '', '1', '4', NULL, '什么也不用检查', '轮胎有没有清洗', '备胎在什么位置', '轮胎的紧固和气压', 'D', '对轮胎的检查是为了确保在行车过程中能安全行驶，由此本题应选D。', 15, 1, '2021-12-19 01:44:41', '2021-12-19 01:44:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41228, '会车中道路一侧有障碍，双方机动车应如何做？', '', '1', '4', NULL, '无障碍一方让对向先行', '速度慢的让速度快的先行', '有障碍的一方让对向先行', '速度快的让速度慢的先行', 'C', '根据规定，有障碍的让无障碍的一方先行。但有障碍的一方已经驶入有障碍路段而无障碍一方未驶入的，有障碍的一方先行。', 15, 1, '2021-12-19 01:44:42', '2021-12-19 01:44:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41229, '机动车在狭窄的坡路会车时，正确的会车方法是什么？', '', '1', '4', NULL, '下坡车让上坡车', '坡顶交会时距离坡顶远的一方让行', '上坡车让下坡车', '下坡车已行至中途而上坡车未上坡时，让上坡车', 'A', '《道路交通安全法实施条例》第四十八条： 在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行。', 15, 1, '2021-12-19 01:44:43', '2021-12-19 01:44:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41230, '超车时前方机动车不减速、不让道，驾驶人怎么办？', '', '1', '4', NULL, '连续鸣喇叭加速超越', '加速继续超越', '停止继续超车', '紧跟其后，伺机再超', 'C', '前方车辆不减速、不让道的话，说明此时是不具备超车条件的，所以应该停止继续超车。', 15, 1, '2021-12-19 01:44:44', '2021-12-19 01:44:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41231, '超车时，发现前方机动车正在超车，驾驶人怎么办？', '', '1', '4', NULL, '紧跟其后，伺机超越', '加速强行超越', '连续鸣喇叭催前车让路', '停止超车，让前方机动车先超车', 'D', '若强行超车，很有可能发生危险。只有在前车完成超车后，才能看清前方道路的状况，此时超车也才较为安全。因此选D。', 15, 1, '2021-12-19 01:44:45', '2021-12-19 01:44:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41232, '机动车在雨天临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯', '危险报警闪光灯', '前大灯', '倒车灯', 'B', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用一说。', 15, 1, '2021-12-19 01:44:46', '2021-12-19 01:44:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41233, '机动车在雾天临时停车时，应开启什么灯？', '', '1', '4', NULL, '危险报警闪光灯、示廓灯和后位灯', '左转向灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'A', '雾天开车视野不良，因此若要停车，就应该开启危险报警闪光灯，提醒其他车辆注意。', 15, 1, '2021-12-19 01:44:47', '2021-12-19 01:44:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41234, '机动车在夜间临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', 'C', '夜间行驶时视野不良，因此若要停车，应当开启危险报警闪光灯，提醒其他车辆注意。', 15, 1, '2021-12-19 01:44:48', '2021-12-19 01:44:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41235, '机动车在雪天临时停车时，应开启什么灯？', '', '1', '4', NULL, '前后防雾灯、示廓灯和后位灯', '倒车灯、示廓灯和后位灯', '前大灯、示廓灯和后位灯', '危险报警闪光灯、示廓灯和后位灯', 'D', '报警灯就是前后两黄灯的一亮一灭，四个灯是同时的。 示廓灯就是指表示整车轮廓的，在车的最边最高处，一般是红色小灯。 后位灯一般是指让后面车辆看到本车而使用的，一般是红色，晚上打开车灯时发亮。 因此本题选D。', 15, 1, '2021-12-19 01:44:49', '2021-12-19 01:44:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41236, '机动车停车的错误做法是什么？', '', '1', '4', NULL, '应当在规定地点停放', '禁止在人行道上停放', '在道路上临时停车时，不得妨碍其他车辆和行人通行', '可以停放在非机动车道上', 'D', '注意本题考的是错误的做法，机动车不能停放在非机动车道上，因此本题选D。', 15, 1, '2021-12-19 01:44:50', '2021-12-19 01:44:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41237, '夜间驾驶机动车在窄路或者窄桥遇自行车对向驶来时，要怎样使用灯光？', '', '1', '4', NULL, '连续变换远、近光灯', '使用示廓灯', '使用远光灯', '使用近光灯', 'D', '根据《中华人民共和国道路交通安全法实施条例》第四十八条 在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 15, 1, '2021-12-19 01:44:51', '2021-12-19 01:44:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41238, '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', '', '1', '4', NULL, '前后雾灯', '近光灯', '远光灯', '危险报警闪光灯', 'B', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。', 15, 1, '2021-12-19 01:44:52', '2021-12-19 01:44:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41239, '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？', '', '1', '4', NULL, '使用远光灯', '使用近光灯', '使用危险报警闪光灯', '交替使用远近光灯示意', 'D', '《中华人民共和国道路交通安全法实施条例》第五十九条 机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', 15, 1, '2021-12-19 01:44:53', '2021-12-19 01:44:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41240, '夜间驾驶机动车在照明条件良好的路段跟车行驶怎样使用灯光？', '', '1', '4', NULL, '关闭前照灯', '使用远光灯', '关闭所有车灯', '使用近光灯', 'D', '第四十八条　在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；（二）在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行；（三）在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；（四）在狭窄的山路，不靠山体的一方先行；（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。', 15, 1, '2021-12-19 01:44:54', '2021-12-19 01:44:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41241, '机动车驶入双向行驶隧道前，要如何使用灯光？', '', '1', '4', NULL, '开启危险报警闪光灯', '开启远光灯', '开启雾灯', '开启近光灯', 'D', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。 若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', 15, 1, '2021-12-19 01:44:56', '2021-12-19 01:44:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41242, '行车中遇抢救伤员的救护车从本车道逆向驶来时，要怎样做？', '', '1', '4', NULL, '靠边减速或停车让行', '占用其他车道行驶', '加速变更车道避让', '在原车道内继续行驶', 'A', '遇到正在执行任务的特种车辆，应当予以让行。而本题的救护车正是因为抢救伤员而逆行，因此其他车辆要给予方便，为其让行。因此正确的做法是靠边减速或停车让行。', 15, 1, '2021-12-19 01:44:57', '2021-12-19 01:44:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41243, '驾驶机动车在雨天遇到撑雨伞和穿雨衣的行人在路边行走怎样礼让？', '', '1', '4', NULL, '以正常速度行驶', '临近鸣喇叭示意', '加速从左侧绕行', '提前减速鸣喇叭', 'D', '首先下雨天本来视野就不好，其次行人这样的着装很难在第一时间意识到周围的交通状况变化。因此驾驶人在遇到此类行人时，应当先鸣喇叭示意，再以较低的车速通过。因此本题选D。', 15, 1, '2021-12-19 01:44:58', '2021-12-19 01:44:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41244, '驾驶机动车变更车道时，属于交通陋习的是什么行为？', '', '1', '4', NULL, '提前开启转向灯', '仔细观察后变更车道', '随意并线', '不得妨碍其他车道正常行驶的车', 'C', '注意题目，是属于交通陋习哦~随意并线肯定是不行的，会妨碍他人行驶的哦！', 15, 1, '2021-12-19 01:44:59', '2021-12-19 01:44:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41245, '驾驶人边驾车边吸烟的做法有什么影响？', '', '1', '4', NULL, '妨碍安全驾驶', '可提高注意力', '可缓解驾驶疲劳', '不影响驾驶操作', 'A', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，另外缓解疲劳没有根据，因此本题选A。', 15, 1, '2021-12-19 01:45:00', '2021-12-19 01:45:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41246, '下长坡时，控制车速的正确方法是什么？', '', '1', '4', NULL, '空挡滑行', '挂低速挡', '踏下离合器踏板滑行', '使用驻车制动器', 'B', '挂低速挡是最安全的做法。下坡时不能空挡滑行，也不能踏下离合器踏板滑行，而驻车制动是在停车后用的。', 15, 1, '2021-12-19 01:45:01', '2021-12-19 01:45:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41247, '下长坡连续使用行车制动会造成什么不良后果？', '', '1', '4', NULL, '缩短发动机使用寿命', '驾驶人容易疲劳', '容易造成机动车倾翻', '制动器制动效果下降', 'D', '持续用刹车，会造成刹车片发热，从而制动力减小，严重的会丧失制动，所以长下坡要用低档，用发动机制动。因此本题选D。', 15, 1, '2021-12-19 01:45:02', '2021-12-19 01:45:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41248, '驾驶机动车在山区道路怎样跟车行驶？', '', '1', '4', NULL, '紧随前车之后', '加大安全距离', '减小纵向间距', '尽快超越前车', 'B', '山区道路复杂多变，因此跟车时也要非常注意，因此本题选B。', 15, 1, '2021-12-19 01:45:03', '2021-12-19 01:45:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41249, '山区上坡路段跟车过程中遇前车停车时怎么办？', '', '1', '4', NULL, '从前车两侧超越', '紧跟前车后停车', '保持大距离停车', '连续鸣喇叭提示', 'C', '上坡路段停车的车辆容易出现后溜的情况，因此后车在跟车时要加大跟车距离，确保安全，所以本题选C。', 15, 1, '2021-12-19 01:45:04', '2021-12-19 01:45:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41250, '下长坡控制车速最安全的方法是什么？', '', '1', '4', NULL, '挂入空挡滑行', '踏下离合器滑行', '利用发动机制动', '持续踏制动踏板', 'C', '下长坡利用发动机制动，就是挂入低档的意思，其他选项的做法容易导致熄火或者刹车失灵。', 15, 1, '2021-12-19 01:45:05', '2021-12-19 01:45:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41251, '驾驶机动车上坡行驶如何保持充足动力？', '', '1', '4', NULL, '在车速下降前减挡', '在车速下降后减挡', '在车速过低时减挡', '尽量使用越级减挡', 'A', '车速慢了再换挡不容易前进甚至会熄火，造成车掉下坡来。因此本题选A。', 15, 1, '2021-12-19 01:45:06', '2021-12-19 01:45:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41252, '机动车驶近坡道顶端等影响安全视距的路段时，要如何保证安全？', '', '1', '4', NULL, '快速通过', '使用危险报警闪光灯', '减速慢行并鸣喇叭示意', '随意通行', 'C', '注意“影响安全视距的路段”，也就是说在此路段行驶时无法完全掌握路况，因此要减速慢行谨慎小心，同时鸣喇叭告知其他车辆。因此本题选C。', 15, 1, '2021-12-19 01:45:07', '2021-12-19 01:45:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41253, '夜间驾驶机动车超车遇前车不让路时怎样处置？', '', '1', '4', NULL, '连续鸣喇叭提示', '开远光灯尾随行驶', '保持距离等待让行', '连续变换前照灯远、近光', 'C', '遇到前车不让路时，只能等到前车让路了才能超车，否则容易发生危险，因此本题选C。', 15, 1, '2021-12-19 01:45:08', '2021-12-19 01:45:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41254, '夜间会车前，两车在相距150米之外交替变换前照灯远近光的作用是什么？', '', '1', '4', NULL, '会车前两车之间相互提示', '驾驶操作的习惯行为', '便于双方观察前方情况', '驾驶人之间的一种礼节', 'C', '夜间会车，由于视线受限的关系，必须借助灯光等了解周围情况，因此这么做是为了了解情况，因此本题选C。', 15, 1, '2021-12-19 01:45:09', '2021-12-19 01:45:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41255, '机动车在夜间行驶如何保证安全？', '', '1', '4', NULL, '以最高设计车速行驶', '降低速度，谨慎驾驶', '保持现有速度行驶', '以超过规定的最高车速行驶', 'B', '由于夜间行驶视野不良，因此要降低车速行驶，同时要注意观察周围情况，因此本题选B。', 15, 1, '2021-12-19 01:45:10', '2021-12-19 01:45:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41256, '驾驶机动车在雨天临时停车注意什么？', '', '1', '4', NULL, '开启危险报警闪光灯', '开启前后雾灯', '开启近光灯', '在车后设置警告标志', 'A', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用。因此本题选A。', 15, 1, '2021-12-19 01:45:11', '2021-12-19 01:45:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41257, '当机动车在湿滑路面上行驶时，路面附着力随着车速的增加如何变化？', '', '1', '4', NULL, '急剧增大', '逐渐增大', '急剧减小', '没有变化', 'C', '雨天本来就湿滑，车速增加，惯性随之加大，路面附着力会急剧减小。', 15, 1, '2021-12-19 01:45:12', '2021-12-19 01:45:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41258, '在冰雪路面上减速或停车，要怎样降低车速？', '', '1', '4', NULL, '充分利用行车制动器', '充分利用发动机的牵制作用', '充分利用驻车制动器', '充分利用缓速器', 'B', '充分利用发动机的牵制作用是使车辆在低档位平稳降低车速至停车，其他三种方式在在冰雪路面上极易使车辆滑行，因此本题选B。', 15, 1, '2021-12-19 01:45:14', '2021-12-19 01:45:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41259, '驾驶机动车在冰雪路面怎样跟车行驶？', '', '1', '4', NULL, '保持较大的安全距离', '开启危险报警闪光灯', '不断变换前照灯远近光', '适时鸣喇叭提示前车', 'A', '冰雪路面易滑，制动距离延长，所以要加大安全距离。', 15, 1, '2021-12-19 01:45:15', '2021-12-19 01:45:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41260, '驾驶机动车在结冰的道路上怎样会车？', '', '1', '4', NULL, '两车临近时减速', '适当加速交会', '提前减速缓慢交会', '尽量靠近中线交会', 'C', '由于是结冰的道路，说明路面很易滑，因此在这样的路面上会车时，要注意减速慢行，因此本题选C', 15, 1, '2021-12-19 01:45:16', '2021-12-19 01:45:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41261, '驾驶机动车在山区冰雪道路上遇前车正在爬坡时如何处置？', '', '1', '4', NULL, '前车通过后再爬坡', '迅速超越前车爬坡', '低速超越前车爬坡', '紧随前车后爬坡', 'A', '题的关键在于“冰雪道路”，也就是说这样的路面状况很差，极易滑。因此若跟在前车后面，很有可能因前车后溜滑坡而发生事故。因此只能等前车通过后再爬坡。', 15, 1, '2021-12-19 01:45:17', '2021-12-19 01:45:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41262, '驾驶机动车在冰雪路面行车注意什么？', '', '1', '4', NULL, '制动距离延长', '抗滑能力变大', '路面附着力变大', '制动距离变短', 'A', '冰雪路面，摩擦力减小，抗滑能力减小，所以说制动距离就会变长。因为要更长的时间才能刹住车。', 15, 1, '2021-12-19 01:45:18', '2021-12-19 01:45:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41263, '驾驶机动车遇到大雾或特大雾等能见度过低天气时如何做？', '', '1', '4', NULL, '开启前照灯低速行驶', '开启雾灯低速行驶', '选择安全地点停车', '紧靠路边低速行驶', 'C', '注意题中的“浓雾或特大雾等能见度过低天气”，也就是说可能出现无法正常行驶的情况，因此为了安全起见，应停车，而停车则应选择安全的地点。 因此本题选C。', 15, 1, '2021-12-19 01:45:19', '2021-12-19 01:45:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41264, '雾天对安全行车的主要影响是什么？', '', '1', '4', NULL, '易发生侧滑', '能见度低', '行驶阻力大', '视野变宽', 'B', '雾天的能见度很低，容易造成视野不良。而易发生侧滑的应该是滑溜的路面，行驶阻力大这一点更无从得知。因此本题选B。', 15, 1, '2021-12-19 01:45:20', '2021-12-19 01:45:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41265, '驾驶机动车在雾天怎样跟车行驶？', '', '1', '4', NULL, '保持大间距', '开启远光灯', '开启近光灯', '适时鸣喇叭', 'A', '雾中行车应该尽量低速行驶，尤其是要与前车保持足够的安全车距，万一前方出现问题，也可为自己留有足够的应急距离和反应时间。', 15, 1, '2021-12-19 01:45:21', '2021-12-19 01:45:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41266, '驾驶机动车在雾天两车交会时怎样做最安全？', '', '1', '4', NULL, '开启远光灯', '低速大间距', '开启近光灯', '开启雾灯', 'B', '雾天行车能见度低，视野不良，因此与其他车辆会车时要低速会车，加大会车距离。因此本题选B。', 15, 1, '2021-12-19 01:45:22', '2021-12-19 01:45:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41267, '在泥泞路段行车容易出现什么现象？', '', '1', '4', NULL, '行驶阻力大', '车轮侧滑', '机动车颠簸', '方向失控', 'B', '泥泞路面行驶阻力小，凹凸不平的路面机动车才会颠簸，横风时方向才会失控，因此本题选B。', 15, 1, '2021-12-19 01:45:23', '2021-12-19 01:45:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41268, '驾驶机动车通过漫水桥，停车观察水情确认安全后，怎样通过？', '', '1', '4', NULL, '挂高速挡快速通过', '时刻观察水流的变化', '做好随时停车准备', '挂低速挡匀速通过', 'D', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 15, 1, '2021-12-19 01:45:24', '2021-12-19 01:45:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41269, '遇紧急情况避险时，要沉着冷静，坚持什么样的处理原则？', '', '1', '4', NULL, '先避人后避物', '先避物后避车', '先避车后避人', '先避物后避人', 'A', '以人为本，先避人后避物。', 15, 1, '2021-12-19 01:45:25', '2021-12-19 01:45:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41270, '轮胎气压过低时，高速行驶可能导致什么结果？', '', '1', '4', NULL, '气压不稳', '气压增高', '行驶阻力减小', '爆胎', 'D', '轮胎气压过低或过高都会导致爆胎。轮胎气压过低，在车子行驶过程中，会让轮胎不正常工作，若是高速行驶的话，产生摩擦之后就很容易发生爆胎了。', 15, 1, '2021-12-19 01:45:26', '2021-12-19 01:45:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41271, '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么情况发生？', '', '1', '4', NULL, '气压不稳', '气压更低', '行驶阻力增大', '爆胎', 'D', '轮胎气不足，就会造成轮胎与地面的摩擦范围增大，所以会使温度升高，从而爆胎。因此本题选D。', 15, 1, '2021-12-19 01:45:27', '2021-12-19 01:45:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41272, '避免爆胎的错误做法是什么？', '', '1', '4', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽里的异物', '更换有裂纹或有很深损伤的轮胎', 'A', '轮胎缺气行驶是爆胎的祸根。车辆的缺气（轮胎胎压低于标准胎压）行驶时，随着胎压的下降，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。如果车辆在低速行驶，也会伤胎，而且潜伏期长，隐蔽性大，更有危害性，为以后高速行车时埋下爆胎隐患。所以A是错误的做法', 15, 1, '2021-12-19 01:45:28', '2021-12-19 01:45:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41273, '在事故现场抢救伤员的基本要求是什么？', '', '1', '4', NULL, '先治伤，后救命', '先救命，后治伤', '先帮助轻伤员', '后救助重伤员', 'B', '必须先救人，否则很有可能因为送医不及时而造成伤者死亡。而受伤的人员没有生命危险，可以暂缓一下。因此选B。', 15, 1, '2021-12-19 01:45:29', '2021-12-19 01:45:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41274, '下列关于驾驶摩托车的说法正确的是哪一项？', '', '1', '4', NULL, '双手可以临时离开转向把', '可以随意操作转向把', '严禁双手同时离开转向把', '严禁原地转动转向把', 'C', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得有下列行为：驾驶摩托车手离车把或者在车把上悬挂物品。所以，是严禁双手同时离开把手的。', 15, 1, '2021-12-19 01:45:30', '2021-12-19 01:45:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41275, '使用摩托车制动的错误做法是______。', '', '1', '4', NULL, '同时使用前后制动', '先使用前轮制动', '先使用后轮制动', '不能过早使用前制动', 'B', '摩托车先使用前轮制动，容易发生翻车，十分危险。', 15, 1, '2021-12-19 01:45:32', '2021-12-19 01:45:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41276, '夏季驾驶摩托车的错误做法是______。', '', '1', '4', NULL, '长时间高速行驶', '用油门控制车速', '尽量不用或少用制动', '尽量不要载人或载货', 'A', '夏天温度本来就很高，若一直高速行驶，很有可能让引擎过热，容易发生危险。', 15, 1, '2021-12-19 01:45:33', '2021-12-19 01:45:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41277, '摩托车后轮胎爆裂时，驾驶人应保持镇定，______，极力控制摩托车保持直线行驶，减速停车。', '', '1', '4', NULL, '迅速转动转向把', '双手紧握转向把', '迅速向相反方向转动转向把', '迅速采取制动措施', 'B', '双手紧握转向把是为了控制好车的方向，避免其他意外发生。', 15, 1, '2021-12-19 01:45:34', '2021-12-19 01:45:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41278, '驾驶人发现转向不灵活时，错误的做法是什么？', '', '1', '4', NULL, '应尽快减速停车', '在安全地点停车', '继续驾驶', '停车查明原因', 'C', '发现转向不灵活时若是继续驾驶，而不是停车找原因的话，很有可能会发生状况。因此选项C的做法不对。所以本题选C。', 15, 1, '2021-12-19 01:45:35', '2021-12-19 01:45:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41279, '摩托车驾驶人最有效的防护装备是______。', '', '1', '4', NULL, '眼镜', '专用安全头盔', '手套', '安全护膝', 'B', '人的大脑是身体中最重要的，专用安全头盔是用来保护大脑的，所以是最有效的。', 15, 1, '2021-12-19 01:45:36', '2021-12-19 01:45:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41280, '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', '', '2', '4', NULL, '', '', '', '', 'A', '驾驶机动车上道路行驶前，驾驶人要对机动车的安全技术性能进行认真检查，不得驾驶安全设施不全或者机件不符合技术标准等具有安全隐患的机动车，确保出行前一定都正常。', 15, 1, '2021-12-19 01:45:37', '2021-12-19 01:45:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41281, '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', '', '2', '4', NULL, '', '', '', '', 'B', '驾车要谨慎，所以出现迅速基本都是错误！并且起步后【迅速】转向驶入正常行驶车道会使正常行车道上快速直行的车辆无反应时间，易发生危险造成交通事故。', 15, 1, '2021-12-19 01:45:38', '2021-12-19 01:45:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41282, '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', '', '2', '4', NULL, '', '', '', '', 'A', '一看二慢三汇入，这样做既出于安全考虑，同时也不影响其他车辆的正常行驶。', 15, 1, '2021-12-19 01:45:39', '2021-12-19 01:45:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41283, '驾驶机动车汇入车流时不能影响其他机动车通行。', '', '2', '4', NULL, '', '', '', '', 'A', '汇入车流的前提是不能影响其他车辆的正常通行，因此本题答案是对的。', 15, 1, '2021-12-19 01:45:40', '2021-12-19 01:45:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41284, '驾驶机动车从辅路汇入主路车流时要迅速。', '', '2', '4', NULL, '', '', '', '', 'B', '驾车要谨慎，不要那么“迅速”！注意是汇入车流，所以正确的做法是要减速慢行，在不影响其他车辆正常行驶的情况下汇入主路。', 15, 1, '2021-12-19 01:45:41', '2021-12-19 01:45:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41285, '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', '', '2', '4', NULL, '', '', '', '', 'B', '预计在超车过程中与对面来车有会车可能时，应当放弃超车。若加速超越，有可能会引起不必要的事故。', 15, 1, '2021-12-19 01:45:42', '2021-12-19 01:45:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41286, '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', '', '2', '4', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。因此此题是错的。', 15, 1, '2021-12-19 01:45:43', '2021-12-19 01:45:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41287, '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', '', '2', '4', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法》第四章第一节 第三十七条 道路划设专用车道的，在专用车道内，只准许规定的车辆通行，其他车辆不得进入专用车道内行驶。所以本题是错的。', 15, 1, '2021-12-19 01:45:44', '2021-12-19 01:45:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41288, '机动车行经交叉路口，不得超车。', '', '2', '4', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法》第四十三条 同车道行驶的机动车，后车应当与前车保持足以采取紧急制动措施的安全距离。有下列情形之一的，不得超车：行经铁路道口、交叉路口、窄桥、弯道、陡坡、隧道、人行横道、市区交通流量大的路段等没有超车条件的。 通过隧道，铁路道口，急转弯路段，窄路，窄桥或遇前方道路上有车辆掉头时，不得超车。所以此题是对的。', 15, 1, '2021-12-19 01:45:45', '2021-12-19 01:45:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41289, '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '', '2', '4', NULL, '', '', '', '', 'B', '使用近光灯可以让开车的双方看到彼此，且不会互相干扰视线，同时也可以提醒周围人员，所以在照明良好的路段也要使用近光灯。', 15, 1, '2021-12-19 01:45:46', '2021-12-19 01:45:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41290, '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', '', '2', '4', NULL, '', '', '', '', 'A', '在行驶过程中要留意与前后车的距离，因此若是贸然使用制动，很容易使后车来不及反应发生追尾，所以本题正确。', 15, 1, '2021-12-19 01:45:47', '2021-12-19 01:45:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41291, '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', '', '2', '4', NULL, '', '', '', '', 'B', '跟车距离很重要，不同的道路对跟车距离要求是不一样的。不保持必要的安全距离，发生紧急状况，反应距离不足，容易造成追尾事故。所以此题错误。', 15, 1, '2021-12-19 01:45:48', '2021-12-19 01:45:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41292, '机动车遇有急弯路时要在进入弯路后减速。', '', '2', '4', NULL, '', '', '', '', 'B', '遇到急弯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。因此本题错误。', 15, 1, '2021-12-19 01:45:50', '2021-12-19 01:45:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41293, '驾驶机动车遇到骑自行车人占道影响通行时，可连续鸣喇叭加速从其左侧绕行。', '', '2', '4', NULL, '', '', '', '', 'B', '遇到骑自行车人占道影响通行时，应减速礼让，保持安全距离。连续鸣喇叭催促，加速绕行是不对的。', 15, 1, '2021-12-19 01:45:51', '2021-12-19 01:45:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41294, '驾驶机动车遇到成群青少年绕过路边停放的机动车时，要主动减速让行。', '', '2', '4', NULL, '', '', '', '', 'A', '遇到这种情况，要随时注意发生突发状况，因此要减速让行，以免发生意外。所以本题正确。', 15, 1, '2021-12-19 01:45:52', '2021-12-19 01:45:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41295, '驾驶人一边驾车，一边吸烟对安全行车无影响。', '', '2', '4', NULL, '', '', '', '', 'B', '这样做不仅妨碍驾驶，也会分散驾驶人的注意，所以错误。', 15, 1, '2021-12-19 01:45:53', '2021-12-19 01:45:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41296, '行车中不开转向灯强行并线是违法行为。', '', '2', '4', NULL, '', '', '', '', 'A', '转弯、掉头、并线等应该开启转向灯，这是规定。不开转向灯强行并线，很容易和后方车辆发生刮擦或碰撞，并且责任全部在你，是一个违反交通法的行为。', 15, 1, '2021-12-19 01:45:54', '2021-12-19 01:45:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41297, '驾驶人在确认后方无来车的情况下，可以不开转向灯变更车道。', '', '2', '4', NULL, '', '', '', '', 'B', '变更车道时要提前开启转向灯，养成良好的开车习惯，防患于未然，因此本题错误。', 15, 1, '2021-12-19 01:45:55', '2021-12-19 01:45:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41298, '机动车行驶中遇有自行车借道通行时，可急促鸣喇叭示意让道。', '', '2', '4', NULL, '', '', '', '', 'B', '发生这种情况，应当予以让行，避免发生碰撞，看到有急促鸣喇叭的行为，基本都是错误的。', 15, 1, '2021-12-19 01:45:56', '2021-12-19 01:45:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41299, '驾驶人边驾车，边打手持电话是违法行为。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条：驾驶机动车不得有下列行为：拨打接听手持电话、观看电视等妨碍安全驾驶的行为。', 15, 1, '2021-12-19 01:45:57', '2021-12-19 01:45:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41300, '在正常行车中，尽量靠近中心线或压线行驶，不给对向机动车留有侵占行驶路线的机会。', '', '2', '4', NULL, '', '', '', '', 'B', '首先，题目中的压线行驶就是错误行为。其次，行车时要往中心线靠拢，但是遇到对向来车时，要主动减速向右避让。', 15, 1, '2021-12-19 01:45:58', '2021-12-19 01:45:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41301, '女驾驶人穿高跟鞋驾驶机动车，不利于安全行车。', '', '2', '4', NULL, '', '', '', '', 'A', '穿高跟鞋开车，不能很好地感知刹车和油门，自然是不利于安全开车的。', 15, 1, '2021-12-19 01:45:59', '2021-12-19 01:45:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41302, '驾驶人频繁变更车道不属于驾驶陋习。', '', '2', '4', NULL, '', '', '', '', 'B', '变更车道不能太过频繁，否则会影响正常交通，属于陋习，因此本题错误。', 15, 1, '2021-12-19 01:46:00', '2021-12-19 01:46:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41303, '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '避免水滑最根本的办法就是降低车速。减速或者停车都是为了安全。', 15, 1, '2021-12-19 01:46:01', '2021-12-19 01:46:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41304, '雨天超车要开启前照灯，连续鸣喇叭迅速超越。', '', '2', '4', NULL, '', '', '', '', 'B', '这种天气里面能见度低，车辆应该慢行，就算要超车，也不能连续鸣喇叭，而且要确保安全的情况下超车。 所以此题的关键在“迅速”两字。 因此本题错误。', 15, 1, '2021-12-19 01:46:02', '2021-12-19 01:46:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41305, '在雨天湿滑路面行车要尽量避免紧急制动。', '', '2', '4', NULL, '', '', '', '', 'A', '由于湿滑路面很易滑，若是采取紧急制动，容易发生侧滑或横滑，因此本题正确。', 15, 1, '2021-12-19 01:46:03', '2021-12-19 01:46:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41306, '冰雪道路行车，由于积雪对光线的反射，极易造成驾驶人眩目。', '', '2', '4', NULL, '', '', '', '', 'A', '因为太阳光通过冰雪的折射到人的眼睛里，冰雪在太阳光下就好像一面镜子，折射到驾驶员眼睛里就容易造成炫目了。', 15, 1, '2021-12-19 01:46:04', '2021-12-19 01:46:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41307, '在冰雪道路上行车时，机动车的稳定性降低，加速过急时车轮易空转或溜滑。', '', '2', '4', NULL, '', '', '', '', 'A', '轮胎与地面的摩擦系数降低，轮胎不易附着在地面上，只要一加速，就极易空转或溜滑。', 15, 1, '2021-12-19 01:46:05', '2021-12-19 01:46:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41308, '雪天行车中，在有车辙的路段要循车辙行驶。', '', '2', '4', NULL, '', '', '', '', 'A', '雪天行驶时，道路通常会被雪所掩盖，因此无法了解路面的实际情况。 因此为了避免不可见的危险，就要尽可能沿着车辙行驶。因为这样安全系数更高一些。', 15, 1, '2021-12-19 01:46:07', '2021-12-19 01:46:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41309, '冰雪路面处理情况不能使用紧急制动，但可采取急转向的方法躲避。', '', '2', '4', NULL, '', '', '', '', 'B', '冰雪路面行驶过程中不能采取紧急制动或是急转方向的方法，这是因为冰雪路面易滑，这样做容易造成车子侧滑或侧翻。因此本题错误。', 15, 1, '2021-12-19 01:46:08', '2021-12-19 01:46:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41310, '在雪天临时停车要开启前照灯和雾灯。', '', '2', '4', NULL, '', '', '', '', 'B', '雪天在道路上临时停车，应当开启危险报警闪光灯，用以提醒其他车辆注意。因此本题错误。', 15, 1, '2021-12-19 01:46:09', '2021-12-19 01:46:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41311, '大雾天气能见度低，开启远光灯会提高能见度。', '', '2', '4', NULL, '', '', '', '', 'B', '大雾天气应该开启雾灯和危险警示闪光灯。开启远光灯会引起折射，反而更加不易看清路况。因此本题错误。', 15, 1, '2021-12-19 01:46:10', '2021-12-19 01:46:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41312, '雾天公路行车可多使用喇叭引起对向注意；听到对向机动车鸣喇叭，也要鸣喇叭回应。', '', '2', '4', NULL, '', '', '', '', 'A', '雾天视野不好，看不清楚的情况下，只能通过声音来判断其他车辆的位置。因此雾天比较多用喇叭来“交流”，当听到其他车辆鸣喇叭时，也该告知对方自己所在的位置，避免因看不清发生事故。因此本题正确。', 15, 1, '2021-12-19 01:46:11', '2021-12-19 01:46:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41313, '驾驶机动车在大雾天临时停车后，只开启雾灯和近光灯。', '', '2', '4', NULL, '', '', '', '', 'B', '驾驶机动车在大雾天临时停车后，应开启雾灯和危险报警闪光灯。', 15, 1, '2021-12-19 01:46:12', '2021-12-19 01:46:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41314, '驾驶机动车在雾天行车要开启雾灯。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第五十八条：机动车在夜间没有路灯、照明不良或者遇有雾、雨、雪、沙尘、冰雹等低能见度情况下行驶时，应当开启前照灯、示廓灯和后位灯，但同方向行驶的后车与前车近距离行驶时，不得使用远光灯。机动车雾天行驶应当开启雾灯和危险报警闪光灯。', 15, 1, '2021-12-19 01:46:13', '2021-12-19 01:46:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41315, '大风天气行车中，如果遇到狂风袭来，感觉机动车产生横向偏移时，要急转方向以恢复行驶方向。', '', '2', '4', NULL, '', '', '', '', 'B', '当车遇到横风时如果发生的横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法就是收油，等车速自己下来，这样就可以让车轮恢复抓地力，然后再进行打轮刹车等动作。 这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时打轮的话不管是前轮离地还是后轮离地，都容易发生甩尾，再加上当时的横风，可能还会加重甩尾的趋势。 即本题错误。', 15, 1, '2021-12-19 01:46:14', '2021-12-19 01:46:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41316, '机动车行至泥泞或翻浆路段时，要停车观察，选择平整、坚实或有车辙的路段缓慢通过。', '', '2', '4', NULL, '', '', '', '', 'A', '停车观察是在通过泥泞或翻浆路段之前的操作，也就是说看清楚路况之后再行驶。遇到这类情况，大家都知道要走好走的路，那么什么样的路是好走的？就是平整、坚实的或是有车辙的。这三种是并列的，并不是一定要同时满足这三个条件，可以任选其一。 因此这题是对的。', 15, 1, '2021-12-19 01:46:15', '2021-12-19 01:46:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41317, '泥泞路对安全行车的影响是车轮极易空转和侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '因为泥泞路面很易滑，因此车轮容易空转或是侧滑，因此本题正确。', 15, 1, '2021-12-19 01:46:16', '2021-12-19 01:46:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41318, '机动车涉水后，驾驶人要间断轻踩制动踏板，以恢复制动效能。', '', '2', '4', NULL, '', '', '', '', 'A', '通过积水路面时，会进水，若持续轻踏制动踏板，磨擦后会产生污物，粘附在轮毂内后会影响制动效果。 因此本题正确。', 15, 1, '2021-12-19 01:46:17', '2021-12-19 01:46:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41319, '漫水道路行车时，要挂高速挡，快速通过。', '', '2', '4', NULL, '', '', '', '', 'B', '不应该挂高速挡，而是要“应选用低速挡，以保证汽车有足够的驱动力；入水时应平稳，以防溅起的水花落入电器设备，造成电器短路；发动机中速运转，保持车速，中途不得停车、换挡和急转转向盘”这样才对。', 15, 1, '2021-12-19 01:46:18', '2021-12-19 01:46:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41320, '驾驶机动车通过漫水路时驾驶人要挂低速挡匀速通过。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十四条：机动车行经漫水路或者漫水桥时，应当停车察明水情，确认安全后，低速通过。', 15, 1, '2021-12-19 01:46:19', '2021-12-19 01:46:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41321, '涉水驾驶要保持车速均匀有足够动力，避免停留。', '', '2', '4', NULL, '', '', '', '', 'A', '水中行驶是有一定阻力的，动力不足很有可能会造成涉水时熄火。保持车速均匀有足够动力，避免停留是正确的。', 15, 1, '2021-12-19 01:46:20', '2021-12-19 01:46:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41322, '车速较高，前方发生紧急情况时，要先转方向避让，再采取制动减速，以减小碰撞损坏程度。', '', '2', '4', NULL, '', '', '', '', 'B', '车速较高的情况下，若直接转向，很有可能发生侧翻，此题就错在“要先转方向避让”。', 15, 1, '2021-12-19 01:46:21', '2021-12-19 01:46:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41323, '驾驶人发现轮胎漏气，将机动车驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', '', '2', '4', NULL, '', '', '', '', 'A', '当车辆漏气时，引起轮胎不正常工作，为了避免意外发生，应该驶离主干道，在安全地方检查；也不应该紧急制动，避免后面车辆反应不及时引起事故。', 15, 1, '2021-12-19 01:46:22', '2021-12-19 01:46:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41324, '行车中当机动车突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用抢挂低速档的方法，利用发动机制动使机动车减速。', '', '2', '4', NULL, '', '', '', '', 'A', '如果爆胎时，方向盘会偏向爆胎处，紧急制动会引起翻车。所以只能用抢档，也就是提前挂低档的方法减速。', 15, 1, '2021-12-19 01:46:24', '2021-12-19 01:46:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41325, '避免机动车爆胎的正确做法是降低轮胎气压。', '', '2', '4', NULL, '', '', '', '', 'B', '轮胎气压过低会导致爆胎。轮胎缺气行驶是爆胎的祸根。车辆的缺气（轮胎胎压低于标准胎压）行驶时，随着胎压的下降，轮胎与地面的摩擦成倍增加，胎温急剧升高，轮胎变软，强度急剧下降。这种情况下，如果车辆高速行驶，就可能导致爆胎。因此本题错误。', 15, 1, '2021-12-19 01:46:25', '2021-12-19 01:46:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41326, '驾驶人行车中意识到爆胎时，要轻踏制动踏板，缓慢减速停车。', '', '2', '4', NULL, '', '', '', '', 'A', '意识到爆胎，要紧握方向盘，掌握好方向，轻踩制动踏板，缓速减速停车。汽车爆胎重心会偏向爆胎的一侧，这时千万不能紧急制动，会造成侧翻。因此本题正确。', 15, 1, '2021-12-19 01:46:26', '2021-12-19 01:46:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41327, '行车中遇突然爆胎时，驾驶人要急踏制动踏板减速停车。', '', '2', '4', NULL, '', '', '', '', 'B', '机动车爆胎时，紧急制动的话很容易造成车子侧翻，要轻踏制动踏板慢慢减速停车，因此本题错误。', 15, 1, '2021-12-19 01:46:27', '2021-12-19 01:46:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41328, '行车中当驾驶人意识到机动车爆胎时，应在控制住方向的情况下采取紧急制动，迫使机动车迅速停住。', '', '2', '4', NULL, '', '', '', '', 'B', '不是不可避免的事故，不要贸然使用紧急制动。', 15, 1, '2021-12-19 01:46:28', '2021-12-19 01:46:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41329, '摩托车驾驶人及乘坐人员应当按规定戴安全头盔。', '', '2', '4', NULL, '', '', '', '', 'B', '安全头盔是为了保护头部的，安全起见，肯定是要戴的。', 15, 1, '2021-12-19 01:46:29', '2021-12-19 01:46:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41330, '在道路同方向划有2条以上机动车道的，摩托车应当在最左侧车道行驶。', '', '2', '4', NULL, '', '', '', '', 'B', '道路交通安全法实施条例》第四十四条 在道路同方向划有2条以上机动车道的，左侧为快速车道，右侧为慢速车道。在快速车道行驶的机动车应当按照快速车道规定的速度行驶，未达到快速车道规定的行驶速度的，应当在慢速车道行驶。摩托车应当在最右侧车道行驶。有交通标志标明行驶速度的，按照标明的行驶速度行驶。慢速车道内的机动车超越前车时，可以借用快速车道行驶。因此此题是错的。', 15, 1, '2021-12-19 01:46:30', '2021-12-19 01:46:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41331, '轻便摩托车只允许乘载学龄前儿童。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十五条第三例：机动车载人应当遵守下列规定：摩托车后座不得乘坐未满12周岁的未成年人，轻便摩托车不得载人。所以本题选择“错误”。', 15, 1, '2021-12-19 01:46:31', '2021-12-19 01:46:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41332, '驾驶摩托车时可单手离车把，但不得双手同时离把。', '', '2', '4', NULL, '', '', '', '', 'B', '按照《道路交通安全法实施条例》，无论是单手离把还是双手离把都是不允许的，所以本题错误。', 15, 1, '2021-12-19 01:46:32', '2021-12-19 01:46:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41333, '驾驶摩托车时不得在车把上悬挂物品。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法实施条例》第六十二条规定：驾驶机动车不得有下列行为：驾驶摩托车手离车把或者在车把上悬挂物品。所以，本题是对的。', 15, 1, '2021-12-19 01:46:33', '2021-12-19 01:46:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41334, '乘坐两轮摩托车的人应当侧向骑坐。', '', '2', '4', NULL, '', '', '', '', 'B', '《中华人民共和国道路交通安全法实施条例》第七十七条 乘坐机动车应当遵守下列规定：（一）不得在机动车道上拦乘机动车；（二）在机动车道上不得从机动车左侧上下车；（三）开关车门不得妨碍其他车辆和行人通行；（四）机动车行驶中，不得干扰驾驶，不得将身体任何部分伸出车外，不得跳车；（五）乘坐两轮摩托车应当正向骑坐。', 15, 1, '2021-12-19 01:46:34', '2021-12-19 01:46:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41335, '摩托车不得牵引车辆或者被其他车辆牵引。', '', '2', '4', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法实施条例》第六十一条 牵引故障机动车应当遵守下列规定：汽车吊车和轮式专用机械车不得牵引车辆。摩托车不得牵引车辆或者被其他车辆牵引。', 15, 1, '2021-12-19 01:46:35', '2021-12-19 01:46:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41336, '使用伪造机动车号牌、行驶证的，交通警察可以扣留摩托车。', '', '2', '4', NULL, '', '', '', '', 'A', '《道路交通安全法》第九十六条规定：伪造、变造或者使用伪造、变造的机动车登记证书、号牌、行驶证、驾驶证的，由公安机关交通管理部门予以收缴，扣留该机动车，处15日以下拘留，并处2000元以上5000元以下罚款；构成犯罪的，依法追究刑事责任。摩托车也属于机动车，出现以上行为也是要被扣留的。', 15, 1, '2021-12-19 01:46:36', '2021-12-19 01:46:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41337, '驾驶摩托车，应穿着颜色鲜明的长袖及长裤服装，易被其他交通参与者发现。', '', '2', '4', NULL, '', '', '', '', 'A', '这条题目的着重点是颜色鲜明的衣服，越易被发现就证明你越安全。因此是对的。', 15, 1, '2021-12-19 01:46:37', '2021-12-19 01:46:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41338, '穿高跟鞋驾驶摩托车，不利于安全行车。', '', '2', '4', NULL, '', '', '', '', 'A', '高跟鞋踩刹车有安全隐患，不利于安全，所以此题是对的。', 15, 1, '2021-12-19 01:46:38', '2021-12-19 01:46:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41339, '驾驶摩托车可以穿拖鞋。', '', '2', '4', NULL, '', '', '', '', 'B', '交通法规定：禁止赤脚、穿拖鞋驾驶摩托车。被交警捉到罚100元，扣2分。所以此题是错的。', 15, 1, '2021-12-19 01:46:39', '2021-12-19 01:46:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41340, '狂风袭来，可能会使行驶中的摩托车产生横向偏移。', '', '2', '4', NULL, '', '', '', '', 'A', '有人认为这题是错的，因为题中并没有指出风向。但大家注意题中的可能两个字，也就是这只是一种假设的可能性，因此这题是对的。', 15, 1, '2021-12-19 01:46:40', '2021-12-19 01:46:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41341, '在冰雪道路上行车时，摩托车的稳定性降低，加速过急时车轮极易空转或滑溜。', '', '2', '4', NULL, '', '', '', '', 'A', '无论是小轿车、摩托车还是非机动车，在冰雪道路上行车时，稳定性都会降低，加速过急时极易发生空转或溜滑。', 15, 1, '2021-12-19 01:46:42', '2021-12-19 01:46:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41342, '在泥泞路上制动时，摩托车车轮易发生侧滑或甩尾，导致交通事故。', '', '2', '4', NULL, '', '', '', '', 'A', '泥泞道路容易侧滑，不管是制动还是紧急制动都容易侧滑。主要是因为车轮与地面的摩擦力过低导致车体不易控制。', 15, 1, '2021-12-19 01:46:43', '2021-12-19 01:46:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41343, '摩托车高速行驶时，仅使用前制动，驾驶人易因惯性从车上甩出摔伤。', '', '2', '4', NULL, '', '', '', '', 'A', '这边使用前制动就是前刹车，前面的轮子停了，后面的轮子还有速度，就会随着惯性后轮飞出去，人也会翻出去的！所以有经验的人一般都是先刹后刹车的！', 15, 1, '2021-12-19 01:46:44', '2021-12-19 01:46:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41344, '驾驶摩托车使用制动时，先使用前轮制动，后使用后轮制动。', '', '2', '4', NULL, '', '', '', '', 'A', '在速度较快的情况下，摩托车使用前轮制动易发生车尾腾空，驾驶人易抛出摔伤，所以应先轻使用后轮制动让车辆车速降底再使用前轮制动。', 15, 1, '2021-12-19 01:46:45', '2021-12-19 01:46:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41345, '驾驶摩托车紧急制动时，应特别注意使车身垂直于路面，以免摩托车侧滑倾倒。', '', '2', '4', NULL, '', '', '', '', 'A', '摩托车不比四轮汽车，平衡性本来就稍微差一些。若是紧急制动时，车身有侧偏，很有可能就侧倾了。', 15, 1, '2021-12-19 01:46:46', '2021-12-19 01:46:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41346, '摩托车在较高速度转弯过程中，应当尽量不用或少用制动，否则易产生侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '摩托车在高速转弯时用制动容易抱死，就会很容易发生侧滑。', 15, 1, '2021-12-19 01:46:47', '2021-12-19 01:46:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41347, '摩托车通过弯道或曲线路段时，应提前减速，换入空挡。', '', '2', '4', NULL, '', '', '', '', 'B', '摩托车通过弯道或曲线路段时，应提前减速是对的，但不能换入空挡。所以最后面说换入空挡是错的。', 15, 1, '2021-12-19 01:46:48', '2021-12-19 01:46:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41348, '侧身乘坐摩托车极不安全，一旦发生侧滑，由于重心不稳，会被甩出造成伤亡。', '', '2', '4', NULL, '', '', '', '', 'A', '侧身坐摩托车，重心偏向一边，不能保证两边的平衡，一旦出现侧滑，很容易被甩出，造成伤亡。', 15, 1, '2021-12-19 01:46:49', '2021-12-19 01:46:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41349, '摩托车在颠簸路面上行驶时，驾驶人应采用低挡低速，尽量选择坑大的地方行驶，以减小颠簸。', '', '2', '4', NULL, '', '', '', '', 'B', '题目中只是说“大坑”，并没有说明坑深还是坑浅，万一是深坑的话可能会造成更大的颠簸甚至伤亡，所以本题是错误的。', 15, 1, '2021-12-19 01:46:50', '2021-12-19 01:46:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41350, '摩托车前轮爆胎时，会产生严重的左右摆动，驾驶人应立即减小油门，紧握转向把，逐级减挡，减速停车。', '', '2', '4', NULL, '', '', '', '', 'A', '此时若迅速踩制动，很有可能使得轮胎抱死，这样一来就很容易发生危险了。因此应该采取逐级减档的方式减速，直至停车。', 15, 1, '2021-12-19 01:46:51', '2021-12-19 01:46:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41351, '摩托车遇危险、复杂路况时，驾驶人应以中低速匀速行驶，注意保持车身平衡，谨慎通过。', '', '2', '4', NULL, '', '', '', '', 'A', '只有这样做才能确保行车安全，同时也能及时躲避危险。', 15, 1, '2021-12-19 01:46:52', '2021-12-19 01:46:52', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41352, '摩托车下长坡时要减挡行驶，以充分利用发动机的制动作用。', '', '2', '4', NULL, '', '', '', '', 'A', '下坡不能空挡滑行，减速，利用发动机制动才是正确的做法。', 15, 1, '2021-12-19 01:46:53', '2021-12-19 01:46:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41353, '摩托车在下坡行驶时，可充分利用空挡滑行', '', '2', '4', NULL, '', '', '', '', 'B', '危险动作，下坡时，不准熄火或用空挡。如果下坡空档滑行，只靠刹车来控制车速，遇到连续下坡的时候可能会造成刹车负担过重，刹车片过热而失灵。', 15, 1, '2021-12-19 01:46:54', '2021-12-19 01:46:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41354, '摩托车在山区道路下急坡时，切忌超车。', '', '2', '4', NULL, '', '', '', '', 'A', '《中华人民共和国道路交通安全法实施条例》第七十三条 行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。', 15, 1, '2021-12-19 01:46:55', '2021-12-19 01:46:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41355, '摩托车通过立交桥时，如发现选择路线错误，应立即在原地掉头或倒车更改路线。', '', '2', '4', NULL, '', '', '', '', 'B', '立交上一般都只有一车道，而且其它车辆几乎都是以转弯的形式前进，视线不好，如果选择原地掉头或倒车，危险程度很高极易产生重大交通事故。如果走错路线就只能错到下一出口可以掉头的时候再返回原路线或选择其它的路线到达目的地。', 15, 1, '2021-12-19 01:46:56', '2021-12-19 01:46:56', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41356, '摩托车涉水后，制动器的制动效果不会改变。', '', '2', '4', NULL, '', '', '', '', 'B', '摩托车经过积水道路后，制动器可能会进水，后制动效果逐渐变差，此时极易发生事故，直接影响到行车安全。', 15, 1, '2021-12-19 01:46:57', '2021-12-19 01:46:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41357, '摩托车在冰雪路制动时，驾驶人应采用点制动，并在制动时双脚做好落地的准备。', '', '2', '4', NULL, '', '', '', '', 'A', '冰雪路面较滑，容易发生车轮抱死或者侧滑。所谓点制动就是慢慢连续踩刹车，踩一下松开，再踩。这样做能使车辆缓慢稳定地停下来。', 15, 1, '2021-12-19 01:46:59', '2021-12-19 01:46:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41358, '车辆行至泥泞或翻浆路段时，应停车观察，选择平整、坚实的路段缓慢通过。', '', '2', '4', NULL, '', '', '', '', 'A', '泥泞翻浆路段行车容易发生侧滑，应该停车仔细观察后，降低车速，选择平整、坚实的路段缓慢通过。', 15, 1, '2021-12-19 01:47:00', '2021-12-19 01:47:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41359, '驾驶人发现轮胎漏气，将摩托车驶离主车道时，不要采用紧急制动，以免造成翻车或后车制动不及时导致追尾事故。', '', '2', '4', NULL, '', '', '', '', 'A', '连续轻踏制动，缓慢将车停下，更换轮胎。', 15, 1, '2021-12-19 01:47:01', '2021-12-19 01:47:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41360, '摩托车爆胎时，驾驶人应迅速踏下制动踏板减速，极力控制转向把，迅速停车。', '', '2', '4', NULL, '', '', '', '', 'B', '制动踏板=脚刹，爆胎后，驾驶人在尚未控制车速的时候，是不能急踩刹车的，要不然会横甩或者翻车以致后面来车追尾。正确的做法应该是紧握把手，控制方向，极力保持直线行驶，利用发动机制动，减档或抢挡减速，然后轻踏制动踏板缓慢减速，平稳靠边停车。', 15, 1, '2021-12-19 01:47:02', '2021-12-19 01:47:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41361, '摩托车爆胎后，驾驶人在尚未控制住车速前，不要冒险使用制动器停车，以避免摩托车横甩发生更大的险情。', '', '2', '4', NULL, '', '', '', '', 'A', '制动踏板=脚刹，爆胎后，驾驶人在尚未控制车速的时候，是不能急踩刹车的，要不然会横甩或者翻车以致后面来车追尾。正确的做法应该是紧握把手，控制方向，极力保持直线行驶，利用发动机制动，减档或抢挡减速，然后轻踏制动踏板缓慢减速，平稳靠边停车。', 15, 1, '2021-12-19 01:47:03', '2021-12-19 01:47:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41362, '摩托车爆胎时，驾驶人应在控制住方向的情况下采取紧急制动，迫使摩托车迅速停住。', '', '2', '4', NULL, '', '', '', '', 'B', '控制方向是对的，但是迫使摩托车迅速停住，这句话是错的，这样会导致翻车，造成更大的事故，应该是缓慢降速。', 15, 1, '2021-12-19 01:47:04', '2021-12-19 01:47:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41363, '转弯时速度过快，摩托车容易冲出弯道或侧滑。', '', '2', '4', NULL, '', '', '', '', 'A', '转弯速度越快，角速度越大，离心力也越大，这样一来车子的抓地力就变小了，此时就容易发生侧滑。因此本题是对的。', 15, 1, '2021-12-19 01:47:05', '2021-12-19 01:47:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41364, '行至两座山谷之间，如果遇到较强的横风，感觉摩托车产生横向偏移时，要急转转向把调正行进方向。', '', '2', '4', NULL, '', '', '', '', 'B', '当车遇到横风时如果发生横移，因为在横移的瞬间车轮是离开地面的，所以才会横移，最好的方法是收油，等车速自己下来，这样就可以让车轮回复抓地力，然后再进行打轮刹车等动作。这题判错的原因主要是当时车轮是离地的，也就是说车轮是没有抓地力的，如果这时打轮的话不管是前轮离地还是后轮离地，都容易发生甩尾，再加上当时的横风，可能还会加重甩尾的趋势。', 15, 1, '2021-12-19 01:47:06', '2021-12-19 01:47:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41365, '摩托车高速行驶时急转向，极易造成侧滑相撞或在离心力作用下倾翻的事故。', '', '2', '4', NULL, '', '', '', '', 'A', '摩托车高速行驶路过急转弯时，车身会有一定的倾斜，稍有不慎就会发生侧滑或者侧翻。所以，过急弯的时候最好提前减速。', 15, 1, '2021-12-19 01:47:07', '2021-12-19 01:47:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41366, '驾驶摩托车时应当穿着有脚后跟、鞋底不容易滑的鞋或靴，以保证用脚换档的灵活，准确而可靠。', '', '2', '4', NULL, '', '', '', '', 'A', '穿跟脚鞋子或者靴子比较容易感知刹车或者油门，从而不易出现踩空或者用力过猛的情况。', 15, 1, '2021-12-19 01:47:08', '2021-12-19 01:47:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41367, '驾驶摩托车前必须戴好安全头盔，调整后视镜的角度至能够看清左右两侧后方情况。', '', '2', '4', NULL, '', '', '', '', 'A', '戴好安全头盔是为了保护头部，调整后视镜的角度至能够看清左右两侧后方情况也是为了观察周围情况，一切都是出于安全考虑。', 15, 1, '2021-12-19 01:47:09', '2021-12-19 01:47:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41368, '乘坐摩托车的人，不必戴安全头盔。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第五十一条规定：“机动车行驶时，驾驶人、乘坐人员应当按规定使用安全带，摩托车驾驶人及乘坐人员应当按规定戴安全头盔。”', 15, 1, '2021-12-19 01:47:10', '2021-12-19 01:47:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41369, '通过居民小区时需要注意什么？', '', '3', '4', NULL, '遵守标志', '低速行驶', '不鸣喇叭', '避让居民', 'ABCD', '通过居民小区时需要注意【遵守标志】【低速行驶】【不鸣喇叭】【避让居民】避免交通事故。', 15, 1, '2021-12-21 10:41:05', '2021-12-21 10:41:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41370, '驾驶机动车通过学校时要注意什么？', '', '3', '4', NULL, '观察标志标线', '减速慢行', '不要鸣喇叭', '快速通过', 'ABC', '在有学校的路段一般都会设置提醒有学校或是禁止鸣喇叭的交通标志，因此在经过学校时不仅要观察交通标志，还要记住不得鸣喇叭。另外，由于学校周围会有学生走动，因此在途经时车速一定要慢，避免撞到人或其他。因此本题要选ABC。', 15, 1, '2021-12-21 10:41:06', '2021-12-21 10:41:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41371, '驾驶机动车遇到校车在道路右侧停车上下学生时，应注意什么？', '', '3', '4', NULL, '同向只有一条机动车道，后方机动车应当停车等待', '同向有两条机动车道，左侧车道后方机动车可以减速通过', '同向有三条机动车道，中间车道后方机动车应当停车等待', '同向有三条机动车道，左侧车道后方机动车可以减速通过', 'ACD', '校车在道路上停车上下学生，应当靠道路右侧停靠，开启危险报警闪光灯，打开停车指示标志。1、校车在同方向只有一条机动车道的道路上停靠时，后方车辆应当停车等待，不得超越；2、校车在同方向有两条机动车道的道路上停靠时，后方和相邻机动车道上的机动车应当停车等待，不得超越；3、校车在同方向有两条以上机动车道的道路上停靠时，校车停靠车道后方和相邻机动车道上的机动车应当停车等待，其他机动车道上的机动车应当减速通过。校车后方停车等待的机动车不得鸣喇叭或者使用灯光催促校车。根据规定，选项ACD符合要求，因此本题答案选ACD。', 15, 1, '2021-12-21 10:41:07', '2021-12-21 10:41:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41372, '机动车行经没有交通信号的道路，遇行人横过道路时，以下做法错误的是什么？', '', '3', '4', NULL, '减速或停车避让', '鸣喇叭催促', '寻找间隙穿插驶过', '绕前通过', 'BCD', '即便是在有交通信号灯的道路上，遇行人横过道路时，都应该减速或者停车避让，文明驾驶。在没有交通信号灯的路口更应该注意减速或停车避让。鸣喇叭催促、寻找间隙穿插驶过、绕前通过都极其容易导致事故发生。（可以适当鸣喇叭提醒行人注意，但是不能鸣喇叭催促，鸣喇叭催促意味着持续多次鸣喇叭，这是不合适的）', 15, 1, '2021-12-21 10:41:08', '2021-12-21 10:41:08', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41373, '驾驶机动车过程中遇到专注于使用手机的行人时，以下说法正确的是什么？', '', '3', '4', NULL, '注意观察', '从一侧加速绕过', '谨慎驾驶', '做好停车准备', 'ACD', '驾驶机动车过程中遇到专注于使用手机的行人时，应当注意观察、谨慎驾驶，随时做好停车准备。', 15, 1, '2021-12-21 10:41:09', '2021-12-21 10:41:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41374, '夜间会车时，对面来车不关闭远光灯怎么办？', '', '3', '4', NULL, '及时减速让行，必要时靠边停车', '开启远光灯，迫使来车变换灯光', '视线向右平移，防止眩目', '交替变换远近光灯，提醒来车', 'ACD', '可交替使用远近光灯，同时减速靠右侧行驶或停车，或者视线向右平移，防止眩目。交替变换远近光灯为了提醒对向来车要关闭远光灯，如果对方不理，出于安全只能靠右侧慢行或停车。因此本题选择“及时减速让行，必要时靠边停车”“视线向右平移，防止眩目”“交替变换远近光灯，提醒来车”。', 15, 1, '2021-12-21 10:41:10', '2021-12-21 10:41:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41375, '夜间路边临时停车，以下做法错误的是什么？', '', '1', '4', NULL, '不开启灯光', '开远光灯', '开危险报警闪光灯', '开启示廓灯、后位灯', 'AB', '夜间临时停车要开启危险报警闪光灯、示廓灯和后位灯，用来提醒其他车辆。其次，不能开远光灯，因为会干扰其他驾驶员的视线。（大家注意，题目是夜间路边临时停车的错误做法，而非正确做法）', 15, 1, '2021-12-19 01:47:18', '2021-12-19 01:47:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41376, '夜间驾驶机动车会车时，对方一直使用远光灯，以下做法正确的是什么？', '', '1', '4', NULL, '不停变换远近光灯以及鸣喇叭提醒对方', '视线适当右移，避免直视灯光', '降低车速，靠右行驶', '变换远光灯行驶', 'BC', '只有在没有路灯或者没有照明的情况下才能用远光灯，一般都用近光灯。夜间会车，如果对方一直使用远光灯，我方应该视线适当右移，避免直视灯光。并且降低车速，靠右行驶。变换远光灯会使对方驾驶人看不清道路，因此选项D错误；同时可以短暂变换远近光灯以及鸣喇叭提醒，但是不能不停的，因此选项A错误。（虽然对方不文明行驶，但是我方应尽量做到文明行驶，主动避让）', 15, 1, '2021-12-19 01:47:19', '2021-12-19 01:47:19', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41377, '林某驾车以110公里/小时的速度在城市道路行驶，与一辆机动车追尾后弃车逃离被群众拦下。经鉴定，事发时林某血液中的酒精浓度为135.8毫克/百毫升。林某的主要违法行为是什么？', '', '3', '4', NULL, '醉酒驾驶', '超速驾驶', '疲劳驾驶', '肇事逃逸', 'ABD', '林某的主要违法行为有：1、\"以110公里/小时的速度在城市道路行驶\"属于超速行驶；2、\"与一辆机动车追尾后弃车逃逸\"属于肇事逃逸；3、\"血液中的酒精浓度为135.8毫克/百毫升\"属于醉酒驾驶。', 15, 1, '2021-12-21 10:41:13', '2021-12-21 10:41:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41378, '驾驶汽车遇雨、雪、雾等视线不清或路面较滑时怎样安全会车？', '', '3', '4', NULL, '降低车速行驶', '加大横向间距', '应当加速行驶', '必要时停车避让', 'ABD', '1、A项D项目都是十分必要的。2、汽车的横向安全间距就是指汽车在并排行驶过程中，或者超车时并排行驶时，能保证两车安全，不发生事故的最小距离。因此雨、雪、雾等天气行驶时应尽量加大横向间距，B项正确。3、雨天雪天路面与轮胎摩擦力减小，加速行驶容易导致车辆侧滑或者刹车制动距离增大而出事故。雾天可见度本来就低，平时发现前方车辆或路况稍远距离就可以，但是雾天距离很近才能发现，加速行驶极易造成事故，C选项是不对的。', 15, 1, '2021-12-21 10:41:14', '2021-12-21 10:41:14', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41379, '雨天影响安全行车的主要因素有哪些？', '', '3', '4', NULL, '视线受阻', '路面湿滑', '附着力变小', '行驶阻力增大', 'ABC', '雨天路面与轮胎之间会有雨水，雨水就起到了润滑的作用，所以阻力减小，雨天容易打滑。因此本题选择“视线受阻”“路面湿滑”“附着力变小”。', 15, 1, '2021-12-21 10:41:15', '2021-12-21 10:41:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41380, '雨天安全行车的注意事项是什么？', '', '3', '4', NULL, '避免紧急制动、紧急转向', '保持足够的安全距离', '注意非机动车和行人动态', '选择安全车速行驶', 'ABCD', '雨天行车，受视线及路面的影响，要减速慢行，同时要注意行车和非机动车的动态，并与其他车辆保持较大的安全距离，也不能采取紧急制动或是急转方向的方法，因为容易造成侧滑或横滑状况。 因此本题选ABCD。', 15, 1, '2021-12-21 10:41:16', '2021-12-21 10:41:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41381, '雨天驾驶机动车减速慢行的主要原因是什么？', '', '3', '4', NULL, '影响驾驶人视野', '过快的速度会使得机动车油耗增加', '制动距离会增大', '紧急制动易发生侧滑', 'ACD', '雨天视距变短；路面湿滑，速度过快容易侧滑；高速行驶制动距离会变长。因此本题选ACD。', 15, 1, '2021-12-21 10:41:17', '2021-12-21 10:41:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41382, '雨天驾驶机动车，不宜超车的主要原因是什么？', '', '3', '4', NULL, '不能准确判断周围车辆距离', '周围车辆驾驶人不容易看清超车信号', '道路湿滑，车辆易出现侧滑现象', '不能够及时发现危险情况', 'ABCD', '雨中行车，要随时注意前车的行驶速度和方向，绝不可因前车速度慢而加速超车。尤其是在高速公路上，由于各车道的车速相对较高，司机的视角变窄，加上路面湿滑，强行越线超车时，稍动方向就很容易造成车轮打滑，极易造成与其他车辆发生刮蹭，引发车辆侧翻等意外事故。所以以上选项均正确。', 15, 1, '2021-12-21 10:41:18', '2021-12-21 10:41:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41383, '雨天驾驶机动车，不可以急踩制动踏板的主要原因是什么？', '', '1', '4', NULL, '易导致后车追尾', '会相应增大油耗', '易产生侧滑', '会相应减少油耗', 'AC', '雨天路面湿滑，紧急制动容易产生侧滑，后车也会反应不及造成追尾事故。', 15, 1, '2021-12-19 01:47:26', '2021-12-19 01:47:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41384, '冰雪路面对行车有哪些不利影响？', '', '3', '4', NULL, '车辆操控难度增大', '制动距离延长', '易产生车轮滑转', '极易发生侧滑', 'ABCD', '冰雪路面对行车不利影响有【车辆操控难度增大】【制动距离延长】【易产生车轮滑转】【极易发生侧滑】。', 15, 1, '2021-12-21 10:41:20', '2021-12-21 10:41:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41385, '雾天机动车在道路上通行，驾驶人要怎样做？', '', '3', '4', NULL, '减速慢行', '保持安全车距', '正确使用灯光', '高速行驶', 'ABC', '雾天开车，能见度低，视野不良，因此车速要放慢，同时和其他车辆保持安全距离，视野不良时车灯的使用就显得很重要了。 因此本题选ABC。', 15, 1, '2021-12-21 10:41:22', '2021-12-21 10:41:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41386, '为什么大雪天气，在有雪泥的路上超车危险？', '', '3', '4', NULL, '雪泥可以增加轮胎的附着力', '飞起的雪泥使视线不好', '雪泥下的路面更容易打滑', '遇紧急情况制动距离长', 'BCD', '雪后路上行车将更加艰难，也极易发生交通事故，本题主要考查的是超车的危险性，所以很容易做出选择。', 15, 1, '2021-12-21 10:41:23', '2021-12-21 10:41:23', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41387, '通过泥泞道路时，以下做法正确的是？', '', '3', '4', NULL, '停车观察前方道路', '避免使用行车制动', '尽量避免中途换挡', '提前换入低速挡', 'ABCD', '在泥泞路段行车，首先应降低车速，控制在一档或二挡位，中途不要换挡，如换挡可能使车停顿后很难再起步。因此本题选ABCD。', 15, 1, '2021-12-21 10:41:24', '2021-12-21 10:41:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41388, '机动车通过隧道时，禁止以下哪些行为？', '', '3', '4', NULL, '超车', '停车', '掉头', '倒车', 'ABCD', '根据相关规定，在隧道内不能倒车、掉头、停车或是超车。因此本题选ABCD。', 15, 1, '2021-12-21 10:41:25', '2021-12-21 10:41:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41389, '在隧道内通行时哪些行为是不正确的？', '', '1', '4', NULL, '会车使用远光灯', '在隧道内超车', '会车时保持安全间距', '开启近光灯行驶', 'AB', '题中问的是不正确的行为。在隧道内使用远光灯和超车都是十分危险的行为。', 15, 1, '2021-12-19 01:47:33', '2021-12-19 01:47:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41390, '驾驶机动车行经驼峰桥会车时，以下做法正确的是什么？', '', '3', '4', NULL, '降低车速', '靠右通行', '鸣喇叭示意', '抢行通过', 'ABC', '驾驶机动车行经驼峰桥会车时，行车视线受阻，正确的做法：降低车速；靠右通行；鸣喇叭示意。', 15, 1, '2021-12-21 10:41:27', '2021-12-21 10:41:27', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41391, '驾驶机动车在山区道路下坡路段尽量避免超车，以下说法正确的是什么？', '', '1', '4', NULL, '下坡路段车辆由于重力作用，车速容易过快', '下坡路段由于重力作用，车辆比平路时操控困难', '下坡路段车辆阻力很大', '下坡路段前车车速较快，难以超越', 'AB', '驾驶机动车在山区道路下坡路段尽量避免超车，因为下坡路段车辆由于重力作用，车速容易过快，车辆比平路时更难操控，此时超车容易发生危险。（无论前车车速慢还是快，在下坡路都应该尽量避免超车，因此选项D错误）', 15, 1, '2021-12-19 01:47:35', '2021-12-19 01:47:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41392, '驾驶机动车在山区道路上坡路段接近坡顶时，超车存在风险，以下说法正确的是什么？', '', '3', '4', NULL, '接近坡顶时视线受阻，无法观察坡顶之后道路走向', '接近坡顶时视线受阻，无法观察对向来车情况', '接近坡顶时车速较慢', '接近坡顶时视线受阻，无法观察坡顶之后是否有障碍物', 'ABD', '驾驶机动车在山区道路上坡路段接近坡顶时，接近坡顶时视线受阻需要注意：观察坡顶之后道路走向；观察对向来车情况；观察坡顶之后是否有障碍物。', 15, 1, '2021-12-21 10:41:29', '2021-12-21 10:41:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41393, '驾驶机动车在山区上坡路段行驶，以下做法正确的是什么？', '', '3', '4', NULL, '应尽量匀速前进', '应尽量避免换挡', '时刻注意下行车辆', '应选择高速挡', 'ABC', '驾驶机动车在山区上坡路段行驶，应该挂低档、尽量匀速前进，且途中减挡可能会导致机动车熄火。因此本题选ABC。', 15, 1, '2021-12-21 10:41:30', '2021-12-21 10:41:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41394, '驾驶机动车在山区路段超车时，以下做法正确的是什么？', '', '3', '4', NULL, '提前开启左转向灯', '提前鸣喇叭', '确认前车让超后超越', '直接加速超越', 'ABC', '《中华人民共和国道路交通管理条例》第五十条：机动车超车，必须遵守下列规定:行经交叉路口、人行横道、漫水路、漫水桥等，不准超车。驾驶机动车在山区路段超车时，需要注意：提前开启左转向灯；提前鸣喇叭；确认前车让超后超越。', 15, 1, '2021-12-21 10:41:31', '2021-12-21 10:41:31', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41395, '关于停车，以下做法错误的是什么？', '', '3', '4', NULL, '在交叉路口停车', '在铁路道口停车', '在山区易落石路段停车', '在停车场停车', 'ABC', '在交叉路口、隧道、桥梁、立交桥上、公交车站、消防栓、铁路与公路交叉口及人行道上不能临时停车和停放车辆。（大家注意，铁道路口停车观望，是为了确保能安全通过铁道路口，与题中所说停车属于不同性质）', 15, 1, '2021-12-21 10:41:32', '2021-12-21 10:41:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41396, '应该选择什么地点停车？', '', '1', '4', NULL, '停车场', '道路施划的停车泊位内', '人行横道', '施工路段', 'AB', '停车场。道路施划的停车泊位内，不可以乱停乱放，扰乱社会正常秩序。', 15, 1, '2021-12-19 01:47:40', '2021-12-19 01:47:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41397, '以下什么地点不能停车？', '', '1', '4', NULL, '人行横道', '停车场', '山区容易塌方、泥石流路段', '道路施划的停车泊位内', 'AC', '人行横道不能停车；山区容易塌方、泥石流路段停车容易发生危险。所以应该在停车场或者停车泊位内停车。', 15, 1, '2021-12-19 01:47:41', '2021-12-19 01:47:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41398, '关于超车，以下说法正确的是什么？', '', '3', '4', NULL, '提前开启左转向灯', '夜间交替使用远近光灯', '鸣喇叭提示', '加速从右侧超越', 'ABC', '《道路交通安全法实施条例》第四十七条机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下。应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越。在与被超车辆拉开必要的安全距离后。开启右转向灯，驶回原车道。', 15, 1, '2021-12-21 10:41:35', '2021-12-21 10:41:35', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41399, '关于超车，以下说法正确的是什么？', '', '1', '4', NULL, '超车时从前车左侧超越', '超车时从前车右侧超越', '超车完毕，立即开启右转向灯驶回原车道', '超车完毕，与被超车拉开必要的安全距离后开启右转向灯驶回原车道', 'AD', '《道路交通安全法实施条例》第四十七条机动车超车时，应当提前开启左转向灯、变换使用远、近光灯或者鸣喇叭。在没有道路中心线或者同方向只有1条机动车道的道路上，前车遇后车发出超车信号时，在条件许可的情况下。应当降低速度、靠右让路。后车应当在确认有充足的安全距离后，从前车的左侧超越。在与被超车辆拉开必要的安全距离后。开启右转向灯，驶回原车道。（选项C错在立即二字，应该开启右转向灯，但不应该立即驶回原车道，应该确认与被超车辆有一定的安全距离后再驶回）', 15, 1, '2021-12-19 01:47:43', '2021-12-19 01:47:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41400, '掉头时，以下做法正确的是什么？', '', '1', '4', NULL, '不开转向灯', '提前开启左转向灯', '在掉头车道掉头', '在直行车道掉头', 'BC', '《道路交通安全法实施条例》第五十七条机动车应当按照下列规定使用转向灯：(一)向左转弯、向左变更车道、准备超车、驶离停车地点或者掉头时，应当提前开启左转向灯；(二)向右转夸、向右变更车道、超车完毕驶回原车道、靠路边停车时。应当提前开启右转向灯。', 15, 1, '2021-12-19 01:47:44', '2021-12-19 01:47:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41401, '跟车行驶时，要留有足够的安全距离，是因为什么？', '', '3', '4', NULL, '遇到紧急情况时，能有足够的避让空间', '跟车越近，越不容易掌握前车前方的情况', '防止因前车尾灯损坏，不能及时发现前车制动', '跟车太近，容易发生追尾', 'ABCD', '遇到紧急情况时，能有足够的避让空间；跟车越近，越不容易掌握前车前方的情况；防止因前车尾灯损坏，不能及时发现前车制动；跟车太近，容易发生追尾。', 15, 1, '2021-12-21 10:41:39', '2021-12-21 10:41:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41402, '在道路上怎样安全跟车行驶？', '', '3', '4', NULL, '注意观察前车动态', '随时做好减速准备', '尽量靠路左侧行驶', '保持安全距离', 'ABD', '在道路上安全跟车行驶的方法是“注意观察前车动态”“随时做好减速准备”“保持安全距离”避免交通事故。（选项C会影响对向车辆进行超车等正常行驶行为，是不安全的跟车行驶行为）', 15, 1, '2021-12-21 10:41:40', '2021-12-21 10:41:40', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41403, '以下跟车情况中，应当注意的情形有哪些？', '', '3', '4', NULL, '跟随出租汽车行驶时，要预防其随时可能靠边停车上下乘客', '当前方汽车贴有实习标志时，应该增大跟车距离，预防前车紧急制动', '前方为装满货物的大货车时，应增大跟车距离并避免长时间跟随，以预防货物抛洒和车后盲区带来的危险', '雾天跟车行驶，注意前车紧急制动', 'ABCD', '以上四种情况是行车过程中较为常见的情况，都要注意。', 15, 1, '2021-12-21 10:41:41', '2021-12-21 10:41:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41404, '关于影响制动停车距离的因素，以下说法正确的是什么？', '', '3', '4', NULL, '车辆行驶速度', '驾驶人的反应时间', '路面状况', '载货量的多少以及制动器的结构形式等', 'ABCD', '车辆行驶速度、路面状况、驾驶人的反应时间、载货量的多少以及制动器的结构形式都会影响到制动停车的距离。', 15, 1, '2021-12-21 10:41:42', '2021-12-21 10:41:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41405, '行车中发动机突然熄火后，要采取什么措施？', '', '3', '4', NULL, '立即停车检修', '立即开启危险报警闪光灯', '将机动车移到不妨碍交通的地点停车', '放置故障车警告标志', 'BCD', '选项“立即停车检修”的做法会影响正常交通，若是后方有来车，很有可能会发生追尾等事故。而BCD三个选项是正确的做法，既不影响交通，也能确保驾驶人的安全。', 15, 1, '2021-12-21 10:41:43', '2021-12-21 10:41:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41406, '机动车行驶至转弯路段时，易引发事故的驾驶行为有什么？', '', '3', '4', NULL, '机动车占对向道行驶', '在弯道内急转转向盘', '在驶入弯道前不减速', '机动车靠路右侧行驶', 'ABC', '选项A容易与对向来车发生擦碰，选项BC容易造成侧滑，严重的话会侧翻，因此本题选ABC。', 15, 1, '2021-12-21 10:41:44', '2021-12-21 10:41:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41407, '驾驶机动车准备进入拥堵的环形路口时，以下做法错误的是什么？', '', '3', '4', NULL, '继续驶入拥堵路口', '鸣喇叭让路口内的车让行', '快速驶入路口', '让路口内的车先行', 'ABC', '驾驶机动车准备进入拥堵的环形路口时，应当注意观察让路口内的车先行。', 15, 1, '2021-12-21 10:41:45', '2021-12-21 10:41:45', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41408, '机动车避免爆胎的正确做法是什么？', '', '3', '4', NULL, '降低轮胎气压', '定期检查轮胎', '及时清理轮胎沟槽内的异物', '更换有裂纹或损伤的轮胎', 'BCD', '对于选项BC相信大家都没有疑问，选项D的意思是把有裂纹或损伤的轮胎更换掉，所以D也是正确的。 而选项A，错误的原因是因为若是降低轮胎气压过低，还是会造成爆胎。因此降低轮胎气压这一招不安全。 因此本题要选BCD。', 15, 1, '2021-12-21 10:41:46', '2021-12-21 10:41:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41409, '出车前检查的目的是什么？', '', '3', '4', NULL, '确认机动车车胎是否损毁', '确认周围是否有障碍物', '确认在车辆附近是否存在安全隐患', '确认出车方向的安全性', 'ABCD', '确认机动车车胎是否损毁是为了防止在行车途中发生爆胎，造成意外事故；其他三个选项是一样的道理，都是为了确认周围环境的安全。', 15, 1, '2021-12-21 10:41:47', '2021-12-21 10:41:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41410, '行车中驾驶人接打手机或发短信有什么危害？', '', '3', '4', NULL, '影响乘车人休息', '分散驾驶注意力', '影响正常驾驶操作', '遇紧急情况反应不及', 'BCD', '接打手机或者发短信时不能双手握住方向盘，会影响正常驾驶操作，此时也要分心与对方交流，会分散注意力，此时发生紧急状况，肯定会反应不及。', 15, 1, '2021-12-21 10:41:48', '2021-12-21 10:41:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41411, '下列做法哪些可以有效避免驾驶疲劳？', '', '3', '4', NULL, '连续驾驶不超过4小时', '用餐不宜过饱', '保持良好的睡眠', '餐后适当休息后驾车', 'ABCD', '超过4小时属于疲劳驾驶；用餐过饱因为人体的主要功能用于胃部消化，脑部供氧不足，容易发困，所以不宜过饱；睡眠任何情况下都要保持良好；餐后要休息好才能开车。', 15, 1, '2021-12-21 10:41:49', '2021-12-21 10:41:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41412, '发生交通事故后，防止二次事故的有效措施是什么？', '', '3', '4', NULL, '疏散人员', '开启危险报警闪光灯', '标记伤员的原始位置', '正确放置危险警告标志', 'ABD', '选项“疏散人员”“开启危险警报闪光灯”“正确放置危险敬告标志”都是防止二次事故的有效措施，而选项“标记伤员的原始位置”的时候要是发生什么意外就是第二次伤害了。因此本题选ABD。', 15, 1, '2021-12-21 10:41:50', '2021-12-21 10:41:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41413, '当您遇到以下车辆时，需要礼让的是什么?', '', '3', '4', NULL, '救护车', '消防车', '警车', '校车', 'ABCD', '从理论上讲，不管是特种车，普通车，还是行人，我们都得礼让。对自己负责，对别人负责。', 15, 1, '2021-12-21 10:41:51', '2021-12-21 10:41:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41414, '行车中遇儿童时，应怎样做？', '', '1', '4', NULL, '长鸣喇叭催促', '减速慢行，必要时停车避让', '迅速从一侧通过', '加速绕行', 'B', '儿童的行动变化性非常大，为了保护儿童安全，一定要减速慢行，必要时停车避让。', 15, 1, '2021-12-19 01:47:59', '2021-12-19 01:47:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41415, '驾驶机动车接近路口时，可以加速鸣喇叭通过。', '', '2', '4', NULL, '', '', '', '', 'B', '通过路口时，需要减速慢行，注意路况。没遇到特殊情况时请不要鸣喇叭，请做一个文明驾驶者。', 15, 1, '2021-12-19 01:48:00', '2021-12-19 01:48:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41416, '路口黄灯持续闪烁，警示驾驶人要注意�t望，确认安全通过。', '', '2', '4', NULL, '', '', '', '', 'A', '路口黄灯持续闪烁，警示驾驶人要注意�t望，确认安全通过。', 15, 1, '2021-12-19 01:48:01', '2021-12-19 01:48:01', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41417, '这个路面标记是何含义？', '/uploadFile/yuanBeiTu41417.gif', '1', '4', NULL, '自行车专用道', '非机动车道', '摩托车专用道', '电瓶车专用道', 'B', '图中是非机动车道标记。禁止机动车占道通行。在考试中，自行车就是代表非机动车。', 15, 1, '2021-12-19 01:48:02', '2021-12-19 01:48:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41418, '驾驶机动车遇到这样的行人怎样行驶？', '/uploadFile/yuanBeiTu41418.gif', '1', '4', NULL, '从其前方绕过', '从其身后绕行', '鸣喇叭提醒', '主动停车礼让', 'D', '由图可知，有残疾人正在过马路。文明礼让，应主动停车让行。', 15, 1, '2021-12-19 01:48:04', '2021-12-19 01:48:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41419, '驾驶机动车在这种情况下要尽快加速通过。', '/uploadFile/yuanBeiTu41419.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知是积水路面，加速行驶不利于安全，而且水花会溅到行人身上，不文明。因此本题错误。', 15, 1, '2021-12-19 01:48:05', '2021-12-19 01:48:05', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41420, '驾驶机动车在有这种标志的路口怎样通过最安全？', '/uploadFile/yuanBeiTu41420.gif', '1', '4', NULL, '停车观察路口情况', '加速尽快进入路口', '减速观察左后方情况', '减速缓慢进入路口', 'D', '有倒三角，中间有“让”字的，乃是减速让行。路中并不需要观察左后方情况，需要观察的是前方路况，因此需要减速缓慢的进入路口。', 15, 1, '2021-12-19 01:48:06', '2021-12-19 01:48:06', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41421, '行车中，遇非机动车抢行时，应怎样做？', '', '1', '4', NULL, '鸣喇叭警告', '加速通过', '减速让行', '临近时突然加速', 'C', '文明驾驶，礼貌让行，注意非机动车的安全。', 15, 1, '2021-12-19 01:48:07', '2021-12-19 01:48:07', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41422, '驾驶机动车看到路边有这种标志时怎样行驶？', '/uploadFile/yuanBeiTu41422.gif', '1', '4', NULL, '采取紧急制动', '减速注意观察', '断续鸣喇叭', '做好绕行准备', 'B', '由图可知，前方道路是学校，要注意儿童。通过这样的道路时，要减速慢行，同时注意周围情况，必要时停车让行。', 15, 1, '2021-12-19 01:48:09', '2021-12-19 01:48:09', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41423, '驶近没有人行横道的交叉路口时，发现有人横穿道路，应怎样做？', '', '1', '4', NULL, '减速或停车让行', '鸣喇叭示意其让道', '抢在行人之前通过', '立即变道绕过行人', 'A', '如果没有人行横道，行人就有可能从任何地方走出。遇到行人一定要减速或停车让行，注意行人的安全。', 15, 1, '2021-12-19 01:48:10', '2021-12-19 01:48:10', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41424, '驾驶机动车在该处不影响行人正常通行的情况下可以掉头。', '/uploadFile/yuanBeiTu41424.gif', '2', '4', NULL, '', '', '', '', 'B', '如图，中心线为实线，不能掉头。', 15, 1, '2021-12-19 01:48:11', '2021-12-19 01:48:11', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41425, '驾驶机动车遇到这种情况的人行横道怎样通过？', '', '1', '4', NULL, '减速通过', '加速通过', '鸣喇叭通过', '紧急制动', 'A', '由图可知，这是一个既没有交通信号灯也没有交警指挥的交叉路口，因此通过时一定要减速慢行，并注意观察周围情况。', 15, 1, '2021-12-19 01:48:12', '2021-12-19 01:48:12', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41426, '如图所示，当您看到这个标志时，应该想到什么？', '/uploadFile/yuanBeiTu41426.gif', '1', '4', NULL, '前方有人行横道', '应当相应快速行驶', '视野范围内无行人可以保持原速行驶', '视野范围内无行人可以适当加速通过', 'A', '注意行人标志，用以警告车辆驾驶人减速慢行，注意行人，设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', 15, 1, '2021-12-19 01:48:13', '2021-12-19 01:48:13', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41427, '在交叉路口遇到这种情况时，要在红灯亮以前加速通过路口。', '/uploadFile/yuanBeiTu41427.gif', '2', '4', NULL, '', '', '', '', 'B', '黄灯亮了，车身没越过白色停止线，所以要停车等待。', 15, 1, '2021-12-19 01:48:15', '2021-12-19 01:48:15', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41428, '驾车接近人行横道无人情况下，可以加速通过人行横道。', '', '2', '4', NULL, '', '', '', '', 'B', '通过人行横道时需要减速慢行，注意路况。加速通过会来不及啊判断路况，容易发生交通事故。', 15, 1, '2021-12-19 01:48:16', '2021-12-19 01:48:16', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41429, '驾驶机动车在没有交通信号的路口要加速通过。', '', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法》第四十四条：通过没有交通信号灯、交通标志、交通标线或者交通警察指挥的交叉路口时，应当减速慢行，并让行人和优先通过的车辆先行。', 15, 1, '2021-12-19 01:48:17', '2021-12-19 01:48:17', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41430, '驾驶机动车遇到这种情况要如何行驶？', '/uploadFile/yuanBeiTu41430.gif', '1', '4', NULL, '低速缓慢通过', '加速通过', '连续鸣喇叭通过', '保持正常车速通过', 'A', '由图可知，路面上有水。低速缓慢通过，既能保证安全，又能防止水花溅到行人。', 15, 1, '2021-12-19 01:48:18', '2021-12-19 01:48:18', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41431, '驾驶机动车遇到这种情况的行人可连续鸣喇叭催其让道。', '/uploadFile/yuanBeiTu41431.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知，前方有老人，连续鸣喇叭会使老人受惊吓，甚至发生危险，而且也不礼貌。因此本题描述错误。', 15, 1, '2021-12-19 01:48:20', '2021-12-19 01:48:20', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41432, '驾驶机动车遇到这种情况时，要快速向左绕过。', '/uploadFile/yuanBeiTu41432.gif', '2', '4', NULL, '', '', '', '', 'B', '碰到这样的情况，必须减速慢行或停车避让。保证行车安全。', 15, 1, '2021-12-19 01:48:21', '2021-12-19 01:48:21', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41433, '遇到这种情形时要停车避让行人。', '/uploadFile/yuanBeiTu41433.gif', '2', '4', NULL, '', '', '', '', 'A', '根据《道路交通安全法》第四十七条，机动车行经没有交通信号的道路时，遇行人横过道路，应当避让。', 15, 1, '2021-12-19 01:48:22', '2021-12-19 01:48:22', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41434, '驾驶机动车在这种情况下怎样礼让行人？', '/uploadFile/yuanBeiTu41434.gif', '1', '4', NULL, '等行人通过后再起步', '起步从行人前方绕过', '鸣喇叭告知行人让道', '起步后缓慢靠近行人', 'A', '虽然前方是绿灯，但由于前方有行人正在通过人行横道线，因此要等行人通过后再行驶，以免发生意外。因此本题选A。', 15, 1, '2021-12-19 01:48:24', '2021-12-19 01:48:24', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41435, '驾驶机动车突然遇到这种情况怎样做？', '/uploadFile/yuanBeiTu41435.gif', '1', '4', NULL, '减速或停车让行', '从行人前方绕行', '持续鸣喇叭提醒', '从行人后方绕行', 'A', '遇到有行人横穿马路，还是跑动着的，必须要减速慢行，必要时停车，确保安全。因此本题选A。', 15, 1, '2021-12-19 01:48:25', '2021-12-19 01:48:25', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41436, '驾驶机动车遇到这样的行人怎样礼让？', '/uploadFile/yuanBeiTu41436.gif', '1', '4', NULL, '加速从前方绕过', '加速从身后绕行', '减速或停车让行', '连续鸣喇叭提醒', 'C', '由图可知，有行人正在过马路，应减速或停车让行。', 15, 1, '2021-12-19 01:48:26', '2021-12-19 01:48:26', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41437, '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '/uploadFile/yuanBeiTu41437.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第三十八条：黄灯亮时，已越过停止线的车辆可以继续通行；未经过停止线的则禁止继续通行。', 15, 1, '2021-12-19 01:48:28', '2021-12-19 01:48:28', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41438, '驾驶机动车在路口遇到这种情况要随时准备停车礼让。', '/uploadFile/yuanBeiTu41438.gif', '2', '4', NULL, '', '', '', '', 'A', '由图可知，此为没有交通信号灯的路口。本车还未到路口，应减速或停车礼让已到路口的车辆。', 15, 1, '2021-12-19 01:48:29', '2021-12-19 01:48:29', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41439, '驾驶机动车遇到这样的情况要停车让行。', '/uploadFile/yuanBeiTu41439.gif', '2', '4', NULL, '', '', '', '', 'A', '遇到小朋友排队通过人行横道，应当减速慢行，必要时停车让行，因此本题正确。', 15, 1, '2021-12-19 01:48:30', '2021-12-19 01:48:30', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41440, '在路口遇这种情形要减速让行。', '/uploadFile/yuanBeiTu41440.gif', '2', '4', NULL, '', '', '', '', 'A', '当驾车在路口遇到图中减速让行标志时，机动车应主动减速让行。', 15, 1, '2021-12-19 01:48:32', '2021-12-19 01:48:32', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41441, '在学校门口遇到这种情况要做好随时停车的准备。', '/uploadFile/yuanBeiTu41441.gif', '2', '4', NULL, '', '', '', '', 'A', '如图是前方路段是学校，要注意儿童。因此此时要减速慢行，必要时停车。因此本题正确。', 15, 1, '2021-12-19 01:48:33', '2021-12-19 01:48:33', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41442, '驾驶机动车在路口遇到这种情况的行人怎么办？', '/uploadFile/yuanBeiTu41442.gif', '1', '4', NULL, '及时减速停车让行', '鸣喇叭示意其让道', '加速从行人前通过', '开前照灯示意其让道', 'A', '虽然横穿道路的行人违规，但面对这样的情况，驾驶员依旧要减速慢行，必要时停车避让，因此本题选A。', 15, 1, '2021-12-19 01:48:34', '2021-12-19 01:48:34', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41443, '驾驶机动车在这种情况下可以适当鸣喇叭加速通过。', '/uploadFile/yuanBeiTu41443.gif', '2', '4', NULL, '', '', '', '', 'B', '通过人行横道，一定要减速慢行，必要时停车。因此本题错误。', 15, 1, '2021-12-19 01:48:36', '2021-12-19 01:48:36', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41444, '这个地面标记是什么标线？', '/uploadFile/yuanBeiTu41444.gif', '1', '4', NULL, '人行横道预告', '交叉路口预告', '减速让行预告', '停车让行预告', 'A', '这种标线在到达人行横道前的道路30-50米处设置，它的作用是用来提示驾驶员，前方已接近人行横道，应减速慢行，并须注意行人横过马路。', 15, 1, '2021-12-19 01:48:37', '2021-12-19 01:48:37', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41445, '在这种路口怎样进行掉头？', '/uploadFile/yuanBeiTu41445.gif', '1', '4', NULL, '在人行横道上掉头', '进入路口后掉头', '从右侧车道掉头', '从中心线虚线处掉头', 'D', '虚线处可掉头，实线是不能压的，所以实线处不能掉头、变道等。因此应该从图中中心线虚线处掉头才对。', 15, 1, '2021-12-19 01:48:38', '2021-12-19 01:48:38', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41446, '驾驶机动车在这个位置怎样安全通过？', '/uploadFile/yuanBeiTu41446.gif', '1', '4', NULL, '加速从行人前通过', '从行人后绕行通过', '减速、鸣喇叭示意', '停车等待行人通过', 'D', '此时虽为绿灯，但是人行道上有行人通行，应先停车等待行人通过。', 15, 1, '2021-12-19 01:48:39', '2021-12-19 01:48:39', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41447, '行车中对出现这种行为的人不能礼让。', '/uploadFile/yuanBeiTu41447.gif', '2', '4', NULL, '', '', '', '', 'B', '安全驾驶，文明行车。虽然行人的这种行为是不可取的，但驾驶人还是要予以让行，因此本题错误。', 15, 1, '2021-12-19 01:48:41', '2021-12-19 01:48:41', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41448, '行车中遇列队横过道路的学生时，应怎样做？', '', '1', '4', NULL, '提前加速抢行', '停车让行', '降低车速、缓慢通过', '连续鸣喇叭催促', 'B', '列队横过道路的学生，人数多，通行时间长。降低车速还是容易撞上，停车让行更安全。', 15, 1, '2021-12-19 01:48:42', '2021-12-19 01:48:42', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41449, '浓雾天气能见度低，开启远光灯会提高能见度。', '', '2', '4', NULL, '', '', '', '', 'B', '不能开启远光灯，因浓雾会反射灯光。应开启雾灯。', 15, 1, '2021-12-19 01:48:43', '2021-12-19 01:48:43', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41450, '遇到这种情形时，应怎么办？', '/uploadFile/yuanBeiTu41450.gif', '1', '4', NULL, '从行人前方绕行', '停车让行人先行', '鸣喇叭提醒行人', '从行人后方绕行', 'B', '通过人行横道应减速慢行，遇到行人则需停车让行。', 15, 1, '2021-12-19 01:48:44', '2021-12-19 01:48:44', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41451, '突然出现这种情况，驾驶人要及时减速或停车避让。', '/uploadFile/yuanBeiTu41451.gif', '2', '4', NULL, '', '', '', '', 'A', '由图可知，有小朋友正在过马路。这时应及时减速，必要时停车让行。', 15, 1, '2021-12-19 01:48:46', '2021-12-19 01:48:46', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41452, '驾驶机动车在人行横道前遇到这种情况一定要减速慢行。', '/uploadFile/yuanBeiTu41452.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知，有行人正从人行横道线上横穿道路，而机动车已临近人行横道线，此时应停车让行，以免相撞，因此本题错误。', 15, 1, '2021-12-19 01:48:47', '2021-12-19 01:48:47', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41453, '驾驶机动车在学校门口遇到这种情况怎样行驶？', '/uploadFile/yuanBeiTu41453.gif', '1', '4', NULL, '从列队前方绕过', '减速慢行通过', '及时停车让行', '从列队空隙穿过', 'C', '由图可知，前方人行横道线上有学生排着队通过，此时应当等学生们通过后再通过。因此在临近人行横道线时，应当停车让行。所以本题选C。', 15, 1, '2021-12-19 01:48:48', '2021-12-19 01:48:48', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41454, '路口最前端的双白虚线是什么含义？', '/uploadFile/yuanBeiTu41454.gif', '1', '4', NULL, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', 'C', '虚线可以压线，就是可以过去，边上有倒三角“让”字，所以是减速让行路口，单根白色实线是停止线，俩根白色实线是停车让行线，俩根白色虚线是减速让行线。', 15, 1, '2021-12-19 01:48:49', '2021-12-19 01:48:49', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41455, '路口最前端的双白实线是什么含义？', '/uploadFile/yuanBeiTu41455.gif', '1', '4', NULL, '等候放行线', '停车让行线', '减速让行线', '左弯待转线', 'B', '停车让行线是在没有信号灯状态下。停止线是遇有信号灯红灯时表示车辆等候放行信号的位置,不能超越或压此线。路口，单根白色实线是停止线，俩根白色实线是停车让行线，俩根白色虚线是减速让行线。', 15, 1, '2021-12-19 01:48:50', '2021-12-19 01:48:50', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41456, '图中圈内白色横实线是何含义？', '/uploadFile/yuanBeiTu41456.gif', '1', '4', NULL, '停止线', '让行线', '减速线', '待转线', 'A', '路口，单根白色实线是停止线，俩根白色实线是停车让行线，俩根白色虚线是减速让行线。', 15, 1, '2021-12-19 01:48:51', '2021-12-19 01:48:51', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41457, '在路口遇到这种情形时怎样做？', '/uploadFile/yuanBeiTu41457.gif', '1', '4', NULL, '停在网状线区域内等待', '停在路口以外等待', '跟随前车通过路口', '停在路口内等待', 'B', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。根据规定，题目中情形为了不加重拥堵，机动车应该停在路口以外等待。B项符合要求，因此本题答案为B。', 15, 1, '2021-12-19 01:48:53', '2021-12-19 01:48:53', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41458, '遇到这种情况的路段，可以进入网状线区域内停车等候。', '/uploadFile/yuanBeiTu41458.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第五十三条：机动车在遇有前方机动车停车排队等候或者缓慢行驶时，应当依次排队，不得从前方车辆两侧穿插或者超越行驶，不得在人行横道、网状线区域内停车等候。', 15, 1, '2021-12-19 01:48:54', '2021-12-19 01:48:54', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41459, '驾驶机动车遇到这种情况的人行横道线可以加速通过。', '/uploadFile/yuanBeiTu41459.gif', '2', '4', NULL, '', '', '', '', 'B', '遇到前方有人行横道应减速慢行，接近时停车让行。而由图中更可知有行人即将穿过，因此本题错误。', 15, 1, '2021-12-19 01:48:55', '2021-12-19 01:48:55', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41460, '驾驶机动车在有这种标志的路口怎样通过最安全？', '/uploadFile/yuanBeiTu41460.gif', '1', '4', NULL, '停车观察路口情况', '加速尽快进入路口', '减速缓慢进入路口', '减速观察左后方情况', 'A', '如图右侧为停车让行标志，因此在此路段要停车观察路口情况。', 15, 1, '2021-12-19 01:48:57', '2021-12-19 01:48:57', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41461, '驾驶机动车在居民小区遇行人缓慢过马路时可以连续鸣喇叭。', '', '2', '4', NULL, '', '', '', '', 'B', '通过居民小区时需要注意【遵守标志】、【低速行驶】、【不鸣喇叭】、【避让居民】避免交通事故。题目描述错误，本题答案为错。', 15, 1, '2021-12-19 01:48:58', '2021-12-19 01:48:58', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41462, '这个标志是何含义？', '/uploadFile/yuanBeiTu41462.gif', '1', '4', NULL, '低速行驶', '注意行人', '行人先行', '步行', 'D', '蓝色表示告示，图中为人在走路。所以是步行。', 15, 1, '2021-12-19 01:48:59', '2021-12-19 01:48:59', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41463, '驾驶机动车在道路上遇到这种情形在其后可以鸣喇叭催催。', '/uploadFile/yuanBeiTu41463.gif', '2', '4', NULL, '', '', '', '', 'B', '由图可知前方是陪着骑车儿童的长辈，以防儿童突然改变方向或是从小车子上掉落，因此要保持一定的安全距离，减速慢行，必要时停车避让。鸣喇叭是错误的行为。', 15, 1, '2021-12-19 01:49:00', '2021-12-19 01:49:00', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41464, '如图所示，接近人行横道线时怎样安全行驶？', '/uploadFile/yuanBeiTu41464.gif', '1', '4', NULL, '连续鸣喇叭警告', '加速从前方绕过', '出现危险再减速', '主动减速让行', 'D', '根据《道路交通安全法》第47条：机动车行经人行横道时，应当减速行驶；遇到行人正在通过人行横道，应当停车让行。机动车行经没有交通信号灯的道路时，遇行人横过道路，应当避让，因此选择D，主动减速让行。', 15, 1, '2021-12-19 01:49:02', '2021-12-19 01:49:02', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41465, '驾驶机动车看到这个标志时要及时减速，注意观察。', '/uploadFile/yuanBeiTu41465.gif', '2', '4', NULL, '', '', '', '', 'A', '人行横道标志，通过时应当减速慢行，注意观察。', 15, 1, '2021-12-19 01:49:03', '2021-12-19 01:49:03', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41466, '行驶到这个路口可以压斑马线掉头。', '/uploadFile/yuanBeiTu41466.gif', '2', '4', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', 15, 1, '2021-12-19 01:49:04', '2021-12-19 01:49:04', NULL, NULL, '0');
INSERT INTO `subject` VALUES (41532, '行驶到这个路口可以压斑马线掉头。', '/uploadFile/yuanBeiTu41532.gif', '2', '1', NULL, '', '', '', '', 'B', '《道路交通安全法实施条例》第四十九条：机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。', NULL, 1, '2021-12-21 10:44:11', '2021-12-21 10:44:11', NULL, NULL, '0');
