DROP TABLE IF EXISTS `subject`;
CREATE TABLE `subject`  (
  `s_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '题目id',
  `s_question` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '问题',
  `s_img` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '图片路径',
  `s_type` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '题目类型',
  `s_km` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '题目科目',
  `s_car` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '1',
  `item_a` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `item_b` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `item_c` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `item_d` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `answer` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `s_explain` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '解释',
  `chapter_id` int(6) NULL DEFAULT NULL COMMENT '题目节章',
  `know_id` int(6) NULL DEFAULT NULL COMMENT '知识点',
  `add_time` datetime NULL DEFAULT NULL COMMENT '添加时间',
  `mod_time` datetime NULL DEFAULT NULL COMMENT '修改时间',
  `success_count` bigint(20) NULL DEFAULT NULL COMMENT '答对次数',
  `error_count` bigint(20) NULL DEFAULT NULL COMMENT '答错次数',
  `s_disable` char(1) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '0' COMMENT '是否停用',
  PRIMARY KEY (`s_id`) USING BTREE,
  UNIQUE INDEX `s_id`(`s_id`) USING BTREE,
  INDEX `subject_ibfk_2`(`chapter_id`) USING BTREE,
  INDEX `subject_ibfk_3`(`know_id`) USING BTREE,
  INDEX `s_type`(`s_type`, `s_km`) USING BTREE,
  CONSTRAINT `subject_ibfk_2` FOREIGN KEY (`chapter_id`) REFERENCES `chapter` (`chapter_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `subject_ibfk_3` FOREIGN KEY (`know_id`) REFERENCES `know_points` (`know_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 41588 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

